INSERT INTO `me_question` VALUES (1, '小型车车道', '小型车专用车道', '多乘员车辆专用车道', '机动车车道', '4', 'http://images.juheapi.com/jztk/c1c2subject1/1.jpg', '这个标志是何含义？', '此为机动车车道，比多乘员车辆专用车道少俩人。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (2, '线形诱导标志', '合流诱导标志', '分流诱导标志', '转弯诱导标志', '1', 'http://images.juheapi.com/jztk/c1c2subject1/2.jpg', '这个标志是何含义呢？', '线型诱导标线型诱导标志，用于引导车辆驾驶人改变行驶方向，促使安全运行。视需要设于易肇事之弯道路段，小半径匝道曲线或中央隔离设施及渠化设施的端部。线形诱导标的颜色规定为：指示性线形诱导标一般道路为蓝底白图案,高速公路为绿底白图案，用以提供一般性行驶指示;警告性线形诱导标为红底白图案，可使车辆驾驶人提高警觉，并准备防范应变之措施。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (3, '车道数变少', '合流处', '应急车道', '向左变道', '1', 'http://images.juheapi.com/jztk/c1c2subject1/3.jpg', '这个标志是何含义？', '三车道变成二车道故车道数变少，选A。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (4, '禁止自行车通行车道', '非机动车车道', '自行车专用车道', '停放自行车路段', '2', 'http://images.juheapi.com/jztk/c1c2subject1/4.jpg', '这个标志是何含义？', '此图为非机动车车道，别误以为自行车专用车道，没有自行车专用车道这一说。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (5, '停车让行', '单行路', '会车先行', '对向先行', '3', 'http://images.juheapi.com/jztk/c1c2subject1/5.jpg', '这个标志是何含义？', '表示会车先行，此标志设在车道以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (6, '右转车道', '掉头车道', '左转车道', '直行车道', '4', 'http://images.juheapi.com/jztk/c1c2subject1/6.jpg', '这个标志是何含义？', '表示只准一切车辆直行。此标志设在直行的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (7, '减速慢行', '注意危险', '拥堵路段', '事故多发路段', '2', 'http://images.juheapi.com/jztk/c1c2subject1/7.jpg', '这个标志是何含义？', '注意危险：用以促使车辆驾驶员谨慎慢行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (8, '车道方向预告', '交叉路口预告', '分道信息预告', '分岔处预告', '2', 'http://images.juheapi.com/jztk/c1c2subject1/8.jpg', '这个标志是何含义？', '此为交叉路口预告，记住吧。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (9, '距有人看守铁路道口50米', '距无人看守铁路道口100米', '距有人看守铁路道口100米', '距无人看守铁路道口50米', '2', 'http://images.juheapi.com/jztk/c1c2subject1/9.jpg', '这个标志是何含义？', '一道红线是50米，二道100米。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (10, '分向行驶车道', '掉头和左转合用车道', '禁止左转和掉头车道', '直行和左转合用车道', '2', 'http://images.juheapi.com/jztk/c1c2subject1/10.jpg', '这个标志是何含义？', '左转和掉头合并在一个标志里，你应该能看到的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (11, '省道编号', '国道编号', '县道编号', '乡道编号', '3', 'http://images.juheapi.com/jztk/c1c2subject1/11.jpg', '这个标志是何含义？', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底的，国道是红底的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (12, '前方40米减速', '最低时速40公里', '限制40吨轴重', '限制最高时速40公里', '4', 'http://images.juheapi.com/jztk/c1c2subject1/12.jpg', '这个标志是何含义？', '限制最高时速40公里：表示该标志至前方限制速度标志的路段内，机动车行驶速度不得超过标志所示数值。此标志设在需要限制车辆速度的路段的起点。以图为例：限制行驶时速不得超过40公里。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403395592196, '十字交叉路口预告', '互通立体交叉预告', 'Y型交叉路口预告', '环行交叉路口预告', '4', 'http://images.juheapi.com/jztk/c1c2subject1/13.jpg', '这个标志是何含义？', '环形交叉口是在几条相交道路的平面交叉口中央设置一个半径较大的中心岛，使所有经过交叉口的直行和左转车辆都绕着中心岛作逆时针方向行驶，在其行驶过程中将车流的冲突点变  为交织点，从而保证交叉口的行车安全，提高交叉口的通行能力。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403395592197, '专用停车场', '露天停车场', '室内停车场', '内部停车场', '3', 'http://images.juheapi.com/jztk/c1c2subject1/14.jpg', '这个标志是何含义？', '你看那个P字上边还有个遮挡，说明是室内的，如果没有上边那个遮挡就是露天停车场。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403395592198, '道路流量监测', '减速拍照区', '全路段抓拍', '交通监控设备', '4', 'http://images.juheapi.com/jztk/c1c2subject1/15.jpg', '这个标志是何含义？', '交通监控设备标志属于指路标志，蓝底、白图形、白边框、蓝色衬边。用于告知机动车驾驶人该区域设置有固定式交通监控设备。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563714, '靠道路左侧停车', '左侧是下坡路段', '只准向左转弯', '靠左侧道路行驶', '4', 'http://images.juheapi.com/jztk/c1c2subject1/16.jpg', '这个标志是何含义？', '靠左侧道路行驶：表示只准一切车辆靠左侧道路行驶。此标志设在车辆必须靠左侧行驶的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563715, '大型客车专用车道', '公交线路专用车道', '快速公交系统专用车道', '多乘员车辆专用车道', '2', 'http://images.juheapi.com/jztk/c1c2subject1/17.jpg', '这个标志是何含义？', '此为公交线路专用车道，与快速公交系统专用车道的区别是少了“快速公交”四个字。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563716, '小型汽车专用车道', '机动车专用车道', '出租汽车专用车道', '多乘员车辆专用车道', '4', 'http://images.juheapi.com/jztk/c1c2subject1/18.jpg', '这个标志是何含义？', '此标志为多乘员车辆专用车道，比机动车专用车道标志上的小车多了2个小人，所以它是多乘员车辆专用车道。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563717, '休息区', '服务区', '停车场', '观景台', '1', 'http://images.juheapi.com/jztk/c1c2subject1/19.jpg', '这个标志是何含义？', '这个是休息区，服务区是有加油，通车，吃饭的组合标志，停车场就是一个P，图中没有加油站和吃饭的，所以它只是一个休息区。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563718, '直行和掉头合用车道', '直行和左转合用车道', '直行和右转车道', '分向行驶车道', '2', 'http://images.juheapi.com/jztk/c1c2subject1/20.jpg', '这个标志是何含义？', '表示只准一切车辆直行和向左转弯。此标志设在车辆必须直行和向左转弯的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563719, '平面交叉路口', '环行平面交叉', '注意交互式道路', '注意分离式道路', '4', 'http://images.juheapi.com/jztk/c1c2subject1/21.jpg', '这个标志是何含义？', '注意分离式道路：用以警告车辆驾驶人注意前方平面交叉的被交道路是分离式道路。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563720, '丁字交叉路口预告', '道路分叉处预告', 'Y型交叉路口预告', '十字交叉路口预告', '1', 'http://images.juheapi.com/jztk/c1c2subject1/22.jpg', '这个标志是何含义？', '你看它长的多像丁字。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563721, '地点距离', '行驶路线', '终点地名', '行驶方向', '1', 'http://images.juheapi.com/jztk/c1c2subject1/23.jpg', '这个标志是何含义？', '你看那个地点名和KM公里多明显，所以本题的答案是地点距离。如果有箭头的一半是行驶方向。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563722, '告知方向信息', '警告前方危险', '限制车辆、行人通行', '指示车辆、行人行进', '4', '', '指示标志的作用是什么？', '指示标志是交通标志中主要标志的一种，用以指示车辆和行人按规定方向、地点行驶。 指示标志的颜色为蓝底、白色图案；形状分为圆形、长方形和正方形。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563723, '塌方路段', '施工路段', '前方工厂', '道路堵塞', '2', 'http://images.juheapi.com/jztk/c1c2subject1/25.jpg', '这个标志是何含义？', '施工路段：用以告示前方道路施工，车辆应减速慢行或绕道行驶。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563724, '注意危险', '右侧绕行', '左侧绕行', '单向通行', '3', 'http://images.juheapi.com/jztk/c1c2subject1/26.jpg', '这个标志是何含义？', '左侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，左侧绕行通过。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563725, '直行和左转车道', '直行和辅路出口车道', '直行和右转合用车道', '分向行驶车道', '3', 'http://images.juheapi.com/jztk/c1c2subject1/27.jpg', '这个标志是何含义？', '表示只准一切车辆直行和向右转弯。此标志设在车辆必须直行和向右转弯）的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563726, '限制宽度为3.5米', '解除3.5米限高', '限制车距为3.5米', '限制高度为3.5米', '4', 'http://images.juheapi.com/jztk/c1c2subject1/28.jpg', '这个标志是何含义？', '限制高度为3.5米：表示禁止装载高度超过标志所示数值的车辆通行。此标志设在最大允许高度受限制的地方。 以图为例：装载高度不得超过3.5米。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563727, '禁止通行', '减速行驶', '限时进入', '禁止驶入', '1', 'http://images.juheapi.com/jztk/c1c2subject1/29.jpg', '这个标志是何含义？', '禁止通行：表示禁止一切车辆和行人通行。此标志设在禁止通行的道路入口处。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563728, '十字交叉路口预告', '互通式立体交叉预告', 'Y型交叉路口预告', '环行交叉路口预告', '2', 'http://images.juheapi.com/jztk/c1c2subject1/30.jpg', '这个标志是何含义？', '互通式立体交叉：路线交叉中的一种重要类型。交叉道路之间立体交叉并以匝道相互连通以实现交通转换的交叉。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563729, '右侧是下坡路段', '靠右侧道路行驶', '靠道路右侧停车', '只准向右转弯', '2', 'http://images.juheapi.com/jztk/c1c2subject1/31.jpg', '这个标志是何含义？', '靠右侧道路行驶：表示只准一切车辆靠右侧道路行驶。此标志设在车辆必须靠右侧行驶的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563730, '禁止变道', '禁止左转', '禁止直行', '禁止掉头', '4', 'http://images.juheapi.com/jztk/c1c2subject1/32.jpg', '这个标志是何含义？', '禁止掉头：表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563731, '注意双向行驶', '靠两侧行驶', '注意潮汐车道', '可变车道', '3', 'http://images.juheapi.com/jztk/c1c2subject1/33.jpg', '这个标志是何含义？', '“潮汐车道”是指根据早晚交通流量不同情况，对有条件的道路，试点开辟潮汐车道，通过车道灯的指示方向变化，控制主干道车道行驶方向，来调整车道数。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563732, '直行和向左转弯', '直行和向右转弯', '禁止直行和向右转弯', '只准向左和向右转弯', '2', 'http://images.juheapi.com/jztk/c1c2subject1/34.jpg', '这个标志是何含义？', '直行和向右转弯：表示只准一切车辆直行和向右转弯。此标志设在车辆必须直行和向右转弯）的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563733, '禁止直行和向左变道', '禁止直行和向左转弯', '允许直行和向左变道', '禁止直行和向右转弯', '2', 'http://images.juheapi.com/jztk/c1c2subject1/35.jpg', '这个标志是何含义？', '禁止直行和向左转弯：表示前方路口禁止一切车辆直行和向左转弯。此标志设在禁止直行和向左转弯的路口前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563734, '观景台', '停车场', '休息区', '停车位', '1', 'http://images.juheapi.com/jztk/c1c2subject1/36.jpg', '这个标志是何含义？', '观景台那三个字多明显，你再不选它，叫它情何以堪。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563735, '指路标志', '指示标志', '禁令标志', '警告标志', '1', 'http://images.juheapi.com/jztk/c1c2subject1/37.jpg', '这属于哪一类标志？', '他是指方向的故为指路标志。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563736, '乡道编号', '县道编号', '省道编号', '国道编号', '4', 'http://images.juheapi.com/jztk/c1c2subject1/38.jpg', '这个标志是何含义？', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底的，国道是红底的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563737, '距无人看守铁路道口150米', '距无人看守铁路道口100米', '距有人看守铁路道口100米', '距有人看守铁路道口150米', '1', 'http://images.juheapi.com/jztk/c1c2subject1/39.jpg', '这个标志是何含义？', '一道红线是50米，三道150米。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563738, '两侧街道', '干路先行', '停车让行', '单行路', '2', 'http://images.juheapi.com/jztk/c1c2subject1/40.jpg', '这个标志是何含义？', '表示干路先行，此标志设在车道以前适当位置。答题技巧：记住哥一句话，谁粗谁先行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563739, '向右转弯', '单行路', '只准直行', '直行车道', '1', 'http://images.juheapi.com/jztk/c1c2subject1/41.jpg', '这个标志是何含义？', '向右转弯：表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563740, '允许临时停车', '允许长时停车', '禁止长时停车', '禁止停放车辆', '4', 'http://images.juheapi.com/jztk/c1c2subject1/42.jpg', '这个标志是何含义？', '禁止停放车辆：表示在限定的范围内，禁止一切车辆临时或长时停放。此标志设在禁止车辆停放的地方。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563741, '左转行驶车道', '直线行驶车道', '右转行驶车道', '分向行驶车道', '4', 'http://images.juheapi.com/jztk/c1c2subject1/43.jpg', '这个标志是何含义？', '同向多车道的公路一般都设计了分向行驶车道，正常行驶过程中可以变更车道，当你行驶至路口时要变换到你要行驶的车道。例如要左转弯时要提前变更车道至左转弯车道；要右转弯时要提前变更车道至右转弯车道，不能占用他人行车道，以最大限度的保障通行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563742, '有人看守铁路道口', '无人看守铁路道口', '多股铁路与道路相交', '注意避让火车', '3', 'http://images.juheapi.com/jztk/c1c2subject1/44.jpg', '这个标志是何含义？', '多股铁路与道路相交，则应在铁路道口标志上方设置叉形符号。叉形符号交叉点到警告标志三角形顶点的距离为40cm。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563743, '禁止向右转弯', '禁止掉头', '禁止直行', '禁止向左转弯', '3', 'http://images.juheapi.com/jztk/c1c2subject1/45.jpg', '这个标志是何含义？', '禁止直行：表示前方路口禁止一切车辆直行。此标志设在禁止直行的路口前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563744, '禁止临时停车', '禁止长时停车', '禁止停放车辆', '允许长时停车', '2', 'http://images.juheapi.com/jztk/c1c2subject1/46.jpg', '这个标志是何含义？', '禁止长时停车：禁止车辆长时停放，临时停放不受限制。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563745, '隧道减速', '隧道开远光灯', '隧道开灯', '隧道开示宽灯', '3', 'http://images.juheapi.com/jztk/c1c2subject1/47.jpg', '这个标志是何含义？', '这个应该能看懂吧？很明显那个小车开灯的效果。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563746, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/48.jpg', '驾驶机动车在前方路口不能右转弯。', '如果路口只有圆形的红黄绿灯，那么不管是红灯亮还是绿灯亮都可以右转弯，如果路口是箭头形状，那么向右转的箭头没亮绿灯就不能走了，黄灯也不能走。所以，这题注意看灯的形状。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403416563747, 'Y型交叉口', '主路让行', '注意分流', '注意合流', '4', 'http://images.juheapi.com/jztk/c1c2subject1/49.jpg', '这个标志是何含义？', '黄底为警告标志，所以排除A和B，图很明显的为合流标志，所以选D', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563748, '事故易发路段', '施工路段', '减速慢行路段', '拥堵路段', '1', 'http://images.juheapi.com/jztk/c1c2subject1/50.jpg', '这个标志是何含义？', '事故易发路段：此标志设在交通事故易发路段以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563749, '两侧通行', '右侧通行', '左侧通行', '不准通行', '2', 'http://images.juheapi.com/jztk/c1c2subject1/51.jpg', '这个标志是何含义？', '用于引导行车方向，提示道路使用者前方线形变化，注意谨慎驾驶。设于中央隔离设施端部、渠化设施的端部、桥头等。答题技巧：∧表示俩侧通行，/这个方向的表示左侧通行，这个方向表示右侧通行。交通指路标志大全：http://www.jszks.com/onepage/16752.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563750, '掉头', '倒车', '左转', '绕行', '1', 'http://images.juheapi.com/jztk/c1c2subject1/52.jpg', '这个标志是何含义？', '允许掉头标志', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563751, '指示车辆行进', '警告前方危险', '禁止或限制行为', '告知方向信息', '3', '', '禁令标志的作用是什么？', '禁令标志是交通标志中主要标志的一种，对车辆加以禁止或限制的标志，如禁止通行、禁止停车、禁止左转弯、禁止鸣喇叭、限制速度、限制重量等。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403416563752, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/54.jpg', '驾驶机动车在这种情况下可以右转弯。', '根据《中华人民共和国道路交通安全法》的相关规定；红灯亮时（满屏灯）右转弯车辆在不影响其他车辆、行人的通行条件下第一台车可以通行，第一台车如等候信号停止时第二台车不可以从前车的左或右侧绕行，<b>但是箭头灯红灯亮时右转弯车辆是绝对不可以通行的，必须要等绿红亮时通行。</b> \r<br/>图片中显示的是箭头灯，并且右转箭头灯是红灯，根据规定，机动车在图中这种情况下是不能右转弯的。题目描述有误，因此本题答案为错。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403441729538, '停车让行', '不准临时停车', '不准车辆驶入', '不准长时间停车', '1', 'http://images.juheapi.com/jztk/c1c2subject1/55.jpg', '这个标志是何含义？', '停车让行：表示车辆必须在停止线以外停车了望，确认安全后，才准许通行。停车让行标志在下列情况下设置：(1)与交通流量较大的干路平交的支路路口；(2)无人看守的铁路道口；(3)其他需要设置的地方。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403441729539, '直行和向右转弯', '直行和向左转弯', '禁止直行和向左转弯', '只准向右和向左转弯', '2', 'http://images.juheapi.com/jztk/c1c2subject1/56.jpg', '这个标志是何含义？', '直行和向左转弯：表示只准一切车辆直行和向左转弯。此标志设在车辆必须直行和向左转弯的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403441729540, '40米减速行驶路段', '最低时速40公里', '最高时速40公里', '解除时速40公里限制', '4', 'http://images.juheapi.com/jztk/c1c2subject1/57.jpg', '这个标志是何含义？', '解除时速40公里限制：表示限制速度路段结束。此标志设在限制车辆速度路段的重点。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403441729541, '右侧通行', '左侧通行', '向右行驶', '环岛行驶', '4', 'http://images.juheapi.com/jztk/c1c2subject1/58.jpg', '这个标志是何含义？', '环岛行驶：表示只准车辆靠右环行。此标志设在环岛面向路口来车方向适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403441729542, '临时停靠站', '大客车停靠站', '公交车停靠站', '应急停车带', '3', 'http://images.juheapi.com/jztk/c1c2subject1/59.jpg', '红色圆圈内标线含义是什么？', '都写了公交站了，不许选错了', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403441729543, '车多路段', '慢行', '拥堵路段', '施工路段', '2', 'http://images.juheapi.com/jztk/c1c2subject1/60.jpg', '这个标志是何含义？', '慢行：此标志设在前方需要减速慢行的路段以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403441729544, '有人看守铁路道口', '多股铁路与道路相交', '立交式的铁路道口', '无人看守铁路道口', '1', 'http://images.juheapi.com/jztk/c1c2subject1/61.jpg', '这个标志是何含义？', '有火车形状的，为无人看守的铁路口，此图为有人看守的铁路口。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403441729545, '路面低洼', '驼峰桥', '路面不平', '路面高突', '3', 'http://images.juheapi.com/jztk/c1c2subject1/62.jpg', '这个标志是何含义？', '路面不平：用以提醒车辆驾驶人减速慢行。设在路面颠簸路段或桥头跳车较严重的地点以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403441729546, '不准小型车通行', '只准小型车行驶', '机动车行驶', '禁止小型车行驶', '3', 'http://images.juheapi.com/jztk/c1c2subject1/63.jpg', '这个标志是何含义？', '看到小汽车你应该想到的是机动车行驶。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403441729547, '环行交叉路口预告', '十字交叉路口预告', 'Y型交叉路口预告', '丁字交叉路口预告', '3', 'http://images.juheapi.com/jztk/c1c2subject1/64.jpg', '这个标志是何含义？', '俩个箭头加上你来的道路形成一个Y字。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403462701058, '非机动车道', '摩托车专用道', '电瓶车专用道', '自行车专用道', '1', 'http://images.juheapi.com/jztk/c1c2subject1/65.jpg', '这个路面标记是何含义？', '这是非机动车（就是不烧油的）不要把四轮的开到他们的道上', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895361, '易滑路段', '急转弯路', '试车路段', '曲线路段', '1', 'http://images.juheapi.com/jztk/c1c2subject1/66.jpg', '这个标志是何含义？', '易滑路段：此标志设在路面的摩擦系数不能满足相应行驶速度下要求紧急刹车距离的路段前适当位置。行驶至此路段必须减速慢行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895362, '两侧通行', '不准通行', '左侧通行', '右侧通行', '1', 'http://images.juheapi.com/jztk/c1c2subject1/67.jpg', '这个标志是何含义？', '用于引导行车方向，提示道路使用者前方线形变化，注意谨慎驾驶。设于中央隔离设施端部、渠化设施的端部、桥头等。∧表示俩侧通行，/这个方向的表示左侧通行，这个方向表示右侧通行。交通指路标志大全：http://www.jszks.com/onepage/16752.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895363, '隧道出口距离', '隧道入口距离', '隧道跟车距离', '隧道总长度', '1', 'http://images.juheapi.com/jztk/c1c2subject1/68.jpg', '这个标志是何含义？', '用于指示到前方隧道出口的距离。设在长度超过3 000 m的特长隧道内，从距离隧道出口2 000m处开始每500 m设置一块，直至隧道出口。该标志一般设置在隧道侧壁上。不要纠结选D，正确答案就是A，牢记吧。交通指路标志大全：http://www.jszks.com/onepage/16752.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895364, '高速公路右侧出口预告', '高速公路下一出口预告', '高速公路地点、方向预告', '高速公路左侧出口预告', '3', 'http://images.juheapi.com/jztk/c1c2subject1/69.jpg', '这个标志是何含义？', '高速公路地点、方向预告：用于指示高速公路或城市快速路两个行驶方向，设在驶入高速公路或城市快速路的匝道分岔点处。在目的地信息之上，可增加前往高速公路的编号信息。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895365, '高速公路下一出口预告', '高速公路右侧出口预告', '高速公路左侧出口预告', '高速公路目的地预告', '3', 'http://images.juheapi.com/jztk/c1c2subject1/70.jpg', '这个标志是何含义？', '高速公路左侧出口预告：用于预告前方出口，在距离高速公路或城市快速路减速车道的渐变段起点处。出口预告标志应同时附着出口编号标志。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895366, '掉头车道', '绕行车道', '分向车道', '左转车道', '1', 'http://images.juheapi.com/jztk/c1c2subject1/71.jpg', '这个标志是何含义？', '常识题。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895367, '正确', '错误', '', '', '1', '', '机动车号牌损毁，机动车所有人要向登记地车辆管理所申请补领、换领。', '机动车号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向登记地车辆管理所申请补领、换领。申请时，机动车所有人应当填写申请表并提交身份证明。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403466895368, '正确', '错误', '', '', '1', '', '绿灯亮表示前方路口允许机动车通行。', '红灯停，绿灯行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403466895369, '道路施工提示标线', '车行道横向减速标线', '车行道纵向减速标线', '车道变少提示标线', '2', 'http://images.juheapi.com/jztk/c1c2subject1/74.jpg', '路面上的白色标线是何含义？', '<p><img src=http://nimg.cysq.com/yxlimg/20a43532.jpg>\r<br/>横向减速标线\r<br/><img src=http://nimg.cysq.com/yxlimg/2ec73df9.jpg>\r<br/>纵向减速标线</p>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895370, '高速公路报警电话', '高速公路公用电话', '高速公路紧急电话', '高速公路救援电话', '4', 'http://images.juheapi.com/jztk/c1c2subject1/75.jpg', '这个标志是何含义？', '高速公路救援电话：用于指示救援电话号码，没有设置紧急电话的高速公路上应设置救援电话标志。标志上号码为示例。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895371, '大型客车专用车道', '多乘员车专用车道', '公交车专用车道', 'BRT车辆专用车道', '4', 'http://images.juheapi.com/jztk/c1c2subject1/76.jpg', '这个标志是何含义？', '此图为BRT车辆专用车道，与公交车专用车道的区别是多了“快速公交”四个字。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895372, '傍山险路', '悬崖路段', '注意落石', '危险路段', '3', 'http://images.juheapi.com/jztk/c1c2subject1/77.jpg', '这个标志是何含义？', '注意落石：此标志设在左侧有落石危险的傍山路段之前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403466895373, '正确', '错误', '', '', '1', '', '驾驶机动车不按照规定避让校车的，一次记6分。', '根据规定，驾驶机动车不按照规定避让校车的，一次记6分 。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403471089666, '接近移动障碍物标线', '远离狭窄路面标线', '接近障碍物标线', '接近狭窄路面标线', '3', 'http://images.juheapi.com/jztk/c1c2subject1/79.jpg', '路面上的黄色填充标线是何含义？', '双黄线就是警告注意越界，“打叉的物体”就是不可移动障碍物。因此选接近障碍物标线。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403471089667, '指示前方道路是Y型交叉口', '指示前方道路是分离式道路', '指示前方道路仅可左右转弯', '指示前方道路需向左右合流', '3', 'http://images.juheapi.com/jztk/c1c2subject1/80.jpg', '路面上导向箭头是何含义？', '可以左转也可以右转，三叉路口', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403471089668, '高速公路救援电话号码', '高速公路服务电话号码', '高速公路报警电话号码', '高速公路交通广播频率', '4', 'http://images.juheapi.com/jztk/c1c2subject1/81.jpg', '这个标志是何含义？', '高速公路交通广播频率：用以指示收听高速公路或城市快速路交通信息广播的频率，可在适当地点设置，根据需要可重复设置。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403475283969, '会车时停车让右侧车先行', '右侧道路禁止车通行', '前方是双向通行路段', '会车时停车让对方车先行', '4', 'http://images.juheapi.com/jztk/c1c2subject1/82.jpg', '这个标志是何含义？', '会车时停车让对方车先行：表示车辆会车时，必须停车让对方车先行。设置在会车有困难的狭窄路段的一端或由于某种原因只能开放一条车道作双向通行路段的一端。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403483672577, '高速公路停车区预告', '高速公路避险处预告', '高速公路停车场预告', '高速公路服务区预告', '1', 'http://images.juheapi.com/jztk/c1c2subject1/83.jpg', '这个标志是何含义？', '高速公路停车区是有茶杯的，高速公路停车场是只有一个P。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403483672578, '处管制，并处罚金', '处2年以上徒刑', '处拘役，并处罚金', '处2年以下徒刑', '3', '', '醉酒驾驶机动车在道路上行驶会受到什么处罚？', '按照《道路交通安全法》规定：醉酒驾驶机动车辆，吊销驾照，5年内不得重新获取驾照，经过判决后处以拘役，并处罚金。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403483672579, '禁止鸣高音喇叭', '禁止鸣低音喇叭', '应当鸣喇叭', '禁止鸣喇叭', '3', 'http://images.juheapi.com/jztk/c1c2subject1/85.jpg', '这个标志是何含义？', '表示机动车行至该标志处必须鸣喇叭。此标志设在公路的急转弯处、陡坡等视线不良路段的起点。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403483672580, '左转让行', '直行单行路', '向右单行路', '向左单行路', '4', 'http://images.juheapi.com/jztk/c1c2subject1/86.jpg', '这个标志是何含义？', '表示一切车辆向左单向行驶。此标志设在单行路的路口和入口处的适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403483672581, '平均速度为100公里/小时', '最低限速为100公里/小时', '解除100公里/小时限速', '最高限速为100公里/小时', '4', 'http://images.juheapi.com/jztk/c1c2subject1/87.jpg', '这个路面标记是何含义？', '黄色是警示你别超了，一越你就黄了', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403483672582, '禁止向左转弯', '向左和向右转弯', '禁止向左右转弯', '禁止向右转弯', '2', 'http://images.juheapi.com/jztk/c1c2subject1/88.jpg', '这个标志是何含义？', '向左和向右转弯：表示只准一切车辆向左和向右转弯。此标志设在车辆必须向左和向右转弯的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403487866881, '仅允许上下人员', '仅允许装卸货物', '禁止长时间停车', '禁止停放车辆', '4', 'http://images.juheapi.com/jztk/c1c2subject1/89.jpg', '路缘石上的黄色实线是何含义？', '黄色实线表示禁止停放车辆，黄色虚线表示禁止长时间停车。题目图片是黄色实线，因此表示禁止停放车辆。选项D正确，答案选D。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403487866882, '路面不平', '路面高突', '路面低洼', '驼峰桥', '2', 'http://images.juheapi.com/jztk/c1c2subject1/90.jpg', '这个标志是何含义？', '路面高突：用以提醒车辆驾驶人减速慢行。设在路面突然高突以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403487866883, '露天停车场', '紧急停车带', '停车位', '错车道', '4', 'http://images.juheapi.com/jztk/c1c2subject1/91.jpg', '这个标志是何含义？', '错车道指的是在单车道道路上，可通视的一定距离内，供车辆交错避让用的一段加宽车道。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403487866884, '非机动车引导线', '路口导向线', '车道连接线', '小型车转弯线', '2', 'http://images.juheapi.com/jztk/c1c2subject1/92.jpg', '图中圈内黄色虚线是什么标线？', '黄色虚线 划于路段中时，用以分隔同向行驶的交通流或作为行车安全距离识别线；划于路口时，用以引导车辆行进。\r<br/>例如：当我方车道上的车辆与对面道路上的车同时左转弯时，一律需要在线内行驶，保证有足够的安全距离。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403487866885, '直行和左转弯行驶', '直行和右转弯行驶', '立体交叉直行和右转弯行驶', '立体交叉直行和左转弯行驶', '3', 'http://images.juheapi.com/jztk/c1c2subject1/93.jpg', '这个标志是何含义？', '立体交叉直行和右转弯行驶：表示车辆在立交处可以直行和按图示路线右转弯行驶。此标志设在立交右转弯出口处适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403487866886, '禁止通行', '指示通行', '限制通行', '警告提醒', '2', '', '指示标线的作用是什么？', '指示标线是指示通行的，具体的红色标志表示禁止、限制；黄色标志表示警告。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403487866887, '十字交叉路口', '环行交叉路口', 'T型交叉路口', 'Y型交叉路口', '1', 'http://images.juheapi.com/jztk/c1c2subject1/95.jpg', '这个标志是何含义？', '十字交叉路口：除了基本形十字路口外，还有部分变异的十字路口，如：五路交叉路口、变形十字路口、变形五路交叉路口等。五路以上的路口均按十字路口对待。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403487866888, '小型车转弯线', '车道连接线', '非机动车引导线', '路口导向线', '4', 'http://images.juheapi.com/jztk/c1c2subject1/96.jpg', '图中圈内白色虚线是什么标线？', '白色虚线划于路段中时，用以分隔同向行驶的交通流或作为行车安全距离识别线；划于路口时，用以引导车辆行进。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403487866889, '道路入口标线', '可跨越式分道线', '道路入口减速线', '道路出口标线', '1', 'http://images.juheapi.com/jztk/c1c2subject1/97.jpg', '路面上白色虚线和三角地带标线组成的是什么标线？', '<p><img src=http://tp.mnks.cn/tp_303.jpg>\r<br/>道路出口标线\r<br/><img src=http://tp.mnks.cn/tp_304.jpg>\r<br/>道路入口标线</p>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403492061186, '正确', '错误', '', '', '2', '', '驾驶人违反交通运输管理法规发生重大事故后，逃逸或者有其他特别恶劣情节的，处7年以上有期徒刑。', '《刑法》第一百三十三条规定：\r<br/>1、违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；\r<br/>2、<b>交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；</b>\r<br/>3、因逃逸致人死亡的，处七年以上有期徒刑。\r<br/>根据规定， 题目中的情况应该判处 <b>[ 三年以上七年以下有期徒刑 ]</b> ，并非七年以上有期徒刑。题目描述错误，因此答案为错。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403492061187, '单侧可跨越同向车道分界线', '禁止跨越同向车行道分界线', '双侧可跨越同向车道分界线', '禁止跨越对向车行道分界线', '2', 'http://images.juheapi.com/jztk/c1c2subject1/99.jpg', '路中心白色实线是何含义？', '图中两车道明显为同方向，而且实线不能压，因此路中心白色实线是禁止跨越同向车行道分界线。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403492061188, '人行横道预告', '交叉路口预告', '减速让行预告', '停车让行预告', '1', 'http://images.juheapi.com/jztk/c1c2subject1/100.jpg', '这个地面标记是什么标线？', '这种标线在到达人行横道前的道路30-50米处设置，它的作用是用来提示驾驶员，前方已接近人行横道，应减速慢行，并须注意行人横过马路。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403496255490, '正确', '错误', '', '', '2', '', '使用其他机动车号牌、行驶证的一次记3分。', '机动车驾驶人有下列违法行为之一，一次记12分：<br/>（一）驾驶与准驾车型不符的机动车的；<br/>（二）饮酒后驾驶机动车的；<br/>（三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数20％以上的；<br/>（四）造成交通事故后逃逸，尚不构成犯罪的；<br/>（五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的；<br/><b>（六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；</b><br/>（七）驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的；<br/>（八）驾驶营运客车在高速公路车道内停车的；<br/>（九）驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的；<br/>（十）连续驾驶中型以上载客汽车、危险物品运输车辆超过4小时未停车休息或者停车休息时间少于20分钟的；', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403496255491, '发动机易熄火', '易发生侧滑', '能见度低，视线不清', '行驶阻力增大', '3', '', '雾天对安全行车的主要影响是什么？', '雾天的能见度很低，容易造成视野不良。而易发生侧滑的应该是滑溜的路面，雾天与行驶阻力大和发动机熄火并无直接关联。因此本题选D', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403496255492, '保持车距标记', '最小间距标记', '速度限制标记', '道路编号标记', '3', 'http://images.juheapi.com/jztk/c1c2subject1/103.jpg', '这个路面数字标记是何含义？', '<p><img src=http://tp.mnks.cn/tp_321.jpg>\r<br/>限制速度\r<br/><img src=http://tp.mnks.cn/tp_322.jpg>\r<br/>限制最高速度\r<br/><img src=http://tp.mnks.cn/tp_323.jpg>\r<br/>限制最低速度</p>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403496255493, '直行或向右转弯', '向左转弯', '直行或向左转弯', '向左、向右转弯', '1', 'http://images.juheapi.com/jztk/c1c2subject1/104.jpg', '这个路口允许车辆怎样行驶？', '请看灯，直行和右转灯是绿的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403496255494, '加速进入两侧车道行驶', '进入右侧车道行驶', '减速进入两侧车道行驶', '禁止车辆在两侧车道通行', '4', 'http://images.juheapi.com/jztk/c1c2subject1/105.jpg', '遇到这种情况时怎样行驶？', '两侧车道都是红灯，都不可以通行，所以是禁止车辆在两侧车道通行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403496255495, '高速公路右侧出口预告', '高速公路目的地预告', '高速公路左侧出口预告', '高速公路下一出口预告', '1', 'http://images.juheapi.com/jztk/c1c2subject1/106.jpg', '这个标志是何含义？', '高速公路右侧出口预告：用于预告前方出口，设置在距离高速公路或城市快速路减速车道的渐变段起点处。出口预告标志应同时附着出口编号标志。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403496255496, '向左单行路', '向右单行路', '直行单行路', '右转让行', '2', 'http://images.juheapi.com/jztk/c1c2subject1/107.jpg', '这个标志是何含义？', '表示一切车辆向右单向行驶。此标志设在单行路的路口和入口处的适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403496255497, '正确', '错误', '', '', '1', '', '驾驶机动车发生财产损失交通事故后，当事人对事实及成因无争议移动车辆时需要对现场拍照或者标划停车位置。', '《道路交通事故处理程序规定》第十三条：机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。<b>车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。</b>非机动车与非机动车或者行人发生财产损失事故，基本事实及成因清楚的，当事人应当先撤离现场，再协商处理损害赔偿事宜。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403496255498, '正确', '错误', '', '', '2', '', '交通警察对未放置保险标志上道路行驶的车辆可依法扣留行驶证。', '《道路交通安全法》第九十五条：<b>上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。</b>当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。\r<br/>根据规定，对于不放置保险标志上路的处罚，扣留的是机动车，并不会扣留行驶证。因此题目描述有误，本题答案应为错误。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403496255499, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/110.jpg', '这辆红色轿车可以在该车道行驶。', '中间红灯表示中间车道禁行。因此红色轿车不可以在该车道行驶。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403496255500, '禁止跨越对向车道中心线', '限制跨越对向车道中心线', '单向行驶车道分界中心线', '可跨越同向车道中心线', '4', 'http://images.juheapi.com/jztk/c1c2subject1/111.jpg', '路中白色虚线是什么标线？', '白色表示同向两车道之间的标线，虚线可跨越超车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449794, '车辆可临时越线行驶', '车辆禁止越线行驶', '应急车道分界线', '人行横道分界线', '1', 'http://images.juheapi.com/jztk/c1c2subject1/112.jpg', '路右侧车行道边缘白色虚线是什么含义？', '<p><img src=http://tp.mnks.cn/tp_292.jpg>\r<br/>禁止跨越白色实线\r<br/><img src=http://tp.mnks.cn/tp_293.jpg>\r<br/>允许跨越白色虚线</p>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449795, 'Y型交叉路口', 'T型交叉路口', '十字交叉路口', '环行交叉路口', '2', 'http://images.juheapi.com/jztk/c1c2subject1/113.jpg', '这个标志是何含义？', 'T型交叉路口：丁字形标志原则上设在与交叉口形状相符的道路上。右侧丁字路口，此标志设在进入T字路口以前的适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449796, '车辆可临时跨越', '禁止车辆跨越', '机动车可临时跨越', '非机动车可临时跨越', '2', 'http://images.juheapi.com/jztk/c1c2subject1/114.jpg', '路两侧的车行道边缘白色实线是什么含义？', '<p><img src=http://tp.mnks.cn/tp_292.jpg><br/>禁止跨越白色实线</p><p><img src=http://tp.mnks.cn/tp_293.jpg><br/>允许跨越白色虚线</p>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449797, '直行或向右转弯', '向右转弯', '向左转弯', '直行', '3', 'http://images.juheapi.com/jztk/c1c2subject1/115.jpg', '这个路口允许车辆怎样行驶？', '左转的灯是绿色，所以可以向左转弯。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449798, '电器设备易受潮短路', '能见度降低，视野模糊', '行驶阻力增大', '制动性能差，方向易跑偏', '4', '', '冰雪道路对安全行车的主要影响是什么？', '冰雪道路，因汽车轮胎与路面的摩擦系数减小、附着力大大降低，汽车驱动轮很容易打滑或空转，尤其是上坡、起步、停车时还会出现后溜车的现象；车辆在行驶中如果突然加速或减速，很容易造成侧滑及方向跑偏现象；遇情况紧急制动时，制动距离会大大延长。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449799, '车距确认线', '减速行驶线', '车速确认线', '路口减速线', '1', 'http://images.juheapi.com/jztk/c1c2subject1/117.jpg', '图中圈内的白色折线是什么标线？', '白色平行粗实线，用以提供车辆驾驶人保持行车安全距离的参考，与车距确认标志配合使用。设在常发生超车、易肇事或其他有需要路段。高速公路车距确认标线作用为，见此标线时，驾驶人应与前车保持行车安全距离，避免发生交通事故。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449800, '中心圈', '导流线', '网状线', '停车区', '3', 'http://images.juheapi.com/jztk/c1c2subject1/118.jpg', '图中路口中央黄色路面标记是什么标线？', '路面黄色网状线表示严格禁止一切车辆长时或临时停车，防止交通阻塞。当黄色网状线前方有车辆停驶时，后车必须在黄色网状线外等候，直到确认黄色网状线前方有足够空间停驶本车时，方可驶过黄色网状线。 另有黄色网状线是施画在重要国家机关、军队、大型企事业单位和公交枢纽门前，确保车辆进出时不致被门前拥堵车辆堵住的措施。网状线是黄色线，停车时禁止占用。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449801, '堤坝路', '临崖路', '傍水路', '易滑路', '1', 'http://images.juheapi.com/jztk/c1c2subject1/119.jpg', '这个标志是何含义？', '堤坝路：用以提醒车辆驾驶人小心驾驶，设在沿水库、湖泊、河流等堤坝道路以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449802, '路面附着力增大', '无法观察到暗坑和凸起的路面', '能见度低，视野模糊', '日光反射阻挡视线', '2', '', '水淹路面影响行车安全，不易通行的原因是什么？', '因为水淹没了路面，所以看不清是暗坑和凸起的路面。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449803, '正确', '错误', '', '', '1', '', '夜间行车，驾驶人视距变短，影响观察，同时注意力高度集中，易产生疲劳。', '高度集中注意力，比较费神，时间久了就会疲倦。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403500449804, '指示前方可左转或掉头', '指示前方可直行或左转', '指示前方直行向左变道', '指示前方可直行或掉头', '4', 'http://images.juheapi.com/jztk/c1c2subject1/122.jpg', '这个导向箭头是何含义？', '直行或者掉头都可以', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449805, '营运客车专用车道', '大客车专用车道', '出租车专用车道', '公交专用车道', '4', 'http://images.juheapi.com/jztk/c1c2subject1/123.jpg', '图中圈内两条黄色虚线间的区域是何含义？', '黄色虚线中的车道里有写字，黄色虚线是想突出引动驾驶员注意的，因此本题是公交专用车道。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449806, '减速让行', '潮汐车道', '分离式道路', '双向交通', '4', 'http://images.juheapi.com/jztk/c1c2subject1/124.jpg', '这个标志是何含义？', '双向交通：用以提醒车辆驾驶人注意会车。设在由双向分离行驶，因某种原因出现临时性或永久性的不分离双向行驶的路段，或由单向行驶进人双向行驶的路段以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449807, '人行横道', '学生通道', '注意行人', '儿童通道', '1', 'http://images.juheapi.com/jztk/c1c2subject1/125.jpg', '这个标志是何含义？', '表示该处为专供行人横穿马路的通道。此标志设在人行横道的两侧。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449808, '高速公路服务区预告', '高速公路避险处预告', '高速公路客车站预告', '高速公路停车场预告', '4', 'http://images.juheapi.com/jztk/c1c2subject1/126.jpg', '这个标志预告什么？', '<p><img src=http://tp.mnks.cn/tp_283.jpg>\r<br/>高速公路停车区\r<br/><img src=http://tp.mnks.cn/tp_284.jpg>\r<br/>高速公路停车场\r<br/><img src=http://tp.mnks.cn/tp_282.jpg>\r<br/>高速公路服务区预告</p>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449809, '正确', '错误', '', '', '1', '', '驾驶人记分没有达到满分，有罚款尚未缴纳的，记分转入下一记分周期。', '《中华人民共和国道路交通安全法实施条例》第二十四条：机动车驾驶人在一个记分周期内记分未达到12分，所处罚款已经缴纳的，记分予以清除；记分虽未达到12分，但尚有罚款未缴纳的，记分转入下一记分周期。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403500449810, '正确', '错误', '', '', '2', '', '黄灯持续闪烁，表示机动车可以加速通过。', '黄灯持续闪烁，表明已经越过停止线的车辆可以行使，没越过停止线的车辆要停车等待，机动车不能加速通过。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403500449811, '2分', '3分', '12分', '6分', '3', '', '使用伪造、变造的机动车号牌一次记几分？', '《公安部令第123号》规定，使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的，一次记12分 。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449812, '减速慢行信号', '靠边停车信号', '停止信号', '右转弯信号', '4', 'http://images.juheapi.com/jztk/c1c2subject1/130.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449813, '省道编号', '县道编号', '乡道编号', '国道编号', '3', 'http://images.juheapi.com/jztk/c1c2subject1/131.jpg', '这个标志是何含义？', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底的，国道是红底的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449814, '右转弯信号', '减速慢行信号', '靠边停车信号', '变道信号', '4', 'http://images.juheapi.com/jztk/c1c2subject1/132.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449815, '向右变道', '分流处', '路面变宽', '车道数增加', '4', 'http://images.juheapi.com/jztk/c1c2subject1/133.jpg', '这个标志是何含义？', '俩个车道变成三个，表示车道数增加。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403500449816, '指示向左变道', '指示前方直行', '指示前方左转弯', '指示前方右转弯', '3', 'http://images.juheapi.com/jztk/c1c2subject1/134.jpg', '这个导向箭头是何含义？', '请记住了哦，不难的吧', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403504644097, '国界', '边防', '边界', '海关', '4', 'http://images.juheapi.com/jztk/c1c2subject1/135.jpg', '这个标志是何含义？', '海关那俩个大字那么清晰你再不选D，D得有多伤心。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403504644098, '在火车到来前通过', '不得越过停止线', '边观察边缓慢通过', '不换挡加速通过', '2', 'http://images.juheapi.com/jztk/c1c2subject1/136.jpg', '驾驶机动车在铁路道口看到这种信号灯时怎样行驶？', '在铁路道口，红灯已亮，表示可能有火车驶来，此时绝对禁止通行，不得越过停止线。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403504644099, '立体交叉直行和左转弯行驶', '立体交叉直行和右转弯行驶', '直行和左转弯行驶', '直行和右转弯行驶', '1', 'http://images.juheapi.com/jztk/c1c2subject1/137.jpg', '这个标志是何含义？', '立体交叉直行和左转弯行驶：表示车辆在立交处可以直行和按图示路线左转弯行驶。此标志设在立交左转弯出口处适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403504644100, '指示向左变道', '指示前方直行', '指示前方掉头', '指示前方右转', '3', 'http://images.juheapi.com/jztk/c1c2subject1/138.jpg', '这个导向箭头是何含义？', '请记住了 so easy!!', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403504644101, '驾驶人体力下降', '驾驶人易产生冲动、幻觉', '能见度低、不利于观察道路交通情况', '路面复杂多变', '3', '', '夜间道路环境对安全行车的主要影响是什么？', '<b>夜间行车视线差，在没有照明的路段，驾驶人视线普遍受到影响，不能清楚看到前方事物，或者不能看清和判断前方事物的距离，尤其是在会车的情况下，由于受对向车辆灯光的影响，会产生视觉的盲点，导致看不清或看不见前方事物，夜间特大道路交通事故明显高于白天。</b>夜间行车由于不能见到道路两旁的景观，对驾驶员兴奋性刺激物小，因此最易产生驾驶疲劳，如稍有感觉就应振作精神或停车休息片刻。因此本题选A。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403504644102, '内部停车场', '专用停车场', '露天停车场', '室内停车场', '3', 'http://images.juheapi.com/jztk/c1c2subject1/140.jpg', '这个标志是何含义？', '如果P字上边没有遮挡就是露天停车场。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403513032705, '直行车道', '只准直行', '单行路', '禁止直行', '2', 'http://images.juheapi.com/jztk/c1c2subject1/141.jpg', '这个标志是何含义？', '只准直行：表示只准一切车辆直行。此标志设在直行的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403513032706, '驼峰桥', '路面高突', '路面低洼', '不平路面', '1', 'http://images.juheapi.com/jztk/c1c2subject1/142.jpg', '这个标志是何含义？', '驼峰桥：用以提醒车辆驾驶人谨慎驾驶。设在拱度很大，影响视距的驼峰桥以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403513032707, '禁止在路口掉头', '禁止向左向右变道', '禁止向左向右转弯', '禁止车辆直行', '3', 'http://images.juheapi.com/jztk/c1c2subject1/143.jpg', '这个标志是何含义？', '禁止向左向右转弯：表示前方路口禁止一切车辆向左向右转弯。此标志设在禁止向左向右转弯的路口前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403513032708, '各种车辆', '小型客车', '中型客车', '小型货车', '2', 'http://images.juheapi.com/jztk/c1c2subject1/144.jpg', '这个标志提示哪种车型禁止通行？', '此标志表示禁止小型客车驶入。轿车属于小型客车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403513032709, '最低限速50公里/小时', '高度限速50公里/小时', '水平高度50米', '海拔高度50米', '1', 'http://images.juheapi.com/jztk/c1c2subject1/145.jpg', '这个标志是何含义？', '表示机动车驶入前方道路之最低时速限制。此标志设在高速公路或其他道路限速路段的起点。高度限速标志是白底红字。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403513032710, '避险车道', '应急车道', '路肩', '急弯道', '1', 'http://images.juheapi.com/jztk/c1c2subject1/146.jpg', '这个标志是何含义？', '避险车道是指在长陡下坡路段行车道外侧增设的供速度失控车辆驶离正线安全减速的专用车道。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403513032711, '应急避难场所', '生活服务区', '行人专用通道', '横过道路设施', '1', 'http://images.juheapi.com/jztk/c1c2subject1/147.jpg', '这个标志是何含义？', '你看他的动作时在跑，所以是紧急的情况，所以答案是A。应急避难场所是应对突发公共事件的一项灾民安置措施，是现代化大城市用于民众躲避火灾、爆炸、洪水、地震、疫情等重大突发公共事件的安全避难场所。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403513032712, '禁止车辆掉头', '禁止向左变道', '禁止向左转弯', '禁止驶入左车道', '3', 'http://images.juheapi.com/jztk/c1c2subject1/148.jpg', '这个标志是何含义？', '禁止向左转弯：表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403513032713, '右转车道', '掉头车道', '左转车道', '分向车道', '3', 'http://images.juheapi.com/jztk/c1c2subject1/149.jpg', '这个标志是何含义？', '表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403513032714, '正确', '错误', '', '', '2', '', '初次申领驾驶证的驾驶人在实习期内可以单独驾驶机动车上高速公路行驶。', '《公安部令第123号》第六十五条：<b>驾驶人在实习期内驾驶机动车上高速公路行驶，应当由持相应或者更高准驾车型驾驶证三年以上的驾驶人陪同。</b>其中，驾驶残疾人专用小型自动挡载客汽车的，可以由持有小型自动挡载客汽车以上准驾车型驾驶证的驾驶人陪同。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403513032715, '正确', '错误', '', '', '1', '', '驾驶人将机动车交给驾驶证被吊销的人驾驶的，交通警察依法扣留驾驶证。', '六种情形下可以扣留驾驶证 新法规定，有下列情形之一的，可以扣留机动车驾驶证： <br/>(一)饮酒、醉酒后驾驶机动车的； <br/>(二)<b>机动车驾驶人将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的；<br/></b> (三)机动车行驶超过规定时速百分之五十的； <br/>(四)驾驶拼装或者已达到报废标准的机动车的； <br/>(五)发生重大交通事故，构成犯罪的； <br/>(六)在一个记分周期内累积记分达到12分的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403517227010, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/c1c2subject1/152.jpg', '驾驶机动车遇到这种信号灯亮时，如果已越过停止线，可以继续通行。', '《道路交通安全法实施条例》第三十八条：黄灯亮时，已越过停止线的车辆可以继续通行；未经过停止线的则禁止继续通行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403517227011, '3年', '5年', '1年', '2年', '1', '', '驾驶人因服兵役、出国（境）等原因无法办理审验时，延期审验期限最长不超过多长时间？', '《机动车驾驶证申领和使用规定》第五十条：机动车驾驶人因服兵役、出国（境）等原因，无法在规定时间内办理驾驶证期满换证、提交身体条件证明的，可以向机动车驾驶证核发地车辆管理所申请延期办理，<b>延期期限最长不超过三年</b>。延期期间机动车驾驶人不得驾驶机动车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403517227012, '左转弯待转信号', '靠边停车信号', '减速慢行信号', '左转弯信号', '4', 'http://images.juheapi.com/jztk/c1c2subject1/154.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403517227013, '行驶阻力变小', '车轮极易滑转和侧滑', '能见度低，视野模糊', '路面附着力增大', '2', '', '泥泞道路对安全行车的主要影响是什么？', '泥泞道路，泥土附着在轮胎上，轮胎和地面的摩擦减小，附着力也随之减小，车轮容易发生滑转和侧滑；同时因为道路状况变差，行驶阻力也变大。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403517227014, '道路施工提示标线', '车行道纵向减速标线', '车行道横向减速标线', '车道变少提示标线', '2', 'http://images.juheapi.com/jztk/c1c2subject1/156.jpg', '路面上的菱形块虚线是何含义？', '<p><img src=http://nimg.cysq.com/yxlimg/20a43532.jpg>\r<br/>横向减速标线\r<br/><img src=http://nimg.cysq.com/yxlimg/2ec73df9.jpg>\r<br/>纵向减速标线</p>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403517227015, '正确', '错误', '', '', '2', '', '行车中突遇对向车辆强行超车，占据自己车道时，可不予避让，迫使对方让路。', '行车要始终记得安全文明驾驶，对方要超车，应及时减速避让。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403517227016, '停车线', '减速线', '导流线', '网状线', '3', 'http://images.juheapi.com/jztk/c1c2subject1/158.jpg', '图中圈内三角填充区域是什么标线？', '导流线的形式主要为一个或几个根据路口地形设置的白色V形线或斜纹线区域，表示车辆必须按规定的路线行驶，不得压线或越线行驶。 主要用于过宽、不规则或行驶条件比较复杂的交叉路口,立体交叉的匝道口或其他特殊地点。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403517227017, '不变', '无规律', '变长', '变短', '4', '', '夜间驾驶人对物体的观察明显比白天差，视距会有什么变化？', '夜晚，在路上行车，由于车灯作用，看到的是黑色背景中的亮色物体，所以亮色物体偏大，而眼睛依旧以近大远小的感觉判断，故视距减小。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403517227018, '机动车驾驶证被暂扣期间驾驶机动车的', '以隐瞒、欺骗手段补领机动车驾驶证的', '驾驶机动车不按照规定避让校车的', '驾驶故意污损号牌的机动车上道路行驶', '4', '', '有下列哪种违法行为的机动车驾驶人将被一次记12分？', '《公安部令第123号》规定，上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的，一次记12分 。其余均为6分。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403517227019, '待转线', '减速线', '让行线', '停止线', '4', 'http://images.juheapi.com/jztk/c1c2subject1/161.jpg', '图中圈内白色横实线是何含义？', '<p><img src=http://tp.mnks.cn/tp_332.jpg>\r<br/>停止线\r<br/><img src=http://tp.mnks.cn/tp_333.jpg>\r<br/>停车让行线\r<br/><img src=http://tp.mnks.cn/tp_334.jpg>\r<br/>减速让行线</p>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403517227020, '左转弯待转信号', '靠边停车信号', '右转弯信号', '减速慢行信号', '3', 'http://images.juheapi.com/jztk/c1c2subject1/162.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403517227021, '指示标线', '禁止标线', '警告标志', '辅助标线', '1', 'http://images.juheapi.com/jztk/c1c2subject1/163.jpg', '路中心黄色虚线属于哪一类标线？', '根据GB 5768.3-2009《道路交通标志和标线第3部分：道路交通标线》的规定：\r<br/>黄色虚线，划于路段中时，用以分隔对向行驶的交通流。\r<br/>起到指示的作用，属于指示标线。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403534004225, '正确', '错误', '', '', '1', '', '在狭窄的路段会车时，应做到礼让三先：先慢、先让、先停。', '文明驾驶，礼貌让行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403534004226, '超过规定速度10%', '疲劳后驾驶机动车', '行车中未系安全带', '饮酒后驾驶机动车', '4', '', '驾驶人有哪种情形，交通警察可依法扣留机动车驾驶证？', '六种情形下可以扣留驾驶证。新法规定，有下列情形之一的，可以扣留机动车驾驶证：<br/> <b> (一)饮酒、醉酒后驾驶机动车的；</b>  <br/>(二)机动车驾驶人将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的；  <br/>(三)机动车行驶超过规定时速百分之五十的； <br/>(四)驾驶拼装或者已达到报废标准的机动车的；  <br/>(五)发生重大交通事故，构成犯罪的；  <br/>(六)在一个记分周期内累积记分达到12分的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403534004227, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/c1c2subject1/166.jpg', '在铁路道口遇到两个红灯交替闪烁时要停车等待。', '在铁路道口，一个红灯亮或两个红灯闪烁时都是禁止通行的，要停车等待。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403534004228, '旅游区距离', '旅游区方向', '旅游区符号', '旅游区类别', '1', 'http://images.juheapi.com/jztk/c1c2subject1/167.jpg', '这个标志是何含义？', '遇此类标志是指旅游区的距离还有多远（亲不要激动 安全第一 慢行', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403534004229, '正确', '错误', '', '', '1', '', '伪造、变造或者使用伪造、变造驾驶证的驾驶人构成犯罪的，将依法追究刑事责任。', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403534004230, '30日', '40日', '50日', '60日', '1', '', '驾驶证记载的驾驶人信息发生变化的要在多长时间内申请换证？', '《公安部令第123号》第五十一条：<br/>具有下列情形之一的，机动车驾驶人应当在三十日内到机动车驾驶证核发地车辆管理所申请换证：<br/><b>（一）在车辆管理所管辖区域内，机动车驾驶证记载的机动车驾驶人信息发生变化的；</b><br/>（二）机动车驾驶证损毁无法辨认的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403534004231, '禁止掉头', '禁止左转', '禁止右转', '禁止直行', '3', 'http://images.juheapi.com/jztk/c1c2subject1/170.jpg', '路面上的黄色标记是何含义？', '一个X加上一个右转，加一起就是禁止右转，爆弱了', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403538198529, '双向分道线', '潮汐车道线', '可跨越分道线', '单向分道线', '2', 'http://images.juheapi.com/jztk/c1c2subject1/171.jpg', '路中两条双黄色虚线是什么标线？', '路中两条双黄色虚线是潮汐车道线。\r<br/>“潮汐车道”是指根据早晚交通流量不同情况，对有条件的道路，试点开辟潮汐车道，通过车道灯的指示方向变化，控制主干道车道行驶方向，来调整车道数，提高车道使用效率。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403538198530, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/172.jpg', '驾驶机动车在路口看到这种信号灯亮时，要加速通过。', '《道路交通安全法实施条例》第三十八条：黄灯亮时，已越过停止线的车辆可以继续通行；未经过停止线的则禁止继续通行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403538198531, '尽量绕道躲避', '立即报警，停车观望', '协助保护现场，并立即报警', '加速通过，不予理睬', '3', '', '行车中遇有前方发生交通事故，需要帮助时，应怎样做？', '维护道路交通安全，人人有责。当他人出了事故，请协助保护现场，并立即报警。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403538198532, '禁止直行', '允许掉头', '禁止掉头', '禁止转弯', '3', 'http://images.juheapi.com/jztk/c1c2subject1/174.jpg', '路面上的黄色标记是何含义？', '一个X加上一个向回掉头的标记，加一起就是禁止掉头', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403538198533, '正确', '错误', '', '', '1', '', '对未按照国家规定投保交强险的车辆，交通警察可依法予以扣留。', '《道路交通安全法》第九十八条：机动车所有人、管理人未按照国家规定投保机动车第三者责任强制保险的，由公安机关交通管理部门扣留车辆至依照规定投保后，并处依照规定投保最低责任限额应缴纳的保险费的二倍罚款。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403538198534, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/176.jpg', '驾驶机动车在路口遇到这种信号灯禁止通行。', '仔细看图，图中三个圆形灯从左到右分别是红色、黄色、绿灯。此时绿灯亮起，根据“红灯停，绿灯行”的原则，车辆是可以通行的。题目描述错误，本题答案为错。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403546587138, '路面宽度渐变标线', '车行道变多标线', '接近障碍物标线', '施工路段提示线', '1', 'http://images.juheapi.com/jztk/c1c2subject1/177.jpg', '路面上的黄色标线是何含义？', '左下角有一个标志表示路面变窄，所以黄线就是表示路面宽度有变化。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403546587139, '路面湿滑，视线受阻', '发动机易熄火', '行驶阻力增大', '电器设备易受潮短路', '1', '', '雨天对安全行车的主要影响是什么？', '雨天路面湿滑，雨水会使视线受阻，所以更要注意行车安全。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403546587140, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/179.jpg', '驶离高速公路可以从这个位置直接驶入匝道。', '不能压实线直接过去的啊，应该提前换到驶离的车道，在进入匝道。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403546587141, '左转弯待转信号', '靠边停车信号', '左转弯信号', '右转弯信号', '3', 'http://images.juheapi.com/jztk/c1c2subject1/180.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403546587142, '2分', '3分', '6分', '12分', '4', '', '驾驶与准驾车型不符的机动车一次记几分？', '根据公安部《机动车驾驶证申领和使用规定》（附件3：道路交通安全违法行为记分分值），<b>驾驶与准驾车型不符的机动车的，一次应记12分。</b>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403546587143, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/182.jpg', '驾驶机动车行驶到这个位置时，如果车前轮已越过停止线可以继续通过。', '车全部越过停车线才可以继续通过，红灯时前轮刚过线必须停下来，否则就是闯红灯。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403550781441, '高速公路紧急停车带', '高速公路避让处所', '高速公路客车站', '高速公路停车区', '1', 'http://images.juheapi.com/jztk/c1c2subject1/183.jpg', '这个标志是何含义？', '高速公路紧急停车带：用于指示紧急停车的位置，设在紧急停车带的前端。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403554975745, '交叉路停车线', '左弯待转区线', '掉头引导线', '小型车转弯线', '2', 'http://images.juheapi.com/jztk/c1c2subject1/184.jpg', '图中圈内两条白色虚线是什么标线？', '左弯待转区是专门供左转弯车辆使用的。<br/>当直行信号灯绿灯亮时，左转弯是红灯时，左转弯的车辆可以进入左转弯待转区等候，待左转弯信号灯绿灯亮了，等候在待转区的车辆可以快速通过路口；当直行、左转弯信号灯都是红灯时，车辆是不可以进入左弯待转区的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403554975746, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/c1c2subject1/185.jpg', '驾驶机动车在这种情况下不能直行和左转弯', '直行和左转都是红灯，所以不能直行和左转。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403554975747, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/c1c2subject1/186.jpg', '驾驶机动车要选择绿色箭头灯亮的车道行驶。', '只有绿灯是表示允许通行，机动车要选择绿色箭头灯亮的车道行驶。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403559170050, '减速或停车让行', '鸣喇叭示意其让道', '立即变道绕过行人', '抢在行人之前通过', '1', '', '驶近没有人行横道的交叉路口时，发现有人横穿道路，应怎样做？', '如果没有人行横道，行人就有可能从任何地方走出。遇到行人一定要减速或停车让行，注意行人的安全。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170051, '导向车道线', '可变导向车道线', '方向引导线', '单向行驶线', '1', 'http://images.juheapi.com/jztk/c1c2subject1/188.jpg', '图中圈内白色实线是什么标线？', '将车流诱导到应行驶的路线的设施的总称。导向车道：大城市主要平交路口两端，引导车辆分道行驶的车道，其分界线用白色单实线，表明进入该车道的车辆不得变更车道或压线行驶。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170052, '允许在左侧车道行驶', '分隔对向行驶的交通流', '分隔同向行驶的交通流', '禁止跨越对向行车道', '2', 'http://images.juheapi.com/jztk/c1c2subject1/189.jpg', '路中黄色分界线的作用是什么？', '白色表示同向可跨越车道，黄色表示反向可跨越车道；虚线表示可跨越车道。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170053, '100公里/小时', '110公里/小时', '60公里/小时', '90公里/小时', '4', 'http://images.juheapi.com/jztk/c1c2subject1/190.jpg', '在这条车道行驶的最低车速是多少？', '高速路同方向有3条车道，最左侧的为110公里到120公里，中间的为90公里到110公里，最右侧的速度范围为60公里到90公里。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170054, '紧跟其后，伺机再超', '停止继续超车', '加速继续超越', '连续鸣喇叭加速超越', '2', '', '驾驶人在超车时，前方车辆不减速、不让道，应怎样做？', '行车安全第一，如果继续强制超车，会有发生事故的危险。应该停止继续超车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170055, '进入路口等待', '左转弯行驶', '加速直行通过', '不得越过停止线', '4', 'http://images.juheapi.com/jztk/c1c2subject1/192.jpg', '驾驶机动车在路口直行遇到这种信号灯应该怎样行驶？', '亮红灯时，车辆应该在停止线内停车等待。', 1, 1, 1, NULL, 0);
