INSERT INTO `me_question` VALUES (98403559170060, '正确', '错误', '', '', '1', '', '驾驶人一边驾车，一边打手持电话是违法行为。', '新修订的《机动车驾驶证申领和使用规定》公布后，2013年1月1日起施行，其中规定：驾驶机动车有拨打、接听手持电话等妨碍安全驾驶的行为的一次记2分。并且违反了《道路交通安全法》的相关规定，属于违法行为。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403559170061, '正确', '错误', '', '', '2', '', '大型客车、牵引车、城市公交车、中型客车、大型货车驾驶人应当每两年提交一次身体条件证明.', '2013年1月1日起施行的公安部123号令，已明确规定，大、中型客货车驾驶人每年参加审验，审验时自己申报身体条件情况', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403559170062, '前后雾灯', '危险报警闪光灯', '前大灯', '倒车灯', '2', '', '车辆在雨天临时停车时，应开启什么灯？', '即使不是在雨天，我们在路边临时停车时，也要开启危险报警闪光灯。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170063, '正确', '错误', '', '', '2', '', '驾驶人在道路上醉酒驾驶机动车的处3年以上有期徒刑。', '醉酒驾驶机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；5年内不得重新取得机动车驾驶证。\r<br/>醉酒驾驶营运机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；10年内不得重新取得机动车驾驶证，重新取得机动车驾驶证后，不得驾驶营运机动车。\r<br/>醉驾不用处3年以上有期徒刑。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403559170064, '机动车更换发动机', '加装前后防撞装置', '改变车身颜色', '更换车身或者车架', '2', '', '已注册登记的小型载客汽车有下列哪种情形，所有人不需要办理变更登记？', '《道路交通安全法实施条例》第六条：已注册登记的机动车有下列情形之一的，<b>机动车所有人应当向登记该机动车的公安机关交通管理部门申请变更登记</b>： \r<br/><b>(一)改变机动车车身颜色的； \r<br/>(二)更换发动机的； \r<br/>(三)更换车身或者车架的； </b>\r<br/>(四)因质量有问题，制造厂更换整车的； \r<br/>(五)营运机动车改为非营运机动车或者非营运机动车改为营运机动车的；\r<br/>(六)机动车所有人的住所迁出或者迁入公安机关交通管理部门管辖区域的。\r<br/>注意题目说的是 <b>[不需要办理变更登记的]</b> ，结合法规和题目要求，更换发动机、改变车身颜色、更换车身或者车架都需要办理变更登记，而B项加装前后防撞装置是不需要办理变更登记的，因此本题答案选B。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170065, '2分', '3分', '6分', '12分', '3', '', '驾驶人驾驶机动车违反道路交通信号灯通行一次记多少分?', '驾驶机动车违反道路交通信号灯通行的一次记6分。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170066, '高速公路左侧出口预告', '高速公路目的地预告', '高速公路右侧出口预告', '高速公路下一出口预告', '4', 'http://images.juheapi.com/jztk/c1c2subject1/203.jpg', '这个标志是何含义？', '高速公路下一出口预告：预告下一出口可到达地点。当互通式立体交叉间距大于或等于3 km、小于5 km时，应设置下一出口预告标志，预告下一出口的信息和距离。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170067, '高速公路出口', '高速公路起点', '高速公路入口', '高速公路终点', '2', 'http://images.juheapi.com/jztk/c1c2subject1/204.jpg', '这个标志是何含义？', '高速公路起点：设在高速公路或城市快速路的起点。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170068, '靠右侧加速行驶', '尽量礼让对方先行', '不侵占对方道路，正常行驶', '示意对方停车让行', '2', '', '会车中遇到对方来车行进有困难需借道时，应怎样做？', '如果遇到对方来车行进有困难需借道，请驾驶员尽量礼让对方先行，文明行车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403559170069, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/c1c2subject1/206.jpg', '驾驶机动车在这种情况下不能左转弯。', '有图片可知，左转箭头此时是红灯，红灯禁止通行，因此在这种情况下不能左转弯。题目描述无误，因此本题答案为对。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403559170070, '正确', '错误', '', '', '1', '', '车速超过规定时速50%以上的一次记12分。', '《公安部令第123号》规定，驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的，一次记12分。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403559170071, '正确', '错误', '', '', '1', '', '一个合格的驾驶人，不仅表现在技术的娴熟上，更重要的是应该具有良好的驾驶行为习惯和道德修养。', '娴熟的技术固然重要，但没有良好的驾驶习惯和道德修养，同样会破坏正常的交通秩序。因此合格驾驶员应该具备良好的驾驶行为习惯和道德修养。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403559170072, '直行信号', '转弯信号', '停止信号', '靠边停车信号', '1', 'http://images.juheapi.com/jztk/c1c2subject1/209.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403580141569, '分道信息预告', '道路分岔处预告', '地点和距离预告', '十字交叉路口预告', '4', 'http://images.juheapi.com/jztk/c1c2subject1/210.jpg', '这个标志是何含义？', '上边有十字能看到吧？', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403584335874, '正确', '错误', '', '', '1', '', '驾驶机动车发生财产损失交通事故，当事人对事实及成因无争议的可先撤离现场。', '《道路交通事故处理程序规定》第十三条：<b>机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。</b>车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。非机动车与非机动车或者行人发生财产损失事故，基本事实及成因清楚的，当事人应当先撤离现场，再协商处理损害赔偿事宜。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403584335875, '正确', '错误', '', '', '1', '', '距离交叉路口50米以内的路段不能停车。', '根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br/>（1）在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；<br/>（2）<b>交叉路口、</b>铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；<br/>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；<br/>（4）车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行；<br/>（5）路边停车应当紧靠道路右侧，机动车驾驶人不得离车，上下人员或者装卸物品后，立即驶离；<br/>（6）城市公共汽车不得在站点以外的路段停车上下乘客。<br/>', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403584335876, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/213.jpg', '可以从这个位置直接驶入高速公路行车道。', '应该按照标线行驶，不要乱开。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403588530178, '沿左侧车道掉头', '该路口不能掉头', '选择中间车道掉头', '在路口内掉头', '2', 'http://images.juheapi.com/jztk/c1c2subject1/214.jpg', '遇到这种情况的路口，以下做法正确的是什么？', '此处有禁止掉头的标志，所以是不可以掉头的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403588530179, '减速、低速会车或停车让行', '向左占道，让对方减速让行', '打开前照灯，示意对方停车让行', '加速选择理想位置', '1', '', '会车前选择的交会位置不理想时，应怎样做？', '发现交会位置不理想时，应减速或停车让行，以寻找下一个理想会车位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403588530180, '正确', '错误', '', '', '1', '', '驾驶机动车在铁路道口、桥梁、陡坡、隧道或者容易发生危险的路段不能掉头。', '《中华人民共和国道路交通安全法实施条例》第四十九条第一款：<br/>“机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段,不得掉头。”', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403588530181, '急剧制动低速通过', '靠弯道外侧行驶', '充分减速并靠右侧行驶', '借对向车道行驶', '3', '', '驾驶车辆行至道路急转弯处，应怎样做？', '安全驾驶，减速慢行。急转弯处切记减速靠右侧慢行，否则不仅容易侧翻，还容易因为视线死角与对向来车发生碰撞。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403588530182, '禁止临时停车', '禁止上下人员', '禁止装卸货物', '禁止长时停车', '4', 'http://images.juheapi.com/jztk/c1c2subject1/218.jpg', '路缘石上的黄色虚线是何含义？', '虚线是禁止长时间停车，实线是禁止临时或长时间停车。<br/>图中所示为虚线，所以禁止长时停车，可以临时停车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403588530183, 'T型路口', '分流路口', '减速通行', '此路不通', '4', 'http://images.juheapi.com/jztk/c1c2subject1/219.jpg', '这个标志是何含义？', '用以指示前方道路为死胡同，无出口、不能通行。该标志为蓝底白色街区红色图案。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403588530184, '高速公路公用电话', '高速公路报警电话', '高速公路紧急电话', '高速公路救援电话', '3', 'http://images.juheapi.com/jztk/c1c2subject1/220.jpg', '这个标志是何含义？', '高速公路紧急电话：用于指示高速公路紧急电话的位置，可单独设置也可设在紧急电话的立柱上或箱体上。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403588530185, '高速公路客车站预告', '高速公路避险处预告', '高速公路服务区预告', '高速公路收费站预告', '3', 'http://images.juheapi.com/jztk/c1c2subject1/221.jpg', '这个标志是何含义？', '高速公路服务区预告：用于预告服务区的位置，设在减速车道起点处。表示一般服务区预告标志。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403588530186, '按顺序停车等候', '鸣喇叭示意前方车辆快速行驶', '选择空当逐车超越', '继续穿插绕行', '1', '', '发现前方道路堵塞，正确的做法是什么？', '如果遇到前方拥堵，应该按顺序停车等候。随便加塞或者鸣喇叭都不是文明行车行为。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403588530187, '停车领卡', '停车缴费', 'ETC通道', '停车检查', '1', 'http://images.juheapi.com/jztk/c1c2subject1/223.jpg', '这个标志是何含义？', '停车领卡：用以提示停车领卡，设在进入高速公路或城市快速路收费站入口一侧的适当位置。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403588530188, '60公里/小时', '90公里/小时', '100公里/小时', '110公里/小时', '3', 'http://images.juheapi.com/jztk/c1c2subject1/224.jpg', '在这条车道行驶的最低车速是多少？', '高速路同方向有2条车道，左侧车的速度范围为100公里到120公里，右侧车的速度范围为60公里到100公里。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403588530189, '停车占用机动车道', '距离路边超过30厘米', '在有禁停标线路段停车', '在非机动车道停车', '3', 'http://images.juheapi.com/jztk/c1c2subject1/225.jpg', '这样在路边临时停放机动车有什么违法行为？', '图中右侧路边是黄色的禁止停车标线，所以此处是不能停车的。', 1, 1, 1, NULL, 0);
