INSERT INTO `me_question` VALUES (98403588530191, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/227.jpg', '这辆小型载客汽车进入高速公路行车道的行为是正确的。', '这辆车直接驶入了行车道，要先在加速车道，也就是靠右的车道加速后才能驶入，所以错', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403588530192, '向右急转弯', '向右绕行', '连续弯路', '向左急转弯', '1', 'http://images.juheapi.com/jztk/c1c2subject1/228.jpg', '这个标志是何含义？', '向右急转弯：向右急弯路标志，设在右急转弯的道路前方适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403601113090, '5米以内', '10米以内', '50米以内', '30米以内', '3', 'http://images.juheapi.com/jztk/c1c2subject1/229.jpg', '在距这段路多少米以内的路段不能停放机动车？', '根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：\r<br/>（1）在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；\r<br/><b>（2）交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；</b>\r<br/>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；\r<br/>（4）车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行；\r<br/>（5）路边停车应当紧靠道路右侧，机动车驾驶人不得离车，上下人员或者装卸物品后，立即驶离。\r<br/>（6）城市公共汽车不得在站点以外的路段停车上下乘客。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403601113091, '标志或标线', '《道路交通安全法》', '车辆说明书', '地方法规', '1', '', '在标志、标线齐全的高速公路上行车，应当按照什么规定的车道和车速行驶？', '根据规定，在标志、标线齐全的路段，应该按标志、标线行驶。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403601113092, '禁止右转', '路口警示', '准许直行', '加速通过', '2', 'http://images.juheapi.com/jztk/c1c2subject1/231.jpg', '驾驶机动车在路口遇到这种信号灯表示什么意思？', '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403601113093, '限制车辆通行', '提示限速信息', '提供方向信息', '警告前方危险', '3', '', '指路标志的作用是什么？', '指路标志是传递道路方向，地点，距离信息的标志。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403601113094, '正确', '错误', '', '', '2', '', '机动车在道路上发生轻微交通事故且妨碍交通时，不需移动。', '《中华人民共和国道路交通安全法》第七十条：在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403601113095, '从中心线虚线处掉头', '从右侧车道掉头', '进入路口后掉头', '在人行横道上掉头', '1', 'http://images.juheapi.com/jztk/c1c2subject1/234.jpg', '在这种路口怎样进行掉头？', '虚线处可掉头，实线是不能压的，所以实线处不能掉头、变道等。因此应该从图中中心线虚线处掉头才对。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403601113096, '正确', '错误', '', '', '1', '', '社会车辆距离消防栓或者消防队(站)门前30米以内的路段不能停车。', '根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br/>（1）在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；<br/>（2）交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；<br/><b>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；</b><br/>（4）车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行；<br/>（5）路边停车应当紧靠道路右侧，机动车驾驶人不得离车，上下人员或者装卸物品后，立即驶离；<br/>（6）城市公共汽车不得在站点以外的路段停车上下乘客。<br/>', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403609501698, '寻找机会超越前车', '从车辆空间穿插通过', '减速停车，依次排队等候', '鸣喇叭催促', '3', '', '行车中发现前方道路拥堵时，应怎样做？', '减速停车依次排队，文明驾驶。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403609501699, '正确', '错误', '', '', '2', '', '机动车行驶中，车上少年儿童可不使用安全带。', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。相比较成人，少年儿童自我保护能力更低，更应该系安全带。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403609501700, '没按规定握转向盘', '座椅角度不对', '没系安全带', '驾驶姿势不正确', '3', 'http://images.juheapi.com/jztk/c1c2subject1/238.jpg', '这位驾驶人违反法律规定的行为是什么？', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。很明显，图中司机没有系安全带，属于违法行为。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403609501701, '距离路边超过30厘米', '在有禁停标线路段停车', '距离加油站不到30米', '停车占用非机动车道', '3', 'http://images.juheapi.com/jztk/c1c2subject1/239.jpg', '这样临时停放红色轿车有什么违法行为？', '图为加油站，规定加油站30米内不得停车。公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的车辆以外的车辆，不得停车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403609501702, '注意行人', '有人行横道', '村庄或集镇', '有小学校', '3', 'http://images.juheapi.com/jztk/c1c2subject1/240.jpg', '这个标志是何含义？', '村庄：用以提醒车辆驾驶人小心驾驶。设在紧靠村庄、集镇且视线不良的路段以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403613696001, '虚线一侧禁止越线', '实线一侧禁止越线', '实线一侧允许越线', '两侧均可越线行驶', '2', 'http://images.juheapi.com/jztk/c1c2subject1/241.jpg', '路中心黄色虚实线是何含义？', '虚线可压，实线不可压。实线一侧禁止越线。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403613696002, '连续鸣喇叭提醒其让路', '持续鸣喇叭并加速超越', '让自行车先行', '注意观察动态，减速慢行，留有足够的安全距离', '4', '', '行车中超越同向行驶的自行车时，应怎样做？', '驾驶机动车遇行人和非机动车肯定要减速慢行，否则会容易撞上，但“慢行”相对机动车的快行是慢的，而相对自行车来的速度来说还是比较快的。并且超车必须要确保安全距离（左右距离），防止与自行车相撞。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403613696003, '水渠', '桥梁', '隧道', '涵洞', '3', 'http://images.juheapi.com/jztk/c1c2subject1/243.jpg', '这个标志是何含义？', '隧道：用以提醒车辆驾驶人注意慢行。设在双向行驶并且照明不好的隧道口前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403613696004, '高速公路入口预告', '高速公路终点预告', '高速公路起点预告', '高速公路出口预告', '1', 'http://images.juheapi.com/jztk/c1c2subject1/244.jpg', '这个标志是何含义？', '高速公路入口预告：用于指示进入高速公路或城市快速路的入口，设在进入高速公路或城市快速路前的被交道路上适当位置。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403613696005, '非机动车道', '避让非机动车', '禁止非机动车通行', '注意非机动车', '4', 'http://images.juheapi.com/jztk/c1c2subject1/245.jpg', '这个标志是何含义？', '黄底标志为警告标志，自行车优势非机动车所以答案是注意非机动车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403613696006, '隧道入口', '气象台', '注意横风', '风向标', '3', 'http://images.juheapi.com/jztk/c1c2subject1/246.jpg', '这个标志是何含义？', '注意横风：此标志设在经常有很强的侧风并有必要引起注意的路段前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403626278914, '高速公路收费处', '高速公路检查站', '设有ETC的收费站', '高速公路领卡处', '3', 'http://images.juheapi.com/jztk/c1c2subject1/247.jpg', '这个标志是何含义？', '设有ETC的收费站：用以指示前方收费站。设在高速公路、城市快速路主线上渐变段起点处。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403626278915, '不得少于20米', '不得少于10米', '不得少于50米', '不得少于30米', '3', '', '驾驶小型载客汽车在高速公路上时速低于100公里时的最小跟车距离是多少？', '根据《中华人民共和国道路交通安全法实施条例》第八十条 机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离，车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403626278916, '两侧通行', '右侧通行', '左侧通行', '不准通行', '3', 'http://images.juheapi.com/jztk/c1c2subject1/249.jpg', '这个标志是何含义？', '用于引导行车方向，提示道路使用者前方线形变化，注意谨慎驾驶。设于中央隔离设施端部、渠化设施的端部、桥头等。答题技巧：∧表示俩侧通行，/这个方向的表示左侧通行，这个方向表示右侧通行。交通指路标志大全：http://www.jszks.com/onepage/16752.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403630473218, '最低限速为80公里/小时', '平均速度为80公里/小时', '最高限速为80公里/小时', '解除80公里/小时限速', '1', 'http://images.juheapi.com/jztk/c1c2subject1/250.jpg', '这个路面标记是何含义？', '白色为最低限速，黄色为最高限速。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403630473219, '高速公路特殊天气建议速度', '高速公路特殊天气最低速度', '高速公路特殊天气平均速度', '高速公路特殊天气最高速度', '1', 'http://images.juheapi.com/jztk/c1c2subject1/251.jpg', '这个标志是何含义？', '高速公路特殊天气建议速度：用以提醒驾驶员在雨、雪、雾等特殊天气下，以建议速度行驶，和白色半圆状车距确认线配合使用。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403630473220, '堤坝路', '傍山险路', '落石路', '临崖路', '2', 'http://images.juheapi.com/jztk/c1c2subject1/252.jpg', '这个标志是何含义？', '傍山险路：用以提醒车辆驾驶人小心驾驶。设在傍山险路路段以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403630473221, '禁止借道', '禁止变道', '禁止超车', '禁止掉头', '3', 'http://images.juheapi.com/jztk/c1c2subject1/253.jpg', '这个标志是何含义？', '禁止超车：表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403630473222, '漫水桥', '渡口', '船用码头', '过水路面', '2', 'http://images.juheapi.com/jztk/c1c2subject1/254.jpg', '这个标志是何含义？', '渡口：用以提醒车辆驾驶人谨慎驾驶。设在车辆渡口以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403630473223, '将车停在道路中间', '将车停到不妨碍交通的地方', '开启近光灯或雾灯', '就地停车排除故障', '2', '', '机动车在道路上发生故障，需要停车排除时，驾驶人应该怎么办？', '机动车在行驶道路上发生故障，应将机动车移至不妨碍交通的地方停放，并要在来车方向的50—100米的地方设置警示标志。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403630473224, '高速公路里程编号', '高速公路界牌编号', '高速公路命名编号', '高速公路路段编号', '3', 'http://images.juheapi.com/jztk/c1c2subject1/256.jpg', '这个标志是何含义？', '高速公路命名编号：用于指示高速公路的编号，作为指路标志的路线信息，出现在高速公路、城市快速路、一般道路指路标志版面中。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403630473225, '低速行驶', '注意行人', '步行', '行人先行', '3', 'http://images.juheapi.com/jztk/c1c2subject1/257.jpg', '这个标志是何含义？', '表示该街道只供步行。此标志设在步行街的两端。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403630473226, '正确', '错误', '', '', '1', '', '行车中遇到执行紧急任务的消防车、救护车、工程救险车时要及时让行。', '《道路法》第五十三条第1款　警车、消防车、救护车、工程救险车执行紧急任务时，其他车辆和行人应当让行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403630473227, '正确', '错误', '', '', '2', '', '驾驶机动车行经城市没有列车通过的铁路道口时允许超车。', '《中华人民共和国道路交通安全法》第四十三条：\r<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：\r<br/>（一）前车正在左转弯、掉头、超车的；\r<br/>（二）与对面来车有会车可能的；\r<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；\r<br/>（四）行经<b>铁路道口、</b>交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403630473228, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/260.jpg', '在道路上遇到这种情况可以从两侧超车。', '《中华人民共和国道路交通安全法》第四十三条：<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br/>（一）前车正在左转弯、掉头、超车的；<br/>（二）与对面来车有会车可能的；<br/><b>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；</b><br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403630473229, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/261.jpg', '在这个路口不能掉头。', '此处有指示掉头的标志，而且虚线是可以临时跨越的，所以是可以掉头的。题目描述有误，因此本题答案为错。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403630473230, '正确', '错误', '', '', '1', '', '驾驶机动车在高速公路要按照限速标志标明的车速行驶。', '由于高速公路上车速较快，危险性较高。为了保障安全，一定要严格按照限速标志的车速行驶。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403651444738, '减速慢行，必要时停车避让', '长鸣喇叭催促', '迅速从一侧通过', '加速绕行', '1', '', '行车中遇儿童时，应怎样做？', '儿童的行动变化性非常大，为了保护儿童安全，一定要减速慢行，必要时停车避让。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403651444739, '不能变换挡位', '可以变换挡位', '可换为高挡', '停车观察', '1', '', '驾驶车辆驶入铁路道口前减速降挡，进入道口后应怎样做？', '进路口前停车观望，进了就不能换挡，如果换挡不当，就会熄火，非常危险。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403651444740, '正确', '错误', '', '', '1', '', '驾驶车辆向右变更车道时，应提前开启右转向灯，注意观察，在确保安全的情况下，驶入要变更的车道。', '向右变道时，提前开启右转向灯，是为了提示后车。同时要观察周围情况，具有变道条件时再变更车道。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403651444741, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/266.jpg', '这辆停在路边的机动车没有违法行为。', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。警告标志与车的距离不足50米，而且没有开启危险报警闪光灯，存在违法行为。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403651444742, '鸣喇叭警告，不得进入', '加速行驶，紧跟前车，不让其进入', '挤靠“加塞”车辆，逼其离开', '主动礼让，确保行车安全', '4', '', '车辆在拥挤路段低速行驶时，遇其他车辆强行插队，应怎样做？', '遇到其他车辆强行插队，如果我们不礼让的话就会有发生事故的风险。做文明司机，主动礼让，确保安全。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403651444743, '正确', '错误', '', '', '1', '', '饮酒后驾驶机动车的一次记12分。', '根据规定，<b>饮酒驾驶机动车辆，罚款1000元—2000元、记12分并暂扣驾照6个月</b>；饮酒驾驶营运机动车，罚款5000元，记12分，处以15日以下拘留，并且5年内不得重新获得驾照。醉酒驾驶机动车辆，吊销驾照，5年内不得重新获取驾照，经过判决后处以拘役，并处罚金；醉酒驾驶营运机动车辆，吊销驾照，10年内不得重新获取驾照，终生不得驾驶营运车辆，经过判决后处以拘役，并处罚金。\r<br/>题目描述正确，因此答案为对。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403651444744, '鸣喇叭示意', '主动停车避让', '加速倒车', '继续倒车', '2', '', '在一般道路倒车时，若发现有过往车辆通过，应怎样做？', '倒车时视线盲区较大，如果有意外情况无法及时处理。因此一旦后方有车辆通过，为了安全一定要停车避让。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403651444745, '停车让行人先行', '从行人前方绕行', '鸣喇叭提醒行人', '从行人后方绕行', '1', 'http://images.juheapi.com/jztk/c1c2subject1/270.jpg', '遇到这种情形时，应怎么办？', '通过人行横道应减速慢行，遇到行人则需停车让行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403651444746, '正确', '错误', '', '', '1', '', '在道路上临时停车不得妨碍其他车辆和行人通行。', '《道路交通安全法》第五十六条：在道路上临时停车的，不得妨碍其他车辆和行人通行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403651444747, '正确', '错误', '', '', '1', '', '驾驶车辆在道路上行驶时，应当按照规定的速度安全行驶。', '安全驾驶，请驾驶员按照规定的速度行驶。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804865, '驶入减速车道', '继续向前行驶', '车速保持100公里/小时', '车速降到40公里/小时以下', '1', 'http://images.juheapi.com/jztk/c1c2subject1/273.jpg', '驾驶机动车驶离高速公路时，在这个位置怎样行驶？', '这是高速路口出口，当要驶离高速路时，要先进入减速车道。正确操作是：提前打开右转向灯，并逐渐降低车速，不能压实线，要越过虚线进入减速车道。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804866, '可靠弯道外侧行驶', '可短时间借用对方的车道', '可加速沿弯道切线方向通过', '应“减速、鸣喇叭、靠右行”', '4', '', '山区道路车辆进入弯道前，在对面没有来车的情况下，应怎样做？', '在急转弯处，应该减速慢行；鸣喇叭，靠右行，是为了提醒对面来车。总之，安全驾驶为主。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804867, '正确', '错误', '', '', '1', '', '在泥泞路上制动时,车轮易发生侧滑或甩尾，导致交通事故。', '常识题，泥泞路摩擦系数低。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804868, '正确', '错误', '', '', '1', '', '驾驶人在发生交通事故后因抢救伤员变动现场时要标明位置。', '《道路交通安全法》第七十条：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。<b>因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。 </b><br/>在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804869, '主要街道', '单向行驶路段', '交通流量大的路段', '单向两条行车道', '3', '', '驾驶机动车行经市区下列哪种道路时不得超车？', '《中华人民共和国道路交通安全法》第四十三条：<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br/>（一）前车正在左转弯、掉头、超车的；<br/>（二）与对面来车有会车可能的；<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；<br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、<b>市区交通流量大的路段</b>等没有超车条件的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804870, '2分', '3分', '6分', '12分', '4', '', '上道路行驶的机动车故意遮挡、污损、不按规定安装机动车号牌的一次记几分？', '《公安部令第123号》规定，上道路行驶的机动车未悬挂机动车号牌的，<b>或者故意遮挡、污损、不按规定安装机动车号牌的，一次记12分 。</b>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804871, '在人行横道上停车', '距离路边超过30厘米', '在有禁停标线路段停车', '在非机动车道停车', '1', 'http://images.juheapi.com/jztk/c1c2subject1/279.jpg', '这样在路边临时停放机动车有什么违法行为？', '图中汽车停在人行横道。规定是：在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804872, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/280.jpg', '这个路段可以在非机动车道上临时停车。', '在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804873, '正确', '错误', '', '', '2', '', '变更车道时只需开启转向灯，便可迅速转向驶入相应的行车道。', '<b>开转向灯后，要充分观察确认安全后方可使入车道，</b>本题错在“迅速”且没有观察。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804874, '正在停车', '减速让行', '正在掉头', '正常行驶', '3', '', '同车道行驶的车辆遇前车有下列哪种情形时不得超车？', '同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：  ①《《前车正在左转弯、掉头、超车的》》;  ②与对面来车有会车可能的;  ③前车为执行紧急任务的警车、消防车、救护车、工程救险车的;  ④行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804875, '进入应急车道行驶', '尽快驶离高速公路', '尽快在路边停车', '在路肩低速行驶', '2', '', '驾驶机动车在高速公路遇到能见度低于50米的气象条件时，车速不得超过20公里/小时，还应怎么做？', '1、能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；\r<br/>2、能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离； 　　\r<br/><b>3、能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。</b>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804876, '正确', '错误', '', '', '2', '', '漫水道路行车时，应挂高速档，快速通过。', '不应该挂高速挡，而是要选用低速挡，以保证汽车有足够的驱动力;入水时应平稳，以防溅起的水花落入电器设备，造成电器短路；发动机中速运转，保持车速，中途不得停车、换挡和急转转向盘', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804877, '危险报警闪光灯', '远光灯', '近光灯', '雾灯', '3', '', '夜间车辆通过照明条件良好的路段时，应使用什么灯？', '照明条件良好，开启近光就够了。远光会影响前车和前面行人的视线，容易引发危险。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804878, '没有开启危险报警闪光灯', '没有将车停到路边', '没有立即排除故障', '没有设置警告标志', '4', 'http://images.juheapi.com/jztk/c1c2subject1/286.jpg', '找出这辆故障车有哪种违法行为？', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804879, '机动车行驶证', '从业资格证', '居民身份证', '机动车通行证', '1', '', '驾驶人未携带哪种证件驾驶机动车上路，交通警察可依法扣留车辆？', '《道路交通安全违法行为处理程序规定》第十三条 “有下列情形之一的，因无其他机动车驾驶人代替驾驶、违法行为尚未消除、需要调查或者证据保全等原因不能立即放行的，可以扣留车辆：    （一）未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未携带行驶证、机动车驾驶证的；', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804880, '开启右转向灯', '开启左转向灯', '开启危险报警闪光灯', '开启前照灯', '2', 'http://images.juheapi.com/jztk/c1c2subject1/288.jpg', '在这个位置时怎样使用灯光？', '《道路交通安全法实施条例》第七十九条：机动车从匝道驶入高速公路，<b>应当开启左转向灯</b>，在不妨碍已在高速公路内的机动车正常行驶的情况下驶入车道。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804881, '正确', '错误', '', '', '1', '', '对未放置检验合格标志上道路行驶的车辆，交通警察可依法予以扣留。', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，<b>未放置检验合格标志、</b>保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804882, '提前加速抢行', '停车让行', '降低车速、缓慢通过', '连续鸣喇叭催促', '2', '', '行车中遇列队横过道路的学生时，应怎样做？', '列队横过道路的学生，人数多，通行时间长。降低车速还是容易撞上，停车让行更安全。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804883, '正确', '错误', '', '', '1', '', '机动车在夜间道路上发生故障难以移动时要开启危险报警闪光灯、示廓灯、后位灯。', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启<b>危险报警闪光灯</b>并在车后50米至100米处设置警告标志，夜间还应当同时开启<b>示廓灯和后位灯</b>。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804884, '减速，保持足够间距，随时准备停车', '保持正常车速行驶', '鸣喇叭提醒，加速通过', '随时准备紧急制动', '1', '', '车辆驶近停在车站的公交车辆时，为预防公交车突然起步或行人从车前穿出，应怎样做？', '遇到行人，减速行驶，随时准备停车。如果紧急制动的话可能会导致后车追尾，所有选择A。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804885, '处20元以上200元以下罚款', '取消申领驾驶证资格', '1年内不得再次申领驾驶证', '2年内不能再次申领驾驶证', '3', '', '提供虚假材料申领驾驶证的申请人会承担下列哪种法律责任？', '修订后的《机动车驾驶证申领和使用规定》第七十八条第一款规定：“隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在一年内不得再次申领机动车驾驶证。”', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804886, '正确', '错误', '', '', '1', '', '造成交通事故后逃逸构成犯罪的人不能申请机动车驾驶证。', '《交通法》第一百零一条 违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。<br/><b>造成交通事故后逃逸的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。</b>', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804887, '加速通过', '立即停车', '先减速注意观察行人、非机动车动态，确认安全后再通过', '鸣喇叭示意行人让道', '3', '', '车辆驶近人行横道时，应怎样做？', '因为行人具有随意性，所以要先减速注意观察行人、非机动车动态，确认安全后再通过。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804888, '靠左侧路边逆向停放', '在停车泊位内停放', '在路边随意停放', '在人行道上停放', '2', '', '驾驶机动车需要在路边停车时怎样选择停车地点？', '一般人行道上是不能停车的，路边也不能随意停车。在路边可以顺向临时停车，但是不能逆向停车。所以最佳停车地点还是停车泊位内。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804889, '正确', '错误', '', '', '2', '', '在道路上超车时，应尽量加大横向距离，必要时可越实线超车。', '实线不能跨越，因此不能越实线超车，本题错误。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804890, '正确', '错误', '', '', '1', '', '距离宽度不足4米的窄路50米以内的路段不能停车。', '根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br/>（1）在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；<br/>（2）交叉路口、铁路道口、急转弯、<b>宽度不足4米的窄路、</b>桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；<br/>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；<br/>（4）车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行；<br/>（5）路边停车应当紧靠道路右侧，机动车驾驶人不得离车，上下人员或者装卸物品后，立即驶离；<br/>（6）城市公共汽车不得在站点以外的路段停车上下乘客。<br/>', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804891, '逼对方靠右行驶', '用大灯警示对方', '主动给对方让行', '紧靠道路中心行驶', '3', '', '行车中遇到对向来车占道行驶，应怎样做？', '记住文明驾驶，礼貌让行。不开赌气车，安全第一。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804892, '正确', '错误', '', '', '1', '', '距离桥梁、陡坡、隧道50米以内的路段不能停车。', '《中华人民共和国道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定：交叉路口、铁路道口、急弯路、宽度不足４米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804893, '在路口前实线区内根据需要', '进入路口实线区内', '在路口停止线前', '在虚线区按导向箭头指示', '4', '', '驾驶车辆在交叉路口前变更车道时，应怎样驶入要变更的车道？', '实线不可跨线行驶，应该在虚线区按导向箭头指示变更车道。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804894, '加速通过', '正常行驶', '连续鸣喇叭', '减速慢行', '4', '', '当驾驶车辆行经两侧有行人且有积水的路面时，应怎样做？', '文明驾驶，减速慢行，防止水溅至行人身上。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804895, '及时将伤者送医院抢救或拨打急救电话', '尽量避开，少惹麻烦', '绕过现场行驶', '借故避开现场', '1', '', '行车中遇交通事故受伤者需要抢救时，应怎样做？', '伤者需要帮助，我们应及时伸出援助之手，及时送伤者去医院或拨打急救电话。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804896, '正确', '错误', '', '', '1', '', '谨慎驾驶的三原则是集中注意力、仔细观察和提前预防。', '黄金原则，牢记，对我们以后驾车是有很大的帮助的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804897, '正确', '错误', '', '', '1', '', '驾驶车辆变更车道时，应提前开启转向灯，注意观察，保持安全距离，驶入要变更的车道。', '行车变道时，提前开启转向灯，是为了提示后车。同时要观察周围情况，具有变道条件时再变更车道。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804898, '正确', '错误', '', '', '1', '', '打开机动车车门时，不得妨碍其他车辆和行人通行。', '《道路交通安全法实施条例》第七十七条：乘坐机动车应当遵守下列规定：<br/>(一)不得在机动车道上拦乘机动车;<br/>(二)在机动车道上不得从机动车左侧上下车;<br/><b>(三)开关车门不得妨碍其他车辆和行人通行;</b><br/>(四)机动车行驶中，不得干扰驾驶，不得将身体任何部分伸出车外，不得跳车;<br/>(五)乘坐两轮摩托车应当正向骑坐。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804899, '100公里/小时', '80公里/小时', '60公里/小时', '50公里/小时', '3', 'http://images.juheapi.com/jztk/c1c2subject1/307.jpg', '在这段高速公路上行驶的最低车速是多少？', '红圈白底限制最高速度，蓝底限制最低速度，此路段最低车速是60公里每小时。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804900, '正确', '错误', '', '', '1', '', '驾驶人违反交通运输管理法规发生重大事故致人重伤的可能判处3年以下徒刑或拘役。', '刑法第一百三十三条规定：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804901, '未放置检验合格标志', '未携带身份证', '未放置城市环保标志', '未携带机动车登记证书', '1', '', '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '未放置检验合格标志，未悬挂机动车号牌，未放置保险标志的，未携带机动车行驶证的都是可依法扣留车辆的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804902, '连续下坡', '下陡坡', '上陡坡', '连续上坡', '1', 'http://images.juheapi.com/jztk/c1c2subject1/310.jpg', '这个标志是何含义？', '连续下坡：用以提醒车辆驾驶人小心驾驶。当连续下坡总长大于3 km后，应重复设置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804903, '正确', '错误', '', '', '2', '', '驾驶人在道路上驾驶机动车追逐竞驶，情节恶劣的处3年以下有期徒刑。', '道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。不用判刑。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804904, '正确', '错误', '', '', '1', '', '行车中遇到正在进行作业的道路养护车辆、工程作业车时要注意避让。', '按照规定，行车途中遇到正在进行作业的道路养护车辆、工程作业车时要注意避让。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804905, '正确', '错误', '', '', '2', '', '驾驶小型汽车下陡坡时允许熄火滑行。', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在下陡坡时熄火或者空挡滑行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804906, '可直接驶入交叉路口', '不能驶入交叉路口', '可借对向车道通过路口', '在保证安全的情况下驶入交叉路口', '2', '', '在堵车的交叉路口绿灯亮时，车辆应怎样做？', '前方堵车，这时进入交叉路口只能更堵。因此堵车时即便绿灯亮了，也不要驶入路口内。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804907, '正确', '错误', '', '', '1', '', '机动车发生财产损失交通事故，对应当自行撤离现场而未撤离造成交通堵塞的，可以对驾驶人处以200元罚款。', '《道路交通事故处理程序规定》第十三条：对应当自行撤离现场而未撤离的，交通警察应当责令当事人撤离现场；造成交通堵塞的，对驾驶人处以200元罚款；驾驶人有其他道路交通安全违法行为的，依法一并处罚。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804908, '堤坝路', '上陡坡', '连续上坡', '下陡坡', '4', 'http://images.juheapi.com/jztk/c1c2subject1/316.jpg', '这个标志是何含义？', '下陡坡：用以提醒车辆驾驶人小心驾驶。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804909, '注意行人', '注意儿童', '学校区域', '人行横道', '1', 'http://images.juheapi.com/jztk/c1c2subject1/317.jpg', '这个标志是何含义？', '注意行人：用以警告车辆驾驶人减速慢行，注意行人。设在行人密集，或不易被驾驶员发现的人行横道线以前适当位置。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804910, '正确', '错误', '', '', '1', '', '女驾驶人穿高跟鞋驾驶车辆，不利于安全行车。', '穿高跟鞋踩制动踏板，会受力不均，同时也不能正确感知是否将制动踩到底，容易造成事故。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804911, '正确', '错误', '', '', '1', '', '不要在驾驶室的前后窗范围内悬挂和放置妨碍驾驶人视线的物品。', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804912, '加油起步', '鸣喇叭', '提高发动机转速', '观察周围交通情况', '4', '', '车辆临时靠边停车后准备起步时，应先怎样做？', '起步时要先观察路况，小心驾驶，安全第一。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804913, '2年以下', '3年以下', '7年以下', '7年以上', '4', '', '驾驶人违反交通运输管理法规发生重大事故后，因逃逸致人死亡的，处多少年有期徒刑？', '《中华人民共和国刑法》第一百三十三条：\r<br/>违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；\r<br/>交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；\r<br/><b>因逃逸致人死亡的，处七年以上有期徒刑。</b>\r<br/>在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。\r<br/>有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804914, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/322.jpg', '在道路与铁路道口遇到一个红灯亮时要尽快通过道口。', '车辆通过设有交通信号的铁路道口时，必须遵守下列规定： 1）两个红灯交替闪烁或红灯稳定亮时，表示火车接近道口，禁止通行； 2）红灯熄灭白灯亮时，表示道口开通，准许车辆通行； 3）当红灯和白灯同时熄灭时，表示停电或者设备发生故障，道口信号无效，在这种情况下，必须听从道口看守人员和道口安全管理人员的指挥，在道口前减速或者停车，确认安全无误后迅速通行。凡遇到道口栏杆（栏门）关闭、音响器发出报警、道口信号显示红色灯光或者道口看守人员示意火车即将通过等任意一种情况时，车辆严禁抢行，必须依次停在停止线外，没有停止线的，停在距最外钢轨的5米以外，并且不得影响栏杆或者栏门的关闭。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804915, '正确', '错误', '', '', '1', '', '冰雪道路行车，由于积雪对光线的反射，极易造成驾驶人目眩而产生错觉。', '因为太阳光通过冰的折射到人的眼睛里，冰在太阳光下就好像是一面镜子。所以会容易目眩而产生幻觉。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804916, '未携带机动车登记证书', '未携带保险合同', '未放置城市环保标志', '未放置保险标志', '4', '', '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '未放置检验合格标志，未悬挂机动车号牌，未放置保险标志的，未携带机动车行驶证的都是可依法扣留车辆的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804917, '正确', '错误', '', '', '1', '', '路口黄灯持续闪烁，警示驾驶人要注意�望，确认安全通过。', '路口黄灯持续闪烁，警示驾驶人要注意�望，确认安全通过。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804918, '左转弯信号', '左转弯待转信号', '减速慢行信号', '右转弯信号', '2', 'http://images.juheapi.com/jztk/c1c2subject1/326.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804919, '多乘员车辆专用车道', '小型客车专用车道', '未载客出租车专用车道', '大型客车专用车道', '1', 'http://images.juheapi.com/jztk/c1c2subject1/327.jpg', '道路最左侧白色虚线区域是何含义？', '图上有字，“多乘员专用”，因此本题是多乘员车辆专用车道', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804920, '等候放行线', '停车让行线', '减速让行线', '左弯待转线', '3', 'http://images.juheapi.com/jztk/c1c2subject1/328.jpg', '路口最前端的双白虚线是什么含义？', '<p><img src=http://tp.mnks.cn/tp_332.jpg><br/>停止线</p><p><img src=http://tp.mnks.cn/tp_333.jpg><br/>停车让行线</p><p><img src=http://tp.mnks.cn/tp_334.jpg><br/>减速让行线</p>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804921, '右转弯信号', '靠边停车信号', '左转弯信号', '停止信号', '4', 'http://images.juheapi.com/jztk/c1c2subject1/329.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804922, '正确', '错误', '', '', '2', '', '行车中在道路情况良好的条件下可以观看车载视频。', '《实施条例》第六十二条，驾驶机动车不得有下列行为：\r<br/>(一)在车门、车厢没有关好时行车；\r<br/>(二)在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品；\r<br/><b>(三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为；</b>\r<br/>(四)下陡坡时熄火或者空挡滑行；\r<br/>(五)向道路上抛撒物品；\r<br/>(六)驾驶摩托车手离车把或者在车把上悬挂物品；\r<br/>(七)连续驾驶机动车超过4小时未停车休息或者停车休息时间少于20分钟；\r<br/>(八)在禁止鸣喇叭的区域或者路段鸣喇叭。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804923, '正确', '错误', '', '', '1', '', '驾驶人有使用其他车辆检验合格标志嫌疑的，交通警察可依法扣留车辆。', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：有伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、 保险标志、驾驶证或者<b>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑的。</b>', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804924, '正确', '错误', '', '', '1', '', '驾驶人因服兵役、出国（境）等原因延期审验期间不得驾驶机动车。', '第六十三条  机动车驾驶人因服兵役、出国（境）等原因，无法在规定时间内办理驾驶证期满换证、审验、提交身体条件证明的，可以向机动车驾驶证核发地车辆管理所申请延期办理。申请时应当填写申请表，并提交机动车驾驶人的身份证明、机动车驾驶证和延期事由证明。<br/>延期期限最长不超过三年。【延期期间机动车驾驶人不得驾驶机动车】。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804925, '正确', '错误', '', '', '1', '', '对使用其他车辆号牌、行驶证的车辆，交通警察可依法予以扣留。', '《道路交通安全法》第九十六条：使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处二千元以上五千元以下罚款。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804926, '制动距离延长', '抗滑能力变大', '制动性能没有变化', '路面附着力增大', '1', '', '冰雪路行车时应注意什么？', '车辆在冰雪路面上行驶，<b>因汽车轮胎与路面的摩擦系数减小、附着力大大降低，</b>所以遇有冰雪路面，车辆在行驶中最重要的是车辆制动问题，要采取点刹、并和前后车保持车距离。遇情况紧急制动时，制动距离会大大延长。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804927, '14个月', '12个月', '6个月', '10个月', '2', '', '道路交通违法行为累积记分周期是多长时间？', '《公安部令第123号》第五十五条：道路交通安全违法行为累积记分周期（即记分周期）为12个月，满分为12分，从机动车驾驶证初次领取之日起计算。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804928, '正确', '错误', '', '', '1', '', '驾驶人有使用其他车辆号牌、行驶证嫌疑的，交通警察可依法扣留车辆。', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：有伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、 保险标志、驾驶证或者<b>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑</b>的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804929, '开启危险报警闪光灯', '开启前照灯', '开启左转向灯', '开启右转向灯', '4', 'http://images.juheapi.com/jztk/c1c2subject1/337.jpg', '进入减速车道时怎样使用灯光？', '减速车道在右侧，应该开启右转向灯提醒后车注意减速避让，然后从虚线处进入减速车道。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804930, '正确', '错误', '', '', '2', '', '在道路上跟车行驶时，跟车距离不是主要的，只须保持与前车相等的速度，即可防止发生追尾事故。', '跟车时，与前车保持相同的速度，只能让两车间的距离保持不变，并不能保证安全。而保持足够的安全距离，才能有效防止追尾的发生。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804931, '正确', '错误', '', '', '2', '', '驾驶机动车时可以向道路上抛撒物品。', '《实施条例》第六十二条，驾驶机动车不得有下列行为：\r<br/>(一)在车门、车厢没有关好时行车；\r<br/>(二)在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品；\r<br/>(三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为；\r<br/>(四)下陡坡时熄火或者空挡滑行；\r<br/><b>(五)向道路上抛撒物品；</b>\r<br/>(六)驾驶摩托车手离车把或者在车把上悬挂物品；\r<br/>(七)连续驾驶机动车超过4小时未停车休息或者停车休息时间少于20分钟；\r<br/>(八)在禁止鸣喇叭的区域或者路段鸣喇叭。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804932, '正确', '错误', '', '', '2', '', '驾驶人驾驶有达到报废标准嫌疑机动车上路的，交通警察依法予以拘留。', '1、根据《中华人民共和国道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证。\r<br/>2、题目中表述的是“达到报废标准嫌疑机动车”，并未确定为已经报废，按规定无须处罚。\r<br/>因此，题目所说的“交通警察依法予以拘留”是不正确的，本题答案应为错误。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804933, '正确', '错误', '', '', '1', '', '道路交通事故中，机动车无号牌、检验合格标志、保险标志时，要保护现场并立即报警。', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，当事人应当保护现场并立即报警： 　　<br/>（一）造成人员死亡、受伤的； 　　<br/>（二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　<br/>（三）<b>机动车无号牌、无检验合格标志、无保险标志的；</b> 　　<br/>（四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的； 　　<br/>（五）碰撞建筑物、公共设施或者其他设施的；　　<br/>（六）驾驶人无有效机动车驾驶证的； 　　<br/>（七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； 　　<br/>（八）当事人不能自行移动车辆的。 　　<br/>发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804934, '正确', '错误', '', '', '2', '', '对有使用伪造或变造检验合格标志嫌疑的车辆，交通警察只进行罚款处罚。', '根据《中华人民共和国道路交通安全法》第九十六条规定：<br/><b>伪造、变造或者使用伪造、变造的检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处十日以下拘留，并处一千元以上三千元以下罚款；构成犯罪的，依法追究刑事责任。</b><br/>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处二千元以上五千元以下罚款。 当事人提供相应的合法证明或者补办相应手续的，应当及时退还机动车。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804935, '正确', '错误', '', '', '1', '', '道路交通安全违法行为累积记分一个周期满分为12分。', '《公安部令第123号》第五十五条：<br/>道路交通安全违法行为累积记分周期（即记分周期）为12个月，满分为12分，从机动车驾驶证初次领取之日起计算。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804936, '下陡坡', '连续上坡', '上陡坡', '堤坝路', '3', 'http://images.juheapi.com/jztk/c1c2subject1/344.jpg', '这个标志是何含义？', '上陡坡：用以提醒车辆驾驶人小心驾驶。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804937, '发生侧滑、引发交通事故', '因视线模糊而撞车', '不被其他车辆驾驶人发现', '引起发动机熄火', '1', '', '下雨后路面湿滑，车辆行驶中紧急制动时，容易导致什么？', '<b>下雨时路面潮湿，轮胎与路面之间的滑动摩擦因素小，相应的摩擦力也要变小。</b>所以紧急制动时，汽车轮胎停止滚动，而向前滑动。或者说滑动摩擦力不能有效减慢汽车速度，导致汽车还要向前滑出很大段距离。因此天雨路滑，宜减速行驶。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804938, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/c1c2subject1/346.jpg', '遇到这种情形时要停车避让行人。', '根据《道路交通安全法》第四十七条，机动车行经没有交通信号的道路时，遇行人横过道路，应当避让。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804939, '处6个月徒刑', '处1年以上徒刑', '处管制，并处罚金', '处拘役，并处罚金', '4', '', '驾驶机动车在道路上追逐竞驶，情节恶劣，会受到什么处罚？', '根据《中华人民共和国刑法修正案（八）》规定“在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金”。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804940, '正确', '错误', '', '', '2', '', '驾驶机动车在道路上发生交通事故要立即将车移到路边。', '《道路交通安全法》第七十条：<b>在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；</b>造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804941, '未悬挂机动车号牌', '未携带身份证', '未携带保险合同', '未放置城市环保标志', '1', '', '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '未放置检验合格标志，未悬挂机动车号牌，未放置保险标志的，未携带机动车行驶证的都是可依法扣留车辆的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804942, '实习期驾驶人', '取得驾驶证的人', '驾驶证被吊销的人', '驾驶证记分达到6分的人', '3', '', '驾驶人将机动车交由什么样的人驾驶的，交通警察可依法扣留机动车驾驶证？', '六种情形下可以扣留驾驶证 新法规定，有下列情形之一的，可以扣留机动车驾驶证： (一)饮酒、醉酒后驾驶机动车的；<b>(二)机动车驾驶人将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的；</b> (三)机动车行驶超过规定时速百分之五十的； (四)驾驶拼装或者已达到报废标准的机动车的； (五)发生重大交通事故，构成犯罪的； (六)在一个记分周期内累积记分达到12分的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804943, '7年以上', '3年以下', '3年以上7年以下', '10年以上', '3', '', '驾驶人违反交通运输管理法规发生重大事故致人死亡且逃逸的，处多少年有期徒刑？', '《中华人民共和国刑法》第一百三十三条：\r<br/>1、违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；\r<br/>2、<b>交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；</b>\r<br/>3、因逃逸致人死亡的，处七年以上有期徒刑。\r<br/>\r<br/>题干中所说“发生重大事故致人死亡且逃逸的”，可理解为：先发生事故致人死亡，然后逃逸（先死后逃）。结合法规，题干所描述的情况属于情节特别恶劣的交通肇事行为，并且在肇事后逃逸，因此应该处“3年以上7年以下有期徒刑”，所以选C。\r<br/>\r<br/>【拓展延伸】\r<br/>如果肇事司机逃逸前，因交通事故已致人死亡了，死因可归结到交通事故上；\r<br/>但若肇事司机逃逸后，人再死亡，死因则变成交通事故+因逃逸致其救治不及时，属于罪加一等，因此处罚更加严重。\r<br/>\r<br/>有个口诀可以教给大家：\r<br/>先死后逃，3年以上7年以下；\r<br/>先逃后死，7年以上。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804944, '正确', '错误', '', '', '2', '', '驾驶人违反交通运输管理法规发生重大事故后，因逃逸致人死亡的，处3年以上7年以下有期徒刑。', '发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；<b>因逃逸致人死亡的，处七年以上有期徒刑。</b>', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804945, '正确', '错误', '', '', '1', '', '小型汽车驾驶人发生交通事故造成人员死亡，承担同等以上责任未被吊销驾驶证的，应当在记分周期结束后30日内接受审验。', '《机动车驾驶证申领和使用规定》（公安部令第123号）第六十条：<br/>小型汽车驾驶人发生交通事故造成人员死亡承担同等以上责任未被吊销机动车驾驶证的，应当在本记分周期结束后三十日内到公安机关交通管理部门接受审验。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804946, '连续变换远、近光灯', '不断鸣喇叭', '使用近光灯，减速或停车避让', '使用远光灯', '3', '', '夜间驾驶车辆遇自行车对向驶来时，应怎样做？', '文明驾驶，礼貌让行。交替使用远近光灯可能会使对方看不清，所以最好的是减速或者停车避让。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804947, '正确', '错误', '', '', '2', '', '高速公路因发生事故造成堵塞时，可在右侧紧急停车带或路肩行驶。', '紧急停车带：一般用于高速公路发生车祸等事故以后，警车、救护车、消防车等救援车辆的紧急通行车道，其他车辆除特殊情况下，严禁占用该车道行驶或长时间停车。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804948, '正确', '错误', '', '', '1', '', '对发生道路交通事故需要收集证据的事故车，交通警察可以依法扣留。', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：对发生道路交通事故，因收集证据需要的，可以依法扣留事故车辆。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804949, '正确', '错误', '', '', '1', '', '驾驶人违反交通运输管理法规发生重大事故使公私财产遭受重大损失的可能处3年以下徒刑或拘役。', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804950, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/c1c2subject1/358.jpg', '这辆小轿车不能在这个位置停车。', '人行横道禁止停车，因为会阻碍行人走路。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804951, '注射胰岛素', '酒醉经历', '吸烟成瘾', '注射毒品', '4', '', '3年内有下列哪种行为的人不得申请机动车驾驶证？', '《机动车驾驶证申领和使用规定》第十二条：三年内有吸食、注射毒品行为或者解除强制隔离戒毒措施未满三年，或者长期服用依赖性精神药品成瘾尚未戒除的，不得申请机动车驾驶证。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804952, '机动车驾驶证', '居民身份证', '机动车通行证', '从业资格证', '1', '', '驾驶人未携带哪种证件驾驶机动车上路，交通警察可依法扣留车辆？', '《道路交通安全违法行为处理程序规定》第十三条 “有下列情形之一的，因无其他机动车驾驶人代替驾驶、违法行为尚未消除、需要调查或者证据保全等原因不能立即放行的，可以扣留车辆：    （一）未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未携带行驶证、机动车驾驶证的；', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804953, '正确', '错误', '', '', '1', '', '对有伪造或变造号牌、行驶证嫌疑的车辆，交通警察可依法予以扣留', '这个没有问题。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804954, '正确', '错误', '', '', '2', '', '车辆在路边起步后应尽快提速，并向左迅速转向驶入正常行驶道路。', '需要先观察，安全才能开过去，不是一起步就迅速进入行驶道行驶，万一后面有车是很危险的，所以“迅速”是错的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804955, '正确', '错误', '', '', '1', '', '发生交通事故造成人员受伤时，要保护现场并立即报警。', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，<b>当事人应当保护现场并立即报警：</b> 　　\r<br/><b>（一）造成人员死亡、受伤的； </b>　　\r<br/>（二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　\r<br/>（三）机动车无号牌、无检验合格标志、无保险标志的； 　　\r<br/>（四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的； 　　\r<br/>（五）碰撞建筑物、公共设施或者其他设施的；　　\r<br/>（六）驾驶人无有效机动车驾驶证的； 　　\r<br/>（七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； 　　\r<br/>（八）当事人不能自行移动车辆的。 　　\r<br/>发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804956, '导向车道线', '方向引导线', '可变导向车道线', '单向行驶线', '3', 'http://images.juheapi.com/jztk/c1c2subject1/364.jpg', '图中圈内的锯齿状白色实线是什么标线？', '可变导向车道的意思就是进入该车道后有不止一个车道走向，如有的路口情况允许右转和直行（即右转和直行合并为一个车道），或者调头和左转合并为一个车道（一般用于左侧车道）。可变导向车道有着灵活性，不同于普通的车道指示线，普通的车道只要进入车道直行就是直行，左转就是左转。一般这样的车道设在交通比较复杂的路段。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804957, '正确', '错误', '', '', '1', '', '夜间行车，驾驶人的视野受限，很难观察到灯光照射区域以外的交通情况，因此要减速行驶。', '晚上开车的时候受到光线暗的局限，相对观察事物的距离也就受到局限，所以视距比较短，因此很难观察到灯光照射区域以外的交通情况。为了安全考虑，减速行驶是必须的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804958, '正确', '错误', '', '', '1', '', '车辆起步前，驾驶人应对车辆周围交通情况进行观察，确认安全时再开始起步。', '起步前的第一步就是驾驶员要先观察车周围，确认安全时再开始起步。因此本题是对的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804959, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/367.jpg', '在交叉路口遇到这种情况享有优先通行权。', '右转让左转。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804960, '正确', '错误', '', '', '1', '', '通过经常发生塌方、泥石流的山区地段，避免停车。', '经常发生塌方、泥石流的都属于危险地段，所以能尽快离开就尽快离开，避免停车。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804961, '从前车右侧路肩进入路口', '从有空隙一侧进入路口', '每车道一辆依次交替驶入路口', '向左变道穿插进入路口', '3', '', '驾驶机动车在车道减少的路口，遇到前方车辆依次停车或缓慢行驶时怎么办？', '《道路交通安全法实施条例》第五十三条：机动车在车道减少的路口、路段，遇有前方机动车停车排队等候或者缓慢行驶的，应当每车道一辆依次交替驶入车道减少后的路口、路段。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804962, '注意野生动物', '注意牲畜', '动物公园', '开放的牧区', '1', 'http://images.juheapi.com/jztk/c1c2subject1/370.jpg', '这个标志是何含义？', '这个是头鹿啊，是野生动物啊。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804963, '正确', '错误', '', '', '2', '', '立交桥上一般都是单向行驶，车辆不必减速行驶。', '立交桥上车多，弯多，路窄，考虑到安全问题，还是要减速行驶。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804964, '非紧急情况时不得在应急车道行驶或者停车', '可在减速车道或加速车道上超车、停车', '可在紧急停车带停车装卸货物', '可在路肩停车上下人员', '1', '', '机动车在高速公路行驶，下列做法正确的是？', '紧急车道只能在紧急情况下才能使用，非紧急情况不得占用。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804965, '正确', '错误', '', '', '2', '', '车辆在高速公路行驶时，可以仅凭感觉确认车速。', '不仅是在高速行车中，任何时候都不能凭感觉去确认车速。要通过速度表确认。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804966, '指示禁行', '指示车道', '指示合流', '指示直行', '4', 'http://images.juheapi.com/jztk/c1c2subject1/374.jpg', '这个导向箭头是何含义？', '亲，直行哦~~', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804967, '正确', '错误', '', '', '1', '', '驾驶车辆通过人行横道线时，应注意礼让行人。', '安全第一，文明驾驶，礼貌让行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804968, '正确', '错误', '', '', '2', '', '变更车道时，应开启转向灯，迅速驶入侧方车道。', '<b>开启转向灯后，应该充分观察确认安全后再驶入。</b>本题错在“迅速”两字，且没有观察。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804969, '提高车速抢先通过', '鸣喇叭抢先通过', '停车避让', '保持正常车速行驶', '3', '', '车辆行至交叉路口，遇有转弯的车辆抢行，应怎样做？', '如果遇有转弯的车辆抢行，一定要文明驾驶，礼貌让行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804970, '可借对向车道通过', '依次停在路口外等候', '从前车两侧穿插通过', '进入路口内等候', '2', '', '驾驶机动车遇有前方交叉路口交通阻塞时怎么办？', '《中华人民共和国道路交通安全法实施条例》第五十三条：<br/>机动车遇有前方交叉路口交通阻塞时，应当依次停在路口以外等候，不得进入路口。 所以应该选A。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804971, '正确', '错误', '', '', '1', '', '在冰雪道路上行车时，车辆的稳定性降低，加速过急时车轮极易空转或溜滑。', '冰雪道路，轮胎与地面的摩擦力较小，吸附力较低，只要一加速，就比较容易打滑或者溜空。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804972, '正确', '错误', '', '', '2', '', '车辆在高速公路上行车，可以频繁地变更车道。', '车辆在高速公路上行车，可以变更车道，但不可以“频繁”的变更车道。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804973, '正确', '错误', '', '', '1', '', '掉头过程中，应严格控制车速，仔细观察道路前后方情况，确认安全后方可前进或倒车。', '掉头过程中，要控制好车速，仔细观察周围情况。这样能最大程度的避免掉头中发生事故。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804974, '正确', '错误', '', '', '1', '', '驾驶机动车不能进入红色叉形灯或者红色箭头灯亮的车道。', '《道路交通安全法实施条例》第四十条规定： 红色叉形灯或者箭头灯亮时，禁止本车道车辆通行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804975, '正确', '错误', '', '', '1', '', '车辆转弯时应沿道路右侧行驶，不要侵占对方的车道，做到“左转转大弯，右转转小弯”。', '由于<b>我国规定机动车要靠右行驶，无论在十字路口，弯路都是各行其路，所以左转弧度大，右转弧度就小。</b>右转转小弯左转转大弯，就能最大程度避免与对方来车发生碰撞。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804976, '正确', '错误', '', '', '1', '', '车辆驶离高速公路时，应当经减速车道减速后进入匝道。', '下高速公路时必须在减速车道减速至60码以下才能出高速公路。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804977, '正确', '错误', '', '', '2', '', '驾驶小型载客汽车在高速公路行驶的最低车速为90公里/小时。', '《道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804978, '靠边减速或停车让行', '占用其他车道行驶', '加速变更车道避让', '在原车道内继续行驶', '1', '', '行车中遇抢救伤员的救护车从本车道逆向驶来时，应怎样做？', '遇到正在执行任务的特种车辆，应当靠边减速或停车让行，为其让路。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804979, '依靠惯性加速冲过', '挂空挡滑行驶过', '保持原速通过', '低速缓慢平稳通过', '4', '', '车辆通过凹凸路面时，应怎样做？', '遇到凹凸不平的路面，要减速缓慢通过，安全第一。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804980, '正确', '错误', '', '', '2', '', '车辆行至交叉路口时，左转弯车辆在任何时段都可以进入左弯待转区。', '直行车辆放行时才可进入左转待转区。你到路口观察一下就明白了！', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804981, '保持50米以上', '保持60米以上', '保持100米以上', '保持80米以上', '3', '', '驾驶小型载客汽车在高速公路上时速超过100公里时的跟车距离是多少？', '《道路交通安全法实施条例》第八十条：机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离，车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804982, '减速通过', '匀速通过', '一停、二看、三通过', '加速通过', '3', '', '驾驶车辆通过无人看守的铁路道口时，应怎样做？', '一停，是停在未到铁道前，不是停在铁路上，二看，是在铁道前观察是否有火车要经过，三通过，才是安全通过。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804983, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/391.jpg', '遇到这种情况时，要加速从红车前变更车道。', '图中车辆按照道路提示需要向左变道，所以需要减速或者停车等待左侧车道直行车辆通行完毕后变道，而不可能是加速。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804984, '正确', '错误', '', '', '1', '', '通过隧道时，不得超车。', '《中华人民共和国道路交通安全法》 第四十三条 同车道行驶的机动车，……有下列情形之一的，不得超车： <br/>（一）前车正在左转弯、掉头、超车的； <br/>（二）与对面来车有会车可能的； <br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br/>（四）<b>行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。</b>', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804985, '正确', '错误', '', '', '2', '', '驾驶机动车通过漫水路时要加速行驶。', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804986, '正确', '错误', '', '', '2', '', '行驶在高速公路上遇大雾视线受阻时，应当立即紧急制动停车。', '<b>错在“紧急制动”上，要逐渐减速停车，</b>大雾天气视线不佳，紧急制动容易发生追尾事故，要逐渐减速停车。并且要开启危险警示灯以及雾灯。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804987, '依次排队行驶', '占对向车道超越', '从右侧借道超越', '从两侧随意超越', '1', 'http://images.juheapi.com/jztk/c1c2subject1/395.jpg', '遇到前方车辆缓慢行驶时怎样行驶？', '图中明显看出前方车辆较为拥堵，应该依次排队行驶，此时超车的话，容易造成交通事故。其次，左边是黄实线，不能越线超车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804988, '正确', '错误', '', '', '2', '', '驾驶人持超过有效期的驾驶证可以在1年内驾驶机动车。', '《道路交通安全法实施条例》第二十八条：机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804989, '鸣喇叭示意其让道', '让其先行', '紧随其后鸣喇叭', '加速绕过', '2', '', '行车中遇有非机动车准备绕过停放的车辆时，应怎样做？', '文明驾驶，礼貌让行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804990, '加速通过', '长鸣喇叭', '保持正常速度行驶', '预留出横向安全距离，减速行驶', '4', '', '行车中超越右侧停放的车辆时，为预防其突然起步或开启车门，应怎样做？', '留出安全距离、并减速，万一右侧车辆启动才有足够的空间和时间避让。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804991, '开启危险报警闪光灯通行', '直接加速直行通过', '让右方道路车辆先行', '让左方道路车辆先行', '3', 'http://images.juheapi.com/jztk/c1c2subject1/399.jpg', '在路口直行时,遇这种情形如何通行？', '右方的车直行，左方的车左转，左转让直行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804992, '正确', '错误', '', '', '2', '', '驾驶机动车在没有中心线的城市道路上，最高速度不能超过每小时50公里。', '《中华人民共和国道路交通安全法实施条例》第四十五条：<br/>机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br/><b>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里。</b><br/>(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804993, '人行横道灯', '注意行人', '注意信号灯', '交叉路口', '3', 'http://images.juheapi.com/jztk/c1c2subject1/401.jpg', '这个标志是何含义？', '注意信号灯：用以警告车辆驾驶人注意前方路段设有信号灯，应依信号灯指示行车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804994, '加速通过', '鸣喇叭警告', '减速让行', '临近时突然加速', '3', '', '行车中，遇非机动车抢行时，应怎样做？', '文明驾驶，礼貌让行，注意非机动车的安全。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804995, '正确', '错误', '', '', '1', '', '车辆上坡行驶，要提前观察路况、坡道长度，及时减挡使车辆保持充足的动力。', '提前观察路况可以增加行车时的预见性，及时减档保持充足的动力才能顺利上坡。低档速度慢，但是动力足，才能保证不熄火，顺利上坡。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804996, '靠道路中心行驶', '加速让路', '继续加速行驶', '减速，靠右侧行驶', '4', '', '驾驶的车辆正在被其他车辆超越时，应怎样做？', '超车从左边，如果我们正在被其他车辆超车，应该靠右边减速让行。安全第一。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804997, '紧靠道路中心', '不减速', '加速', '减速或停车让行', '4', '', '在山区道路遇对向来车时，应怎样会车？', '山区道路比较复杂，容易出事故，为了安全，会车时要减速或者停车让行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680804998, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/406.jpg', '在这种情况下可以借右侧公交车道超车。', '超车只能从左侧超车，并且车道内已经注明“公交车专用车道”，则此车道为公交车专用，其他车辆一般情况下不准占用。仅当发生事故、正常道路封闭有标示注明允许借用或者交警指示等特殊情况允许借道行驶。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680804999, '保持匀速通过', '尽快加速通过', '停车察明水情', '低速缓慢通过', '3', 'http://images.juheapi.com/jztk/c1c2subject1/407.jpg', '驾驶机动车遇到这种桥时首先怎样办？', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805000, '正确', '错误', '', '', '1', '', '车辆在山区道路行车下陡坡时，不得超车。', '《道路交通安全法》规定，在陡坡上不得超车，掉头。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805001, '100公里/小时', '90公里/小时', '120公里/小时', '110公里/小时', '2', 'http://images.juheapi.com/jztk/c1c2subject1/409.jpg', '在这条车道行驶的最高车速是多少？', '高速公路上同方向有2条车道的，左侧车道的最低车速为每小时100公里。但是图中最高限度标志为90公里/小时，所以应根据标志来选择90公里/小时。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805002, '正确', '错误', '', '', '2', '', '机动车在道路上发生故障难以移动时要在车后50米以内设置警告标志。', '机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。 所以不是在50米内，应该是50米以外100米内。扩展提示：高速公路上要在车后150米外设置警告标志。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805003, '主要街道', '高架路', '人行横道', '环城高速', '3', '', '驾驶机动车行经下列哪种路段不得超车？', '《中华人民共和国道路交通安全法》第四十三条：<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br/>（一）前车正在左转弯、掉头、超车的；<br/>（二）与对面来车有会车可能的；<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；<br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、<b>人行横道、</b>市区交通流量大的路段等没有超车条件的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805004, '开启前照灯，继续行驶', '开启示廓灯、雾灯，靠右行驶', '开启危险报警闪光灯，继续行驶', '开启危险报警闪光灯和雾灯，选择安全地点停车', '4', '', '遇有浓雾或特大雾天能见度过低，行车困难时，应怎样做？', '浓雾或特大雾天，能见度过低，此时开车属于十分危险的行为，一定要开启危险报警闪光灯和雾灯，选择安全地点停车。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805005, '记分达到10分', '记分达到6分', '驾驶证丢失、损毁', '驾驶证接近有效期', '3', '', '驾驶人出现下列哪种情况，不得驾驶机动车？', '驾驶证是证明驾驶员驾驶资格的重要凭证，<b>驾驶证损毁，丢失，就相当于是无证驾驶，所以在此期间驾驶员是不能驾驶车辆的。</b>驾驶证接近有效期，应该去交通管理所去换证。驾驶证累积记分达到12分的，应该重新进行理论考试，考试通过，才能重新领回驾照。\r<br/>因此本题答案应为A。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805006, '不得逆向或并列停放', '只要出去方便，可随意停放', '可逆向停放', '可并列停放', '1', '', '机动车在道路边临时停车时，应怎样做？', '在路边临时停车时应遵循“不影响正常交通”的原则，因此除了要靠路边之外，还要尽可能留出空间便于其他车辆通行，因此不得逆向或并列停放。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805007, '正确', '错误', '', '', '2', '', '雨天路面湿滑，车辆制动距离增大，行车中尽量使用紧急制动减速。', '因为路面比较滑，如果紧急制动（急刹车）容易造成车辆失控，最好不要紧急制动。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805008, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/c1c2subject1/416.jpg', '遇到这种情况不能超车。', '前车正在超车时，后面的车此时超车的话，肯定会导致两车相撞的，所以应该放弃超车。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805009, '正确', '错误', '', '', '2', '', '在高速公路上遇分流交通管制时，可不驶出高速公路，就地靠边停靠等待管制结束后继续前行。', '高速公路上不能随便靠边停车，以免造成更大的堵塞。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805010, '关闭前大灯', '开启远光灯', '开启雾灯', '变换远、近光灯', '4', '', '驾驶机动车在夜间超车时怎样使用灯光？', '《实施条例》第四十七条规定：机动车超车时，应当提前开启左转向灯、<b>变换使用远、近光灯或者鸣喇叭</b>。夜间视线不佳，应该变换使用远、近光灯提醒其他车辆。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805011, '前后雾灯', '倒车灯', '前大灯', '危险报警闪光灯', '4', '', '车辆在雪天临时停车时，应开启什么灯？', '一般情况下，我们在路边临时停车，均需开启危险报警闪光灯。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805012, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/420.jpg', '在交叉路口遇到这种情况时，要在红灯亮以前加速通过路口。', '黄灯亮了，车身没越过白色停止线，所以要停车等待。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805013, '山区道路', '城市高架路', '城市快速路', '窄桥、弯道', '4', '', '驾驶机动车在下列哪种路段不得超车？', '《中华人民共和国道路交通安全法》第四十三条：\r<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：\r<br/>（一）前车正在左转弯、掉头、超车的；\r<br/>（二）与对面来车有会车可能的；\r<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；\r<br/>（四）行经铁路道口、交叉路口、<b>窄桥、弯道、</b>陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805014, '加速提前抢过', '鸣喇叭示意对向车辆让道', '迅速占用车道，迫使对向来车停车让道', '降低速度或停车，让对向来车优先通行', '4', '', '行车中需要借道绕过前方障碍物，但对向来车已接近障碍物时，应怎样做？', '请注意看题干“对向来车已接近障碍物时”，对方的车更接近障碍物，文明礼让，对向来车优先通行。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805015, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/423.jpg', '行经这种交通标线的路段要加速行驶。', '要减速通过，白色的菱形标志就是提醒你前方接近人行横道，所以要减速防止撞到行人。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805016, '左右两侧均可超越', '从前车的右侧超越', '从前车的左侧超越', '从无障碍一侧超越', '3', 'http://images.juheapi.com/jztk/c1c2subject1/424.jpg', '这种情况超车时，从前车的哪一侧超越？', '超车只能从左侧超越。因为驾驶员在左边，左边超车，驾驶员能观察到相对行驶车道有无来车。而右方超车驾驶员是看不到前方是否来车或速度慢的车辆。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805017, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/425.jpg', '遇到这种情况的路段，可以进入网状线区域内停车等候。', '《道路交通安全法实施条例》第五十三条：机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，不得在人行横道、网状线区域内停车等候。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805018, '谨慎超越', '放弃超车', '并行一段距离后再超越', '加速超越', '2', '', '进入左侧道路超车，无法保证与正常行驶前车的横向安全间距时，应怎样做？', '没有超车条件，放弃超车是最安全的行为。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805019, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/427.jpg', '在路口这个位置时可以加速通过路口。', '这是红灯信号，应该禁止通过，应停车等待绿灯，再通过。\r<br/>题目描述有误，本题答案应为错误。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805020, '适当加大安全距离', '紧随前车之后', '适当减小安全距离', '尽可能寻找超车机会', '1', '', '车辆在山区道路跟车行驶时，应怎样做？', '山区道路行车，要加大安全距离，发生事故时，有反应时间和反应距离。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805021, '正确', '错误', '', '', '1', '', '车辆通过学校和小区应注意观察标志标线，低速行驶，不要鸣喇叭。', '<b>学校区域和居民区通常禁止鸣笛，减少噪音，</b>以免影响居民生活和学生学习。同时，这些地方人流量大，要时时留意交通状况，减速慢行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805022, '正确', '错误', '', '', '2', '', '在高速公路上行驶感觉疲劳时，应立即停车休息。', '在高速公路上行驶感觉疲困时，应该找到最近的服务区停车休息，高速不可随便停车。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805023, '正确', '错误', '', '', '2', '', '驾驶机动车在没有交通信号的路口要尽快通过。', '《道路交通安全法》第四十四条：通过没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口时，应当减速慢行，并让行人和优先通过的车辆先行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805024, '鸣喇叭催其让行', '保持正常车速行驶', '提前减速或停车避让', '向左占道，谨慎驶过', '3', '', '车辆在较窄的山路上行驶时，如果靠山体的一方不让行，应怎样做？', '如果靠山体的一方不让行，为了保障行车安全，一定要提前减速或停车避让。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805025, '直行', '向右转弯', '向左转弯', '直行或向左转弯', '2', 'http://images.juheapi.com/jztk/c1c2subject1/433.jpg', '这个路口允许车辆怎样行驶？', '右转的灯是绿的，所以只能向右转弯。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805027, '离开路面', '由路中移到路侧', '距离不变', '由高变低', '2', '', '夜间行车中，前方出现弯道时，灯光照射会发生怎样的变化？', '车辆在平面路上行驶，灯光距离不变；在出现一般的弯道，灯光由路中移动到路侧；上坡时，灯光由高变低；出现急弯或大坑时，灯光离开路面。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805028, '驾驶机动车违反道路交通信号灯', '未取得校车驾驶资格驾驶校车', '驾驶与准驾车型不符的机动车', '饮酒后驾驶机动车', '1', '', '有下列哪种违法行为的机动车驾驶人将被一次记6分？', '《公安部令第123号》规定，驾驶机动车违反道路交通信号灯通行的，一次记6分 。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805029, '加速行驶', '绕道行驶', '保持正常车速行驶', '谨慎选择行车速度', '4', '', '驾驶人行车中看到注意儿童标志的时候，应怎样做？', '看见儿童标志，需谨慎驾驶，避免伤及儿童。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805030, '占对方道路转弯', '在弯道中心转弯', '加速鸣喇叭通过', '减速鸣喇叭示意', '4', 'http://images.juheapi.com/jztk/c1c2subject1/438.jpg', '在这种路段如何行驶？', '《道路交通安全法实施条例》第五十九条规定：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。<b>机动车驶近急弯、坡道顶端等影响安全视距的路段以及超车或者遇有紧急情况时，应当减速慢行，并鸣喇叭示意。</b>\r<br/>因此，本题答案应为A。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805031, '减速慢行', '加速通过', '大型车先行', '左侧车辆先行', '1', '', '驾驶机动车通过没有交通信号的交叉路口怎样行驶？', '交叉路口的车流量本来就比较大，在没有交通信号的交叉路口，更应该减速慢行，同时注意观察周围情况，确认可以通过后再通过。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805032, '空调开关', '点火开关', '雨刷开关', '灯光开关', '2', 'http://images.juheapi.com/jztk/c1c2subject1/440.jpg', '这是什么操纵装置？', '这个是点火装置没错。灯光开关在左手边，雨刷开关在右手边。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805033, '立即停车', '在原地掉头', '在原地倒车驶回', '继续向前行驶，寻找下一个出口', '4', '', '高速公路上行车，如果因疏忽驶过出口，应怎样做？', '高速公路行车不可掉头、不可倒车，错过出口，只能继续行驶，寻找下个出口。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805034, '从左侧跨越实线超越', '从两侧随意超越', '依次排队等候', '从右侧借道超越', '3', 'http://images.juheapi.com/jztk/c1c2subject1/442.jpg', '遇有这种排队等候的情形怎么做？', '前方拥堵，请耐心等候。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805035, '正确', '错误', '', '', '1', '', '车辆行至急转弯处时，应减速并靠右侧行驶，防止与越过弯道中心线的对方车辆相撞。', '通过急弯靠右行驶，给对向来车流出足够的行车空间，慢行能在遇到突发状况时有足够的反应时间。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805036, '指示直行或掉头', '指示直行或左转弯', '指示直行或向左变道', '指示向左转弯或掉头', '2', 'http://images.juheapi.com/jztk/c1c2subject1/444.jpg', '这个导向箭头是何含义？', '左或者直行', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805037, '关闭远光灯', '使用危险报警闪光灯', '交替使用远近光灯', '使用远光灯', '3', 'http://images.juheapi.com/jztk/c1c2subject1/445.jpg', '在这种环境下通过路口如何使用灯光？', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。\r<br/>因此，本题答案应为D。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805038, '前车采取制动时没看后视镜', '前车采取制动过急', '后车超车时距离前车太近', '后车未与前车保持安全距离', '4', 'http://images.juheapi.com/jztk/c1c2subject1/446.jpg', '这两辆车发生追尾的主要原因是什么？', '因为有人行横道线，所以司机应该知道要提前减速，速度降得低了，后车要刹车就应该应该不会再追尾了。追尾都是后车的责任，因为没有保持足有的安全距离。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805039, '3分', '2分', '12分', '6分', '3', '', '造成交通事故后逃逸，尚不构成犯罪的一次记几分？', '《公安部令第123号》附件2，造成交通事故后逃逸，尚不构成犯罪的，一次记12分 。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805040, '15公里/小时', '20公里/小时', '40公里/小时', '30公里/小时', '4', '', '驾驶机动车在泥泞道路行驶时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br/>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br/>（二）掉头、转弯、下陡坡时；　　 <br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br/>（四）在冰雪、<b>泥泞</b>的道路上行驶时；', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805041, '正确', '错误', '', '', '2', '', '机动车在高速公路上发生故障时，在来车方向50至100米处设置警告标志。', '《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；警告标志应当设置在故障车来车方向150米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805042, '正确', '错误', '', '', '1', '', '车辆长时间停放时，应选择停车场停车。', '路边只能临时停车，要长时间停车，应该选择停车场。以免妨碍其他车辆通行。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805043, '正确', '错误', '', '', '1', '', '夜间行车，要尽量避免超车，确需超车时，可变换远近光灯向前车示意。', '只要在确保安全和不违规的情况下都可以超车。<b>变换远近灯光是为了提醒前方车辆的注意，</b>虽然只是一个动作，但对于保证安全来说却是很重要的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805044, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/452.jpg', '在这段道路上一定要减少鸣喇叭的频率。', '右侧的标志是禁止鸣喇叭，不是“减少”，而是绝对不能鸣喇叭。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805045, '刑事责任', '民事责任', '直接责任', '经济责任', '1', '', '机动车驾驶人违法驾驶造成重大交通事故构成犯罪的，依法追究什么责任？', '根据《道路交通安全法》第一百零一条规定：<b>违反道路交通安全法律、法规规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，\r\n\r\n并由公安机关交通管理部门吊销机动车驾驶证。</b>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805046, '注意牲畜', '注意野生动物', '野生动物保护区', '大型畜牧场', '1', 'http://images.juheapi.com/jztk/c1c2subject1/454.jpg', '这个标志是何含义？', '这个是头牛，是牲畜。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805047, '机动车保险单', '机动车行驶证', '出厂合格证明', '机动车登记证', '2', '', '下列哪种证件是驾驶机动车上路行驶应当随车携带？', '《道路交通安全法》第十一条：驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，<b>并随车携带机动车行驶证。</b>因此本题答案应为C。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805048, '正确', '错误', '', '', '2', '', '车辆在高速公路匝道上可以停车。', '按照规定：驶入确定匝道后，迅速提高车速，但不得超过规定速度，<b>在匝道上，不准超车、掉头、停车、和倒车。</b>', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805049, '正确', '错误', '', '', '2', '', '车辆驶入匝道后，迅速将车速提高到每小时60公里以上。', '应该是从匝道进入高速公路加速车道，在加速车道里迅速将车速提高到每小时60公里以上，而不是匝道。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805050, '正确', '错误', '', '', '2', '', '机动车发生财产损失交通事故，对应当自行撤离现场而未撤离的，交通警察不可以责令当事人撤离现场。', '《道路交通事故处理程序规定》第十三条：<b>对应当自行撤离现场而未撤离的，交通警察应当责令当事人撤离现场；</b>造成交通堵塞的，对驾驶人处以200元罚款；驾驶人有其他道路交通安全违法行为的，依法一并处罚。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805051, '倒车灯', '近光灯', '雾灯', '远光灯', '3', '', '雾天行车时，应及时开启什么灯？', '雾天行车应开启雾灯和危险报警闪光灯，本题选D。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805052, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/460.jpg', '点火开关在ON位置，车用电器不能使用。', 'LOCK-----（锁止），ACC------------ (断电)\r<br/>ON--------（通电），START--------（起动）\r<br/>点火开关在ON位置，车用电器是可以使用的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805053, '正确', '错误', '', '', '1', '', '驾驶机动车通过窄路、窄桥时的最高速度不能超过每小时30公里。', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br/>（一）进出非机动车道，通过铁路道口、急弯路、<b>窄路、窄桥时；</b>　　 <br/>（二）掉头、转弯、下陡坡时；　　 <br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br/>（四）在冰雪、泥泞的道路上行驶时。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805054, '离合器踏板', '制动踏板', '驻车制动器', '加速踏板', '1', 'http://images.juheapi.com/jztk/c1c2subject1/462.jpg', '这是什么踏板？', '这个不知如何解释，记住吧，亲，你会开车以后这个东东用的频率是相当高的。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805055, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/c1c2subject1/463.jpeg', '机动车仪表板上（如图所示）亮表示启用地板及前风窗玻璃吹风。', '扇型的是前风窗，曲线向上表示前风窗玻璃吹风。箭头向下是地板出风。所以本题是对的。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805056, '正确', '错误', '', '', '1', '', '连续降雨天气，山区公路可能会出现路肩疏松和堤坡坍塌现象，行车时应选择道路中间坚实的路面，避免靠近路边行驶。', '山区公路连续降雨，如果开外侧，容易掉下悬崖，如果开内侧容易被塌方和落石砸到。<b>选择在道路中间行驶，不仅能最大程度避免意外情况的发生，当对面有车过来时，驾驶员也能第一时间观察到做出合适的应对。</b>', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805057, '50', '60', '30', '40', '2', '', '驾驶车辆进入高速公路加速车道后，应尽快将车速提高到每小时多少公里以上？', '高速公路加速车道最低行驶速度60公里每小时！ 《道路交通安全法实施条例》第78条规定：<b>“高速公路最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。”</b>', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805058, '依次停车等候', '鸣喇叭让前车让路', '从右侧占道转弯', '从前车左侧转弯', '1', '', '在路口右转弯遇同车道前车等候放行信号时如何行驶？', '《道路交通安全法实施条例》第五十一条：机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行，向右转弯遇有同车道前车正在等候放行信号时，依次停车等候。', 1, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (98403680805059, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/c1c2subject1/467.jpg', '这个标志的含义是指示此处设有室内停车场', '你看那个P字上边没有遮挡，说明是露天停车场，如果P字上边有遮挡就是室内停车场了。', 1, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (98403680805060, '正确', '错误', '', '', '1', '', '车辆不得在高速公路匝道上倒车。', '按照规定：驶入确定匝道后，迅速提高车速，但不得超过规定速度，在匝道上，不准超车、掉头、停车、和倒车。', 1, 1, 1, NULL, 1);
