INSERT INTO `me_question` VALUES (99559429722578, 'T型路口', '分流路口', '减速通行', '此路不通', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1088.jpg', '这个标志是何含义？', '用以指示前方道路为死胡同，无出口、不能通行。该标志为蓝底白色街区红色图案。', 1, 2, 3, NULL, 0);
