INSERT INTO `me_question` VALUES (99559429722580, '可靠弯道外侧行驶', '可短时间借用对方的车道', '可加速沿弯道切线方向通过', '应“减速、鸣喇叭、靠右行”', '4', '', '山区道路车辆进入弯道前，在对面没有来车的情况下，应怎样做？', '在急转弯处，应该减速慢行；鸣喇叭，靠右行，是为了提醒对面来车。总之，安全驾驶为主。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722581, '抢行通过', '提前加速通过', '减速避让，必要时停车让行', '按优先权规定正常行驶不予避让', '3', '', '车辆在交叉路口有优先通行权的，遇有车辆抢行时，应怎样做？', '遇到抢行车辆时，考虑到安全第一，要主动避让。', 1, 2, 3, NULL, 0);
