INSERT INTO `me_question` VALUES (99559429722583, '会车时停车让右侧车先行', '右侧道路禁止车通行', '前方是双向通行路段', '会车时停车让对方车先行', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1093.jpg', '这个标志是何含义？', '会车时停车让对方车先行：表示车辆会车时，必须停车让对方车先行。设置在会车有困难的狭窄路段的一端或由于某种原因只能开放一条车道作双向通行路段的一端。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722584, '电动自行车行驶', '非机动车停车位', '非机动车停放区', '非机动车行驶', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1094.jpg', '这个标志是何含义？', '看到自行车你就想他是非机动车就行了。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722585, '正确', '错误', '', '', '1', '', '叉车货叉定位应可靠，货叉挂钩的支承面、定位面不得有明显缺陷。', '', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722586, '公交车', '私家车', '出租车', '公务用车', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1096.jpg', '道路上划设这种标线的车道内允许下列哪类车辆通行？', '请注意地面的字，公交专用车道。所以是只有公交车才能行驶。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722587, '提前开启右转向灯', '不用开启转向灯', '提前开启左转向灯', '提前开启近光灯', '3', '', '驾驶机动车在道路上向左变更车道时如何使用灯光？', '《道路交通安全法实施条例》第五十七条,机动车应当按照下列规定使用转向灯：\r<br/><b>(一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；</b>\r<br/> (二)向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。\r<br/>根据规定，向左变更车道应该<b>提前开启左转向灯</b>，选项C符合规定，因此本题答案为C。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722588, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1098.jpg', '驾驶机动车在这种情况下不能直行和左转弯', '直行和左转都是红灯，所以不能直行和左转。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722589, '大型客车专用车道', '公交线路专用车道', '快速公交系统专用车道', '多乘员车辆专用车道', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1099.jpg', '这个标志是何含义？', '此为公交线路专用车道，与快速公交系统专用车道的区别是少了“快速公交”四个字。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722590, '傍山险路', '悬崖路段', '注意落石', '危险路段', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1100.jpg', '这个标志是何含义？', '注意落石：此标志设在左侧有落石危险的傍山路段之前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722591, '右转车道', '掉头车道', '左转车道', '分向车道', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1101.jpg', '这个标志是何含义？', '表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722592, '左转让行', '直行单行路', '向右单行路', '向左单行路', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1102.jpg', '这个标志是何含义？', '表示一切车辆向左单向行驶。此标志设在单行路的路口和入口处的适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722593, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1103.gif', '图中标志设在禁止运输危险品车辆驶入路段的入口处。', '', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722594, '道路施工提示标线', '车行道纵向减速标线', '车行道横向减速标线', '车道变少提示标线', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1104.jpg', '路面上的菱形块虚线是何含义？', '<p><img src=http://nimg.cysq.com/yxlimg/20a43532.jpg>\r<br/>横向减速标线\r<br/><img src=http://nimg.cysq.com/yxlimg/2ec73df9.jpg>\r<br/>纵向减速标线</p>', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722595, '分向行驶车道', '掉头和左转合用车道', '禁止左转和掉头车道', '直行和左转合用车道', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1105.jpg', '这个标志是何含义？', '左转和掉头合并在一个标志里，你应该能看到的。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722596, '违反道路通行规定', '超过规定时速50%', '造成交通事故后逃逸', '驾车没带驾驶证', '2', '', '下列哪种行为会受到200元以上2000元以下罚款，并处吊销机动车驾驶证？', '第九十九条　有下列行为之一的，由公安机关交通管理部门处二百元以上二千元以下罚款：\r<br/>（一）未取得机动车驾驶证、机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的；\r<br/>（二）将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的；\r<br/>（三）造成交通事故后逃逸，尚不构成犯罪的；\r<br/><b>（四）机动车行驶超过规定时速百分之五十的；（超过规定时速50%）</b>\r<br/>（五）强迫机动车驾驶人违反道路交通安全法律、法规和机动车安全驾驶要求驾驶机动车，造成交通事故，尚不构成犯罪的；\r<br/>（六）违反交通管制的规定强行通行，不听劝阻的；\r<br/>（七）故意损毁、移动、涂改交通设施，造成危害后果，尚不构成犯罪的；\r<br/>（八）非法拦截、扣留机动车辆，不听劝阻，造成交通严重阻塞或者较大财产损失的。\r<br/><b>行为人有前款第二项、第四项情形之一的，可以并处吊销机动车驾驶证</b>；有第一项、第三项、第五项至第八项情形之一的，可以并处十五日以下拘留。\r<br/>\r<br/>A选项中，违反道路通行规定的说法太宽泛了，不适用于具体的处罚标准。\r<br/>C选项中，造成交通事故逃逸的处罚是：吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。如果造成交通事故逃逸，不构成犯罪的，处罚款200以上2000以下。但选项中没有说明是否构成犯罪，因此不能判断罚款金额。\r<br/>D选项中，驾车没带驾驶证的处罚是：扣留机动车，通知驾驶人提供驾驶证。\r<br/>\r<br/>综上，本题应该选B。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722597, '确认安全后通过', '右转弯加速通过', '加速直行通过', '左转弯加速通过', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1107.jpg', '遇到这种情况的路口怎样通过？', '这个路口只有一个信号灯，也就说只有黄灯，而且一直在闪烁，表示警示，提醒过往车辆注意瞭望。并且当前路口有其他车辆转弯，有行人接近，必须确认安全后通过。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722598, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1108.jpg', '驾驶机动车在路口遇到这种信号灯亮时，要在停止线前停车�望。', '绿灯，赶紧的走你。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722599, '40公里/小时', '30公里/小时', '50公里/小时', '70公里/小时', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1109.jpg', '在这段城市道路上行驶的最高速度不能超过多少？', '《中华人民共和国道路交通安全法实施条例》<br/>第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br/>  <b>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；</b><br/>  (二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722600, '正确', '错误', '', '', '1', '', '驾驶机动车在雾天行车开启雾灯和危险报警闪光灯。', '《中华人民共和国道路交通安全法实施条例》第五十八条：机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。<b>机动车雾天行驶应当开启雾灯和危险报警闪光灯。</b>\r<br/>因此，本题答案应为正确。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722601, '快速车道', '慢速车道', '应急车道', '专用车道', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1111.jpg', '这段道路红车所在车道是什么车道？', '《道路交通安全法实施条例》第四十四条：在道路同方向划有2条以上机动车道的，左侧为快速车道，右侧为慢速车道。红色车在最左侧，所以是在快速车道。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722602, '有人看守铁路道口', '无人看守铁路道口', '多股铁路与道路相交', '注意避让火车', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1112.jpg', '这个标志是何含义？', '多股铁路与道路相交，则应在铁路道口标志上方设置叉形符号。叉形符号交叉点到警告标志三角形顶点的距离为40cm。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722603, '正确', '错误', '', '', '2', '', '叉车多路换向阀操纵手柄应能有效、迅速地开通或切断电路。', '', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722604, '禁止临时停车', '禁止长时停车', '禁止停放车辆', '允许长时停车', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1114.jpg', '这个标志是何含义？', '禁止长时停车：禁止车辆长时停放，临时停放不受限制。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722605, '减速慢行', '保持正常车速通过', '空挡滑行通过', '加速通过', '1', '', '驾驶人在行车中经过积水路面时，应怎样做？', '行车中经过积水路面时，应特别留意减速慢行；<b>加速或保持正常车速通过，溅起的水花会冲击前挡风玻璃，阻挡驾驶员的视线，</b>无法观察前方情况，极易导致车辆失控或交通事故。另外，驾驶车辆行经两侧有行人或非机动车行驶且有积水的路面时，更应减速慢行，<b>以免泥水飞溅到行人或非机动车驾驶员身上。</b>', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722606, '省道编号', '国道编号', '县道编号', '乡道编号', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1116.jpg', '这个标志是何含义？', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底的，国道是红底的。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722607, '在路口前实线区内根据需要', '进入路口实线区内', '在路口停止线前', '在虚线区按导向箭头指示', '4', '', '驾驶车辆在交叉路口前变更车道时，应怎样驶入要变更的车道？', '实线不可跨线行驶，应该在虚线区按导向箭头指示变更车道。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722608, '注意危险', '右侧绕行', '左侧绕行', '单向通行', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1118.jpg', '这个标志是何含义？', '左侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，左侧绕行通过。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722609, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1119.jpg', '在这种急弯道路上行车应交替使用远近光灯。', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。\r<br/>题目描述正确，因此本题答案应为正确。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722610, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1120.jpg', '在铁路道口遇到两个红灯交替闪烁时要停车等待。', '在铁路道口，一个红灯亮或两个红灯闪烁时都是禁止通行的，要停车等待。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722611, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1121.jpg', '遇到这种情况时，中间车道不允许车辆通行。', '中间车道是红灯，故不允许车辆通行，两边是绿灯，所以应该在两边通行。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722612, '直接起步通过', '起步后从行人后方绕过', '等行人通过后再起步', '起步后从行人前方绕过', '3', '', '行驶车道绿灯亮时，但车辆前方人行横道仍有行人行走，应怎样做？', '从前方绕、后方绕、直接通过都会惊扰到行人，有可能造成事故。正确做法是等行人通过后再起步。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722613, '正确', '错误', '', '', '2', '', '轮式专用机械车驾驶人在现场作业时可将头伸出车外。', '', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722614, '低速行驶', '注意行人', '步行', '行人先行', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1124.jpg', '这个标志是何含义？', '表示该街道只供步行。此标志设在步行街的两端。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722615, '右侧通行', '左侧通行', '向右行驶', '环岛行驶', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1125.jpg', '这个标志是何含义？', '环岛行驶：表示只准车辆靠右环行。此标志设在环岛面向路口来车方向适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722616, '禁止直行和向左变道', '禁止直行和向左转弯', '允许直行和向左变道', '禁止直行和向右转弯', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1126.jpg', '这个标志是何含义？', '禁止直行和向左转弯：表示前方路口禁止一切车辆直行和向左转弯。此标志设在禁止直行和向左转弯的路口前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722617, '露天停车场', '紧急停车带', '停车位', '错车道', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1127.jpg', '这个标志是何含义？', '错车道指的是在单车道道路上，可通视的一定距离内，供车辆交错避让用的一段加宽车道。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722618, '正确', '错误', '', '', '1', '', '机动车号牌损毁，机动车所有人要向登记地车辆管理所申请补领、换领。', '机动车号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向登记地车辆管理所申请补领、换领。申请时，机动车所有人应当填写申请表并提交身份证明。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722619, '注意野生动物', '注意牲畜', '动物公园', '开放的牧区', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1129.jpg', '这个标志是何含义？', '这个是头鹿啊，是野生动物啊。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722620, '高速公路救援电话号码', '高速公路服务电话号码', '高速公路报警电话号码', '高速公路交通广播频率', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1130.jpg', '这个标志是何含义？', '高速公路交通广播频率：用以指示收听高速公路或城市快速路交通信息广播的频率，可在适当地点设置，根据需要可重复设置。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722621, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1131.jpg', '驾驶机动车遇到这种信号灯，可在对面直行车前直接向左转弯。', '通过有交通标志标线的路口，右转弯让左转弯，转弯车让直行车。对面车辆是直行车，不能在它面前直接左转，要让行。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722622, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1132.jpg', '驾驶机动车遇到这种信号灯亮时，如果已越过停止线，可以继续通行。', '《道路交通安全法实施条例》第三十八条：黄灯亮时，已越过停止线的车辆可以继续通行；未经过停止线的则禁止继续通行。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722623, '高速公路右侧出口预告', '高速公路目的地预告', '高速公路左侧出口预告', '高速公路下一出口预告', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1133.jpg', '这个标志是何含义？', '高速公路右侧出口预告：用于预告前方出口，设置在距离高速公路或城市快速路减速车道的渐变段起点处。出口预告标志应同时附着出口编号标志。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722624, '左转弯待转信号', '靠边停车信号', '减速慢行信号', '左转弯信号', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1134.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722625, '禁止驶入路口', '禁止向右转弯', '禁止变更车道', '禁止车辆掉头', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1135.jpg', '这个标志是何含义？', '禁止向右转弯：表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722626, '禁止通行', '减速行驶', '限时进入', '禁止驶入', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1136.jpg', '这个标志是何含义？', '禁止通行：表示禁止一切车辆和行人通行。此标志设在禁止通行的道路入口处。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722627, '正确', '错误', '', '', '1', '', '在泥泞路上制动时,车轮易发生侧滑或甩尾，导致交通事故。', '常识题，泥泞路摩擦系数低。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722628, '直行和左转车道', '直行和辅路出口车道', '直行和右转合用车道', '分向行驶车道', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1138.jpg', '这个标志是何含义？', '表示只准一切车辆直行和向右转弯。此标志设在车辆必须直行和向右转弯）的路口以前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722629, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1139.jpg', '驾驶机动车要选择绿色箭头灯亮的车道行驶。', '只有绿灯是表示允许通行，机动车要选择绿色箭头灯亮的车道行驶。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722630, '最低限速50公里/小时', '高度限速50公里/小时', '水平高度50米', '海拔高度50米', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1140.jpg', '这个标志是何含义？', '表示机动车驶入前方道路之最低时速限制。此标志设在高速公路或其他道路限速路段的起点。高度限速标志是白底红字。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722632, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1142.jpg', '在这种情况下可加速通过交叉路口。', '当黄灯亮起，车身在停止线里边的，应停车等待。而且，通过交叉路口一般都要减速，确认安全再通过。\r<br/>题目描述有误，本题答案应为错误。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722633, '加速通过', '正常行驶', '连续鸣喇叭', '减速慢行', '4', '', '当驾驶车辆行经两侧有行人且有积水的路面时，应怎样做？', '文明驾驶，减速慢行，防止水溅至行人身上。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722634, '堤坝路', '临崖路', '傍水路', '易滑路', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1144.jpg', '这个标志是何含义？', '堤坝路：用以提醒车辆驾驶人小心驾驶，设在沿水库、湖泊、河流等堤坝道路以前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722635, '保持原有车速行驶', '及时减速、观察后靠右行驶让行', '靠右侧加速行驶', '不让行', '2', '', '行车中遇到后方车辆要求超车时，应怎样做？', '基于安全和和谐的因素，所以要及时减速让行。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722636, '机动车更换发动机', '加装前后防撞装置', '改变车身颜色', '更换车身或者车架', '2', '', '已注册登记的小型载客汽车有下列哪种情形，所有人不需要办理变更登记？', '《道路交通安全法实施条例》第六条：已注册登记的机动车有下列情形之一的，<b>机动车所有人应当向登记该机动车的公安机关交通管理部门申请变更登记</b>： \r<br/><b>(一)改变机动车车身颜色的； \r<br/>(二)更换发动机的； \r<br/>(三)更换车身或者车架的； </b>\r<br/>(四)因质量有问题，制造厂更换整车的； \r<br/>(五)营运机动车改为非营运机动车或者非营运机动车改为营运机动车的；\r<br/>(六)机动车所有人的住所迁出或者迁入公安机关交通管理部门管辖区域的。\r<br/>注意题目说的是 <b>[不需要办理变更登记的]</b> ，结合法规和题目要求，更换发动机、改变车身颜色、更换车身或者车架都需要办理变更登记，而B项加装前后防撞装置是不需要办理变更登记的，因此本题答案选B。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722637, '正确', '错误', '', '', '2', '', '驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的一次记6分。', '《公安部令第123号》规定，驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的，一次记12分 。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722638, '限制宽度为3.5米', '解除3.5米限高', '限制车距为3.5米', '限制高度为3.5米', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1148.jpg', '这个标志是何含义？', '限制高度为3.5米：表示禁止装载高度超过标志所示数值的车辆通行。此标志设在最大允许高度受限制的地方。 以图为例：装载高度不得超过3.5米。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722639, '正确', '错误', '', '', '2', '', '使用其他机动车号牌、行驶证的一次记3分。', '机动车驾驶人有下列违法行为之一，一次记12分：<br/>（一）驾驶与准驾车型不符的机动车的；<br/>（二）饮酒后驾驶机动车的；<br/>（三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数20％以上的；<br/>（四）造成交通事故后逃逸，尚不构成犯罪的；<br/>（五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的；<br/><b>（六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；</b><br/>（七）驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的；<br/>（八）驾驶营运客车在高速公路车道内停车的；<br/>（九）驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的；<br/>（十）连续驾驶中型以上载客汽车、危险物品运输车辆超过4小时未停车休息或者停车休息时间少于20分钟的；', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722640, '正确', '错误', '', '', '2', '', '吊车、装载机、挖掘机等车辆的作业灯应能照清前方场地情况。', '', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722641, '两侧街道', '干路先行', '停车让行', '单行路', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1151.jpg', '这个标志是何含义？', '干路指主路，一般交叉路口，行驶在干路的车辆有优先通行权，支路车辆需避让干路车辆。从支路进入干路的机动车辆在无交通警察指挥和交通信号灯的情况下要避让在干路上行驶的机动车辆，让干路上行驶的机动车辆先行，这就是“干路先行”。这是为避免交通事故而制定的交通规则。 ', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722642, '学校区域', '注意儿童', '人行横道', '注意行人', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1152.jpg', '这个标志是何含义？', '注意儿童：用以警告车辆驾驶人减速慢行，注意儿童。设在小学、幼儿园、少年宫等儿童经常出入地点前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722643, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1153.jpg', '这个地面标记的含义是预告前方设有交叉路口。', '指示标线，人行横道预警棱形图标。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722644, '正确', '错误', '', '', '1', '', '遇到路口情况复杂时，应做到“宁停三分，不抢一秒”。', '文明驾车，安全行驶。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722645, '正确', '错误', '', '', '2', '', '驾驶机动车上坡时,在将要到达坡道顶端时要加速并鸣喇叭。', '肯定是错的，鸣喇叭对，但是加速是错误的。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722646, '不能变换挡位', '可以变换挡位', '可换为高挡', '停车观察', '1', '', '驾驶车辆驶入铁路道口前减速降挡，进入道口后应怎样做？', '进路口前停车观望，进了就不能换挡，如果换挡不当，就会熄火，非常危险。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722647, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1157.jpg', '在这种环境里行车使用近光灯。', '注意观察题目图片，夜间驾驶机动车，机动车前方有人行横道，<b>路边有行人</b>。在这种情况下行车应该使用近光灯，不妨碍行人正常通行。题目描述正确，因此本题答案为对。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722648, '正确', '错误', '', '', '1', '', '一个合格的驾驶人，不仅表现在技术的娴熟上，更重要的是应该具有良好的驾驶行为习惯和道德修养。', '娴熟的技术固然重要，但没有良好的驾驶习惯和道德修养，同样会破坏正常的交通秩序。因此合格驾驶员应该具备良好的驾驶行为习惯和道德修养。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722649, '处15日以下拘留', '终生禁驾', '进行法律法规教育，重新考试', '依法追究刑事责任', '3', '', '公安机关交通管理部门对累积记分达到规定分值的驾驶人怎样处理？', '机动车驾驶人<b>在一个记分周期内累积记分达到12分的，</b>公安机关交通管理部门应当扣留其机动车驾驶证。 机动车驾驶人应当在十五日内到机动车驾驶证核发地或者违法行为地公安机关交通管理部门<b>参加为期七日的道路交通安全法律、法规和相关知识学习。</b>机动车驾驶人参加学习后，车辆管理所应当在二十日内对其进行道路交通安全法律、法规和相关知识考试。考试合格的，记分予以清除，发还机动车驾驶证。考试不合格的，继续参加学习和考试。\r<br/>因此本题答案应为D。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722650, '15公里/小时', '20公里/小时', '30公里/小时', '40公里/小时', '3', '', '驾驶机动车通过铁路道口时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 \r<br/>（一）进出非机动车道，<b>通过铁路道口</b>、急弯路、窄路、窄桥时；　　 \r<br/>（二）掉头、转弯、下陡坡时；　　 \r<br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 \r<br/>（四）在冰雪、泥泞的道路上行驶时；', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722651, '正确', '错误', '', '', '2', '', '在路口遇有交通信号灯和交通警察指挥不一致时，按照交通信号灯通行。', '《道路交通安全法》第三十八条：车辆、行人应当按照交通信号通行；<b>遇有交通警察现场指挥时，应当按照交通警察的指挥通行；</b>在没有交通信号的道路上，应当在确保安全、畅通的原则下通行。\r<br/>题目描述有误，本题答案应为错误。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722652, 'Y型交叉路口', 'T型交叉路口', '十字交叉路口', '环行交叉路口', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1162.jpg', '这个标志是何含义？', 'T型交叉路口：丁字形标志原则上设在与交叉口形状相符的道路上。右侧丁字路口，此标志设在进入T字路口以前的适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722653, '停车让行', '单行路', '会车先行', '对向先行', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1163.jpg', '这个标志是何含义？', '表示会车先行，此标志设在车道以前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722654, '禁止变道', '禁止左转', '禁止直行', '禁止掉头', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1164.jpg', '这个标志是何含义？', '禁止掉头：表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722655, '道路流量监测', '减速拍照区', '全路段抓拍', '交通监控设备', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1165.jpg', '这个标志是何含义？', '交通监控设备标志属于指路标志，蓝底、白图形、白边框、蓝色衬边。用于告知机动车驾驶人该区域设置有固定式交通监控设备。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722656, '国界', '边防', '边界', '海关', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1166.jpg', '这个标志是何含义？', '海关那俩个大字那么清晰你再不选D，D得有多伤心。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722657, '水渠', '桥梁', '隧道', '涵洞', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1167.jpg', '这个标志是何含义？', '隧道：用以提醒车辆驾驶人注意慢行。设在双向行驶并且照明不好的隧道口前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722658, '指示直行或右转弯', '指示向右转弯或掉头', '指示直行或向右变道', '指示直行或掉头', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1168.jpg', '这个导向箭头是何含义？', '直接或右转，送分题', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722659, '两侧通行', '右侧通行', '左侧通行', '不准通行', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1169.jpg', '这个标志是何含义？', '用于引导行车方向，提示道路使用者前方线形变化，注意谨慎驾驶。设于中央隔离设施端部、渠化设施的端部、桥头等。答题技巧：∧表示俩侧通行，/这个方向的表示左侧通行，这个方向表示右侧通行。交通指路标志大全：http://www.jszks.com/onepage/16752.html', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722660, '直行或向右转弯', '向右转弯', '向左转弯', '直行', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1170.jpg', '这个路口允许车辆怎样行驶？', '左转的灯是绿色，所以可以向左转弯。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722661, '加油起步', '鸣喇叭', '提高发动机转速', '观察周围交通情况', '4', '', '车辆临时靠边停车后准备起步时，应先怎样做？', '起步时要先观察路况，小心驾驶，安全第一。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722662, '注意行人', '注意儿童', '学校区域', '人行横道', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1172.jpg', '这个标志是何含义？', '注意行人：用以警告车辆驾驶人减速慢行，注意行人。设在行人密集，或不易被驾驶员发现的人行横道线以前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722663, '正确', '错误', '', '', '2', '', '起重机构的两起重链条的链节销轴与轴承孔的配合间隙不得过小。', '', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722664, '临时停靠站', '港湾式停靠站', '应急停车带', '公交车停靠站', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1174.jpg', '红色圆圈内标线含义是什么？', '在道路车行道外侧，采取局部拓宽路面的公共交通停靠站”——这就是 “港湾式停靠站”，它的基本形式是路面向人行道方向切入3米，长度90米以上形成泊车港湾，据市公交公司营运安全部黄志峰部长介绍，这种先进站点至少有三大优势：公交泊车不堵道；乘客候车安全；司机实现规范驾驶。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722665, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1175.jpg', '驾驶机动车在路口遇到这种信号灯禁止通行。', '仔细看图，图中三个圆形灯从左到右分别是红色、黄色、绿灯。此时绿灯亮起，根据“红灯停，绿灯行”的原则，车辆是可以通行的。题目描述错误，本题答案为错。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722666, '向右转弯', '单行路', '只准直行', '直行车道', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1176.jpg', '这个标志是何含义？', '向右转弯：表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722667, '注意长时鸣喇叭', '无人看守铁路道口', '有人看守铁路道口', '多股铁路与道路相交', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1177.jpg', '这个标志是何含义？', '有火车形状的，为无人看守的铁路口，还有一个栅栏形式的图片为有人看守的铁路口。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722668, '人行横道灯', '注意行人', '注意信号灯', '交叉路口', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1178.jpg', '这个标志是何含义？', '注意信号灯：用以警告车辆驾驶人注意前方路段设有信号灯，应依信号灯指示行车。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722669, '正确', '错误', '', '', '2', '', '轮式专用机械车下陡坡时，最高行驶速度不得超过每小时30公里。', '正确答案应该是15公里。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722670, '十字交叉路口', '环行交叉路口', 'T型交叉路口', 'Y型交叉路口', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1180.jpg', '这个标志是何含义？', '十字交叉路口：除了基本形十字路口外，还有部分变异的十字路口，如：五路交叉路口、变形十字路口、变形五路交叉路口等。五路以上的路口均按十字路口对待。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722671, '禁止长时鸣喇叭', '断续鸣喇叭', '减速鸣喇叭', '禁止鸣喇叭', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1181.jpg', '这个标志是何含义？', '禁止鸣喇叭：表示禁止鸣喇叭。此标志设在需要禁止鸣喇叭的地方。禁止鸣喇叭的时间和范围可用辅助标志说明。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722672, '人行横道', '学生通道', '注意行人', '儿童通道', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1182.jpg', '这个标志是何含义？', '表示该处为专供行人横穿马路的通道。此标志设在人行横道的两侧。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722673, '禁止借道', '禁止变道', '禁止超车', '禁止掉头', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1183.jpg', '这个标志是何含义？', '禁止超车：表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722674, '加速进入两侧车道行驶', '进入右侧车道行驶', '减速进入两侧车道行驶', '禁止车辆在两侧车道通行', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1184.jpg', '遇到这种情况时怎样行驶？', '两侧车道都是红灯，都不可以通行，所以是禁止车辆在两侧车道通行。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722675, '易滑路段', '急转弯路', '试车路段', '曲线路段', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1185.jpg', '这个标志是何含义？', '易滑路段：此标志设在路面的摩擦系数不能满足相应行驶速度下要求紧急刹车距离的路段前适当位置。行驶至此路段必须减速慢行。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722676, '倒车灯开关', '刮水器开关', '危险报警闪光灯开关', '灯光、信号组合开关', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1186.jpg', '这是什么操纵装置？', '左手边控制灯光、信号组合开关，右手边控制刮水器开关。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722678, '开启转向灯的同时变更车道', '在道路同方向划有2条以上机动车道的，不得影响相关车道内行驶的机动车的正常行驶', '在车辆较少路段，可以随意变更车道', '遇前方道路拥堵，可以向应急车道变更', '2', '', '驾驶机动车变更车道时，以下做法正确的是什么？', '《道路交通安全法》规定：驾驶机动车变更车道时，在道路同方向划有2条以上机动车道的，不得影响相关车道内行驶的机动车的正常行驶。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722679, '驻车制动器操纵杆', '节气门操纵杆', '变速器操纵杆', '离合器操纵杆', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1189.jpg', '这是什么操纵装置？', '变速器操纵杆也就是换挡的，当我们速度越开越快的时候也要对应上相应的档位，故此为变速器操纵杆。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722680, '转向灯开关', '前照灯开关', '刮水器开关', '除雾器开关', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1190.jpg', '这是什么操纵装置？', '你看那些曲线，代表的就是雾气。题目扩展：上边的按钮是前挡风除雾，下边的按钮是后挡风除雾。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722681, '指示向左变道', '指示前方直行', '指示前方掉头', '指示前方右转', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1191.jpg', '这个导向箭头是何含义？', '本题箭头是掉头的箭头，那么本题是指示前方掉头。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722682, '主要街道', '单向行驶路段', '交通流量大的路段', '单向两条行车道', '3', '', '驾驶机动车行经市区下列哪种道路时不得超车？', '《中华人民共和国道路交通安全法》第四十三条：<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br/>（一）前车正在左转弯、掉头、超车的；<br/>（二）与对面来车有会车可能的；<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；<br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、<b>市区交通流量大的路段</b>等没有超车条件的。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722683, '正确', '错误', '', '', '2', '', '车辆在高速公路匝道上可以停车。', '按照规定：驶入确定匝道后，迅速提高车速，但不得超过规定速度，<b>在匝道上，不准超车、掉头、停车、和倒车。</b>', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722684, '正确', '错误', '', '', '2', '', '全挂车倒车时转向盘转动方向与单车倒车方向相反。', '全挂车特点:本身无动力，独立承载，依靠其他车辆牵引行驶的车辆.半挂车特点:本身无动力，与主车共同承载，依靠主车牵引行驶的车辆', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722685, '正确', '错误', '', '', '2', '', '驾驶人实习期内可以单独驾驶大型客车、电车、起重车、牵引车。', '注意单独2字，关键就在这', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722686, '3年内', '终身', '1年内', '5年内', '1', '', '以欺骗、贿赂等不正当手段取得驾驶证被依法撤销驾驶许可的，多长时间不得重新申请驾驶许可？', '吊二撤三醉五逃终身。\r<br/>根据公安部令第123号规定：申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。\r<br/><b>申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可；申请人在三年内不得再次申领机动车驾驶证。</b>', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722687, '使用远光灯，有利于观察路面情况', '禁止使用远光灯，避免灯光照射至前车后视镜造成前车驾驶人眩目', '使用远光灯，有利于告知前方驾驶人后方有来车', '禁止使用远光灯，避免灯光照射至前车后视镜造成自己眩目', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1197.jpg', '如图所示，夜间驾驶机动车与同方向行驶的前车距离较近时，以下做法正确的是什么？', '夜间驾驶机动车与同方向行驶的前车距离较近时，禁止使用远光灯，避免灯光照射至前车后视镜造成前车驾驶人眩目。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722689, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1199.jpg', '在道路上遇到这种情况可以从两侧超车。', '《中华人民共和国道路交通安全法》第四十三条：<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br/>（一）前车正在左转弯、掉头、超车的；<br/>（二）与对面来车有会车可能的；<br/><b>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；</b><br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722690, '车辆可临时跨越', '禁止车辆跨越', '机动车可临时跨越', '非机动车可临时跨越', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1200.jpg', '路两侧的车行道边缘白色实线是什么含义？', '<p><img src=http://tp.mnks.cn/tp_292.jpg><br/>禁止跨越白色实线</p><p><img src=http://tp.mnks.cn/tp_293.jpg><br/>允许跨越白色虚线</p>', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722691, '不靠山体的一方先行', '靠山体的一方先行', '重车让空车先行', '速度慢的先行', '1', '', '驾驶机动车在没有道路中心线的狭窄山路怎样会车？', '在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：\r<br/>1)减速靠右行驶，并与其他车辆、行人保持必要的安全距离。\r<br/>2)在有障碍的路段，无障碍的一方先行：但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。\r<br/>3)在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行。\r<br/>4)<b>在狭窄的山路，不靠山体的一方先行。</b>\r<br/>5)夜间会车应当在距相对方向来车150m以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722692, '禁止在路口掉头', '禁止向左向右变道', '禁止向左向右转弯', '禁止车辆直行', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1202.jpg', '这个标志是何含义？', '禁止向左向右转弯：表示前方路口禁止一切车辆向左向右转弯。此标志设在禁止向左向右转弯的路口前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722693, '施工路段绕行', '双向交通', '左右绕行', '注意危险', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1203.jpg', '这个标志是何含义？', '左右绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，绕行通过。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722694, '右侧是下坡路段', '靠右侧道路行驶', '靠道路右侧停车', '只准向右转弯', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1204.jpg', '这个标志是何含义？', '靠右侧道路行驶：表示只准一切车辆靠右侧道路行驶。此标志设在车辆必须靠右侧行驶的路口以前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722695, '向右急转弯', '向右绕行', '连续弯路', '向左急转弯', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1205.jpg', '这个标志是何含义？', '向右急转弯：向右急弯路标志，设在右急转弯的道路前方适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722696, '省道编号', '国道编号', '县道编号', '乡道编号', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1206.jpg', '这个标志是何含义？', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底的，国道是红底的。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722697, '正确', '错误', '', '', '2', '', '黄灯持续闪烁，表示机动车可以加速通过。', '黄灯持续闪烁，表明已经越过停止线的车辆可以行使，没越过停止线的车辆要停车等待，机动车不能加速通过。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722698, '避险车道', '应急车道', '路肩', '急弯道', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1208.jpg', '这个标志是何含义？', '避险车道是指在长陡下坡路段行车道外侧增设的供速度失控车辆驶离正线安全减速的专用车道。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722699, '距无人看守铁路道口150米', '距无人看守铁路道口100米', '距有人看守铁路道口100米', '距有人看守铁路道口150米', '1', 'http://images.juheapi.com/jztk/a2b2subject1/1209.jpg', '这个标志是何含义？', '一道红线是50米，三道150米。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722700, '堤坝路', '傍山险路', '落石路', '临崖路', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1210.jpg', '这个标志是何含义？', '傍山险路：用以提醒车辆驾驶人小心驾驶。设在傍山险路路段以前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722701, '路面不平', '路面低洼', '路面高突', '有驼峰桥', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1211.jpg', '这个标志是何含义？', '路面低洼：用以提醒车辆驾驶人减速慢行。设在路面突然低洼以前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722702, '限制车辆通行', '提示限速信息', '提供方向信息', '警告前方危险', '3', '', '指路标志的作用是什么？', '指路标志是传递道路方向，地点，距离信息的标志。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722703, '减速慢行', '注意危险', '拥堵路段', '事故多发路段', '2', 'http://images.juheapi.com/jztk/a2b2subject1/1213.jpg', '这个标志是何含义？', '注意危险：用以促使车辆驾驶员谨慎慢行。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722704, '进入路口等待', '左转弯行驶', '加速直行通过', '不得越过停止线', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1214.jpg', '驾驶机动车在路口直行遇到这种信号灯应该怎样行驶？', '亮红灯时，车辆应该在停止线内停车等待。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722705, '靠道路左侧停车', '左侧是下坡路段', '只准向左转弯', '靠左侧道路行驶', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1215.jpg', '这个标志是何含义？', '靠左侧道路行驶：表示只准一切车辆靠左侧道路行驶。此标志设在车辆必须靠左侧行驶的路口以前适当位置。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722706, '40米减速行驶路段', '最低时速40公里', '最高时速40公里', '解除时速40公里限制', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1216.jpg', '这个标志是何含义？', '解除时速40公里限制：表示限制速度路段结束。此标志设在限制车辆速度路段的重点。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722707, '正确', '错误', '', '', '1', '', '驾驶人一边驾车，一边打手持电话是违法行为。', '新修订的《机动车驾驶证申领和使用规定》公布后，2013年1月1日起施行，其中规定：驾驶机动车有拨打、接听手持电话等妨碍安全驾驶的行为的一次记2分。并且违反了《道路交通安全法》的相关规定，属于违法行为。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722708, '泥泞道路', '低洼路面', '过水路面', '渡口', '3', 'http://images.juheapi.com/jztk/a2b2subject1/1218.jpg', '这个标志是何含义？', '过水路面指的是通过平时无水或流水很少的宽浅河流而修筑的在洪水期间容许水流浸过的路面。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722709, '堤坝路', '上陡坡', '连续上坡', '下陡坡', '4', 'http://images.juheapi.com/jztk/a2b2subject1/1219.jpg', '这个标志是何含义？', '下陡坡：用以提醒车辆驾驶人小心驾驶。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722710, '前车减速让行', '前车正在左转弯', '前车靠边停车', '前车正在右转弯', '2', '', '驾驶机动车在下列哪种情形下不能超越前车？', '《道路交通安全法》第四十三条： <br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： <br/><b>（一）前车正在左转弯、掉头、超车的； </b><br/>（二）与对面来车有会车可能的； <br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722711, '正确', '错误', '', '', '1', '', '驾驶人发现轮胎漏气，将车辆驶离主车道时，不要采用紧急制动，以免造成翻车或后车采取制动不及时导致追尾事故。', '轮胎漏气时不能采取紧急制动，容易造成翻车。应控制住方向，慢慢停车。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722712, '50公里/小时', '60公里/小时', '30公里/小时', '40公里/小时', '3', '', '驾驶机动车下陡坡、转弯、掉头时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br/>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br/>（二）<b>掉头、转弯、下陡坡</b>时；　　 <br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br/>（四）在冰雪、泥泞的道路上行驶时；', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722714, '5年内', '10年内', '20年内', '终生', '4', '', '机动车驾驶人造成事故后逃逸构成犯罪的，吊销驾驶证且多长时间不得重新取得驾驶证？', '《道路交通安全法》第一百零一条规定：<b>造成交通事故后逃逸的且构成犯罪的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。</b>上述规定泛指各类交通事故，只要是造成交通事故后逃逸的，就应当由公安机关交通管理部门吊销其机动车驾驶证，并且终身不得重新取得驾驶证，而不论其造成交通事故后果的大小。也就是说，法律规定惩罚的交通肇事逃逸行为，与驾驶人造成交通事故后果的轻重无关；构成犯罪的，依法追究刑事责任。', 1, 2, 3, NULL, 0);
INSERT INTO `me_question` VALUES (99559429722715, '正确', '错误', '', '', '2', '', '驾驶人一边驾车，一边吸烟对安全行车无影响。', '驾驶车辆吸烟不仅对车上乘客有害，也会让驾驶员分心，对安全行车是有影响的。', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (99559429722716, '正确', '错误', '', '', '1', '', '起重机构的两起重链条张紧度应均匀，不得扭曲变形，端部连接牢靠。', '', 1, 2, 3, NULL, 1);
INSERT INTO `me_question` VALUES (101461567496193, '右侧通行', '左侧通行', '向右行驶', '环岛行驶', '4', 'http://images.juheapi.com/jztk/a1b1subject1/1.jpg', '这个标志是何含义？', '环岛行驶：表示只准车辆靠右环行。此标志设在环岛面向路口来车方向适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461567496194, '右转车道', '掉头车道', '左转车道', '分向车道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/2.jpg', '这个标志是何含义？', '表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461567496195, '禁止借道', '禁止变道', '禁止超车', '禁止掉头', '3', 'http://images.juheapi.com/jztk/a1b1subject1/3.jpg', '这个标志是何含义？', '禁止超车：表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461567496196, '立体交叉直行和左转弯行驶', '立体交叉直行和右转弯行驶', '直行和左转弯行驶', '直行和右转弯行驶', '1', 'http://images.juheapi.com/jztk/a1b1subject1/4.jpg', '这个标志是何含义？', '立体交叉直行和左转弯行驶：表示车辆在立交处可以直行和按图示路线左转弯行驶。此标志设在立交左转弯出口处适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461567496197, '观景台', '停车场', '休息区', '停车位', '1', 'http://images.juheapi.com/jztk/a1b1subject1/5.jpg', '这个标志是何含义？', '观景台那三个字多明显，你再不选它，叫它情何以堪。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461567496198, '高速公路报警电话', '高速公路公用电话', '高速公路紧急电话', '高速公路救援电话', '4', 'http://images.juheapi.com/jztk/a1b1subject1/6.jpg', '这个标志是何含义？', '高速公路救援电话：用于指示救援电话号码，没有设置紧急电话的高速公路上应设置救援电话标志。标志上号码为示例。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461567496199, '机动车驾驶证', '居民身份证', '机动车通行证', '从业资格证', '1', '', '驾驶人未携带哪种证件驾驶机动车上路，交通警察可依法扣留车辆？', '《道路交通安全违法行为处理程序规定》第十三条 “有下列情形之一的，因无其他机动车驾驶人代替驾驶、违法行为尚未消除、需要调查或者证据保全等原因不能立即放行的，可以扣留车辆：    （一）未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未携带行驶证、机动车驾驶证的；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461567496200, '施工路段绕行', '双向交通', '左右绕行', '注意危险', '3', 'http://images.juheapi.com/jztk/a1b1subject1/8.jpg', '这个标志是何含义？', '左右绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，绕行通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461567496201, '驼峰桥', '路面高突', '路面低洼', '不平路面', '1', 'http://images.juheapi.com/jztk/a1b1subject1/9.jpg', '这个标志是何含义？', '驼峰桥：用以提醒车辆驾驶人谨慎驾驶。设在拱度很大，影响视距的驼峰桥以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461567496202, '指路标志', '指示标志', '禁令标志', '警告标志', '1', 'http://images.juheapi.com/jztk/a1b1subject1/10.jpg', '这属于哪一类标志？', '他是指方向的故为指路标志。', 1, 3, 4, NULL, 0);
