INSERT INTO `me_question` VALUES (101461567496206, '禁止长时鸣喇叭', '断续鸣喇叭', '减速鸣喇叭', '禁止鸣喇叭', '4', 'http://images.juheapi.com/jztk/a1b1subject1/14.jpg', '这个标志是何含义？', '禁止鸣喇叭：表示禁止鸣喇叭。此标志设在需要禁止鸣喇叭的地方。禁止鸣喇叭的时间和范围可用辅助标志说明。', 1, 3, 4, NULL, 0);
