INSERT INTO `me_question` VALUES (101461588467713, '电动自行车行驶', '非机动车停车位', '非机动车停放区', '非机动车行驶', '4', 'http://images.juheapi.com/jztk/a1b1subject1/16.jpg', '这个标志是何含义？', '看到自行车你就想他是非机动车就行了。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467714, '高速公路收费处', '高速公路检查站', '设有ETC的收费站', '高速公路领卡处', '3', 'http://images.juheapi.com/jztk/a1b1subject1/17.jpg', '这个标志是何含义？', '设有ETC的收费站：用以指示前方收费站。设在高速公路、城市快速路主线上渐变段起点处。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467715, '易滑路段', '急转弯路', '试车路段', '曲线路段', '1', 'http://images.juheapi.com/jztk/a1b1subject1/18.jpg', '这个标志是何含义？', '易滑路段：此标志设在路面的摩擦系数不能满足相应行驶速度下要求紧急刹车距离的路段前适当位置。行驶至此路段必须减速慢行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467716, '高速公路服务区预告', '高速公路避险处预告', '高速公路客车站预告', '高速公路停车场预告', '4', 'http://images.juheapi.com/jztk/a1b1subject1/19.jpg', '这个标志预告什么？', '<p><img src=http://tp.mnks.cn/tp_283.jpg>\r<br/>高速公路停车区\r<br/><img src=http://tp.mnks.cn/tp_284.jpg>\r<br/>高速公路停车场\r<br/><img src=http://tp.mnks.cn/tp_282.jpg>\r<br/>高速公路服务区预告</p>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467717, '开启危险报警闪光灯', '开启车上所有灯光', '禁止车上人员下车', '在车前方设置警告标志', '1', '', '机动车在道路上发生故障，难以移动时下列做法正确的是什么？', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467718, '注意长时鸣喇叭', '无人看守铁路道口', '有人看守铁路道口', '多股铁路与道路相交', '2', 'http://images.juheapi.com/jztk/a1b1subject1/21.jpg', '这个标志是何含义？', '有火车形状的，为无人看守的铁路口，还有一个栅栏形式的图片为有人看守的铁路口。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467719, '正确', '错误', '', '', '2', '', '驾驶人驾驶有达到报废标准嫌疑机动车上路的，交通警察依法予以拘留。', '1、根据《中华人民共和国道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证。\r<br/>2、题目中表述的是“达到报废标准嫌疑机动车”，并未确定为已经报废，按规定无须处罚。\r<br/>因此，题目所说的“交通警察依法予以拘留”是不正确的，本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467720, '距无人看守铁路道口150米', '距无人看守铁路道口100米', '距有人看守铁路道口100米', '距有人看守铁路道口150米', '1', 'http://images.juheapi.com/jztk/a1b1subject1/23.jpg', '这个标志是何含义？', '一道红线是50米，三道150米。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467721, '正确', '错误', '', '', '2', '', '驾驶小型汽车下陡坡时允许熄火滑行。', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在下陡坡时熄火或者空挡滑行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467722, '2分', '3分', '6分', '12分', '4', '', '上道路行驶的机动车故意遮挡、污损、不按规定安装机动车号牌的一次记几分？', '《公安部令第123号》规定，上道路行驶的机动车未悬挂机动车号牌的，<b>或者故意遮挡、污损、不按规定安装机动车号牌的，一次记12分 。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467723, '小型汽车专用车道', '机动车专用车道', '出租汽车专用车道', '多乘员车辆专用车道', '4', 'http://images.juheapi.com/jztk/a1b1subject1/26.jpg', '这个标志是何含义？', '此标志为多乘员车辆专用车道，比机动车专用车道标志上的小车多了2个小人，所以它是多乘员车辆专用车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467724, '2年以下', '3年以下', '7年以下', '7年以上', '4', '', '驾驶人违反交通运输管理法规发生重大事故后，因逃逸致人死亡的，处多少年有期徒刑？', '《中华人民共和国刑法》第一百三十三条：\r<br/>违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；\r<br/>交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；\r<br/><b>因逃逸致人死亡的，处七年以上有期徒刑。</b>\r<br/>在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。\r<br/>有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467725, '正确', '错误', '', '', '1', '', '机动车达到国家规定的强制报废标准的不能办理注册登记。', '《机动车登记规定》有下列情形之一的，不予办理注册登记：机动车达到国家规定的强制报废标准的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467726, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/29.jpg', '驾驶机动车行驶到这个位置时，如果车前轮已越过停止线可以继续通过。', '车全部越过停车线才可以继续通过，红灯时前轮刚过线必须停下来，否则就是闯红灯。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467727, '6分', '3分', '2分', '12分', '4', '', '使用伪造、变造的行驶证一次记几分？', '《公安部令第123号》规定，使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的，一次记12分 。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467728, '车辆可临时跨越', '禁止车辆跨越', '机动车可临时跨越', '非机动车可临时跨越', '2', 'http://images.juheapi.com/jztk/a1b1subject1/31.jpg', '路两侧的车行道边缘白色实线是什么含义？', '<p><img src=http://tp.mnks.cn/tp_292.jpg><br/>禁止跨越白色实线</p><p><img src=http://tp.mnks.cn/tp_293.jpg><br/>允许跨越白色虚线</p>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467729, '3分', '2分', '12分', '6分', '3', '', '造成交通事故后逃逸，尚不构成犯罪的一次记几分？', '《公安部令第123号》附件2，造成交通事故后逃逸，尚不构成犯罪的，一次记12分 。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467730, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/33.jpg', '驾驶机动车在路口看到这种信号灯亮时，要加速通过。', '《道路交通安全法实施条例》第三十八条：黄灯亮时，已越过停止线的车辆可以继续通行；未经过停止线的则禁止继续通行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467731, '有人看守铁路道口', '无人看守铁路道口', '多股铁路与道路相交', '注意避让火车', '3', 'http://images.juheapi.com/jztk/a1b1subject1/34.jpg', '这个标志是何含义？', '多股铁路与道路相交，则应在铁路道口标志上方设置叉形符号。叉形符号交叉点到警告标志三角形顶点的距离为40cm。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467732, '正确', '错误', '', '', '1', '', '伪造、变造或者使用伪造、变造驾驶证的驾驶人构成犯罪的，将依法追究刑事责任。', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467733, '停车让行', '不准临时停车', '不准车辆驶入', '不准长时间停车', '1', 'http://images.juheapi.com/jztk/a1b1subject1/36.jpg', '这个标志是何含义？', '停车让行：表示车辆必须在停止线以外停车了望，确认安全后，才准许通行。停车让行标志在下列情况下设置：(1)与交通流量较大的干路平交的支路路口；(2)无人看守的铁路道口；(3)其他需要设置的地方。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467734, '直行车道', '只准直行', '单行路', '禁止直行', '2', 'http://images.juheapi.com/jztk/a1b1subject1/37.jpg', '这个标志是何含义？', '只准直行：表示只准一切车辆直行。此标志设在直行的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467735, '人行横道灯', '注意行人', '注意信号灯', '交叉路口', '3', 'http://images.juheapi.com/jztk/a1b1subject1/38.jpg', '这个标志是何含义？', '注意信号灯：用以警告车辆驾驶人注意前方路段设有信号灯，应依信号灯指示行车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467736, '堤坝路', '傍山险路', '落石路', '临崖路', '2', 'http://images.juheapi.com/jztk/a1b1subject1/39.jpg', '这个标志是何含义？', '傍山险路：用以提醒车辆驾驶人小心驾驶。设在傍山险路路段以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467737, '减速让行', '潮汐车道', '分离式道路', '双向交通', '4', 'http://images.juheapi.com/jztk/a1b1subject1/40.jpg', '这个标志是何含义？', '双向交通：用以提醒车辆驾驶人注意会车。设在由双向分离行驶，因某种原因出现临时性或永久性的不分离双向行驶的路段，或由单向行驶进人双向行驶的路段以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467738, '18周岁以上60周岁以下', '18周岁以上70周岁以下', '24周岁以上70周岁以下', '21周岁以上50周岁以下', '2', '', '申请小型汽车准驾车型驾驶证的人年龄条件是多少？', '新交规 公安部123号令\r<br/><b>1、申请小型汽车、小型自动挡汽车、残疾人专用小型自动挡载客汽车、轻便摩托车准驾车型的，在18周岁以上、70周岁以下；</b> 　　\r<br/>2、申请低速载货汽车、三轮汽车、普通三轮摩托车、普通二轮摩托车或者轮式自行机械车准驾车型的，在18周岁以上，60周岁以下； \r<br/>3、申请城市公交车、大型货车、无轨电车或者有轨电车准驾车型的，在20周岁以上，50周岁以下； \r<br/>4、申请中型客车准驾车型的，在21周岁以上，50周岁以下； 　　\r<br/>5、申请牵引车准驾车型的，在24周岁以上，50周岁以下； 　　\r<br/>6、申请大型客车准驾车型的，在26周岁以上，50周岁以下。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467739, '车多路段', '慢行', '拥堵路段', '施工路段', '2', 'http://images.juheapi.com/jztk/a1b1subject1/42.jpg', '这个标志是何含义？', '慢行：此标志设在前方需要减速慢行的路段以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467740, '高速公路客车站预告', '高速公路避险处预告', '高速公路服务区预告', '高速公路收费站预告', '3', 'http://images.juheapi.com/jztk/a1b1subject1/43.jpg', '这个标志是何含义？', '高速公路服务区预告：用于预告服务区的位置，设在减速车道起点处。表示一般服务区预告标志。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467741, '正确', '错误', '', '', '1', '', '驾驶机动车在高速公路要按照限速标志标明的车速行驶。', '由于高速公路上车速较快，危险性较高。为了保障安全，一定要严格按照限速标志的车速行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467742, '注意双向行驶', '靠两侧行驶', '注意潮汐车道', '可变车道', '3', 'http://images.juheapi.com/jztk/a1b1subject1/45.jpg', '这个标志是何含义？', '“潮汐车道”是指根据早晚交通流量不同情况，对有条件的道路，试点开辟潮汐车道，通过车道灯的指示方向变化，控制主干道车道行驶方向，来调整车道数。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467743, '高速公路停车区预告', '高速公路避险处预告', '高速公路停车场预告', '高速公路服务区预告', '1', 'http://images.juheapi.com/jztk/a1b1subject1/46.jpg', '这个标志是何含义？', '高速公路停车区是有茶杯的，高速公路停车场是只有一个P。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467744, '掉头', '倒车', '左转', '绕行', '1', 'http://images.juheapi.com/jztk/a1b1subject1/47.jpg', '这个标志是何含义？', '允许掉头标志', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467745, '左转行驶车道', '直线行驶车道', '右转行驶车道', '分向行驶车道', '4', 'http://images.juheapi.com/jztk/a1b1subject1/48.jpg', '这个标志是何含义？', '同向多车道的公路一般都设计了分向行驶车道，正常行驶过程中可以变更车道，当你行驶至路口时要变换到你要行驶的车道。例如要左转弯时要提前变更车道至左转弯车道；要右转弯时要提前变更车道至右转弯车道，不能占用他人行车道，以最大限度的保障通行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467746, '注意野生动物', '注意牲畜', '动物公园', '开放的牧区', '1', 'http://images.juheapi.com/jztk/a1b1subject1/49.jpg', '这个标志是何含义？', '这个是头鹿啊，是野生动物啊。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467747, '高速公路下一出口预告', '高速公路右侧出口预告', '高速公路左侧出口预告', '高速公路目的地预告', '3', 'http://images.juheapi.com/jztk/a1b1subject1/50.jpg', '这个标志是何含义？', '高速公路左侧出口预告：用于预告前方出口，在距离高速公路或城市快速路减速车道的渐变段起点处。出口预告标志应同时附着出口编号标志。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467748, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/51.jpg', '遇到这种情况时，中间车道不允许车辆通行。', '中间车道是红灯，故不允许车辆通行，两边是绿灯，所以应该在两边通行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467749, '不得少于20米', '不得少于10米', '不得少于50米', '不得少于30米', '3', '', '驾驶小型载客汽车在高速公路上时速低于100公里时的最小跟车距离是多少？', '根据《中华人民共和国道路交通安全法实施条例》第八十条 机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离，车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467750, '向左单行路', '向右单行路', '直行单行路', '右转让行', '2', 'http://images.juheapi.com/jztk/a1b1subject1/53.jpg', '这个标志是何含义？', '表示一切车辆向右单向行驶。此标志设在单行路的路口和入口处的适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467751, '正确', '错误', '', '', '1', '', '机动车驾驶人在实习期内有记满12分记录的，注销其实习的准驾车型驾驶资格。', '公安部123号令规定：<br/>第六十四条 机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的12个月为实习期。<br/>第六十九条 <b>机动车驾驶人在实习期内有记满12分记录的，注销其实习的准驾车型驾驶资格。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467752, '允许临时停车', '允许长时停车', '禁止长时停车', '禁止停放车辆', '4', 'http://images.juheapi.com/jztk/a1b1subject1/55.jpg', '这个标志是何含义？', '禁止停放车辆：表示在限定的范围内，禁止一切车辆临时或长时停放。此标志设在禁止车辆停放的地方。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467753, '停车领卡', '停车缴费', 'ETC通道', '停车检查', '1', 'http://images.juheapi.com/jztk/a1b1subject1/56.jpg', '这个标志是何含义？', '停车领卡：用以提示停车领卡，设在进入高速公路或城市快速路收费站入口一侧的适当位置。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467754, '禁止驶入路口', '禁止向右转弯', '禁止变更车道', '禁止车辆掉头', '2', 'http://images.juheapi.com/jztk/a1b1subject1/57.jpg', '这个标志是何含义？', '禁止向右转弯：表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467755, '会车时停车让右侧车先行', '右侧道路禁止车通行', '前方是双向通行路段', '会车时停车让对方车先行', '4', 'http://images.juheapi.com/jztk/a1b1subject1/58.jpg', '这个标志是何含义？', '会车时停车让对方车先行：表示车辆会车时，必须停车让对方车先行。设置在会车有困难的狭窄路段的一端或由于某种原因只能开放一条车道作双向通行路段的一端。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467756, '正确', '错误', '', '', '1', '', '驾驶人有使用其他车辆保险标志嫌疑的，交通警察可依法扣留车辆。', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：有伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、 保险标志、驾驶证或者使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467757, '出租车专用上下客停车位', '平行式停车位', '倾斜式停车位', '垂直式停车位', '2', 'http://images.juheapi.com/jztk/a1b1subject1/60.jpg', '这种白色矩形标线框含义是什么？', '路边的平行的停车位，亲 侧方停车以后要好好学哦~', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467758, '道路施工提示标线', '车行道横向减速标线', '车行道纵向减速标线', '车道变少提示标线', '2', 'http://images.juheapi.com/jztk/a1b1subject1/61.jpg', '路面上的白色标线是何含义？', '<p><img src=http://nimg.cysq.com/yxlimg/20a43532.jpg>\r<br/>横向减速标线\r<br/><img src=http://nimg.cysq.com/yxlimg/2ec73df9.jpg>\r<br/>纵向减速标线</p>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467759, '两侧街道', '干路先行', '停车让行', '单行路', '2', 'http://images.juheapi.com/jztk/a1b1subject1/62.jpg', '这个标志是何含义？', '干路指主路，一般交叉路口，行驶在干路的车辆有优先通行权，支路车辆需避让干路车辆。从支路进入干路的机动车辆在无交通警察指挥和交通信号灯的情况下要避让在干路上行驶的机动车辆，让干路上行驶的机动车辆先行，这就是“干路先行”。这是为避免交通事故而制定的交通规则。 ', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467760, '禁止跨越对向车道中心线', '限制跨越对向车道中心线', '单向行驶车道分界中心线', '可跨越同向车道中心线', '4', 'http://images.juheapi.com/jztk/a1b1subject1/63.jpg', '路中白色虚线是什么标线？', '白色表示同向两车道之间的标线，虚线可跨越超车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467761, '禁止直行和向左变道', '禁止直行和向左转弯', '允许直行和向左变道', '禁止直行和向右转弯', '4', 'http://images.juheapi.com/jztk/a1b1subject1/64.jpg', '这个标志是何含义？', '禁止直行和向右转弯：表示前方路口禁止一切车辆直行和向右转弯。此标志设在禁止直行和向右转弯的路口前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467762, '指示直行或右转弯', '指示向右转弯或掉头', '指示直行或向右变道', '指示直行或掉头', '1', 'http://images.juheapi.com/jztk/a1b1subject1/65.jpg', '这个导向箭头是何含义？', '直接或右转，送分题', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467763, '正确', '错误', '', '', '2', '', '交通警察对未放置保险标志上道路行驶的车辆可依法扣留行驶证。', '《道路交通安全法》第九十五条：<b>上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。</b>当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。\r<br/>根据规定，对于不放置保险标志上路的处罚，扣留的是机动车，并不会扣留行驶证。因此题目描述有误，本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467764, '指示前方可左转或掉头', '指示前方可直行或左转', '指示前方直行向左变道', '指示前方可直行或掉头', '4', 'http://images.juheapi.com/jztk/a1b1subject1/67.jpg', '这个导向箭头是何含义？', '直行或者掉头都可以', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467765, '国界', '边防', '边界', '海关', '4', 'http://images.juheapi.com/jztk/a1b1subject1/68.jpg', '这个标志是何含义？', '海关那俩个大字那么清晰你再不选D，D得有多伤心。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467766, '最低限速50公里/小时', '高度限速50公里/小时', '水平高度50米', '海拔高度50米', '1', 'http://images.juheapi.com/jztk/a1b1subject1/69.jpg', '这个标志是何含义？', '表示机动车驶入前方道路之最低时速限制。此标志设在高速公路或其他道路限速路段的起点。高度限速标志是白底红字。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467767, '右转车道', '掉头车道', '左转车道', '直行车道', '4', 'http://images.juheapi.com/jztk/a1b1subject1/70.jpg', '这个标志是何含义？', '表示只准一切车辆直行。此标志设在直行的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467768, '禁止通行', '减速行驶', '限时进入', '禁止驶入', '1', 'http://images.juheapi.com/jztk/a1b1subject1/71.jpg', '这个标志是何含义？', '禁止通行：表示禁止一切车辆和行人通行。此标志设在禁止通行的道路入口处。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467769, '高速公路右侧出口预告', '高速公路下一出口预告', '高速公路地点、方向预告', '高速公路左侧出口预告', '3', 'http://images.juheapi.com/jztk/a1b1subject1/72.jpg', '这个标志是何含义？', '高速公路地点、方向预告：用于指示高速公路或城市快速路两个行驶方向，设在驶入高速公路或城市快速路的匝道分岔点处。在目的地信息之上，可增加前往高速公路的编号信息。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467770, '高速公路紧急停车带', '高速公路避让处所', '高速公路客车站', '高速公路停车区', '1', 'http://images.juheapi.com/jztk/a1b1subject1/73.jpg', '这个标志是何含义？', '高速公路紧急停车带：用于指示紧急停车的位置，设在紧急停车带的前端。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467771, '小型车转弯线', '车道连接线', '非机动车引导线', '路口导向线', '4', 'http://images.juheapi.com/jztk/a1b1subject1/74.jpg', '图中圈内白色虚线是什么标线？', '白色虚线划于路段中时，用以分隔同向行驶的交通流或作为行车安全距离识别线；划于路口时，用以引导车辆行进。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467772, '掉头车道', '绕行车道', '分向车道', '左转车道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/75.jpg', '这个标志是何含义？', '常识题。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467773, '距无人看守铁路道口100米', '距有人看守铁路道口100米', '距无人看守铁路道口50米', '距有人看守铁路道口50米', '3', 'http://images.juheapi.com/jztk/a1b1subject1/76.jpg', '这个标志是何含义？', '一道红线是50米，一道50米。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467774, '禁止向左转弯', '向左和向右转弯', '禁止向左右转弯', '禁止向右转弯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/77.jpg', '这个标志是何含义？', '向左和向右转弯：表示只准一切车辆向左和向右转弯。此标志设在车辆必须向左和向右转弯的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467775, '直行和掉头合用车道', '直行和左转合用车道', '直行和右转车道', '分向行驶车道', '2', 'http://images.juheapi.com/jztk/a1b1subject1/78.jpg', '这个标志是何含义？', '表示只准一切车辆直行和向左转弯。此标志设在车辆必须直行和向左转弯的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467776, '开启危险报警闪光灯', '开启前照灯', '开启左转向灯', '开启右转向灯', '4', 'http://images.juheapi.com/jztk/a1b1subject1/79.jpg', '进入减速车道时怎样使用灯光？', '减速车道在右侧，应该开启右转向灯提醒后车注意减速避让，然后从虚线处进入减速车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467777, '指示向左变道', '指示前方直行', '指示前方掉头', '指示前方右转弯', '4', 'http://images.juheapi.com/jztk/a1b1subject1/80.jpg', '这个导向箭头是何含义？', '本题箭头方向是向右，那么本题是指示前方右转弯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467778, '正确', '错误', '', '', '2', '', '驾驶人在道路上醉酒驾驶机动车的处3年以上有期徒刑。', '醉酒驾驶机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；5年内不得重新取得机动车驾驶证。\r<br/>醉酒驾驶营运机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；10年内不得重新取得机动车驾驶证，重新取得机动车驾驶证后，不得驾驶营运机动车。\r<br/>醉驾不用处3年以上有期徒刑。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467779, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/82.jpg', '驾驶机动车遇到这种信号灯，可在对面直行车前直接向左转弯。', '通过有交通标志标线的路口，右转弯让左转弯，转弯车让直行车。对面车辆是直行车，不能在它面前直接左转，要让行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467780, '接近移动障碍物标线', '远离狭窄路面标线', '接近障碍物标线', '接近狭窄路面标线', '3', 'http://images.juheapi.com/jztk/a1b1subject1/83.jpg', '路面上的黄色填充标线是何含义？', '双黄线就是警告注意越界，“打叉的物体”就是不可移动障碍物。因此选接近障碍物标线。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467781, '超过规定速度10%', '疲劳后驾驶机动车', '行车中未系安全带', '饮酒后驾驶机动车', '4', '', '驾驶人有哪种情形，交通警察可依法扣留机动车驾驶证？', '六种情形下可以扣留驾驶证。新法规定，有下列情形之一的，可以扣留机动车驾驶证：<br/> <b> (一)饮酒、醉酒后驾驶机动车的；</b>  <br/>(二)机动车驾驶人将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的；  <br/>(三)机动车行驶超过规定时速百分之五十的； <br/>(四)驾驶拼装或者已达到报废标准的机动车的；  <br/>(五)发生重大交通事故，构成犯罪的；  <br/>(六)在一个记分周期内累积记分达到12分的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467782, '未放置检验合格标志', '未携带身份证', '未放置城市环保标志', '未携带机动车登记证书', '1', '', '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '未放置检验合格标志，未悬挂机动车号牌，未放置保险标志的，未携带机动车行驶证的都是可依法扣留车辆的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467783, '正确', '错误', '', '', '1', '', '道路交通安全违法行为累积记分一个周期满分为12分。', '《公安部令第123号》第五十五条：<br/>道路交通安全违法行为累积记分周期（即记分周期）为12个月，满分为12分，从机动车驾驶证初次领取之日起计算。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467784, '隧道出口距离', '隧道入口距离', '隧道跟车距离', '隧道总长度', '1', 'http://images.juheapi.com/jztk/a1b1subject1/87.jpg', '这个标志是何含义？', '用于指示到前方隧道出口的距离。设在长度超过3 000 m的特长隧道内，从距离隧道出口2 000m处开始每500 m设置一块，直至隧道出口。该标志一般设置在隧道侧壁上。不要纠结选D，正确答案就是A，牢记吧。交通指路标志大全：http://www.jszks.com/onepage/16752.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467785, '靠道路左侧停车', '左侧是下坡路段', '只准向左转弯', '靠左侧道路行驶', '4', 'http://images.juheapi.com/jztk/a1b1subject1/88.jpg', '这个标志是何含义？', '靠左侧道路行驶：表示只准一切车辆靠左侧道路行驶。此标志设在车辆必须靠左侧行驶的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467786, '挡住其去路', '保持原车速行驶', '尽可能减速避让、直至停车', '加速行驶', '3', '', '行车中突遇对方车辆强行超车，占据自己车道，正确的做法是什么？', '行车要记得安全文明驾驶。后方要超车，尽可能减速避让、直至停车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467787, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/90.jpg', '驶离高速公路可以从这个位置直接驶入匝道。', '不能压实线直接过去的啊，应该提前换到驶离的车道，在进入匝道。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467788, '正确', '错误', '', '', '2', '', '对有使用伪造或变造检验合格标志嫌疑的车辆，交通警察只进行罚款处罚。', '根据《中华人民共和国道路交通安全法》第九十六条规定：<br/><b>伪造、变造或者使用伪造、变造的检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处十日以下拘留，并处一千元以上三千元以下罚款；构成犯罪的，依法追究刑事责任。</b><br/>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处二千元以上五千元以下罚款。 当事人提供相应的合法证明或者补办相应手续的，应当及时退还机动车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467789, '右转弯信号', '靠边停车信号', '左转弯信号', '停止信号', '4', 'http://images.juheapi.com/jztk/a1b1subject1/92.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467790, '路面湿滑，视线受阻', '发动机易熄火', '行驶阻力增大', '电器设备易受潮短路', '1', '', '雨天对安全行车的主要影响是什么？', '雨天路面湿滑，雨水会使视线受阻，所以更要注意行车安全。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467791, '正确', '错误', '', '', '1', '', '驾驶人在发生交通事故后因抢救伤员变动现场时要标明位置。', '《道路交通安全法》第七十条：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。<b>因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。 </b><br/>在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467792, '行驶阻力变小', '车轮极易滑转和侧滑', '能见度低，视野模糊', '路面附着力增大', '2', '', '泥泞道路对安全行车的主要影响是什么？', '泥泞道路，泥土附着在轮胎上，轮胎和地面的摩擦减小，附着力也随之减小，车轮容易发生滑转和侧滑；同时因为道路状况变差，行驶阻力也变大。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467793, '60公里/小时', '90公里/小时', '100公里/小时', '110公里/小时', '3', 'http://images.juheapi.com/jztk/a1b1subject1/96.jpg', '在这条车道行驶的最低车速是多少？', '高速路同方向有2条车道，左侧车的速度范围为100公里到120公里，右侧车的速度范围为60公里到100公里。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467794, '正确', '错误', '', '', '1', '', '道路交通事故中，驾驶人有饮酒、醉酒嫌疑时，要保护现场并立即报警。', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，当事人应当保护现场并立即报警： 　　<br/>（一）造成人员死亡、受伤的； 　　<br/>（二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　<br/>（三）机动车无号牌、无检验合格标志、无保险标志的； 　　<br/>（四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的； 　　<br/>（五）碰撞建筑物、公共设施或者其他设施的；　　<br/>（六）驾驶人无有效机动车驾驶证的； 　　<br/><b>（七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； </b>　　<br/>（八）当事人不能自行移动车辆的。 　　<br/>发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467795, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/98.jpg', '这辆红色轿车可以在该车道行驶。', '中间红灯表示中间车道禁行。因此红色轿车不可以在该车道行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467796, '十字交叉路口预告', '互通立体交叉预告', 'Y型交叉路口预告', '环行交叉路口预告', '4', 'http://images.juheapi.com/jztk/a1b1subject1/99.jpg', '这个标志是何含义？', '环形交叉口是在几条相交道路的平面交叉口中央设置一个半径较大的中心岛，使所有经过交叉口的直行和左转车辆都绕着中心岛作逆时针方向行驶，在其行驶过程中将车流的冲突点变  为交织点，从而保证交叉口的行车安全，提高交叉口的通行能力。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467797, '专用待客停车位', '专用上下客停车位', '机动车限时停车位', '固定停车方向停车位', '4', 'http://images.juheapi.com/jztk/a1b1subject1/100.jpg', '这种停车标线含义是什么？', '这是固定停车方向停车位，车头要按箭头方向停放。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467798, '正确', '错误', '', '', '2', '', '行车中在道路情况良好的条件下可以观看车载视频。', '《实施条例》第六十二条，驾驶机动车不得有下列行为：\r<br/>(一)在车门、车厢没有关好时行车；\r<br/>(二)在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品；\r<br/><b>(三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为；</b>\r<br/>(四)下陡坡时熄火或者空挡滑行；\r<br/>(五)向道路上抛撒物品；\r<br/>(六)驾驶摩托车手离车把或者在车把上悬挂物品；\r<br/>(七)连续驾驶机动车超过4小时未停车休息或者停车休息时间少于20分钟；\r<br/>(八)在禁止鸣喇叭的区域或者路段鸣喇叭。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467799, '临时停靠站', '大客车停靠站', '公交车停靠站', '应急停车带', '3', 'http://images.juheapi.com/jztk/a1b1subject1/102.jpg', '红色圆圈内标线含义是什么？', '“公交车”三个字很明显，此标线为公交车停靠站。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467800, '右转弯信号', '减速慢行信号', '靠边停车信号', '变道信号', '4', 'http://images.juheapi.com/jztk/a1b1subject1/103.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467801, '线形诱导标志', '合流诱导标志', '分流诱导标志', '转弯诱导标志', '1', 'http://images.juheapi.com/jztk/a1b1subject1/104.jpg', '这个标志是何含义？', '线型诱导标线型诱导标志，用于引导车辆驾驶人改变行驶方向，促使安全运行。视需要设于易肇事之弯道路段，小半径匝道曲线或中央隔离设施及渠化设施的端部。线形诱导标的颜色规定为：指示性线形诱导标一般道路为蓝底白图案,高速公路为绿底白图案，用以提供一般性行驶指示;警告性线形诱导标为红底白图案，可使车辆驾驶人提高警觉，并准备防范应变之措施。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467802, '正确', '错误', '', '', '1', '', '造成交通事故后逃逸，尚不构成犯罪的一次记12分。', '《公安部令第123号》规定，造成交通事故后逃逸，尚不构成犯罪的，一次记12分。凡是逃逸的均为12分。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467803, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/106.jpg', '在道路与铁路道口遇到一个红灯亮时要尽快通过道口。', '车辆通过设有交通信号的铁路道口时，必须遵守下列规定： 1）两个红灯交替闪烁或红灯稳定亮时，表示火车接近道口，禁止通行； 2）红灯熄灭白灯亮时，表示道口开通，准许车辆通行； 3）当红灯和白灯同时熄灭时，表示停电或者设备发生故障，道口信号无效，在这种情况下，必须听从道口看守人员和道口安全管理人员的指挥，在道口前减速或者停车，确认安全无误后迅速通行。凡遇到道口栏杆（栏门）关闭、音响器发出报警、道口信号显示红色灯光或者道口看守人员示意火车即将通过等任意一种情况时，车辆严禁抢行，必须依次停在停止线外，没有停止线的，停在距最外钢轨的5米以外，并且不得影响栏杆或者栏门的关闭。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467804, '正确', '错误', '', '', '2', '', '驾驶人违反交通运输管理法规发生重大事故致人死亡的处3年以上有期徒刑。', '据《刑法》第一百三十三条：\r<br/>违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；\r<br/>交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；\r<br/>因逃逸致人死亡的，处七年以上有期徒刑。\r<br/>依据法规是3年以下的，所以，本题选择【错误】。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467805, '提示前方有左弯或需向左绕行', '提示前方有障碍需向左合流', '提示前方有右弯或需向右合流', '提示前方有左弯或需向左合流', '3', 'http://images.juheapi.com/jztk/a1b1subject1/108.jpg', '路面上导向箭头是何含义？', '按图箭头意思就是要向右小转变，可能是道路右转，可能是两个不同的车道要汇合', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467806, '堤坝路', '上陡坡', '连续上坡', '下陡坡', '4', 'http://images.juheapi.com/jztk/a1b1subject1/109.jpg', '这个标志是何含义？', '下陡坡：用以提醒车辆驾驶人小心驾驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467807, '正确', '错误', '', '', '1', '', '驾驶人在实习期内驾驶机动车时，应当在车身后部粘贴或者悬挂统一式样的实习标志。', '《公安部令第123号》第六十四条：在实习期内驾驶机动车的，应当在车身后部粘贴或者悬挂统一式样的实习标志。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467808, '大型客车专用车道', '多乘员车专用车道', '公交车专用车道', 'BRT车辆专用车道', '4', 'http://images.juheapi.com/jztk/a1b1subject1/111.jpg', '这个标志是何含义？', '此图为BRT车辆专用车道，与公交车专用车道的区别是多了“快速公交”四个字。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467809, '正确', '错误', '', '', '1', '', '驾驶人因服兵役、出国（境）等原因延期审验期间不得驾驶机动车。', '第六十三条  机动车驾驶人因服兵役、出国（境）等原因，无法在规定时间内办理驾驶证期满换证、审验、提交身体条件证明的，可以向机动车驾驶证核发地车辆管理所申请延期办理。申请时应当填写申请表，并提交机动车驾驶人的身份证明、机动车驾驶证和延期事由证明。<br/>延期期限最长不超过三年。【延期期间机动车驾驶人不得驾驶机动车】。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467810, '多乘员车辆专用车道', '小型客车专用车道', '未载客出租车专用车道', '大型客车专用车道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/113.jpg', '道路最左侧白色虚线区域是何含义？', '图上有字，“多乘员专用”，因此本题是多乘员车辆专用车道', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467811, '内部停车场', '专用停车场', '露天停车场', '室内停车场', '3', 'http://images.juheapi.com/jztk/a1b1subject1/114.jpg', '这个标志是何含义？', '如果P字上边没有遮挡就是露天停车场。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467812, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/115.jpg', '这辆小型载客汽车驶离高速公路行车道的方法是正确的。', '图中车辆进入减速车道并未压实线，并且开启了转向灯，操作正确，所以是对的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467813, '禁止向右转弯', '禁止掉头', '禁止直行', '禁止向左转弯', '3', 'http://images.juheapi.com/jztk/a1b1subject1/116.jpg', '这个标志是何含义？', '禁止直行：表示前方路口禁止一切车辆直行。此标志设在禁止直行的路口前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467814, '提高车速抢先通过', '鸣喇叭抢先通过', '停车避让', '保持正常车速行驶', '3', '', '车辆行至交叉路口，遇有转弯的车辆抢行，应怎样做？', '如果遇有转弯的车辆抢行，一定要文明驾驶，礼貌让行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467815, '停车占用机动车道', '距离路边超过30厘米', '在有禁停标线路段停车', '在非机动车道停车', '3', 'http://images.juheapi.com/jztk/a1b1subject1/118.jpg', '这样在路边临时停放机动车有什么违法行为？', '图中右侧路边是黄色的禁止停车标线，所以此处是不能停车的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467816, '正确', '错误', '', '', '1', '', '女驾驶人穿高跟鞋驾驶车辆，不利于安全行车。', '穿高跟鞋踩制动踏板，会受力不均，同时也不能正确感知是否将制动踩到底，容易造成事故。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467817, '正确', '错误', '', '', '1', '', '车辆行至急转弯处时，应减速并靠右侧行驶，防止与越过弯道中心线的对方车辆相撞。', '通过急弯靠右行驶，给对向来车流出足够的行车空间，慢行能在遇到突发状况时有足够的反应时间。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467818, '正确', '错误', '', '', '1', '', '行车中遇到正在进行作业的道路养护车辆、工程作业车时要注意避让。', '按照规定，行车途中遇到正在进行作业的道路养护车辆、工程作业车时要注意避让。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467819, '正确', '错误', '', '', '1', '', '对使用其他车辆号牌、行驶证的车辆，交通警察可依法予以扣留。', '《道路交通安全法》第九十六条：使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处二千元以上五千元以下罚款。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467820, '正确', '错误', '', '', '2', '', '机动车在道路上发生轻微交通事故且妨碍交通时，不需移动。', '《中华人民共和国道路交通安全法》第七十条：在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467821, '高速公路客车站', '高速公路休息区', '高速公路加油站', '高速公路服务区', '3', 'http://images.juheapi.com/jztk/a1b1subject1/124.jpg', '这个标志是何含义？', '高速公路加油站：用于指示加油站的位置，设在通往加油站的入口附近。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467822, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/125.jpg', '这个路段可以在非机动车道上临时停车。', '在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467823, '前后雾灯', '危险报警闪光灯', '前大灯', '倒车灯', '2', '', '车辆在雨天临时停车时，应开启什么灯？', '即使不是在雨天，我们在路边临时停车时，也要开启危险报警闪光灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467824, '不准小型车通行', '只准小型车行驶', '机动车行驶', '禁止小型车行驶', '3', 'http://images.juheapi.com/jztk/a1b1subject1/127.jpg', '这个标志是何含义？', '看到小汽车你应该想到的是机动车行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467825, '驶入减速车道', '继续向前行驶', '车速保持100公里/小时', '车速降到40公里/小时以下', '1', 'http://images.juheapi.com/jztk/a1b1subject1/128.jpg', '驾驶机动车驶离高速公路时，在这个位置怎样行驶？', '这是高速路口出口，当要驶离高速路时，要先进入减速车道。正确操作是：提前打开右转向灯，并逐渐降低车速，不能压实线，要越过虚线进入减速车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467826, '限制车辆通行', '提示限速信息', '提供方向信息', '警告前方危险', '3', '', '指路标志的作用是什么？', '指路标志是传递道路方向，地点，距离信息的标志。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467827, '违反交通信号灯', '使用伪造机动车号牌', '拨打、接听手机的', '违反禁令标志指示', '2', '', '驾驶人有下列哪种违法行为一次记12分？', '机动车驾驶人有下列违法行为之一，一次记12分：<br/>（一）驾驶与准驾车型不符的机动车的；<br/>（二）饮酒后驾驶机动车的；<br/>（三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数20％以上的；<br/>（四）造成交通事故后逃逸，尚不构成犯罪的；<br/>（五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的；<br/><b>（六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；</b><br/>（七）驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的；<br/>（八）驾驶营运客车在高速公路车道内停车的；<br/>（九）驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的；<br/>（十）连续驾驶中型以上载客汽车、危险物品运输车辆超过4小时未停车休息或者停车休息时间少于20分钟的；<br/>（十一）未取得校车驾驶资格驾驶校车的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467828, 'Y型交叉路口', 'T型交叉路口', '十字交叉路口', '环行交叉路口', '2', 'http://images.juheapi.com/jztk/a1b1subject1/131.jpg', '这个标志是何含义？', 'T型交叉路口：丁字形标志原则上设在与交叉口形状相符的道路上。右侧丁字路口，此标志设在进入T字路口以前的适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467829, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/132.jpg', '驾驶机动车遇到这种信号灯亮时，如果已越过停止线，可以继续通行。', '《道路交通安全法实施条例》第三十八条：黄灯亮时，已越过停止线的车辆可以继续通行；未经过停止线的则禁止继续通行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467830, '允许在左侧车道行驶', '分隔对向行驶的交通流', '分隔同向行驶的交通流', '禁止跨越对向行车道', '2', 'http://images.juheapi.com/jztk/a1b1subject1/133.jpg', '路中黄色分界线的作用是什么？', '白色表示同向可跨越车道，黄色表示反向可跨越车道；虚线表示可跨越车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467831, '正确', '错误', '', '', '1', '', '机动车在夜间道路上发生故障难以移动时要开启危险报警闪光灯、示廓灯、后位灯。', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启<b>危险报警闪光灯</b>并在车后50米至100米处设置警告标志，夜间还应当同时开启<b>示廓灯和后位灯</b>。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467832, '正确', '错误', '', '', '1', '', '对发生道路交通事故需要收集证据的事故车，交通警察可以依法扣留。', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：对发生道路交通事故，因收集证据需要的，可以依法扣留事故车辆。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467833, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/136.jpg', '在这段道路上一定要减少鸣喇叭的频率。', '右侧的标志是禁止鸣喇叭，不是“减少”，而是绝对不能鸣喇叭。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467834, '残疾人出入口', '注意残疾人', '残疾人休息处', '残疾人专用通道', '2', 'http://images.juheapi.com/jztk/a1b1subject1/137.jpg', '这个标志是何含义？', '黄底标志为警告标志，所以是注意残疾人。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467835, '直行和向右转弯', '直行和向左转弯', '禁止直行和向左转弯', '只准向右和向左转弯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/138.jpg', '这个标志是何含义？', '直行和向左转弯：表示只准一切车辆直行和向左转弯。此标志设在车辆必须直行和向左转弯的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467836, '正确', '错误', '', '', '2', '', '车辆在高速公路上行车，可以频繁地变更车道。', '车辆在高速公路上行车，可以变更车道，但不可以“频繁”的变更车道。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467837, '正常行驶', '及时让行', '开启危险报警闪光灯行驶', '不得变更车道', '2', 'http://images.juheapi.com/jztk/a1b1subject1/140.jpg', '在这种情形中前车怎样行驶？', '《道路法》第五十三条第1款　警车、消防车、救护车、工程救险车执行紧急任务时，其他车辆和行人应当让行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467838, '正确', '错误', '', '', '2', '', '黄灯持续闪烁，表示机动车可以加速通过。', '黄灯持续闪烁，表明已经越过停止线的车辆可以行使，没越过停止线的车辆要停车等待，机动车不能加速通过。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467839, '正确', '错误', '', '', '1', '', '车辆不得在高速公路匝道上倒车。', '按照规定：驶入确定匝道后，迅速提高车速，但不得超过规定速度，在匝道上，不准超车、掉头、停车、和倒车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467840, '交叉路停车线', '左弯待转区线', '掉头引导线', '小型车转弯线', '2', 'http://images.juheapi.com/jztk/a1b1subject1/143.jpg', '图中圈内两条白色虚线是什么标线？', '左弯待转区是专门供左转弯车辆使用的。<br/>当直行信号灯绿灯亮时，左转弯是红灯时，左转弯的车辆可以进入左转弯待转区等候，待左转弯信号灯绿灯亮了，等候在待转区的车辆可以快速通过路口；当直行、左转弯信号灯都是红灯时，车辆是不可以进入左弯待转区的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467841, '最左侧', '第二条', '最右侧', '第三条', '1', '', '在同向4车道高速公路上行车，车速高于每小时110公里的车辆应在哪条车道上行驶？', '交通安全法实施条例第七十八条规定了同向两车道、三车道的限速规范：二车道左侧车道最低限速100，三车道左到右依次110、90、60。 <b>同向四车道记着培训老师讲的：左到右最低限速依次110、90、90、60。</b>因此本题选A。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467842, '持续鸣喇叭示意其让道', '加速绕行', '提前鸣喇叭，并适当降低车速', '以正常速度行驶', '3', '', '雨天行车，遇撑雨伞和穿雨衣的行人在公路上行走时，应怎样做？', '安全驾驶，减速慢行。遇到撑雨伞和穿雨衣会使视线受阻碍，所以要鸣喇叭，雨天路滑所以要减速。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467843, '直接起步通过', '起步后从行人后方绕过', '等行人通过后再起步', '起步后从行人前方绕过', '3', '', '行驶车道绿灯亮时，但车辆前方人行横道仍有行人行走，应怎样做？', '从前方绕、后方绕、直接通过都会惊扰到行人，有可能造成事故。正确做法是等行人通过后再起步。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467844, '告知方向信息', '警告前方危险', '限制车辆、行人通行', '指示车辆、行人行进', '4', '', '指示标志的作用是什么？', '指示标志是交通标志中主要标志的一种，用以指示车辆和行人按规定方向、地点行驶。 指示标志的颜色为蓝底、白色图案；形状分为圆形、长方形和正方形。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467845, '正确', '错误', '', '', '1', '', '驾驶机动车发生财产损失交通事故后，当事人对事实及成因无争议移动车辆时需要对现场拍照或者标划停车位置。', '《道路交通事故处理程序规定》第十三条：机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。<b>车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。</b>非机动车与非机动车或者行人发生财产损失事故，基本事实及成因清楚的，当事人应当先撤离现场，再协商处理损害赔偿事宜。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467846, '正确', '错误', '', '', '1', '', '行车中遇到执行紧急任务的消防车、救护车、工程救险车时要及时让行。', '《道路法》第五十三条第1款　警车、消防车、救护车、工程救险车执行紧急任务时，其他车辆和行人应当让行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467847, '旅游区距离', '旅游区方向', '旅游区符号', '旅游区类别', '1', 'http://images.juheapi.com/jztk/a1b1subject1/150.jpg', '这个标志是何含义？', '遇此类标志是指旅游区的距离还有多远（亲不要激动 安全第一 慢行', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467848, '正确', '错误', '', '', '2', '', '《道路交通安全法实施条例》规定，高速公路上最高时速不得超过120公里。因此在高速公路上行驶只要时速不超过120公里就不违法。', '不同车型的限速不同，不同车道的限速也不同。而且高速上不仅有最高限速，还有最低限速，并不是不高于120公里/小时就可以了的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467849, '立即停车', '在原地掉头', '在原地倒车驶回', '继续向前行驶，寻找下一个出口', '4', '', '高速公路上行车，如果因疏忽驶过出口，应怎样做？', '高速公路行车不可掉头、不可倒车，错过出口，只能继续行驶，寻找下个出口。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467850, '塌方路段', '施工路段', '前方工厂', '道路堵塞', '2', 'http://images.juheapi.com/jztk/a1b1subject1/153.jpg', '这个标志是何含义？', '施工路段：用以告示前方道路施工，车辆应减速慢行或绕道行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467851, '学校区域', '注意儿童', '人行横道', '注意行人', '2', 'http://images.juheapi.com/jztk/a1b1subject1/154.jpg', '这个标志是何含义？', '注意儿童：用以警告车辆驾驶人减速慢行，注意儿童。设在小学、幼儿园、少年宫等儿童经常出入地点前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467852, '加速通过', '立即停车', '先减速注意观察行人、非机动车动态，确认安全后再通过', '鸣喇叭示意行人让道', '3', '', '车辆驶近人行横道时，应怎样做？', '因为行人具有随意性，所以要先减速注意观察行人、非机动车动态，确认安全后再通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467853, '车辆多', '红灯亮', '绿灯亮', '暂停服务', '3', '', '驶入高速公路的收费口时，应选择怎样的入口？', '绿灯是表示收费站正常运行的，所以应该选择绿灯亮的入口。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467854, '向左绕行', '连续弯路', '向左急转弯', '向右急转弯', '3', 'http://images.juheapi.com/jztk/a1b1subject1/157.jpg', '这个标志是何含义？', '向左急转弯：向左急弯路标志设在左急转弯的道路前方适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467855, '正确', '错误', '', '', '1', '', '机动车行驶证灭失、丢失，机动车所有人要向登记地车辆管理所申请补领、换领。', '机动车号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向登记地车辆管理所申请补领、换领。申请时，机动车所有人应当填写申请表并提交身份证明。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467856, '机动车驾驶证被暂扣期间驾驶机动车的', '以隐瞒、欺骗手段补领机动车驾驶证的', '驾驶机动车不按照规定避让校车的', '驾驶故意污损号牌的机动车上道路行驶', '4', '', '有下列哪种违法行为的机动车驾驶人将被一次记12分？', '《公安部令第123号》规定，上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的，一次记12分 。其余均为6分。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467857, '正确', '错误', '', '', '2', '', '车辆行至交叉路口时，左转弯车辆在任何时段都可以进入左弯待转区。', '直行车辆放行时才可进入左转待转区。你到路口观察一下就明白了！', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467858, '正确', '错误', '', '', '1', '', '驾驶人在驾驶证核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。', '《机动车驾驶证申领和使用规定》第四十九条：机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向迁入地车辆管理所申请换证。机动车驾驶人在核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467859, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/162.jpg', '在铁路道口遇到两个红灯交替闪烁时要停车等待。', '在铁路道口，一个红灯亮或两个红灯闪烁时都是禁止通行的，要停车等待。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467860, '禁止通行', '指示通行', '限制通行', '警告提醒', '2', '', '指示标线的作用是什么？', '指示标线是指示通行的，具体的红色标志表示禁止、限制；黄色标志表示警告。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467861, '正确', '错误', '', '', '1', '', '驾驶人将机动车交给驾驶证被吊销的人驾驶的，交通警察依法扣留驾驶证。', '六种情形下可以扣留驾驶证 新法规定，有下列情形之一的，可以扣留机动车驾驶证： <br/>(一)饮酒、醉酒后驾驶机动车的； <br/>(二)<b>机动车驾驶人将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的；<br/></b> (三)机动车行驶超过规定时速百分之五十的； <br/>(四)驾驶拼装或者已达到报废标准的机动车的； <br/>(五)发生重大交通事故，构成犯罪的； <br/>(六)在一个记分周期内累积记分达到12分的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467862, '开启右转向灯', '开启左转向灯', '开启危险报警闪光灯', '开启前照灯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/165.jpg', '在这个位置时怎样使用灯光？', '《道路交通安全法实施条例》第七十九条：机动车从匝道驶入高速公路，<b>应当开启左转向灯</b>，在不妨碍已在高速公路内的机动车正常行驶的情况下驶入车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467863, '向右急转弯', '向右绕行', '连续弯路', '向左急转弯', '1', 'http://images.juheapi.com/jztk/a1b1subject1/166.jpg', '这个标志是何含义？', '向右急转弯：向右急弯路标志，设在右急转弯的道路前方适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467864, '正确', '错误', '', '', '1', '', '驾驶车辆变更车道时，应提前开启转向灯，注意观察，保持安全距离，驶入要变更的车道。', '行车变道时，提前开启转向灯，是为了提示后车。同时要观察周围情况，具有变道条件时再变更车道。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467865, '注射胰岛素', '酒醉经历', '吸烟成瘾', '注射毒品', '4', '', '3年内有下列哪种行为的人不得申请机动车驾驶证？', '《机动车驾驶证申领和使用规定》第十二条：三年内有吸食、注射毒品行为或者解除强制隔离戒毒措施未满三年，或者长期服用依赖性精神药品成瘾尚未戒除的，不得申请机动车驾驶证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467866, '正确', '错误', '', '', '1', '', '驾驶机动车在上道路行驶前驾驶人要按规定系好安全带。', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467867, '正确', '错误', '', '', '2', '', '行驶在高速公路上遇大雾视线受阻时，应当立即紧急制动停车。', '<b>错在“紧急制动”上，要逐渐减速停车，</b>大雾天气视线不佳，紧急制动容易发生追尾事故，要逐渐减速停车。并且要开启危险警示灯以及雾灯。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467868, '提前加速抢行', '停车让行', '降低车速、缓慢通过', '连续鸣喇叭催促', '2', '', '行车中遇列队横过道路的学生时，应怎样做？', '列队横过道路的学生，人数多，通行时间长。降低车速还是容易撞上，停车让行更安全。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467869, '连续鸣喇叭提醒其让路', '持续鸣喇叭并加速超越', '让自行车先行', '注意观察动态，减速慢行，留有足够的安全距离', '4', '', '行车中超越同向行驶的自行车时，应怎样做？', '驾驶机动车遇行人和非机动车肯定要减速慢行，否则会容易撞上，但“慢行”相对机动车的快行是慢的，而相对自行车来的速度来说还是比较快的。并且超车必须要确保安全距离（左右距离），防止与自行车相撞。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467870, '旅游区距离', '旅游区类别', '旅游区方向', '旅游区符号', '3', 'http://images.juheapi.com/jztk/a1b1subject1/173.jpg', '这个标志是何含义？', '快到云居寺了，箭头指的是方向', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467871, '不得逆向或并列停放', '只要出去方便，可随意停放', '可逆向停放', '可并列停放', '1', '', '机动车在道路边临时停车时，应怎样做？', '在路边临时停车时应遵循“不影响正常交通”的原则，因此除了要靠路边之外，还要尽可能留出空间便于其他车辆通行，因此不得逆向或并列停放。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467872, '正确', '错误', '', '', '2', '', '驾驶人在道路上驾驶机动车追逐竞驶，情节恶劣的处3年以下有期徒刑。', '道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。不用判刑。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467873, '丁字交叉路口预告', '道路分叉处预告', 'Y型交叉路口预告', '十字交叉路口预告', '1', 'http://images.juheapi.com/jztk/a1b1subject1/176.jpg', '这个标志是何含义？', '你看它长的多像丁字。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467874, '抢行通过', '提前加速通过', '减速避让，必要时停车让行', '按优先权规定正常行驶不予避让', '3', '', '车辆在交叉路口有优先通行权的，遇有车辆抢行时，应怎样做？', '遇到抢行车辆时，考虑到安全第一，要主动避让。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467875, '正确', '错误', '', '', '1', '', '机动车发生财产损失交通事故，对应当自行撤离现场而未撤离造成交通堵塞的，可以对驾驶人处以200元罚款。', '《道路交通事故处理程序规定》第十三条：对应当自行撤离现场而未撤离的，交通警察应当责令当事人撤离现场；造成交通堵塞的，对驾驶人处以200元罚款；驾驶人有其他道路交通安全违法行为的，依法一并处罚。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467876, '非紧急情况时不得在应急车道行驶或者停车', '可在减速车道或加速车道上超车、停车', '可在紧急停车带停车装卸货物', '可在路肩停车上下人员', '1', '', '机动车在高速公路行驶，下列做法正确的是？', '紧急车道只能在紧急情况下才能使用，非紧急情况不得占用。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467877, '不得超过100公里/小时', '不得超过90公里/小时', '不得超过60公里/小时', '不得超过80公里/小时', '3', '', '驾驶机动车在高速公路遇到能见度低于200米的气象条件时，最高车速是多少？', '高速公路行车限速：（1）最低限速60公里/小时（2）特殊情况限速：能见度小于200米限速60公里/小时、能见度小于100米限速40公里/小时、能见度小于50米限速20公里/小时。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467878, '150米以外', '50～150米', '50米以内', '50～100米', '1', 'http://images.juheapi.com/jztk/a1b1subject1/181.jpg', '这辆在高速公路上临时停放的故障车，警告标志应该设置在车后多远处？', '《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；但是，警告标志应当设置在故障车来车方向一百五十米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467879, '正确', '错误', '', '', '2', '', '车辆在高速公路行驶时，可以仅凭感觉确认车速。', '不仅是在高速行车中，任何时候都不能凭感觉去确认车速。要通过速度表确认。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467880, '车道数变少', '合流处', '应急车道', '向左变道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/183.jpg', '这个标志是何含义？', '三车道变成二车道故车道数变少，选A。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467881, '不准让行', '会车让行', '减速让行', '停车让行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/184.jpg', '这个标志是何含义？', '减速让行：表示车辆应减速让行，告示车辆驾驶员必须慢行或停车，观察干路行车情况，在确保干道车辆优先的前提下，认为安全时方可续行。此标志设在视线良好交叉道路的次要路口。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467882, '适当加大安全距离', '紧随前车之后', '适当减小安全距离', '尽可能寻找超车机会', '1', '', '车辆在山区道路跟车行驶时，应怎样做？', '山区道路行车，要加大安全距离，发生事故时，有反应时间和反应距离。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467883, '连续变换远、近光灯', '不断鸣喇叭', '使用近光灯，减速或停车避让', '使用远光灯', '3', '', '夜间驾驶车辆遇自行车对向驶来时，应怎样做？', '文明驾驶，礼貌让行。交替使用远近光灯可能会使对方看不清，所以最好的是减速或者停车避让。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467884, '加油起步', '鸣喇叭', '提高发动机转速', '观察周围交通情况', '4', '', '车辆临时靠边停车后准备起步时，应先怎样做？', '起步时要先观察路况，小心驾驶，安全第一。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467885, '正确', '错误', '', '', '2', '', '使用其他机动车号牌、行驶证的一次记3分。', '机动车驾驶人有下列违法行为之一，一次记12分：<br/>（一）驾驶与准驾车型不符的机动车的；<br/>（二）饮酒后驾驶机动车的；<br/>（三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数20％以上的；<br/>（四）造成交通事故后逃逸，尚不构成犯罪的；<br/>（五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的；<br/><b>（六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；</b><br/>（七）驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的；<br/>（八）驾驶营运客车在高速公路车道内停车的；<br/>（九）驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的；<br/>（十）连续驾驶中型以上载客汽车、危险物品运输车辆超过4小时未停车休息或者停车休息时间少于20分钟的；', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467886, '傍山险路', '悬崖路段', '注意落石', '危险路段', '3', 'http://images.juheapi.com/jztk/a1b1subject1/189.jpg', '这个标志是何含义？', '注意落石：此标志设在左侧有落石危险的傍山路段之前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467887, '在非机动车道停车', '在有禁停标志路段停车', '在公共汽车站停车', '停车占用人行道', '3', 'http://images.juheapi.com/jztk/a1b1subject1/190.jpg', '这样停放机动车什么违法行为？', '图为公共汽车站。公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点３０米以内的路段，除使用上述设施的以外，不得停车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467888, '漫水桥', '渡口', '船用码头', '过水路面', '2', 'http://images.juheapi.com/jztk/a1b1subject1/191.jpg', '这个标志是何含义？', '渡口：用以提醒车辆驾驶人谨慎驾驶。设在车辆渡口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467889, '正确', '错误', '', '', '1', '', '设有安全带装置的车辆，应要求车内乘员系安全带。', '行车过程中，即使是设有安全装置的车内，驾驶员与非驾驶员都要系安全带，安全第一!', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467890, '正确', '错误', '', '', '1', '', '车辆通过铁道路口时，应用低速挡安全通过，中途不得换挡，以避免发动机熄火。', '通过铁路道口，安全第一。换挡容易熄火，中途换挡如果发生熄火，很容易产生危险。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467891, '鸣喇叭示意', '主动停车避让', '加速倒车', '继续倒车', '2', '', '在一般道路倒车时，若发现有过往车辆通过，应怎样做？', '倒车时视线盲区较大，如果有意外情况无法及时处理。因此一旦后方有车辆通过，为了安全一定要停车避让。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467892, '抓住任何机会尽量', '选择宽阔的缓上坡路段', '选择较长的下坡路', '选择较缓的下坡路', '2', '', '在山区道路超车时，应怎样超越？', '下坡不允许超车，超车选择宽阔的缓上坡路段比较安全。上坡有阻力，不宜滑车，也不宜撞车，所以上坡宽阔地可以超车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467893, '靠道路右侧加速行驶', '主动减速并靠右侧行驶', '让出适当空间加速行驶', '迅速减速或紧急制动', '2', '', '遇后车发出超车信号后，只要具备让超条件应怎样做？', '文明礼让，和谐驾驶。如果遇到后车发出超车信号，我们应该主动减速并靠右侧行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467894, '挂入空挡滑行', '利用发动机制动', '关闭发动机熄火滑行', '踏下离合器滑行', '2', '', '下长坡时，控制车速除了刹车制动以外还有什么有效的辅助方法？', '<b>发动机制动就是挂低档，让车利用低档位的惯性制动。</b>下长坡时，可以通过这种方式有效控制车速。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467895, '前后雾灯', '倒车灯', '前大灯', '危险报警闪光灯', '4', '', '车辆在雪天临时停车时，应开启什么灯？', '一般情况下，我们在路边临时停车，均需开启危险报警闪光灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467896, '靠道路中心行驶', '加速让路', '继续加速行驶', '减速，靠右侧行驶', '4', '', '驾驶的车辆正在被其他车辆超越时，应怎样做？', '超车从左边，如果我们正在被其他车辆超车，应该靠右边减速让行。安全第一。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467897, '正确', '错误', '', '', '1', '', '行车中遇残疾人影响通行时，应主动减速礼让。', '残疾人行动不便，遇到残疾人一定要减速让行，必要时还需停车让行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467898, '正确', '错误', '', '', '1', '', '小型客车行驶在平坦的高速公路上，突然有颠簸感觉时，应迅速降低车速，防止爆胎。', '如果突然有颠簸的感觉，应该是压到了不小的障碍物。这时候为了确保轮胎不爆胎，一定要迅速减速，然后开到安全的路边检查。\r<br/>\r<br/>【拓展延伸】\r<br/>迅速降低车速≠紧急制动。爆胎后虽然严禁紧急制动，但题目所说的是突然有颠簸感觉时，要采取迅速降低车速来避免爆胎，而迅速降低车速可以采用收油减挡等方式，并非紧急制动。注意审题。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467899, '正确', '错误', '', '', '2', '', '在高速公路上遇分流交通管制时，可不驶出高速公路，就地靠边停靠等待管制结束后继续前行。', '高速公路上不能随便靠边停车，以免造成更大的堵塞。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467900, '谨慎超越', '放弃超车', '并行一段距离后再超越', '加速超越', '2', '', '进入左侧道路超车，无法保证与正常行驶前车的横向安全间距时，应怎样做？', '没有超车条件，放弃超车是最安全的行为。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467901, '会缩短发动机寿命', '增加驾驶人的劳动强度', '容易造成车辆倾翻', '会使制动器温度升高而使制动效果急剧下降', '4', '', '下长坡连续使用行车制动会导致什么？', '持续用刹车，会造成刹车片发热，从而制动力减小，严重的会丧失制动能力，所以长下坡要用低挡，用发动机制动。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467902, '正确', '错误', '', '', '2', '', '车辆驶入匝道后，迅速将车速提高到每小时60公里以上。', '应该是从匝道进入高速公路加速车道，在加速车道里迅速将车速提高到每小时60公里以上，而不是匝道。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467903, '紧靠道路中心', '不减速', '加速', '减速或停车让行', '4', '', '在山区道路遇对向来车时，应怎样会车？', '山区道路比较复杂，容易出事故，为了安全，会车时要减速或者停车让行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467904, '正确', '错误', '', '', '2', '', '漫水道路行车时，应挂高速档，快速通过。', '不应该挂高速挡，而是要选用低速挡，以保证汽车有足够的驱动力;入水时应平稳，以防溅起的水花落入电器设备，造成电器短路；发动机中速运转，保持车速，中途不得停车、换挡和急转转向盘', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467905, '寻找机会超越前车', '从车辆空间穿插通过', '减速停车，依次排队等候', '鸣喇叭催促', '3', '', '行车中发现前方道路拥堵时，应怎样做？', '减速停车依次排队，文明驾驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467906, '道路流量监测', '减速拍照区', '全路段抓拍', '交通监控设备', '4', 'http://images.juheapi.com/jztk/a1b1subject1/209.jpg', '这个标志是何含义？', '交通监控设备标志属于指路标志，蓝底、白图形、白边框、蓝色衬边。用于告知机动车驾驶人该区域设置有固定式交通监控设备。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467907, '按规定设置警告标志', '车上人员不能下车', '迅速报警', '开启危险报警闪光灯', '2', '', '机动车在高速公路上发生故障时错误的做法是什么？', '《道路交通安全法》第五十二条：\r<br/>机动车在道路上发生故障，需要停车排除故障时，驾驶人应当立即<b>开启危险报警闪光灯</b>，将机动车移至不妨碍交通的地方停放；难以移动的，应当<b>持续开启危险报警闪光灯</b>，并在来车方向设置警告标志等措施扩大示警距离，必要时迅速报警。\r<br/>第六十八条：\r<br/>机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；警告标志应当设置在故障车来车方向一百五十米以外，<b>车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。</b>\r<br/>题目要求是发生故障时的错误做法是什么，选项中ABD都是正确的，C项错误（车上人应应当迅速转移到右侧路肩上或者应急车道内，不应该不下车）。因此本题答案选C。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467908, '加速行驶', '绕道行驶', '保持正常车速行驶', '谨慎选择行车速度', '4', '', '驾驶人行车中看到注意儿童标志的时候，应怎样做？', '看见儿童标志，需谨慎驾驶，避免伤及儿童。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467909, '正确', '错误', '', '', '2', '', '车辆在交叉路口绿灯亮后，遇非机动车抢道行驶时，可以不让行。', '安全第一，文明驾驶，礼貌让行。无论何时都应该主动让行，确保其安全。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467910, '驾驶人体力下降', '驾驶人易产生冲动、幻觉', '能见度低、不利于观察道路交通情况', '路面复杂多变', '3', '', '夜间道路环境对安全行车的主要影响是什么？', '<b>夜间行车视线差，在没有照明的路段，驾驶人视线普遍受到影响，不能清楚看到前方事物，或者不能看清和判断前方事物的距离，尤其是在会车的情况下，由于受对向车辆灯光的影响，会产生视觉的盲点，导致看不清或看不见前方事物，夜间特大道路交通事故明显高于白天。</b>夜间行车由于不能见到道路两旁的景观，对驾驶员兴奋性刺激物小，因此最易产生驾驶疲劳，如稍有感觉就应振作精神或停车休息片刻。因此本题选A。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467911, '休息区', '服务区', '停车场', '观景台', '1', 'http://images.juheapi.com/jztk/a1b1subject1/214.jpg', '这个标志是何含义？', '这个是休息区，服务区是有加油，通车，吃饭的组合标志，停车场就是一个P，图中没有加油站和吃饭的，所以它只是一个休息区。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467912, '正确', '错误', '', '', '1', '', '在道路上临时停车不得妨碍其他车辆和行人通行。', '《道路交通安全法》第五十六条：在道路上临时停车的，不得妨碍其他车辆和行人通行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467913, '正确', '错误', '', '', '1', '', '驾驶人有使用其他车辆检验合格标志嫌疑的，交通警察可依法扣留车辆。', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：有伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、 保险标志、驾驶证或者<b>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑的。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467914, '正确', '错误', '', '', '1', '', '驾驶人违反交通运输管理法规发生重大事故使公私财产遭受重大损失的可能处3年以下徒刑或拘役。', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467915, '制动距离延长', '抗滑能力变大', '制动性能没有变化', '路面附着力增大', '1', '', '冰雪路行车时应注意什么？', '车辆在冰雪路面上行驶，<b>因汽车轮胎与路面的摩擦系数减小、附着力大大降低，</b>所以遇有冰雪路面，车辆在行驶中最重要的是车辆制动问题，要采取点刹、并和前后车保持车距离。遇情况紧急制动时，制动距离会大大延长。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467916, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/219.jpg', '遇到这种情况下可以从右侧超车。', '超车只能从左侧超车，任何情况下都是不能从右边超车的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467917, '正确', '错误', '', '', '1', '', '在冰雪道路上行车时，车辆的稳定性降低，加速过急时车轮极易空转或溜滑。', '冰雪道路，轮胎与地面的摩擦力较小，吸附力较低，只要一加速，就比较容易打滑或者溜空。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467918, '直行信号', '转弯信号', '停止信号', '靠边停车信号', '1', 'http://images.juheapi.com/jztk/a1b1subject1/221.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467919, '正确', '错误', '', '', '2', '', '大型客车、牵引车、城市公交车、中型客车、大型货车驾驶人应当每两年提交一次身体条件证明.', '2013年1月1日起施行的公安部123号令，已明确规定，大、中型客货车驾驶人每年参加审验，审验时自己申报身体条件情况', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467920, '交通情况单一', '坡长弯急，视距不足', '车流密度大', '道路标志少', '2', '', '山区道路对安全行车的主要影响是什么？', '<b>山区道路的特点是：坡多、路窄、急弯多，不像平原地区视线开阔。</b>最关键的一点是山区道路行驶时不容易掌握周围情况，也就是所谓视距不足。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467921, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/224.jpg', '驾驶机动车要选择绿色箭头灯亮的车道行驶。', '只有绿灯是表示允许通行，机动车要选择绿色箭头灯亮的车道行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467922, '中心圈', '导流线', '网状线', '停车区', '3', 'http://images.juheapi.com/jztk/a1b1subject1/225.jpg', '图中路口中央黄色路面标记是什么标线？', '路面黄色网状线表示严格禁止一切车辆长时或临时停车，防止交通阻塞。当黄色网状线前方有车辆停驶时，后车必须在黄色网状线外等候，直到确认黄色网状线前方有足够空间停驶本车时，方可驶过黄色网状线。 另有黄色网状线是施画在重要国家机关、军队、大型企事业单位和公交枢纽门前，确保车辆进出时不致被门前拥堵车辆堵住的措施。网状线是黄色线，停车时禁止占用。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467923, '正确', '错误', '', '', '1', '', '车速超过规定时速50%以上的一次记12分。', '《公安部令第123号》规定，驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的，一次记12分。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467924, '空挡滑行', '低挡行驶', '制动减速', '提前减挡', '1', '', '驾驶机动车下陡坡时不得有哪些危险行为？', '《道路交通安全法实施条例》第六十二条规定，驾驶机动车下陡坡时不得熄火或者空挡滑行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467925, '正确', '错误', '', '', '1', '', '车辆在山区道路行车下陡坡时，不得超车。', '《道路交通安全法》规定，在陡坡上不得超车，掉头。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467926, '十字交叉路口预告', '互通式立体交叉预告', 'Y型交叉路口预告', '环行交叉路口预告', '2', 'http://images.juheapi.com/jztk/a1b1subject1/229.jpg', '这个标志是何含义？', '互通式立体交叉：路线交叉中的一种重要类型。交叉道路之间立体交叉并以匝道相互连通以实现交通转换的交叉。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467927, '向右变道', '分流处', '路面变宽', '车道数增加', '4', 'http://images.juheapi.com/jztk/a1b1subject1/230.jpg', '这个标志是何含义？', '俩个车道变成三个，表示车道数增加。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467928, '直行和左转弯行驶', '直行和右转弯行驶', '立体交叉直行和右转弯行驶', '立体交叉直行和左转弯行驶', '3', 'http://images.juheapi.com/jztk/a1b1subject1/231.jpg', '这个标志是何含义？', '立体交叉直行和右转弯行驶：表示车辆在立交处可以直行和按图示路线右转弯行驶。此标志设在立交右转弯出口处适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467929, '使用其他车辆行驶证', '车速超过规定时速50%以上', '违法占用应急车道行驶', '饮酒后驾驶机动车', '3', '', '驾驶人有下列哪种违法行为一次记6分？', '（公安部令123号）机动车驾驶人有下列违法行为之一，一次记6分：<br/>(一)机动车驾驶证被暂扣期间驾驶机动车的;<br/>(二)驾驶机动车违反道路交通信号灯通行的;<br/>(三)驾驶营运客车(不包括公共汽车)、校车载人超过核定人数未达20%的，或者驾驶其他载客汽车载人超过核定人数20%以上的;<br/>(四)驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速未达20%的;<br/>(五)驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路以外的道路上行驶或者驾驶其他机动车行驶超过规定时速20%以上未达到50%的;<br/>(六)驾驶货车载物超过核定载质量30%以上或者违反规定载客的;<br/>(七)驾驶营运客车以外的机动车在高速公路车道内停车的;<br/><b>(八)驾驶机动车在高速公路或者城市快速路上违法占用应急车道行驶的;</b><br/>(九)低能见度气象条件下，驾驶机动车在高速公路上不按规定行驶的;<br/>(十)驾驶机动车运载超限的不可解体的物品，未按指定的时间、路线、速度行驶或者未悬挂明显标志的;<br/>(十一)驾驶机动车载运爆炸物品、易燃易爆化学物品以及剧毒、放射性等危险物品，未按指定的时间、路线、速度行驶或者未悬挂警示标志并采取必要的安全措施的;<br/>(十二)以隐瞒、欺骗手段补领机动车驾驶证的;<br/>(十三)连续驾驶中型以上载客汽车、危险物品运输车辆以外的机动车超过4小时未停车休息或者停车休息时间少于20分钟的;<br/>(十四)驾驶机动车不按照规定避让校车的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467930, '正确', '错误', '', '', '1', '', '对未按照国家规定投保交强险的车辆，交通警察可依法予以扣留。', '《道路交通安全法》第九十八条：机动车所有人、管理人未按照国家规定投保机动车第三者责任强制保险的，由公安机关交通管理部门扣留车辆至依照规定投保后，并处依照规定投保最低责任限额应缴纳的保险费的二倍罚款。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467931, '正确', '错误', '', '', '2', '', '车速超过规定时速达到50%的一次记3分。', '《公安部令第123号》规定，驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的，一次记12分。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467932, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/235.jpg', '在这段道路上，只要不影响其他车辆通行的前提下可以掉头。', '黄线用来区分不同方向的车道，一般画在马路正中，好像一条隔离带，把马路隔成两个方向： 1、无论单黄线还是双黄线，只要是实线，就严禁跨越的，比如超车、转弯、掉头等。行车时没有特别情况就不应该越线。 <b>2、无论单黄线还是双黄线，只要是虚线，就可以在保证安全的情况下超车或掉头。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467933, '指示向左变道', '指示前方直行', '指示前方掉头', '指示前方右转', '3', 'http://images.juheapi.com/jztk/a1b1subject1/236.jpg', '这个导向箭头是何含义？', '本题箭头是掉头的箭头，那么本题是指示前方掉头。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467934, '前车采取制动时没看后视镜', '前车采取制动过急', '后车超车时距离前车太近', '后车未与前车保持安全距离', '4', 'http://images.juheapi.com/jztk/a1b1subject1/237.jpg', '这两辆车发生追尾的主要原因是什么？', '因为有人行横道线，所以司机应该知道要提前减速，速度降得低了，后车要刹车就应该应该不会再追尾了。追尾都是后车的责任，因为没有保持足有的安全距离。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467935, '指示直行或掉头', '指示直行或左转弯', '指示直行或向左变道', '指示向左转弯或掉头', '2', 'http://images.juheapi.com/jztk/a1b1subject1/238.jpg', '这个导向箭头是何含义？', '左或者直行', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467936, '靠右侧行驶', '不允许直行', '直行单行路', '直行车让行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/239.jpg', '这个标志是何含义？', '表示一切车辆单向行驶。此标志设在单行路的路口和入口处的适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467937, '不变', '无规律', '变长', '变短', '4', '', '夜间驾驶人对物体的观察明显比白天差，视距会有什么变化？', '夜晚，在路上行车，由于车灯作用，看到的是黑色背景中的亮色物体，所以亮色物体偏大，而眼睛依旧以近大远小的感觉判断，故视距减小。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467938, '正确', '错误', '', '', '1', '', '夜间行车，遇对面来车未关闭远光灯时，应减速行驶，以防两车灯光的交汇处有行人通过时发生事故。', '灯光刺行人眼看不清会发生事故。因此本题是对的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467939, '可借道超车', '占用对面车道', '依次行驶', '穿插等候的车辆', '3', '', '驾驶机动车遇到前方车辆停车排队等候或缓慢行驶时怎么办？', '《道路交通安全法》第四十五条：<br/>机动车遇有前方车辆停车排队等候或者缓慢行驶时，不得借道超车或者占用对面车道，不得穿插等候的车辆。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467940, '过路车', '大客车', '同行车', '清障车', '4', '', '机动车在高速公路上发生故障或交通事故无法正常行驶时由什么车拖曳或牵引？', '《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；但是，警告标志应当设置在故障车来车方向一百五十米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。<b>机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当由救援车、清障车拖曳、牵引。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467941, '正确', '错误', '', '', '1', '', '对有伪造或变造号牌、行驶证嫌疑的车辆，交通警察可依法予以扣留', '对有伪造或变造号牌、行驶证嫌疑的车辆，交通警察可依法予以扣留', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467942, '避险车道', '应急车道', '路肩', '急弯道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/245.jpg', '这个标志是何含义？', '避险车道是指在长陡下坡路段行车道外侧增设的供速度失控车辆驶离正线安全减速的专用车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467943, '右转弯信号', '变道信号', '减速慢行信号', '靠边停车信号', '3', 'http://images.juheapi.com/jztk/a1b1subject1/246.jpg', '这一组交通警察手势是什么信号？', '如果看到别人发火，我们会做由上往下压的动作让他压压火别爆发，同理，由下往下压是让你减速慢行', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467944, '指示向左变道', '指示前方直行', '指示前方左转弯', '指示前方右转弯', '3', 'http://images.juheapi.com/jztk/a1b1subject1/247.jpg', '这个导向箭头是何含义？', '箭头方向是左转弯，因此本题是指示前方左转弯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467945, '及时将伤者送医院抢救或拨打急救电话', '尽量避开，少惹麻烦', '绕过现场行驶', '借故避开现场', '1', '', '行车中遇交通事故受伤者需要抢救时，应怎样做？', '伤者需要帮助，我们应及时伸出援助之手，及时送伤者去医院或拨打急救电话。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467946, '20公里/小时', '30公里/小时', '40公里/小时', '50公里/小时', '2', '', '驾驶机动车通过急弯路时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，<b>最高行驶速度不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 \r<br/>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 \r<br/>（二）掉头、转弯、下陡坡时；　　 \r<br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 \r<br/>（四）在冰雪、泥泞的道路上行驶时；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467947, '正确', '错误', '', '', '1', '', '夜间行车，驾驶人视距变短，影响观察，同时注意力高度集中，易产生疲劳。', '高度集中注意力，比较费神，时间久了就会疲倦。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467948, '左转弯信号', '左转弯待转信号', '减速慢行信号', '右转弯信号', '2', 'http://images.juheapi.com/jztk/a1b1subject1/251.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467949, '预告宽度为3米', '限制宽度为3米', '解除3米限宽', '限制高度为3米', '2', 'http://images.juheapi.com/jztk/a1b1subject1/252.jpg', '这个标志是何含义？', '限制宽度为3米：表示禁止装载宽度超过标志所示数值的车辆通行。此标志设在最大允许宽度受限制的地方。 以图为例：装载宽度不得超过3米', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467950, '正确', '错误', '', '', '1', '', '酒后驾驶发生重大交通事故被依法追究刑事责任的人不能申请机动车驾驶证。', '饮酒后或者醉酒驾驶机动车发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证，终生不得重新取得机动车驾驶证。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467951, '两侧通行', '不准通行', '左侧通行', '右侧通行', '1', 'http://images.juheapi.com/jztk/a1b1subject1/254.jpg', '这个标志是何含义？', '用于引导行车方向，提示道路使用者前方线形变化，注意谨慎驾驶。设于中央隔离设施端部、渠化设施的端部、桥头等。∧表示俩侧通行，/这个方向的表示左侧通行，这个方向表示右侧通行。交通指路标志大全：http://www.jszks.com/onepage/16752.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467952, '最左侧车道', '中间车道', '不用变道', '最右侧车道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/255.jpg', '在这个路口左转弯选择哪条车道？', '只有最左侧车道有左转的标志，而且目前所处位置是虚线位置，是可以变道的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467953, '30公里/小时', '40公里/小时', '50公里/小时', '70公里/小时', '3', 'http://images.juheapi.com/jztk/a1b1subject1/256.jpg', '在这条城市道路上行驶的最高速度不能超过多少？', '《中华人民共和国道路交通安全法实施条例》第四十五条：在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：\r<br/>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；\r<br/><b>(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。</b>\r<br/>图中道路，是同方向有1条车道的城市道路，所以最高速度为50公里/小时，本题答案应为C。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467954, '正确', '错误', '', '', '2', '', '驾驶机动车掉头、转弯、下陡坡时的最高速度不能超过每小时40公里。', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br/>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br/><b>（二）掉头、转弯、下陡坡时；</b>　　 <br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br/>（四）在冰雪、泥泞的道路上行驶时。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467955, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/258.jpg', '在这段道路上不能掉头。', '《道路交通安全法实施条例》第四十九条：机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467956, '可借对向车道通过', '依次停在路口外等候', '从前车两侧穿插通过', '进入路口内等候', '2', '', '驾驶机动车遇有前方交叉路口交通阻塞时怎么办？', '《中华人民共和国道路交通安全法实施条例》第五十三条：<br/>机动车遇有前方交叉路口交通阻塞时，应当依次停在路口以外等候，不得进入路口。 所以应该选A。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467957, '违反道路通行规定', '超过规定时速50%', '造成交通事故后逃逸', '驾车没带驾驶证', '2', '', '下列哪种行为会受到200元以上2000元以下罚款，并处吊销机动车驾驶证？', '第九十九条　有下列行为之一的，由公安机关交通管理部门处二百元以上二千元以下罚款：\r<br/>（一）未取得机动车驾驶证、机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的；\r<br/>（二）将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的；\r<br/>（三）造成交通事故后逃逸，尚不构成犯罪的；\r<br/><b>（四）机动车行驶超过规定时速百分之五十的；（超过规定时速50%）</b>\r<br/>（五）强迫机动车驾驶人违反道路交通安全法律、法规和机动车安全驾驶要求驾驶机动车，造成交通事故，尚不构成犯罪的；\r<br/>（六）违反交通管制的规定强行通行，不听劝阻的；\r<br/>（七）故意损毁、移动、涂改交通设施，造成危害后果，尚不构成犯罪的；\r<br/>（八）非法拦截、扣留机动车辆，不听劝阻，造成交通严重阻塞或者较大财产损失的。\r<br/><b>行为人有前款第二项、第四项情形之一的，可以并处吊销机动车驾驶证</b>；有第一项、第三项、第五项至第八项情形之一的，可以并处十五日以下拘留。\r<br/>\r<br/>A选项中，违反道路通行规定的说法太宽泛了，不适用于具体的处罚标准。\r<br/>C选项中，造成交通事故逃逸的处罚是：吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。如果造成交通事故逃逸，不构成犯罪的，处罚款200以上2000以下。但选项中没有说明是否构成犯罪，因此不能判断罚款金额。\r<br/>D选项中，驾车没带驾驶证的处罚是：扣留机动车，通知驾驶人提供驾驶证。\r<br/>\r<br/>综上，本题应该选B。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467958, '40公里/小时', '50公里/小时', '60公里/小时', '30公里/小时', '4', '', '驾驶机动车在进出非机动车道时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 \r<br/>（一）<b>进出非机动车道</b>，通过铁路道口、急弯路、窄路、窄桥时；　　 \r<br/>（二）掉头、转弯、下陡坡时；　　 \r<br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 \r<br/>（四）在冰雪、泥泞的道路上行驶时；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467959, '占对方道路转弯', '在弯道中心转弯', '加速鸣喇叭通过', '减速鸣喇叭示意', '4', 'http://images.juheapi.com/jztk/a1b1subject1/262.jpg', '在这种路段如何行驶？', '《道路交通安全法实施条例》第五十九条规定：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。<b>机动车驶近急弯、坡道顶端等影响安全视距的路段以及超车或者遇有紧急情况时，应当减速慢行，并鸣喇叭示意。</b>\r<br/>因此，本题答案应为A。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467960, '正确', '错误', '', '', '1', '', '驾驶机动车在没有交通信号的路口遇到前方车辆缓慢行驶时要依次交替通行。', '《中华人民共和国道路交通安全法》第四十五条明确规定：在车道减少的路段、路口，或者在没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口遇到停车排队等候或者缓慢行驶时，机动车应当依次交替通行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467961, '减速让行', '正常行驶', '正在超车', '正在停车', '3', '', '同车道行驶的车辆遇前车有下列哪种情形时不得超车？', '同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：  ①《《前车正在左转弯、掉头、超车的》》;  ②与对面来车有会车可能的;  ③前车为执行紧急任务的警车、消防车、救护车、工程救险车的;  ④行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467962, '省道编号', '国道编号', '县道编号', '乡道编号', '1', 'http://images.juheapi.com/jztk/a1b1subject1/265.jpg', '这个标志是何含义？', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底的，国道是红底的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467963, '应急避难场所', '生活服务区', '行人专用通道', '横过道路设施', '1', 'http://images.juheapi.com/jztk/a1b1subject1/266.jpg', '这个标志是何含义？', '你看他的动作时在跑，所以是紧急的情况，所以答案是A。应急避难场所是应对突发公共事件的一项灾民安置措施，是现代化大城市用于民众躲避火灾、爆炸、洪水、地震、疫情等重大突发公共事件的安全避难场所。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467964, '正确', '错误', '', '', '2', '', '驾驶小型载客汽车在高速公路行驶的最低车速为90公里/小时。', '《道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467965, '指示前方可直行或掉头', '指示前方可左转或掉头', '指示前方可直行或向左变道', '指示前方可直行或左转', '2', 'http://images.juheapi.com/jztk/a1b1subject1/268.jpg', '这个导向箭头是何含义？', '其实是一个左转+掉头的综合体 orz', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467966, '解除禁止超车', '准许变道行驶', '解除禁止变道', '解除禁止借道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/269.jpg', '这个标志是何含义？', '解除禁止超车：表示禁止超车路段结束。此标志设在禁止超车的终点。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467967, '禁止直行和向左变道', '禁止直行和向左转弯', '允许直行和向左变道', '禁止直行和向右转弯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/270.jpg', '这个标志是何含义？', '禁止直行和向左转弯：表示前方路口禁止一切车辆直行和向左转弯。此标志设在禁止直行和向左转弯的路口前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467968, '正确', '错误', '', '', '1', '', '驾驶机动车不按照规定避让校车的，一次记6分。', '根据规定，驾驶机动车不按照规定避让校车的，一次记6分 。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467969, '正确', '错误', '', '', '2', '', '驾驶人违反交通运输管理法规发生重大事故后，因逃逸致人死亡的，处3年以上7年以下有期徒刑。', '发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；<b>因逃逸致人死亡的，处七年以上有期徒刑。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467970, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/273.jpg', '驾驶机动车在路口遇到这种信号灯亮时，要在停止线前停车�望。', '绿灯，赶紧的走你。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461588467971, '减速慢行', '加速通过', '大型车先行', '左侧车辆先行', '1', '', '驾驶机动车通过没有交通信号的交叉路口怎样行驶？', '交叉路口的车流量本来就比较大，在没有交通信号的交叉路口，更应该减速慢行，同时注意观察周围情况，确认可以通过后再通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461588467972, '鸣喇叭示意其让道', '让其先行', '紧随其后鸣喇叭', '加速绕过', '2', '', '行车中遇有非机动车准备绕过停放的车辆时，应怎样做？', '文明驾驶，礼貌让行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576578, '提前开启右转向灯', '不用开启转向灯', '提前开启左转向灯', '提前开启近光灯', '3', '', '驾驶机动车在道路上向左变更车道时如何使用灯光？', '《道路交通安全法实施条例》第五十七条,机动车应当按照下列规定使用转向灯：\r<br/><b>(一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；</b>\r<br/> (二)向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。\r<br/>根据规定，向左变更车道应该<b>提前开启左转向灯</b>，选项C符合规定，因此本题答案为C。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576579, '正确', '错误', '', '', '1', '', '绿灯亮表示前方路口允许机动车通行。', '红灯停，绿灯行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576580, '车辆可临时越线行驶', '车辆禁止越线行驶', '应急车道分界线', '人行横道分界线', '1', 'http://images.juheapi.com/jztk/a1b1subject1/278.jpg', '路右侧车行道边缘白色虚线是什么含义？', '<p><img src=http://tp.mnks.cn/tp_292.jpg>\r<br/>禁止跨越白色实线\r<br/><img src=http://tp.mnks.cn/tp_293.jpg>\r<br/>允许跨越白色虚线</p>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576581, '加速提前抢过', '鸣喇叭示意对向车辆让道', '迅速占用车道，迫使对向来车停车让道', '降低速度或停车，让对向来车优先通行', '4', '', '行车中需要借道绕过前方障碍物，但对向来车已接近障碍物时，应怎样做？', '请注意看题干“对向来车已接近障碍物时”，对方的车更接近障碍物，文明礼让，对向来车优先通行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576582, '正确', '错误', '', '', '1', '', '在大雨天行车，为避免发生“水滑”而造成危险，要控制速度行驶。', '雨天路面积水，摩擦变小轮胎很容易打滑。安全起见，雨天要减速行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576583, '30日', '40日', '50日', '60日', '1', '', '驾驶证记载的驾驶人信息发生变化的要在多长时间内申请换证？', '《公安部令第123号》第五十一条：<br/>具有下列情形之一的，机动车驾驶人应当在三十日内到机动车驾驶证核发地车辆管理所申请换证：<br/><b>（一）在车辆管理所管辖区域内，机动车驾驶证记载的机动车驾驶人信息发生变化的；</b><br/>（二）机动车驾驶证损毁无法辨认的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576584, '直行或向右转弯', '向左转弯', '直行或向左转弯', '向左、向右转弯', '1', 'http://images.juheapi.com/jztk/a1b1subject1/282.jpg', '这个路口允许车辆怎样行驶？', '请看灯，直行和右转灯是绿的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576585, '50公里/小时', '60公里/小时', '30公里/小时', '40公里/小时', '3', '', '驾驶机动车下陡坡、转弯、掉头时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br/>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br/>（二）<b>掉头、转弯、下陡坡</b>时；　　 <br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br/>（四）在冰雪、泥泞的道路上行驶时；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576586, '减速行驶线', '车速确认线', '路口减速线', '车距确认线', '4', 'http://images.juheapi.com/jztk/a1b1subject1/284.jpg', '图中圈内的白色半圆状标记是什么标线？', '白色半圆形车距确认线半球半径为30cm，间隔50m设置，一般在一定路段内连续设置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576587, '大型客车专用车道', '公交线路专用车道', '快速公交系统专用车道', '多乘员车辆专用车道', '2', 'http://images.juheapi.com/jztk/a1b1subject1/285.jpg', '这个标志是何含义？', '此为公交线路专用车道，与快速公交系统专用车道的区别是少了“快速公交”四个字。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576588, '正确', '错误', '', '', '2', '', '机动车在道路上发生故障难以移动时要在车后50米以内设置警告标志。', '机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。 所以不是在50米内，应该是50米以外100米内。扩展提示：高速公路上要在车后150米外设置警告标志。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576589, '连续下坡', '下陡坡', '上陡坡', '连续上坡', '1', 'http://images.juheapi.com/jztk/a1b1subject1/287.jpg', '这个标志是何含义？', '连续下坡：用以提醒车辆驾驶人小心驾驶。当连续下坡总长大于3 km后，应重复设置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576590, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/288.jpg', '这辆小轿车不能在这个位置停车。', '人行横道禁止停车，因为会阻碍行人走路。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576591, '加速通过', '长鸣喇叭', '保持正常速度行驶', '预留出横向安全距离，减速行驶', '4', '', '行车中超越右侧停放的车辆时，为预防其突然起步或开启车门，应怎样做？', '留出安全距离、并减速，万一右侧车辆启动才有足够的空间和时间避让。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576592, '处6个月徒刑', '处1年以上徒刑', '处管制，并处罚金', '处拘役，并处罚金', '4', '', '驾驶机动车在道路上追逐竞驶，情节恶劣，会受到什么处罚？', '根据《中华人民共和国刑法修正案（八）》规定“在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金”。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576593, '开启危险报警闪光灯通行', '直接加速直行通过', '让右方道路车辆先行', '让左方道路车辆先行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/291.jpg', '在路口直行时,遇这种情形如何通行？', '右方的车直行，左方的车左转，左转让直行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576594, '下陡坡', '连续上坡', '上陡坡', '堤坝路', '3', 'http://images.juheapi.com/jztk/a1b1subject1/292.jpg', '这个标志是何含义？', '上陡坡：用以提醒车辆驾驶人小心驾驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576595, '右侧是下坡路段', '靠右侧道路行驶', '靠道路右侧停车', '只准向右转弯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/293.jpg', '这个标志是何含义？', '靠右侧道路行驶：表示只准一切车辆靠右侧道路行驶。此标志设在车辆必须靠右侧行驶的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576596, '正确', '错误', '', '', '1', '', '驾驶机动车在道路上超车完毕驶回原车道时开启右转向灯。', '《道路交通安全法实施条例实施条例》第五十七条：机动车应当按照下列规定使用转向灯：\r<br/>（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；\r<br/><b>（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。</b>\r<br/>题目描述无误，本题答案为对。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576597, '危险报警闪光灯', '远光灯', '近光灯', '雾灯', '3', '', '夜间车辆通过照明条件良好的路段时，应使用什么灯？', '照明条件良好，开启近光就够了。远光会影响前车和前面行人的视线，容易引发危险。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576598, '正确', '错误', '', '', '2', '', '驾驶机动车通过漫水路时要加速行驶。', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576599, '正确', '错误', '', '', '1', '', '车辆下长坡时要减挡行驶，以充分利用发动机的制动作用。', '变速箱高档位高速持续运转中，迅速降低变速箱的档位转速，使变速箱的低档位转速对车辆本身的惯性高速产生抑制，从而产生短时间内的所谓发动机制动(变速箱制动)。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576600, '山区道路', '城市高架路', '城市快速路', '窄桥、弯道', '4', '', '驾驶机动车在下列哪种路段不得超车？', '《中华人民共和国道路交通安全法》第四十三条：\r<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：\r<br/>（一）前车正在左转弯、掉头、超车的；\r<br/>（二）与对面来车有会车可能的；\r<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；\r<br/>（四）行经铁路道口、交叉路口、<b>窄桥、弯道、</b>陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576601, '70公里/小时', '50公里/小时', '40公里/小时', '30公里/小时', '4', '', '驾驶机动车遇雾、雨、雪等能见度在50米以内时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 \r<br/>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 \r<br/>（二）掉头、转弯、下陡坡时；　　 \r<br/>（三）<b>遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时</b>；\r<br/>（四）在冰雪、泥泞的道路上行驶时；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576602, '低速行驶', '注意行人', '步行', '行人先行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/300.jpg', '这个标志是何含义？', '表示该街道只供步行。此标志设在步行街的两端。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576603, '正确', '错误', '', '', '2', '', '驾驶机动车在没有中心线的城市道路上，最高速度不能超过每小时50公里。', '《中华人民共和国道路交通安全法实施条例》第四十五条：<br/>机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br/><b>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里。</b><br/>(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576604, '正确', '错误', '', '', '1', '', '造成交通事故后逃逸构成犯罪的人不能申请机动车驾驶证。', '《交通法》第一百零一条 违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。<br/><b>造成交通事故后逃逸的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576605, '注意行人', '有人行横道', '村庄或集镇', '有小学校', '3', 'http://images.juheapi.com/jztk/a1b1subject1/303.jpg', '这个标志是何含义？', '村庄：用以提醒车辆驾驶人小心驾驶。设在紧靠村庄、集镇且视线不良的路段以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576606, '2分', '3分', '12分', '6分', '3', '', '使用伪造、变造的机动车号牌一次记几分？', '《公安部令第123号》规定，使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的，一次记12分 。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576607, '正确', '错误', '', '', '1', '', '驾驶机动车通过交叉路口要遵守交通信号。', '《道路交通安全法》第四十四条：机动车通过交叉路口，应当按照交通信号灯、交通标志、交通标线或者交通警察的指挥通过；通过没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口时，应当减速慢行，并让行人和优先通行的车辆先行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576608, '正确', '错误', '', '', '1', '', '夜间通过没有路灯或路灯照明不良时，应将近光灯转换为远光灯，但同向行驶的后车不得使用远光灯。', '通过没有路灯或路灯照明不良的路段（非路口）时可以开启远光灯，但是前方有车或行人时，为了不影响他人视线，应关闭远光，改用近光。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576609, '正确', '错误', '', '', '2', '', '初次申领驾驶证的驾驶人在实习期内可以单独驾驶机动车上高速公路行驶。', '《公安部令第123号》第六十五条：<b>驾驶人在实习期内驾驶机动车上高速公路行驶，应当由持相应或者更高准驾车型驾驶证三年以上的驾驶人陪同。</b>其中，驾驶残疾人专用小型自动挡载客汽车的，可以由持有小型自动挡载客汽车以上准驾车型驾驶证的驾驶人陪同。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576610, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/308.jpg', '遇到这种情况的路段，可以进入网状线区域内停车等候。', '《道路交通安全法实施条例》第五十三条：机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，不得在人行横道、网状线区域内停车等候。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576611, '长时停车位', '限时停车位', '免费停车位', '专用停车位', '2', 'http://images.juheapi.com/jztk/a1b1subject1/309.jpg', '这种白色矩形标线框含义是什么？', '有时限的停车位~~', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576612, '正确', '错误', '', '', '2', '', '雨天路面湿滑，车辆制动距离增大，行车中尽量使用紧急制动减速。', '因为路面比较滑，如果紧急制动（急刹车）容易造成车辆失控，最好不要紧急制动。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576613, '保持原有车速行驶', '及时减速、观察后靠右行驶让行', '靠右侧加速行驶', '不让行', '2', '', '行车中遇到后方车辆要求超车时，应怎样做？', '基于安全和和谐的因素，所以要及时减速让行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576614, '临时停靠站', '港湾式停靠站', '应急停车带', '公交车停靠站', '2', 'http://images.juheapi.com/jztk/a1b1subject1/312.jpg', '红色圆圈内标线含义是什么？', '在道路车行道外侧，采取局部拓宽路面的公共交通停靠站”——这就是 “港湾式停靠站”，它的基本形式是路面向人行道方向切入3米，长度90米以上形成泊车港湾，据市公交公司营运安全部黄志峰部长介绍，这种先进站点至少有三大优势：公交泊车不堵道；乘客候车安全；司机实现规范驾驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576615, '6分', '12分', '2分', '3分', '2', '', '上道路行驶的机动车未悬挂机动车号牌的一次记几分？', '《公安部令第123号》规定，上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的，一次记12分 。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576616, '高速公路出口预告', '高速公路入口预告', '高速公路终点预告', '高速公路起点预告', '3', 'http://images.juheapi.com/jztk/a1b1subject1/314.jpg', '这个标志是何含义？', '高速公路终点预告：用于指示高速公路或城市快速路终点，设在高速公路或城市快速路终点处。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576617, '高速公路救援电话号码', '高速公路服务电话号码', '高速公路报警电话号码', '高速公路交通广播频率', '4', 'http://images.juheapi.com/jztk/a1b1subject1/315.jpg', '这个标志是何含义？', '高速公路交通广播频率：用以指示收听高速公路或城市快速路交通信息广播的频率，可在适当地点设置，根据需要可重复设置。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576618, '6个月', '12个月', '2年', '3个月', '2', '', '机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的多长时间为实习期？', '《公安部令第123号》第六十四条：机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的12个月为实习期。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576619, '人行横道线', '减速让行线', '停车让行线', '路口示意线', '1', 'http://images.juheapi.com/jztk/a1b1subject1/317.jpg', '图中圈内的路面标记是什么标线？', '人行横道线俗称斑马线,是由一条一条的白色线组成的主要是供行人穿越马路之用', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576620, '路面不平', '路面高突', '路面低洼', '驼峰桥', '2', 'http://images.juheapi.com/jztk/a1b1subject1/318.jpg', '这个标志是何含义？', '路面高突：用以提醒车辆驾驶人减速慢行。设在路面突然高突以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576621, '40公里/小时', '30公里/小时', '50公里/小时', '70公里/小时', '2', 'http://images.juheapi.com/jztk/a1b1subject1/319.jpg', '在这段城市道路上行驶的最高速度不能超过多少？', '《中华人民共和国道路交通安全法实施条例》<br/>第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br/>  <b>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；</b><br/>  (二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576622, '正确', '错误', '', '', '1', '', '驾驶机动车超车应该提前开启左转向灯、变换使用远近光灯或鸣喇叭。', '《中华人民共和国道路交通安全法实施条例》第四十七条：<br/>机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576623, '小型车车道', '小型车专用车道', '多乘员车辆专用车道', '机动车车道', '4', 'http://images.juheapi.com/jztk/a1b1subject1/321.jpg', '这个标志是何含义？', '此为机动车车道，比多乘员车辆专用车道少俩人。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576624, '向右转弯', '单行路', '只准直行', '直行车道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/322.jpg', '这个标志是何含义？', '向右转弯：表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576625, '分向行驶车道', '掉头和左转合用车道', '禁止左转和掉头车道', '直行和左转合用车道', '2', 'http://images.juheapi.com/jztk/a1b1subject1/323.jpg', '这个标志是何含义？', '左转和掉头合并在一个标志里，你应该能看到的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576626, '左侧变窄', '窄桥', '窄路', '右侧变窄', '2', 'http://images.juheapi.com/jztk/a1b1subject1/324.jpg', '这个标志是何含义？', '窄桥：用以警告车辆驾驶人注意前方桥面宽度变窄，应谨慎驾驶。设在桥面净宽较两端路面宽度变窄，且桥的净宽小于6 m的桥梁以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576627, '正确', '错误', '', '', '1', '', '通过经常发生塌方、泥石流的山区地段，避免停车。', '经常发生塌方、泥石流的都属于危险地段，所以能尽快离开就尽快离开，避免停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576628, '处20元以上200元以下罚款', '取消申领驾驶证资格', '1年内不得再次申领驾驶证', '2年内不能再次申领驾驶证', '3', '', '提供虚假材料申领驾驶证的申请人会承担下列哪种法律责任？', '修订后的《机动车驾驶证申领和使用规定》第七十八条第一款规定：“隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在一年内不得再次申领机动车驾驶证。”', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576629, '右转车道', '掉头车道', '左转车道', '分向车道', '3', 'http://images.juheapi.com/jztk/a1b1subject1/327.jpg', '这个标志是何含义？', '表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576630, '正确', '错误', '', '', '2', '', '驾驶机动车在没有中心线的公路上，最高速度不能超过每小时70公里。', '《中华人民共和国道路交通安全法实施条例》第四十五条：<br/>机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br/><b>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；</b><br/>(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576631, '高速公路右侧出口预告', '高速公路目的地预告', '高速公路左侧出口预告', '高速公路下一出口预告', '1', 'http://images.juheapi.com/jztk/a1b1subject1/329.jpg', '这个标志是何含义？', '高速公路右侧出口预告：用于预告前方出口，设置在距离高速公路或城市快速路减速车道的渐变段起点处。出口预告标志应同时附着出口编号标志。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576632, '正确', '错误', '', '', '1', '', '驾驶机动车上路行驶应当按规定悬挂号牌。', '《道路交通安全法》第十一条：驾驶机动车上道路行驶，<b>应当悬挂机动车号牌</b>，放置检验合格标志、保险标志，并随车携带机动车行驶证。因此本题答案应为正确。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576633, '靠路口中心点右侧转弯', '靠路口中心点左侧转弯', '骑路口中心点转弯', '不能左转弯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/331.jpg', '在这个路口怎样左转弯？', '注意，本题中出现的红绿灯是最普通的三色灯，不具备指示通行方向的作用，在没有限制行进方向的路口，前方绿灯即可视为左右前均为放行状态。所以只要选择正确的左转方式就可以了，规定左转要靠路口中心点左侧转弯。所以应该选B。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576634, '持续重踏', '间断重踏', '持续轻踏', '间断轻踏', '4', '', '车辆涉水后，应保持低速行驶，怎样操作制动踏板，以恢复制动效果？', '采用间断轻踏摩擦刹车片来加热蒸发水分，恢复制动', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576635, '路面不平', '路面低洼', '路面高突', '有驼峰桥', '2', 'http://images.juheapi.com/jztk/a1b1subject1/333.jpg', '这个标志是何含义？', '路面低洼：用以提醒车辆驾驶人减速慢行。设在路面突然低洼以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576636, '正确', '错误', '', '', '1', '', '驾驶机动车通过窄路、窄桥时的最高速度不能超过每小时30公里。', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br/>（一）进出非机动车道，通过铁路道口、急弯路、<b>窄路、窄桥时；</b>　　 <br/>（二）掉头、转弯、下陡坡时；　　 <br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br/>（四）在冰雪、泥泞的道路上行驶时。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576637, '鸣喇叭催其让行', '保持正常车速行驶', '提前减速或停车避让', '向左占道，谨慎驶过', '3', '', '车辆在较窄的山路上行驶时，如果靠山体的一方不让行，应怎样做？', '如果靠山体的一方不让行，为了保障行车安全，一定要提前减速或停车避让。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576638, '正确', '错误', '', '', '1', '', '车辆后轮胎爆裂，车尾会摇摆不定，驾驶人应双手紧握转向盘，控制车辆保持直线行驶，减速停车。', '<b>爆胎后，首先要控制住方向，保持直线行驶，再减速停车。</b>因为如果突然爆胎，瞬间靠右会导致与后方来车相撞，应该先保持直线行驶，再减速停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576639, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/337.jpeg', '机动车 仪表板上（如图所示）亮表示发动机可能机油压力过高。', '图片表示发动机可能机油压力不足。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576640, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/338.jpg', '这辆小型载客汽车进入高速公路行车道的行为是正确的。', '这辆车直接驶入了行车道，要先在加速车道，也就是靠右的车道加速后才能驶入，所以错', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576641, '依法追究刑事责任', '处200以上2000元以下罚款', '吊销机动车行驶证', '处15日以下拘留', '2', '', '对驾驶拼装机动车上路行驶的驾驶人，会受到下列哪种处罚？', '根据《中华人民共和国道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。<b>对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证。</b>选项中只有C项符合规定，因此本题答案为C。\r<br/>\r<br/>【拓展延伸】\r<br/>本题很多学员容易选择“D、吊销机动车行驶证”，注意上面的法规说明是［<b>吊销驾驶证</b>］不是“行驶证”。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576642, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/340.jpg', '在道路上遇到这种情况可以从两侧超车。', '《中华人民共和国道路交通安全法》第四十三条：<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br/>（一）前车正在左转弯、掉头、超车的；<br/>（二）与对面来车有会车可能的；<br/><b>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；</b><br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576643, '不靠山体的一方先行', '靠山体的一方先行', '重车让空车先行', '速度慢的先行', '1', '', '驾驶机动车在没有道路中心线的狭窄山路怎样会车？', '在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：\r<br/>1)减速靠右行驶，并与其他车辆、行人保持必要的安全距离。\r<br/>2)在有障碍的路段，无障碍的一方先行：但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。\r<br/>3)在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行。\r<br/>4)<b>在狭窄的山路，不靠山体的一方先行。</b>\r<br/>5)夜间会车应当在距相对方向来车150m以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576644, '正确', '错误', '', '', '2', '', '驾驶机动车行经城市没有列车通过的铁路道口时允许超车。', '《中华人民共和国道路交通安全法》第四十三条：\r<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：\r<br/>（一）前车正在左转弯、掉头、超车的；\r<br/>（二）与对面来车有会车可能的；\r<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；\r<br/>（四）行经<b>铁路道口、</b>交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576645, '正确', '错误', '', '', '2', '', '夜间驾驶机动车在窄路、窄桥会车时正确的做法是使用远光灯。', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯，<b>在窄路、窄桥与非机动车会车时应当使用近光灯。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576646, '50', '60', '30', '40', '2', '', '驾驶车辆进入高速公路加速车道后，应尽快将车速提高到每小时多少公里以上？', '高速公路加速车道最低行驶速度60公里每小时！ 《道路交通安全法实施条例》第78条规定：<b>“高速公路最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。”</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576647, '正确', '错误', '', '', '2', '', '驾驶机动车在没有交通信号的路口要尽快通过。', '《道路交通安全法》第四十四条：通过没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口时，应当减速慢行，并让行人和优先通过的车辆先行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576648, '依靠惯性加速冲过', '挂空挡滑行驶过', '保持原速通过', '低速缓慢平稳通过', '4', '', '车辆通过凹凸路面时，应怎样做？', '遇到凹凸不平的路面，要减速缓慢通过，安全第一。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576649, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/347.jpeg', '机动车仪表板上（如图所示）亮，表示驻车制动器操纵杆可能没松到底。', '图中标志表示行车制动系统异常或故障，并非驻车制动器操纵杆没松到底。题目描述错误，因此答案为错。\r<br/><p><img src=http://tp.mnks.cn/tp_374.jpg>\r<br/>行车制动故障\r<br/><img src=http://tp.mnks.cn/tp_376.jpg>\r<br/>驻车制动没松</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576650, '关闭远光灯', '使用危险报警闪光灯', '交替使用远近光灯', '使用远光灯', '3', 'http://images.juheapi.com/jztk/a1b1subject1/348.jpg', '在这种环境下通过路口如何使用灯光？', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。\r<br/>因此，本题答案应为D。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576651, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/349.jpg', '在这种急弯道路上行车应交替使用远近光灯。', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。\r<br/>题目描述正确，因此本题答案应为正确。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576652, '依次停车等候', '鸣喇叭让前车让路', '从右侧占道转弯', '从前车左侧转弯', '1', '', '在路口右转弯遇同车道前车等候放行信号时如何行驶？', '《道路交通安全法实施条例》第五十一条：机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行，向右转弯遇有同车道前车正在等候放行信号时，依次停车等候。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576653, '开启危险报警闪光灯', '提前开启右转向灯', '变换使用远近光灯', '不用指示灯提示', '2', '', '驾驶机动车在道路上靠路边停车过程中如何使用灯光？', '《道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯： (一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯； <b>(二)向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。</b>\r<br/>注意题干所说的是“靠边停车过程中”，而非已经靠边停车了。所以应该提前开启右转向灯，本题答案应为D。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576654, '正确', '错误', '', '', '1', '', '违反交通信号灯通行的一次记6分。', '《公安部令第123号》规定，驾驶机动车违反道路交通信号灯通行的，一次记6分 。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576655, '从前车右侧路肩进入路口', '从有空隙一侧进入路口', '每车道一辆依次交替驶入路口', '向左变道穿插进入路口', '3', '', '驾驶机动车在车道减少的路口，遇到前方车辆依次停车或缓慢行驶时怎么办？', '《道路交通安全法实施条例》第五十三条：机动车在车道减少的路口、路段，遇有前方机动车停车排队等候或者缓慢行驶的，应当每车道一辆依次交替驶入车道减少后的路口、路段。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576656, '离开路面', '由路中移到路侧', '距离不变', '由高变低', '2', '', '夜间行车中，前方出现弯道时，灯光照射会发生怎样的变化？', '车辆在平面路上行驶，灯光距离不变；在出现一般的弯道，灯光由路中移动到路侧；上坡时，灯光由高变低；出现急弯或大坑时，灯光离开路面。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576657, '停在网状线区域内等待', '停在路口以外等待', '跟随前车通过路口', '停在路口内等待', '2', 'http://images.juheapi.com/jztk/a1b1subject1/355.jpg', '在路口遇到这种情形时怎样做？', '《道路交通安全法实施条例》第五十三条：机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，<b>不得在人行横道、网状线区域内停车等候。</b>\r<br/>根据规定，题目中情形为了不加重拥堵，机动车应该停在路口以外等待。B项符合要求，因此本题答案为B。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576658, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/356.jpg', '在交叉路口遇到这种情况享有优先通行权。', '右转让左转。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576659, '路面附着力增大', '无法观察到暗坑和凸起的路面', '能见度低，视野模糊', '日光反射阻挡视线', '2', '', '水淹路面影响行车安全，不易通行的原因是什么？', '因为水淹没了路面，所以看不清是暗坑和凸起的路面。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576660, '未按规定悬挂号牌', '故意遮挡号牌', '占用非机动车道', '逆向行驶', '2', 'http://images.juheapi.com/jztk/a1b1subject1/358.jpg', '这辆在道路上行驶的机动车有下列哪种违法行为？', '《道路交通安全法》第十一条规定：机动车号牌应当按照规定悬挂并保持清晰、完整，不得故意遮挡、污损。 \r<br/>观察可知，图中机动车的车号牌被故意遮挡住了。但并没有存在逆向行驶、不悬挂号牌、占用非机动车道这些违法行为，因此本题答案应为C。\r<br/>\r<br/>【拓展延伸】\r<br/>图中黄色虚线，设置在 <b>[ 双向道路 ]</b> 的中间，并非单向车道。图中车辆在对向车道上，并未逆向行驶，注意审图。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576661, '路面宽度渐变标线', '车行道变多标线', '接近障碍物标线', '施工路段提示线', '1', 'http://images.juheapi.com/jztk/a1b1subject1/359.jpg', '路面上的黄色标线是何含义？', '左下角有一个标志表示路面变窄，所以黄线就是表示路面宽度有变化。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576662, '30公里/小时', '40公里/小时', '50公里/小时', '70公里/小时', '4', 'http://images.juheapi.com/jztk/a1b1subject1/360.jpg', '在这条公路上行驶的最高速度不能超过多少？', '根据《中华人民共和国道路交通安全法实施条例》第四十五条：……在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：　　(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；　　(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。图为公路，所以最高时速为70公里/每小时。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576663, '适当减速通过', '空挡滑行通过', '停车确认安全后通过', '加速尽快通过', '3', '', '驾驶机动车通过没有交通信号和管理人员的铁路道口怎样通过？', '《道路交通安全法》第四十六条：机动车通过铁路道口时，应当按照交通信号或者管理人员的指挥通行；<b>没有交通信号或者管理人员的，应当减速或者停车，在确认安全后通过。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576664, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/362.jpeg', '打开后雾灯开关，（如图所示）亮起。', '图中是前雾灯亮起。\r<br/><p><img src=http://tp.mnks.cn/tp_365.jpg>\r<br/>前雾灯\r<br/><img src=http://tp.mnks.cn/tp_366.jpg>\r<br/>后雾灯</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576665, '正确', '错误', '', '', '2', '', '驾驶机动车在道路上超车时可以不使用转向灯。', '《道路交通安全法实施条例实施条例》第五十七条 机动车应当按照下列规定使用转向灯：<b>（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；</b>（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。\r<br/>超车必须使用转向灯，否则极易造成严重的交通事故。题目描述有误，本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576666, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/364.jpg', '这辆红色轿车变更车道的方法和路线是正确的。', '红车在变道的时候没有打转向灯，并且红车明显没留出足够安全的行车距离强行并线，这是错误的变更车道方式。\r<br/>题目描述有误，本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576667, '正确', '错误', '', '', '1', '', '驾驶机动车在雾天行车开启雾灯和危险报警闪光灯。', '《中华人民共和国道路交通安全法实施条例》第五十八条：机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。<b>机动车雾天行驶应当开启雾灯和危险报警闪光灯。</b>\r<br/>因此，本题答案应为正确。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576668, '3年', '5年', '1年', '2年', '1', '', '驾驶人因服兵役、出国（境）等原因无法办理审验时，延期审验期限最长不超过多长时间？', '《机动车驾驶证申领和使用规定》第五十条：机动车驾驶人因服兵役、出国（境）等原因，无法在规定时间内办理驾驶证期满换证、提交身体条件证明的，可以向机动车驾驶证核发地车辆管理所申请延期办理，<b>延期期限最长不超过三年</b>。延期期间机动车驾驶人不得驾驶机动车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576669, '正确', '错误', '', '', '2', '', '驾驶机动车上坡时,在将要到达坡道顶端时要加速并鸣喇叭。', '肯定是错的，鸣喇叭对，但是加速是错误的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576670, '正确', '错误', '', '', '1', '', '车辆不得在高速公路匝道上掉头。', '按照规定：驶入确定匝道后，迅速提高车速，但不得超过规定速度，在匝道上，不准超车、掉头、停车、和倒车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576671, '正确', '错误', '', '', '1', '', '驾驶机动车上路前应当检查车辆安全技术性能。', '《道路交通安全法》第二十一条规定：驾驶人驾驶机动车上道路行驶前，应当对机动车的安全技术性能进行认真检查；不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576672, '专用停车场', '露天停车场', '室内停车场', '内部停车场', '3', 'http://images.juheapi.com/jztk/a1b1subject1/370.jpg', '这个标志是何含义？', '你看那个P字上边还有个遮挡，说明是室内的，如果没有上边那个遮挡就是露天停车场。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576673, '可直接驶入交叉路口', '不能驶入交叉路口', '可借对向车道通过路口', '在保证安全的情况下驶入交叉路口', '2', '', '在堵车的交叉路口绿灯亮时，车辆应怎样做？', '前方堵车，这时进入交叉路口只能更堵。因此堵车时即便绿灯亮了，也不要驶入路口内。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576674, '将车停在道路中间', '将车停到不妨碍交通的地方', '开启近光灯或雾灯', '就地停车排除故障', '2', '', '机动车在道路上发生故障，需要停车排除时，驾驶人应该怎么办？', '机动车在行驶道路上发生故障，应将机动车移至不妨碍交通的地方停放，并要在来车方向的50—100米的地方设置警示标志。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576675, '机动车更换发动机', '加装前后防撞装置', '改变车身颜色', '更换车身或者车架', '2', '', '已注册登记的小型载客汽车有下列哪种情形，所有人不需要办理变更登记？', '《道路交通安全法实施条例》第六条：已注册登记的机动车有下列情形之一的，<b>机动车所有人应当向登记该机动车的公安机关交通管理部门申请变更登记</b>： \r<br/><b>(一)改变机动车车身颜色的； \r<br/>(二)更换发动机的； \r<br/>(三)更换车身或者车架的； </b>\r<br/>(四)因质量有问题，制造厂更换整车的； \r<br/>(五)营运机动车改为非营运机动车或者非营运机动车改为营运机动车的；\r<br/>(六)机动车所有人的住所迁出或者迁入公安机关交通管理部门管辖区域的。\r<br/>注意题目说的是 <b>[不需要办理变更登记的]</b> ，结合法规和题目要求，更换发动机、改变车身颜色、更换车身或者车架都需要办理变更登记，而B项加装前后防撞装置是不需要办理变更登记的，因此本题答案选B。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576676, '正确', '错误', '', '', '1', '', '在狭窄的路段会车时，应做到礼让三先：先慢、先让、先停。', '文明驾驶，礼貌让行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576677, '示廓灯或近光灯', '雾灯', '远光灯', '危险报警闪光灯', '1', '', '车辆驶入双向行驶隧道前，应开启什么灯？', '<b>凡是车辆进入双向行驶隧道，都应该开启近光灯或示廓灯，</b>提醒其他车辆，避免发生事故。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576678, '从中心线虚线处掉头', '从右侧车道掉头', '进入路口后掉头', '在人行横道上掉头', '1', 'http://images.juheapi.com/jztk/a1b1subject1/376.jpg', '在这种路口怎样进行掉头？', '虚线处可掉头，实线是不能压的，所以实线处不能掉头、变道等。因此应该从图中中心线虚线处掉头才对。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576679, '正确', '错误', '', '', '1', '', '冰雪道路行车，由于积雪对光线的反射，极易造成驾驶人目眩而产生错觉。', '因为太阳光通过冰的折射到人的眼睛里，冰在太阳光下就好像是一面镜子。所以会容易目眩而产生幻觉。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576680, '停车让行人先行', '从行人前方绕行', '鸣喇叭提醒行人', '从行人后方绕行', '1', 'http://images.juheapi.com/jztk/a1b1subject1/378.jpg', '遇到这种情形时，应怎么办？', '通过人行横道应减速慢行，遇到行人则需停车让行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576681, '正确', '错误', '', '', '2', '', '驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的一次记6分。', '《公安部令第123号》规定，驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的，一次记12分 。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576682, '保持匀速通过', '尽快加速通过', '停车察明水情', '低速缓慢通过', '3', 'http://images.juheapi.com/jztk/a1b1subject1/380.jpg', '驾驶机动车遇到这种桥时首先怎样办？', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576683, '使用远光灯', '使用雾灯', '不使用灯光', '开启右转向灯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/381.jpg', '在这种天气条件下行车如何使用灯光？', '能见度这么低一定要开启雾灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576684, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/382.jpg', '车在这种条件的道路上，最高速度不能超过每小时50公里。', '冰雪路面最高速度不能超过每小时30公里。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576685, '110公里/小时', '120公里/小时', '90公里 /小时', '100公里/小时', '1', 'http://images.juheapi.com/jztk/a1b1subject1/383.jpg', '在这条高速公路上行驶时的最高速度不能超过多少？', '《道路交通安全法实施条例》第七十八条：道路限速标志标明的车速与上述车道行驶车速的规定不一致的，按照道路限速标志标明的车速行驶。\r<br/>图中的红圈白底图标，是限速标志。表示最高行驶速度不得超过110公里/小时，因此本题应该选B。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576686, '指示标线', '禁止标线', '警告标志', '辅助标线', '1', 'http://images.juheapi.com/jztk/a1b1subject1/384.jpg', '路中心黄色虚线属于哪一类标线？', '根据GB 5768.3-2009《道路交通标志和标线第3部分：道路交通标线》的规定：\r<br/>黄色虚线，划于路段中时，用以分隔对向行驶的交通流。\r<br/>起到指示的作用，属于指示标线。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576687, '15公里/小时', '20公里/小时', '30公里/小时', '40公里/小时', '3', '', '驾驶机动车通过铁路道口时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 \r<br/>（一）进出非机动车道，<b>通过铁路道口</b>、急弯路、窄路、窄桥时；　　 \r<br/>（二）掉头、转弯、下陡坡时；　　 \r<br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 \r<br/>（四）在冰雪、泥泞的道路上行驶时；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576688, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/386.jpg', '可以从这个位置直接驶入高速公路行车道。', '应该按照标线行驶，不要乱开。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576689, '正确', '错误', '', '', '2', '', '机动车行驶中，车上少年儿童可不使用安全带。', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。相比较成人，少年儿童自我保护能力更低，更应该系安全带。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576690, '正确', '错误', '', '', '1', '', '夜间驾驶机动车通过人行横道时需要交替使用远近光灯。', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。\r<br/>题目描述正确，因此本题答案应为正确。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576691, '正确', '错误', '', '', '2', '', '驾驶机动车超车后立即开启右转向灯驶回原车道。', '错，不能立即，应在与被超车辆拉开必要的安全距离后，开启右转向灯驶回原车道。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576692, '倒车灯', '近光灯', '雾灯', '远光灯', '3', '', '雾天行车时，应及时开启什么灯？', '雾天行车应开启雾灯和危险报警闪光灯，本题选D。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576693, '正确', '错误', '', '', '2', '', '夜间通过无交通信号灯控制的交叉路口时，不得变换远、近光灯。', '换远近光灯是为了让来车认识到前方有车，应注意减速慢行注意安全。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576694, '正确', '错误', '', '', '2', '', '拼装的机动车只要认为安全就可以上路行驶。', '《道路交通安全法》第一百条规定：驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废，处200元以上2000元以下罚款，并吊销机动车驾驶证。\r<br/>无论司机认为拼装车是否安全，都不能开上路，一旦开上路就是违法行为。本题描述有误，因此本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576695, '尽量绕道躲避', '立即报警，停车观望', '协助保护现场，并立即报警', '加速通过，不予理睬', '3', '', '行车中遇有前方发生交通事故，需要帮助时，应怎样做？', '维护道路交通安全，人人有责。当他人出了事故，请协助保护现场，并立即报警。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576696, '逼对方靠右行驶', '用大灯警示对方', '主动给对方让行', '紧靠道路中心行驶', '3', '', '行车中遇到对向来车占道行驶，应怎样做？', '文明驾驶，礼貌让行。不开赌气车，安全第一。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576697, '保持车距标志', '提醒危险标志', '检验合格标志', '产品合格标志', '3', '', '下列哪种标志是驾驶机动车上路行驶应当在车上放置的标志？', '《道路交通安全法》第十一条：驾驶机动车上道路行驶，应当悬挂机动车号牌，<b>放置检验合格标志</b>、保险标志，并随车携带机动车行驶证。因此本题答案应为D。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576698, '正确', '错误', '', '', '1', '', '行车中当驾驶人意识到爆胎时，应在控制住方向的情况下，轻踏制动踏板，使车辆缓慢减速，逐渐平稳地停靠于路边。', '这种情况下为了保障安全，驾驶员首先要做的就是控制住方向，然后缓慢减速，平稳停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576699, '关闭前大灯', '开启远光灯', '开启雾灯', '变换远、近光灯', '4', '', '驾驶机动车在夜间超车时怎样使用灯光？', '《实施条例》第四十七条规定：机动车超车时，应当提前开启左转向灯、<b>变换使用远、近光灯或者鸣喇叭</b>。夜间视线不佳，应该变换使用远、近光灯提醒其他车辆。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576700, '正确', '错误', '', '', '1', '', '驾驶机动车在道路上违反交通安全法规的行为属于违法行为。', '题干中明确表述“违反道路交通安全法规”，违反法律法规即视为违法行为。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576701, '正确', '错误', '', '', '1', '', '车辆在通过山区道路弯道时，要做到“减速、鸣喇叭、靠右行”。', '通过弯道时，大家都知道要减速，而在山区道路，由于前方状况很难了解，因此，若要经过弯道，应当鸣号告知其他车辆你要通过弯道了，靠右行是为了避免在弯道时遇到对向来车，若不靠右行驶，很可能会与来车发生碰撞。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576702, '正确', '错误', '', '', '1', '', '行车中，发现行人突然横过道路时，应迅速减速避让。', '发现行人突然横过道路时，应迅速减速避让，确保行人安全。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576703, '正确', '错误', '', '', '1', '', '驾驶机动车在铁路道口、桥梁、陡坡、隧道或者容易发生危险的路段不能掉头。', '《中华人民共和国道路交通安全法实施条例》第四十九条第一款：<br/>“机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段,不得掉头。”', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576704, '正确', '错误', '', '', '1', '', '驾驶人有使用其他车辆号牌、行驶证嫌疑的，交通警察可依法扣留车辆。', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：有伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、 保险标志、驾驶证或者<b>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑</b>的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576705, '集中注意力谨慎驾驶', '立即减速靠边停车', '以正常速度行驶', '减速行驶', '2', '', '在暴雨天气驾车，刮水器无法刮净雨水时，应怎样做？', '暴雨天气驾车，刮水器无法刮净雨水的话，司机很难完全看清楚路况，十分危险。为了安全起见，一定要立即减速靠边停车，不能再继续向前行驶。选项C符合要求，因此答案选C。\r<br/>\r<br/>【拓展延伸】\r<br/>发生这种情况后，司机绝不能继续向前开，“A减速行驶”、“B集中注意力谨慎驾驶”、“D以正常速度行驶”都包含了继续行驶的意思，是错误的。\r<br/>而很多人会认为C项“立即”是错误的，可以这样理解：<b>立即减速靠边停车≠紧急制动</b>，是说司机意识到危险情况后，立即采取了减速并靠边停车这样的应对措施。是没有问题的，注意审题。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576706, '正确', '错误', '', '', '1', '', '车辆在山区上坡路驾驶，减挡要及时、准确、迅速，避免拖挡行驶导致发动机动力不足。', '上坡前要减档，避免拖挡行驶导致的发动机动力不足而熄火。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576707, '减速，保持足够间距，随时准备停车', '保持正常车速行驶', '鸣喇叭提醒，加速通过', '随时准备紧急制动', '1', '', '车辆驶近停在车站的公交车辆时，为预防公交车突然起步或行人从车前穿出，应怎样做？', '遇到行人，减速行驶，随时准备停车。如果紧急制动的话可能会导致后车追尾，所有选择A。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576708, '在人行横道上停车', '距离路边超过30厘米', '在有禁停标线路段停车', '在非机动车道停车', '1', 'http://images.juheapi.com/jztk/a1b1subject1/406.jpg', '这样在路边临时停放机动车有什么违法行为？', '图中汽车停在人行横道。规定是：在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576709, '前后雾灯开启', '前后位置灯开启', '前照灯开启', '危险报警闪光灯开启', '2', 'http://images.juheapi.com/jztk/a1b1subject1/407.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '位置灯就是车辆示宽灯，也就是当你开启双跳灯后，前后左右四个闪的灯 宽灯就是转向信号灯同时闪烁，用于警告作用。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576710, '分道信息预告', '道路分岔处预告', '地点和距离预告', '十字交叉路口预告', '4', 'http://images.juheapi.com/jztk/a1b1subject1/408.jpg', '这个标志是何含义？', '上边有十字能看到吧？', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576711, '正确', '错误', '', '', '1', '', '车辆驶离高速公路时，应当经减速车道减速后进入匝道。', '下高速公路时必须在减速车道减速至60码以下才能出高速公路。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576712, '减速慢行信号', '靠边停车信号', '停止信号', '右转弯信号', '4', 'http://images.juheapi.com/jztk/a1b1subject1/410.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576713, '正确', '错误', '', '', '1', '', '车辆长时间停放时，应选择停车场停车。', '路边只能临时停车，要长时间停车，应该选择停车场。以免妨碍其他车辆通行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576714, '100公里/小时', '110公里/小时', '60公里/小时', '90公里/小时', '4', 'http://images.juheapi.com/jztk/a1b1subject1/412.jpg', '在这条车道行驶的最低车速是多少？', '高速路同方向有3条车道，最左侧的为110公里到120公里，中间的为90公里到110公里，最右侧的速度范围为60公里到90公里。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576715, '正确', '错误', '', '', '2', '', '变更车道时只需开启转向灯，便可迅速转向驶入相应的行车道。', '<b>开转向灯后，要充分观察确认安全后方可使入车道，</b>本题错在“迅速”且没有观察。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576716, '正确', '错误', '', '', '2', '', '驾驶人将机动车交给驾驶证被暂扣的人驾驶的，交通警察给予口头警告。', '《道路交通安全法》第九十九条：将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处吊销机动车驾驶证。\r<br/>并不是口头警告这么简单。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576717, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/415.jpeg', '开启前照灯远光时仪表板上（如图所示）亮起。', '<p><img src=http://tp.mnks.cn/tp_387.jpg>\r<br/>前照远光灯</p>\r<br/><p><img src=http://tp.mnks.cn/tp_388.jpg>\r<br/>前照近光灯</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576718, '处20以上200元以下罚款', '追究刑事责任', '处15日以下拘留', '吊销机动车驾驶证', '4', '', '对驾驶已达到报废标准的机动车上路行驶的驾驶人，会受到下列哪种处罚？', '根据《中华人民共和国道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。<b>对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证。</b>因此本题答案为B。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576719, '正确', '错误', '', '', '1', '', '饮酒后驾驶机动车的一次记12分。', '根据规定，<b>饮酒驾驶机动车辆，罚款1000元—2000元、记12分并暂扣驾照6个月</b>；饮酒驾驶营运机动车，罚款5000元，记12分，处以15日以下拘留，并且5年内不得重新获得驾照。醉酒驾驶机动车辆，吊销驾照，5年内不得重新获取驾照，经过判决后处以拘役，并处罚金；醉酒驾驶营运机动车辆，吊销驾照，10年内不得重新获取驾照，终生不得驾驶营运车辆，经过判决后处以拘役，并处罚金。\r<br/>题目描述正确，因此答案为对。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576720, '保持正常速度行驶', '提前减速、观察，谨慎驾驶', '鸣喇叭，迅速通过', '提前加速通过', '2', '', '车辆在主干道上行驶，驶近主支干道交汇处时，为防止与从支路突然驶入的车辆相撞，应怎样做？', '最好的做法就是提前减速、观察，谨慎驾驶。这样才能避免与支路驶来的车相撞。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576721, '正确', '错误', '', '', '1', '', '机动车号牌损毁，机动车所有人要向登记地车辆管理所申请补领、换领。', '机动车号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向登记地车辆管理所申请补领、换领。申请时，机动车所有人应当填写申请表并提交身份证明。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576722, '没有开启危险报警闪光灯', '没有将车停到路边', '没有立即排除故障', '没有设置警告标志', '4', 'http://images.juheapi.com/jztk/a1b1subject1/420.jpg', '找出这辆故障车有哪种违法行为？', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576723, '正确', '错误', '', '', '2', '', '行车中突遇对向车辆强行超车，占据自己车道时，可不予避让，迫使对方让路。', '行车要始终记得安全文明驾驶，对方要超车，应及时减速避让。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576724, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/422.jpg', '在这个路口可以掉头。', '《道路交通安全法实施条例》第四十九条：机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576725, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/423.jpg', '打开位置灯开关，（如图所示）亮起。', '该指示灯是用来显示车辆示宽灯（也叫示廓灯、位置灯）的工作状态，平时为熄灭状态，当示宽灯打开时，该指示灯随即点亮。当示宽灯关闭或者关闭示宽灯打开大灯时，该指示灯自动熄灭。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576726, '刑事责任', '民事责任', '直接责任', '经济责任', '1', '', '机动车驾驶人违法驾驶造成重大交通事故构成犯罪的，依法追究什么责任？', '根据《道路交通安全法》第一百零一条规定：<b>违反道路交通安全法律、法规规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，\r\n\r\n并由公安机关交通管理部门吊销机动车驾驶证。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576727, '正确', '错误', '', '', '1', '', '夜间行车，要尽量避免超车，确需超车时，可变换远近光灯向前车示意。', '只要在确保安全和不违规的情况下都可以超车。<b>变换远近灯光是为了提醒前方车辆的注意，</b>虽然只是一个动作，但对于保证安全来说却是很重要的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576728, '可靠弯道外侧行驶', '可短时间借用对方的车道', '可加速沿弯道切线方向通过', '应“减速、鸣喇叭、靠右行”', '4', '', '山区道路车辆进入弯道前，在对面没有来车的情况下，应怎样做？', '在急转弯处，应该减速慢行；鸣喇叭，靠右行，是为了提醒对面来车。总之，安全驾驶为主。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576729, '双向分道线', '潮汐车道线', '可跨越分道线', '单向分道线', '2', 'http://images.juheapi.com/jztk/a1b1subject1/427.jpg', '路中两条双黄色虚线是什么标线？', '路中两条双黄色虚线是潮汐车道线。\r<br/>“潮汐车道”是指根据早晚交通流量不同情况，对有条件的道路，试点开辟潮汐车道，通过车道灯的指示方向变化，控制主干道车道行驶方向，来调整车道数，提高车道使用效率。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576730, '正确', '错误', '', '', '1', '', '夜间行车，驾驶人的视野受限，很难观察到灯光照射区域以外的交通情况，因此要减速行驶。', '晚上开车的时候受到光线暗的局限，相对观察事物的距离也就受到局限，所以视距比较短，因此很难观察到灯光照射区域以外的交通情况。为了安全考虑，减速行驶是必须的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576731, '正确', '错误', '', '', '1', '', '驾驶机动车在高速公路违法占用应急车道行驶的一次记6分。', '《公安部令第123号》规定，驾驶机动车在高速公路或者城市快速路上违法占用应急车道行驶的，一次记6分 。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576732, '正确', '错误', '', '', '1', '', '驾驶车辆向右变更车道时，应提前开启右转向灯，注意观察，在确保安全的情况下，驶入要变更的车道。', '向右变道时，提前开启右转向灯，是为了提示后车。同时要观察周围情况，具有变道条件时再变更车道。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576733, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/431.jpg', '驾驶机动车在路口遇到这种信号灯禁止通行。', '仔细看图，图中三个圆形灯从左到右分别是红色、黄色、绿灯。此时绿灯亮起，根据“红灯停，绿灯行”的原则，车辆是可以通行的。题目描述错误，本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576734, '前方40米减速', '最低时速40公里', '限制40吨轴重', '限制最高时速40公里', '4', 'http://images.juheapi.com/jztk/a1b1subject1/432.jpg', '这个标志是何含义？', '限制最高时速40公里：表示该标志至前方限制速度标志的路段内，机动车行驶速度不得超过标志所示数值。此标志设在需要限制车辆速度的路段的起点。以图为例：限制行驶时速不得超过40公里。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576735, '职业资格证', '身份证', '驾驶证', '工作证', '3', '', '驾驶机动车应当随身携带哪种证件？', '《道路交通安全法》第十九条：驾驶人应当按照驾驶证载明的准驾车型驾驶机动车；驾驶机动车时，应当随身携带机动车驾驶证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576736, '2分', '3分', '6分', '12分', '4', '', '驾驶与准驾车型不符的机动车一次记几分？', '根据公安部《机动车驾驶证申领和使用规定》（附件3：道路交通安全违法行为记分分值），<b>驾驶与准驾车型不符的机动车的，一次应记12分。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576737, '正确', '错误', '', '', '1', '', '在泥泞路上制动时,车轮易发生侧滑或甩尾，导致交通事故。', '常识题，泥泞路摩擦系数低。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576738, '正确', '错误', '', '', '1', '', '机动车在高速公路上遇前方交通受阻时，应当跟随前车顺序排队，并立即开启危险报警闪光灯，防止追尾。', '开启危险警报闪光灯是为了提醒后车：前方有故障，请减速慢行，谨防追尾。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576739, '正确', '错误', '', '', '2', '', '行车中当驾驶人意识到车辆爆胎时，应在控制住方向的情况下采取紧急制动，迫使车辆迅速停住。', '车辆爆胎后，应先控制方向，保持直线行驶，然后利用发动机制动，缓慢减速停车。不能采取紧急制动，容易翻车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576740, '压力表', '电流表', '水温表', '燃油表', '4', 'http://images.juheapi.com/jztk/a1b1subject1/438.jpg', '这个仪表是何含义？', '汽油不够了，看到那个加油站常见的机器没。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576741, '减速通过', '匀速通过', '一停、二看、三通过', '加速通过', '3', '', '驾驶车辆通过无人看守的铁路道口时，应怎样做？', '一停，是停在未到铁道前，不是停在铁路上，二看，是在铁道前观察是否有火车要经过，三通过，才是安全通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576742, '正确', '错误', '', '', '2', '', '驾驶机动车违反道路交通安全法律法规发生交通事故属于交通违章行为。', '题干中有明确表述“违反道路交通安全法”，因此属于违法行为，并非违章行为。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576743, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/441.jpeg', '机动车仪表板上（如图所示）亮表示发动机可能机油量不足。', '图中的标志是机油的标志。 该指示灯用来显示发动机内机油的压力状况，该指示灯常亮，说明该车发动机机油压力低于规定标准，需要维修。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576744, '驻车制动解除', '行车制动器失效', '制动系统出现异常', '制动踏板没回位', '3', 'http://images.juheapi.com/jztk/a1b1subject1/442.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '可能出现故障仪表板上的图标才会亮。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576745, '正确', '错误', '', '', '2', '', '车辆应靠高速公路右侧的路肩上行驶。', '路肩通常是让有事故的车辆停靠维修的，如果车辆没有问题的话不能在路肩上行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576746, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/444.jpeg', '机动车仪表板上（如图所示）亮时，提醒驾驶人座椅没调整好。', '该指示灯用来显示安全带是否处于锁止状态，当该灯点亮时，说明安全带没有及时的扣紧。有些车型会有相应的提示音。当安全带被及时扣紧后，该指示灯自动熄灭。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576747, '后雾灯打开', '前照灯近光打开', '前照灯远光打开', '前雾灯打开', '1', 'http://images.juheapi.com/jztk/a1b1subject1/445.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '前雾灯朝左，后雾灯朝右。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576748, '减速、低速会车或停车让行', '向左占道，让对方减速让行', '打开前照灯，示意对方停车让行', '加速选择理想位置', '1', '', '会车前选择的交会位置不理想时，应怎样做？', '发现交会位置不理想时，应减速或停车让行，以寻找下一个理想会车位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576749, '正确', '错误', '', '', '1', '', '机动车在高速公路上发生故障时，将车上人员迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', '《道路交通安全法》第五十二条： \r<br/>机动车在道路上发生故障，需要停车排除故障时，驾驶人应当立即开启危险报警闪光灯，将机动车移至不妨碍交通的地方停放；难以移动的，应当持续开启危险报警闪光灯，并在来车方向设置警告标志等措施扩大示警距离，必要时迅速报警。 \r<br/>第六十八条： \r<br/>机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；警告标志应当设置在故障车来车方向一百五十米以外，<b>车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。 </b>\r<br/>根据规定，题目描述无误，因此本题答案为对。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576750, '禁止车辆掉头', '禁止向左变道', '禁止向左转弯', '禁止驶入左车道', '3', 'http://images.juheapi.com/jztk/a1b1subject1/448.jpg', '这个标志是何含义？', '禁止向左转弯：表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576751, '暂扣驾驶证', '吊销驾驶证', '追究刑事责任', '处10日以下拘留', '2', '', '驾驶拼装机动车上路行驶的驾驶人，除按规定接受罚款外，还要受到哪种处理？', '根据《中华人民共和国道路交通安全法》的规定，对驾驶拼装的机动车或已达到报废标准的机动车上路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证，对车辆予以收缴，强制报废。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576752, '行李舱开启', '一侧车门开启', '发动机舱开启', '燃油箱盖开启', '1', 'http://images.juheapi.com/jztk/a1b1subject1/450.jpeg', '机动车仪表板上（如图所示）这个符号表示什么？', '后边为行李舱，前边为发动机舱。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576753, '紧跟其后，伺机再超', '停止继续超车', '加速继续超越', '连续鸣喇叭加速超越', '2', '', '驾驶人在超车时，前方车辆不减速、不让道，应怎样做？', '行车安全第一，如果继续强制超车，会有发生事故的危险。应该停止继续超车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576754, '发动机转速表', '行驶速度表', '区间里程表', '百公里油耗表', '1', 'http://images.juheapi.com/jztk/a1b1subject1/452.jpg', '这个仪表是何含义？', '此为发动机转速表，跟时速表对比记忆。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576755, '危险报警闪光灯闪烁', '右转向指示灯闪烁', '左转向指示灯闪烁', '车前后位置灯闪烁', '1', 'http://images.juheapi.com/jztk/a1b1subject1/453.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '危险报警闪光灯，通常称为“双蹦”（红三角里有个！的标志按扭开关，俗称双闪灯 或双跳灯），是一种提醒其他车辆与行人注意本车发生了特殊情况的信号灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576756, '没有系好安全带', '安全带出现故障', '安全带系得过松', '已经系好安全带', '1', 'http://images.juheapi.com/jztk/a1b1subject1/454.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '此为没有系好安全带。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576757, '正确', '错误', '', '', '2', '', '车辆在高速公路匝道提速到每小时60公里以上时，可直接驶入行车道。', '高速公路匝道是高速公路出口或入口靠右侧的一条道路（一般在150－200米）。入口处匝道过后是加速车道，出口处匝道紧接在减速道之后。匝道内车速一般要求低于40km/h，加速车道内应迅速加速至60km/h以上。　 <br/>驾驶车辆从匝道进入高速公路加速车道后，打开左转向灯或者不打开均可，尽快将车速提高到每小时60公里以上，<b>并仔细观察车道上车辆行驶的情况，选择驶入行车道的时机。</b>不准在加速车道紧急制动或停车。行车道车辆稀少时，可以正常行驶车辆后驶入行车道；遇高速公路正常行驶车辆尾随相距较近时，应控制好车速，在所有车辆通过后再驶入行车道。<br/><b>本题错在直接两字，没有一个观察的动作。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576758, '正确', '错误', '', '', '1', '', '车辆通过学校和小区应注意观察标志标线，低速行驶，不要鸣喇叭。', '<b>学校区域和居民区通常禁止鸣笛，减少噪音，</b>以免影响居民生活和学生学习。同时，这些地方人流量大，要时时留意交通状况，减速慢行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576759, '防抱死制动系统出现故障', '驻车制动器处于解除状态', '行车制动系统出现故障', '驻车制动器处于制动状态', '4', 'http://images.juheapi.com/jztk/a1b1subject1/457.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '驻车制动器处于制动状态，就是手刹没松开。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576760, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/458.jpeg', '机动车仪表板上（如图所示）亮时，提醒驾驶人安全带插头未插入锁扣。', '该指示灯用来显示安全带是否处于锁止状态，当该灯点亮时，说明安全带没有及时的扣紧。有些车型会有相应的提示音。当安全带被及时扣紧后，该指示灯自动熄灭。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576761, '正确', '错误', '', '', '2', '', '车辆在高速公路匝道上可以停车。', '按照规定：驶入确定匝道后，迅速提高车速，但不得超过规定速度，<b>在匝道上，不准超车、掉头、停车、和倒车。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576762, '正确', '错误', '', '', '1', '', '机动车驾驶人在实习期内驾驶机动车不得牵引挂车。', '《公安部令第123号》第六十五条规定：机动车驾驶人在<b>实习期内</b>不得驾驶公共汽车、营运客车或者执行任务的警车、消防车、救护车、工程救险车以及载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车；<b>驾驶的机动车不得牵引挂车。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576763, '水温表', '燃油表', '电流表', '压力表', '1', 'http://images.juheapi.com/jztk/a1b1subject1/461.jpg', '这个仪表是何含义？', '看到那个温度计没说明他是管温度的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576764, '使用所学车型的教练车由教练员随车指导', '使用所学车型的教练车单独驾驶学习', '使用所学车型的教练车由非教练员的驾驶人随车指导', '使用私家车由教练员随车指导', '1', '', '未取得驾驶证的学员在道路上学习驾驶技能，下列哪种做法是正确的？', '《公安部令第123号》规定：未取得驾驶证的学员在道路上学习驾驶技能，使用所学车型的教练车由教练员随车指导。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576765, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/463.jpg', '在交叉路口遇到这种情况时，要在红灯亮以前加速通过路口。', '黄灯亮了，车身没越过白色停止线，所以要停车等待。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576766, '正确', '错误', '', '', '1', '', '驾驶车辆进入交叉路口前，应降低行驶速度，注意观察，确认安全。', '进入交叉路口时，要安全驾驶，减速前行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576767, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/465.jpeg', '机动车仪表板上（如图所示）亮，表示行车制动系统可能出现故障。', '圆圈里面是“P” 表示驻车制动器处于制动状态，即手刹没松开，但是车辆没有发生故障。<br/>里面是“！” 表示制动系统异常或故障。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576768, '正确', '错误', '', '', '2', '', '上路行驶的机动车未随车携带身份证的，交通警察可依法扣留机动车。', '《道路交通安全法》第九十五条规定：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车。\r<br/>不携带身份证的话，是不会被扣留车辆的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576769, '5年内', '10年内', '20年内', '终生', '4', '', '机动车驾驶人造成事故后逃逸构成犯罪的，吊销驾驶证且多长时间不得重新取得驾驶证？', '《道路交通安全法》第一百零一条规定：<b>造成交通事故后逃逸的且构成犯罪的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。</b>上述规定泛指各类交通事故，只要是造成交通事故后逃逸的，就应当由公安机关交通管理部门吊销其机动车驾驶证，并且终身不得重新取得驾驶证，而不论其造成交通事故后果的大小。也就是说，法律规定惩罚的交通肇事逃逸行为，与驾驶人造成交通事故后果的轻重无关；构成犯罪的，依法追究刑事责任。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576770, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/468.jpeg', '机动车仪表板上（如图所示）亮时，表示驻车制动器处于制动状态。', '圆圈里面是“P” 表示驻车制动器处于制动状态，即手刹没松开，但是车辆没有发生故障。\r<br/>里面是“！” 表示制动系统异常或故障。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576771, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/469.jpg', '仪表显示当前车速是20公里/小时。', '图显示的单位是 1/min×1000，意思是每一分钟（注意是分钟）1000转。转速指针为2，即2000转每分钟。<br/>而车速还要根据车轮大小来确定。【2×车轮半径×3.14（圆周率）】车轮周长×转速。<br/>此图，应该是<b>转速标示图</b>。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576772, '车前后位置灯亮起', '车前后示宽灯亮起', '左转向指示灯闪烁', '右转向指示灯闪烁', '4', 'http://images.juheapi.com/jztk/a1b1subject1/470.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '这个没有问题吧。。。老衲也不知如何解释这个题。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576773, '正确', '错误', '', '', '1', '', '伪造、变造机动车驾驶证构成犯罪的将被依法追究刑事责任。', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处十五日以下拘留，并处二千元以上五千元以下罚款；<b>构成犯罪的，依法追究刑事责任。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576774, '正确', '错误', '', '', '1', '', '对未取得驾驶证驾驶机动车的，追究其法律责任。', '《道路交通安全法》第九十九条规定：<b>未取得机动车驾驶证、</b>机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处十五日以下拘留。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576775, '18个月', '16个月', '12个月', '6个月', '3', '', '机动车驾驶人初次申领驾驶证后的实习期是多长时间？', '《公安部令第123号》第六十四条规定：机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的12个月为实习期。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576776, '前车减速让行', '前车正在左转弯', '前车靠边停车', '前车正在右转弯', '2', '', '驾驶机动车在下列哪种情形下不能超越前车？', '《道路交通安全法》第四十三条： <br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： <br/><b>（一）前车正在左转弯、掉头、超车的； </b><br/>（二）与对面来车有会车可能的； <br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576777, '加速通过', '鸣喇叭警告', '减速让行', '临近时突然加速', '3', '', '行车中，遇非机动车抢行时，应怎样做？', '文明驾驶，礼貌让行，注意非机动车的安全。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576778, '遇到道路拥堵时', '在道路上跟车行驶时', '机动车发生故障停车时', '引领后车行驶时', '3', '', '危险报警闪光灯可用于下列什么场合？', '危险报警闪光灯是一种提醒其他车辆与行人注意本车发生了特殊情况的信号灯。因此当机动车发生故障停车时，可以使用危险报警闪光灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576779, '行李舱开启', '发动机舱开启', '燃油箱盖开启', '一侧车门开启', '2', 'http://images.juheapi.com/jztk/a1b1subject1/477.jpeg', '机动车仪表板上（如图所示）这个符号表示什么？', '前盖为发动机舱，后边为行李舱。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576780, 'T型路口', '分流路口', '减速通行', '此路不通', '4', 'http://images.juheapi.com/jztk/a1b1subject1/478.jpg', '这个标志是何含义？', '用以指示前方道路为死胡同，无出口、不能通行。该标志为蓝底白色街区红色图案。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576781, '左右两侧均可超越', '从前车的右侧超越', '从前车的左侧超越', '从无障碍一侧超越', '3', 'http://images.juheapi.com/jztk/a1b1subject1/479.jpg', '这种情况超车时，从前车的哪一侧超越？', '超车只能从左侧超越。因为驾驶员在左边，左边超车，驾驶员能观察到相对行驶车道有无来车。而右方超车驾驶员是看不到前方是否来车或速度慢的车辆。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576782, '直接向右转弯', '抢在对面车前右转弯', '鸣喇叭催促', '先让对面车左转弯', '4', 'http://images.juheapi.com/jztk/a1b1subject1/480.jpg', '在这个路口右转弯如何通行？', '对面左转车辆已到路口中间，本车先让对面左转弯车转弯再右转。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576783, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/481.jpeg', '打开左转向灯开关，（如图所示）亮起。', '图中朝右的箭头亮起，表示打开的是右转向灯。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576784, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/482.jpg', '在路口遇这种情形要减速让行。', '当驾车在路口遇到图中减速让行标志时，机动车应主动减速让行。\r<br/><p><img src=http://tp.mnks.cn/tp_177.jpg>\r<br/>减速让行\r<br/><img src=http://tp.mnks.cn/tp_176.jpg>\r<br/>停车让行\r<br/></p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576785, '防抱死制动系统故障', '驻车制动器处于解除状态', '行车制动系统故障', '安全气囊处于故障状态', '1', 'http://images.juheapi.com/jztk/a1b1subject1/483.jpeg', '机动车仪表板上（如图所示）亮表示什么？', 'ABS就是防抱死制动系统', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576786, '3年', '5年', '6年', '12年', '3', '', '初次申领的机动车驾驶证的有效期为多少年？', '机动车驾驶证有效期首次申领为6年，6年有效期内每个记分周期均未达到12分的，换发10年有效期的机动车驾驶证；在机动车驾驶证的10年有效期内，每个记分周期均未达到12分的，换发长期有效的机动车驾驶证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576787, '雪地起步模式', '空气循环', '空调制冷', '冷风暖气风扇', '4', 'http://images.juheapi.com/jztk/a1b1subject1/485.jpeg', '机动车仪表板上（如图所示）这个符号表示什么？', '看它长的多像风扇。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576788, '大型客货车', '出租汽车', '执行任务的救护车', '公共汽车', '3', '', '同车道行驶的车辆前方遇到下列哪种车辆不得超车？', '亲，人命关天，您就别超了。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576789, '正确', '错误', '', '', '1', '', '通过铁路道口时，不得超车。', '《中华人民共和国道路交通安全法》 第四十三条 同车道行驶的机动车，……有下列情形之一的，不得超车： <br/>（一）前车正在左转弯、掉头、超车的； <br/>（二）与对面来车有会车可能的； <br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br/>（四）<b>行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576790, '海关检查', '停车检查', '边防检查', '禁止通行', '2', 'http://images.juheapi.com/jztk/a1b1subject1/488.jpg', '这个标志是何含义？', '停车检查：表示机动车必须停车接受检查。此标志设在关卡将近处，以便要求车辆接受检查或缴费等手续。标志中可加注说明检查事项。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576791, '减速慢行', '正常行驶', '加速通过', '连续鸣喇叭', '1', '', '当驾驶车辆行经两侧有非机动车行驶且有积水的路面时，应怎样做？', '文明驾驶。减速行驶能够防止水溅到行人身上。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576792, '处3年以下徒刑或者拘役', '处3年以上7年以下徒刑', '处5年以上徒刑', '处7年以上徒刑', '1', '', '驾驶人违反交通运输管理法规发生重大事故致人重伤、死亡，可能会受到什么刑罚？', '《刑法》第一百三十三条规定：<b>违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；</b>交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576793, '地板及迎面出风', '空气内循环', '空气外循环', '侧面及地板出风', '1', 'http://images.juheapi.com/jztk/a1b1subject1/491.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '亲，这个真的选A。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576794, '正确', '错误', '', '', '1', '', '科目三考试分为道路驾驶技能考试和安全文明驾驶常识考试两部分。', '机动车驾驶人考试内容分为道路交通安全法律、法规和相关知识考试科目（以下简称“科目一”）、场地驾驶技能考试科目（以下简称“科目二”）、<b>道路驾驶技能和安全文明驾驶常识考试科目（以下简称“科目三”）。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576795, '正确', '错误', '', '', '1', '', '准驾车型为小型汽车的，可以驾驶小型自动挡载客汽车。', '小型汽车（C1）准驾车型：小型、微型载客汽车以及轻型、微型载货汽车；轻型、微型专项作业车，小型、微型自动挡载客汽车以及轻型、微型自动挡载货汽车，低速载货汽车，三轮汽车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576796, '3次', '4次', '5次', '6次', '3', '', '在驾驶技能准考证明的有效期内，科目二和科目三道路驾驶技能考试预约次数不得超过多少次？', '在驾驶技能准考证明有效期内，科目二和科目三道路驾驶技能考试预约考试的次数不得超过五次。第五次预约考试仍不合格的，已考试合格的其他科目成绩作废。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576797, '已开启前雾灯', '已开启前照灯近光', '已开启前照灯远光', '已开启后雾灯', '3', 'http://images.juheapi.com/jztk/a1b1subject1/495.jpeg', '机动车仪表板上（如图所示）亮时表示什么？', '远光灯的灯光走向是直的。近光灯的灯光走向是朝下的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576798, '正确', '错误', '', '', '1', '', '车辆在冰雪路面紧急制动易产生侧滑，应低速行驶，可利用发动机制动进行减速。', '发动机制动有三个显著特点：一是由于差速器的作用，可将制动力矩平均地分配在左右车轮上，减少侧滑、甩尾的可能性；二是有效地减少脚制动的使用频率，避免因长时间使用制动器，导致制动器摩擦片的温度升高，使制动力下降，甚至失去作用；<b>三是车速始终被限定在一定范围内，有利于及时降速或停车，确保行车安全。</b>\r<br/>因此在冰雪路面上，应该尽可能选择发动机制动减速。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576799, '主要街道', '高架路', '人行横道', '环城高速', '3', '', '驾驶机动车行经下列哪种路段不得超车？', '《中华人民共和国道路交通安全法》第四十三条：<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br/>（一）前车正在左转弯、掉头、超车的；<br/>（二）与对面来车有会车可能的；<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；<br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、<b>人行横道、</b>市区交通流量大的路段等没有超车条件的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576800, '正确', '错误', '', '', '2', '', '服用国家管制的精神药品可以短途驾驶机动车。', '《道路交通安全法》第二十二条规定：饮酒、<b>服用国家管制的精神药品</b>或者麻醉药品，或者患有妨碍安全驾驶机动车的疾病，或者过度疲劳影响安全驾驶的，不得驾驶机动车。本题题干表述有误，因此本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576801, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/499.jpg', '在这个路口不能掉头。', '此处有指示掉头的标志，而且虚线是可以临时跨越的，所以是可以掉头的。题目描述有误，因此本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576802, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/500.jpg', '未上坡的车辆遇到这种情况让对向下坡车先行。', '《道路交通安全法实施条例》第四十八条： 在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行。图中下坡的车已经到了中途，可以先行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576804, '喝咖啡后', '喝牛奶后', '饮酒后', '喝茶后', '3', '', '驾驶人在下列哪种情况下不能驾驶机动车？', '2011年2月25日，十一届全国人大常委会第十九次会议表决通过了刑法修正案（八）。首次将醉酒驾车这种严重危害群众利益的行为规定为犯罪，并于5月1日正式实施。具体规定为：在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。\r<br/>国家严禁机动车驾驶员酒后驾驶，因此本题应选A。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576805, '正确', '错误', '', '', '1', '', '车辆下坡行驶，要适当控制车速，充分利用发动机进行制动。', '下坡时不能空挡滑行，应充分利用发动机制动。防止下坡时刹车发生打滑，所以应该利用发动机制动。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576806, '正确', '错误', '', '', '2', '', '驾驶人持超过有效期的驾驶证可以在1年内驾驶机动车。', '《道路交通安全法实施条例》第二十八条：机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576807, '正确', '错误', '', '', '1', '', '车辆在高速公路以每小时100公里的速度行驶时，距同车道前车100米以上为安全距离。', '根据《中华人民共和国道路交通安全法实施条例》第八十条 机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离，车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576808, '车距确认线', '减速行驶线', '车速确认线', '路口减速线', '1', 'http://images.juheapi.com/jztk/a1b1subject1/506.jpg', '图中圈内的白色折线是什么标线？', '白色平行粗实线，用以提供车辆驾驶人保持行车安全距离的参考，与车距确认标志配合使用。设在常发生超车、易肇事或其他有需要路段。高速公路车距确认标线作用为，见此标线时，驾驶人应与前车保持行车安全距离，避免发生交通事故。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576809, '正确', '错误', '', '', '1', '', '车辆行至泥泞或翻浆路段时，应停车观察，选择平整、坚实的路段缓慢通过。', '泥泞翻浆路段行车容易发生侧滑，应该停车仔细观察后，降低车速，选择平整、坚实的路段缓慢通过。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576810, '提示前车，将要向左变更行驶路线', '提示前车，将要向右变更行驶路线', '提示后车，将要向左变更行驶路线', '提示后车，将要向右变更行驶路线', '3', '', '机动车向左转弯、向左变更车道，驶离停车地点及掉头时，提前开启左转向灯是为了什么？', '开启左转向灯，主要是为了提醒后车，开启左转向灯，当然是向左变更车道了。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576811, '在路口前实线区内根据需要', '进入路口实线区内', '在路口停止线前', '在虚线区按导向箭头指示', '4', '', '驾驶车辆在交叉路口前变更车道时，应怎样驶入要变更的车道？', '实线不可跨线行驶，应该在虚线区按导向箭头指示变更车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576812, '减速慢行', '保持正常车速通过', '空挡滑行通过', '加速通过', '1', '', '驾驶人在行车中经过积水路面时，应怎样做？', '行车中经过积水路面时，应特别留意减速慢行；<b>加速或保持正常车速通过，溅起的水花会冲击前挡风玻璃，阻挡驾驶员的视线，</b>无法观察前方情况，极易导致车辆失控或交通事故。另外，驾驶车辆行经两侧有行人或非机动车行驶且有积水的路面时，更应减速慢行，<b>以免泥水飞溅到行人或非机动车驾驶员身上。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576813, '道路入口标线', '可跨越式分道线', '道路入口减速线', '道路出口标线', '1', 'http://images.juheapi.com/jztk/a1b1subject1/511.jpg', '路面上白色虚线和三角地带标线组成的是什么标线？', '<p><img src=http://tp.mnks.cn/tp_303.jpg>\r<br/>道路出口标线\r<br/><img src=http://tp.mnks.cn/tp_304.jpg>\r<br/>道路入口标线</p>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576814, '注意行人', '注意儿童', '学校区域', '人行横道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/512.jpg', '这个标志是何含义？', '注意行人：用以警告车辆驾驶人减速慢行，注意行人。设在行人密集，或不易被驾驶员发现的人行横道线以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576815, '停车让对方车辆通过', '开启左转向灯向左行驶', '开前照灯告知对方让行', '加速超越障碍后会车', '1', 'http://images.juheapi.com/jztk/a1b1subject1/513.jpg', '遇到这种情形怎样行驶？', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，在有障碍的路段，<b>无障碍的一方先行；</b>但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576816, '减速或停车让行', '鸣喇叭示意其让道', '立即变道绕过行人', '抢在行人之前通过', '1', '', '驶近没有人行横道的交叉路口时，发现有人横穿道路，应怎样做？', '如果没有人行横道，行人就有可能从任何地方走出。遇到行人一定要减速或停车让行，注意行人的安全。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576817, '正确', '错误', '', '', '1', '', '驾驶机动车不能进入红色叉形灯或者红色箭头灯亮的车道。', '《道路交通安全法实施条例》第四十条规定： 红色叉形灯或者箭头灯亮时，禁止本车道车辆通行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576818, '警告标志', '禁令标志', '指示标志', '指路标志', '3', 'http://images.juheapi.com/jztk/a1b1subject1/516.jpg', '这属于哪一种标志？', '指示标志是交通标志中主要标志的一种，，用以指示车辆和行人按规定方向、地点行驶。 指示标志的颜色为蓝底、白图案；形状分为圆形、长方形和正方形。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576819, '正确', '错误', '', '', '1', '', '车辆发生爆胎后，驾驶人在尚未控制住车速前，不要冒险使用行车制动器停车，以避免车辆横甩发生更大的险情。', '爆胎后，驾驶人在尚未控制住车速的时候，是不能急踩刹车的，此举极易导致翻车，甚至与后面来车追尾。正确的做法应该是紧握方向盘，控制方向，极力保持直线行驶，利用发动机制动，减档或抢档减速度，轻踏制动踏板缓慢减速，平稳靠边停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576820, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/518.jpg', '这个标志的含义是前方即将行驶至Y型交叉路口？', '交叉路口：用以警告车辆驾驶人谨慎慢行，注意横向来车。设在平面交叉路口驶入路段的适当位置。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576821, '禁驶区', '网状线', '中心圈', '导流线', '3', 'http://images.juheapi.com/jztk/a1b1subject1/519.jpg', '这个路面标记是什么标线？', '中心圈，设置在交叉路口中心的白色圆形或菱形区域，用以区分车辆大、小转弯，及交叉路口车辆左右转弯的指示，车辆不得压线行驶。机动车向左转弯时，必须紧靠中心圈小转弯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576822, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/520.jpg', '这个标志的含义是提醒车辆驾驶人前方是无人看守铁路道口。', '有人看守铁路道口：用以警告车辆驾驶人注意慢行或及时停车。设在车辆驾驶人不易发现的道口以前适当位置。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576823, '对方饮酒的', '对事实及成因有争议的', '未造成人身伤亡，对事实及成因无争议的', '造成人身伤亡的', '3', '', '驾驶机动车发生以下交通事故，哪种情况适用自行协商解决？', '《中华人民共和国道路交通安全法实施条例》第八十六条规定:“机动车与机动车、机动车与非机动车在道路上发生未造成人身伤亡的交通事故，当事人对事实及成因无争议的，在记录交通事故的时间、地点、对方当事人的姓名和联系方式、机动车牌号、驾驶证号、保险凭证号、碰撞部位，并共同签名后，撤离现场，自行协商损害赔偿事宜。当事人对交通事故事实及成因有争议的，应当迅速报警。”', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576824, '正确', '错误', '', '', '1', '', '上路行驶的机动车未放置检验合格标志的, 交通警察可依法扣留机动车。', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576826, '50a', '50b', '50c', '50d', '3', 'http://images.juheapi.com/jztk/a1b1subject1/524.jpg', '如图所示，以下哪种情况可以超车?(图50a.50b. 50c. 50d)', '大家都知道的，实线一侧是不可以跨越的，只有虑线的一侧才可以，所以只有50c具备超车条件。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576827, '不得牵引挂车', '允许牵引1辆挂车', '可以牵引不超过本身载质量的挂车', '允许牵引总质量700千克以下的挂车', '1', '', '下列关于大型、中型载客汽车能否牵引挂车的说法，正确的是什么？', '客车是禁止拖挂车辆的，因为客车的用途是载客，要保证旅客的安全为本。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576828, '提醒车辆驾驶人前方有两个及以上的连续上坡路段', '提醒车辆驾驶人前方有向上的陡坡路段', '提醒车辆驾驶人前方有向下的陡坡路段', '提醒车辆驾驶人前方有两个及以上的连续下坡路段', '4', 'http://images.juheapi.com/jztk/a1b1subject1/526.jpg', '这个标志是何含义？', '连续下坡：用以提醒车辆驾驶人小心驾驶。当连续下坡总长大于3 km后，应重复设置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576829, '违章行为', '违法行为', '过失行为', '违规行为', '2', '', '驾驶机动车跨越双实线行驶属于什么行为？', '《道路交通安全法》规定双实线是不能跨越的，违反道路交通安全法，就是违法行为。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576830, '易滑路段', '急转弯路', '反向弯路', '连续弯路', '3', 'http://images.juheapi.com/jztk/a1b1subject1/528.jpg', '这是什么交通标志？', '反向弯路：用以警告车辆驾驶人减速慢行。设置位置为两反向圆曲线起点的外面，但不应进入相邻的圆曲线内。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576831, '使用远光灯', '不能使用近光灯', '不能使用远光灯', '使用雾灯', '3', 'http://images.juheapi.com/jztk/a1b1subject1/529.jpg', '在这种雨天跟车行驶使用灯光，以下做法正确的是？', '《道路交通安全法实施条例》第五十八条：机动车在夜间没有路灯、照明不良或者遇有雾、<b>雨</b>、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，<b>但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。</b>机动车雾天行驶应当开启雾灯和危险报警闪光灯。\r<br/>图中情况为下雨天，正确的做法应该是打开近光灯，不能使用远光灯。因此本题答案应为C。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576832, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/530.jpg', '这个标志的含义是提醒车辆驾驶人前方路段通过村庄或集镇。', '警告标志：注意村庄，注意行车安全。用以提醒车辆驾驶人小心驾驶。设在紧靠村庄、集镇且视线不良的路段以前适当位置。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576833, '高速公路出口', '高速公路起点', '高速公路入口', '高速公路终点', '2', 'http://images.juheapi.com/jztk/a1b1subject1/531.jpg', '这个标志是何含义？', '高速公路起点：设在高速公路或城市快速路的起点。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576834, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/532.jpg', '这个标志的作用是用以警告车辆驾驶人谨慎慢行，注意横向来车。', '图中是“十字交叉”标志，用以警告车辆驾驶人谨慎慢行，注意横向来车相交，设在视线不良的平面交叉路口驶入路段的适当位置的标志。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576835, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/533.jpg', '这个标志的含义是告示前方是塌方路段，车辆应绕道行驶。', '<b>图中是 [施工路段] 标志。</b>用以告示前方道路施工，车辆应减速慢行或绕道行驶。该标志可以作为临时标志支设在施工路段以前适当位置。题目描述有误，因此本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576836, '交叉路口', '窄路窄桥', '路宽车少', '弯道陡坡', '3', '', '夜间行车，可选择下列哪个地段超车?', '超车前，应选择视线良好、平直宽阔、左右均无障碍且前方路段200米范围内没有来车的道路，在保证安全的前提下方能超车。千万不要盲目超车。几个选项中，当然是选路宽车少的路段了。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576837, '2年', '3年', '4年', '5年', '4', '', '营运载客汽车从注册登记之日起，多长时间以内每年检验1次？', '载客的,从注册起,5年内,每年检1次，非载客的,从注册起,10年内,每年检1次,超过10年的,每6个月检1次，看来对客车是要求的严重些!', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576838, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/536.jpeg', '打开前雾灯开关，（如图所示）亮起。', '图中是后雾灯亮起。\r<br/><p><img src=http://tp.mnks.cn/tp_365.jpg>\r<br/>前雾灯\r<br/><img src=http://tp.mnks.cn/tp_366.jpg>\r<br/>后雾灯</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576839, '正确', '错误', '', '', '1', '', '正在执行社区戒毒、强制隔离戒毒、社区康复措施，车辆管理所将注销其驾驶证。', '机动车驾驶人具有下列情形之一的，车辆管理所应当注销其机动车驾驶证：<br/>（一）死亡的；<br/>（二）提出注销申请的；<br/>（三）丧失民事行为能力，监护人提出注销申请的；<br/>（四）身体条件不适合驾驶机动车的；<br/>（五）有器质性心脏病、癫痫病、美尼尔氏症、眩晕症、癔病、震颤麻痹、精神病、痴呆以及影响肢体活动的神经系统疾病等妨碍安全驾驶疾病的；<br/>（六）<b>被查获有吸食、注射毒品后驾驶机动车行为，正在执行社区戒毒、强制隔离戒毒、社区康复措施，或者长期服用依赖性精神药品成瘾尚未戒除的。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576840, '切断动力输出', '自动控制方向', '减轻制动惯性', '防止车轮抱死', '4', 'http://images.juheapi.com/jztk/a1b1subject1/538.jpg', '机动车在紧急制动时ABS系统会起到什么作用？', 'ABS防抱死系统；车轮不卡死、硬化路面制动时间短平稳、可以用力踩制动、系统自动张合8次/每秒。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576842, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/540.jpg', '这个标志的含义是提醒车辆驾驶人前方是过水路面或漫水桥路段。', '警告标志：过水路面，应当减速慢行。用以提醒车辆驾驶人谨慎慢行。设在过水路面或漫水桥路段以前适当位置。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576843, '6个月', '1年', '2年', '3年', '1', '', '大型、中型非营运载客汽车从注册登记之日起，超过10年的，每多长时间检验1次？', '载客的,从注册起,5年内,每年检1次，非载客的,从注册起,10年内,每年检1次,超过10年的,每6个月检1次，看来对客车是要求的严重些!', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576844, '砸碎前挡风玻璃', '砸碎侧挡风玻璃', '撬开车门', '先行灭火，再撬开车门', '2', '', '客车失火无法开启车门时，为了减少伤害，应尽快组织乘客如何逃生？', '因为在客车两侧都备有用来敲碎玻璃的逃生锤！', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576845, '优先保护乘客人身安全', '先抢救财产', '根据情况进行抢救', '先抢救贵重物品', '1', '', '客车在行驶中遇险时，要做到哪些？', '以人为本，人是最重要的 。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576848, '发动机主油道堵塞', '发动机机油压力过低', '发动机曲轴箱漏气', '发动机机油压力过高', '2', 'http://images.juheapi.com/jztk/a1b1subject1/546.jpeg', '发动机起动后仪表板上（如图所示）亮表示什么？', '像茶壶一样的就是机油的标志，各位记住就是啦。 该指示灯用来显示发动机内机油的压力状况。打开钥匙门，车辆开始自检时，指示灯点亮，启动后熄灭。该指示灯常亮，说明该车发动机机油压力低于规定标准，需要维修。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576849, '4', '4.2', '5', '5.5', '1', '', '载客汽车行李架载货时，从地面起高度不得超过多少米？', '从安全角度想选数字越小会越准确。 ', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576850, '10a', '10b', '10c', '10d', '4', 'http://images.juheapi.com/jztk/a1b1subject1/548.jpg', '下列哪个指示灯亮表示车辆在使用远光灯?(图10a. 10b. 10c. 10d)', '10a：车灯总开关；10b：前后雾灯；10c：驻车制动器处于制动状态；10d：远光灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576852, '正确', '错误', '', '', '1', '', '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人联系电话、联系地址以及从业单位等信息发生变化的，应当在信息变更后三十日内，向驾驶证核发地车辆管理所备案。', '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人联系电话、从业单位等信息发生变化未及时申报变更信息的，公安机关交通管理部门处二十元以上二百元以下罚款。机动车驾驶人联系电话、联系地址等信息发生变化，应当在信息变更后三十日内，向驾驶证核发地车辆管理所备案。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576853, '1年', '2年', '3年', '4年', '1', '', '申请人在考试过程中有贿赂、舞弊行为的，申请人在多少年内不得再次申领机动车驾驶证？', '根据123号令第六章法律责任的规定：<b>申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576854, 'B车自负责任', 'A车负全部责任', '各负一半责任', '偶然事件，不可避免', '2', 'http://images.juheapi.com/jztk/a1b1subject1/552.jpg', '如图所示，A车货物掉落，导致B车与掉落货物发生碰撞，以下说法正确的是什么？', '造成该事故的原因是货车上货物固定不牢，货物遗洒导致后面的机动车事故，货车应负全部责任。 \r<br/>\r<br/>【拓展延伸】 \r<br/>很多人认为后车跟车距离过近也要承担事故责任，但后方轿车在事故发生前与货车的距离是未知的，因为货车货物掉落，极有可能是在紧急刹车后，货物固定不牢，在惯性作用下向后洒落，砸到了后方的轿车。货车紧急刹车，但后车躲避不及，本来保持一定车距，但只够避免追尾，却没料到会被货物砸到。大家可以这样考虑。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576856, '系好安全带', '在车内等待', '不能拨打手机、吸烟', '不要在车内随意走动', '3', '', '客车在加油站加油时，驾驶人应当提醒乘客怎么做？', '怕引起火灾，安全第一。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576858, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/556.jpg', '这个标志的含义是告示车辆驾驶人应慢行或停车，确保干道车辆优先。', '减速让行，表示车辆应减速让行，告示车辆驾驶人必须慢行或停车，观察干道行车情况，在确保干道车辆优先的前提下，认为安全时方可续行。设于视线良好交叉道路次要道路路口。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576859, '正确', '错误', '', '', '1', '', '初次申领的机动车驾驶证的有效期为6年。', '《机动车驾驶证申领和使用规定》第九条：机动车驾驶证有效期分为六年、十年和长期。<br/>《中华人民共和国道路交通安全法实施条例》：第二十六条 机动车驾驶人在机动车驾驶证的6年有效期内，每个记分周期均未达到12分的，换发10年有效期的机动车驾驶证；在机动车驾驶证的10年有效期内，每个记分周期均未达到12分的，换发长期有效的机动车驾驶证。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576860, '应停车察明水情，确认安全后，低速通过', '应停车察明水情，确认安全后，快速通过', '应减速观察水情，然后加速行驶通过', '可随意通行', '1', 'http://images.juheapi.com/jztk/a1b1subject1/558.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？ ', '图中所示，河水已经漫过路面，驾驶机动车遇到这种情况时，应停车察明水情，确认安全后，低速通过；不可随意通行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576861, '保护好现场再协商', '不要移动车辆', '疏导其他车辆绕行', '撤离现场自行协商', '4', '', '驾驶机动车发生交通事故未造成人身伤亡的，责任明确双方无争议时，应当如何处置？', '《中华人民共和国道路交通安全法》第七十条：明确规定在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576862, '前风窗玻璃除霜或除雾', '后风窗玻璃刮水器及洗涤器', '前风窗玻璃刮水器及洗涤器', '后风窗玻璃除霜或除雾', '3', 'http://images.juheapi.com/jztk/a1b1subject1/560.jpeg', '（如图所示）这个符号的开关控制什么装置？', '此为前风窗玻璃刮水器及洗涤器。关于如何区分前风窗玻璃刮水器及洗涤器和后风窗玻璃刮水器及洗涤器，你只需记住一点半弧形的图标是前风窗，四四方方的图标就是后风窗。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576863, '刮水器开关', '前照灯开关', '转向灯开关', '除雾器开关', '1', 'http://images.juheapi.com/jztk/a1b1subject1/561.jpg', '这是什么操纵装置？', '左手边转向灯开关，右手边刮水器开关。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576865, '2000元', '5000元', '10000元', '20000元', '2', '', '饮酒后驾驶营运机动车的，处十五日拘留，并处多少罚款？', '饮酒后驾驶营运机动车的，处十五日拘留，并处五千元罚款，吊销机动车驾驶证，五年内不得重新取得机动车驾驶证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576866, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/564.jpg', '这个标志的含义是指示此处设有室内停车场。', '你看那个P字上边还有个遮挡，说明是室内的，如果没有上边那个遮挡就是露天停车场。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576867, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/565.jpg', '这个标志的含义是提醒前方道路变为不分离双向行驶路段。', '警告标志注意双向交通。用以提醒车辆驾驶人注意会车。设在由双向分离行驶，因某种原因出现临时性或永久性的不分离双向行驶的路段，或由单向行驶进人双向行驶的路段以前适当位置。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576868, '提示前方有左弯或需向左合流', '提示前方有右弯或需向右合流', '提示前方右侧有障碍需向左合流', '提示前方有左弯或或许向左绕行', '1', 'http://images.juheapi.com/jztk/a1b1subject1/566.jpg', '这个导向箭头是何含义？', '导向箭头：提示前方道路有左弯或需向左合流。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576869, '糖尿病', '红绿色盲', '高血压', '怀孕', '2', '', '以下哪种身体条件，不可以申请机动车驾驶证?', '红绿色盲会影响观察红绿灯，而我们在路上开车一般都是根据交通信号灯行驶的，所以不能申请驾照。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576870, '前风窗玻璃除霜', '后风窗玻璃刮水器', '后风窗玻璃除霜', '前风窗玻璃刮水器', '4', 'http://images.juheapi.com/jztk/a1b1subject1/568.jpeg', '（如图所示）这个符号的开关控制什么装置？', '此为前风窗玻璃刮水器。关于如何区分前风窗玻璃刮水器及洗涤器和后风窗玻璃刮水器及洗涤器，你只需记住一点半弧形的图标是前风窗，四四方方的图标就是后风窗。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576871, '加速通过', '正常行驶', '连续鸣喇叭', '减速慢行', '4', '', '当驾驶车辆行经两侧有行人且有积水的路面时，应怎样做？', '文明驾驶，减速慢行，防止水溅至行人身上。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576872, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/570.jpeg', '机动车仪表板上（如图所示）亮，提示发动机舱开启。', '此为行李舱开启指示灯。当机动车仪表板上该符号亮起，表示行李舱开启。题目描述错误，因此本题答案为错。\r<br/>\r<br/>【拓展延伸】\r<br/><p><img src=http://tp.mnks.cn/tp_423.jpg>\r<br/>行李舱开启</p><p><img src=http://tp.mnks.cn/tp_404.jpg>\r<br/>发动机舱开启</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576873, '等候放行线', '停车让行线', '减速让行线', '左弯待转线', '3', 'http://images.juheapi.com/jztk/a1b1subject1/571.jpg', '路口最前端的双白虚线是什么含义？', '<p><img src=http://tp.mnks.cn/tp_332.jpg><br/>停止线</p><p><img src=http://tp.mnks.cn/tp_333.jpg><br/>停车让行线</p><p><img src=http://tp.mnks.cn/tp_334.jpg><br/>减速让行线</p>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576874, '60，100', '70，100', '40，80', '30，80', '1', '', '驾驶机动车在高速公路上行驶，遇低能见度气象条件时，能见度在200米以下，车速不得超过每小时多少公里，与同车道前车至少保持多少米的距离？', '驾驶机动车在高速公路上行驶，遇低能见度气象条件时，能见度在200米以下，车速不得超过60公里/小时，与同车道前车至少保持100米的车距以保证安全。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576875, '过失行为', '违规行为', '违章行为', '违法行为', '4', '', '驾驶机动车在道路上违反道路交通安全法的行为，属于什么行为？', '题干中明确表述“违反道路交通安全法”，因此该行为属于违法行为。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576876, '关闭所有灯光', '开启近光灯', '关闭前照灯', '开启远光灯', '2', '', '夜间驾驶机动车在窄路、窄桥会车怎样使用灯光？', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯，<b>在窄路、窄桥与非机动车会车时应当使用近光灯。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576877, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/575.jpg', '这个标志的含义是提醒车辆驾驶人前方路面颠簸或有桥头跳车现象。', '警告标志：路面不平，提醒车辆驾驶人减速慢行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576878, '正确', '错误', '', '', '1', '', '驾驶机动车在人行横道上临时停车属于违法行为。', '《道路交通安全法》规定人行横道上不允许停车。违反安全法就是违法行为。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576879, '正确', '错误', '', '', '1', '', '驾驶人要按照驾驶证载明的准驾车型驾驶车辆。', '《公安部令第123号》规定：驾驶与准驾驶车型不符的机动车的违法行为，一次记12分。处罚200以上1000以下的罚款。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576880, '正确', '错误', '', '', '2', '', '在造成人员死亡的交通事故中承担主要责任的，不得申请增加大型客车准驾车型，但可以申请增加中型客车准驾车型。', '第十五条  有下列情形之一的，不得申请大型客车、牵引车、中型客车、大型货车准驾车型：（一）发生交通事故造成人员死亡，承担同等以上责任的.', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576881, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/579.jpg', '将转向灯开关向下拉，右转向灯亮。', '向上提：右转向灯亮，（顺时针方向，向右打方向盘的方向） \r<br/>向下拉：左转向灯亮，（逆时针方向，向左打方向盘的方向）', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576882, '道路交通安全违法行为、交通事故处理情况', '驾驶人身体条件', '记满12分后参加学习和考试情况', '驾驶车辆累计行驶里程', '4', '', '以下不属于机动车驾驶证审验内容的是什么？', '机动车驾驶证审验内容：（一）道路交通安全违法行为、交通事故处理情况；（二）身体条件情况；（三）道路交通安全违法行为记分及记满12分后参加学习和考试情况。驾驶车辆累计行驶里程不在审验范围内。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576884, '非机动车道', '摩托车专用道', '电瓶车专用道', '自行车专用道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/582.jpg', '这个路面标记是何含义？', '这是非机动车（就是不烧油的）不要把四轮的开到他们的道上', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576885, '正确', '错误', '', '', '1', '', '申请人因故不能按照预约时间参加考试的，应当提前一日申请取消预约，对申请人未按照预约考试时间参加考试的，判定该次考试不合格。', '申请人因故不能按照预约时间参加考试的，应当提前一日申请取消预约。对申请人未按照预约考试时间参加考试的，判定该次考试不合格。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576886, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/584.jpg', '这个标志的含义是提醒车辆驾驶人前方有很强的侧向风。', '注意横风，这种标志会出现在一些风口或是宽阔的地段,提醒驾驶员前方会有较强侧风。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576887, '指示前方道路是Y型交叉口', '指示前方道路是分离式道路', '指示前方道路仅可左右转弯', '指示前方道路需向左右合流', '3', 'http://images.juheapi.com/jztk/a1b1subject1/585.jpg', '路面上导向箭头是何含义？', '可以左转也可以右转，三叉路口', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576888, '小型汽车、小型自动挡汽车', '大型客车、牵引车、中型客车', '普通三轮摩托车、普通二轮摩托车、轻便摩托车', '低速载货汽车、三轮汽车', '2', '', '申请增加哪些准驾车型的，不得有在造成人员死亡的交通事故中承担同等以上责任的记录？', '这个题目，只要认真想一下，哪些驾照持有人容易出事，还有申请大型客车驾照的人一般都是驾驶技术过关的，所以这个题目可以从驾照方面考虑，所以选择B! ', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576889, '正确', '错误', '', '', '1', '', '为保障乘车人不受伤害，客车驾驶人或乘务员应当向乘客讲解安全门的使用方法。', '记忆题', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576890, '两侧变窄路段', '车速测试路段', '车距确认路段', '注意保持车距', '4', 'http://images.juheapi.com/jztk/a1b1subject1/588.jpg', '这个标志是何含义？', '车距确认标线作为车辆驾驶人保持行车安全距离的参考，视需要设于较长直线段、易发生追尾事故或其他需要的路段，应与车距确认标志配合使用。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576891, '单侧可跨越同向车道分界线', '禁止跨越同向车行道分界线', '双侧可跨越同向车道分界线', '禁止跨越对向车行道分界线', '2', 'http://images.juheapi.com/jztk/a1b1subject1/589.jpg', '路中心白色实线是何含义？', '图中两车道明显为同方向，而且实线不能压，因此路中心白色实线是禁止跨越同向车行道分界线。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576893, '公安机关交通管理部门约束至酒醒', '吊销驾驶证', '五年内不得重新取得机动车驾驶证', '记6分', '4', '', '关于醉酒驾驶机动车的处罚，以下说法错误的是什么？', '根据规定，醉酒驾驶机动车的，由公安机关交通管理部门约束至酒醒，<b>吊销机动车驾驶证</b>，依法追究刑事责任，五年内不得重新取得机动车驾驶证。\r<br/>注意，题目说要选择“说法错误的是什么”，选项D“记6分”与规定的“吊销机动车驾驶证”不符，是错误的。因此本题答案选D。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576895, '正确', '错误', '', '', '2', '', '驾驶客车在山区、桥梁、高速公路遇紧急情况避险时，应先转动转向盘避让，再使用制动减速。', '', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576898, '公共汽车', '大型客车', '大型货车', '中型客车', '1', '', '机动车驾驶人在实习期内不得驾驶那类车型？', '因为不符合资格；公共汽车，人多，需要更高级别的驾照。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576899, '合法来源凭证', '临时行驶车号牌', '借用的机动车号牌', '法人单位证明', '2', '', '机动车购买后尚未注册登记，需要临时上道路行驶的，可以凭什么临时上道路行驶？', '《道路交通安全法》第八条：国家对机动车实行登记制度。机动车经公安机关交通管理部门登记后，方可上道路行驶。尚未登记的机动车，需要临时上道路行驶的，应当取得临时通行牌证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576900, '正确', '错误', '', '', '2', '', '机动车驾驶人在实习期内可以单独驾驶大型营运客车。', '机动车驾驶人在实习期内不得驾驶公共汽车、营运客车或者执行任务的警车、消防车、救护车、工程救险车以及载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车；驾驶的机动车不得牵引挂车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576901, '正确', '错误', '', '', '1', '', '机动车驾驶证遗失、损毁无法辨认时，机动车驾驶人应当向机动车驾驶证核发地车辆管理所申请补发。', '具有下列情形之一的，机动车驾驶人应当在三十日内到机动车驾驶证核发地车辆管理所申请换证：<br/>(一)在车辆管理所管辖区域内，机动车驾驶证记载的机动车驾驶人信息发生变化的;<br/>(二)<b>机动车驾驶证损毁无法辨认的。</b><br/>根据123号令第五十四条:<b>机动车驾驶证遗失的，机动车驾驶人应当向机动车驾驶证核发地车辆管理所申请补发。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576902, '发动机温度过高', '发动机冷却系故障', '发动机润滑系故障', '发动机温度过低', '1', 'http://images.juheapi.com/jztk/a1b1subject1/600.jpeg', '行车中仪表板上（如图所示）亮表示什么？', '此标志亮起说明发动机水温过高，可能是冷却液不足引起的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576903, '正确', '错误', '', '', '1', '', '城市公共汽车不得在站点以外的路段停车上下乘客。', '这个大家都知道的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576904, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/602.jpg', '这个标志的含义是指示此处设有室内停车场', '你看那个P字上边没有遮挡，说明是露天停车场，如果P字上边有遮挡就是室内停车场了。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576905, '正确', '错误', '', '', '2', '', '交通标志和交通标线不属于交通信号。', '《公安部令第123号》第二十五条规定：交通信号包括交通信号灯、交通标志、交通标线和交通警察的指挥。\r<br/>题目描述有误，因此本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576906, '正确', '错误', '', '', '2', '', '当行人出现交通安全违法行为时，车辆可以不给行人让行。', '安全第一，文明驾驶，礼貌让行。无论何时都应该让行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576907, '两侧变窄', '右侧变窄', '左侧变窄', '桥面变窄', '3', 'http://images.juheapi.com/jztk/a1b1subject1/605.jpg', '这是什么交通标志？', '左侧变窄：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576908, '正确', '错误', '', '', '1', '', '年龄在60岁以上持有准驾车型为大型客车的驾驶人，应当到驾驶证核发地车辆管理所换领准驾车型为小型汽车或小型自动挡汽车的驾驶证。', '为了安全起见，超过年龄，驾照要降级。 ', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576909, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/607.jpg', '这个标志的含义是提醒前方桥面宽度变窄。', '此为<b>两侧变窄标志</b>。常设在两侧变窄道路，提示司机看到此标志时应请提前减速，前方道路即将变窄，车辆密集度增加，行使空间降低，易发生车祸，所以请一定要提前减速。题目描述错误，因此本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576910, '继续加速行驶', '稍向右侧行驶，保证横向安全距离', '靠道路中心行驶', '加速向右侧让路', '2', '', '驾驶的车辆正在被其他车辆超越时，若此时后方有跟随行驶的车辆，应怎样做？', '稍向右侧减速慢行，和前车、超车的车辆保持一定的安全距离。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576911, '正确', '错误', '', '', '1', '', '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人，在记分周期内有记分的，应当在记分周期结束后三十日内到公安机关交通管理部门接受审验。', '《公安部令第123号》第六十八条：持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人有下列情形之一的，车辆管理所应当注销其最高准驾车型驾驶资格，并通知机动车驾驶人在三十日内办理降级换证业务，在一个记分周期内有记满12分记录的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576912, '正确', '错误', '', '', '2', '', '已持有小型汽车驾驶证，申请增加中型客车准驾车型的，应当在申请前最近一个记分周期内没有满分记录。', '已持有小型汽车驾驶证,申请增加大型货车准驾车型的,应当在申请前最近一个记分周期内没有满分记录.', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576913, '大型货车.', '大型客车', '中型客车', '牵引车', '1', '', '年满20周岁，可以初次申请下列哪种准驾车型？', '1、申请小型汽车、小型自动挡汽车、残疾人专用小型自动挡载客汽车、轻便摩托车准驾车型的，在18周岁以上、70周岁以下;<br/>2、申请低速载货汽车、三轮汽车、普通三轮摩托车、普通二轮摩托车或者轮式自行机械车准驾车型的，在18周岁以上，60周岁以下;<br/>3、<b>申请城市公交车、大型货车、无轨电车或者有轨电车准驾车型的，在20周岁以上，50周岁以下;</b><br/>4、申请中型客车准驾车型的，在21周岁以上，50周岁以下;<br/>5、申请牵引车准驾车型的，在24周岁以上，50周岁以下;<br/>6、申请大型客车准驾车型的，在26周岁以上，50周岁以下。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576914, '营运客车专用车道', '大客车专用车道', '出租车专用车道', '公交专用车道', '4', 'http://images.juheapi.com/jztk/a1b1subject1/612.jpg', '图中圈内两条黄色虚线间的区域是何含义？', '黄色虚线中的车道里有写字，黄色虚线是想突出引动驾驶员注意的，因此本题是公交专用车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576915, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/613.jpg', '驾驶机动车在这种情况下不能左转弯。', '有图片可知，左转箭头此时是红灯，红灯禁止通行，因此在这种情况下不能左转弯。题目描述无误，因此本题答案为对。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576917, '正确', '错误', '', '', '2', '', '驾驶人违反交通运输管理法规发生重大事故后，逃逸或者有其他特别恶劣情节的，处7年以上有期徒刑。', '《刑法》第一百三十三条规定：\r<br/>1、违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；\r<br/>2、<b>交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；</b>\r<br/>3、因逃逸致人死亡的，处七年以上有期徒刑。\r<br/>根据规定， 题目中的情况应该判处 <b>[ 三年以上七年以下有期徒刑 ]</b> ，并非七年以上有期徒刑。题目描述错误，因此答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576918, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/616.jpg', '路中心的双黄实线作用是分隔对向交通流，在保证安全的前提下，可越线超车或转弯。', '黄色虚线是中心线，可以跨越；黄色单实线就已经不能跨越了；黄色双实线是严格禁止压线或者跨越！', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576919, '正确', '错误', '', '', '1', '', '客车在起步前，驾驶人应向乘客强调不要将手、头部以及其他部位伸出窗外。', '因为在行经这些路面时，很容易发生颠簸，让人失去平衡。大家都有这样的感觉，坐在后排起伏的幅度较大，很多时候通过颠簸路面时，会有强烈的波动。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576920, '正确', '错误', '', '', '1', '', '严禁在车辆未停稳或行驶途中开启车门，以防乘客被甩出酿成事故。', '', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576921, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/619.jpg', '遇到这种情况可以优先通行。', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，在有障碍的路段，无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576922, '正确', '错误', '', '', '1', '', '夜间起步前，应当先开启近光灯。', '夜间起步，先开启近光灯观察路况，确认周围情况后，在开启左转向灯，起步。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576923, '高速公路终点地名预告', '高速公路行驶路线预告', '高速公路行驶方向预告', '高速公路地点距离预告', '4', 'http://images.juheapi.com/jztk/a1b1subject1/621.jpg', '这个标志是何含义？', '高速公路地点距离预告：预告高速公路或城市快速路前方所要经过的重要的地点、道路的名称和距离，设置在互通式立体交叉加速车道的渐变段终点以后1 km以上路段的合适位置处。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576924, '路口警示', '禁止通行', '提醒注意', '准许通行', '4', 'http://images.juheapi.com/jztk/a1b1subject1/622.jpg', '前方路口这种信号灯亮表示什么意思？', '绿灯行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576925, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/623.gif', '图中标志的含义是禁止小型客车通行。', '四个窗的是大型客车，三个窗的是小型客车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576926, '正确', '错误', '', '', '1', '', '载客汽车除车身外部的行李架和内置的行李箱外，不得载货。', '应该客车是不可以载货的，但再仔细审题目的话，说的是客车的构造包含了行李架和行李箱，既然是行李架和行李箱是放行李的，又怎么能载货呢。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576927, '正确', '错误', '', '', '2', '', '客车在起步前，应检查行李包是否装捆牢固，长、宽、高及重量是否符合规定， 可将行李放置在门道或者过道上。', '为了安全考虑，不可将行李放置在门道或者过道上。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576928, '正确', '错误', '', '', '1', '', '客车在遇险的一刹那，应果断地采取一切有效措施保护乘客不受伤害或少受伤害，要避免车辆发生倾覆。', '', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576929, '正确', '错误', '', '', '1', '', '客车起步、行驶和停车时，应当平稳，避免乘客受伤。', '无论是什么车都应该平稳的，无可争议的正确。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576930, '正确', '错误', '', '', '1', '', '客车驾驶人在行车中，应始终保持良好的心理状态，不得带着不良情绪驾驶车辆。', '', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576931, '正确', '错误', '', '', '1', '', '营运机动车在规定检验期限内经安全技术检验合格的，不再重复进行安全技术检验。', '已经检测合格的不需要重复检测。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576932, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/630.jpg', '这个标志的含义是告示前方道路有障碍物，车辆左侧绕行。', '警告标志：左侧绕行，注意安全。用以告示前方道路有障碍物，车辆应按标志指示减速慢行。设在道路障碍物前适当位置。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576933, '提醒车辆驾驶人前方道路沿水库、湖泊、河流', '提醒车辆驾驶人前方有向上的陡坡路段', '提醒车辆驾驶人前方有向下的陡坡路段', '提醒车辆驾驶人前方有两个及以上的连续上坡路段', '3', 'http://images.juheapi.com/jztk/a1b1subject1/631.jpg', '这个标志是何含义？', '下陡坡：用以提醒车辆驾驶人小心驾驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576934, '正确', '错误', '', '', '2', '', '驾驶人在驾驶证丢失后3个月内还可以驾驶机动车。', '机动车驾驶人在机动车驾驶证丢失后，不得驾驶机动车。如果驾驶人在驾驶证丢失期间开车上路，其行为虽不按“未取得机动车驾驶证”处理，但仍要受到法律的制裁，即依照“未随车携带驾驶证”的情况进行处罚。\r<br/>题目描述有误，因此本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576936, '6个月', '1年', '2年', '3年', '1', '', '营运载客汽车从注册登记之日起，超过5年的，每多长时间检验1次？', '载客的,从注册起,5年内,每年检1次,超过5年的,每6个月检1次，看来对客车是要求的严重些!', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576937, '正确', '错误', '', '', '1', '', '铰接式客车不得进入高速公路。', '就是大通道，两节车厢，中间有链接。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576938, '乘客', '超载机动车的驾驶人或者所有人', '公安机关交通管理部门', '转运机动车的驾驶人或者所有人', '2', '', '公路客运车辆载客超过核定乘员，公安机关交通管理部门依法扣留机动车后，驾驶人应当将超载的乘车人转运，费用由谁承担？', '怎么可能是JC叔叔掏钱，你懂的！', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576939, '正确', '错误', '', '', '2', '', '营运机动车改为非营运机动车的，机动车所有人不需要向公安机关交通管理部门申请登记。', '肯定是需要告诉警察叔叔的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576941, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/639.jpg', '这个标志的含义是提醒车辆驾驶人前方是急转弯路段。', '<p><img src=http://tp.mnks.cn/tp_123.jpg>\r<br/>向右急弯路\r<br/><img src=http://tp.mnks.cn/tp_124.jpg>\r<br/>向左急弯路\r<br/><img src=http://tp.mnks.cn/tp_142.jpg>\r<br/>易滑路段</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576942, '1年', '2年', '3年', '年限没有规定', '2', '', '取得驾驶牵引车准驾车型资格多少年以上的，并在申请前最近5个记分周期内没有满分记录的，可以申请增加大型客车？', '载客汽车是5年，牵引车是2年 ', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576943, '正确', '错误', '', '', '1', '', '安装乘客安全带的车辆，应要求乘客在起步前按规定使用安全带。', '', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576944, '正确', '错误', '', '', '1', '', '造成交通事故后逃逸且构成犯罪的驾驶人,将吊销驾驶证且终生不得重新取得驾驶证。', '吊二撤三醉五逃终生，这样就好理解了吧！', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576945, '正确', '错误', '', '', '1', '', '驾驶人的机动车驾驶证被依法扣留、暂扣的情况下不得驾驶机动车。', '《道路交通安全法实施条例》第二十八条：机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576946, '不减速通过', '加速尽快通过', '空挡滑行通过', '减速或停车观察', '4', 'http://images.juheapi.com/jztk/a1b1subject1/644.jpg', '怎样通过这样的路口？', '右上角的图标为无人看守的铁道路口，驾驶员要减速或者停车确认安全后再通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576947, '住地交警支队车辆管理所', '驾驶证核发地车辆管理所', '登记地车辆管理所', '当地公安局', '3', '', '机动车登记证书、号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向哪个部门申请补领、换领。', '根据《机动车登记规定》第四十四条的规定：机动车号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向登记地车辆管理所申请补领、换领。申请时，机动车所有人应当填写申请表并提交身份证明。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576948, '单向行驶车道分界线', '禁止跨越对向车行道分界线', '双侧可跨越同向车道分界线', '可跨越对向车道分界线', '2', 'http://images.juheapi.com/jztk/a1b1subject1/646.jpg', '路中心的黄色斜线填充是何含义？', '图中两侧车道很明显是不同方向的，并且双黄线是禁止跨越的，因此路中心的黄色斜线填充为禁止跨越对向车行道分界线。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576949, '100公里/小时', '80公里/小时', '60公里/小时', '50公里/小时', '3', 'http://images.juheapi.com/jztk/a1b1subject1/647.jpg', '在这段高速公路上行驶的最低车速是多少？', '红圈白底限制最高速度，蓝底限制最低速度，此路段最低车速是60公里每小时。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576950, '正确', '错误', '', '', '2', '', '大型载客汽车允许牵引总质量700千克以下的挂车。', '大型载客汽车不允许挂车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576951, '车窗没关闭', '车门没关闭', '音响没关闭', '顶窗没关闭', '2', '', '在什么情况下不得行车？', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在车门、车厢没有关好时行车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576952, '注意避让标志', '注意新手标志', '统一式样的实习标志', '注意车距标志', '3', '', '在实习期内驾驶机动车的，应当在车身后部粘贴或者悬挂哪种标志？', '《公安部令第123号》第六十四条规定：在实习期内驾驶机动车的，应当在车身后部粘贴或者悬挂<b>统一式样的实习标志</b>。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576953, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/651.jpeg', '机动车仪表板上（如图所示）亮时提醒发动机需要加注机油。', '图示灯亮了是提醒发动机温度过高，不是需要加注机油。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576954, '正确', '错误', '', '', '1', '', '小型汽车科目二考试内容包括倒车入库、坡道定点停车和起步、侧方停车、曲线行驶、直角转弯。', '《公安部令第123号》第二十五条:科目二考试内容包括小型汽车、小型自动挡汽车、残疾人专用小型自动挡载客汽车和低速载货汽车考试倒车入库、坡道定点停车和起步、侧方停车、曲线行驶、直角转弯。省级公安机关交通管理部门可以根据实际增加考试内容。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576955, '低速载货汽车', '小型汽车', '二轮摩托车', '轻型自动挡载货汽车', '4', '', '准驾车型为小型自动挡汽车的，可以驾驶以下哪种车型？', '小型自动挡汽车（C2）准驾车型：小型、微型自动挡载客汽车以及轻型、微型自动挡载货汽车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576956, '正确', '错误', '', '', '1', '', '车辆前轮胎爆裂，危险较大，方向会立刻向爆胎车轮一侧跑偏，直接影响驾驶人对转向盘的控制。', '爆胎后，车辆整体平衡会受到影响，会向爆胎的一边跑偏，直接影响到驾驶人对转向盘的控制。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576957, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/655.jpg', '驾驶这种机动车上路行驶没有违法行为。', '《公安部令第123号》规定，上道路行驶的机动车未悬挂机动车号牌的，或者<b>故意遮挡、污损</b>、不按规定安装机动车号牌的，一次记12分 。图中车辆很明显遮挡住了车号牌，属于违法行为。本题表述有误，因此本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576958, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/656.jpg', '路中心黄色虚线的含义是分隔对向交通流，在保证安全的前提下，可越线超车或转弯。', '黄色虚线是中心线，可以跨越；黄色单实线就已经不能跨越了；黄色双实线是严格禁止压线或者跨越', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576959, '正确', '错误', '', '', '2', '', '初次申领的机动车驾驶证的有效期为4年。', '根据规定，初次取得驾驶证的有效期是六年，在六年有效期里的每个记分周期未达到12分的换发十年有效期驾驶证，在十年有效期里的每个记分周期未达到12分的换发长期驾驶证。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576960, '1年', '2年', '3年', '4年', '3', '', '驾驶技能准考证明的有效期是多久？', '驾驶技能准考证明的有效期为三年，申请人应当在有效期内完成科目二和科目三考试。未在有效期内完成考试的，已考试合格的科目成绩作废。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576961, '不必变换灯光', '150米以外', '100米以内', '50米以内', '2', '', '夜间在道路上会车时，距离对向来车多远将远光灯改用近光灯？', '《中华人民共和国道路交通安全法》第四十八条：<br/>在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：<br/>(一)减速靠右行驶，并与其他车辆、行人保持必要的安全距离；<br/>(二)在有障碍的路段，无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行；<br/>(三)在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行；<br/>(四)在狭窄的山路，不靠山体的一方先行；<br/>(五)<b>夜间会车应当在距相对方向来车150米以外改用近光灯，</b>在窄路、窄桥与非机动车会车时应当使用近光灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576962, '3年内', '终身', '1年内', '5年内', '1', '', '以欺骗、贿赂等不正当手段取得驾驶证被依法撤销驾驶许可的，多长时间不得重新申请驾驶许可？', '吊二撤三醉五逃终身。\r<br/>根据公安部令第123号规定：申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。\r<br/><b>申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可；申请人在三年内不得再次申领机动车驾驶证。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576963, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/661.jpg', '这个标志的含义是警告前方出现向左的急转弯路。', '向左急弯路：用以警告车辆驾驶人减速慢行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576964, '旅游区标志', '作业区标志', '告示标志', '高速公路标志', '1', 'http://images.juheapi.com/jztk/a1b1subject1/662.jpg', '这属于哪一种标志？', '旅游区方向：设在通往旅游区各连接道路的交叉口处。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576965, '6个月', '1年', '2年', '3年', '4', '', '申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的（被撤销的），申请人在多长时间内不得再次申领机动车驾驶证。', '申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可；申请人在三年内不得再次申领机动车驾驶证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576967, '每3年', '每2年', '每6个月', '每1年', '4', '', '年龄在60周岁以上的驾驶人多长时间提交一次身体条件证明？', '持有准驾车型为C1的驾驶证，年龄在60岁以下的驾驶员，不需要每年提交身体条件证明，只需要换证时一并提交即可，如果超过60岁就需要每年提交', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576968, '保持速度通过', '鸣笛催促', '减速慢行', '加速通过', '3', 'http://images.juheapi.com/jztk/a1b1subject1/666.jpg', '如何通过这种交叉路口？', '路旁有多名行人马上要横穿马路，一定要减速慢行，保证行人安全。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576969, '正确', '错误', '', '', '2', '', '机动车登记证书、号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向居住地车辆管理所申请补领、换领。', '根据《机动车登记规定》第四十四条的规定：机动车号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向<b>登记地车</b>辆管理所申请补领、换领。申请时，机动车所有人应当填写申请表并提交身份证明。\r<br/>本题题干说的是“居住地车辆管理所”，是不正确的，本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576970, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/668.jpg', '驾驶机动车在这种情况下不能直行和左转弯', '直行和左转都是红灯，所以不能直行和左转。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576971, '1年', '2年', '3年', '4年', '1', '', '隐瞒有关情况或者提供虚假材料申请机动车驾驶证，申请人在多少年内不得再次申领机动车驾驶证？', '《机动车驾驶证申领和使用规定》第六章第七十八条规定：<b>隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在一年内不得再次申领机动车驾驶证。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576972, '直行或向右转弯', '向右转弯', '向左转弯', '直行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/670.jpg', '这个路口允许车辆怎样行驶？', '左转的灯是绿色，所以可以向左转弯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576973, '正确', '错误', '', '', '2', '', '机动车驾驶证有效期分为6年、10年、20年。', '《中华人民共和国道路交通安全法实施条例》第二十六条明确规定： 机动车驾驶人在机动车驾驶证的6年有效期内，每个记分周期均未达到12分的，换发10年有效期的机动车驾驶证；在机动车驾驶证的10年有效期内，每个记分周期均未达到12分的，换发长期有效的机动车驾驶证。\r<br/>有效期为6年，10年，长期。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576974, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/672.jpeg', '机动车仪表板上（如图所示）亮时，不影响正常行驶。', '图中报警灯是驻车制动器及系统故障指示灯。行车途中该灯亮起，表示制动系统出了问题或故障，机动车不能上路。题目描述错误，因此本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576975, '正确', '错误', '', '', '2', '', '准驾车型为小型自动挡汽车的，可以驾驶低速载货汽车。', '小型自动挡汽车（C2）准驾车型：小型、微型自动挡载客汽车以及轻型、微型自动挡载货汽车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576976, '低速载货汽车', '中型客车', '三轮摩托车', '轮式自行机械', '1', '', '准驾车型为小型汽车的，可以驾驶下列哪种车辆？', '小型汽车（C1）准驾车型：小型、微型载客汽车以及轻型、微型载货汽车；轻型、微型专项作业车，小型、微型自动挡载客汽车以及轻型、微型自动挡载货汽车，低速载货汽车，三轮汽车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576977, '正确', '错误', '', '', '1', '', '驾驶机动车遇到沙尘、冰雹、雨、雾、结冰等气象条件时应降低行驶速度。', '安全第一，毫无疑问。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576978, '10分钟', '15分钟', '20分钟', '5分钟', '3', '', '驾驶人连续驾驶4小时以上，停车休息的时间不得少于多少？', '《道路交通安全法实施条例》第六十二条：连续驾驶机动车超过4小时应停车休息，停车休息时间不少于20分钟。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576979, '进入应急车道行驶', '尽快驶离高速公路', '尽快在路边停车', '在路肩低速行驶', '2', '', '驾驶机动车在高速公路遇到能见度低于50米的气象条件时，车速不得超过20公里/小时，还应怎么做？', '1、能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；\r<br/>2、能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离； 　　\r<br/><b>3、能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576980, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/678.jpeg', '开启前照灯近光时仪表板上（如图所示）亮起。', '<p><img src=http://tp.mnks.cn/tp_387.jpg>\r<br/>前照远光灯</p>\r<br/><p><img src=http://tp.mnks.cn/tp_388.jpg>\r<br/>前照近光灯</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576981, '正确', '错误', '', '', '1', '', '一个合格的驾驶人，不仅表现在技术的娴熟上，更重要的是应该具有良好的驾驶行为习惯和道德修养。', '娴熟的技术固然重要，但没有良好的驾驶习惯和道德修养，同样会破坏正常的交通秩序。因此合格驾驶员应该具备良好的驾驶行为习惯和道德修养。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576982, '燃油泵出现异常或者故障', '发动机点火系统出现故障', '发动机供油系统出现异常', '油箱内燃油已到最低液面', '4', 'http://images.juheapi.com/jztk/a1b1subject1/680.jpeg', '发动机起动后仪表板上（如图所示）亮表示什么？', '这个是汽油不足了，多像加油站加油的那个机器，记住机油和汽油是不一样的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576983, '中型客车', '大型客车', '普通三轮摩托车', '牵引车', '3', '', '初次申领机动车驾驶证的，可以申请下列哪种准驾车型？', '初次申领机动车驾驶证的，可以申请准驾车型为城市公交车、大型货车、小型汽车、小型自动挡汽车、低速载货汽车、三轮汽车、残疾人专用小型自动挡载客汽车、普通三轮摩托车、普通二轮摩托车、轻便摩托车、轮式自行机械车、无轨电车、有轨电车的机动车驾驶证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576984, '虚线一侧禁止越线', '实线一侧禁止越线', '实线一侧允许越线', '两侧均可越线行驶', '2', 'http://images.juheapi.com/jztk/a1b1subject1/682.jpg', '路中心黄色虚实线是何含义？', '虚线可压，实线不可压。实线一侧禁止越线。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576985, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/683.jpg', '这辆停在路边的机动车没有违法行为。', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。警告标志与车的距离不足50米，而且没有开启危险报警闪光灯，存在违法行为。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576986, '执行任务的警车', '大型客货车', '出租汽车', '城市公交车', '1', '', '同车道行驶的车辆前方遇到下列哪种车辆不得超车？', '如果是真的执行任务的警车（此处不好判断），一定要让行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576987, '迎面出风', '空气外循环', '风窗玻璃除霜', '空气内循环', '4', 'http://images.juheapi.com/jztk/a1b1subject1/685.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '此为空气内循环，当箭头是从车外进来的就是空气外循环。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576988, '鸣喇叭警告，不得进入', '加速行驶，紧跟前车，不让其进入', '挤靠“加塞”车辆，逼其离开', '主动礼让，确保行车安全', '4', '', '车辆在拥挤路段低速行驶时，遇其他车辆强行插队，应怎样做？', '遇到其他车辆强行插队，如果我们不礼让的话就会有发生事故的风险。做文明司机，主动礼让，确保安全。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576989, '远光灯开关', '近光灯开关', '车灯总开关', '后雾灯开关', '3', 'http://images.juheapi.com/jztk/a1b1subject1/687.jpeg', '机动车仪表板上（如图所示）这个符号表示什么？', '此为车灯总开关。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576990, '正确', '错误', '', '', '2', '', '对违法驾驶发生重大交通事故且构成犯罪的，不追究其刑事责任。', '《道路交通安全法》第一百零一条规定：违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576991, '正确', '错误', '', '', '2', '', '机动车在环形路口内行驶，遇有其他车辆强行驶入时，只要有优先权就可以不避让', '当然错拉，有优先权就了不起呀?有优先权也要考虑到安全啊，不然容易发生两车相撞。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576992, '平均速度为100公里/小时', '最低限速为100公里/小时', '解除100公里/小时限速', '最高限速为100公里/小时', '4', 'http://images.juheapi.com/jztk/a1b1subject1/690.jpg', '这个路面标记是何含义？', '黄色是警示你别超了，一越你就黄了', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576993, '有人看守铁路道口', '多股铁路与道路相交', '立交式的铁路道口', '无人看守铁路道口', '1', 'http://images.juheapi.com/jztk/a1b1subject1/691.jpg', '这个标志是何含义？', '有火车形状的，为无人看守的铁路口，此图为有人看守的铁路口。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576994, '辅助标线', '警告标志', '禁止标线', '指示标线', '3', 'http://images.juheapi.com/jztk/a1b1subject1/692.jpg', '路中心的双黄实线属于哪一类标线？', '1.指示标线：道路上用黄色虚线、白色虚线划分的标线。2.禁止标线：道路上用黄色双实、单实线、白色的双实和单实划分的标线。3.警告标线：道路上用黄线划成的各种图形或线段以警告车通行的标线。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576995, '最低限速为80公里/小时', '平均速度为80公里/小时', '最高限速为80公里/小时', '解除80公里/小时限速', '1', 'http://images.juheapi.com/jztk/a1b1subject1/693.jpg', '这个路面标记是何含义？', '白色为最低限速，黄色为最高限速。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576996, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/694.jpeg', '机动车仪表板上（如图所示）亮时，防抱死制动系统处于打开状态。', '此标志为防抱死制动系统出现故障，应该停车进行全面检查。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655576997, '不能变换挡位', '可以变换挡位', '可换为高挡', '停车观察', '1', '', '驾驶车辆驶入铁路道口前减速降挡，进入道口后应怎样做？', '进路口前停车观望，进了就不能换挡，如果换挡不当，就会熄火，非常危险。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576998, '按顺序停车等候', '鸣喇叭示意前方车辆快速行驶', '选择空当逐车超越', '继续穿插绕行', '1', '', '发现前方道路堵塞，正确的做法是什么？', '如果遇到前方拥堵，应该按顺序停车等候。随便加塞或者鸣喇叭都不是文明行车行为。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655576999, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/697.jpg', '仪表显示当前发动机转速是6000转/分钟。', '题目中仪表中有KM，这是速度和里程表，表示当前汽车的行驶速度是60公里/小时。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655577000, '正在停车', '减速让行', '正在掉头', '正常行驶', '3', '', '同车道行驶的车辆遇前车有下列哪种情形时不得超车？', '同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：  ①《《前车正在左转弯、掉头、超车的》》;  ②与对面来车有会车可能的;  ③前车为执行紧急任务的警车、消防车、救护车、工程救险车的;  ④行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577001, '正确', '错误', '', '', '1', '', '驾驶机动车在道路上违反道路通行规定应当接受相应的处罚。', '违反道路通行规定应该接受相应处罚，以提高司机的安全行车意识，避免类似情况再次发生。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655577002, '左转向指示灯闪烁', '车前后示宽灯亮起', '车前后位置灯亮起', '右转向指示灯闪烁', '1', 'http://images.juheapi.com/jztk/a1b1subject1/700.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '这个没有问题吧。。。老衲也不知如何解释这个题。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577003, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/701.jpeg', '开启危险报警闪光灯时，（如图所示）闪烁。', '此标记为危险报警闪烁灯的标记，开启危险报警闪烁灯，此标志闪烁。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655577004, '后雾灯打开', '前照灯近光打开', '前照灯远光打开', '前雾灯打开', '4', 'http://images.juheapi.com/jztk/a1b1subject1/702.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '前雾灯朝左，后雾灯朝右。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577005, '空气内循环', '空气外循环', '迎面吹风', '风窗玻璃除霜', '2', 'http://images.juheapi.com/jztk/a1b1subject1/703.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '此为空气外循环，当箭头在车内转的时候是空气内循环。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577006, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/704.jpeg', '机动车仪表板上（如图所示）亮时，提醒发动机需要补充机油。', '这个是燃油液面高度指示灯。图中所示，说明是<b>燃油</b>已到最低液面。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655577007, '一个记分周期末', '有效期满换发驾驶证时', '记分周期未满分', '记分周期满12分', '2', '', '持小型汽车驾驶证的驾驶人在下列哪种情况下需要接受审验？', '小型汽车、摩托车等车型驾驶人审验的情形有两种：　　\r<br/>一是发生交通事故造成人员死亡承担同等以上责任未被吊销驾驶证的，应当在记分周期结束后三十日内到公安交管部门接受审验；\r<br/><b>二是驾驶证转到异地或者有效期满换证时，应当到公安交管部门接受审验。</b>　　\r<br/>需要提醒的是，驾驶人没有按照规定参加审验仍驾驶机动车的，公安交管部门将处200元以上500元以下罚款。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577008, '保持车距标记', '最小间距标记', '速度限制标记', '道路编号标记', '3', 'http://images.juheapi.com/jztk/a1b1subject1/706.jpg', '这个路面数字标记是何含义？', '<p><img src=http://tp.mnks.cn/tp_321.jpg>\r<br/>限制速度\r<br/><img src=http://tp.mnks.cn/tp_322.jpg>\r<br/>限制最高速度\r<br/><img src=http://tp.mnks.cn/tp_323.jpg>\r<br/>限制最低速度</p>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577009, '省道编号', '县道编号', '乡道编号', '国道编号', '3', 'http://images.juheapi.com/jztk/a1b1subject1/707.jpg', '这个标志是何含义？', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底的，国道是红底的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577011, '在考试过程中出现身体不适', '在考试过程中有舞弊行为', '不能按照教学大纲认真练习驾驶技能', '未参加理论培训', '2', '', '存在以下哪种行为的申请人在一年内不得再次申领机动车驾驶证？', '《机动车驾驶证申领和使用规定》第六章第七十八条：隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在一年内不得再次申领机动车驾驶证。<b>申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577012, '50公里/小时', '40公里/小时', '30公里/小时', '20公里/小时', '3', '', '驾驶机动车在冰雪道路行驶时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br/>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br/>（二）掉头、转弯、下陡坡时；　　 <br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br/>（四）在<b>冰雪</b>、泥泞的道路上行驶时；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577013, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/711.jpg', '驾驶机动车在这种情况下可以右转弯。', '根据《中华人民共和国道路交通安全法》的相关规定；红灯亮时（满屏灯）右转弯车辆在不影响其他车辆、行人的通行条件下第一台车可以通行，第一台车如等候信号停止时第二台车不可以从前车的左或右侧绕行，<b>但是箭头灯红灯亮时右转弯车辆是绝对不可以通行的，必须要等绿红亮时通行。</b> \r<br/>图片中显示的是箭头灯，并且右转箭头灯是红灯，根据规定，机动车在图中这种情况下是不能右转弯的。题目描述有误，因此本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655577014, '禁止临时停车', '禁止上下人员', '禁止装卸货物', '禁止长时停车', '4', 'http://images.juheapi.com/jztk/a1b1subject1/712.jpg', '路缘石上的黄色虚线是何含义？', '虚线是禁止长时间停车，实线是禁止临时或长时间停车。<br/>图中所示为虚线，所以禁止长时停车，可以临时停车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577015, '转向灯开关', '前照灯开关', '刮水器开关', '除雾器开关', '4', 'http://images.juheapi.com/jztk/a1b1subject1/713.jpg', '这是什么操纵装置？', '你看那些曲线，代表的就是雾气。题目扩展：上边的按钮是前挡风除雾，下边的按钮是后挡风除雾。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577016, '倒车灯开关', '刮水器开关', '危险报警闪光灯开关', '灯光、信号组合开关', '4', 'http://images.juheapi.com/jztk/a1b1subject1/714.jpg', '这是什么操纵装置？', '左手边控制灯光、信号组合开关，右手边控制刮水器开关。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577019, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/717.jpg', '灯光开关旋转到这个位置时，全车灯光点亮。', '当前旋钮箭头对应的只是示宽灯，不是全车灯光点亮。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655577020, '正确', '错误', '', '', '2', '', '驾驶人一边驾车，一边吸烟对安全行车无影响。', '驾驶车辆吸烟不仅对车上乘客有害，也会让驾驶员分心，对安全行车是有影响的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655577021, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/719.jpg', '将转向灯开关向上提，左转向灯亮。', '向上提：右转向灯亮；\r<br/>向下拉：左转向灯亮。\r<br/>题目描述及图片显示均错误，因此本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655577025, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/723.jpg', '这个标志的含义是警告前方道路有障碍物，车辆减速绕行。', '这个警告标志是向右急转弯。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655577029, '正确', '错误', '', '', '2', '', '驾驶人在观察后方无来车的情况下，未开转向灯就变更车道也是合理的。', '这是错误的行车方法，<b>不论在何种情况下变更车道或者转弯，都必须开启转向灯，安全第一。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461655577030, '观察左侧无车后，可以左转', '从该处直接左转', '不得左转，应当直行', '倒车退到虚线处换到左转车道', '3', 'http://images.juheapi.com/jztk/a1b1subject1/728.jpg', '如图所示，驾驶机动车行驶至此位置时，以下做法正确的是什么？', '驾驶机动车行驶至此位置时，已经在直行车道上，只能直行。而且已经驶过虚线位置到达实线位置，就更不能压线左转。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577031, '6小时', '8小时', '10小时', '4小时', '4', '', '驾驶人连续驾驶不得超过多长时间？', '《道路交通安全法实施条例》第六十二条：连续驾驶机动车超过4小时应停车休息，停车休息时间不少于20分钟。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577032, '所学驾校', '驾驶证核发地车辆管理所', '派出所', '全国任何地方公安机关交通管理部门', '2', '', '补领机动车驾驶证应到以下哪个地方办理？', '如果出现驾驶证丢失需补办等办理情况，需要到驾驶证核发地车辆管理所补领机动车驾驶证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577033, '机动车行驶证', '从业资格证', '居民身份证', '机动车通行证', '1', '', '驾驶人未携带哪种证件驾驶机动车上路，交通警察可依法扣留车辆？', '《道路交通安全违法行为处理程序规定》第十三条 “有下列情形之一的，因无其他机动车驾驶人代替驾驶、违法行为尚未消除、需要调查或者证据保全等原因不能立即放行的，可以扣留车辆：    （一）未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未携带行驶证、机动车驾驶证的；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577036, '40，50', '40，40', '50，40', '50，30', '1', '', '驾驶机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件下，能见度在100米以下时，车速不得超过每小时多少公里，与同车道前车至少保持多少米的距离？', '能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；<b>能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；</b>能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577040, '机动车在道路左侧通行，非机动车和行人随意通行', '机动车在道路左侧通行，非机动车和行人在道路两侧通行', '机动车在道路中间通行，非机动车和行人在道路两侧通行', '机动车、非机动车和行人可随意通行', '3', '', '道路没有划分机动车道、非机动车道和人行道的，以下说法正确的是什么？', '按规定：驾驶机动车经过无划分车道的道路时，机动车在道路中间通行，非机动车和行人在道路两侧通行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461655577041, '未造成人员伤亡的', '未发生财产损失事故', '未损害公共设施及建筑物的', '驾驶人有酒后驾驶嫌疑的', '4', '', '发生交通事故时，下列哪种情况下当事人应当保护现场并立即报警？', '酒后驾驶属违法行为，应及时报警。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074051, '正确', '错误', '', '', '1', '', '驾驶车辆在道路上行驶时，应当按照规定的速度安全行驶。', '安全驾驶，请驾驶员按照规定的速度行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074053, '正确', '错误', '', '', '1', '', '机动车紧急制动时，ABS系统在提供最大制动力的同时能使车前轮保持转向能力。', 'ABS防抱死系统；车轮不卡死、硬化路面制动时间短平稳、可以用力踩制动、系统自动张合8次/每秒。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074054, '使用远光灯，有利于观察路面情况', '禁止使用远光灯，避免灯光照射至前车后视镜造成前车驾驶人眩目', '使用远光灯，有利于告知前方驾驶人后方有来车', '禁止使用远光灯，避免灯光照射至前车后视镜造成自己眩目', '2', 'http://images.juheapi.com/jztk/a1b1subject1/747.jpg', '如图所示，夜间驾驶机动车与同方向行驶的前车距离较近时，以下做法正确的是什么？', '夜间驾驶机动车与同方向行驶的前车距离较近时，禁止使用远光灯，避免灯光照射至前车后视镜造成前车驾驶人眩目。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074056, '风窗玻璃除雾器', '风窗玻璃刮水器', '危险报警闪光灯', '照明、信号装置', '2', 'http://images.juheapi.com/jztk/a1b1subject1/749.jpg', '这个开关控制机动车哪个部位？', '这个控制风窗玻璃刮水器的。风窗玻璃除雾器和危险报警闪光灯都是按钮形式的，照明、信号装置在你的左手边。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074058, '放弃超越C车', '加速超越C车', '鸣喇叭示意B车让行后超车', '直接向左变更车道，迫使B车让行', '1', 'http://images.juheapi.com/jztk/a1b1subject1/751.jpg', '如图所示，驾驶机动车在这种情况下，当C车减速让超车时，A车应该如何行驶？', '因为C车减速让超车的同时又与来向B车进行会车，此时继续超车容易造成危险，所以应当放弃超越C车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074059, '闯红灯', '无证驾驶', '疲劳驾驶', '醉驾', '4', '', '以下哪项行为可构成危险驾驶罪？', '危险驾驶罪是指在道路上醉酒驾驶机动车或者在道路上驾驶机动车追逐竞驶，情节恶劣的行为。其他三个选项不符合要求。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074060, '间歇制动', '持续制动', '紧急制动', '缓踏制动踏板', '3', '', '防抱死制动系统（ABS）在什么情况下可以最大限度发挥制动器效能?', 'ABS的目的就是为了防止刹车的时候一脚踩死，导致翻车什么的。 不过刹车刹死就比较慢（速度N快的时候），效果等于踩下刹车，再松下刹车，反复几次！', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074061, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/754.jpeg', '机动车发生故障时，（如图所示）闪烁。', '图片中是危险报警闪光灯。当机动车发生故障时，该灯并不会闪烁。只有人为开启时，该该灯才会闪烁。题目描述有误，因此本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074062, '路面低洼', '驼峰桥', '路面不平', '路面高突', '3', 'http://images.juheapi.com/jztk/a1b1subject1/755.jpg', '这个标志是何含义？', '路面不平：用以提醒车辆驾驶人减速慢行。设在路面颠簸路段或桥头跳车较严重的地点以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074065, '20毫克/100毫升', '60毫克/100毫升', '80毫克/100毫升', '50毫克/100毫升', '3', '', '机动车驾驶人血液中酒精含量大于或者等于多少可认定为醉驾？', '驾驶人的血液中酒精含量在80毫克/100毫升以上，含（80毫克/100毫升）都属于醉酒驾驶', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074066, '主要责任', '次要责任', '同等责任', '全部责任', '4', '', '驾驶机动车发生交通事故后当事人故意破坏、伪造现场、毁灭证据的，应当承担什么责任？', '发生交通事故后当事人逃逸的，故意破坏、伪造现场、毁灭证据的，均承担全部责任。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074067, '正确', '错误', '', '', '1', '', '驾驶有ABS系统的机动车在紧急制动的同时转向可能会发生侧滑。', 'ABS防抱死系统；车轮不卡死、硬化路面制动时间短平稳、可以用力踩制动、系统自动张合8次/每秒。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074068, '醉酒后驾驶机动车的', '伪造、变造机动车驾驶证的', '饮酒后驾驶机动车的', '使用伪造、变造机动车驾驶证的', '3', '', '机动车驾驶人有以下哪种违法行为的，暂扣六个月机动车驾驶证？', '<b>饮酒驾驶机动车辆，罚款1000元—2000元、记12分并暂扣驾照6个月；</b>饮酒驾驶营运机动车，罚款5000元，记12分，处以15日以下拘留，并且5年内不得重新获得驾照。醉酒驾驶机动车辆，吊销驾照，5年内不得重新获取驾照，经过判决后处以拘役，并处罚金；醉酒驾驶营运机动车辆，吊销驾照，10年内不得重新获取驾照，终生不得驾驶营运车辆，经过判决后处以拘役，并处罚金。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074071, '正确', '错误', '', '', '1', '', '驾驶车辆通过人行横道线时，应注意礼让行人。', '安全第一，文明驾驶，礼貌让行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074073, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/766.jpeg', '机动车仪表板上（如图所示）一直亮，表示安全气囊处于工作状态。', '圆圈表示安全气囊，此标志亮，说明安全气囊处于故障状态。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074075, '必须报警，等候警察处理', '开车离开现场', '确保安全的情况下，对现场拍照，然后将车辆移至路边等不妨碍交通的地点', '停在现场保持不动', '3', '', '发生无人员伤亡的、财产轻微损失的交通事故后，以下做法正确的是什么？', '《中华人民共和国道路交通安全法实施条例》第八十六条规定:“机动车与机动车、机动车与非机动车在道路上发生未造成人身伤亡的交通事故，当事人对事实及成因无争议的，<b>在记录交通事故的时间、地点、对方当事人的姓名和联系方式、机动车牌号、驾驶证号、保险凭证号、碰撞部位，并共同签名后，撤离现场</b>，自行协商损害赔偿事宜。当事人对交通事故事实及成因有争议的，应当迅速报警。”', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074076, '腰部', '胸部', '头部', '颈部', '4', '', '安全头枕在发生追尾事故时，能有效保护驾驶人的什么部位？', '安全头枕是保护颈椎。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074077, '近光灯', '前后雾灯', '远光灯', '左右转向灯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/770.jpg', '旋转开关这一档控制机动车哪个部位？', '此处为雾灯的控制。下边的控制前雾灯，上边的控制后雾灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074078, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/771.jpg', '上下搬动这个开关前风窗玻璃刮水器开始工作。', '正确，这个没问题，左侧的开关是管灯光的，右侧的开关是管雨刷的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074081, '靠边减速或停车让行', '在原车道继续行驶', '占用非机动车道行驶', '鸣喇叭示意其让道', '1', '', '驾驶机动车行驶过程中，遇道路养护车辆从本车道逆向驶来时，以下做法正确的是什么？', '《中华人民共和国道路交通安全法》第五十四条条明确规定：<b>道路养护车辆、工程作业车进行作业时，在不影响过往车辆通行的前提下，其行驶路线和方向不受交通标志、标线限制，过往车辆和人员应当注意避让。</b>\r<br/>如果道路养护车逆向驶来，一定要靠边减速或停车让行，避免正向相撞。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074082, '迅速报警', '占道继续和对方争辩', '找中间人帮忙解决', '自行协商损害赔偿事宜', '1', '', '驾驶机动车发生交通事故，仅造成财产损失的，但是对交通事故事实及成因有争议的，应当怎么处理？', '对于事故成因不明的交通事故，应及时报警，交由警察处理。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074088, '核发地', '暂住地', '居住地', '户籍地', '1', '', '机动车驾驶证遗失的，机动车驾驶人应当向哪里的车辆管理所申请补发？', '驾驶证遗失、损毁无法辨认时驾驶员应当向驾驶证核发地车辆管理所申请补发。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074089, '打开危险报警闪光灯，夜间还应开启示廓灯、后位灯', '在车后150米以外设置安全警告标志', '车内乘员应下车辅助将故障车辆推移到紧急停车带上', '所有人员需离开故障车辆，在紧急停车带或护栏以外安全位置报警并等候救援', '3', '', '在高速公路上驾驶机动车，车辆发生故障后的处置方法，以下说法错误的是什么？', '《道路交通安全法》规定：机动车在高速公路上发生故障时，驾驶人应当立即开启危险报警闪光灯，将车辆移至不妨碍交通的地点，并在车后150米设置警告标志。夜间还应当同时开启示廓灯和后位灯。驾驶人和乘车人应当迅速转移到右侧路肩上或者应急车道内。\r<br/>如果发生故障无法正常行驶的，应当由救援车、清障车拖曳、牵引。而不是车内乘员将其推到紧急停车带上。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074090, '停在交叉路口，等待安全时左转', '继续向前行驶', '在确保安全的情况下，倒车然后左转', '原地掉头后，右转', '2', '', '当您即将通过交叉路口的时候，才意识到要左转而不是向前，以下说法正确的是什么?', '按规定，交叉路口，不可以停车，不可以倒车，只能继续向前行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074092, '未悬挂机动车号牌', '未携带身份证', '未携带保险合同', '未放置城市环保标志', '1', '', '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '未放置检验合格标志，未悬挂机动车号牌，未放置保险标志的，未携带机动车行驶证的都是可依法扣留车辆的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074093, '正确', '错误', '', '', '2', '', '驾驶机动车碰撞建筑物、公共设施后可即行撤离现场。', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，当事人应当保护现场并立即报警： 　　<br/>（一）造成人员死亡、受伤的； 　　<br/>（二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　<br/>（三）机动车无号牌、无检验合格标志、无保险标志的； 　　<br/>（四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的； 　　<br/><b>（五）碰撞建筑物、公共设施或者其他设施的；</b>　　<br/>（六）驾驶人无有效机动车驾驶证的； 　　<br/>（七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； 　　<br/>（八）当事人不能自行移动车辆的。 　　<br/>发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074094, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/787.jpg', '按下这个开关，后风窗玻璃除霜器开始工作。', '这题表述的意思是：当前按下的这个图标即亮灯的这个表示的是前窗玻璃除霜器开始工作。<br/>核心的意思是这两个图标，一个表示前窗玻璃除霜器，一个表示后窗玻璃除霜器。<br/>带扇形的那个是前窗标识，下面那个长方形的是后窗标识。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074096, '左右转向灯', '倒车灯', '示廓灯', '报警闪光灯', '1', 'http://images.juheapi.com/jztk/a1b1subject1/789.jpg', '提拉这个开关控制机动车哪个部位？', '向上提：右转向灯亮，（顺时针方向，向右打方向盘的方向） \r<br/>向下拉：左转向灯亮，（逆时针方向，向左打方向盘的方向）', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074097, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/790.jpg', '灯光开关在该位置时，后雾灯点亮。', '停的位置雾灯是冲右的，此为后雾灯，故正确。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074098, '接近没有交通信号灯控制的交叉路口时', '与对向机动车会车时', '接近人行横道时', '城市道路照明条件不良时', '2', '', '夜间驾驶机动车在没有中心隔离设施或者没有中心线的道路上行驶，以下哪种情况下应当改用近光灯？', '夜间驾驶机动车在没有中心隔离设施或者没有中心线的道路上行驶，在会车时，使用远光灯，会使对方看不清路况从而容易发生事故，应该切换近光灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074100, '正确', '错误', '', '', '2', '', '在道路上跟车行驶时，跟车距离不是主要的，只须保持与前车相等的速度，即可防止发生追尾事故。', '跟车时，与前车保持相同的速度，只能让两车间的距离保持不变，并不能保证安全。而保持足够的安全距离，才能有效防止追尾的发生。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074104, '减速慢行', '注意危险', '拥堵路段', '事故多发路段', '2', 'http://images.juheapi.com/jztk/a1b1subject1/797.jpg', '这个标志是何含义？', '注意危险：用以促使车辆驾驶员谨慎慢行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074105, '不要移动车辆', '疏导其他车辆绕行', '撤离现场自行协商', '保护好现场再协商', '3', '', '在道路上发生未造成人员伤亡且无争议的轻微交通事故如何处置？', '《道路交通安全法》第七十条：<b>在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；</b>不立即撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074106, '禁止在路口掉头', '禁止向左向右变道', '禁止向左向右转弯', '禁止车辆直行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/799.jpg', '这个标志是何含义？', '禁止向左向右转弯：表示前方路口禁止一切车辆向左向右转弯。此标志设在禁止向左向右转弯的路口前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074107, '正确', '错误', '', '', '2', '', '在道路上超车时，应尽量加大横向距离，必要时可越实线超车。', '实线不能跨越，因此不能越实线超车，本题错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074108, '40米减速行驶路段', '最低时速40公里', '最高时速40公里', '解除时速40公里限制', '4', 'http://images.juheapi.com/jztk/a1b1subject1/801.jpg', '这个标志是何含义？', '解除时速40公里限制：表示限制速度路段结束。此标志设在限制车辆速度路段的重点。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074110, '高速公路公用电话', '高速公路报警电话', '高速公路紧急电话', '高速公路救援电话', '3', 'http://images.juheapi.com/jztk/a1b1subject1/803.jpg', '这个标志是何含义？', '高速公路紧急电话：用于指示高速公路紧急电话的位置，可单独设置也可设在紧急电话的立柱上或箱体上。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074111, '堤坝路', '临崖路', '傍水路', '易滑路', '1', 'http://images.juheapi.com/jztk/a1b1subject1/804.jpg', '这个标志是何含义？', '堤坝路：用以提醒车辆驾驶人小心驾驶，设在沿水库、湖泊、河流等堤坝道路以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074112, '最高速度', '限制速度', '建议速度', '最低速度', '3', 'http://images.juheapi.com/jztk/a1b1subject1/805.jpg', '这个标志是何含义？', '此标志表示建议减速。用以提醒车辆驾驶人建议的速度行驶，设在弯道、出口、匝道的适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074113, '平面交叉路口', '环行平面交叉', '注意交互式道路', '注意分离式道路', '4', 'http://images.juheapi.com/jztk/a1b1subject1/806.jpg', '这个标志是何含义？', '注意分离式道路：用以警告车辆驾驶人注意前方平面交叉的被交道路是分离式道路。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074114, '正确', '错误', '', '', '1', '', '使用伪造、变造的驾驶证一次记12分。', '机动车驾驶人有下列违法行为之一，一次记12分：使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074116, '正确', '错误', '', '', '1', '', '驾驶人一边驾车，一边打手持电话是违法行为。', '新修订的《机动车驾驶证申领和使用规定》公布后，2013年1月1日起施行，其中规定：驾驶机动车有拨打、接听手持电话等妨碍安全驾驶的行为的一次记2分。并且违反了《道路交通安全法》的相关规定，属于违法行为。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074117, '在加速车道上加速，同时要开启左转向灯', '密切注意左侧行车道的车流状态，同时用后视镜观察后方的情况', '充分利用加速车道的长度加速,确认安全后,平顺地进入行车道', '经加速车道充分加速后，可直接驶入最左侧车道', '4', '', '驾驶机动车由加速车道进入高速公路行驶，以下做法错误的是什么？', '《中华人民共和国道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。在高速公路上行驶的小型载客汽车最高车速不得超过每小时120公里，其他机动车不得超过每小时100公里，摩托车不得超过每小时80公里。同方向有2条车道的，左侧车道的最低车速为每小时100公里；同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里。因此，<b>驾车进入高速公路后，不能直接驶入最左车道，要循序渐进。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074118, '吊销驾驶证', '拘留驾驶人', '警告', '罚款', '4', '', '机动车驾驶人补领机动车驾驶证后，使用原机动车驾驶证驾驶的，除由公安机关交通管理部门收回原机动车驾驶证外，还应当受到何种处罚？', '机动车驾驶人补领机动车驾驶证后，继续使用原机动车驾驶证的,公安机关交通管理部门可处二十元以上二百元以下罚款。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074120, '警告标志', '指路标志', '指示标志', '禁令标志', '1', 'http://images.juheapi.com/jztk/a1b1subject1/813.jpg', '这属于哪一种标志？', '交叉路口：用以警告车辆驾驶人谨慎慢行，注意横向来车。设在平面交叉路口驶入路段的适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074122, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/815.jpg', '这个标志的含义是表示车辆会车时，对方车辆应停车让行。', '此为<b>会车让行标志</b>，表示车辆会车时，面对标志的车辆必须停车让对方车辆先行。题目描述有误，因此本题答案为错。\r<br/>\r<br/>【拓展延伸】\r<br/><p><img src=http://tp.mnks.cn/tp_176.jpg>\r<br/>停车让行</p><p><img src=http://tp.mnks.cn/tp_178.jpg>\r<br/>会车让行</p><p><img src=http://tp.mnks.cn/tp_219.jpg>\r<br/>会车先行</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074123, '未携带机动车登记证书', '未携带保险合同', '未放置城市环保标志', '未放置保险标志', '4', '', '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '未放置检验合格标志，未悬挂机动车号牌，未放置保险标志的，未携带机动车行驶证的都是可依法扣留车辆的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074124, 'A车负全部责任', 'B车负全部责任', '都无责任，后果自行承担', '各负一半责任', '2', 'http://images.juheapi.com/jztk/a1b1subject1/817.jpg', '如图所示，在这起交通事故中，以下说法正确的是什么？', 'A车在车道上正常行驶，B车掉头导致事故发生，因此，B车负全责。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074125, '15日以下拘留', '吊销驾驶证', '扣留车辆', '5年不得重新取得新驾驶证', '2', '', '将机动车交由未取得机动车驾驶证的人驾驶的，由公安交通管理部门处二百元以上二千元以下罚款，可以并处以下哪种处罚？', '将机动车交由未取得机动车驾驶证的人驾驶的，由公安交通管理部门处二百元以上二千元以下罚款，并处以吊销驾驶证的处罚。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074126, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/819.jpg', '如图所示，A车具有优先通行权。', '左转让直行！右转让左转！', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074128, '应减速观察水情，然后加速行驶通过', '应停车察明水情，确认安全后，低速通过', '应停车察明水情，确认安全后，快速通过', '可随意通行', '2', 'http://images.juheapi.com/jztk/a1b1subject1/821.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？ ', '机动车在有积水较深的桥下通过行车时，应当停车察明水情，确认安全后，低速通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074131, '正确', '错误', '', '', '2', '', '装有ABS系统的机动车在冰雪路面上会最大限度缩短制动距离。', '我们要知道，采用ABS系统的机动车，可以在滚动、抱死两种状态中间不断切换，让轮胎尽量保持最大静摩擦力，也能让轮胎防滑部分充分发挥效用。\r<br/>ABS系统的作用不是缩短制动距离，题目描述有误，因此本题答案错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074132, '9a', '9b', '9c', '9d', '3', 'http://images.juheapi.com/jztk/a1b1subject1/825.jpg', '下列哪个指示灯亮表示车辆在使用近光灯?(图9a. 9b. 9c. 9d)', '9a是车廓灯，9b是前后雾灯，9c是近光灯，9d制动系统出现异常或故障。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074134, '速度和里程表', '发动机转速表', '最高时速值表', '百公里油耗表', '1', 'http://images.juheapi.com/jztk/a1b1subject1/827.jpg', '这个仪表是何含义？', '图为时速表，看到那个KM没？那个是公里的意思，也就是里程表的意思。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074135, '两侧变窄', '右侧变窄', '左侧变窄', '桥面变窄', '1', 'http://images.juheapi.com/jztk/a1b1subject1/828.jpg', '这是什么交通标志？', '两侧变窄：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074136, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/829.jpg', '在这种情况下可以加速通过人行横道。', '图中可以看出，人行横道上正在有老人过马路，所以应该是停车等待，等待行人通过后，再驶离人行横道。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074138, '使用近光灯', '不使用灯光', '使用远光灯', '使用雾灯', '1', 'http://images.juheapi.com/jztk/a1b1subject1/831.jpg', '在这种天气条件下行车如何使用灯光？', '一般雪天能见度低，应该注意灯光的正确使用。一是开启近光灯，帮助驾驶员�望前方情况，使驾驶员对前方情况看得清楚一些。二是开启示廓灯和前后位灯，示意自己车辆的存在，使前后行驶的车辆，能够看到自己的具体位置及通行情况，方便他们采取相应的措施。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074139, '单手握方向盘，对机动车控制力下降', '驾驶人注意力不集中，不能及时判断危险', '电话的信号会对汽车电子设备的运行造成干扰', '驾驶人对路况观察不到位，容易导致操作失误', '3', '', '驾驶机动车时接打电话容易引发事故，以下原因错误的是什么?', '驾驶机动车时接打电话容易引发事故，主要是因为接电话时单手握方向盘，对机动车控制力下降。而且驾驶人注意力不集中，不能及时判断危险。同时驾驶人对路况观察不到位，容易导致操作失误。电话信号并不会对汽车电子设备的运行造成干扰。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074140, '12个月', '24个月', '3个月', '6个月', '1', '', '道路交通安全违法行为累积记分的周期是多长时间？', '123号令第五十五条规定：道路交通安全违法行为<b>累积记分周期（即记分周期）为12个月</b>，满分为12分，从机动车驾驶证初次领取之日起计算。因此本题答案应为C。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074141, '任何情况下都不允许上高速', '不需要其他人员陪同', '需要持有相应或者更高准驾车型驾驶证三年以上的驾驶人陪同', '需要持有相应或者更高准驾车型驾驶证、同在实习期内的驾驶人陪同', '3', '', '实习期驾驶人驾驶机动车上高速公路行驶，以下做法正确的是什么？', '《道路交通安全法》规定：实习期驾驶人驾驶机动车上高速公路行驶，应当由持相应或者更高准驾车型驾驶证三年以上的驾驶人陪同。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074142, '转弯的机动车让直行的车辆、行人先行', '没有交通标志、标线控制时，在进入路口前停�望，让右方道路的来车先行', '相对方向行驶的右转弯机动车让左转弯的车辆先行', '相对方向行驶的左转弯机动车让右转弯的车辆先行', '4', '', '驾驶机动车通过未设置交通信号灯的交叉路口时，下列说法错误的是什么？', '驾驶机动车通过未设置交通信号灯的交叉路口时：<br/>转弯的机动车应当让直行的车辆、行人先行；<br/>没有交通标志、标线控制时，在进入路口前停车瞭望，让右方道路的来车先行；<br/><b>相对方向行驶的右转弯机动车让左转弯的车辆先行。</b><br/>题目问的是错误的，所以选择D要看清题目哦！', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074143, '加速驶离高速公路', '在应急车道上停车等待', '可以继续行驶，但车速不得超过每小时40公里', '以不超过每小时20公里的车速从最近的出口尽快驶离高速公路', '4', '', '驾驶机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，能见度在50米以下时，以下做法正确的是什么？', '《道路交通安全法》规定：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074144, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/837.jpg', '仪表显示油箱内存油量已在警告线以内。', '左边可以看到那个加油的机器，指针到红色区，说明油量不足了。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074147, '调整驾驶座椅，保证踩踏踏板舒适', '调整安全带的松紧与高低', '调整适合驾驶的方向盘位置', '调整安全头枕高度，使头枕正对驾驶人的颈椎', '4', '', '驾驶机动车前，以下说法错误的是什么？', '驾驶机动车前，需要：调整驾驶座椅，保证踩踏踏板舒适；调整安全带的松紧与高低；调整适合驾驶方向盘的位置。<b>安全头枕需要对准后脑勺来保护驾驶员的颈椎。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074148, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/841.jpg', '如图所示，这这种情况下只要后方、对向无来车，可以掉头。', '简单来说：黄线是用来画分来回车道的，黄实线是禁止越线行驶。黄虚线是在对面没车来的情况下，可以在黄虚线越过转弯或者是掉头。白实线是用来规分同方向车道的，实线是表示同方向行驶，不能越线到另一同方向车道行驶。白虚线是虚线同方向车道的，可以越线到另一同方向车道行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074149, '以便随时停车', '给该车让出足够的超车空间', '避让行人与非机动车', '提醒其他交通参与者注意避让', '2', '', '驾驶机动车正在被其他车辆超越时，被超车辆减速靠右侧行驶的目的是什么?', '\"《道路交通安全法实施条例》第四十七条：机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。在没有道路中心线或者同方向只有1条机动车道的道路上，前车遇后车发出超车信号时，在条件许可的情况下，应当降低速度、靠右让路。后车应当在确认有充足的安全距离后，从前车的左侧超越，在与被超车辆拉开必要的安全距离后，开启右转向灯，驶回原车道。\"', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074150, '易滑路段', '急转弯路', '反向弯路', '连续弯路', '4', 'http://images.juheapi.com/jztk/a1b1subject1/843.jpg', '这是什么交通标志？', '连续弯路：用以警告车辆驾驶人减速慢行。设置位置为连续弯路起点的外面，当连续弯路总长度大于500m时，应重复设置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074151, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/844.jpg', '这个标志的含义是提醒车辆驾驶人前方是车辆渡口。', '警告标志：渡口。用以提醒车辆驾驶人谨慎驾驶。设在车辆渡口以前适当位置。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074152, '④③①②', '①②③④', '③②①④', '②③①④', '4', '', '驾驶机动车在高速公路发生故障，需要停车排除故障时，以下做法先后顺序正确的是？ ①放置警告标志，转移乘车人员至安全处，迅速报警②开启危险报警闪光灯③将车辆移至不妨碍交通的位置④等待救援', '驾驶机动车在高速公路发生故障，需要停车排除故障时，应当进行如下顺序的操作：开启危险报警闪光灯；将车辆移至不妨碍交通的位置；放置警告标志，转移乘车人员至安全处，迅速报警；等待救援。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074153, '体现对老年人的关心', '例行程序仅供参考', '检查是否患有老年常见病', '检查是否患有妨碍安全驾驶的疾病', '4', '', '年龄在60周岁以上的机动车驾驶人，应当每年进行一次身体检查的目的是什么？', '根据《机动车驾驶证申领使用规定》第四十八条规定年龄在60周岁以上或者持有大型客车、牵引车、城市公交车、中型客车、大型货车、无轨电车、有轨电车准驾车型的机动车驾驶人，应当每年进行一次身体检查，在记分周期结束后十五日内，提交县级或者部队团级以上医疗机构出具的有关身体条件的证明，检查是否患有妨碍安全驾驶的疾病。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074155, '51a', '51b', '51c', '51d', '1', 'http://images.juheapi.com/jztk/a1b1subject1/848.jpg', '如图所示，以下哪种情况可以超车?(图51a.51b. 51c. 51d)', '如图所示，能够超车的必须满足两个条件：一是必须在虚线的一侧，而是对向没有来车。满足这个两个条件的就只有51a了。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074156, '右侧车让左侧车先行', '每车道一辆依次交替驶入左侧车道', '左侧车让右侧车先行', '右侧车寻找空隙提前进入左侧车道', '2', 'http://images.juheapi.com/jztk/a1b1subject1/849.jpg', '如图所示，驾驶机动车行驶至车道减少的路段时，遇前方机动车排队等候或行驶缓慢时，以下做法正确的是什么？', '驾驶机动车行驶至车道减少的路段时，前方机动车排队等候或行驶缓慢时，应当每车道一辆依次交替驶入左侧车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074157, '加速进入两侧车道行驶', '进入右侧车道行驶', '减速进入两侧车道行驶', '禁止车辆在两侧车道通行', '4', 'http://images.juheapi.com/jztk/a1b1subject1/850.jpg', '遇到这种情况时怎样行驶？', '两侧车道都是红灯，都不可以通行，所以是禁止车辆在两侧车道通行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074160, '注意避让', '若洒水车有指示箭头，在确保安全的情况下按箭头指示方向变更车道', '若洒水车无指示箭头，在确保安全的情况下选择合适的车道变更', '通过洒水车时应急加速通过', '4', '', '驾驶机动车遇到前方低速行驶的洒水车作业时，以下做法错误的是什么？', '驾驶机动车遇到前方低速行驶的洒水车作业时，需要注意避让。若洒水车有指示箭头，在确保安全的情况下按箭头指示方向变更车道；若洒水车无指示箭头，在确保安全的情况下选择合适的车道变更。不能加速通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074161, '正确', '错误', '', '', '1', '', '驾驶人在机动车驾驶证的6年有效期内，每个记分周期均未达到12分的，换发10年有效期的机动车驾驶证。', '机动车驾驶人在机动车驾驶证的六年有效期内，每个记分周期均未记满12分的，换发十年有效期的机动车驾驶证；在机动车驾驶证的十年有效期内，每个记分周期均未记满12分的，换发长期有效的机动车驾驶证。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074162, '尽快加速通过', '靠边停车等待', '注意�望安全通过', '紧急制动', '3', 'http://images.juheapi.com/jztk/a1b1subject1/855.jpg', '驾驶机动车遇到这种信号灯不断闪烁时怎样行驶？', '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074164, '醉酒驾驶机动车的', '故意遮挡机动车号牌的', '使用其他车辆保险标志的', '因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的', '4', '', '以下哪种行为处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证？', '依据《中华人民共和国道路交通安全法》：饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。<b>因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074165, '驾驶机动车不按规定避让校车的', '驾驶证被暂扣期间驾驶机动车的', '驾驶机动车违反道路交通信号灯通行的', '驾驶与准驾车型不符的机动车的', '4', '', '以下哪种行为机动车驾驶人将被一次记12分？', '机动车驾驶人有下列违法行为之一，一次记12分：\r<br/>1、驾驶营运客车超载20％以上（将校车列入其中）\r<br/>2、严重超速（新规定加入校车）\r<br/>3、故意遮挡号牌（由扣6分调整为扣12分）\r<br/>4、驾驶营运客车在高速公路车道内停车（由扣6分调整为扣12分）\r<br/>5、连续驾驶中型以上载客汽车、危险物品运输车辆超过4小时未停车休息或者停车休息时间少于20分钟（由扣6分调整为扣12分）\r<br/>6、未取得校车驾驶资格驾驶校车（新增）\r<br/>7、饮酒后驾驶机动车\r<br/><b>8、驾驶与准驾车型不符</b>\r<br/>9、造成交通事故后逃逸\r<br/>10、伪造、更改号牌和证件\r<br/>11、驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074166, '左侧', '中部', '右侧', '前方', '2', '', '客车遇碰刮或制动失效时，应迅速告知乘客向车厢哪个位置或没有被刮碰的一侧挤靠，并抓住车内固定物，注意防范车身变形挤伤身体？', '中间最安全，四周都有可能被碰着，为什么汽车都用铁的做，不用纸做的原因就在这里！', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074167, '停车让行线', '减速让行线', '左弯待转线', '等候放行线', '1', 'http://images.juheapi.com/jztk/a1b1subject1/860.jpg', '路口最前端的双白实线是什么含义？', '<p><img src=http://tp.mnks.cn/tp_332.jpg>\r<br/>停止线\r<br/><img src=http://tp.mnks.cn/tp_333.jpg>\r<br/>停车让行线\r<br/><img src=http://tp.mnks.cn/tp_334.jpg>\r<br/>减速让行线</p>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074168, '注意牲畜', '注意野生动物', '野生动物保护区', '大型畜牧场', '1', 'http://images.juheapi.com/jztk/a1b1subject1/861.jpg', '这个标志是何含义？', '这个是头牛，是牲畜。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074169, '距有人看守铁路道口50米', '距无人看守铁路道口100米', '距有人看守铁路道口100米', '距无人看守铁路道口50米', '2', 'http://images.juheapi.com/jztk/a1b1subject1/862.jpg', '这个标志是何含义？', '一道红线是50米，二道100米。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074170, '停车线', '减速线', '导流线', '网状线', '3', 'http://images.juheapi.com/jztk/a1b1subject1/863.jpg', '图中圈内三角填充区域是什么标线？', '导流线的形式主要为一个或几个根据路口地形设置的白色V形线或斜纹线区域，表示车辆必须按规定的路线行驶，不得压线或越线行驶。 主要用于过宽、不规则或行驶条件比较复杂的交叉路口,立体交叉的匝道口或其他特殊地点。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074171, '高速公路特殊天气建议速度', '高速公路特殊天气最低速度', '高速公路特殊天气平均速度', '高速公路特殊天气最高速度', '1', 'http://images.juheapi.com/jztk/a1b1subject1/864.jpg', '这个标志是何含义？', '高速公路特殊天气建议速度：用以提醒驾驶员在雨、雪、雾等特殊天气下，以建议速度行驶，和白色半圆状车距确认线配合使用。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074172, '高速公路缴费车道', '高速公路检查车道', '高速公路领卡车道', '高速公路ETC车道', '4', 'http://images.juheapi.com/jztk/a1b1subject1/865.jpg', '这个标志是何含义？', '高速公路ETC车道：用于指示电子不停车收费车道，设在收费广场渐变段起点前300 m处。表示单向3个收费车道时ETC车道指示标志。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074173, '人行横道', '学生通道', '注意行人', '儿童通道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/866.jpg', '这个标志是何含义？', '表示该处为专供行人横穿马路的通道。此标志设在人行横道的两侧。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074174, '限制宽度为3.5米', '解除3.5米限高', '限制车距为3.5米', '限制高度为3.5米', '4', 'http://images.juheapi.com/jztk/a1b1subject1/867.jpg', '这个标志是何含义？', '限制高度为3.5米：表示禁止装载高度超过标志所示数值的车辆通行。此标志设在最大允许高度受限制的地方。 以图为例：装载高度不得超过3.5米。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074175, '高速公路入口预告', '高速公路终点预告', '高速公路起点预告', '高速公路出口预告', '1', 'http://images.juheapi.com/jztk/a1b1subject1/868.jpg', '这个标志是何含义？', '高速公路入口预告：用于指示进入高速公路或城市快速路的入口，设在进入高速公路或城市快速路前的被交道路上适当位置。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074176, '3分', '2分', '6分', '12分', '4', '', '饮酒后驾驶机动车一次记几分？', '道路交通安全违法行为记分分值：\r<br/>　　一、机动车驾驶人有下列违法行为之一，<b>一次记12分：</b>\r<br/>　　（一）驾驶与准驾车型不符的机动车的；\r<br/>　　<b>（二）饮酒后驾驶机动车的；</b>\r<br/>　　（三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数20%以上的；\r<br/>　　（四）造成交通事故后逃逸，尚不构成犯罪的；\r<br/>　　（五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的；\r<br/>　　（六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；\r<br/>　　（七）驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的；\r<br/>　　（八）驾驶营运客车在高速公路车道内停车的；\r<br/>　　（九）驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20%以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50%以上，以及驾驶其他机动车行驶超过规定时速50%以上的；\r<br/>　　（十）连续驾驶中型以上载客汽车、危险物品运输车辆超过4小时未停车休息或者停车休息时间少于20分钟的；\r<br/>　　（十一）未取得校车驾驶资格驾驶校车的。\r<br/>　', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074177, '禁止直行', '允许掉头', '禁止掉头', '禁止转弯', '3', 'http://images.juheapi.com/jztk/a1b1subject1/870.jpg', '路面上的黄色标记是何含义？', '一个X加上一个向回掉头的标记，加一起就是禁止掉头', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074178, '不得超过40公里/小时', '不得超过60公里/小时', '不得超过80公里/小时', '不得超过90公里/小时', '1', '', '驾驶机动车在高速公路遇到能见度低于100米的气象条件时，最高车速是多少？', '《道路交通安全法实施条例》第八十一条：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：(一)能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；<b>(二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；</b>(三)能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074179, '向左转弯', '禁止直行', '直行车道', '单行路', '1', 'http://images.juheapi.com/jztk/a1b1subject1/872.jpg', '这个标志是何含义？', '向左转弯：表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074180, '车道方向预告', '交叉路口预告', '分道信息预告', '分岔处预告', '2', 'http://images.juheapi.com/jztk/a1b1subject1/873.jpg', '这个标志是何含义？', '此标志表示大交通量的四车道以上公路交叉路口预告', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074181, 'Y型交叉口', '主路让行', '注意分流', '注意合流', '4', 'http://images.juheapi.com/jztk/a1b1subject1/874.jpg', '这个标志是何含义？', '黄底为警告标志，所以排除A和B，图很明显的为合流标志，所以选D', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074182, '泥泞道路', '低洼路面', '过水路面', '渡口', '3', 'http://images.juheapi.com/jztk/a1b1subject1/875.jpg', '这个标志是何含义？', '过水路面指的是通过平时无水或流水很少的宽浅河流而修筑的在洪水期间容许水流浸过的路面。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074183, '仅允许上下人员', '仅允许装卸货物', '禁止长时间停车', '禁止停放车辆', '4', 'http://images.juheapi.com/jztk/a1b1subject1/876.jpg', '路缘石上的黄色实线是何含义？', '黄色实线表示禁止停放车辆，黄色虚线表示禁止长时间停车。题目图片是黄色实线，因此表示禁止停放车辆。选项D正确，答案选D。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074184, '正确', '错误', '', '', '2', '', '驾驶机动车找不到停车位时可以借人行道停放。', '《中华人民共和国道路交通安全法》第五十六条“机动车应当在规定地点停放，禁止在人行道上停放机动车。”', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074185, '两侧通行', '右侧通行', '左侧通行', '不准通行', '2', 'http://images.juheapi.com/jztk/a1b1subject1/878.jpg', '这个标志是何含义？', '用于引导行车方向，提示道路使用者前方线形变化，注意谨慎驾驶。设于中央隔离设施端部、渠化设施的端部、桥头等。答题技巧：∧表示俩侧通行，/这个方向的表示左侧通行，这个方向表示右侧通行。交通指路标志大全：http://www.jszks.com/onepage/16752.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074186, '正确', '错误', '', '', '1', '', '距离交叉路口50米以内的路段不能停车。', '根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br/>（1）在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；<br/>（2）<b>交叉路口、</b>铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；<br/>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；<br/>（4）车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行；<br/>（5）路边停车应当紧靠道路右侧，机动车驾驶人不得离车，上下人员或者装卸物品后，立即驶离；<br/>（6）城市公共汽车不得在站点以外的路段停车上下乘客。<br/>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074187, '正确', '错误', '', '', '1', '', '小型汽车驾驶人发生交通事故造成人员死亡，承担同等以上责任未被吊销驾驶证的，应当在记分周期结束后30日内接受审验。', '《机动车驾驶证申领和使用规定》（公安部令第123号）第六十条：<br/>小型汽车驾驶人发生交通事故造成人员死亡承担同等以上责任未被吊销机动车驾驶证的，应当在本记分周期结束后三十日内到公安机关交通管理部门接受审验。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074188, '保持50米以上', '保持60米以上', '保持100米以上', '保持80米以上', '3', '', '驾驶小型载客汽车在高速公路上时速超过100公里时的跟车距离是多少？', '《道路交通安全法实施条例》第八十条：机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离，车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074189, '两侧通行', '右侧通行', '左侧通行', '不准通行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/882.jpg', '这个标志是何含义？', '用于引导行车方向，提示道路使用者前方线形变化，注意谨慎驾驶。设于中央隔离设施端部、渠化设施的端部、桥头等。答题技巧：∧表示俩侧通行，/这个方向的表示左侧通行，这个方向表示右侧通行。交通指路标志大全：http://www.jszks.com/onepage/16752.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074190, '指示车辆行进', '警告前方危险', '禁止或限制行为', '告知方向信息', '3', '', '禁令标志的作用是什么？', '禁令标志是交通标志中主要标志的一种，对车辆加以禁止或限制的标志，如禁止通行、禁止停车、禁止左转弯、禁止鸣喇叭、限制速度、限制重量等。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074191, '禁止跨越对向车行道分界线', '可跨越对向车道分界线', '双侧可跨越同向车道分界线', '单向行驶车道分界线', '1', 'http://images.juheapi.com/jztk/a1b1subject1/884.jpg', '路中心双黄实线是何含义？', '双黄实线表示禁止跨越。因此是禁止跨越对向车行道分界线。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074192, '水渠', '桥梁', '隧道', '涵洞', '3', 'http://images.juheapi.com/jztk/a1b1subject1/885.jpg', '这个标志是何含义？', '隧道：用以提醒车辆驾驶人注意慢行。设在双向行驶并且照明不好的隧道口前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074193, '5米以内', '10米以内', '50米以内', '30米以内', '3', 'http://images.juheapi.com/jztk/a1b1subject1/886.jpg', '在距这段路多少米以内的路段不能停放机动车？', '根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：\r<br/>（1）在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；\r<br/><b>（2）交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；</b>\r<br/>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；\r<br/>（4）车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行；\r<br/>（5）路边停车应当紧靠道路右侧，机动车驾驶人不得离车，上下人员或者装卸物品后，立即驶离。\r<br/>（6）城市公共汽车不得在站点以外的路段停车上下乘客。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074194, '正确', '错误', '', '', '1', '', '不要在驾驶室的前后窗范围内悬挂和放置妨碍驾驶人视线的物品。', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074195, '100公里/小时', '90公里/小时', '120公里/小时', '110公里/小时', '2', 'http://images.juheapi.com/jztk/a1b1subject1/888.jpg', '在这条车道行驶的最高车速是多少？', '高速公路上同方向有2条车道的，左侧车道的最低车速为每小时100公里。但是图中最高限度标志为90公里/小时，所以应根据标志来选择90公里/小时。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074196, '靠边减速或停车让行', '占用其他车道行驶', '加速变更车道避让', '在原车道内继续行驶', '1', '', '行车中遇抢救伤员的救护车从本车道逆向驶来时，应怎样做？', '遇到正在执行任务的特种车辆，应当靠边减速或停车让行，为其让路。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074197, '正确', '错误', '', '', '1', '', '遇紧急情况时，客车的顶窗也可用作疏散乘客的紧急出口。', '', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074198, '环行交叉路口预告', '十字交叉路口预告', 'Y型交叉路口预告', '丁字交叉路口预告', '3', 'http://images.juheapi.com/jztk/a1b1subject1/891.jpg', '这个标志是何含义？', '俩个箭头加上你来的道路形成一个Y字。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074199, '道路入口标线', '可跨越式分道线', '道路出口减速线', '道路出口标线', '4', 'http://images.juheapi.com/jztk/a1b1subject1/892.jpg', '路面由白色虚线和三角地带标线组成的是什么标线？', '道路出口标线为白色虚线，主要用于高速公路和其他采用立体交叉道路。该标线作用为，高速公路出入口横向标线或三角地带标线是为驶入或驶出匝道车辆提供安全交汇引导，减少与突出部缘石碰撞。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074200, '正确', '错误', '', '', '1', '', '机动车在高速公路上发生交通事故无法正常行驶时，用救援车、清障车拖曳、牵引。', '《中华人民共和国道路交通安全法》第六十八条： 机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当由救援车、清障车拖曳、牵引。 所以本题是正确的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074201, '隧道入口', '气象台', '注意横风', '风向标', '3', 'http://images.juheapi.com/jztk/a1b1subject1/894.jpg', '这个标志是何含义？', '注意横风：此标志设在经常有很强的侧风并有必要引起注意的路段前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074202, '正确', '错误', '', '', '1', '', '路口黄灯持续闪烁，警示驾驶人要注意�望，确认安全通过。', '路口黄灯持续闪烁，警示驾驶人要注意�望，确认安全通过。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074203, '2分', '3分', '6分', '12分', '3', '', '驾驶人驾驶机动车违反道路交通信号灯通行一次记多少分?', '驾驶机动车违反道路交通信号灯通行的一次记6分。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074204, '发生侧滑、引发交通事故', '因视线模糊而撞车', '不被其他车辆驾驶人发现', '引起发动机熄火', '1', '', '下雨后路面湿滑，车辆行驶中紧急制动时，容易导致什么？', '<b>下雨时路面潮湿，轮胎与路面之间的滑动摩擦因素小，相应的摩擦力也要变小。</b>所以紧急制动时，汽车轮胎停止滚动，而向前滑动。或者说滑动摩擦力不能有效减慢汽车速度，导致汽车还要向前滑出很大段距离。因此天雨路滑，宜减速行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074205, '发动机易熄火', '易发生侧滑', '能见度低，视线不清', '行驶阻力增大', '3', '', '雾天对安全行车的主要影响是什么？', '雾天的能见度很低，容易造成视野不良。而易发生侧滑的应该是滑溜的路面，雾天与行驶阻力大和发动机熄火并无直接关联。因此本题选D', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074206, '待转线', '减速线', '让行线', '停止线', '4', 'http://images.juheapi.com/jztk/a1b1subject1/899.jpg', '图中圈内白色横实线是何含义？', '<p><img src=http://tp.mnks.cn/tp_332.jpg>\r<br/>停止线\r<br/><img src=http://tp.mnks.cn/tp_333.jpg>\r<br/>停车让行线\r<br/><img src=http://tp.mnks.cn/tp_334.jpg>\r<br/>减速让行线</p>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074207, '靠左侧路边逆向停放', '在停车泊位内停放', '在路边随意停放', '在人行道上停放', '2', '', '驾驶机动车需要在路边停车时怎样选择停车地点？', '一般人行道上是不能停车的，路边也不能随意停车。在路边可以顺向临时停车，但是不能逆向停车。所以最佳停车地点还是停车泊位内。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074208, '直行', '向右转弯', '向左转弯', '直行或向左转弯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/901.jpg', '这个路口允许车辆怎样行驶？', '右转的灯是绿的，所以只能向右转弯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074209, '禁止右转', '路口警示', '准许直行', '加速通过', '2', 'http://images.juheapi.com/jztk/a1b1subject1/902.jpg', '驾驶机动车在路口遇到这种信号灯表示什么意思？', '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074210, '处15日以下拘留', '终生禁驾', '进行法律法规教育，重新考试', '依法追究刑事责任', '3', '', '公安机关交通管理部门对累积记分达到规定分值的驾驶人怎样处理？', '机动车驾驶人<b>在一个记分周期内累积记分达到12分的，</b>公安机关交通管理部门应当扣留其机动车驾驶证。 机动车驾驶人应当在十五日内到机动车驾驶证核发地或者违法行为地公安机关交通管理部门<b>参加为期七日的道路交通安全法律、法规和相关知识学习。</b>机动车驾驶人参加学习后，车辆管理所应当在二十日内对其进行道路交通安全法律、法规和相关知识考试。考试合格的，记分予以清除，发还机动车驾驶证。考试不合格的，继续参加学习和考试。\r<br/>因此本题答案应为D。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074211, '正确', '错误', '', '', '1', '', '驾驶人违反交通运输管理法规发生重大事故致人重伤的可能判处3年以下徒刑或拘役。', '刑法第一百三十三条规定：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074212, '导向车道线', '方向引导线', '可变导向车道线', '单向行驶线', '3', 'http://images.juheapi.com/jztk/a1b1subject1/905.jpg', '图中圈内的锯齿状白色实线是什么标线？', '可变导向车道的意思就是进入该车道后有不止一个车道走向，如有的路口情况允许右转和直行（即右转和直行合并为一个车道），或者调头和左转合并为一个车道（一般用于左侧车道）。可变导向车道有着灵活性，不同于普通的车道指示线，普通的车道只要进入车道直行就是直行，左转就是左转。一般这样的车道设在交通比较复杂的路段。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074213, '导向车道线', '可变导向车道线', '方向引导线', '单向行驶线', '1', 'http://images.juheapi.com/jztk/a1b1subject1/906.jpg', '图中圈内白色实线是什么标线？', '将车流诱导到应行驶的路线的设施的总称。导向车道：大城市主要平交路口两端，引导车辆分道行驶的车道，其分界线用白色单实线，表明进入该车道的车辆不得变更车道或压线行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074214, '禁止临时停车', '禁止长时停车', '禁止停放车辆', '允许长时停车', '2', 'http://images.juheapi.com/jztk/a1b1subject1/907.jpg', '这个标志是何含义？', '禁止长时停车：禁止车辆长时停放，临时停放不受限制。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074215, '14个月', '12个月', '6个月', '10个月', '2', '', '道路交通违法行为累积记分周期是多长时间？', '《公安部令第123号》第五十五条：道路交通安全违法行为累积记分周期（即记分周期）为12个月，满分为12分，从机动车驾驶证初次领取之日起计算。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074216, '正确', '错误', '', '', '2', '', '驾驶机动车在道路上发生交通事故要立即将车移到路边。', '《道路交通安全法》第七十条：<b>在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；</b>造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074217, '右侧绕行', '单向通行', '注意危险', '左侧绕行', '1', 'http://images.juheapi.com/jztk/a1b1subject1/910.jpg', '这个标志是何含义？', '右侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，右侧绕行通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074218, '十字交叉路口', '环行交叉路口', 'T型交叉路口', 'Y型交叉路口', '1', 'http://images.juheapi.com/jztk/a1b1subject1/911.jpg', '这个标志是何含义？', '十字交叉路口：除了基本形十字路口外，还有部分变异的十字路口，如：五路交叉路口、变形十字路口、变形五路交叉路口等。五路以上的路口均按十字路口对待。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074219, '左转弯待转信号', '靠边停车信号', '右转弯信号', '减速慢行信号', '3', 'http://images.juheapi.com/jztk/a1b1subject1/912.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074220, '标志或标线', '《道路交通安全法》', '车辆说明书', '地方法规', '1', '', '在标志、标线齐全的高速公路上行车，应当按照什么规定的车道和车速行驶？', '根据规定，在标志、标线齐全的路段，应该按标志、标线行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074221, '正确', '错误', '', '', '1', '', '驾驶机动车在隧道、陡坡等特殊路段不得超车。', '《中华人民共和国道路交通安全法》第四十三条：<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br/>（一）前车正在左转弯、掉头、超车的；<br/>（二）与对面来车有会车可能的；<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；<br/>（四）行经铁路道口、交叉路口、窄桥、弯道、<b>陡坡、隧道、</b>人行横道、市区交通流量大的路段等没有超车条件的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074222, '停车让行', '单行路', '会车先行', '对向先行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/915.jpg', '这个标志是何含义？', '表示会车先行，此标志设在车道以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074223, '道路施工提示标线', '车行道纵向减速标线', '车行道横向减速标线', '车道变少提示标线', '2', 'http://images.juheapi.com/jztk/a1b1subject1/916.jpg', '路面上的菱形块虚线是何含义？', '<p><img src=http://nimg.cysq.com/yxlimg/20a43532.jpg>\r<br/>横向减速标线\r<br/><img src=http://nimg.cysq.com/yxlimg/2ec73df9.jpg>\r<br/>纵向减速标线</p>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074224, '非机动车引导线', '路口导向线', '车道连接线', '小型车转弯线', '2', 'http://images.juheapi.com/jztk/a1b1subject1/917.jpg', '图中圈内黄色虚线是什么标线？', '黄色虚线 划于路段中时，用以分隔同向行驶的交通流或作为行车安全距离识别线；划于路口时，用以引导车辆行进。\r<br/>例如：当我方车道上的车辆与对面道路上的车同时左转弯时，一律需要在线内行驶，保证有足够的安全距离。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074225, '禁止鸣高音喇叭', '禁止鸣低音喇叭', '应当鸣喇叭', '禁止鸣喇叭', '3', 'http://images.juheapi.com/jztk/a1b1subject1/918.jpg', '这个标志是何含义？', '表示机动车行至该标志处必须鸣喇叭。此标志设在公路的急转弯处、陡坡等视线不良路段的起点。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074226, '地点距离', '行驶路线', '终点地名', '行驶方向', '1', 'http://images.juheapi.com/jztk/a1b1subject1/919.jpg', '这个标志是何含义？', '你看那个地点名和KM公里多明显，所以本题的答案是地点距离。如果有箭头的一半是行驶方向。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074227, '人行横道预告', '交叉路口预告', '减速让行预告', '停车让行预告', '1', 'http://images.juheapi.com/jztk/a1b1subject1/920.jpg', '这个地面标记是什么标线？', '这种标线在到达人行横道前的道路30-50米处设置，它的作用是用来提示驾驶员，前方已接近人行横道，应减速慢行，并须注意行人横过马路。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074228, '正确', '错误', '', '', '1', '', '车辆转弯时应沿道路右侧行驶，不要侵占对方的车道，做到“左转转大弯，右转转小弯”。', '由于<b>我国规定机动车要靠右行驶，无论在十字路口，弯路都是各行其路，所以左转弧度大，右转弧度就小。</b>右转转小弯左转转大弯，就能最大程度避免与对方来车发生碰撞。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074229, '左转弯待转信号', '靠边停车信号', '减速慢行信号', '左转弯信号', '4', 'http://images.juheapi.com/jztk/a1b1subject1/922.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074230, '乡道编号', '县道编号', '省道编号', '国道编号', '4', 'http://images.juheapi.com/jztk/a1b1subject1/923.jpg', '这个标志是何含义？', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底的，国道是红底的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074231, '正确', '错误', '', '', '1', '', '驾驶人记分没有达到满分，有罚款尚未缴纳的，记分转入下一记分周期。', '《中华人民共和国道路交通安全法实施条例》第二十四条：机动车驾驶人在一个记分周期内记分未达到12分，所处罚款已经缴纳的，记分予以清除；记分虽未达到12分，但尚有罚款未缴纳的，记分转入下一记分周期。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074232, '直行和向左转弯', '直行和向右转弯', '禁止直行和向右转弯', '只准向左和向右转弯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/925.jpg', '这个标志是何含义？', '直行和向右转弯：表示只准一切车辆直行和向右转弯。此标志设在车辆必须直行和向右转弯）的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074233, '正确', '错误', '', '', '1', '', '距离宽度不足4米的窄路50米以内的路段不能停车。', '根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br/>（1）在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；<br/>（2）交叉路口、铁路道口、急转弯、<b>宽度不足4米的窄路、</b>桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；<br/>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；<br/>（4）车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行；<br/>（5）路边停车应当紧靠道路右侧，机动车驾驶人不得离车，上下人员或者装卸物品后，立即驶离；<br/>（6）城市公共汽车不得在站点以外的路段停车上下乘客。<br/>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074234, '禁止变道', '禁止左转', '禁止直行', '禁止掉头', '4', 'http://images.juheapi.com/jztk/a1b1subject1/927.jpg', '这个标志是何含义？', '禁止掉头：表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074235, '正确', '错误', '', '', '1', '', '在道路上发生交通事故造成人身伤亡时，要立即抢救受伤人员并迅速报警。', '根据《中华人民共和国道路交通安全法》第七十条的规定：<b>在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。</b>因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074236, '高速公路左侧出口预告', '高速公路目的地预告', '高速公路右侧出口预告', '高速公路下一出口预告', '4', 'http://images.juheapi.com/jztk/a1b1subject1/929.jpg', '这个标志是何含义？', '高速公路下一出口预告：预告下一出口可到达地点。当互通式立体交叉间距大于或等于3 km、小于5 km时，应设置下一出口预告标志，预告下一出口的信息和距离。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074237, '60日内', '30日内', '90日内', '6个月内', '3', '', '驾驶人在驾驶证有效期满前多长时间申请换证？', '《公安部令第123号》第四十八条：机动车驾驶人应当于机动车驾驶证有效期满前九十日内，向机动车驾驶证核发地车辆管理所申请换证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074238, '靠右侧加速行驶', '尽量礼让对方先行', '不侵占对方道路，正常行驶', '示意对方停车让行', '2', '', '会车中遇到对方来车行进有困难需借道时，应怎样做？', '如果遇到对方来车行进有困难需借道，请驾驶员尽量礼让对方先行，文明行车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074239, '正确', '错误', '', '', '1', '', '驾驶机动车上道路行驶,不允许超过限速标志标明的最高时速。', '这个没问题，按照标志行驶不会错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074240, '禁止掉头', '禁止左转', '禁止右转', '禁止直行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/933.jpg', '路面上的黄色标记是何含义？', '一个X加上一个右转，加一起就是禁止右转，爆弱了', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074241, '正确', '错误', '', '', '2', '', '驾驶机动车时可以向道路上抛撒物品。', '《实施条例》第六十二条，驾驶机动车不得有下列行为：\r<br/>(一)在车门、车厢没有关好时行车；\r<br/>(二)在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品；\r<br/>(三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为；\r<br/>(四)下陡坡时熄火或者空挡滑行；\r<br/><b>(五)向道路上抛撒物品；</b>\r<br/>(六)驾驶摩托车手离车把或者在车把上悬挂物品；\r<br/>(七)连续驾驶机动车超过4小时未停车休息或者停车休息时间少于20分钟；\r<br/>(八)在禁止鸣喇叭的区域或者路段鸣喇叭。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074242, '正确', '错误', '', '', '2', '', '浓雾天气能见度低，开启远光灯会提高能见度。', '不能开启远光灯，因浓雾会反射灯光。应开启雾灯。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074243, '左转弯待转信号', '靠边停车信号', '左转弯信号', '右转弯信号', '3', 'http://images.juheapi.com/jztk/a1b1subject1/936.jpg', '这一组交通警察手势是什么信号？', '手势题详解：http://www.jszks.com/onepage/16203.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074244, '进入路口等待', '左转弯行驶', '加速直行通过', '不得越过停止线', '4', 'http://images.juheapi.com/jztk/a1b1subject1/937.jpg', '驾驶机动车在路口直行遇到这种信号灯应该怎样行驶？', '亮红灯时，车辆应该在停止线内停车等待。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074245, '实习期驾驶人', '取得驾驶证的人', '驾驶证被吊销的人', '驾驶证记分达到6分的人', '3', '', '驾驶人将机动车交由什么样的人驾驶的，交通警察可依法扣留机动车驾驶证？', '六种情形下可以扣留驾驶证 新法规定，有下列情形之一的，可以扣留机动车驾驶证： (一)饮酒、醉酒后驾驶机动车的；<b>(二)机动车驾驶人将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的；</b> (三)机动车行驶超过规定时速百分之五十的； (四)驾驶拼装或者已达到报废标准的机动车的； (五)发生重大交通事故，构成犯罪的； (六)在一个记分周期内累积记分达到12分的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074246, '实体标记', '突起标记', '立面标记', '减速标记', '3', 'http://images.juheapi.com/jztk/a1b1subject1/939.jpg', '这种黄黑相间的倾斜线条是什么标记？', '《道路交通标志和标线》（GB5768-2009）<br/>第6.6条对于立面标记的规定：“立面标记用以提醒驾驶人注意，在行车道或近旁有高出路面的构造物。可设在跨线桥、渡槽等的墩柱立面、隧道洞口侧墙端面及其他障碍物立面上，一般应涂至2.5m以上的高度。标线为黄黑相间的倾斜线条，倾角为45°，线宽均为15cm。设置时应把向下倾斜的一边朝向车行道。”', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074247, '左转让行', '直行单行路', '向右单行路', '向左单行路', '4', 'http://images.juheapi.com/jztk/a1b1subject1/940.jpg', '这个标志是何含义？', '表示一切车辆向左单向行驶。此标志设在单行路的路口和入口处的适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074248, '驾驶机动车违反道路交通信号灯', '未取得校车驾驶资格驾驶校车', '驾驶与准驾车型不符的机动车', '饮酒后驾驶机动车', '1', '', '有下列哪种违法行为的机动车驾驶人将被一次记6分？', '《公安部令第123号》规定，驾驶机动车违反道路交通信号灯通行的，一次记6分 。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074249, '直行和左转车道', '直行和辅路出口车道', '直行和右转合用车道', '分向行驶车道', '3', 'http://images.juheapi.com/jztk/a1b1subject1/942.jpg', '这个标志是何含义？', '表示只准一切车辆直行和向右转弯。此标志设在车辆必须直行和向右转弯）的路口以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074250, '加速左转', '禁止右转', '路口警示', '加速直行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/943.jpg', '前方路口这种信号灯亮表示什么意思？', '黄灯为警示灯，所以是路口警示。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074251, '采取紧急制动', '使用驻车制动', '轻踏制动踏板', '迅速踏下制动踏板', '3', '', '前轮胎爆裂已出现转向时，驾驶人不要过度矫正，应在控制住方向的情况下，应怎样做，使车辆缓慢减速？', '爆胎时不能迅速减速，需慢慢减速。A踩脚刹B拉手刹D踩脚刹都属于迅速减速，此时车子容易发生侧滑。所以要轻踩刹车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074252, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/945.jpg', '遇到这种情况时，要加速从红车前变更车道。', '图中车辆按照道路提示需要向左变道，所以需要减速或者停车等待左侧车道直行车辆通行完毕后变道，而不可能是加速。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074253, '正确', '错误', '', '', '1', '', '道路交通事故中，机动车无号牌、检验合格标志、保险标志时，要保护现场并立即报警。', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，当事人应当保护现场并立即报警： 　　<br/>（一）造成人员死亡、受伤的； 　　<br/>（二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　<br/>（三）<b>机动车无号牌、无检验合格标志、无保险标志的；</b> 　　<br/>（四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的； 　　<br/>（五）碰撞建筑物、公共设施或者其他设施的；　　<br/>（六）驾驶人无有效机动车驾驶证的； 　　<br/>（七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； 　　<br/>（八）当事人不能自行移动车辆的。 　　<br/>发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074254, '露天停车场', '紧急停车带', '停车位', '错车道', '4', 'http://images.juheapi.com/jztk/a1b1subject1/947.jpg', '这个标志是何含义？', '错车道指的是在单车道道路上，可通视的一定距离内，供车辆交错避让用的一段加宽车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074255, '60公里/小时', '90公里/小时', '100公里/小时', '120公里/小时', '4', 'http://images.juheapi.com/jztk/a1b1subject1/948.jpg', '在这段高速公路上行驶的最高车速是多少？', '红圈白底限制最高速度，此路段最高车速是120公里每小时。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074256, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/949.jpg', '遇到这种情形时要停车避让行人。', '根据《道路交通安全法》第四十七条，机动车行经没有交通信号的道路时，遇行人横过道路，应当避让。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074257, '省道编号', '国道编号', '县道编号', '乡道编号', '3', 'http://images.juheapi.com/jztk/a1b1subject1/950.jpg', '这个标志是何含义？', '乡道是白底Y字开头的，县道是白底X开头的，省道是黄底的，国道是红底的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074258, '主要街道', '单向行驶路段', '交通流量大的路段', '单向两条行车道', '3', '', '驾驶机动车行经市区下列哪种道路时不得超车？', '《中华人民共和国道路交通安全法》第四十三条：<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br/>（一）前车正在左转弯、掉头、超车的；<br/>（二）与对面来车有会车可能的；<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；<br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、<b>市区交通流量大的路段</b>等没有超车条件的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074259, '指示禁行', '指示车道', '指示合流', '指示直行', '4', 'http://images.juheapi.com/jztk/a1b1subject1/952.jpg', '这个导向箭头是何含义？', '亲，直行哦~~', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074260, '正确', '错误', '', '', '2', '', '立交桥上一般都是单向行驶，车辆不必减速行驶。', '立交桥上车多，弯多，路窄，考虑到安全问题，还是要减速行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074261, '正确', '错误', '', '', '1', '', '机动车停稳前不能开车门和上下人员。', '安全第一，一定要在车子停稳之后再上下车！', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074262, '正确', '错误', '', '', '1', '', '交通信号包括交通信号灯、交通标志、交通标线和交通警察的指挥。', '全国统一的交通信号分为：交通信号灯、交通标志、交通标线和交通警察的指挥。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074263, '正确', '错误', '', '', '2', '', '饮酒后只要不影响驾驶操作可以短距离驾驶机动车。', '《道路交通安全法》第九十一条：饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。对自己对他人负责，只要饮酒就不开车。本题题干描述有误，因此本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074264, '7年以上', '3年以下', '3年以上7年以下', '10年以上', '3', '', '驾驶人违反交通运输管理法规发生重大事故致人死亡且逃逸的，处多少年有期徒刑？', '《中华人民共和国刑法》第一百三十三条：\r<br/>1、违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；\r<br/>2、<b>交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；</b>\r<br/>3、因逃逸致人死亡的，处七年以上有期徒刑。\r<br/>\r<br/>题干中所说“发生重大事故致人死亡且逃逸的”，可理解为：先发生事故致人死亡，然后逃逸（先死后逃）。结合法规，题干所描述的情况属于情节特别恶劣的交通肇事行为，并且在肇事后逃逸，因此应该处“3年以上7年以下有期徒刑”，所以选C。\r<br/>\r<br/>【拓展延伸】\r<br/>如果肇事司机逃逸前，因交通事故已致人死亡了，死因可归结到交通事故上；\r<br/>但若肇事司机逃逸后，人再死亡，死因则变成交通事故+因逃逸致其救治不及时，属于罪加一等，因此处罚更加严重。\r<br/>\r<br/>有个口诀可以教给大家：\r<br/>先死后逃，3年以上7年以下；\r<br/>先逃后死，7年以上。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074265, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/958.jpg', '行经这种交通标线的路段要加速行驶。', '要减速通过，白色的菱形标志就是提醒你前方接近人行横道，所以要减速防止撞到行人。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074267, '距离路边超过30厘米', '在有禁停标线路段停车', '距离加油站不到30米', '停车占用非机动车道', '3', 'http://images.juheapi.com/jztk/a1b1subject1/960.jpg', '这样临时停放红色轿车有什么违法行为？', '图为加油站，规定加油站30米内不得停车。公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的车辆以外的车辆，不得停车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074268, '正确', '错误', '', '', '1', '', '距离桥梁、陡坡、隧道50米以内的路段不能停车。', '《中华人民共和国道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定：交叉路口、铁路道口、急弯路、宽度不足４米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074269, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/962.jpg', '在这种情况的铁路道口要加速通过。', '前面是火车道，道口红灯已经亮起，表明即将有火车通过。这个时候应当停车等待，等火车过去了再通过。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074270, '沿左侧车道掉头', '该路口不能掉头', '选择中间车道掉头', '在路口内掉头', '2', 'http://images.juheapi.com/jztk/a1b1subject1/963.jpg', '遇到这种情况的路口，以下做法正确的是什么？', '此处有禁止掉头的标志，所以是不可以掉头的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074271, '正确', '错误', '', '', '1', '', '对未放置检验合格标志上道路行驶的车辆，交通警察可依法予以扣留。', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，<b>未放置检验合格标志、</b>保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074272, '记分达到10分', '记分达到6分', '驾驶证丢失、损毁', '驾驶证接近有效期', '3', '', '驾驶人出现下列哪种情况，不得驾驶机动车？', '驾驶证是证明驾驶员驾驶资格的重要凭证，<b>驾驶证损毁，丢失，就相当于是无证驾驶，所以在此期间驾驶员是不能驾驶车辆的。</b>驾驶证接近有效期，应该去交通管理所去换证。驾驶证累积记分达到12分的，应该重新进行理论考试，考试通过，才能重新领回驾照。\r<br/>因此本题答案应为A。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074273, '正确', '错误', '', '', '1', '', '发生交通事故造成人员受伤时，要保护现场并立即报警。', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，<b>当事人应当保护现场并立即报警：</b> 　　\r<br/><b>（一）造成人员死亡、受伤的； </b>　　\r<br/>（二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　\r<br/>（三）机动车无号牌、无检验合格标志、无保险标志的； 　　\r<br/>（四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的； 　　\r<br/>（五）碰撞建筑物、公共设施或者其他设施的；　　\r<br/>（六）驾驶人无有效机动车驾驶证的； 　　\r<br/>（七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； 　　\r<br/>（八）当事人不能自行移动车辆的。 　　\r<br/>发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074274, '事故易发路段', '施工路段', '减速慢行路段', '拥堵路段', '1', 'http://images.juheapi.com/jztk/a1b1subject1/967.jpg', '这个标志是何含义？', '事故易发路段：此标志设在交通事故易发路段以前适当位置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074275, '正确', '错误', '', '', '1', '', '打开机动车车门时，不得妨碍其他车辆和行人通行。', '《道路交通安全法实施条例》第七十七条：乘坐机动车应当遵守下列规定：<br/>(一)不得在机动车道上拦乘机动车;<br/>(二)在机动车道上不得从机动车左侧上下车;<br/><b>(三)开关车门不得妨碍其他车辆和行人通行;</b><br/>(四)机动车行驶中，不得干扰驾驶，不得将身体任何部分伸出车外，不得跳车;<br/>(五)乘坐两轮摩托车应当正向骑坐。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074276, '隧道减速', '隧道开远光灯', '隧道开灯', '隧道开示宽灯', '3', 'http://images.juheapi.com/jztk/a1b1subject1/969.jpg', '这个标志是何含义？', '这个应该能看懂吧？很明显那个小车开灯的效果。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074277, '正确', '错误', '', '', '1', '', '驾驶机动车遇到漫水桥时要察明水情确认安全后再低速通过。', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074278, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/971.jpg', '遇到这种情况不能超车。', '前车正在超车时，后面的车此时超车的话，肯定会导致两车相撞的，所以应该放弃超车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074279, '紧靠路边行驶', '靠路中心行驶', '减速靠右行驶', '借非机动车道行驶', '3', '', '驾驶机动车在没有中心线的道路上遇相对方向来车时怎样行驶？', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，减速靠右行驶，并与其他车辆、行人保持必要的安全距离。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074280, '已开启后雾灯', '已开启前照灯近光', '已开启前照灯远光', '已开启前雾灯', '2', 'http://images.juheapi.com/jztk/a1b1subject1/973.jpeg', '机动车仪表板上（如图所示）亮时表示什么？', '近光灯的灯光走向是朝下的。远光灯的灯光走向是直的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074281, '正确', '错误', '', '', '1', '', '驾驶车辆时，长时间左臂搭在车门窗上，或者长时间右手抓住变速器操纵杆，是一种驾驶陋习。', '长时间左臂搭在车门窗上，或者长时间右手抓住变速器操纵杆，如果出现紧急情况，驾驶员很难做出良好及时的应对，属于驾驶陋习。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074282, '正确', '错误', '', '', '2', '', '车辆在下坡行驶时，可充分利用空挡滑行。', '下坡时，绝对不能熄火或空档滑行。<b>如果下坡空档滑行，只靠刹车来控制车速，遇到连续下坡的时候可能会造成刹车负担过重，刹车片过热而失灵。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074283, '提醒车辆驾驶人前方道路沿水库、湖泊、河流', '提醒车辆驾驶人前方有向上的陡坡路段', '.提醒车辆驾驶人前方有两个及以上的连续上坡路段', '提醒车辆驾驶人前方有向下的陡坡路段', '2', 'http://images.juheapi.com/jztk/a1b1subject1/976.jpg', '这个标志是何含义？', '上陡坡：用以提醒车辆驾驶人小心驾驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074284, '正确', '错误', '', '', '1', '', '车辆进入山区道路后，要特别注意“连续转弯”标志，并主动避让车辆及行人，适时减速和提前鸣喇叭。', '山区本来就是有山的地势，自然就会有坡度，以及拐弯，连续的弯路，容易影响行车的视线，所以要注意“连续转弯”的标志，以防在转弯处突然出现对向来车，为了安全，当然要适时减速和提前鸣喇叭，以提醒车辆与行人。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074285, '大型客货车', '执行任务的消防车', '公共汽车', '出租汽车', '2', '', '同车道行驶的车辆前方遇到下列哪种车辆不得超车？', '亲，人命关天，您就别超了。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074286, '正确', '错误', '', '', '1', '', '社会车辆距离消防栓或者消防队(站)门前30米以内的路段不能停车。', '根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br/>（1）在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；<br/>（2）交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；<br/><b>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；</b><br/>（4）车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行；<br/>（5）路边停车应当紧靠道路右侧，机动车驾驶人不得离车，上下人员或者装卸物品后，立即驶离；<br/>（6）城市公共汽车不得在站点以外的路段停车上下乘客。<br/>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074287, '正确', '错误', '', '', '1', '', '在车门、车厢没有关好时不要驾驶机动车起步。', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在车门、车厢没有关好时行车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074288, '正确', '错误', '', '', '2', '', '机动车发生财产损失交通事故，对应当自行撤离现场而未撤离的，交通警察不可以责令当事人撤离现场。', '《道路交通事故处理程序规定》第十三条：<b>对应当自行撤离现场而未撤离的，交通警察应当责令当事人撤离现场；</b>造成交通堵塞的，对驾驶人处以200元罚款；驾驶人有其他道路交通安全违法行为的，依法一并处罚。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074289, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/982.jpg', '驾驶机动车在路口遇到这种信号灯亮时，不能右转弯。', '只有 红 黄 绿三色且是圆形（如图）交通信号灯的路口，任何情况下都能右转，如果是箭头信号灯的话按箭头方向行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074290, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/983.jpg', '驾驶机动车在前方路口不能右转弯。', '如果路口只有圆形的红黄绿灯，那么不管是红灯亮还是绿灯亮都可以右转弯，如果路口是箭头形状，那么向右转的箭头没亮绿灯就不能走了，黄灯也不能走。所以，这题注意看灯的形状。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074291, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/984.jpg', '驾驶机动车在这种信号灯亮的路口，可以右转弯。', '要看路口的信号灯。如果路口只有红、黄、绿圆形三色的信号灯时，就可以直接右转弯。如果路口信号灯有箭头指示灯时，需要根据箭头灯的指示。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074292, '正确', '错误', '', '', '2', '', '机动车在高速公路上发生故障时，在来车方向50至100米处设置警告标志。', '《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；警告标志应当设置在故障车来车方向150米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074293, '15公里/小时', '20公里/小时', '40公里/小时', '30公里/小时', '4', '', '驾驶机动车在泥泞道路行驶时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br/>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br/>（二）掉头、转弯、下陡坡时；　　 <br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br/>（四）在冰雪、<b>泥泞</b>的道路上行驶时；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074294, '高架路', '交叉路口', '中心街道', '环城高速', '2', '', '驾驶机动车行经下列哪种路段时不得超车？', '《中华人民共和国道路交通安全法》第四十三条：\r<br/>同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：\r<br/>（一）前车正在左转弯、掉头、超车的；\r<br/>（二）与对面来车有会车可能的；\r<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；\r<br/>（四）行经铁路道口、<b>交叉路口、</b>窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074295, '正确', '错误', '', '', '2', '', '风、雨、雪、雾等复杂气象条件，遇前车速度较低时，应开启前照灯，连续鸣喇叭迅速超越。', '复杂天气下超车十分危险，为了道路安全最好不要超车。应该选择合适地点减速停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074297, '正确', '错误', '', '', '1', '', '夜间驾驶人对事物的观察能力明显比白天差，视距变短。', '白天开车的时候由于光线比较充足，所以观察事物的范围比较广，观察事物的视线距离相对也就比较大，<b>而在晚上受到光线暗的局限，相对观察事物的距离也就受到局限，所以视距比较短。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074298, '正确', '错误', '', '', '1', '', '夜间会车时，若对方车辆不关闭远光灯，可变换灯光提示对向车辆，同时减速靠右侧行驶或停车。', '夜间安全驾驶知识中第三条就有：“夜间会车应当在距对方来车150米以外改用近光灯，若对方车辆不关闭远光灯，可连续变换灯光提示对向车辆。当遇对面来车仍不关闭远光灯时，应及时减速靠右侧行驶或停车让行。”', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074299, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/992.jpg', '在这种情况的交叉路口转弯要让直行车先行。', '三个先行原则：转弯的机动车让直行的车辆先行，右方道路来车先行，右转弯车让左转弯车先行。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074300, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/993.jpg', '行至这种情况的铁路道口要停车观察。', '在无人看守的铁道路口，要停车观察，确定安全后，才可以通过！', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074301, '正确', '错误', '', '', '1', '', '通过山区危险路段，尤其是通过经常发生塌方、泥石流的山区地段，应谨慎驾驶，避免停车。', '经常发生塌方、泥石流的都属于危险地段，所以能尽快离开就尽快离开，避免停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074302, '保持原状态行驶', '加速行驶', '降速靠右让路', '迅速停车让行', '3', '', '在没有中心线的道路上发现后车发出超车信号时，如果条件许可如何行驶？', '如果迅速停车的话，会很容易追尾，降速靠右让路是因为超车规定要在左侧超越。安全文明驾驶原则，后车条件许可超车，前车那就减速靠右行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074303, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/996.jpg', '在交叉路口遇到这种情况享有优先通行权。', '右转让左转，没有问题。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074304, '正确', '错误', '', '', '1', '', '在冰雪路面上行车，必须降低车速、加大安全距离。', '冰雪路面行驶时，路面滑摩擦系数小，制动距离比平时要大。因此要考虑到紧急状况。如果出现紧急情况，需要有足够的制动距离。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074305, '正确', '错误', '', '', '2', '', '科目三道路驾驶技能和安全文明驾驶常识考试满分分别为100分，成绩分别达到80和90分的为合格。', '各科目考试的合格标准为： （一）科目一考试满分为100分，成绩达到90分的为合格；<b>（二）科目三道路驾驶技能和安全文明驾驶常识考试满分分别为100分，成绩分别达到90分的为合格。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074306, '正确', '错误', '', '', '1', '', '客车遇险后，应设法开启车门、安全门或紧急出口，迅速疏散车上乘客。', '', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074307, '从左侧跨越实线超越', '从两侧随意超越', '依次排队等候', '从右侧借道超越', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1000.jpg', '遇有这种排队等候的情形怎么做？', '前方拥堵，请耐心等候。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074308, '减速慢行，必要时停车避让', '长鸣喇叭催促', '迅速从一侧通过', '加速绕行', '1', '', '行车中遇儿童时，应怎样做？', '儿童的行动变化性非常大，为了保护儿童安全，一定要减速慢行，必要时停车避让。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074309, '正确', '错误', '', '', '2', '', '机动车上路行驶时，前排乘车人可不系安全带。', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074310, '正确', '错误', '', '', '1', '', '掉头过程中，应严格控制车速，仔细观察道路前后方情况，确认安全后方可前进或倒车。', '掉头过程中，要控制好车速，仔细观察周围情况。这样能最大程度的避免掉头中发生事故。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074311, '正确', '错误', '', '', '2', '', '有吸食、注射毒品后驾驶驾车行为的机动车驾驶人，不会被注销驾驶证。', '被查获有吸食、注射毒品后驾驶机动车行为，正在执行社区戒毒、强制隔离戒毒、社区康复措施，或者长期服用依赖性精神药品成瘾尚未戒除的，车辆管理所应当注销其机动车驾驶证。三年内有吸食、注射毒品行为，或者解除强制隔离戒毒措施未满三年，或者长期服用依赖性精神药品成瘾尚未戒除的，不得申请机动车驾驶证。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074312, '没按规定握转向盘', '座椅角度不对', '没系安全带', '驾驶姿势不正确', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1005.jpg', '这位驾驶人违反法律规定的行为是什么？', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。很明显，图中司机没有系安全带，属于违法行为。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074313, '电器设备易受潮短路', '能见度降低，视野模糊', '行驶阻力增大', '制动性能差，方向易跑偏', '4', '', '冰雪道路对安全行车的主要影响是什么？', '冰雪道路，因汽车轮胎与路面的摩擦系数减小、附着力大大降低，汽车驱动轮很容易打滑或空转，尤其是上坡、起步、停车时还会出现后溜车的现象；车辆在行驶中如果突然加速或减速，很容易造成侧滑及方向跑偏现象；遇情况紧急制动时，制动距离会大大延长。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074314, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1007.jpg', '这个标志的含义是提醒车辆驾驶人前方是无人看守铁路道口。', '无人看守铁路道口：用以警告车辆驾驶人注意慢行或及时停车。设在无人看守铁路道口以前适当位置。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074315, '40公里/小时', '30公里/小时', '50公里/小时', '70公里/小时', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1008.jpg', '在这个弯道上行驶时的最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，<b>最高行驶速度不得超过每小时30公里，</b>其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br/>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br/>（二）<b>掉头、转弯、下陡坡时；</b>　　 <br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br/>（四）在冰雪、泥泞的道路上行驶时；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074316, '正确', '错误', '', '', '1', '', '连续降雨天气，山区公路可能会出现路肩疏松和堤坡坍塌现象，行车时应选择道路中间坚实的路面，避免靠近路边行驶。', '山区公路连续降雨，如果开外侧，容易掉下悬崖，如果开内侧容易被塌方和落石砸到。<b>选择在道路中间行驶，不仅能最大程度避免意外情况的发生，当对面有车过来时，驾驶员也能第一时间观察到做出合适的应对。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074317, '100以上200元以下', '200元以上500元以下', '500元以上1000元以下', '1000元以上', '2', '', '公路客运车辆载客超过额定乘员，但没有超过额定成员20%的，处多少罚款？', '客运车辆载客超载，但是没有超过额定成员20%，罚的不算太狠，所以是200元上，500元以下；而超载超过20%的就要重罚。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074318, '50公里/小时', '40公里/小时', '30公里/小时', '60公里/小时', '3', '', '驾驶机动车通过窄路、窄桥时，最高速度不能超过多少？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br/>（一）进出非机动车道，通过铁路道口、急弯路、<b>窄路、窄桥时；</b>　　 <br/>（二）掉头、转弯、下陡坡时；　　 <br/>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br/>（四）在冰雪、泥泞的道路上行驶时；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074319, '违章行为', '违规行为', '违法行为', '犯罪行为', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1012.jpg', '驾驶这种机动车上路行驶属于什么行为？', '《道路交通安全法》第十一条规定：机动车号牌应当按照规定悬挂并保持清晰、完整，不得故意遮挡、污损。\r<br/>图中机动车的车号牌被故意遮挡住了，违反了《道路交通安全法》的规定，因此属于违法行为。本题答案应为C。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074320, '依次排队行驶', '占对向车道超越', '从右侧借道超越', '从两侧随意超越', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1013.jpg', '遇到前方车辆缓慢行驶时怎样行驶？', '图中明显看出前方车辆较为拥堵，应该依次排队行驶，此时超车的话，容易造成交通事故。其次，左边是黄实线，不能越线超车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074321, '急剧制动低速通过', '靠弯道外侧行驶', '充分减速并靠右侧行驶', '借对向车道行驶', '3', '', '驾驶车辆行至道路急转弯处，应怎样做？', '安全驾驶，减速慢行。急转弯处切记减速靠右侧慢行，否则不仅容易侧翻，还容易因为视线死角与对向来车发生碰撞。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074322, '正确', '错误', '', '', '2', '', '在路口遇有交通信号灯和交通警察指挥不一致时，按照交通信号灯通行。', '《道路交通安全法》第三十八条：车辆、行人应当按照交通信号通行；<b>遇有交通警察现场指挥时，应当按照交通警察的指挥通行；</b>在没有交通信号的道路上，应当在确保安全、畅通的原则下通行。\r<br/>题目描述有误，本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074323, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1016.jpg', '这个标志的含义是提醒车辆驾驶人前方是傍山险路路段。', '<p><img src=http://tp.mnks.cn/tp_143.jpg>\r<br/>注意傍山险路\r<br/><img src=http://tp.mnks.cn/tp_140.jpg>\r<br/>注意落石</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074324, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1017.jpeg', '机动车仪表板上（如图所示）亮时，提醒发动机冷却液可能不足。', '此标志亮起说明发动机水温过高，可能是冷却液不足引起的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074325, '确认安全后通过', '右转弯加速通过', '加速直行通过', '左转弯加速通过', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1018.jpg', '遇到这种情况的路口怎样通过？', '这个路口只有一个信号灯，也就说只有黄灯，而且一直在闪烁，表示警示，提醒过往车辆注意瞭望。并且当前路口有其他车辆转弯，有行人接近，必须确认安全后通过。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074326, '正确', '错误', '', '', '1', '', '在高速公路变更车道时，应提前开启转向灯，观察情况，确认安全后，驶入需要变更的车道。', '变更车道时，需先开启转向灯提示后车，其次要观察路况，确认安全后再变更车道。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074327, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1020.jpg', '在这种情况下可加速通过交叉路口。', '当黄灯亮起，车身在停止线里边的，应停车等待。而且，通过交叉路口一般都要减速，确认安全再通过。\r<br/>题目描述有误，本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074328, '停车等待', '遵守交通信号灯', '靠右侧直行', '可以向右转弯', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1021.jpg', '驾驶机动车在路口遇到这种情况如何行驶？', '<p><img src=http://nimg.cysq.com/yxlimg/8298e043.gif>\r<br/>停止</p>\r<br/>1、首先，在有交警指挥的路口，应首先按照交警指挥进行通行。\r<br/>2、其次，图中交警的手势为停止信号，驾驶员应该按照交警手势停车等待。\r<br/>因此本题答案应为D。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074329, '准许通行', '提醒注意', '路口警示', '禁止通行', '4', 'http://images.juheapi.com/jztk/a1b1subject1/1022.jpg', '前方路口这种信号灯亮表示什么意思？', '红灯停。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074330, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1023.jpg', '在这种情况下可以借右侧公交车道超车。', '超车只能从左侧超车，并且车道内已经注明“公交车专用车道”，则此车道为公交车专用，其他车辆一般情况下不准占用。仅当发生事故、正常道路封闭有标示注明允许借用或者交警指示等特殊情况允许借道行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074331, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1024.jpg', '在路口这个位置时可以加速通过路口。', '这是红灯信号，应该禁止通过，应停车等待绿灯，再通过。\r<br/>题目描述有误，本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074332, '开启转向灯迅速向左转向', '进入左侧车道时适当减速', '不能影响其他车辆正常行驶', '尽快加速进入左侧车道', '3', '', '机动车在道路上变更车道需要注意什么？', '《道路交通安全法实施条例》第四十四条：在道路同方向划有2条以上机动车道的，变更车道的机动车不得影响相关车道内行驶的机动车的正常行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074333, '降低轮胎气压', '定期检查轮胎', '更换有裂纹或有很深损伤的轮胎', '及时清理轮胎沟槽里的异物', '1', '', '避免爆胎的错误的做法是什么？', '当轮胎气压降低，轮胎与地面的摩擦成倍增加，胎温急剧升高，轮胎变软，强度急剧下降。这种情况下，如果车辆高速行驶，就可能导致爆胎。而如果车辆在低速行驶，也会伤胎，而且潜伏期长，隐蔽性高，危害性更大，会埋下日后爆胎的隐患。因此，降低轮胎气压不仅不能避免爆胎，还会抬高爆胎的风险。根据题意，A符合要求，是避免爆胎的错误做法，答案选A。\r<br/>\r<br/>【拓展延伸】\r<br/>想要避免爆胎，一定要定期检查轮胎，及时清理轮胎沟槽内的异物，及时更换有裂纹或有很深损伤的轮胎。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074334, '向右转弯', '停车等待', '向左转弯', '直行通过', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1027.jpg', '有这种信号灯的路口允许机动车如何行驶？', '图中是箭头指示的交通信号灯，只有右转的灯是绿灯，并且我方车辆在右转车道上，所以只能向右转弯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074335, '70公里/小时', '50公里/小时', '40公里/小时', '30公里/小时', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1028.jpg', '在这条公路上行驶的最高速度不能超过多少？', '根据《中华人民共和国道路交通安全法实施条例》第四十五条：……在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：　　(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；　　(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。图为没有道路中心线的公路，所以最高时速为40公里每小时。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074336, '快速车道', '慢速车道', '应急车道', '专用车道', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1029.jpg', '这段道路红车所在车道是什么车道？', '《道路交通安全法实施条例》第四十四条：在道路同方向划有2条以上机动车道的，左侧为快速车道，右侧为慢速车道。红色车在最左侧，所以是在快速车道。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074337, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1030.jpg', '在这段路的最高时速为每小时50公里。', '白底红圈表示最高限速，蓝色牌子表示最低限速。所以最高时速应该为80公里/小时。\r<br/>题目描述有误，因此本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074338, '正确', '错误', '', '', '1', '', '雾天行车多使用喇叭可引起对方注意；听到对方车辆鸣喇叭，也应鸣喇叭回应。', '雾天视线不佳，喇叭可当做一种信号和其他车辆交流，有利于安全行车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074339, '正确', '错误', '', '', '2', '', '驾驶人逃离火灾如果无法开启驾驶室门或车门时，应敲碎前挡风玻璃脱离。', '前挡风是夹胶的，易裂不易破，里面有夹层。为了保护驾驶员，设计的小车前挡风打烂也是不会碎的，要敲碎车窗玻璃逃生。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074340, '公交车', '私家车', '出租车', '公务用车', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1033.jpg', '道路上划设这种标线的车道内允许下列哪类车辆通行？', '请注意地面的字，公交专用车道。所以是只有公交车才能行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074341, '在道路中间通行', '在道路两边通行', '实行分道通行', '可随意通行', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1034.jpg', '驾驶机动车在这种道路上如何通行？', '《道路交通安全法》第三十六条：根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。<b>没有划分机动车道、非机动车道和人行道的，机动车在道路中间通行，非机动车和行人在道路两侧通行。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074342, '气压更低', '行驶阻力增大', '爆胎', '气压不稳', '3', '', '轮胎气压过低时，高速行驶轮胎会出现波浪变形温度升高而导致什么？', '轮胎气不足，会造成轮胎与地面的摩擦范围增大，温度升高从而爆胎。压力过低和过高都会导致爆胎。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074343, '正确', '错误', '', '', '2', '', '行车中前方遇自行车影响通行时，可鸣喇叭提示，加速绕行。', '本题错在加速绕行，鸣喇叭提示是对的。应当减速与其保持安全距离，道路宽度允许通过时谨慎通过。因此本题错了。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074344, '禁驶区', '网状线', '导流线', '中心圈', '4', 'http://images.juheapi.com/jztk/a1b1subject1/1037.jpg', '这个路面标记是什么标线？', '中心圈，设置在交叉路口中心的白色圆形或菱形区域，用以区分车辆大、小转弯，及交叉路口车辆左右转弯的指示，车辆不得压线行驶。机动车向左转弯时，必须紧靠中心圈小转弯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074345, '正确', '错误', '', '', '1', '', '交通信号灯由红灯、绿灯和黄灯组成。', '《公安部令第123号》第二十六条：交通信号灯由红灯、绿灯、黄灯组成。红灯表示禁止通行，绿灯表示准许通行，黄灯表示警示。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074346, '正确', '错误', '', '', '1', '', '客车遇险后，没有安全门的，可用车上配备的铁锤或者其他物品将车窗玻璃敲破，从窗口疏散乘客。', '', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074347, '正确', '错误', '', '', '2', '', '驾驶机动车在道路上向右变更车道可以不使用转向灯。', '只要是变更车道，就要使用相应的转向灯。\r<br/>《中华人民共和国道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯： \r<br/>（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯。 \r<br/><b>（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。</b>\r<br/>无论是向左变道还是向右变道，都应该打开转向灯。题目描述有误，因此本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074348, '正确', '错误', '', '', '1', '', '雪天行车中，在有车辙的路段应循车辙行驶。', '冰雪覆盖下，司机没法观察到路面情况是否安全。如果有车辙的路段证明有车通行过，说明是可以通行的安全路段，应循车辙行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074349, '正确', '错误', '', '', '1', '', '道路交通标线分为指示标线、警告标线、禁止标线。', '道路交通标志  道路交通标志和标线是用图案、符号、文字传递交通管理信息，用以管制及引导交通的一种安全管理设施。<b>《道路交通标志和标线》规定的交通标志分为七大类：</b>\r<br/>  (1)警告标志：警告车辆和行人注意危险地点的标志。\r<br/>  (2)禁令标志：禁止或限制车辆、行人交通行为的标志。\r<br/>  (3)指示标志：指示车辆、行人行进的标志。\r<br/>  (4)指路标志：传递道路方向、地点、距离的标志。\r<br/>  (5)旅游区标志：提供旅游景点方向、距离的标志。\r<br/>  (6)道路施工安全标志：通告道路施工区通行的标志。\r<br/>  (7)辅助标志：附设于主标志下起辅助说明使用的标志。\r<br/> <b> 规定的道路交通标线分为三大类：</b>\r<br/><b>  (1)指示标线</b>：指示车行道、行车方向、路面边缘、人行道等设施的标线。\r<br/>  <b>(2)禁止标线</b>：告示道路交通的遵行、禁止、限制等特殊规定，车辆驾驶人员及行人需要严格遵守的标线。\r<br/>  <b>(3)警告标线</b>：促使车辆驾驶人员及行人了解道路上的特殊情况，提高警觉，准备防范应变措施的标线。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074350, '正确', '错误', '', '', '1', '', '驾驶机动车发生财产损失交通事故，当事人对事实及成因无争议的可先撤离现场。', '《道路交通事故处理程序规定》第十三条：<b>机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。</b>车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。非机动车与非机动车或者行人发生财产损失事故，基本事实及成因清楚的，当事人应当先撤离现场，再协商处理损害赔偿事宜。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074351, '正确', '错误', '', '', '2', '', '驾驶机动车在沙尘天气条件下行车不用开启前照灯、示廓灯和后位灯。', '《道路交通安全法实施条例》第五十八条： 机动车在夜间没有路灯、照明不良或者<b>遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，</b>但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。\r<br/>题目描述有误，本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074352, '正确', '错误', '', '', '2', '', '夜间尾随前车行驶时，后车可以使用远光灯。', '《道路交通安全法实施条例》第五十八条：“机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，<b>但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。</b>机动车雾天行驶应当开启雾灯和危险报警闪光灯。”', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074353, '正确', '错误', '', '', '1', '', '车辆上坡行驶，要提前观察路况、坡道长度，及时减挡使车辆保持充足的动力。', '提前观察路况可以增加行车时的预见性，及时减档保持充足的动力才能顺利上坡。低档速度慢，但是动力足，才能保证不熄火，顺利上坡。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074354, '3分', '6分', '12分', '2分', '3', '', '连续驾驶大型载客汽车超过4小时未停车休息，按照规定将被公安机关交通管理部门给予一次记多少分？', '由于大型载客汽车出现事故会造成很多伤亡等严重问题，故惩罚力度大，超过4个小时未休息直接扣12分。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074355, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1048.jpg', '在这种环境里行车使用近光灯。', '注意观察题目图片，夜间驾驶机动车，机动车前方有人行横道，<b>路边有行人</b>。在这种情况下行车应该使用近光灯，不妨碍行人正常通行。题目描述正确，因此本题答案为对。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074356, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1049.jpg', '这个标志的含义是警告前方有两个相邻的反向转弯道路。', '<p><img src=http://tp.mnks.cn/tp_125.jpg>\r<br/>反向弯路\r<br/><img src=http://tp.mnks.cn/tp_126.jpg>\r<br/>连续弯路</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074357, '正确', '错误', '', '', '2', '', '已经达到报废标准的机动车经大修后可以上路行驶。', '《道路交通安全法》第一百条规定：驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废，处200元以上2000元以下罚款，并吊销机动车驾驶证。 \r<br/>已经达到报废标准的车，无论是否经过大修都不能重新开上路。本题描述有误。因此本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074358, '可以申请', '扣留期间可以临时申请', '暂扣期间可以临时申请', '不得申请补发', '4', '', '机动车驾驶证被依法扣押、扣留、暂扣期间能否申请补发？', '机动车驾驶证被依法扣押、扣留或者暂扣期间，机动车驾驶人不得申请补发。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074359, '正确', '错误', '', '', '2', '', '在高速公路上行驶感觉疲劳时，应立即停车休息。', '在高速公路上行驶感觉疲困时，应该找到最近的服务区停车休息，高速不可随便停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074360, '正确', '错误', '', '', '1', '', '驾驶人发现轮胎漏气，将车辆驶离主车道时，不要采用紧急制动，以免造成翻车或后车采取制动不及时导致追尾事故。', '轮胎漏气时不能采取紧急制动，容易造成翻车。应控制住方向，慢慢停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074361, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1054.jpg', '这个标志的含义是警告前方道路易滑，注意慢行。', '<p><img src=http://tp.mnks.cn/tp_125.jpg>\r<br/>反向弯路\r<br/><img src=http://tp.mnks.cn/tp_142.jpg>\r<br/>易滑路段</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074362, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1055.jpg', '这个标志的含义是提醒车辆驾驶人前方是单向行驶并且照明不好的涵洞。', '图中是注意隧道警告标志。用以提醒车辆驾驶人注意慢行。设在双向行驶并且照明不好的隧道口前适当位置。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074363, '收缴驾驶证', '撤销驾驶许可', '强制恢复车况', '吊销驾驶证', '4', '', '驾驶报废机动车上路行驶的驾驶人，除按规定罚款外，还要受到哪种处理？', '根据《中华人民共和国道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。<b>对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证。</b>因此本题答案为D。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074364, '正确', '错误', '', '', '1', '', '驾驶客车遇非常情况或者发生事故时，应力所能及的将损失降到最低限度，决不能因紧急避险造成二次事故或更大的损失。', '', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074365, '高速公路里程编号', '高速公路界牌编号', '高速公路命名编号', '高速公路路段编号', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1058.jpg', '这个标志是何含义？', '高速公路命名编号：用于指示高速公路的编号，作为指路标志的路线信息，出现在高速公路、城市快速路、一般道路指路标志版面中。查看高速公路标志大全：http://www.jszks.com/onepage/16721.html', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074366, '正确', '错误', '', '', '1', '', '行车中当车辆突然爆胎时，驾驶人切忌慌乱中急踏制动踏板，尽量采用“抢挡”的方法，利用发动机制动使车辆减速。', '车胎爆胎时紧急制动极易导致车辆侧翻。应该利用抢档的方式，用发动机制动减速停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074367, '正确', '错误', '', '', '1', '', '驾驶机动车在道路上掉头时提前开启左转向灯。', '《中华人民共和国道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯： \r<br/><b>（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯。</b>\r<br/>（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。\r<br/>因此，本题答案应为正确。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074368, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1061.jpg', '这个标志的含义是提醒前方左侧行车道或路面变窄。', '左侧变窄：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074369, '正确', '错误', '', '', '1', '', '行车中当车辆前轮爆胎已发生转向时，驾驶人应双手紧握转向盘，尽力控制车辆直线行驶。', '行车中当车辆前轮爆胎已发生转向时：第一步：驾驶人应双手紧握转向盘，尽力控制车辆转回原行驶路线。如果不控制方向，很容易翻车，如果急刹车，也很容翻车。第二步：利用发动机制动减速，打开右转向灯缓慢停靠在路肩上。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074370, '机动车保险单', '机动车行驶证', '出厂合格证明', '机动车登记证', '2', '', '下列哪种证件是驾驶机动车上路行驶应当随车携带？', '《道路交通安全法》第十一条：驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，<b>并随车携带机动车行驶证。</b>因此本题答案应为C。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074371, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1064.jpg', '这个标志的含义是提醒前方两侧行车道或路面变窄。', '<p><img src=http://tp.mnks.cn/tp_130.jpg>\r<br/>两侧变窄\r<br/><img src=http://tp.mnks.cn/tp_133.jpg>\r<br/>窄桥</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074372, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1065.jpg', '这个标志的含义是提醒车辆驾驶人前方是堤坝路段。', '<p><img src=http://tp.mnks.cn/tp_144.jpg>\r<br/>堤坝路\r<br/><img src=http://tp.mnks.cn/tp_143.jpg>\r<br/>傍山险路</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074373, '禁止左转', '此路不通', '禁止通行', '超高绕行', '4', 'http://images.juheapi.com/jztk/a1b1subject1/1066.jpg', '这个标志是何含义？', '4M高度，因为超过所以只能绕行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074374, '正确', '错误', '', '', '2', '', '高速公路因发生事故造成堵塞时，可在右侧紧急停车带或路肩行驶。', '紧急停车带：一般用于高速公路发生车祸等事故以后，警车、救护车、消防车等救援车辆的紧急通行车道，其他车辆除特殊情况下，严禁占用该车道行驶或长时间停车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074375, '两侧变窄', '右侧变窄', '左侧变窄', '桥面变窄', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1068.jpg', '这是什么交通标志？', '右侧变窄：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074376, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1069.jpg', '这种标志的作用是警告车辆驾驶人前方有危险，谨慎通行。', '黄色标志都是表示警告，第一张图警告前方T字路口，第二张图片表示急转弯，第三张图片表示前方道路变窄，要小心驾驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074377, '非机动车道', '避让非机动车', '禁止非机动车通行', '注意非机动车', '4', 'http://images.juheapi.com/jztk/a1b1subject1/1070.jpg', '这个标志是何含义？', '黄底标志为警告标志，自行车优势非机动车所以答案是注意非机动车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074378, '正确', '错误', '', '', '1', '', '不得驾驶具有安全隐患的机动车上道路行驶。', '《道路交通安全法》第二十一条：驾驶人驾驶机动车上道路行驶前，应当对机动车的安全技术性能进行认真检查；<b>不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车。</b>因此本题答案应为正确。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074379, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1072.jpg', '仪表显示当前冷却液的温度是90度。', '此仪表盘温度表示的是冷却液的温度。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074380, '开启前照灯，继续行驶', '开启示廓灯、雾灯，靠右行驶', '开启危险报警闪光灯，继续行驶', '开启危险报警闪光灯和雾灯，选择安全地点停车', '4', '', '遇有浓雾或特大雾天能见度过低，行车困难时，应怎样做？', '浓雾或特大雾天，能见度过低，此时开车属于十分危险的行为，一定要开启危险报警闪光灯和雾灯，选择安全地点停车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074381, '警告标志', '禁令标志', '指示标志', '指路标志', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1074.jpg', '这属于哪一种标志？', '禁令标志是交通标志中主要标志的一种，对车辆加以禁止或限制的标志，如禁止通行、禁止停车、禁止左转弯、禁止鸣喇叭、限制速度、限制重量等。 禁令标志的颜色，除个别标志外，为白底，红圈，红杠，黑图案。图案压杠。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074382, '行动迟缓', '喜欢聚集、围观', '行走随意性大，方向多变', '以上都是', '3', '', '行人参与道路交通的主要特点是什么？', '不同的人行动速度不同，因此A不准确。而B中并不是行人参与交通的特点。行人由于主观能动性强，想法更改自由随意，并不十分受信号灯约束，因此C才是行人参与交通的主要特点。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074383, '两侧车窗玻璃', '电动车门', '车门锁住开锁', '儿童安全锁', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1076.jpeg', '（如图所示）这个符号的开关控制什么装置？', '你看那个长的多像一把钥匙，所以是车门锁住开锁。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074384, '正确', '错误', '', '', '2', '', '预计在超车过程中与对面来车有会车可能时，应提前加速超越。', '预计在超车过程中与对面来车有会车可能时，应当放弃超车。若加速超越，有可能会引起不必要的事故。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074385, '侧面出风', '空气外循环', '迎面出风', '空气内循环', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1078.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '直吹人脸表示迎面出风，垂直向下表示吹地板。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074386, '正确', '错误', '', '', '1', '', '谨慎驾驶的三原则是集中注意力、仔细观察和提前预防。', '黄金原则，牢记，对我们以后驾车是有很大的帮助的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074387, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1080.jpeg', '机动车仪表板上（如图所示）亮，提示发电机向蓄电池充电。', '是蓄电池坏了不是电量不足，所以不是充电就能解决问题的，所以应该是充电系统出现故障。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074388, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1081.jpg', '这个标志的含义是提醒前方右侧行车道或路面变窄。', '右侧变窄：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074389, '交替变换远近光灯提醒路口内车辆让行', '从路口内车辆前迅速插入', '让已在路口内的车辆先行', '鸣喇叭直接进入路口', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1082.jpg', '进入这个路口应该怎样做？', '前方标志为环岛行驶，应该让已进入环岛内的车辆通行。当前车辆不能压白色网状区域。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074390, '正确', '错误', '', '', '1', '', '通过窄路、窄桥时，不得超车。', '《中华人民共和国道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 <b>通过隧道，铁路道口，急转弯路段，窄路，窄桥或遇前方道路上有车辆掉头时，不得超车。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074391, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1084.jpeg', '机动车仪表板上（如图所示）亮，提示右侧车门未关闭。', '从图而知，是左侧车门是开启状态，因此该标志是指左侧车门未关闭。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074392, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1085.jpeg', '机动车仪表板上（如图所示）亮表示启用地板及前风窗玻璃吹风。', '扇型的是前风窗，曲线向上表示前风窗玻璃吹风。箭头向下是地板出风。所以本题是对的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074393, '正确', '错误', '', '', '1', '', '驾驶车辆汇入车流时，应提前开启转向灯，保持直线行驶，通过后视镜观察左右情况，确认安全后汇入合流。', '在汇入车流是先开转向灯的，是为了提示后面的车避让留下进入车流的空隙，保持直行一段，因为是汇入合流，要确认好车与车横向空隙就要用后视镜看左右了。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074394, '正确', '错误', '', '', '1', '', '行车中要文明驾驶，礼让行车，做到不开英雄车、冒险车、赌气车和带病车。', '文明驾车，安全行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074395, '正确', '错误', '', '', '2', '', '车辆在路边起步后应尽快提速，并向左迅速转向驶入正常行驶道路。', '需要先观察，安全才能开过去，不是一起步就迅速进入行驶道行驶，万一后面有车是很危险的，所以“迅速”是错的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074396, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1089.jpg', '这个标志的含义是告示前方道路是单向通行路段。', '警告标志：右侧绕行，注意安全。用以告示前方道路有障碍物，车辆应按标志指示减速慢行。设在道路障碍物前适当位置。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074397, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1090.jpg', '这个标志的含义是提醒车辆驾驶人前方是非机动车道。', '<p><img src=http://tp.mnks.cn/tp_232.jpg>\r<br/>非机动车道</p><p><img src=http://tp.mnks.cn/tp_159.jpg>\r<br/>注意非机动车</p>\r<br/>图中标志是注意非机动车，并非提醒前方是非机动车车道。题目描述有误，因此本题答案应为错误。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074398, '处管制，并处罚金', '处2年以上徒刑', '处拘役，并处罚金', '处2年以下徒刑', '3', '', '醉酒驾驶机动车在道路上行驶会受到什么处罚？', '按照《道路交通安全法》规定：醉酒驾驶机动车辆，吊销驾照，5年内不得重新获取驾照，经过判决后处以拘役，并处罚金。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074399, '洗涤液不足', '制动液不足', '冷却系故障', '冷却液不足', '4', 'http://images.juheapi.com/jztk/a1b1subject1/1092.jpeg', '机动车仪表板上（如图所示）亮表示什么？', 'A,B,C都巨不靠谱，只有D最合理。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074400, '正确', '错误', '', '', '1', '', '行车中从其他道路汇入车流前，应注意观察侧后方车辆的动态。', '行车中从其他道路线汇入车流前，应注意观察侧后方车辆的动态，确保行车安全。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074401, '正确', '错误', '', '', '1', '', '车辆起步前，驾驶人应对车辆周围交通情况进行观察，确认安全时再开始起步。', '起步前的第一步就是驾驶员要先观察车周围，确认安全时再开始起步。因此本题是对的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074402, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1095.jpg', '点火开关在ON位置，车用电器不能使用。', 'LOCK-----（锁止），ACC------------ (断电)\r<br/>ON--------（通电），START--------（起动）\r<br/>点火开关在ON位置，车用电器是可以使用的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074403, '驻车制动器操纵杆', '节气门操纵杆', '变速器操纵杆', '离合器操纵杆', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1096.jpg', '这是什么操纵装置？', '变速器操纵杆也就是换挡的，当我们速度越开越快的时候也要对应上相应的档位，故此为变速器操纵杆。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074404, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1097.jpg', '这个标志的含义是告示前方是拥堵路段，注意减速慢行。', '图中是事故多发地段标志。用以告示前方道路为事故易发路段，谨慎驾驶。设在交通事故易发路段以前适当位置。题目描述错误，本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074405, '前风窗玻璃刮水器及洗涤器', '后风窗玻璃除霜或除雾', '后风窗玻璃刮水器及洗涤器', '前风窗玻璃除霜或除雾', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1098.jpeg', '（如图所示）这个符号的开关控制什么装置？', '此为后风窗玻璃刮水器及洗涤器。关于如何区分前风窗玻璃刮水器及洗涤器和后风窗玻璃刮水器及洗涤器，你只需记住一点半弧形的图标是前风窗，四四方方的图标就是后风窗。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074406, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1099.jpeg', '机动车仪表板上（如图所示）一直亮，表示发动机控制系统故障。', '该标志表示发动机控制系统故障，因此本题正确。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074407, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1100.jpg', '这个标志的含义是告示前方道路施工，车辆左右绕行。', '<b>图中是 [左右绕行] 标志。</b>用以告示前方道路有障碍物，车辆应按标志指示减速慢行。设在道路障碍物前适当位置。并非道路施工，题目描述有误，因此本题答案为错。\r<br/><p><img src=http://tp.mnks.cn/tp_163.jpg>\r<br/>左右绕行标志</p><p><img src=http://tp.mnks.cn/tp_167.jpg>\r<br/>前方施工标志</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074408, '儿童安全锁', '两侧车窗玻璃', '电动车门', '车门锁住开锁', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1101.jpeg', '（如图所示）这个符号的开关控制什么装置？', '你看里边长的多像一个小孩，故选A最靠谱。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074409, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1102.jpg', '点火开关在LOCK位置拔出钥匙转向盘会锁住。', 'LOCK-----（锁止），ACC------------ (断电)<br/>ON--------（通电），START--------（起动）。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074410, '正确', '错误', '', '', '2', '', '变更车道时，应开启转向灯，迅速驶入侧方车道。', '<b>开启转向灯后，应该充分观察确认安全后再驶入。</b>本题错在“迅速”两字，且没有观察。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074411, '正确', '错误', '', '', '1', '', '遇到路口情况复杂时，应做到“宁停三分，不抢一秒”。', '文明驾车，安全行驶。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074412, '防抱死制动系统', '座椅安全带', '座椅安全头枕', '安全玻璃', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1105.jpg', '正面安全气囊与什么配合才能充分发挥保护作用？', '安全气囊和安全带一起使用，起到有效的保护措施，不使用安全带是起不到作用的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074413, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1106.jpg', '将点火开关转到ACC位置起动机工作。', 'LOCK-----（锁止），ACC------------ (断电)\r<br/>ON--------（通电），START--------（起动）', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074414, '正确', '错误', '', '', '1', '', '申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效。', '申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074415, '五年', '十年', '二十年', '终生', '4', '', '饮酒后或者醉酒驾驶机动车发生重大交通事故构成犯罪的，依法追究刑事责任，吊销机动车驾驶证，将多少年内不得申请机动车驾驶证？', '饮酒驾驶机动车辆，罚款1000元—2000元、记12分并暂扣驾照6个月；饮酒驾驶营运机动车，罚款5000元，记12分，处以15日以下拘留，并且5年内不得重新获得驾照。醉酒驾驶机动车辆，吊销驾照，5年内不得重新获取驾照，经过判决后处以拘役，并处罚金；醉酒驾驶营运机动车辆，吊销驾照，10年内不得重新获取驾照，终生不得驾驶营运车辆，经过判决后处以拘役，并处罚金。<br/><b>饮酒后或者醉酒驾驶机动车发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证，终生不得重新取得机动车驾驶证。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074416, '加速踏板', '离合器踏板', '驻车制动器', '制动踏板', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1109.jpg', '这是什么踏板？', '加速踏板俗称油门，控制车速的。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074417, '离合器踏板', '加速踏板', '制动踏板', '驻车制动器', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1110.jpg', '这是什么踏板？', '制动踏板俗称刹车。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074418, '正确', '错误', '', '', '1', '', '倒车过程中要缓慢行驶，注意观察车辆两侧和后方的情况，随时做好停车准备。', '倒车时主要观察车后面和车两侧的情况，整个过程要缓慢谨慎，安全第一。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074419, '正确', '错误', '', '', '1', '', '机动车在发生碰撞时，安全带可以减轻驾乘人员伤害。', '安全带加上安全气囊的双重保护才能充分发挥作用。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074420, '正确', '错误', '', '', '1', '', '安装防抱死制动装置（ABS）的机动车紧急制动时，可用力踏制动踏板。', 'ABS防抱死系统；车轮不卡死、硬化路面制动时间短平稳、可以用力踩制动、系统自动张合8次/每秒。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074421, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1114.jpg', '这种握转向盘的动作是正确的。', '正确的握法是握住方向盘的两边，分别在10点和4点位置，左手为主右手为辅。题中的握法是驾驶的陋习，是有很大的风险的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074422, '节气门操纵杆', '变速器操纵杆', '离合器操纵杆', '驻车制动器操纵杆', '4', 'http://images.juheapi.com/jztk/a1b1subject1/1115.jpg', '这是什么操纵装置？', '驻车制动器操纵杆俗称手刹，能使汽车本体固定在地面或某平面上而不出现相对移动的总成的操纵装置。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074423, '25', '150', '100', '50', '2', '', '车辆因故障必须在高速公路停车时，应在车后方多少米以外设置故障警告标志？', '<b>车辆因故障必须在高速公路停车时，应在车后方（150米）处设置故障警告标志，夜间还需开启示廓灯和后位灯。</b>', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074424, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1117.jpeg', '机动车仪表板上（如图所示）亮，提示左侧车门未关闭。', '从图而知，图标中右侧车门显示开启状态，因此该标志是提示右侧车门未关闭，并非提示左侧车门未关闭。题目描述错误，因此本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074425, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1118.jpg', '点火开关在START位置起动机起动。', 'LOCK-----（锁止），ACC------------ (断电)\r<br/>ON--------（通电），START--------（起动）', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074426, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1119.jpeg', '机动车仪表板上（如图所示）亮，提示行李舱开启。', '后边为行李舱，前边为发动机舱。当机动车仪表板上该符号亮起，表示发动机舱开启。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074427, '保护驾乘人员颈部', '保护驾乘人员胸部', '减轻驾乘人员伤害', '保护驾乘人员腰部', '3', '', '机动车发生碰撞时座椅安全带主要作用是什么？', '安全带在紧急情况下，可以迅速拉紧把人束缚在座椅上，保证安全。可以减轻对驾乘人员的伤害。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074428, '正确', '错误', '', '', '1', '', '通过隧道时，不得超车。', '《中华人民共和国道路交通安全法》 第四十三条 同车道行驶的机动车，……有下列情形之一的，不得超车： <br/>（一）前车正在左转弯、掉头、超车的； <br/>（二）与对面来车有会车可能的； <br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br/>（四）<b>行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074429, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1122.jpeg', '机动车仪表板上（如图所示)亮，提示两侧车门未关闭。', '看标志两侧的门是打开的状态，如果该灯亮了，就是提醒司机车门是打开的状态。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074430, '空调开关', '点火开关', '雨刷开关', '灯光开关', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1123.jpg', '这是什么操纵装置？', '这个是点火装置没错。灯光开关在左手边，雨刷开关在右手边。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074431, '正确', '错误', '', '', '2', '', '通过急转弯路段时，在车辆较少的情况下可以超车。', '《中华人民共和国道路交通安全法》 第四十三条 同车道行驶的机动车，……有下列情形之一的，不得超车： \r<br/>（一）前车正在左转弯、掉头、超车的； \r<br/>（二）与对面来车有会车可能的； \r<br/>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； \r<br/>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。\r<br/> <b>通过隧道，铁路道口，急转弯路段，窄路，窄桥或遇前方道路上有车辆掉头时，不得超车。</b>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074432, '正确', '错误', '', '', '1', '', '机动车发生正面碰撞时，安全气囊加上安全带的双重保护才能充分发挥作用。', '研究表明，假设气囊加安全带是100%安全的话，安全带的作用是70%，气囊的作用是30%。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074433, '2年', '3年', '4年', '5年', '4', '', '已取得驾驶中型客车或者大型货车准驾车型资格多少年以上，可以申请增加大型客车准驾车型？', '', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074434, '离合器踏板', '制动踏板', '驻车制动器', '加速踏板', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1127.jpg', '这是什么踏板？', '从左往右数起，分别是离合，制动踏板，加速踏板。图中指的最中间的是制动踏板。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074437, '2个', '3个', '4个', '5个', '4', '', '持有中型客车或者大型货车驾驶证，申请增加大型客车准驾车型的，应当在申请前最近连续几个记分周期内没有满分记录？', '', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074439, '正确', '错误', '', '', '2', '', '安装防抱死制动装置（ABS）的机动车制动时，制动距离会大大缩短，因此不必保持安全车距。', 'ABS防抱死系统；车轮不卡死、硬化路面制动时间短平稳、可以用力踩制动、系统自动张合8次/每秒。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074440, '低速爬坡', '紧随其后爬坡', '选择适当地点停车，等前车通过后再爬坡', '迅速超越前车', '3', '', '在山区冰雪道路上行车，遇有前车正在爬坡时，后车应怎样做？', '冰雪路面很滑，正在爬坡的车可能会后溜，最安全的做法是选择安全地点停车，等前车通过后再爬坡。这样能避免发生意外事故时两车相撞的可能。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074441, '安全气囊处于工作状态', '安全带没有系好', '防抱死制动系统故障', '安全气囊处于故障状态', '4', 'http://images.juheapi.com/jztk/a1b1subject1/1134.jpeg', '机动车仪表板上（如图所示）一直亮表示什么？', '圆球表示安全气囊，黄色表示处于故障状态。所以该标志亮就表示安全气囊出现故障。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074442, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1135.jpeg', '打开右转向灯开关，（如图所示）亮起。', '图中朝左的箭头亮起，表示打开的是左转向灯。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074443, '发动机舱开启', '燃油箱盖开启', '两侧车门开启', '行李舱开启', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1136.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '这个毫无疑问俩边门都开了。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074444, '1年', '2年', '5年', '10年', '4', '', '醉酒驾驶营运机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，多长时间内不得重新取得机动车驾驶证，并依法追究刑事责任；重新取得机动车驾驶证后，不得驾驶营运机动车？', '醉酒后驾驶营运机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，10年内不得重新取得机动车驾驶证，并依法追究刑事责任', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074445, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1138.jpg', '这个标志的含义是警告车辆驾驶人注意前方设有信号灯。', '警告标志警告车辆驾驶人注意前方设有信号灯。用以警告车辆驾驶人注意前方路段设有信号灯，应依信号灯指示行车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074446, '处5年以上徒刑', '处3年以下徒刑或者拘役', '处3年以上徒刑', '处3年以上7年以下徒刑', '2', '', '驾驶人违反交通运输管理法规发生重大事故使公私财产遭受重大损失，可能会受到什么刑罚？', '《刑法》第一百三十三条规定：<b>违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；</b>交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074447, '充电电流过大', '蓄电池损坏', '电流表故障', '充电电路故障', '4', 'http://images.juheapi.com/jztk/a1b1subject1/1140.jpeg', '机动车仪表板上（如图所示）亮表示什么？', '此为充电电路故障，一般没有故障这些都不会亮的，只有出故障了才会亮。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074449, '参加道路交通安全法律、法规和相关知识考试合格后', '参加场地考试合格后', '参加道路驾驶技能考试合格后', '参加安全文明驾驶常识考试合格后', '1', '', '超过机动车驾驶证有效期一年以上未换证被注销，但未超过2年的，机动车驾驶人应当如何恢复驾驶资格？', '《机动车驾驶证申领和使用规定》：被注销机动车驾驶证未超过两年的，机动车驾驶人考试科目一合格后，可以恢复驾驶资格。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074452, '200元以上500元以下', '500元', '500元以上2000元以下', '2000元以上', '3', '', '公路客运车辆载客超过额定成员20%或者违反规定载货的，处多少罚款？', '客运车辆载客超载，但是没有超过额定成员20%，罚的不算太狠，所以是200元上，500元以下；而超载超过20%的就是500元以上2000元以下。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074453, '减速慢行、鸣喇叭示意', '为拓宽视野，临时占用左侧车道行驶', '加速行驶', '停车观察', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1146.jpg', '如图所示，驾驶机动车遇到这种情况，以下做法正确的是什么？', '驾驶机动车遇到这种情况，应该：减速慢行、鸣喇叭示意。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074454, '保持行驶方向和车速不变', '自己也打开远光灯行驶', '降低车速，谨慎会车', '加速通过，尽快摆脱眩目光线', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1147.jpg', '如图所示，夜间驾驶机动车遇对方使用远光灯，无法看清前方路况时，以下做法正确的是什么？ ', '夜间驾驶机动车遇对方使用远光灯，无法看清前方路况时，降低车速，谨慎会车，以免发生车祸、危险。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074458, '开启转向灯的同时变更车道', '在道路同方向划有2条以上机动车道的，不得影响相关车道内行驶的机动车的正常行驶', '在车辆较少路段，可以随意变更车道', '遇前方道路拥堵，可以向应急车道变更', '2', '', '驾驶机动车变更车道时，以下做法正确的是什么？', '《道路交通安全法》规定：驾驶机动车变更车道时，在道路同方向划有2条以上机动车道的，不得影响相关车道内行驶的机动车的正常行驶。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074460, '道路交通安全违法行为、交通事故处理情况', '身体条件情况', '道路交通安全违法行为记分及记满12分后参加学习和考试情况', '机动车检验情况', '4', '', '驾驶证审验内容不包括以下哪一项？', '驾驶证审验内容包括：道路交通安全违法行为、交通事故处理情况；身体条件情况；道路交通安全违法行为记分及记满12分后参加学习和考试情况。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074461, '警告', '罚款', '拘留', '吊销驾驶证', '2', '', '上道路行驶的机动车驾驶人未携带机动车驾驶证、行驶证的，除扣留机动车外，并受到什么处罚？', '未携带驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供驾驶证，并可以依照道路交通法第九十条的规定予以200元的罚款。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074466, '驾驶与准驾车型不符的机动车', '车速超过规定时速50%以上', '驾驶机动车违反道路交通信号灯', '未取得校车驾驶资格驾驶校车', '3', '', '以下哪种违法行为的机动车驾驶人将被一次记6分？', '二、机动车驾驶人有下列违法行为之一，一次记6分：<br/>（一）机动车驾驶证被暂扣期间驾驶机动车的；<br/><b>（二）驾驶机动车违反道路交通信号灯通行的；</b><br/>（三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数未达20％的，或者驾驶其他载客汽车载人超过核定人数20％以上的；<br/>（四）驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速未达20％的；<br/>（五）驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路以外的道路上行驶或者驾驶其他机动车行驶超过规定时速20%以上未达到50%的；<br/>（六）驾驶货车载物超过核定载质量30%以上或者违反规定载客的；<br/>（七）驾驶营运客车以外的机动车在高速公路车道内停车的；<br/>（八）驾驶机动车在高速公路或者城市快速路上违法占用应急车道行驶的；<br/>（九）低能见度气象条件下，驾驶机动车在高速公路上不按规定行驶的；<br/>（十）驾驶机动车运载超限的不可解体的物品，未按指定的时间、路线、速度行驶或者未悬挂明显标志的；', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074468, '实习期记满12分，注销驾驶证的', '申请人在考试过程中有舞弊行为的', '申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的', '申请人未能在培训过程中认真练习的', '3', '', '申请人有下列哪种行为，三年内不得再次申领机动车驾驶证？', '根据公安部令第123号规定：\r<br/>申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。\r<br/>申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可；申请人在三年内不得再次申领机动车驾驶证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074469, '能见度下降，驾驶人难以及时发现前方车辆', '此类天气条件下的道路上，车辆的制动距离变长', '为车辆安全行驶提供足够的安全距离', '降低恶劣天气对车辆造成的损害', '4', '', '驾驶机动车在高速公路上遇到雨雪天气时，需要降低车速、保持安全距离的原因，以下说法错误的是什么？', '驾驶机动车在高速公路上遇到雨雪天气时能见度下降，驾驶人难以及时发现前方车辆，<b>在此类天气条件下的道路上，车辆的制动距离变长，需要降低车速，为车辆安全行驶提供足够的安全距离。</b>\r<br/>而降低车速、保持安全距离，与降低恶劣天气对车辆造成的损害并无直接关系。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074471, 'A车负全部责任', 'B车负全部责任', '各负一半的责任', 'B车负主要责任', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1164.jpg', '如图所示，在这起交通事故中，以下说法正确的是什么？ ', '这起交通事故主要是因为A车绕开障碍物时未提前观察减速为B车让行造成的，所以A车全责。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074475, '原驾驶证继续使用', '原驾驶证作废，不得继续使用', '原驾驶证特殊情况下使用', '替换使用', '2', '', '机动车驾驶证补领后，以下说法正确的是什么？', '《机动车驾驶证申领和使用规定》：机动车驾驶人补领机动车驾驶证后，原机动车驾驶证作废，不得继续使用。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074476, '违法登记制度', '奖励里程制度', '累积记分制度', '强制报废制度', '3', '', '公安机关交通管理部门对驾驶人的交通违法行为除依法给予行政处罚外,实行下列哪种制度？', '《道路交通安全法》第二十四条：公安机关交通管理部门对机动车驾驶人违反道路交通安全法律、法规的行为，除依法给予行政处罚外，实行累积记分制度。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074478, '加速，在对向车到达前通过', '减速靠右通过', '保持原速继续正常行驶', '鸣喇叭后加速通过', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1171.jpg', '如图所示，驾驶机动车行驶至桥梁涵洞时，以下做法正确的是什么？', '如图所示，一辆车已经行至涵洞里了，而涵洞较窄，所以我方车辆应该减速靠右让对向车辆先行。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074479, '长鸣喇叭催促行人快速通过', '开启远光灯警示行人有车辆驶近', '降低行驶速度，避让行人', '适当加速从行人前方绕行', '3', 'http://images.juheapi.com/jztk/a1b1subject1/1172.jpg', '如图所示，驾驶机动车遇到这种情况，以下做法正确的是什么？', '驾驶机动车遇到图中这种情况，应当降低行驶速度，避让行人。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074480, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1173.jpg', '这个标志的含义是提醒车辆驾驶人前方是桥头跳车较严重的路段。', '此为<b>驼峰桥标志</b>。用以提醒车辆驾驶人谨慎驾驶。设在拱度很大，影响视距的驼峰桥以前适当位置。题目描述错误，因此本题答案为错。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074481, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1174.jpg', '这个地面标记的含义是预告前方设有交叉路口。', '指示标线，人行横道预警棱形图标。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074482, '正确', '错误', '', '', '1', '', '营运客车起步前，乘务人员应提醒乘客系好安全带。', '为了安全，善意的提醒是有必要的。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074483, '正确', '错误', '', '', '1', '', '驾驶人的驾驶证损毁后不得驾驶机动车。', '《中华人民共和国道路交通安全法实施条例》第二十八条：机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074484, '迁出地', '居住地', '所在地', '迁入地', '4', '', '驾驶人户籍迁出原车辆管理所需要向什么地方的车辆管理所提出申请？', '《公安部令第123号》第四十九条：机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向迁入地车辆管理所申请换证。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074485, '6', '8', '10', '12', '2', '', '客运驾驶人24小时内累计驾驶时间原则上不超过多少小时？', '客运驾驶人在24小时内累计驾驶时间不得超过8小时，连续驾驶时间不得超过4小时，每次停车休息时间不少于20分钟。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074486, '正确', '错误', '', '', '2', '', '驾驶机动车在山区道路应紧跟前车之后行驶。', '山区道路突发状况多，需保持一定的距离来应急。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074487, '正确', '错误', '', '', '1', '', '记分满12分的驾驶人拒不参加学习和考试的将被公告驾驶证停止使用。', '《道路交通安全法实施条例》第二十五条：机动车驾驶人记分达到12分，拒不参加公安机关交通管理部门通知的学习，也不接受考试的，由公安机关交通管理部门公告其机动车驾驶证停止使用。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074488, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1181.jpg', '这个标志的含义是警告车辆驾驶人前方是学校区域。', '图中是注意儿童警告标志。用以警告车辆驾驶人减速慢行，注意儿童。设在小学、幼儿园、少年宫等儿童经常出入地点前适当位置。题目描述错误，因此答案为错。\r<br/>\r<br/>【拓展延伸】\r<br/>要注意区分学校标志。学校标志通常是由注意儿童标志和“前方学校，减速慢行”提示语组成，不要混淆。\r<br/><p><img src=http://tp.mnks.cn/tp_136.jpg>\r<br/>注意儿童\r<br/></p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074489, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1182.jpg', '灯光开关在该位置时，前雾灯点亮。', '灯杆上白色圆点对准的是前雾灯标志，因此灯光开关在图中位置时，前雾灯点亮，题目描述正确，因此答案为对。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074490, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/a1b1subject1/1183.jpg', '这个标志的含义是警告车辆驾驶人前方是人行横道。', '图中为注意行人标志，用来警示机动车驾驶人要注意过往行人，谨慎慢行。此标志通常设置在行人密集，或者有人行横道但是不易被发现的区域。提示司机注意行人，而非人行横道。题目描述错误，因此答案为错。\r<br/>\r<br/><p><img src=http://tp.mnks.cn/tp_216.jpg>\r<br/>人行横道\r<br/><img src=http://tp.mnks.cn/tp_135.jpg>\r<br/>注意行人</p>', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074493, '正确', '错误', '', '', '1', '', '客车行经转弯、上下坡、凹凸路时，应让乘客抓住车内固定物，尤其提醒后排乘客注意安全。', '因为在行经这些路面时，很容易发生颠簸，让人失去平衡。大家都有这样的感觉，坐在后排起伏的幅度较大，很多时候通过颠簸路面时，会有强烈的波动。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074494, '正确', '错误', '', '', '1', '', '客运驾驶人日间连续驾驶不得超过4小时，夜间连续驾驶不得超过2小时，每次停车休息时间不少于20分钟。', '看清楚题干，是连续不超过，规定是4个小时。', 1, 3, 4, NULL, 1);
INSERT INTO `me_question` VALUES (101461731074496, '驾驶证被扣押', '驾驶证被扣留', '驾驶证遗失', '驾驶证被暂扣', '3', '', '下列哪种情况可以向机动车驾驶证核发地车辆管理所申请补发?', '驾驶证被扣留、扣押及暂扣都可以再次取回，不用补发。机动车驾驶证遗失、损毁无法辨认时，机动车驾驶人应当向机动车驾驶证核发地车辆管理所申请补发。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074497, '开启右转向灯', '开启危险报警闪光灯', '不用开启转向灯', '开启左转向灯', '1', 'http://images.juheapi.com/jztk/a1b1subject1/1190.jpg', '如图所示，驾驶机动车驶出这个路口时应当怎样使用灯光?', '图中是环岛，驶入开左转向灯，驶出开右转向灯。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (101461731074498, '防抱死制动系统', '电子制动力分配系统', '辅助驾乘人员保护系统', '驾驶人头颈保护系统', '3', '', '安全气囊是一种什么装置？', '安全气囊，设置在车内前方（正副驾驶位），侧方（车内前排和后排）和车顶三个方向。装有安全气囊系统的容器外部都印有Supplemental Inflatable Restraint System，简称SRS)的字样，直译成中文为“辅助可充气约束系统”。旨在减轻乘员的伤害程度，当发生碰撞事故时，避免乘员发生二次碰撞，或车辆发生翻滚等危险情况下被抛离座位。', 1, 3, 4, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790081, '开启右转向灯', '开启危险报警闪光灯', '不用开启转向灯', '开启左转向灯', '3', 'http://images.juheapi.com/jztk/subject4/1.jpg', '驾驶机动车进入这个路口怎样使用灯光？', '由图可知，前方是向右环形路段，因此向右行驶时不需要开启转向灯，所以本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790082, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/2.jpg', '在这个路口左转弯要靠路口中心点左侧转弯。', '如图是中心圈，设在平面交叉路口的中心，用以区分车辆大、小转弯，及交叉路口车辆左右转弯的指示，车辆不得压线行驶。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790083, '允许长时停放车辆', '可以临时停车', '允许长时停车等客', '不允许停放车辆', '2', 'http://images.juheapi.com/jztk/subject4/3.jpg', '右侧标志是何含义？', '禁止车辆长时停放：禁止车辆长时停放，临时停放不受限制。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790084, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/4.jpg', '右侧标志表示只能靠左侧道路行驶。', '靠左侧道路行驶-表示只准一切车辆靠左侧道路行驶。此标志设在车辆必须靠左侧行驶的路口以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790085, '正确', '错误', '', '', '2', '', '在道路上跟车行驶时，跟车距离不是主要的，只须保持与前车相等的速度，即可防止发生追尾事故。', '跟车距离很重要，不同的道路对跟车距离要求是不一样的。假设你跟前车的的速度相等，但是距离是1米，是不可能刹得住车的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790086, '开启左转向灯', '开启报警闪光灯', '不用开转向灯', '开启右转向灯', '4', 'http://images.juheapi.com/jztk/subject4/6.jpg', '驾驶机动车驶出这个环岛路口怎样使用灯光？', '由图可知，前方有两条道可供行驶，而驾驶车辆若想要离开环形路口，就要靠右侧行驶转出，此时要开启右转向灯告知其他车辆你要离开了。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790087, '正确', '错误', '', '', '1', '', '在行驶中，驾驶人在注意与前车保持安全距离的同时，也要谨慎制动，防止被后车追尾。', '在行驶过程中要留意与前后车的距离，因此若是贸然使用制动，很容易使后车来不及反应发生追尾，所以本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790088, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/8.jpg', '右侧标志警告前方道路左侧变宽。', '左侧变窄-用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790089, '向右转弯', '直行和左转弯', '直行和右转弯', '在桥下掉头', '2', 'http://images.juheapi.com/jztk/subject4/9.jpg', '立体交叉处这个标志提示什么？', '立交直行和左转弯行驶-表示车辆在立交处可以直行和按图示路线左转弯行驶。此标志设在立交左转弯出口处适当位置。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790090, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/10.jpg', '驾驶机动车遇到这种情况的人行横道线可以加速通过。', '遇到前方有人行横道应减速慢行，接近时停车让行。而由图中更可知有行人即将穿过，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790091, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/11.jpg', '右侧标志警示前方道路有连续三个或三个以上的弯路。', '反向弯路-用以警告车辆驾驶人减速慢行。设置位置为两反向圆曲线起点的外面，但不应进入相邻的圆曲线内。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790092, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/12.jpg', '右侧这个标志提醒注意前方200米是堤坝道路。', '前方200米是堤坝路-用以提醒车辆驾驶人小心驾驶，设在沿水库、湖泊、河流等堤坝道路以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790093, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/13.jpg', '图中标志提醒障碍物在路中，车辆从右侧绕行。', '左侧绕行-用以告示前方道路有障碍物，车辆应按标志指示减速慢行，左侧绕行通过。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790094, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/14.jpg', '造成这起事故的主要原因是行人从车前横穿。', '由图可知，行人正从人行横道线上穿过，发生这样的事故主要是由于车子没有停车让行造成的，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790095, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/15.jpg', '右侧标志提醒前方路面高突。', '驼峰桥：用以提醒车辆驾驶人谨慎驾驶。设在拱度很大，影响视距的驼峰桥以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790096, '正确', '错误', '', '', '2', '', '机动车可以选择交叉路口进行倒车。', '《中华人民共和国道路交通安全法实施条例》第五十条  机动车倒车时，应当察明车后情况，确认安全后倒车。不得在铁路道口、\"交叉路口\"、单行路、桥梁、急弯、陡坡或者隧道中倒车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790097, '前后雾灯', '近光灯', '远光灯', '危险报警闪光灯', '2', '', '夜间机动车通过照明条件良好的路段时，要怎样使用灯光？', '用近光灯不会照得对方看不见路，同时为了安全起见，开启近光灯能够让相互间清晰可见。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790098, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/18.jpg', '驾驶机动车在这个路口右转弯可以不变更车道。', '由图可知，此为三车道道路，右转弯时应当靠右侧车道，因此如图情形是不可以直接右转弯的，因为车的右侧是白实线，不能碾压。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790099, '尽快加速通过', '靠边停车等待', '注意�望安全通过', '禁止通行', '3', 'http://images.juheapi.com/jztk/subject4/19.jpg', '驾驶机动车遇到这种信号灯不断闪烁时怎样行驶？', '黄灯闪烁，是一种警示，提示注意安全通过。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790100, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/20.jpg', '驾驶机动车在这种情况下不能左转弯。', '左转红灯，所以不能左转，正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790101, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/21.jpg', '驾驶机动车通过这个路口要注意观察左侧情况。', '从图上看，是有机动车从右边道路驶出，因此要注意观察该车的行驶路线。所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790102, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/22.jpg', '右侧标志表示前方路口不准车辆左转。', '禁止向左转弯-表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790103, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/23.jpg', '这个标志警告前方铁路道口有多股铁路与道路相交。', '上面带叉和折线就代表不止一条铁路！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790104, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/24.jpg', '驾驶机动车在这种情况下不能直行和左转弯', '直行和左转都是红灯，所以不能直行和左转。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790105, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/25.jpg', '右侧标志警告前方是上陡坡路段。', '上陡坡-用以提醒车辆驾驶人小心驾驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790106, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/26.jpg', '驾驶机动车在这个路口左转弯要提前按导向箭头指示向左变更车道。', '由图可知，左侧车道是左转弯车道，车辆若要左转弯应当提前变更车道驶入左转弯车道，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790107, '向右转弯', '直行和左转弯', '直行和右转弯', '在桥下掉头', '3', 'http://images.juheapi.com/jztk/subject4/27.jpg', '立体交叉处这个标志提示什么？', '立交直行和右转弯行驶：表示车辆在立交处可以直行和按图示路线右转弯行驶。此标志设在立交右转弯出口处适当位置。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790108, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/28.jpg', '右侧这个标志提示前方是连续急转弯道路。', '易滑-用以促使车辆驾驶人注意慢行。设在路滑容易发生事故的路段以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790109, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/29.jpg', '右侧标志提醒前方是左侧傍山险路。', '注意落石-用以提醒车辆驾驶人注意落石。设在有落石危险的傍山路段以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790110, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/30.jpg', '路面白色反光虚线警告前方路段要减速慢行。', '用于警告车辆驾驶人前方应减速慢行。收费广场减速标线设于收费广场及其前部适当位置，为白色反光虚线。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790111, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/31.jpg', '前方标志告知前方200米处是露天停车场。', '单纯一个P字就是露天停车场，如果P字上边还有个遮挡，就是室内的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790112, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/32.jpg', '驾驶机动车在人行横道前遇到这种情况一定要减速慢行。', '由图可知，有行人正从人行横道线上横穿道路，而机动车已临近人行横道线，此时应停车让行，以免相撞，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790113, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/33.jpg', '右侧标志指示前方路口绕行的路线。', '亲，看清楚试题，问的是有右侧标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790115, '临近时关闭前照灯', '使用近光灯', '使用远光灯', '提前关闭所有灯光', '2', 'http://images.juheapi.com/jztk/subject4/35.jpg', '夜间驾驶机动车遇到这种情况怎样使用灯光？', '据《中华人民共和国道路交通安全法实施条例》第四十八条  在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（五）夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790116, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/36.jpg', '该车道路面导向箭头指示在前方路口仅可直行。', '该车道路面导向箭头指示在前方路口仅可直行或者左转。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790117, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/37.jpg', '前方路口减速让行线表示要停车让干道车先行。', '减速让行不是停车让行!', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790119, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/39.jpg', '驾驶机动车在小区内遇到这样的情况要在自行车前加速通过。', '由图可知，前方是丁字路口，且横向道路有骑自行车的人穿出。遇到这种情况应当让自行车先行，减速慢行注意避让。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790120, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/40.jpg', '前方标志预告高速公路入口在路右侧。', '此为入口预告标志，没有预告高速公路入口在路右侧的标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790121, '鸣喇叭示意其让行', '加速从车前通过', '开前照灯示意其让行', '减速或停车让行', '4', 'http://images.juheapi.com/jztk/subject4/41.jpg', '驾驶机动车在路口直行遇到这种情况怎么办？', '虽然前方绿灯，可以正常行驶通过，但由于对向有来车左转弯，因此应当让其通过后再通过，所以本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790122, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/42.jpg', '驾驶机动车在公交车站遇到这种情况要迅速停车让行。', '由图可知，停靠在一边的公交车后有行人穿出，此时应当予以避让，其一是为了避让行人，其二是以防公交车突然起步。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790123, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/43.jpg', '右侧标志表示前方路段允许超车。', '禁止超车-表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790124, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/44.jpg', '驾驶机动车此时可以加速通过路口。', '由图可知，此时信号灯已经变为黄色，即将转变为红色，此时应当在停车线前停车。否则就是闯黄灯的行为了。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790125, '从列队前方绕过', '减速慢行通过', '及时停车让行', '从列队空隙穿过', '3', 'http://images.juheapi.com/jztk/subject4/45.jpg', '驾驶机动车在学校门口遇到这种情况怎样行驶？', '由图可知，前方人行横道线上有学生排着队通过，此时应当等学生们通过后再通过。因此在临近人行横道线时，应当停车让行。所以本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790126, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/46.jpg', '右侧标志提醒注前方是驼峰桥。', '路面高突-用以提醒车辆驾驶人减速慢行。设在路面突然高突以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790127, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/47.jpg', '右侧标志指示距离设有电子不停车收费车道的收费站1公里。', 'ETC,使用该系统，车主只要在车窗上安装感应卡并预存费用，通过收费站时便不用人工缴费，也无须停车，高速费将从卡中自动扣除。虽然能实现不停车收费，但一般来说，车辆还是需要以较低速度通过。这种收费系统每车收费耗时不到两秒，其收费通道的通行能力是人工收费通道的5到10倍。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790128, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/48.jpg', '右侧标志警告前方路段要注意儿童。', '注意行人-用以警告车辆驾驶人减速慢行，注意行人。设在行人密集，或不易被驾驶员发现的人行横道线以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790129, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/49.jpg', '驾驶机动车在这个路口右转弯时要避让非机动车。', '右转弯时在非机动车道有非机动车正在前进，此时应当注意避让，待非机动车通过后再转弯，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790130, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/50.jpg', '路面标记指示这段道路上最低限速为60公里/小时。', '白色的是最低车速，黄色的是最高车速。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790131, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/51.jpg', '右侧这个标志提醒注意左侧有落石危险。', '注意落石-用以提醒车辆驾驶人注意落石。设在有落石危险的傍山路段以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790132, '在公交车后停车', '迅速超越公交车', '减速，缓慢超越', '连续鸣喇叭提醒', '3', 'http://images.juheapi.com/jztk/subject4/52.jpg', '在这种公交车站怎样预防公交车突然起步？', '注意本题的情景：公交车可能突然起步。因此横向间距要加大，而且要减速慢行，超越时要特别留意公交车的动态，避免发生碰撞，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790133, '靠路左侧转大弯', '靠弯路中心转弯', '靠路右侧转小弯', '借对向车道转弯', '3', 'http://images.juheapi.com/jztk/subject4/53.jpg', '在这种条件的道路上怎样行驶才安全？', '前方是向右急转弯路段，靠右转弯是小弯，靠左转弯是大弯，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790134, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/54.jpg', '右侧这个标志警告前方是无人看守的有多股铁路与道路相交铁路道口。', '这是有人看守铁道路口。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790135, '观察标志标线', '减速慢行', '不要鸣喇叭', '快速通过', '13', '', '驾驶机动车通过学校时要注意什么？', '在有学校的路段一般都会设置提醒有学校或是禁止鸣喇叭的交通标志，因此在经过学校时不仅要观察交通标志，还要记住不得鸣喇叭。另外，由于学校周围会有学生走动，因此在途经时车速一定要慢，避免撞到人或其他。因此本题要选ABC。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107535790137, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/57.jpg', '右侧这个标志提醒注意前方是傍山险路。', '傍山线路-用以提醒车辆驾驶人小心驾驶。设在傍山险路路段以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790138, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/58.jpg', '路两侧的标志提示前方道路线形变化。', '驾驶人员在一条不熟悉的道路上行驶，不可能知道行驶前方存在有潜在的危险。警告标志的作用就是及时地提醒驾驶人员前方道路线形和道路状况的变化，在到达危险点以前有充分时间采取必要行动，确保行驶安全。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790139, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/59.jpg', '驾驶机动车在交叉路口遇到这种情况可以不让行。', '虽然绿灯时可以直行，但由于对向左转车辆已经转到如图位置了，因此应当予以让行，否则会发生碰撞，所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790140, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/60.jpg', '右侧标志提醒前方右侧500米有避险车道。', '避险车道是指在长陡下坡路段行车道外侧增设的供速度失控车辆驶离正线安全减速的专用车道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790141, '开启危险报警闪光灯', '开启远光灯', '开启雾灯', '开启近光灯', '4', '', '机动车驶入双向行驶隧道前，要如何使用灯光？', '凡是车辆进入双向行驶隧道，一般都应该开启近光灯，而不要开启远光灯。若是隧道本身没有灯光的，可以变换灯光行驶，但是会车前要转为近光灯。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790142, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/62.jpg', '驾驶机动车在路口看到这种信号灯亮时，要加速通过。', '路口黄灯持续闪烁，警示驾驶人要注意�望，确认安全通过。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790143, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/63.jpg', '右侧标志提醒前方是非机动车道。', '黄底的是注意的意思，此标志的意思是注意非机动车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790144, '大型货车', '大型客车', '各种机动车', '小型客货车', '3', 'http://images.juheapi.com/jztk/subject4/64.jpg', '右侧标志提示哪种车型不能通行？', '禁止机动车通行：表示禁止某种机动车通行。此标志设在禁止机动车通行的路段入口处。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790145, '边观察边缓慢通过', '不换挡加速通过', '在火车到来前通过', '不得越过停止线', '4', 'http://images.juheapi.com/jztk/subject4/65.jpg', '驾驶机动车在铁路道口看到这种信号灯时怎样行驶？', '红灯，你就停了吧。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790146, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/66.jpg', '右侧标志警告前方路面两侧变窄长度为5公里。', '两侧变窄-用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790147, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/67.jpg', '右侧标志提醒前方有村庄或集镇，建议速度30公里/小时。', '上边是村庄标志-此标志设在不易发现前方有村庄或小城镇的路段以前适当位置。下边是建议车速标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790149, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/69.jpg', '右侧这个标志提醒前方山口注意横风。', '注意横风-用以提醒车辆驾驶人小心驾驶。设在经常有很强的侧向风的路段以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790151, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/71.jpg', '右侧标志表示注意避让直行方向来的机动车。', '直行-表示只准一切车辆直行。此标志设在直行的路口以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790152, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/72.jpg', '右侧标志表示前方路段解除时速40公里限制。', '解除限制速度-表示限制速度路段结束。此标志设在限制车辆速度路段的重点。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790153, '立即停车', '加速通过', '连续鸣喇叭', '减速慢行', '1', 'http://images.juheapi.com/jztk/subject4/73.jpg', '驾驶机动车通过居民小区遇到这种情况怎样处置？', '看到前方有皮球，就要联想到可能会有儿童跑出来捡球，而且距离较近，因此要停车避让。所以本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790154, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/74.jpg', '右侧标志警告前方是向右反向弯路。', '连续弯路-用以警告车辆驾驶人减速慢行。设置位置为连续弯路起点的外面，当连续弯路总长度大于500m时，应重复设置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790155, '加速从行人前通过', '从行人后绕行通过', '减速、鸣喇叭示意', '停车等待行人通过', '4', 'http://images.juheapi.com/jztk/subject4/75.jpg', '驾驶机动车在这个位置怎样安全通过？', '肯定是停车等待，顺便看MM！HOHO！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790157, '向左、向右转弯', '直行或向左转弯', '向左转弯', '直行或向右转弯', '4', 'http://images.juheapi.com/jztk/subject4/77.jpg', '这个路口允许车辆怎样行驶？', '左转是红灯，直行和右转是绿灯。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790158, '提前减速，注意机动车', '保持正常速度行驶', '鸣喇叭，迅速通过', '提前加速，快速通过', '1', 'http://images.juheapi.com/jztk/subject4/78.jpg', '驾驶机动车驶近前方主支干道交汇处要注意什么？', '由图可知，前方会有车辆从支路汇入主路，因此为了避免支路的车辆汇入时发生碰撞，所以要减速慢行，并注意周围的机动车，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790159, '正确', '错误', '', '', '2', '', '道路划设专用车道的，在专用车道内，其他机动车可以借道超车。', '我觉得专用车道指的是“机动车道、非机动车道、人行道”这些都属于专用车道，什么车走什么道，应该就不能借道超车啊，否则你有可能开到人行道上去借道超车！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790160, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/80.jpg', '驾驶机动车在这个路口可以直接向右转弯。', '遇到这样的情况，应当开启右转向灯，确认右侧没有非机动车接近时右转弯，避免发生碰撞，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790161, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/81.jpg', '右侧标志警告前方路段设有信号灯。', '注意信号灯-用以警告车辆驾驶人注意前方路段设有信号灯，应依信号灯指示行车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790162, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/82.jpg', '前方标志预告互通式立交桥通往方向的信息。', '互通式立交桥是城市里最常见的立交桥。互通式立交桥桥上面的道路与桥下面的道路有道路(即匝道)相联结，使上下道路上的车辆可以通行。互通式立交桥包括完全互通和部分互通两种形式，根据其平面上的形状，又有苜蓿叶形、菱形、环形、喇叭形立交桥等类型', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790163, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/83.jpg', '路中心黄色双实线指示可以暂时跨越超车。', '中心黄色双实线表示严格禁止车辆跨线超车或压线行驶,属禁止标线。用以划分上下行方向各有两条或两条以上机动车道而没有设置中央分隔带的道路。除交叉路口或允许车辆左转变（或回转）路段外，均应连续设置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790164, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/84.jpg', '右侧标志表示车辆按箭头示意方向选择行驶车道。', '图示为分向行驶车道标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790165, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/85.jpg', '右侧标志提醒前方路面不平。', '路面不平：用以提醒车辆驾驶人减速慢行。设在路面颠簸路段或桥头跳车较严重的地点以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790166, '靠路右侧转小弯', '靠弯路中心转弯', '借对向车道转弯', '靠路右侧转大弯', '4', 'http://images.juheapi.com/jztk/subject4/86.jpg', '在这种条件的道路上怎样安全行驶？', '由图可知，前方是向左急转弯道路，首先应当靠右侧行驶，靠左转的是小弯，靠右转的是大弯，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790167, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/87.jpg', '路面同向车行道分界线指示不允许跨越超车。', '实线不能跨越，故不能超越。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790168, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/88.jpg', '如下图，左转弯车辆不可以直接进入左转弯待转区，等待放行信号。', '直行的信号灯的意思就是你可以进入左转待转区域了，然后左转的灯变成绿灯就是你可以左转了，这样做也是为了提升道路通行效率。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790169, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/89.jpg', '道路右侧车行道边缘白色虚线指示允许跨越。', '如果是需要通过路口右转弯，是允许被跨越的，但是如果直行就不允许越线占非机动车道行驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790170, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/90.jpg', '右侧标志表示前方路口只能车辆向左转弯。', '向左转弯-表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790171, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/91.jpg', '右侧标志指示高速公路紧急电话的位置。', '此标志的意思是紧急电话号码。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790172, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/92.jpg', '右侧标志表示前方路口要停车让行。', '停车让行-表示车辆必须在停止线以外停车了望，确认安全后，才准许通行。停车让行标志在下列情况下设置：(1)与交通流量较大的干路平交的支路路口；(2)无人看守的铁路道口；(3)其他需要设置的地方。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790173, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/93.jpg', '该车道路面导向箭头提示前方道路需向左合流。', '此题无需解释，跟着箭头走就是向左合流。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790174, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/94.jpg', '图中标志提醒障碍物在路中，车辆从两侧绕行。', '左右绕行-用以告示前方道路有障碍物，车辆应按标志指示减速慢行，绕行通过。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790175, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/95.jpg', '路面白色虚实线指示变道或靠边停车时允许跨越。', '车行道边缘白色虚实线的虚线侧允许车辆越线行驶，实线侧不允许车辆越线行驶，用以规范车辆行驶轨迹。在必要的地点，如公交车站临近路段、允许路边停车路段等，可设置车行道边缘白色虚实线。跨线行驶的车辆，应避让其他正常行驶的车辆、非机动车辆和行人。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790176, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/96.jpg', '左侧标志警示前方道路右侧不能通行。', '这是靠右侧行驶标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790177, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/97.jpg', '右侧标志表示会车时对向车辆先行。', '此图是会车先行的意思，记住哥一句话，谁粗谁先行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790178, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/98.jpg', '驾驶机动车在这种情况下不能超车。', '有图可猜测前方是铁路道口，而根据《中华人民共和国道路交通安全法实施条例》规定，在铁路道口是不能超车的，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790179, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/99.jpg', '前方标志预告高速公路终点距离信息。', '标志的意思是告之道路前方要经过的重要的城镇的地名和距离。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790180, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/100.jpg', '右侧标志指示距离前方加油站入口200米。', '没问题，记住。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790181, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/101.jpg', '这个标志表示前方车道数量增加。', '图中是车道数变少标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790182, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/102.jpg', '右侧标志提醒前方道路正在施工。', '施工标志-用以告示前方道路施工，车辆应减速慢行或绕道行驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790183, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/103.jpg', '右侧标志表示最高车速不准超过每小时50公里。', '此图的标志为最低车速不低于50公里/每小时。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790184, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/104.jpg', '右侧标志提醒前方路段保持车距。', '事故易发路段-此标志设在交通事故易发路段以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790185, '及时减速停车让行', '鸣喇叭示意其让道', '加速从行人前通过', '开前照灯示意其让道', '1', 'http://images.juheapi.com/jztk/subject4/105.jpg', '驾驶机动车在路口遇到这种情况的行人怎么办？', '虽然横穿道路的行人算乱穿，但面对这样的情况驾驶员依旧要减速慢行，必要时停车避让，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790186, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/106.jpg', '前方标志告知向右100米为室内停车场。', '你看那个P字上边还有个遮挡，说明是室内的，如果没有上边那个遮挡就是露天停车场。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790187, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/107.jpg', '该车道路面导向箭头指示在前方路口仅可右转弯。', '图中的标志表示可以直行或者右转弯。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790188, '机动车占对向道行驶', '在弯道内急转转向盘', '在驶入弯道前不减速', '机动车靠路右侧行驶', '13', '', '机动车行驶至转弯路段时，易引发事故的驾驶行为有什么？', '选项A容易与对向来车发生擦碰，选项BC容易造成侧滑，严重的话会侧翻，因此本题选ABC。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107535790189, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/109.jpg', '驾驶机动车在这种信号灯亮的路口，可以右转弯。', '没有问题，只要安全的情况下就可以右转，前边的红灯是管直行的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790190, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/110.jpg', '右侧标志提醒前方经常有牲畜横穿、出入。', '注意野生动物-用以提醒车辆驾驶人注意慢行。设在经过野生动物保护区的公路上，经常有野生动物横穿、出入的地点前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790191, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/111.jpg', '前方标志指示路右侧是高速公路临时停车处。', '图示为紧急停车道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790192, 'T型交叉路口', 'Y型交叉路口', '十字交叉路口', '环行交叉路口', '4', 'http://images.juheapi.com/jztk/subject4/112.jpg', '右侧标志警示前方是什么路口？', '交叉路口-用以警告车辆驾驶人谨慎慢行，注意横向来车。设在平面交叉路口驶入路段的适当位置。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790193, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/113.jpg', '道路右侧白色虚线指示可越线变更车道。', '试题说的是可越线变更车道并没说右侧超车，白色虚线是可以变更车道的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790194, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/114.jpg', '路中黄色虚线指示任何情况都不允许越线绕行。', '黄色虚线是中心线，可以跨越；黄色单实线就已经不能跨越了；黄色双实线是严格禁止压线或者跨越', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790195, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/115.jpg', '驾驶机动车进入居民小区不能超过限速标志限定的速度行驶。', '由图可知，进入此小区车速限制在5公里/小时，也就是说不能超过5公里/小时，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790196, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/116.jpg', '右侧标志告知右前方100米是应急避难场所。', '此适标志用于突发公共事件状态下，供居民紧急疏散、安置的应急避难场所的标志设置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790197, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/117.jpg', '右侧标志警告距前方有人看守铁路道口150米。', '此标志为前方100米处无人看守铁道路口。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790198, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/118.jpg', '右侧标志表示鸣喇叭提醒。', '右侧标志是人行横道的意思。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790199, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/119.jpg', '驾驶机动车看到这个标志时要及时减速。', '如图前方路段要注意儿童，因此为了避免有儿童突然穿出，要减速慢行，必要时停车避让，所以本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790200, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/120.jpg', '允许沿着图中箭头方向驶入高速公路行车道。', '不能跨越左边的白色实线。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790201, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/121.jpg', '路中黄色斜线填充标记警告前方有固定性障碍物。', '用以指示路面有固定性障碍物，警告车辆驾驶人谨慎行车，引导交通流顺畅驶离障碍物区域。接近障碍物标线的颜色，应根据障碍物所在的位置，与对向车行道分界线或同向车行道分界线的颜色一致。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790202, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/122.jpg', '右侧标志警告前方路段要注意儿童。', '不要被标志下边的学校误导，题目问的是标志的意思，注意儿童-用以警告车辆驾驶人减速慢行，注意儿童。设在小学、幼儿园、少年宫等儿童经常出入地点前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790203, '沿直行车道左转', '进入左转弯待转区', '进入直行等待区', '沿左车道左转弯', '2', 'http://images.juheapi.com/jztk/subject4/123.jpg', '驾驶机动车在这个路口怎样左转弯行驶？', '路口施划了左转弯待转区，因此左转弯时应当先进入左转弯待转区后再左转，而且此时是直行车道的绿灯信号。因此应当在左转弯待转区等待，所以本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790204, '禁止车辆在两侧车道通行', '减速进入两侧车道行驶', '进入右侧车道行驶', '加速进入两侧车道行驶', '1', 'http://images.juheapi.com/jztk/subject4/124.jpg', '遇到这种情况时怎样行驶？', '俩侧车道都是红灯，都不可以通行，所以是禁止车辆在两侧车道通行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790205, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/125.jpg', '右侧标志提示一切车辆都不能驶入。', '禁止驶入-表示禁止车辆驶入。此标志设在禁止驶入的路段入口处。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790206, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/126.jpg', '右侧标志提示前方200米是车距确认路段。', '白色折线车距确认线：白色折线车距确认线总宽300cm,线条宽40cm或45cm，从确认基点0m开始，每个5米设置一道，两道为一组，间隔50米,重复设置五组，也可根据现场设置多组。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790207, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/127.jpg', '在这个区域内不允许长时间停放机动车。', '如图是禁止长时间停车标志，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790208, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/128.jpg', '在学校门口遇到这种情况要做好随时停车的准备。', '如图是前方路段是学校要注意儿童。因此此时要减速慢行，必要时停车。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790209, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/129.jpg', '右侧标志指示前方设有避让来车的处所。', '这个不常见，看见这题选对就好！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790210, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/130.jpg', '这个标志指示高速公路交通广播和无线电视频道。', '图示为雷达测速监控。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790211, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/131.jpg', '如下图，左转弯车辆可直接进入左转弯待转区，等待放行信号。', '直行的信号灯是红色是禁止进入左转待转区域的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790212, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/132.jpg', '右侧标志提醒前方路交口向右100米是渡口。', '渡口：用以提醒车辆驾驶人谨慎驾驶。设在车辆渡口以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790213, '迅速加速行驶', '紧跟前车行驶', '靠右加速行驶', '靠右减速让行', '4', 'http://images.juheapi.com/jztk/subject4/133.jpg', '驾驶机动车遇到这种情况怎样礼让？', '只有D最和谐，快选它吧。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790214, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/134.jpg', '这个标志表示前方路段不允许超车。', '解除禁止超车-表示禁止超车路段结束。此标志设在禁止超车的终点。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790215, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/135.jpg', '右侧标志警告前方是向右急转弯路。', '向右急弯路-用以警告车辆驾驶人减速慢行。设置位置为曲线起点的外面，但不应进入相邻的圆曲线内。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790216, '正确', '错误', '', '', '2', '', '机动车遇有急弯路时要在进入弯路后减速。', '遇到急弯路应当提前减速慢行，等到进入弯路后减速很容易发生侧滑或侧翻。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790217, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/137.jpg', '路面上的出口标线用于引导驶出该高速公路。', '高速公路出入口标线为白色虚线，主要用于高速公路和其他采用立体交叉道路。该标线作用为，高速公路出入口横向标线或三角地带标线是为驶入或驶出匝道车辆提供安全交汇引导，减少与突出部缘石碰撞。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790218, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/138.jpg', '右侧标志警示前方道路两侧不能通行。', '这是两侧通行标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790219, '正确', '错误', '', '', '2', '', '机动车倒车时，后方道路条件较好的，应加速倒车，迅速完成操作。', '《中华人民共和国道路交通安全法实施条例》第五十条  机动车倒车时，应当察明车后情况，确认安全后倒车。不得在铁路道口、交叉路口、单行路、桥梁、急弯、陡坡或者隧道中倒车。因此本题错误。另：施主别那么迅速啊， 安全第一！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790220, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/140.jpg', '红色圆圈内标志表示左侧道路只供小型车行驶。', '红色圆圈内标志是机动车行驶车道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790221, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/141.jpg', '右侧标志表示前方是分流路口。', '图中是车道数增加标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790222, '货车超载', '货车违法超车', '客车超员', '客车驾驶人疲劳驾驶', '13', '', '石某驾驶低速载货机动车，运载4.05吨货物（核载1.2吨），行驶至宁津县境内314省道51公里加260米处，在越过道路中心线超越前方同向行驶的机动车时，与对向正常行驶的中型客车（乘载12人，核载11人）正面相撞，造成10人死亡、2人受伤。此事故中的违法行为是什么？', '易错题，超员超载都清楚，但是违规超车得留点心，意思是货车师傅在超车时发现对向有驶来的车时，应该立即停止超车。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107535790223, '正确', '错误', '', '', '2', '', '夜间驾驶机动车在照明条件良好的路段可以不使用灯光。', '用近光灯不会照得对方看不见路，同时为了安全起见，开启近光灯能够让相互间清晰可见。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790224, '正确', '错误', '', '', '1', '', '安装防抱死制动装置（ABS）的机动车紧急制动时，可用力踏制动踏板。', 'ABS技术的出现，就是大大地降低了侧滑，和原地掉头的现象，保证了行驶的安全。如果，你在使用带ABS的车辆，在刹车时的动作，类似不带ABS的车这样轻轻的踩下去，很可能会不能触发ABS系统，也就起不到ABS的作用了，所以，一般就是要求，刹车动作要果断，一脚就踩到底，保证ABS的正确执行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790225, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/145.jpg', '路面中心黄虚线指示在保证安全的情况下可以越线超车。', '黄色虚线是中心线，可以跨越；黄色单实线就已经不能跨越了；黄色双实线是严格禁止压线或者跨越', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790226, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/146.jpg', '驾驶机动车在这种情况下要加速冲过坡顶。', '根据图来看，只能知道前方是一个制高点，但对于前方是平路还是下坡路并不清楚，因此不能加速通过。所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790227, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/147.jpg', '右侧标志表示只能车辆向右转弯。', '向右转弯-表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790228, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/148.jpg', '路面上菱形标识预告前方道路设置人行横道。', '道路路面上的白色菱形图案，是中华人民共和国国家标准（GB5768/1999）《道路交通标志和标线》中新设置的一种交通标线，正式名称为人行横道预告标线。这种标线在到达人行横道前的道路30-50米处设置，它的作用是用来提示驾驶员，前方已接近人行横道，应减速慢行，并须注意行人横过马路', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790229, '沿直行车道右转弯', '停止线前停车等待', '沿右侧道路右转弯', '借非机动车道右转', '3', 'http://images.juheapi.com/jztk/subject4/149.jpg', '驾驶机动车在这个路口怎样右转弯行驶？', '车辆右转弯不受此红灯信号限制，因此只要沿右侧道路右转弯即可。所以本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790230, '靠路中心行驶', '靠路右侧行驶', '在路中间行驶', '靠路左侧行驶', '2', 'http://images.juheapi.com/jztk/subject4/150.jpg', '驾驶机动车在这种道路上怎样会车最安全？', '《中华人民共和国道路交通安全法实施条例》第四十八条  在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（一）减速靠右行驶，并与其他车辆、行人保持必要的安全距离；因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790231, '停车观察路口情况', '加速尽快进入路口', '减速观察左后方情况', '减速缓慢进入路口', '4', 'http://images.juheapi.com/jztk/subject4/151.jpg', '驾驶机动车在有这种标志的路口怎样通过最安全？', '如图交通标志是减速让行标志，因此在此路段要减速慢行，应当注意的还有前方情况，因此本题选D，不能选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790232, '选择下坡路段停放', '选择上坡路段停放', '选择平缓路段停放', '选择坡顶位置停放', '3', '', '驾驶机动车在山区道路因故障停车需要注意什么？', '停车时要尽可能选择安全的地点停车，而不管是上坡路、下坡路或是坡顶都有可能让所停车辆发生溜车，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790233, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/153.jpg', '路缘石的黄色实线指示路边允许临时停、放车辆。', '黄色虚线是允许临时，全黄的就禁止了。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790234, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/154.jpg', '隧道上方标志表示限制高度3.5米。', '表示禁止装载高度超过标志所示数值的车辆通行。此标志设在最大允许高度受限制的地方。 以图为例：装载高度不得超过3.5米。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790235, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/155.jpg', '路口内人行横道线警示行人优先横过道路。', '请记住，无论任何时候都是行人优先。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790236, '直接进入路口内等待', '在路口停止线外等待', '从右侧非机动车道通过', '借对向车道通过路口', '2', 'http://images.juheapi.com/jztk/subject4/156.jpg', '驾驶机动车在交叉路口遇到这种情况如何对待？', '虽然前边是绿灯，但是前边已经堵到路口，你在过去直接把横向的车道也给堵了，还是和谐一点在路口停止线外等待吧。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790237, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/157.jpg', '路中心黄色实虚线指示允许超车时越过。', '无论单黄线还是双黄线，只要是实线，就严禁跨越的，比如超车、转弯、掉头等。行车时没有特别情况就不应该越线。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790238, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/158.jpg', '发生该事故的主要原因是驾驶人倒车前没有进行安全确认。', '倒车没多久就撞到了后方的障碍物，这说明驾驶员在到车前没有了解清楚后方的情况。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790239, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/159.jpg', '驾驶机动车在这种道路上从前车右侧超越最安全。', '超车要从左侧超车，根据如图的情况来看，还是放弃超车比较好。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790240, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/160.jpg', '右侧标志警告距前方有人看守铁路道口100米。', '此标志为前方50米处无人看守铁道路口。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790241, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/161.jpg', '在这个区域内可以临时停车。', '如图是禁止临时或长时停车标志，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790242, '正确', '错误', '', '', '1', '', '驾驶机动车遇到校车在道路右侧停车上下学生，同向有两条机动车道时，左侧车道后方机动车应当停车等待。', '校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越。校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790243, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/163.jpg', '驾驶机动车通过这段山区道路要靠路中心行驶。', '根据如图交通标志来看，前方道路是连续急弯路，因此要靠右侧行驶，以免前方弯道突然出现来车。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790244, '正确', '错误', '', '', '1', '', '机动车涉水后，驾驶人要间断轻踩制动踏板，以恢复制动效能。', '通过积水路面时，会进水，若持续轻踏制动踏板，磨擦后会产生污物，粘附在轮鼓内后会影响制动效果。 因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790245, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/165.jpg', '这个标志提示该路段已实行交通监控。', '交通监控设备标志属于指路标志，蓝底、白图形、白边框、蓝色衬边。用于告知机动车驾驶人该区域设置有固定式交通监控设备。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790246, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/166.jpg', '驾驶机动车在这种道路上超车可借对向车道行驶。', '黄色单实线是不能越线超车的，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790247, '行人从车后穿出', '行人从车前穿出', '公交车突然倒车', '公交车突然起步', '2', 'http://images.juheapi.com/jztk/subject4/167.jpg', '驾驶机动车在这种情况下注意什么？', '由图可知，公交车前方有一名撑伞的女士，因此要考虑到可能这名女士会横穿，所以本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790248, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/168.jpg', '高速公路上的白色折线为行车中判断行车速度提供参考。', '白色折线车距确认线：白色折线车距确认线总宽300cm,线条宽40cm或45cm，从确认基点0m开始，每个5米设置一道，两道为一组，间隔50米,重复设置五组，也可根据现场设置多组。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790249, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/169.jpg', '交通警察发出这种手势信号时要减速慢行。', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790250, '换入空档，滑行通过', '一停、二看、三通过', '加速、观察、快通过', '减速、观察、慢通过', '2', 'http://images.juheapi.com/jztk/subject4/170.jpg', '驾驶机动车怎样通过这个铁路道口？', '由图可知，前方是既没有交通信号灯也没有管理人员看守的铁路道口，在这样的情况下若想要通过，就要一停二看三通过，以确保安全。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790251, '醉酒驾驶', '超速驾驶', '疲劳驾驶', '肇事逃逸', '14', '', '林某驾车以110公里/小时的速度在城市道路行驶，与一辆机动车追尾后弃车逃离被群众拦下。经鉴定，事发时林某血液中的酒精浓度为135.8毫克/百毫升。林某的主要违法行为是什么？', '违法行为一：以110公里/小时的速度在城市道路行驶违法行为二：与一辆机动车追尾后弃车逃离违法行为三：林某血液中的酒精浓度为135.8毫克/百毫升因此本题选ABD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107535790252, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/172.jpg', '路面白色虚线实线指示实线一侧允许跨越。', '虚线一侧可以越线，您所在的这个车道是不可以的，因为是实线。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790253, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/173.jpg', '驾驶机动车可在该路口处向右变更车道。', '因为已经到路口了，路口150米内不得变更车道。他停在左侧道，只能直行或左转。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790254, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/174.jpg', '前方标志预告交叉路口通往方向的信息。', '此为交叉路口预告标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790255, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/175.jpg', '驾驶机动车在这个路段允许超车。', '如图是禁止超车标志，因此不能超车，所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790256, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/176.jpg', '驾驶机动车遇到这种情况要主动减速让后车超越。', '由图可知，后车有超车意图，根据本车的情况，应当予以让行，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790257, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/177.jpg', '路缘石上的黄色虚线指示路边不允许停车上下人员或装卸货物。', '应该是允许短时停车和卸货。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790258, '紧随前车之后', '加大安全距离', '减小纵向间距', '尽快超越前车', '2', '', '驾驶机动车在山区道路怎样跟车行驶？', '山区道路复杂多变，因此跟车时也要非常注意，因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790260, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/180.jpg', '因故障在山区上坡路段长时间停车时，要用这种办法塞住车轮。', '停在如图的上坡路面上，容易后溜，如图做法并不能防止车辆后溜，正确的做法是在轮胎后面塞住。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790261, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/181.jpg', '左侧标志提示前方收费口停车领卡。', '这个没问题，图片显示的很清楚，有个手拿卡，简直就是看图说话。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790262, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/182.jpg', '驾驶机动车遇到这种情况要迅速向左变更车道。', '由图可知，左侧后方有来车，所以为了避免发生碰撞及影响其他车辆正常通行，因此就算要变更车道也应该等后车通过后再变更。正确的做法是，提前开启转向灯，确保左侧车道后方没有车辆临近时向左变更车道。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790263, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/183.jpg', '该车道路面导向箭头指示前方道路仅可左右转弯。', '嗯，看路上的标志，是仅可以左转右转的意思。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790264, '连续鸣喇叭', '快速通过', '减速慢行', '从一侧绕行', '3', 'http://images.juheapi.com/jztk/subject4/184.jpg', '驾驶机动车遇到这样的情景怎样行驶？', '遇到这样的情况要减速慢行，必要时停车避让，以免孩子们突然跑动被撞伤，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790265, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/185.jpg', '右侧标志警告前方道路是向左连续弯路。', '向左急弯路-用以警告车辆驾驶人减速慢行。设置位置为曲线起点的外面，但不应进入相邻的圆曲线内。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790266, '超速行驶', '不按规定会车', '疲劳驾驶', '不按规定使用灯光', '14', '', '周某夜间驾驶大货车在没有路灯的城市道路上以90公里/小时的速度行驶，一直开启远光灯，在通过一窄路时，因加速抢道，导致对面驶来的一辆小客车撞上右侧护栏。周某的主要违法行为是什么？', '第一，夜间没有路灯的情况下90公里/小时，这个肯定是是超速啊，高速上这个速度还可以，没有路灯就危险了。第二，通过窄路时，应该会车减速行驶。第三，应该交替使用近远光灯。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107535790267, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/187.jpg', '右侧标志表示不允许长时鸣喇叭。', '禁止鸣喇叭-表示禁止鸣喇叭。此标志设在需要禁止鸣喇叭的地方。禁止鸣喇叭的时间和范围可用辅助标志说明。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790268, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/188.jpg', '前方标志告知各个路口出口方向的信息。', '前方标志告知各个路口出口方向的信息，此题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790269, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/189.jpg', '看到这种手势信号时可以向左转弯。', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790270, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/190.jpg', '右侧标志警告前方是连续下坡路段。', '下陡坡-用以提醒车辆驾驶人小心驾驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790271, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/191.jpg', '驾驶机动车在该位置不能变更车道。', '由图可知，前方是实线区，实线区不得变更车道，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790272, '从前车两侧超越', '紧跟前车后停车', '保持大距离停车', '连续鸣喇叭提示', '3', '', '山区上坡路段跟车过程中遇前车停车时怎么办？', '上坡路段停车的车辆容易出现后溜的情况，因此后车在跟车时要加大跟车距离，确保安全，所以本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790273, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/193.jpg', '前方标志预告距离高速公路入口1公里。', '没问题，记住吧。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790274, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/194.jpg', '右侧标志表示此处不准鸣喇叭。', '右侧标志是表示此处要鸣喇叭的意思。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790275, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/195.jpg', '驾驶机动车向左变更车道遇到这种情况要注意让行。', '如图情况时，车辆若是想要向左变更车道，就应当让左侧车辆通过后再变道，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790276, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/196.jpg', '右侧标志指示前方收费站设有电子不停车收费行驶车道。', 'ETC车道就是电子不停车收费行驶车道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790277, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/197.jpg', '右侧标志提示距离设有电子不停车收费车道的收费站1公里。', '标志的意思是人工收费站，不是电子不停车收费车道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790278, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/198.jpg', '交通警察发出这种手势可以向左转弯。', '警察的手势不是给你看的，没看人家没看你，看的是右侧。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790279, '正确', '错误', '', '', '2', '', '预计在超车过程中与对面来车有会车可能时，应提前加速超越。', '预计在超车过程中与对面来车有会车可能时，应当放弃超车。若加速超越，有可能会引起不必要的事故。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790280, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/200.jpg', '路面标记指示这段道路上最高限速为50公里/小时。', '限速标志为50公里/小时，所以此题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790281, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/201.jpg', '路右侧白色矩形虚线框内表示允许长时间停车。', '虚线短时停车，实线长时停车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790282, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/202.jpg', '驾驶机动车在学校附近遇到这种情况要尽快加速通过。', '遇到这种情况，要预防有学生在道路上跑动等行为，且前方有人行横道线，因此要减速慢行，必要时停车，所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790283, '经左弯待转区进行掉头', '在路口虚线处进行掉头', '左转信号灯亮时方可掉头', '直行信号灯亮时方可掉头', '2', 'http://images.juheapi.com/jztk/subject4/203.jpg', '驾驶机动车在前方路口怎样掉头？', '在左转待转区是要左转的车辆，与掉头车辆无关。选项C错在方可二字，因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790284, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/204.jpg', '前方标志告知前方道路各行其道的信息。', '不是各行其道，是各方向终点信息。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790285, '靠中心线行驶', '开前照灯行驶', '向路右侧避让', '向车左侧避让', '3', 'http://images.juheapi.com/jztk/subject4/205.jpg', '在这种情况下怎样会车最安全？', '遇到前方车辆越线占道的，应当靠右侧行驶，避免发生碰撞，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790286, '从前车左侧超越', '占对向车道超越', '从前车右侧超越', '跟在前车后行驶', '4', 'http://images.juheapi.com/jztk/subject4/206.jpg', '驾驶机动车在这种情况下怎样做？', '遇到这种情况时，不能超车，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790287, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/207.jpg', '右侧标志表示前方路口不准车辆右转。', '禁止向右转弯-表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790288, '易发生侧滑', '能见度低', '行驶阻力大', '视野变宽', '2', '', '雾天对安全行车的主要影响是什么？', '雾天的能见度很低，容易造成视野不良。而易发生侧滑的应该是滑溜的路面，行驶阻力大这一点更无从得知。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790289, '开远光灯', '交替使用远近光灯', '开近光灯', '开危险报警闪光灯', '2', 'http://images.juheapi.com/jztk/subject4/209.jpg', '夜间驾驶机动车遇到这种情况怎样超车？', '《中华人民共和国道路交通安全法实施条例》第四十七条  机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790290, '正确', '错误', '', '', '2', '', '夜间驾驶汽车在急弯道超车时要不断变换远、近光灯。', '急转弯道你还超车，老实在后边跟着吧。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790291, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/211.jpg', '驾驶机动车在对向没有来车的情况下可以超车。', '如图所示，前方道路是连续急弯路，在这样的路段是不能超车的。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790292, '挂入空挡滑行', '踏下离合器滑行', '利用发动机制动', '持续踏制动踏板', '3', '', '下长坡控制车速最安全的方法是什么？', '动机制动有三个显著特点：一是由于差速器的作用，可将制动力矩平均地分配在左右车轮上，减少侧滑、甩尾的可能性；二是有效地减少脚制动的使用频率，避免因长时间使用制动器，导致制动器摩擦片的温度升高，使制动力下降，甚至失去作用；三是车速始终被限定在一定范围内，有利于及时降速或停车，确保行车安全。因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790293, '尽快加速超越前车', '主动减速放弃超车', '鸣喇叭让前车让路', '开前照灯让对向让速', '2', 'http://images.juheapi.com/jztk/subject4/213.jpg', '驾驶机动车遇到这种情况怎样做最安全？', '本来在这样的道路上是可以超车的，但由于对向有来车，因此避免发生擦碰，要放弃超车。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790294, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/214.jpg', '驾驶机动车在这种情况下临时停车后，为避免机动车后溜可将转向盘向左转。', '这样做，与在轮胎后方塞东西是同样的道理，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790295, '疲劳驾驶', '酒后驾驶', '客车超员', '超速行驶', '3', '', '徐某驾驶一辆中型客车（乘载27人）行至四都镇前岭村壶南头路段，在上坡过程中，机动车发生后溜驶出路外坠入落差约80米的山崖，造成11人死亡、7人受伤。徐某的主要违法行为是什么？', '中型客车核载10人以上19人以下', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790296, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/216.jpg', '左侧标志警告前方注意左侧路口有汇入车辆。', '请记住。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790297, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/217.jpg', '驾驶机动车遇到这种情况时对向机动车优先通过。', '根据规定，有障碍的一方要让无障碍的一方先行，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790298, '安装防滑装置', '提高车速行驶', '降低车速行驶', '断续使用制动', '3', '', '大雨天在高速公路行车时，怎样避免发生“水滑”现象？', '水滑就是因为雨天雨水多了，使轮胎与路面的摩擦系数变小了，就容易侧滑。因此降低车速就能减少水滑发生的可能，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790299, '保持正常车速行驶', '高速行驶避开灯光', '减速或停车让行', '开启远光灯对射', '3', 'http://images.juheapi.com/jztk/subject4/219.jpg', '夜间驾驶机动车遇到这种情况时怎样处理？', '由图可知，对向来车开着远光灯，此时不要意气用事，而是要为了安全考虑，要减速或停车让行，避免会车时发生意外。因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790300, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/220.jpg', '驾驶机动车在这种情况下可以越过中心实线超车。', '中心实线不能越线超车，而且在前方有障碍物的情况，应当避免超车。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790301, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/221.jpg', '交通警察发出这种手势信号可以左转弯。', '图为左转弯待转信号。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790302, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/222.jpg', '驾驶机动车通过这种傍山险路要靠右侧行驶。', '在这种道路上行驶要靠右行驶，以策安全，同时以免对向有来车无法及时避开。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790303, '及时减速让行，必要时靠边停车', '开启远光灯，迫使来车变换灯光', '视线向右平移，防止眩目', '交替变换远近光灯，提醒来车', '15', '', '夜间会车时，对面来车不关闭远光灯怎么办？', '变身圣人吧（PS：您就交替变换远近光灯，闪死丫的）', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107535790304, '正确', '错误', '', '', '1', '', '机动车在路边起步后，应随时注意机动车两侧道路情况，向左缓慢转向，逐渐驶入正常行驶道路。', '一看二慢三汇入，这样做既出于安全考虑，同时也不影响其他车辆的正常行驶。因此本题选A。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790305, '正确', '错误', '', '', '1', '', '涉水驾驶要保持车速均匀有足够动力，避免停留。', '若是动力不够，很有可能在水中停下来，停下来之后很容易熄火。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790306, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/226.jpg', '驾驶机动车在山区道路遇到这种情况要加速超越前车。', '在如图的山区道路上行驶，遇到前方出现这样的情况，不能超车，因为前方有弯道，而且受前车影响无法清楚掌握路况，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790307, '靠左侧减速让行', '靠右侧减速让行', '加速靠左侧让行', '保持原行驶路线', '1', 'http://images.juheapi.com/jztk/subject4/227.jpg', '驾驶机动车遇到这种特殊情况怎样行驶？', '由于超车的是执行公务的警车，因此要减速慢行。另外，由于警车是右侧穿出的，因此只能靠左侧避让。所以本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790308, '开启远光灯', '开启前后雾灯', '只能开启左转向灯', '长时间鸣喇叭', '2', 'http://images.juheapi.com/jztk/subject4/228.jpg', '在这种气象条件下起步要注意哪些方面？', '前面都有雾了，那不开雾灯开什么灯？', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790309, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/229.jpg', '交通警察发出的是禁止通行手势信号。', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790310, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/230.jpg', '路口内中心圈标示左小转弯要沿内侧行驶。', '亲，这都是规定的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790311, '妨碍安全驾驶', '可提高注意力', '可缓解驾驶疲劳', '不影响驾驶操作', '1', '', '驾驶人边驾车边吸烟的做法有什么影响？', '这样做不仅妨碍驾驶，也会分散驾驶人的注意，另外缓解疲劳没有根据，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790312, '服务区', '加速车道', '减速车道', '匝道', '1', '', '在高速公路行车选择什么地方停车？', '驾驶员在高速公路上不能随意靠边停车，而是要停靠在紧急停车带里面，不是应急车道，没有紧急情况占用应急车道是违反高速公路管理规定的，所以要停车就应把车停在紧急停车带或服务区休息。即本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790313, '迅速从车左侧超越', '保持较大跟车距离', '连续鸣喇叭告知', '迅速从车右侧超越', '2', 'http://images.juheapi.com/jztk/subject4/233.jpg', '驾驶机动车遇到这种情形怎么办？', '由图可知，前方车辆从左侧超越后有一个曲线行驶的过程，遇到这种情况应当保持较大的距离，以策安全。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790314, '正确', '错误', '', '', '1', '', '出车前检查冷却液、发动机机油、燃油等是否有渗漏现象。', '确保出行前一定都OK。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790315, '应当在规定地点停放', '禁止在人行道上停放', '在道路上临时停车时，不得妨碍其他机动车和行人通行', '可以停放在非机动车道上', '4', '', '机动车停车的错误做法是什么？', '注意本题考的是错误的做法，机动车不能停放在非机动车道上，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790316, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/236.jpg', '交通警察发出这种手势信号时可以直行通过。', '准许右方的车辆右转弯面向一方代表告知，伸手拦截告知的一方就不能直行，右手斜下告知可以右转。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790317, '正确', '错误', '', '', '2', '', '在雪天临时停车要开启前照灯和雾灯。', '雪天在道路上临时停车，应当开启危险报警闪光灯，用以提醒其他车辆注意。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790318, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/238.jpg', '会车遇到这种情况要低速会车或停车让行。', '由图可知，前方是窄桥，要避免在窄桥上会车，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790319, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/239.jpg', '驾驶机动车遇到这种情形要迅速靠右侧减速让行。', '由图可知，前方车辆为执行公务的消防车，因此遇到这种情况要减速靠右慢行进行避让。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790320, '正确', '错误', '', '', '1', '', '扑救易散发腐蚀性蒸气或有毒气体的火灾时，扑救人员应穿戴防毒面具和相应的防护用品，站在上风处施救。', '由于是“易散发腐蚀性蒸气或有毒气体的火灾”，因此既要做到不中毒，也要做到不被烫伤等，因此在扑救时，要穿戴防毒面具和相应的防护用品，这是为了防止中毒，而站在上风处施救，是因为不会因火势被伤到。因此本题是对的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790321, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/241.jpg', '右侧标志指示前方是T型路口。', '应该是此路不通。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790322, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/242.jpg', '路右侧黄色矩形标线框内表示允许临时停车。', '这么长的简易停车应该是公交车专用停车下客上车的，所以小车不能去停。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790323, '正确', '错误', '', '', '2', '', '驾驶机动车在冰雪路面发生侧滑时，要猛打方向调整。', '此时若是转向调整，容易造成更大的状况，因此正确的做法应该是应降低车速，利用发动机制动进行减速。所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790324, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/244.jpg', '交通警察发出的是左转弯待转手势信号。', '减速慢行的手势是右手，此图是左转弯待转手势信号。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790325, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/245.jpg', '驾驶机动车遇到这种道路，可充分利用空挡滑行。', '根据如图的交通标志来看，是一个连续下坡路，在这种路况下不能空挡滑行，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790326, '正确', '错误', '', '', '1', '', '机动车在高速公路意外撞击护栏时，要稳住方向，适当修正，切忌猛转转向盘。', '此时猛转转向盘很有可能造成车子侧滑或侧翻，造成更大的损伤。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790327, '尽快加速超越前车', '鸣喇叭让前车让路', '保持距离跟车行驶', '从前车的右侧超越', '3', 'http://images.juheapi.com/jztk/subject4/247.jpg', '驾驶机动车在这种道路上怎样行驶最安全？', '在这样的交通状况下，不能超车，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790329, '空挡滑行通过', '保持高速通过', '适当加速通过', '低速缓慢通过', '4', 'http://images.juheapi.com/jztk/subject4/249.jpg', '驾驶机动车在这样的路面如何安全行驶？', '由图中交通标志可知前方是路面不平的道路，因此要减速慢行，避免颠簸等，所以本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790330, '观察', '包扎', '止血', '询问', '3', '', '抢救失血伤员时，要先采取什么措施？', '既然是失血伤员，首先要做的当然是止血。因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790331, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/251.jpg', '驾驶机动车遇到这种道路要提前减速减挡，利用发动机制动控制速度。', '看右侧的标志，此处为下陡坡，故要减速慢行，但是不能空挡滑行所以要利用发动机制动控制速度。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790332, '开启前照灯、示廓灯和后位灯', '高频率鸣喇叭使其他交通参与者知道自己的位置', '同向跟车较近时，应使用远光灯', '适当提高车速，尽快到达目的地，结束行车', '1', '', '驾驶机动车遇到沙尘、冰雹、雾、雨、雪等低能见度条件时，应该怎样做？', '', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790333, '靠边减速或停车让行', '占用其他车道行驶', '加速变更车道避让', '在原车道内继续行驶', '1', '', '行车中遇抢救伤员的救护车从本车道逆向驶来时，要怎样做？', '遇到正在执行任务的特种车辆，应当予以让行。而本题的救护车正是因为抢救伤员而逆行，因此其他车辆要给予方便，为其让行。因此正确的做法是靠边减速或停车让行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790334, '开启远光灯', '只能开启左转向灯', '开启左转向灯、近光灯', '开启危险报警闪光灯', '3', 'http://images.juheapi.com/jztk/subject4/254.jpg', '在这种环境中安全起步怎样使用灯光？', '夜间起步应开启近光灯，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790335, '客车超员', '客车超速行驶', '货车超载', '货车驾驶人经验不足', '10', '', '戚某驾驶大客车，乘载28人（核载55人），由南向北行至一无交通信号控制的交叉路口，以50公里的时速与由东向西行至该路口李某驾驶的重型半挂牵引车（核载40吨，实载55.2吨）侧面相撞，造成12人死亡、17人受伤。此事故中的主要违法行为是什么？', '核载=就是设计的能乘载的人数或者吨数。理论值，标准值。乘载=就是实际乘载的人数，实际值。交叉路口，以时速50公里的速度是超速。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107535790336, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/256.jpg', '如图所示，遇有这种情况要停车让行。', '看到一个自行车前轮没，要让行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790337, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/257.jpg', '道路右侧白色实线标示机动车道与人行道的分界线。', '这是机动车与非机动车的分界线，看旁边有非机动车标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790338, '正确', '错误', '', '', '1', '', '泥泞路对安全行车的影响是车轮极易空转和侧滑。', '因为泥泞路面很易滑，因此车轮容易空转或是侧滑，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790339, '减速让非机动车先行', '连续鸣喇叭告知让道', '从非机动车左侧绕过', '占对向车道加速超越', '1', 'http://images.juheapi.com/jztk/subject4/259.jpg', '驾驶机动车遇到这种情况怎样行驶？', '遇到这种情况时要减速，并让非机动车先通过，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790341, '连续鸣喇叭提示', '开远光灯尾随行驶', '保持距离等待让行', '连续变换前照灯远、近光', '3', '', '夜间驾驶机动车超车遇前车不让路时怎样处置？', '遇到前车不让路时，只能等到前车让路了才能超车，否则容易发生危险，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790342, '在路口向左转弯', '停车等待', '在路口直行', '进入左弯待转区', '2', 'http://images.juheapi.com/jztk/subject4/262.jpg', '看到这种手势信号时怎样行驶？', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790343, '在最外侧行车道上停车', '在内侧行车道上停车', '在应急车道停车', '在匝道口三角地带停车', '3', '', '机动车在高速公路上发生故障需检查时怎样停车？', '高速路最右的道路就是紧急停车道，也就是应急车道。应急车道就是专门在车子发生故障或事故时停靠的。 因此选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790344, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/264.jpg', '路面菱形块虚线警告前方道路要减速慢行。', '车行道纵向减速标线为一组平行于车行道分界线的菱形块虚线。在车行道纵向减速标线的起始位置，设置30 m的渐变段，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790345, '正确', '错误', '', '', '2', '', '驾驶机动车在大雾天临时停车后，只开启雾灯和近光灯。', '雾天停车要开危险报警灯。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790346, '前后防雾灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '危险报警闪光灯、示廓灯和后位灯', '4', '', '机动车在雪天临时停车时，应开启什么灯？', '报警灯就是前后两黄灯的一亮一灭，四个灯是同时的。示廓灯就是指表示整车轮廓的，在车的最边最高处，一般是红色小灯。后位灯一般是指让后面车辆看到本车而使用的，一般是红色，晚上打开车灯时发亮。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790347, '正确', '错误', '', '', '1', '', '救火时不要张嘴呼吸或高声呐喊，以免烟火灼伤上呼吸道。', '这样做能避免吸入有害气体或被烟呛着。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790348, '连续鸣喇叭警告', '加速从前方绕过', '出现危险再减速', '主动减速让行', '4', 'http://images.juheapi.com/jztk/subject4/268.jpg', '驾驶机动车遇到这种情况怎样应对？', '虽然是绿灯可以行驶，但由于前方有非机动车横穿，为了安全，要注意避让，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790349, '等行人通过后再起步', '起步从行人前方绕过', '鸣喇叭告知行人让道', '起步后缓慢靠近行人', '1', 'http://images.juheapi.com/jztk/subject4/269.jpg', '驾驶机动车在这种情况下怎样礼让行人？', '虽然是绿灯，但也要等行人通过以后再行驶，以免发生意外。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790350, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/270.jpg', '机动车在这种情况下可以超车。', '根据《中华人民共和国道路交通安全法实施条例》的规定，行经繁华路段、交叉路口、铁路道口、人行横道、急弯路、宽度不足4米的窄路或者窄桥、陡坡、隧道或者容易发生危险的路段，不得超车。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790351, '正确', '错误', '', '', '2', '', '冰雪路面处理情况不能使用紧急制动，但可采取急转向的方法躲避。', '冰雪路面行驶过程中不能采取紧急制动或是急转方向的方法，这是因为冰雪路面易滑，这样做容易造成车子侧滑或侧翻。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790353, '用沙土覆盖火焰灭火', '向身上喷冷水灭火', '用灭火器进行灭火', '帮助脱掉燃烧的衣服', '2', '', '救助全身燃烧伤员采取哪种应急措施？', '先大家要明确，燃烧过后人体皮肤已经受损因此若是用A的方法，且不说沙土上附着的病原体会造成严重感染，事后的清理和后续治疗也会相当麻烦。而选项B，除了能够给创面降温以外，还有可能是清洁伤口，救治时限可以放宽到12小时。而选项C，则很有可能灭火器喷出的东西会造成身体上的伤害，因此不妥当。选项D，应该是帮助脱去未燃烧的衣服。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790354, '鸣喇叭进行警告', '减速或靠右停车', '开远光灯抗议', '加速反超后告诫', '2', 'http://images.juheapi.com/jztk/subject4/274.jpg', '在这种情况下被超机动车驾驶人怎样应对？', '虽然遇到机动车这样超车，会心里不爽，但为了安全起见，要让对方先通过，因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790355, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/275.jpg', '驾驶机动车在这种情况下可以适当鸣喇叭加速通过。', '遇到这种情况应当减速慢行，必要时停车，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790356, '在车速下降前减挡', '在车速下降后减挡', '在车速过低时减挡', '尽量使用越级减挡', '1', '', '驾驶机动车上坡行驶如何保持充足动力？', '车速慢了再换挡不容易前进甚至会熄火，造成车掉下坡来。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790357, '采取保暖措施', '将伤员转移到有新鲜空气的地方', '进行人工呼吸', '进行胸外心脏按压', '2', '', '救助有害气体中毒伤员，首先采取的措施是什么？', '中毒伤员所处的空间肯定空气不流通，空气新鲜的地方可以让人体的呼吸管道起畅通的作用。因此应当先转移位置到空气新鲜的地方。 只有在讲中毒伤员转移至空气新鲜的地方之后，才能做ACD等措施。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790358, '超速行驶', '不按交通标线行驶', '客车超员', '疲劳驾驶', '1', '', '某日13时10分，罗某驾驶一辆中型客车从高速公路0公里处出发，下午14时10分行至该高速公路125公里加200米处时，发生追尾碰撞，机动车驶出西南侧路外边坡，造成11人死亡、2人受伤。罗某的主要违法行为是什么？', '因为，高速最高时速为120公里/小时，而案例中根据时间可推算出超速5.2公里，所以该案例为超速。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790359, '雾灯和危险报警闪光灯', '雾灯和转向灯', '雾灯和远光灯', '雾灯和近光灯', '1', '', '机动车在雾天行驶时，要开启什么灯？', '《中华人民共和国道路交通安全法实施条例》第五十八条  机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。【机动车雾天行驶应当开启雾灯和危险报警闪光灯】。 因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790360, '挂高速档加速冲坡', '提前观察坡道长度', '尽量避免途中减挡', '上坡前减挡保持动力', '16', '', '驾驶汽车在山区上陡坡路段怎样行驶？', '上陡坡应该挂低档。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107535790361, '制动距离延长', '抗滑能力变大', '路面附着力变大', '制动距离变短', '1', '', '驾驶机动车在冰雪路面行车注意什么？', '由于冰雪路面易滑，因此要比平时更早的踩刹车，也就是说制动距离延长。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790362, '正确', '错误', '', '', '2', '', '机动车电器、汽油着火后可用水来熄灭。', '水的密度比油大，不能用来灭火。而电器类，若是用水熄灭的话，很容易导电。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790363, '急剧增大', '逐渐增大', '急剧减小', '没有变化', '3', '', '当机动车在湿滑路面上行驶时，路面附着力随着车速的增加如何变化？', '湿滑路面就是路面又湿又滑，即很易滑，因此车子与地面的抓地力就会变小。因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790364, '会车前两车之间相互提示', '驾驶操作的习惯行为', '便于双方观察前方情况', '驾驶人之间的一种礼节', '3', '', '夜间会车前，两车在相距150 米之外交替变换前照灯远近光的作用是什么？', '夜间会车，由于视线受限的关系，必须借助灯光等了解周围情况，因此这么做是为了了解情况，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790365, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/285.jpg', '驾驶机动车通过这种傍山险路要靠左侧行驶。', '车辆在道路上行驶应靠右侧行驶，如图这种行为是占道行驶，若是对向有来车，很容易发生事故。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790366, '减速慢行', '保持安全车距', '正确使用灯光', '高速行驶', '13', '', '雾天机动车在道路上通行，驾驶人要怎样做？', '雾天开车，能见度低，视野不良，因此车速要放慢，同时和其他车辆保持安全距离，视野不良时车灯的使用就显得很重要了。因此本题选ABC。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107535790367, '正确', '错误', '', '', '2', '', '伤员骨折处出血时，要先固定，然后止血和包扎伤口。', '本题的关键在于伤员发生这种状况的时机、也就是分轻重缓急来处理主要的病情，如果是突发灾难现场救急，肉眼判断骨折处未伤及动静脉仅皮外划伤无须单独包扎止血、那么可以先行固定，送往医院后再行处置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790368, '连续鸣喇叭加速超越', '加速继续超越', '停止继续超车', '紧跟其后，伺机再超', '3', '', '超车时前方机动车不减速、不让道，驾驶人怎么办？', '安全第一，文明行车，所以选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107535790369, '正确', '错误', '', '', '1', '', '冰雪道路行车，由于积雪对光线的反射，极易造成驾驶人眩目。', '因为太阳光通过冰的折射到人的眼睛里，冰在太阳光下就好像一面镜子。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107535790370, '客车超员', '超速行驶', '疲劳驾驶', '操作不当', '2', '', '罗某驾驶大型卧铺客车（乘载44人，核载44人）行至沿河县境内540县道58公里加500米处时，在结冰路面以每小时44公里速度行驶，导致机动车侧滑翻下公路，造成15人死亡、27人受伤。罗某的主要违法行为是什么？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　（二）掉头、转弯、下陡坡时；　　（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　（四）在冰雪、泥泞的道路上行驶时；　（五）牵引发生故障的机动车时。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064770, '交替变换远近光灯绕过行人', '开启近光灯绕过行人', '使用远光灯绕过行人', '停车让行人优先通过', '4', 'http://images.juheapi.com/jztk/subject4/291.jpg', '夜间驾驶汽车在人行横道前遇行人横过时怎样行驶？', '和谐  和谐第一', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064771, '注意观察动态', '适当减速慢行', '保持安全间距', '鸣喇叭加速超越', '13', 'http://images.juheapi.com/jztk/subject4/292.jpg', '遇到这种同向行驶的非机动车时怎样行驶？', '请以圣人的眼光答题，D最不符合圣人的标准。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064772, '切断一切电源', '戴好防护面具和手套', '关闭阀门制止渗漏', '组织人员向下风方向疏散', '4', '', '液化石油气罐车在运输途中发生大量泄漏时，下列措施错误的是什么？', '为了防止泄漏事件造成更大的伤害和损失，因此采取的措施应该是戴好防护面具和手套、切断一切电源、关闭阀门制止渗漏，并组织人员向上风方向疏散。而本题考的是错误的措施，如此一来，只有选项D是错误的。不是往下风方向，而是应当往上风方向。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064773, '正确', '错误', '', '', '2', '', '机动车在路边起步后应尽快提速，并向左迅速转向驶入正常行驶道路。', '驾车要谨慎，所以出现迅速基本都是错误！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064774, '前轮侧滑，向侧滑方向转动转向盘', '前轮侧滑，向侧滑相反方向转动转向盘', '后轮侧滑，向侧滑方向转动转向盘', '后轮侧滑，向侧滑相反方向转动转向盘', '10', '', '机动车发生侧滑时要如何调整方向？', '如果前轮侧滑时，应稳住油门，纠正方向驶出。当后轮侧滑时，应将方向盘朝侧滑方向转动，待后轮摆正后再驶回路中。下坡中遇到后轮侧滑时，可适当点一下油门，提高车速，待侧滑消除后再按原车速行驶。因此本题选BC。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064775, '正确', '错误', '', '', '2', '', '大雾天气能见度低，开启远光灯会提高能见度。', '雾天，当然是雾灯和危险警报灯。另外，雾天的能见度不是灯能改变的！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064776, '占对向车道行驶', '靠右侧减速行驶', '在道路中心行驶', '紧靠路右侧行驶', '2', 'http://images.juheapi.com/jztk/subject4/297.jpg', '驾驶机动车在这种山区弯路怎样行驶？', '由图可知，前方是向左急转弯路段，因此为了避免前方有来车，应当减速鸣号靠右行，因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064777, '开启前照灯低速行驶', '开启雾灯低速行驶', '选择安全地点停车', '紧靠路边低速行驶', '3', '', '驾驶机动车遇到大雾或特大雾等能见度过低天气时如何做？', '注意题中的“大雾或特大雾等能见度过低天气”，也就是说可能出现无法正常行驶的情况，因此为了安全起见，应停车，而停车则应选择安全的地点。 因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064778, '紧跟其后，伺机超越', '加速强行超越', '连续鸣喇叭催前车让路', '停止超车，让前方机动车先超车', '4', '', '超车时，发现前方机动车正在超车，驾驶人怎么办？', '若强行超车，很有可能发生危险。只有在前车完成超车后，才能看清前方道路的状况，此时超车也才较为安全。因此选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064779, '正确', '错误', '', '', '1', '', '驾驶机动车在雾天行车要开启雾灯。', '《中华人民共和国道路交通安全法实施条例》第五十八条  机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。【机动车雾天行驶应当开启雾灯和危险报警闪光灯】。因此本题选A。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064780, '正确', '错误', '', '', '1', '', '在大雨天行车，为避免发生“水滑”而造成危险，要控制速度行驶。', '控制速度行驶：减速或者停车都是为了安全。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064781, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/302.jpg', '驾驶机动车在这种环境条件下起步前要开启远光灯。', '起步开近光灯，牢记！先近后远！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064782, '正确', '错误', '', '', '2', '', '雨天避免“水滑”现象的有效方法就是保持高速行驶。', '应该是降低车速，实际驾驶员也是把车速尽量降低，不是有“十次事故九次快”吗？何况是雨天。所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064783, '刚下雨的路面', '大雨过后路面', '暴雨中的路面', '大雨中的路面', '1', '', '在雨天哪类路面最容易发生侧滑？', '刚下雨的路面是水和土的混合物简称泥，很滑。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064784, '加速迅速超越', '变换远近光灯超越', '减速放弃超车', '持续鸣喇叭超越', '3', '', '超车过程中，被超车辆突然加速怎么办？', '和谐第一，您就别跟前车飙车了，别斗气，出事故多半是斗气引起的。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064786, '充分利用行车制动器', '充分利用发动机的牵制作用', '充分利用驻车制动器', '充分利用缓速器', '2', '', '在冰雪路面上减速或停车，要怎样降低车速？', '发动机制动有三个显著特点：一是由于差速器的作用，可将制动力矩平均地分配在左右车轮上，减少侧滑、甩尾的可能性；二是有效地减少脚制动的使用频率，避免因长时间使用制动器，导致制动器摩擦片的温度升高，使制动力下降，甚至失去作用；三是车速始终被限定在一定范围内，有利于及时降速或停车，确保行车安全。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064787, '正确', '错误', '', '', '1', '', '雾天公路行车可多使用喇叭引起对向注意；听到对向机动车鸣喇叭，也要鸣喇叭回应。', '雾天视野不好，看不清楚的情况下，只能通过声音来判断其他车辆的位置。因此雾天比较多用喇叭来“交流”，当听到其他车辆鸣喇叭时，也该告知对方自己所在的位置，避免因看不清发生事故。 所以这题是对的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064788, '正确', '错误', '', '', '1', '', '在泥泞路行车中发生侧滑时，要向后轮侧滑的方向转动转向盘适量修正。', '因为好比是后轮向左侧横滑如果向右打方向，会加大横滑的幅度的！因为向右打方向，后轮左侧转动就会快，右侧会变慢，左侧抓地力会下降，所以会加大横滑的幅度的，相反如果向同一方向打，还是这个例子向左侧打，右轮转动会快，左侧转动会慢，抓地力就会提高，就会恢复行使方向的！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064789, '正确', '错误', '', '', '1', '', '驾驶机动车在雨天起步前要使用刮水器。', '刮水器是为了提高汽车在雨天和雪天行驶时驾驶员的能见度而专门设置的，也就是说使用刮水器，在雨天能够让驾驶员能够清楚地透过前挡风玻璃了解周围情况，因此本题选A。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064790, '超速行驶', '客车超员', '疲劳驾驶', '酒后驾驶', '11', '', '吴某驾驶一辆大客车，乘载33人（核载22人），行至163县道7公里加300米处时，机动车失控坠入山沟，造成10人死亡、21人受伤。事后经酒精检测，吴某血液酒精含量为26毫克/百毫升。吴某的主要违法行为是什么？', '1：乘载33人(核载22人)，所以客车超员2：吴某血液酒精含量为26毫克/百毫升，所以酒后驾驶。（此处既然检测到酒精就算酒后驾驶，不然人的血液里边哪里会有酒精）', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064791, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/312.jpg', '在积雪覆盖的冰雪路行车时，可根据路边树木、电杆等参照物判断行驶路线。', '这个常识题。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064792, '正确', '错误', '', '', '2', '', '驾驶汽车遇到牲畜横穿抢道的情况，要及时鸣喇叭进行驱赶。', '小心惹恼了牲畜，把咱的车撞翻了。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064793, '正确', '错误', '', '', '2', '', '雨天超车要开启前照灯，连续鸣喇叭迅速超越。', '这种天气里面能见度低，车辆应该慢行，就算要超车，也不能连续鸣喇叭，而且要确保安全的情况下超车。所以此题的关键在“迅速”两字。 因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064794, '正确', '错误', '', '', '2', '', '机动车因故障不能离开高速公路时，驾乘人员要在车上等候救援。', '标准程序是：当车辆发生故障时，打开双闪警示灯，应将车辆停放在最右侧的应急车道或紧急停车带内，夜间打开尾灯示宽灯，司乘人员应立即下车，在路沿下护坡内等候救援车辆、人员来，由一个人在车辆尾部150米左右放置三角警示牌或警示物），切记不能在高速公路内走动、追逐、等候！不能在故障车辆内等候！一般情况禁止在高速公路上修理车辆。所以，在车上等候是错的，应该在路沿下的护坡或护栏外等候。 因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064795, '比在平路时提前', '比在平路时推迟', '和平路时一样', '要轻踏制动踏板', '1', '', '驾驶机动车在下坡路段停车怎样使用行车制动？', '下坡行驶时，在重力的作用下速度会逐渐提高，比平缓路段需要的距离长。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064796, '正确', '错误', '', '', '1', '', '在紧急情况下为伤员止血时，须先用压迫法止血后再根据出血情况改用其他止血法。', '一般小动脉和静脉出血可用加压包扎止血法，较大的动脉出血，应用止血带止血。但在紧急情况下，须先用压迫法止血，然后再根据出血情况改用其它止血法。指压止血法可以在较大的动脉出血后，用拇指压住出血的血管上方(近心端)，使血管被压闭住，中断血液。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064797, '开启远光灯', '低速大间距', '开启近光灯', '开启雾灯', '2', '', '驾驶机动车在雾天两车交会时怎样做最安全？', '雾天行车能见度低，视野不良，因此与其他车辆会车时要低速会车，目的是为了加大会车距离。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064798, '正确', '错误', '', '', '2', '', '雾天在高速公路上发生事故后，车上人员不要随便下车行走。', '标准程序是：当车辆发生故障时，打开双闪警示灯，应将车辆停放在最右侧的应急车道或紧急停车带内，夜间打开尾灯示宽灯，司乘人员应立即下车，在路沿下等候救援车辆、人员来，由一个人在车辆尾部150米左右放置三角警示牌或警示物，切记不能在高速公路内走动、追逐、等候！不能在故障车辆内等候！一般情况禁止在高速公路上修理车辆。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064799, '先避车后避物', '先避人后避物', '先避车后避人', '先避物后避人', '2', '', '高速公路行车紧急情况避险的处理原则是什么？', '以人为本，所以首先要避人。 因此选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064800, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/321.jpg', '夜间在这种情况下跟车要注意观察前车信号灯的变化，随时做好减速或停车的准备。', '前车的刹车灯亮了，说明前车在减速，所以你也要跟着减速和刹车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064801, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/322.jpg', '交通警察发出这种手势信号可以直行通过。', '这个手势是左转弯。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064802, '使其他交通参与者更容易发现自己', '更容易看清路面情况', '提醒其他车辆我在让行', '以上说法都不对', '1', '', '夜间驾驶汽车通过十字交叉路口交替使用远近光灯的目的是什么？', '交替使用远近光灯就是提醒的作用。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064803, '干燥水泥路面', '下雨开始时的路面', '潮湿水泥路面', '大雨中的路面', '2', '', '最容易发生侧滑的路面是：', '当雨刚开始时便把路面上的那些媒质给弄湿了，此时的路面便像洒上一层薄薄的润滑剂！但在雨中，由于雨水不断增加，路面上的“润滑剂”亦随之被稀释，大雨时，会将其完全冲洗干净，顾有之题目的说法。但是，就算是路面被雨冲洗干净了，也不能掉于轻心地开快车，因为橡胶轮胎遇到水后使大大减少了对地面的付着力，这是它的天性！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064804, '比在平路时提前', '比在平路时推迟', '和平路时一样', '要重踏制动踏板', '2', '', '上坡路段停车怎样使用行车制动？', '汽车上坡行驶时，由于坡度阻力车速自然会降下来，与平缓路段相比较只需轻轻制动。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064805, '正确', '错误', '', '', '1', '', '驾驶机动车汇入车流时不能影响其他机动车通行。', '汇入车流的前提是不能影响其他车辆的正常通行，因此本题选A。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064806, '依次跟车行驶', '从右侧超越', '靠边停车等待', '从左侧超越', '1', 'http://images.juheapi.com/jztk/subject4/327.jpg', '遇到这种前方拥堵路段通行缓慢时怎样行驶？', '请按规矩行驶。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064807, '正确', '错误', '', '', '1', '', '为防止有害气体中毒伤员继续中毒，首先将伤员转移到空气新鲜的地方。', '中毒伤员所处的空间肯定空气不流通，空气新鲜的地方可以让人体的呼吸管道起畅通的作用。因此应当先转移位置到空气新鲜的地方。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064808, '提前减速行驶', '提前鸣喇叭提醒', '不得急加速绕行', '保持安全间距', '17', 'http://images.juheapi.com/jztk/subject4/329.jpg', '雨天遇到这种行人占道行走时怎样通行？', '牢记', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064809, '快速通过', '使用危险报警闪光灯', '减速慢行并鸣喇叭示意', '随意通行', '3', '', '机动车驶近坡道顶端等影响安全视距的路段时，要如何保证安全？', '注意“影响安全视距的路段”，也就是说在此路段行驶时无法完全掌握路况，因此要减速慢行谨慎小心，同时鸣喇叭告知其他车辆。因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064810, '加速直接汇入车流', '从主路内灰色车后汇入车流', '从主路内红色车前汇入车流', '开启转向灯直接汇入车流', '2', 'http://images.juheapi.com/jztk/subject4/331.jpg', '驾驶机动车在这种情况下怎样汇入主路车流？', '前方有个“让”的牌子，所以要让行红色的车子！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064811, '正确', '错误', '', '', '2', '', '变更车道前确认后方无来车时可以不开转向灯变道。', '变更车道时要提前开启转向灯，养成良好的开车习惯，防患于未然，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064813, '视线受阻', '路面湿滑', '附着力变小', '行驶阻力增大', '13', '', '雨天影响安全行车的主要因素有哪些？', '牢记', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064814, '停车观察路口情况', '加速尽快进入路口', '减速缓慢进入路口', '减速观察左后方情况', '1', 'http://images.juheapi.com/jztk/subject4/335.jpg', '驾驶机动车在有这种标志的路口怎样通过最安全？', '前方有个“停”的牌子，肯定是要停车观察在通过！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064815, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/336.jpg', '按照下图红框内的标志，机动车应当在B区域内行驶。', 'B区域是非机动车道，你看那有个非机动车标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064816, '正确', '错误', '', '', '2', '', '机动车在行驶中，遇雨雪天气向右侧滑时，要向左打方向，使其稳定。', '所谓“侧滑”就是车辆急加速、突然制动或启动时扭矩过大而产生的侧向甩动的现象，俗称“甩尾”。造成车辆侧滑的因素很多。雪天，雨天、冰面上行车时突然抬起油门或突然加速；采取紧急制动，制动时后轮比前轮先抱死；都极易发生车辆侧滑而造成事故。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064817, '正确', '错误', '', '', '1', '', '机动车落水后，只有在水快浸满车厢时，才有可能开启车门或摇下车窗玻璃逃生。', '车辆掉水后，车里有空气，车内压力低于车外的压力，这个时候你想打开车门，一是比较困难，二是即使你打开了车门，车外的水一下子涌进来，可能会造成更大的伤害。当水浸满车厢后，内外压力接近时，以上问题都能解决了。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064818, '保持较大的安全距离', '开启危险报警闪光灯', '不断变换前照灯远近光', '适时鸣喇叭提示前车', '1', '', '驾驶机动车在冰雪路面怎样跟车行驶？', '冰雪路面易滑，因此在跟车时要加大跟车距离，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064820, '正确', '错误', '', '', '1', '', '机动车转弯时速度过快，容易发生侧滑。', '转弯速度越快，角速度越大，离心力也越大。这样一来车子的抓地力就变小了，此时就容易发生侧滑。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064821, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/342.jpg', '在山区道路跟车行驶的距离要比平路时大。', '复杂道路一定要加大跟车距离，开车一定要离前车远一点，这样最安全，出了问题有处理的时间。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064822, '避免紧急制动、紧急转向', '保持足够的安全距离', '注意非机动车和行人动态', '选择安全车速行驶', '17', '', '雨天安全行车的注意事项是什么？', '雨天行车，受视线及路面的影响，要减速慢行，同时要注意行车和非机动车的动态，并与其他车辆保持较大的安全距离，也不能采取紧急制动或是急转方向的方法，因为容易造成侧滑或横滑状况。因此本题选ABCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064823, '正确', '错误', '', '', '1', '', '对无骨端外露的骨折伤员肢体固定时，要超过伤口上下关节。', '为什么要超过伤口上下关节呢？这是因为不能让这两个关节活动。这样做能防止骨折端的移位，防止造成血管和神经的损伤，后者的损伤是比较头疼的，比单纯的骨折要麻烦多了，尤其是神经的损伤，很可能会造成感觉或运动方面障碍；此外，防止骨折断端戳破皮肤从而造成骨折端的污染影响愈合。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064824, '尽量选择安全区域停车', '开启危险报警闪光灯', '开启示廓灯和后位灯', '按规定设置警告标志', '17', '', '夜间行车中汽车发生故障需要停车时怎么办？', '都是安全常识啊。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064826, '正确', '错误', '', '', '1', '', '雪天行车中，在有车辙的路段要循车辙行驶。', '雪天行驶时，道路通常会被雪所掩盖，因此无法了解路面的实际情况。因此为了避免不可见的危险，就要尽可能沿着车辙行驶。因为这样安全系数更高一些。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064827, '正确', '错误', '', '', '1', '', '雨天在高速公路行车，为避免发生“水滑”现象而造成方向失控，要降低车速。', '水滑就是因为雨天雨水多了，使轮胎与路面的摩擦系数变小了，就容易侧滑。因此要降低车速行驶，减低发生侧滑的可能性，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064828, '正确', '错误', '', '', '2', '', '移动脊柱骨折的伤员，切勿扶持伤者走动，可用软担架运送。', '注意担架会引起误解，脊柱骨折意味着柔软的脊髓面临机械损伤（哪怕是非常微小的震动）所以必须保持脊柱固定（软担架根本不能规避机械损伤），所以，用软担架运送也是错误的！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064829, '正确', '错误', '', '', '2', '', '易燃液体一旦发生火灾，要及时用水扑救。', '着火时，人们往往立即想到的是用水来灭，但有些火灾却是不能用水来扑救的。如果易燃液体密度比水小，就不应该用水，因为易燃液体会浮在水面继续燃烧。所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064830, '注意观察行人动态', '适当降低车速', '保持安全距离', '提前轻按喇叭提醒', '17', 'http://images.juheapi.com/jztk/subject4/351.jpg', '雨天遇到这些撑雨伞和穿雨衣的行人在路边行走怎样通行？', '常识题', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064831, '救护车', '消防车', '警车', '校车', '17', '', '当您遇到以下车辆时，需要礼让的是什么？', '这些车都是我们需要让行的，因为他们不是我们的未来就是在保障我们基本的生活。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064832, '爆炸品', '氧化性物质', '自燃物品', '易燃固体', '4', '', '火柴、硫黄和赤磷属于哪类危险化学品？', '易燃固体是指在常温下以固态形式存在，燃点较低，遇火受热、撞击、摩擦或接触氧化剂能引起燃烧的物质。这三类物质正好符合，因此选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064833, '正确', '错误', '', '', '1', '', '受伤者在车内无法自行下车时，可设法将其从车内移出，尽量避免二次受伤。', '如题，受伤者既然无法自行下车，那肯定说明是行动不便的，若不设法将伤者从车内转移出来，万一车子发生什么状况，受伤者可能都无法及时躲避，这样很容易造成更大的伤害。因此这题是正确的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064834, '开启远光灯', '适当提高车速', '关闭近光灯', '提前降低车速', '4', '', '驾驶汽车在进出隧道时应注意什么？', '只有D最安全，10次事故9次快，亲，慢点。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064835, '客车超员', '驾驶逾期未检验的机动车', '驾驶与准驾车型不符的机动车', '疲劳驾驶', '3', '', '赵某（持有A2驾驶证）驾驶大型卧铺客车，行驶至叶城县境内219国道226公里加215米处转弯路段时，坠入道路一侧山沟，致16人死亡，26人受伤。赵某的主要违法行为是什么？', 'A2是不能开大型客车的！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064836, '急踏制动踏板减速', '缓抬加速踏板减速', '迅速转向进行调整', '提速增大车轮排水量', '2', '', '雨天机动车在高速公路行驶发生“水滑”现象时怎样处置？', '所谓“水滑”现象就是雨天汽车在积水路面上高速行驶时，轮胎与路面间的存水不能排除，水的压力使轮胎上浮，形成汽车在积水路面上滑行的现象。选项ACD都有可能造成车辆侧滑等事故的发生，而通过缓抬加速踏板的方式减速才能真正让车渐渐减慢，因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064837, '空挡滑行', '减挡，充分利用发动机制动', '踏下离合器滑行', '长时间使用驻车制动器制动', '2', '', '驾驶机动车下长坡时，车速会因为重力作用越来越快，以下控制车速方法正确的是什么？', '发动机制动有三个显著特点：一是由于差速器的作用，可将制动力矩平均地分配在左右车轮上，减少侧滑、甩尾的可能性；二是有效地减少脚制动的使用频率，避免因长时间使用制动器，导致制动器摩擦片的温度升高，使制动力下降，甚至失去作用；三是车速始终被限定在一定范围内，有利于及时降速或停车，确保行车安全。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064838, '正确', '错误', '', '', '2', '', '夜间驾驶汽车在急弯道停车时要开启危险报警闪光灯。', '急转弯不需要开启危险报警闪光灯，危险报警闪光灯，通常称为“双蹦”（红三角里有个！的标志按扭开关，俗称双闪灯 或双跳灯），是一种提醒其他车辆与行人注意本车发生了特殊情况的信号灯。 在驾车过程中遇到浓雾时，能见度低于50米时，由于视线不好，不但应该开启前、后防雾灯，此时还应该开启危险报警闪光灯，以提醒过往车辆及行人的注意，特别是后方行驶的车辆，保持应有的安全距离和必要的安全车速，避免紧急刹引起追尾。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064839, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/360.jpg', '前方标志预告距离下一出口4公里 。', '下一个出口预告：预告下一出口距离。当互通式立体交叉间距大于或等于3 km、小于5 km时，应设置下一出口预告标志，预告下一出口的信息和距离。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064841, '轻踏或间歇踩踏制动踏板', '与其他路面一样踏制动踏板', '重踏制动踏板', '猛踏制动踏板', '1', '', '驾驶未安装制动防抱死装置（ABS）的机动车在冰雪路面怎样使用制动？', '未安装制动防抱死装置的车辆，在冰雪、湿滑、砾石路面或者比较光滑的路面上制动时，要轻踏或间歇踩踏制动踏板。这是因为在冰雪、湿滑、砾石路面或者比较光滑的路面车辆极易抱死失控，而不是侧滑那么简单，不能猛踩刹车，可先踩刹车，然后离合，松开离合后间接性地踩刹车，双手尽力控制方向，这样有效减少刹车距离。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064842, '开启近光灯', '靠右侧行驶', '开启远光灯', '注意对向来车', '14', '', '驾驶汽车进入双向通行的隧道时应注意什么？', '隧道内禁止开远光，影响他人开车视线。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064843, '用手帕包扎', '用毛巾包扎', '用棉质衣服包扎', '用细绳缠绕包扎', '4', '', '在没有绷带急救伤员的情况下，以下救护行为中错误的是什么？', '根据受伤位置的不同和创面的大小，可以利用毛巾、手帕做成简易的三角巾。细绳由于其表面不是平整光滑的，也不够柔软。因此不能用来包扎，而棉质衣服相对比较柔软，因此可以。所以本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064844, '正确', '错误', '', '', '1', '', '制动时前车轮抱死会出现丧失转向能力的情况。', '现在小车都有ABS防抱死系统，紧急刹车时不会再抱死出现丧失转向能力的情况，急刹车你会感觉到脚下会强力反弹，就是ABS启动了，这时一样可以打方向。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064845, '迅速抬上担架送往医院', '适当调整损伤时的姿势', '用绷带对骨折部位进行包扎', '不要移动身体骨折部位', '4', '', '抢救骨折伤员时注意什么？', '面对骨折伤员，抢救时一定要确保不会造成二次伤害，即不能让骨折情况变严重或恶化，因此除了选项D之外，ABC都有可能造成二次伤害，因此本题只能选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064846, '正确', '错误', '', '', '2', '', '救火时不要脱去所穿的化纤服装，以免伤害暴露的皮肤。', '化纤易着是一方面主要是化纤不能耐高温，融化后会紧贴在皮肤上，对皮肤伤害很大，导致烧伤。 因此救火时应当脱去化纤服装避免烧伤。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064847, '注意避让已在路口内车辆', '优先驶入环形路口', '鸣喇叭示意其他车辆让行', '超越前方车辆进入路口', '1', '', '驾驶机动车驶入拥堵的环形路口，以下做法正确的是什么？', '驾驶机动车驶入拥堵的环形路口，需要注意避让已在路口内车辆。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064848, '爆胎后，车辆行驶方向易发生变化，须紧握转向盘', '爆胎后，车辆自身开始减速，所以只需轻踏制动踏板', '爆胎后，紧急制动容易引起侧翻', '轻踏制动踏板进行减速是为了保护轮胎', '8', '', '驾驶员在高速公路上行驶时，车辆左前轮突然爆胎，须第一时间紧握转向盘，然后轻踏制动踏板进行减速，并将车停靠在紧急停车带上。这样做的原因是什么？', '在车辆爆胎时如果不尽力控制方向，或者采取紧急制动都容易引起车辆侧翻，所以需要紧握方向盘，轻踩制动踏板进行减速。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064849, '位置A', '位置B', '位置C', '位置D', '4', 'http://images.juheapi.com/jztk/subject4/370.jpg', '当高速公路上车辆发生故障时，人员应当疏散到下图哪个位置。', '发生事故后人员应该疏散到护栏外D处，车辆停在位置C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064851, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/372.jpg', '驾驶汽车可以在这种急弯处超车。', '这么复杂的路况您就好好在后边跟车吧。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064852, '在成人和儿童之间快速通过', '连续鸣喇叭提示后通过', '从成人身后绕行', '减速鸣喇叭提示，做好随时停车准备', '4', 'http://images.juheapi.com/jztk/subject4/373.jpg', '如图所示，驾驶机动车在乡间道路上行驶，以下做法正确的是什么？', '在乡道上行驶，应该减速鸣喇叭提示，做好随时停车准备。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064853, '加速冲过坡顶', '开启远光灯', '合理控制车速', '交替变换远近光灯', '12', '', '夜间驾驶汽车驶近上坡路坡顶怎样行驶？', '交替变换远近光灯是为了提醒对面坡上的车，因为坡上的车视线看不到你。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064854, '正确', '错误', '', '', '2', '', '机动车在高速公路上，因故障不能离开行车道时，可在行车道上迅速抢修。', '应该立即在车辆后方150米以上的距离设置警示标志，开启示宽灯。车上人员退到路边防护栏外，并拨打应急报警电话。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064855, '在最左侧车道内停车', '在路肩上行驶', '学习驾驶机动车', '倒车', '17', '', '驾驶机动车在城市快速路上行驶，以下做法错误的是什么？', '城市快速道路是指城市道路中设有中央分隔带，具有四条以上的车道，全部或部分采用立体交叉与控制出入，供车辆以较高的速度行驶的道路。以上四个选项都是在城市快速道路中禁止的行为。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064856, '集中注意力', '降低车速', '注意鸣喇叭', '做好停车准备', '17', 'http://images.juheapi.com/jztk/subject4/377.jpg', '在这种路面较窄的急弯处行车时要注意什么？', '谨慎驾驶，安全行驶第一，以上都是安全行驶的条件。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064857, '正确', '错误', '', '', '2', '', '搬运昏迷失去知觉的伤员要采取仰卧位。', '正确的做法是先开放气道，再采取去枕仰卧位，头偏向一侧，保持呼吸道通畅，以防窒息。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064858, '采取保暖措施', '用软板担架运送', '用三角巾固定', '扶持伤者移动', '3', '', '怎样抢救脊柱骨折的伤员？', '应该是原地固定等待专业救护人员，脊柱骨折被移动很有可能将导致高位截瘫。所以本题唯一可以选的就是C了！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064859, '最左侧', '中间', '最右侧', '任意', '1', '', '在同向3车道高速公路上行车，车速高于每小时90公里、低于每小时110公里的机动车不应在哪条车道上行驶？', '高速路的车最低时速是要求60公里，最高不得超过120公里。如果高速路同方向有2条车道，右侧车的速度范围为60公里到100公里，左侧车的速度范围为100公里到120公里。如果高速路同方向有3条车道，最右侧的速度范围为60公里到90公里，中间的为90公里到110公里，最左侧的为110公里到120公里。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064860, '停车等待', '从对向车道加速超越', '连续鸣喇叭催促', '从非机动车道通过', '1', 'http://images.juheapi.com/jztk/subject4/381.jpg', '如图所示，驾驶机动车跟车行驶遇到出租车正在接送乘客时，以下做法正确的是什么？', '驾驶机动车跟车行驶遇到出租车正在接送乘客时，应停车等待。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064861, '超速行驶', '不按交通标线行驶', '客车超员', '疲劳驾驶', '4', '', '某日早上6时，冉某驾驶一辆大客车出发，连续行驶至上午11时，在宣汉县境内宣南路1公里处，坠于公路一侧垂直高度8.5米的陡坎下，造成13人死亡、9人受伤。冉某的主要违法行为是什么？', '规定是说 连续4小时就 要停车休息20分钟！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064862, '混凝土路', '土路', '冰雪路面', '沙土路', '3', '', '机动车在什么样的路面上制动时车轮最容易抱死?', '由于冰雪路面摩擦系数较另外三种路面来说小许多，所以摩擦力比较小，当刹车片和刹车盘之间的摩擦力大于路面和车轮间的摩擦力时,车轮就会抱死而打滑！因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064863, '爆胎', '汽车行驶油耗增大', '操纵失控', '加剧轮胎磨损', '17', '', '汽车各轮胎气压不一致时，容易造成的后果是什么？', '轮胎气压不足或左、右两侧轮胎气压不均匀，会引起制动效果时差，操纵失控，转向困难或转向沉里.同时也降低了轮胎的使用寿命，容易燎胎，增大油耗。载重全大时.以上后果更严重。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064864, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/385.jpg', '如图所示，驾驶机动车在路口前遇黄灯亮时，应停车等待。', '黄灯亮时，已越过停止线的车辆可以继续通行；其它情况需要停车等待。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064865, '不干扰前车视线，有利自己看清道路', '提醒前方车辆让行', '提醒前方车辆减速', '提醒前车后方有车跟随，前车应适当提速', '1', 'http://images.juheapi.com/jztk/subject4/386.jpg', '在这种大雨中，跟车行驶时使用近光灯的目的是？', '图片分析：大雨中，跟车行驶时使用近光灯的目的是不干扰前车视线，有利自己看清道路。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064867, '前车通过后再上坡', '迅速超越前车上坡', '低速超越前车上坡', '紧随前车后上坡', '1', 'http://images.juheapi.com/jztk/subject4/388.jpg', '在山区冰雪道路上遇到这种前车正在上坡的情况如何处置？', '为了防止前车打滑撞到您的车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064868, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/389.jpg', '遇到这种跨江、河、海大桥时，可能会遇到横风，要控制好方向。', '常识题', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064870, '靠路中心减速行驶', '加速甩掉后车', '开启左转向灯警示', '减速靠右侧让行', '4', 'http://images.juheapi.com/jztk/subject4/391.jpg', '夜间遇到这种后车发出超车信号时怎样行驶？', '安全第一，开车重要的就是别斗气，都让着点。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064871, '注意前车信号灯变化', '使用近光灯', '保持安全距离', '做好减速或停车准备', '17', 'http://images.juheapi.com/jztk/subject4/392.jpg', '夜间在这种道路条件怎样跟车行驶？', '谨慎驾驶', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064872, '加速通过', '在骑车人通过前提前加速通过', '停车让行', '连续鸣喇叭使其让行', '3', 'http://images.juheapi.com/jztk/subject4/393.jpg', '如图所示，驾驶机动车行经交叉路口遇到这种情况时，以下做法正确的是什么？', '驾驶机动车行经交叉路口遇到这种情况时，应停车让行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064873, '正确', '错误', '', '', '2', '', '驾驶机动车在山区道路应紧跟前车之后行驶。', '山区道路突发状况多，需保持一定的距离来应急。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064875, '提前减速并停车等待', '借对向车道超越前车', '鸣喇叭催促前车让路', '继续行驶，靠近前车', '1', 'http://images.juheapi.com/jztk/subject4/396.jpg', '如图所示，驾驶机动车遇到前方车辆正在停车时，以下做法正确的是什么？', '驾驶机动车遇到前方车辆正在停车时，需要提前减速并停车等待，等前车走后方可再过。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064877, '适当鸣喇叭加速通过', '长鸣喇叭催促', '挤靠自行车', '减速让行', '4', 'http://images.juheapi.com/jztk/subject4/398.jpg', '如图所示，驾驶机动车遇到这种情况，B车做法正确的是什么？ ', 'B做法不恰当，AC做法易引发交通事故，减速让行才能保证安全。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064878, '减速让行', '加速行驶', '向左打转向迅速超越', '连续鸣喇叭', '1', 'http://images.juheapi.com/jztk/subject4/399.jpg', '如图所示，驾驶机动车遇到右侧车道车辆突然变更车道时，应当如何避让？', '驾驶机动车遇到右侧车道车辆突然变更车道时，应当减速让行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064879, '前面的车可能停下', '右侧视野盲区内可能有自行车直行', '行人可能突然进入本车前的人行横道', '直行的黄色车辆可能影响本车右转弯', '13', 'http://images.juheapi.com/jztk/subject4/400.jpg', '如图所示，驾驶机动车跟随前车右转弯时，应当注意的是什么？', '图中直行的黄色车辆在左侧车道上，不会影响该车右转弯。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064882, '加速汇入主路车流', '可以不避让主路车辆', '无需观察,鸣喇叭示意后汇入车流', '减速慢行，在不妨碍主路车辆行驶的前提下汇入车流', '4', 'http://images.juheapi.com/jztk/subject4/403.jpg', '如图所示，驾驶机动车驶离停车场进主路时，以下做法正确的是什么？', '驾驶机动车驶离停车场进主路时，需要：减速慢行，在不妨碍主路车辆行驶的前提下汇入车流。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064883, '可牵引2辆挂车', '可牵引3辆挂车', '挂车的载质量不得超过载货汽车本身的载质量', '允许牵引总质量超过本身载质量的挂车', '3', '', '载货汽车牵引挂车时,下列做法正确的是？', '当然不能超过本身质量啦，不然拉都拉不动。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064884, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/405.jpg', '高速公路安全距离确认路段用于确认车速在每小时100 公里时的安全距离。', '如图所示，一共竖立了三块绿色的牌子，上面的数字分别为，0m、50m，在远处的一个虽然看不清楚，但也可推算出是100m的牌子，也就是要看在这100米内车子的速度。如此说来，本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064885, '主动减速让行', '加速行驶', '即使有让行条件也不让', '靠道路中心行驶', '1', '', '驾驶机动车，遇到后方执行任务的特种车辆时，以下做法正确的是什么？', '驾驶机动车，遇到后方执行任务的特种车辆时，应主动减速让行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064886, '系好安全带', '调整好后视镜', '不要把身体伸出车外', '不要向车外抛洒物品', '15', '', '驾驶机动车起步前，驾驶人对乘车人需要提出什么要求？', '调整后视镜是您老自己来调的。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064887, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/408.jpg', '右侧标志预告距离高速公路东芦山服务区2公里。', '此图为服务区，没有问题。顺便普及下服务区跟停车区的区别：服务区有吃住加油等服务，停车区属于紧急停车，没有相关的服务。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064888, '两车临近时减速', '适当加速交会', '提前减速缓慢交会', '尽量靠近中线交会', '3', 'http://images.juheapi.com/jztk/subject4/409.jpg', '在这种结冰的道路上怎样会车？', '结冰路面，车况不好控制，得提前预防，减速。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064889, '选择安全的地点', '做到先让、先慢、先停', '靠山体一侧的让行', '不靠山体一侧的让行', '13', 'http://images.juheapi.com/jztk/subject4/410.jpg', '在这种山区危险路段怎样安全会车？', '不靠山体的一侧就是悬崖啊，危险的多，所以靠山体的一侧让行。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064890, '车辆操控难度增大', '制动距离延长', '易产生车轮滑转', '极易发生侧滑', '17', '', '冰雪路面对行车有那些不利影响？', '牢记', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064891, '跟随出租汽车行驶时，要预防其随时可能靠边停车上下乘客', '当前方汽车贴有实习标志时，应该增大跟车距离，预防前车紧急制动', '前方为装满货物的大货车时，应增大跟车距离并避免长时间跟随，以预防货物抛洒和车后盲区带来的危险', '雾天跟车行驶，注意前车紧急制动', '17', '', '以下跟车情况中，应当注意的情形有哪些？', '以上四种情况是行车过程中较为常见的情况，都要注意。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064892, '随时准备停车', '减速行驶，防止泥水溅到行人身上', '减速行驶，注意行人动态', '鸣喇叭提醒行人后，加速通过', '13', 'http://images.juheapi.com/jztk/subject4/413.jpg', '如图所示，驾驶机动车在雨天行驶遇到这种情形时，以下做法正确的是什么？', '驾驶机动车在雨天行驶遇到这种情形时，做法正确的是：随时准备停车；减速行驶，防止泥水溅到行人身上；减速行驶，注意行人动态。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064893, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/414.jpg', '如图所示，在这种情况下要充分减速靠右行驶。', '遇到急转鸯路应当提前减速慢行，等到进入弯路后减速很容易发生侧滑或侧翻。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064894, '马上实施心肺复苏', '使劲掐伤员的人中', '连续拍打伤员面部', '抢救前先检查呼吸', '4', '', '抢救昏迷失去知觉的伤员需注意什么？', '选D是因为抢救前要先确认伤员的生命体征，只有确认了之后才能做出适当的抢救措施。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064895, '绷带', '三角巾', '止血带', '麻绳', '4', '', '包扎止血不能用的物品是什么？', '包扎止血，要求包扎物表面平整而且松软，这样才能有效地扎紧受伤部位体内的血管，阻止血管内的血液再在流通，达到止血的目的。麻绳，一般说来总是比较粗的，是由几股细绳绞合而成的。其表面不是平整光滑的，也不够柔软。因此一般不适合用以止血包扎。有时，可见护士用纱布撕成条，再合并起来用以包扎止血，这是因为纱布比较柔软，合并后较粗一些，包扎效果更好。 因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064897, '安全帽', '灭火器', '安全锤', '冷冻液', '2', '', '驾驶机动车时，为了预防行车中突然起火造成的危险，应随车携带以下哪项物品？', 'A、C、D选项都不能有效灭火，冷冻液是防冻液，是一种化学合成制剂，用于防止发动机的冷却系统在低温时冻结，其次是散热，不能灭火。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064898, '正确', '错误', '', '', '1', '', '变更车道或超车不开启转向灯属于违法行为。', '现在的违规，违章统称违法行为，所以是对的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064899, '提前开启右转向灯', '驶入减速车道', '按减速车道规定的时速行驶', '加速直接驶离高速公路', '13', '', '以下关于驶离高速公路的做法正确的是什么？', '驶离高速公路的做法正确的是：提前开启右转向灯；驶入减速车道；按减速车道规定的时速行驶。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064902, '适当降低行驶车速', '靠右行驶', '集中注意力驾驶', '使用远光灯，提醒对方车辆', '4', 'http://images.juheapi.com/jztk/subject4/423.jpg', '如图所示，浓雾天气中驾驶机动车两车交会，以下做法错误的是什么？ ', '浓雾天气中驾驶机动车两车交会，应当适当集中注意力驾驶、降低行驶车速、靠右行驶。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064903, '转弯前减速', '靠右侧行驶', '鸣喇叭提示', '转弯时加档', '13', 'http://images.juheapi.com/jztk/subject4/424.jpg', '驾驶汽车在山区上这种陡坡道转弯时怎样行驶？', '转弯时要匀速或者减速，安全第一。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064904, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/425.jpg', '路面网状线标示允许进入该区域内等待。', '路面黄色网状线表示严格禁止一切车辆长时或临时停车，防止交通阻塞。当黄色网状线前方有车辆停驶时，后车必须在黄色网状线外等候，直到确认黄色网状线前方有足够空间停驶本车时，方可驶过黄色网状线。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064906, '降低车速', '靠右通行', '鸣喇叭示意', '抢行通过', '13', '', '驾驶机动车行经驼峰桥会车时，以下做法正确的是什么？', '驾驶机动车行经驼峰桥会车时，正确的做法：降低车速；靠右通行；鸣喇叭示意。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064908, '接近坡顶时视线受阻，无法观察坡顶之后道路走向', '接近坡顶时视线受阻，无法观察对向来车情况', '接近坡顶时车速较慢', '接近坡顶时视线受阻，无法观察坡顶之后是否有障碍物', '14', '', '驾驶机动车在山区道路上坡路段接近坡顶时，超车存在风险，以下说法正确的是什么？', '驾驶机动车在山区道路上坡路段接近坡顶时，接近坡顶时视线受阻需要注意：观察坡顶之后道路走向；观察对向来车情况；观察坡顶之后是否有障碍物。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064909, '正确', '错误', '', '', '2', '', '由于冰雪路面不能使用紧急制动，遇到突然情况可采取急转向的方法躲避。', '冰雪路面最忌紧急转向，本来就不好控制你还玩漂移。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064911, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/432.jpg', '驾驶机动车看到这个标志时，将车速迅速提高到40 公里/小时以上。', '那是限速标志，限制速度在40公里以内。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064912, '及时减速让行', '向左猛打转向盘躲避', '向右猛打转向盘躲避', '加速从右侧绕行', '1', 'http://images.juheapi.com/jztk/subject4/433.jpg', '如图所示，夜间驾驶机动车遇到其他机动车突然驶入本车道时，应当如何避让？', 'BC选项中，动作过猛会导致车辆失控，D中加速错误，且超车要从左侧，只有A是对的。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064913, '按照B车、C车、A车的顺序通行', '按照A车、B车、C车的顺序通行', '按照C车、A车、B车的顺序通行', '按照C车、B车、A车的顺序通行', '4', 'http://images.juheapi.com/jztk/subject4/434.jpg', '如图所示，图中车辆如何通行符合安全文明行车要求？ ', '机动车应让行自行车，转弯应让行直行，因此选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064915, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/436.jpg', '右侧路面标记表示可以暂时借用超车。', '旁边是实线就不能越过，而且右侧是非机动车道，亲要注意安全啊。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064918, '保持较大的跟车距离', '开启危险报警闪光灯', '不断变换远近光灯', '持续鸣喇叭提示前车', '1', 'http://images.juheapi.com/jztk/subject4/439.jpg', '在这种冰雪路面怎样跟车行驶？', '冰雪路面太滑了，离前车远点。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064919, '不开启灯光', '开远光灯', '开危险报警闪光灯', '开启示廓灯、后位灯', '7', '', '夜间路边临时停车，以下做法错误的是什么？', '夜间临时停车要开启危险报警闪光灯、示廓灯和后尾灯。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064920, '正确', '错误', '', '', '1', '', '驾驶汽车在交叉路口违法抢行容易引发交通事故。', '按规矩来行驶，尤其是交叉路口不能抢行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064921, '减速避让', '向左打转向盘避让', '向右借用人行道避让', '迎着来车鸣喇叭将其逼回', '1', 'http://images.juheapi.com/jztk/subject4/442.jpg', '如图所示，驾驶机动车遇到对向来车正在强行超车时，以下做法正确的是什么？', '驾驶机动车遇到对向来车正在强行超车时，应当减速避让以防发生危险。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064922, '不超过60公里/小时', '不超过50公里/小时', '不超过40公里/小时', '不超过30公里/小时', '4', 'http://images.juheapi.com/jztk/subject4/443.jpg', '通过这种路面条件较好的窄桥怎样控制车速？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时；', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064924, '靠近山体一侧的车危险性更高', '靠近山体一侧的车更加容易减速', '临崖一侧的车危险性更高', '临崖一侧的车更容易通过', '3', 'http://images.juheapi.com/jztk/subject4/445.jpg', '如图所示，驾驶机动车遇到这种情况，A车应当主动减速让行的原因是什么？', '山路上坡道转弯会车，驾驶机动车遇到这种情况，A车应当主动减速让行，因为临崖一侧的车危险性更高。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064925, '选择安全区域停车', '开启危险报警闪光灯、示廓灯和后位灯', '按规定设置警告标志', '给朋友打电话，坐在车内等待救援', '13', '', '驾驶机动车在夜间发生故障时，以下做法正确的是什么？', '驾驶机动车在夜间发生故障时，应当选择安全区域停车，同时开启危险报警闪光灯、示廓灯和后位灯，按规定设置警告标志，报警或者打给保险公司或者4S店等待救援。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064927, '先治伤，后救命', '先救命，后治伤', '先帮助轻伤员', '后救助重伤员', '2', '', '在事故现场抢救伤员的基本要求是什么？', '必须先救人，否则很有可能因为送医不及时而造成伤者死亡。而受伤的人员没有生命危险，可以暂缓一下。因此选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064929, '快速闪烁前照灯，提醒前方车辆快速驶离路口', '紧急制动保证车辆能够在停车线前停止', '降低车速确认安全后通过', '快速从前方车辆后方绕行', '3', 'http://images.juheapi.com/jztk/subject4/450.jpg', '遇到这样的路口，以下哪做法是正确的？', '图片分析：图中只有一个黄灯的路口，黄灯闪烁起警示作用，请降低车速确认安全后通过。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064930, '不停变换远近光灯以及鸣喇叭提醒对方', '视线适当右移，避免直视灯光', '降低车速，靠右行驶', '变换远光灯行驶', '10', '', '夜间驾驶机动车会车时，对方一直使用远光灯，以下做法正确的是什么？', '只有在没有路灯或者没有照明的情况下才能用远光灯，一般都用近光灯。夜间会车，若使用远光灯，会让对方完全看不清，进而引发交通事故。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064931, '会缩短发动机寿命', '增加车辆油耗', '会使制动器温度升高而使制动效能急剧下降', '容易造成车辆倾翻', '3', '', '驾驶机动车下长坡时，连续使用行车制动器，以下说法正确的是什么？', '驾驶机动车下长坡时，车子向下的冲力很大，采取制动，刹车垫与车轮轴就摩擦起热，刹车垫也磨损严重。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064933, '注意儿童', '禁止停车', '禁止鸣喇叭', '前方禁止通行', '4', 'http://images.juheapi.com/jztk/subject4/454.jpg', '如图所示，驾驶机动车行经该路段时，以下说法错误的是什么？', '驾驶机动车行经小学门口，应当注意儿童，同时右侧标志说明该路段禁止停车、禁止鸣喇叭。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064936, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/457.jpg', '驾驶机动车在这种情况下临时停车后，为避免机动车后溜可将转向盘向右转。', '这样做，与在轮胎前方塞东西是同样的道理，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064937, 'B车、A车、C车', 'C车、A车、B车', 'A车、B车、C车', 'A车、C车、B车', '3', 'http://images.juheapi.com/jztk/subject4/458.jpg', '如图所示，在这种无信号灯控制情况下，A、B、C车的通行权顺序是什么？', '交规说明，在这种无信号灯控制情况下应该转弯让直行，右转车辆要让左转车辆。所以A、B、C车的通行权顺序是：A车、B车、C车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064938, '注意非机动车辆的动向，减速行驶', '谨慎驾驶低速通过', '持续鸣喇叭警告非机动车避让', '保持与非机动车安全车距', '3', '', '驾驶机动车遇到非机动车违法在机动车道上行驶，并阻碍机动车前进时，以下做法错误的是什么？', '驾驶机动车遇到非机动车违法在机动车道上行驶，并阻碍机动车前进时，注意非机动车辆的动向，减速行驶；谨慎驾驶低速通过；保持与非机动车安全车距；避让非机动车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064942, '挂入空挡', '挂入低速挡', '踏下离合器踏板', '挂入高速挡', '2', '', '驾驶汽车在山区道路下陡坡时，怎样利用发动机制动控制车速？', '山区下坡路要挂低档，只有低档能控制车速。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064943, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/464.jpg', '因故障在山区下坡路段长时间停车时，要用这种办法塞住车轮。', '由于是下坡路，因此若是停在此处很容易前溜，而这样做能防止车辆向下溜滑，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064946, '变换使用远近光灯提示', '长时间鸣喇叭', '使用远光灯', '鸣喇叭并使用远光灯', '1', '', '夜间驾驶机动车遇到对向来车未关闭远光灯时，以下做法正确的是什么？', '记住，只有在没有路灯或者没有照明的情况下才能用远光灯，一般都用近光灯。窄路窄桥，你若用远光灯，会让对方完全看不清，掉到桥下去的。夜间驾驶机动车遇到对向来车未关闭远光灯时，变换使用远近光灯提示。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064948, '救护车违反交通信号通行，不予避让', '减速，避让救护车', '按照信号灯指示，正常通行', '加速通过', '2', 'http://images.juheapi.com/jztk/subject4/469.jpg', '如图所示，驾驶机动车遇到执行紧急任务的救护车时，以下做法正确的是什么？', '驾驶机动车遇到执行紧急任务的救护车时，应当减速，避让救护车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064949, '紧跟前车行驶', '加大安全距离', '减小跟车距离', '尽快超越前车', '2', 'http://images.juheapi.com/jztk/subject4/470.jpg', '在这种山区道路怎样跟车行驶？', '道路狭窄，除了B选项其他选项都不够安全。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064950, '减速行驶', '集中注意力谨慎驾驶', '注意观察，减速靠边停车', '以正常速度行驶', '3', 'http://images.juheapi.com/jztk/subject4/471.jpg', '如图所示，驾驶机动车在暴雨天气条件下行驶，当刮水器无法刮净雨水影响行车安全时，以下做法正确的是什么？', '驾驶机动车在暴雨天气条件下行驶，当刮水器无法刮净雨水影响行车安全时，应当注意观察，减速靠边停车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064953, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/474.jpg', '如图所示，起步时此灯亮起表示驻车制动（手刹）放下。', '此灯亮起是在提醒我们驻车制动没有放下，先放下它才能开车！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064954, '可以超车', '可以倒车', '依次通行', '可以掉头', '3', '', '驾驶机动车在匝道内行驶，以下做法正确的是什么？', '“匝道”是指立交桥或高架路的进出道引道。驾驶机动车在匝道内行驶，可依次通行，不可倒车、掉头、超车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064957, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/478.jpg', '路面标记指示前方路口仅允许车辆向右转弯。', '路面上的标志是禁止右转弯。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064959, '迅速关闭发动机', '用覆盖法灭火', '开启发动机罩灭火', '用灭火器灭火', '3', '', '驾驶机动车行驶过程中发动机着火，以下做法错误的是什么？', '驾驶机动车行驶过程中发动机着火，应当迅速关闭发动机，用覆盖法或者灭火器灭火。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064961, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/482.jpg', '前方路口停车让行线表示减速让干道车先行。', '应该是停车让干道车先行，而不是减速让行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064963, '鸣喇叭从左侧超越自行车', '减速待自行车通过后再从B车左侧超越', '停车等待B车驶离后，在原车道行驶', '借用对向车道加速通过', '2', 'http://images.juheapi.com/jztk/subject4/484.jpg', '如图所示，A车正确的做法是什么？', 'B车发生故障，路边临时停车，A车应当减速待自行车通过后再从B车左侧超越。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064966, '可以超车', '可以倒车', '依次通行', '可以掉头', '3', '', '驾驶机动车在高速公路减速车道行驶时，以下做法正确的是什么？', '“匝道”是指立交桥或高架路的进出道引道。驾驶机动车在匝道内行驶，可依次通行，不可倒车、掉头、超车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064968, '减速，停车让行', '交替变换远近光灯加速通过', '连续鸣喇叭后通过', '确保安全加速通过', '1', 'http://images.juheapi.com/jztk/subject4/489.jpg', '如图所示，夜间驾驶机动车通过没有交通信号灯的人行横道时，以下做法正确的是什么？', '夜间驾驶机动车通过没有交通信号灯的人行横道时，应当减速，停车让行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064969, '没有系安全带', '离转向盘距离过近', '没有握紧转向盘', '安全气囊没有打开', '1', 'http://images.juheapi.com/jztk/subject4/490.jpg', '事故中造成这个驾驶人致命伤害的原因是什么？', '有图可看出是驾驶员在冲撞下向前扑出，因为没有安全带的保护，才会致使驾驶员向前倾，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064970, '避免紧急制动', '循车辙行驶', '避免急转方向', '降低车速行驶', '17', 'http://images.juheapi.com/jztk/subject4/491.jpg', '在这种有车辙的冰雪路段怎样行驶？', '安全第一', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064972, '电子不停车收费专用车道', '应急车道', '绿色通道', '快速公交车道', '1', 'http://images.juheapi.com/jztk/subject4/493.jpg', '以下这个标志的含义是什么？', '这个标志的含义是前方有电子不停车收费专用车道。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064973, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/494.jpg', '驾驶机动车在这个路口可以沿掉头车道直接掉头。', '《中华人民共和国道路交通安全法实施条例》第四十九条  机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。而本题的情况是在坡上，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064974, '连续变换远近光灯', '开启危险报警闪光灯', '使用远光灯，减速避让', '使用近光灯，减速避让', '4', '', '夜间驾驶机动车在窄路遇到对面驶来非机动车时，以下做法正确的是什么？', '夜间驾驶机动车在窄路遇到对面驶来非机动车时，使用近光灯，减速避让。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064975, '30米', '50米', '80米', '100米', '2', 'http://images.juheapi.com/jztk/subject4/496.jpg', '驾驶机动车行经此路段多少米内不得停车？', '根据交通法，机动车在道路上临时停车，应当遵守下列规定：(一)在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；(二)交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；(三)公共汽车站、急救站、加油站、消防栓或者消防队(站)门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；(四)车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064976, '尽快将中毒人员移出毒区', '脱去接触有毒空气的衣服', '用清水清洗暴露部位', '原地等待救援', '4', '', '交通事故中急救中毒伤员，以下做法错误的是什么？', '交通事故中急救中毒伤员，需要：尽快将中毒人员移出毒区；脱去接触有毒空气的衣服；用清水清洗暴露部位。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064979, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/500.jpg', '驾驶机动车在该处不影响行人正常通行的情况下可以掉头。', '由图可知是黄色双实线，且本车道为直行车道，因此不能掉头，所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064980, '正确', '错误', '', '', '2', '', '高速公路行车发生火灾时，要将机动车驶进服务区或停车场灭火。', '服务区的车辆多，而且还有加油站，若是这样做会将损失扩大，因此不能这么做，正确的做法是将车停到紧急停车带内予以灭火，若是火势太大，就赶紧逃开吧。。。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064981, '应尽量匀速前进', '应尽量避免换挡', '时刻注意下行车辆', '应选择高速挡', '13', '', '驾驶机动车在山区上坡路段行驶，以下做法正确的是什么？', '驾驶机动车在山区上坡路段行驶，应该挂低档、尽量匀速前进，且途中减挡可能会导致机动车熄火。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064982, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/503.jpg', '该车道路面导向箭头指示前方路口仅可左转弯。', '图片的标志为掉头的标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064983, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/504.jpg', '路面同向车行道分界线指示允许跨越变换车道。', '实线不能跨越，故不能变换车道超车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628064984, '鸣喇叭提示行人后掉头', '对面黄色车辆通过后掉头', '行人通过后掉头', '继续直行，寻找可掉头路段', '4', 'http://images.juheapi.com/jztk/subject4/505.jpg', '如图所示，驾驶机动车在这个路段想要掉头时，以下做法正确的是什么？', '该路段为直行道路，不可掉头或者转弯，车辆想要掉头时需要继续直行，寻找下一个可掉头路段。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064985, '左前方行人可能在前方机动车驶过后马上横穿道路', '左前方行人对是否横穿马路可能犹豫不决，无法准确判断', '前方机动车可能遇有其他横穿道路的行人减速或紧急停车', '前方机动车可能躲避横穿道路的行人，突然变更车道', '17', 'http://images.juheapi.com/jztk/subject4/506.jpg', '如图所示，驾驶机动车遇到这种情况时，驾驶人应注意的是什么？ ', '前方有车辆且有行人欲横穿马路，驾驶机动车遇到这种情况时，驾驶人应注意：左前方行人可能在前方机动车驶过后马上横穿道路；左前方行人对是否横穿马路可能犹豫不决，无法准确判断；前方机动车可能遇有其他横穿道路的行人减速或紧急停车；前方机动车可能躲避横穿道路的行人，突然变更车道。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064986, '用清水喷洒扑灭', '及时报警', '使用车内备用的灭火器灭火', '在来车方向设置警告标志', '16', '', '机动车行驶时突然发生自燃，驾驶人采取的以下紧急避险措施中，正确做法是什么？', '水的密度比油大，不能用来灭火。因此选项A首先排除，BD是用来告知自燃地点，而C选项则是进行灭火。因此BCD都是正确的做法，即本题选BCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064988, '孙某违法停车', '孙某客车超员', '李某超速', '李某疲劳驾驶', '9', '', '某日3时40分，孙某驾驶大客车（乘载54人、核载55人）行至随岳高速公路229公里加300米处，在停车下客过程中，被后方驶来李某驾驶的重型半挂机动车追尾，造成26人死亡，29人受伤。事后查明，李某从昨日18时许出发，途中一直未休息。双方驾驶人的主要违法行为是什么？', '违法行为：1、高速公路上不允许停车下客。2、李某从昨日18时许出发，途中一直未休息。故属于疲劳驾驶。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064989, '从匝道驶入高速公路，应当开启右转向灯', '驶离高速公路进入匝道时，应当开启右转向灯', '可在匝道上超车', '驶入错误的匝道后，可倒车行驶回高速公路', '2', '', '关于在高速公路匝道路段行驶，以下说法正确的是什么？', '“匝道”是指立交桥或高架路的进出道引道。高架：进口路和出口路是分开的，只能顺行，车辆错过了下匝道，就不能从上匝道下路，只能从下一个出口下路。立交桥：匝道的进出，也是按照设定的标志行驶。不管是高架的匝道还是立交桥的匝道，都不能掉头、倒车、超车或是停车。驶离高速公路进入匝道时，应当开启右转向灯。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064990, '迅速向右方倒车躲避', '停车鸣喇叭提示', '迅速向左方倒车躲避', '直接倒车躲避', '2', 'http://images.juheapi.com/jztk/subject4/511.jpg', '如图所示，驾驶机动车发现前车向后溜车，以下做法正确的是什么？', '上坡路上，前车向后溜车，应当立刻停车鸣喇叭提示。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064991, '从左侧超越前车', '鸣喇叭催促前车向前行驶', '从右侧超越前车', '与前车保持安全距离,排队等待', '4', '', '驾驶机动车遇到前方车辆停车，等待行人通过人行横道时，以下做法正确的是什么？', '前车等待行人过道时，自己也应耐心等待，B做法不恰当，AC做法易引发交通事故。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064994, '减速慢行', '正常行驶', '加速通过', '连续鸣喇叭', '1', '', '驾驶机动车行经两侧有非机动车行驶且有积水的路面时，应怎样做？', '驾驶车辆行经两侧有行人或非机动车行驶且有积水的路面时。更应减速慢行，以免泥水飞溅到行人或非机动车驾驶员身上。当我们没车的时候是不是很讨厌那些开车溅我们一身水的人，所以不要当我们有自己的车的时候不要成为那时我们讨厌的人。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064995, '直行通过路口', '停在停止线外等待', '在路口向左转弯', '在路口减速慢行', '3', 'http://images.juheapi.com/jztk/subject4/516.jpg', '看到这种手势信号时怎样行驶？', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064996, '保持与前车的安全距离', '避免转弯时占道行驶', '上陡坡提前换低速挡', '下长坡时，充分利用发动机制动', '17', '', '在山区道路行驶时，驾驶人要注意什么？', '四个选项都是在山区道路安全驾驶的注意事项，因此本题选ABCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628064997, '慢慢制动减速', '迅速制动减速', '迅速向右转向', '采取紧急制动', '1', '', '行车中发现左侧轮胎漏气时怎样处置？', '当车辆漏气时，引起轮胎不正常工作，因此此时就应该减速并找安全的地方停车。迅速向右转向或是紧急制动都有可能造成爆胎等状况的发生，因此不可行。选项A和B的差别在于是快还是要慢，若是动作过快也有可能造成爆胎，因此本题要选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628064998, '正确', '错误', '', '', '2', '', '驾驶机动车的驾驶人不需要系安全带。', '开车一定要系安全带，不系属于违法行为。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065000, '减速并鸣喇叭提示', '交替变换远近光灯提示', '加速通过', '持续鸣喇叭催促', '1', 'http://images.juheapi.com/jztk/subject4/521.jpg', '如图所示，驾驶机动车遇到非机动车占道行驶时，以下做法正确的是什么？', '对于灯光不明的路口，应交替变换远近光灯进行提示；加速通过不利于行车安全；鸣喇叭不必持续进行；因此选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065001, '下坡路段车辆由于重力作用，车速容易过快', '下坡路段由于重力作用，车辆比平路时操控困难', '下坡路段车辆阻力很大', '下坡路段前车车速较快，难以超越', '7', '', '驾驶机动车在山区道路下坡路段尽量避免超车，以下说法正确的是什么？', '驾驶机动车在山区道路下坡路段尽量避免超车，因为下坡路段车辆由于重力作用，车速容易过快，车辆比平路时更难操控，此时超车容易发生危险。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065002, '前方行人可能滞留在人行横道内', '左前方骑自行车者可能突然右转弯', '右前方骑摩托车者可能突然向左变更车道横穿道路', '右前方白色机动车行驶动态', '17', 'http://images.juheapi.com/jztk/subject4/523.jpg', '如图所示，驾驶机动车驶近这样的人行横道时，驾驶人应注意的是什么？', '驾驶机动车驶近这样的人行横道时，驾驶人应注意：前方行人可能滞留在人行横道内；左前方骑自行车者可能突然右转弯；右前方骑摩托车者可能突然向左变更车道横穿道路；右前方白色机动车行驶动态。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065004, '迅速跳车逃生', '双手紧握转向盘', '双脚勾住踏板', '背部紧靠椅背', '16', '', '驾驶机动车突然发生倾翻时，以下做法正确的是什么？', '驾驶机动车突然发生倾翻时，应当双手紧握转向盘、双脚勾住踏板、背部紧靠椅背，在车辆停稳前切不可跳车逃生，以防发生被砸或者碾压危险。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065005, '停在平坦的地方，在启动前检查', '停在平坦的地方，在怠速状态下检查', '无需停在平坦的地方，在启动前检查', '无需停在平坦的地方，在怠速状态下检查', '1', '', '检查机动车机油时，以下做法正确是什么？', '对的检查的方式是需要注意检查的条件.千万不要在汽车发动的时候去检查。检查的时候要把车先停好，然后拉紧手刹，再取出机油尺，先族干净后放进去，然后再重新拿出来检查机油油位。如果在机油尺下端的标记之间.就表示了油且是正常的。如果在标记下面，就表示机油留太少。机油太多的话就会出现在机油最在标记上面。如果少了就需要适里添加机油。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065006, '左侧来车应该给己车让行', '己车应该给左侧来车让行', '不需让行，谁车速快谁先过', '不需让行，己车有优先通行权', '2', 'http://images.juheapi.com/jztk/subject4/527.jpg', '如图所示，驾驶机动车遇到这种主路左侧来车的情况，以下说法正确的是什么？', '图中路考为辅路近主路，此时己车应该给左侧来车让行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065007, '轮胎磨损严重', '轮胎气压过高', '尖锐物体刺伤轮胎', '车辆超载超员', '17', '', '以下哪些能够引起轮胎爆裂？', '引发轮胎爆裂的几种情况：轮胎磨损严重，轮胎气压过高，尖锐物体刺伤轮胎，车辆超载超员。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065008, '正确', '错误', '', '', '1', '', '行车前应对机动车驾驶室、发动机舱、车外部、轮胎进行检查。', '驾驶室内检查包括制动、离合、转向，灯光，油量、电路等，发动机舱里包括机油，水等。一辆车的全部检查都在这里了。所以只能是对的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065011, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/532.jpg', '路面可变导向车道线指示可以随意选择通行方向。', '可变车道，是指车道内侧划了多条斜线，有点像趴下的“非”字，顾名思义就是能随时根据交通流量更改指示方向的车道。这个主要是针对部分高峰时段车流集中，但车道偏少；或者早晚高峰时段来回车流量有明显差异的路段。碰到这种车道，根据可变车道的指示走就行了。是有时段限制的，不是任何时候都可以随意选择通行方向。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065012, '正确', '错误', '', '', '2', '', '机动车落水后，要迅速关闭车窗阻挡车内进水，短暂闭绝空气，可打电话告知救援人员失事地点，等待救援。', '你要是有把握在短时间内被救起来当然是这样最好了，但是在正常情况下，你一旦进入河里并且封闭了空间，那么凭普通人的力气根本就不能再次打开车门了，最后活活的闷死在里面。最安全的方法，还是打开车门快速逃生，车坏了就坏了吧，破财免灾。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065013, '150米以内', '150米以外', '100米以内', '50米以内', '2', '', '夜间驾驶机动车在道路上会车，为避免对方驾驶人眩目，应距离对向来车多远改用近光灯？', '夜间驾驶机动车在道路上会车，为避免对方驾驶人眩目，应距离对向来车150米以外改用近光灯。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065014, '减速、观察、慢行', '鸣喇叭驱赶牲畜', '从牲畜的空隙中穿过', '低速行驶冲开牲畜群', '1', 'http://images.juheapi.com/jztk/subject4/535.jpg', '驾驶机动车看到这种标志需要注意什么？', '如图所示的交通标志是注意牲畜标志，因此在此路段要注意减速慢行，同时注意观察有没有牲畜穿出，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065018, '下车的乘客可能从公交车前方横穿道路', '公交车可能即将启动并向左变更车道', '右侧摩托车可能驶入机动车道并穿插变更车道', '对向车道内的机动车可能违法跨越道路中心线超车', '17', 'http://images.juheapi.com/jztk/subject4/539.jpg', '如图所示，驾驶机动车驶近公交车站时，驾驶人应注意的是什么？', '驾驶机动车驶近公交车站时，驾驶人应注意：下车的乘客可能从公交车前方横穿道路；公交车可能即将启动并向左变更车道；右侧摩托车可能驶入机动车道并穿插变更车道；对向车道内的机动车可能违法跨越道路中心线超车。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065020, '加速通过', '连续鸣喇叭警示', '停车让行', '迅速超越前方非机动车', '14', 'http://images.juheapi.com/jztk/subject4/541.jpg', '如图所示，驾驶机动车遇到这种情形时，以下做法错误的是什么？', '图中机动车遇到了三个状况：有行人穿过、过人行横线、有自行车行车，左侧有车辆汇入，此时驾驶员应当首先停车让行。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065022, '超速行驶', '客车超员', '驾驶逾期未年检机动车', '操作不当', '2', '', '何某驾驶一辆乘载53人的大客车（核载47人），行至宁合高速公路南京境内454公里加100米处，被一辆重型半挂牵引车追尾，导致大客车翻出路侧护栏并起火燃烧，造成17人死亡、27人受伤。何某的主要违法行为是什么？', '核载47人实际乘载53人题干主要透漏的是这个违法信息！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065023, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/544.jpg', '驾驶机动车遇到这种道路要提前减挡，以保持充足动力。', '如图可知，前方道路是上坡路，因此要提前减档，避免因动力不足后溜。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065026, '正确', '错误', '', '', '2', '', '驾驶人一边驾车，一边吸烟对安全行车无影响。', '一心两用妨碍安全驾驶，容易发生危险。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065027, '注意观察', '从一侧加速绕过', '谨慎驾驶', '做好停车准备', '15', '', '驾驶机动车过程中遇到专注于使用手机的行人时，以下说法正确的是什么？', '驾驶机动车过程中遇到专注于使用手机的行人时，应当注意观察、谨慎驾驶，随时做好停车准备。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065028, '在有让超车条件，保证安全的情况下，减速靠右让路', '加速行驶，使他不能超越', '开启危险报警闪光灯，暗示他不要超越', '向左行驶，阻止他超越', '1', '', '驾驶机动车发现后车开启左转向灯发出超车信号时，以下做法正确的是什么？', '行车过程中，要时刻确保安全第一，BCD三个选项都不利于安全行车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065029, '与前车保持50米以上的距离', '车速不得超过40公里/小时', '开启危险报警闪光灯', '在应急车道行驶', '13', '', '驾驶机动车在高速公路上行驶，遇有能见度小于100米的气象条件时，如何安全行驶？', '《中华人民共和国道路交通安全法实施条例》第八十一条：“机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065035, '60公里/小时', '50公里/小时', '40公里/小时', '30公里/小时', '3', 'http://images.juheapi.com/jztk/subject4/556.jpg', '如图所示，驾驶机动车驶入减速车道后最高时速不能超过多少？', '右侧方有限速标志，限速40公里/小时。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065036, '加速缩短会车时间', '减速靠右行驶', '保持安全间距', '注意避让非机动车', '16', 'http://images.juheapi.com/jztk/subject4/557.jpg', '在这种情况下怎样安全会车？', '因为前边有自行车为了安全一定要慢，不要加速缩短会车时间。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065038, '与前车保持足以采取紧急制动措施的安全距离', '与前车保持较近距离，以防加塞', '将注意力全部集中在所跟随的车辆上', '将注意力全部集中在后方的车辆上', '1', '', '关于驾驶机动车跟车行驶，以下做法正确的是什么？', '驾驶机动车跟车行驶时，应当与前车保持足以采取紧急制动措施的安全距离。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065039, '驶入紧急避险车道', '使用发动机制动', '使用驻车制动器制动', '必要时，可用车体刮擦路边障碍物减速', '17', '', '驾驶机动车在下坡行驶过程中行车制动器失效，以下做法正确的是什么？', '以上四个选项都可以在行车制动器失效的情况下起到制动的效果，因此应该选ABCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065040, '紧跟前车通过交叉口', '减速做好停车准备', '立即变更车道', '立即停车', '2', 'http://images.juheapi.com/jztk/subject4/561.jpg', '如图所示，驾驶机动车跟车行驶遇到前车遮挡路口交通信号灯时，以下做法正确的是什么？', '驾驶机动车跟车行驶遇到前车遮挡路口交通信号灯时，应当减速行车，注意观察，做好停车准备。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065041, '紧跟在后面行驶', '从左侧加速让过', '鸣喇叭示意让道', '减速或停车避让', '4', 'http://images.juheapi.com/jztk/subject4/562.jpg', '驾驶机动车看到这样的儿童怎样行驶？', '遇到这样的情况要减速慢行，必要时停车避让，以免孩子从滑板上掉下来或是移动位置。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065043, '视线不清，不能及时发现行人', '行人可能滑倒', '行人可能突然进入行车道', '行人可能会横过道路', '17', 'http://images.juheapi.com/jztk/subject4/564.jpg', '如图所示，驾驶机动车在雨天行驶，驾驶人应当注意的是什么？', '下雨天驾驶人应当注意：视线不清，不能及时发现行人；行人可能滑倒；行人可能突然进入行车道；行人可能会横过道路。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065045, '正确', '错误', '', '', '2', '', '驾驶机动车从辅路汇入主路车流时要迅速。', '驾车要谨慎，不要那么“迅速”！注意是汇入车流，所以正确的做法是要减速慢行，在不影响其他车辆正常行驶的情况下汇入主路，因此本题选B。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065048, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/569.jpg', '夜间行车，后方车辆提示超车，前方遇到这种情况时不能盲目让超。', '让超车时是要在条件允许的情况下减速靠右行，而图中的情况则是前方有行人及非机动车，因此不能盲目让超。所以本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065051, '持续鸣喇叭警示', '交替使用远近光灯提示', '使用近光灯，减速靠右避让', '加速通过', '3', '', '夜间驾驶机动车在农村道路行驶，遇到对向驶来畜力车时，以下做法正确的是什么？', '夜间驾驶机动车在农村道路行驶，遇到对向驶来畜力车时，应当使用近光灯，减速靠右避让。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107628065052, '在不具备超车条件的急弯路段加速超车', '驾驶逾期未检验的机动车', '驾驶与准驾车型不符的机动车', '疲劳驾驶', '8', '', '赵某（持有A2驾驶证）驾驶大型卧铺客车（核载36人），行驶至叶城县境内某处急弯路段，加速超越前车时，坠入道路一侧山沟，致16人死亡，20人受伤。赵某的主要违法行为是什么？', '赵某的主要违法行为是在不具备超车条件的急弯路段加速超车和驾驶与准驾车型不符的机动车。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065055, '正确', '错误', '', '', '2', '', '安全头枕要调整到与颈部平齐的高度。', '应该与头平齐。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065056, '人行横道', '停车场', '山区容易塌方、泥石流路段', '道路施划的停车泊位内', '8', '', '以下什么地点不能停车?', '人行横道，山区容易塌方、泥石流路段。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065057, '道路左侧儿童可能突然跑进路中', '前方行人可能未察觉有机动车驶近', '迎面来车可能造成会车困难', '右侧停放的机动车可能会突然起步', '17', 'http://images.juheapi.com/jztk/subject4/578.jpg', '如图所示，驾驶机动车遇到这种情况时，驾驶人应注意的是什么？', '图中左侧有儿童，前方有来向车辆和行人，右侧有停放车辆，驾驶机动车遇到这种情况时，驾驶人应注意：道路左侧儿童可能突然跑进路中，前方行人可能未察觉有机动车驶近，迎面来车可能造成会车困难，右侧停放的机动车可能会突然起步。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107628065058, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/579.jpg', '高速公路两侧白色半圆状的间隔距离是50米。', '白色半圆状车距确认线：白色半圆形车距确认线半球半径为30cm，间隔50米设置，一般在一定路段内连续设置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065060, '正确', '错误', '', '', '1', '', '驾驶客车遇非常情况或者发生事故时，要力所能及的将损失降到最低限度，决不能因紧急避险造成二次事故或更大的损失。', '要以人为本，要以将伤害和损失降到最低为原则。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107628065061, '行人通过速度较慢可能滞留在道路内', '电动自行车可能发生故障无法及时通过道路', '右侧驶来的机动车可能未能及时停车而进入路口', '考虑雨天路面湿滑，应注意按照限速规定控制车速', '17', 'http://images.juheapi.com/jztk/subject4/582.jpg', '如图所示，驾驶机动车遇到这种情况时，应注意哪些可能发生的危险？ ', '雨天行车，路人、电动车横穿人行横线时红灯变绿灯，驾驶机动车遇到这种情况时，应注意可能出现以下情况：行人通过速度较慢可能滞留在道路内、电动自行车可能发生故障无法及时通过道路、右侧驶来的机动车可能未能及时停车而进入路口，考虑雨天路面湿滑，应注意按照限速规定控制车速。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396419, '疏散人员', '开启危险报警闪光灯', '标记伤员的原始位置', '正确放置危险警告标志', '14', '', '发生交通事故后，防止二次事故的有效措施是什么？', '选项A/B/D都是防止二次事故的有效措施，而选项C则让人有种要明确责任归属的感觉了。因此本题选ABD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396421, '加速行驶，在对面来车交会前超过行人', '减速靠右，等对向车辆通过后，再缓慢超越行人', '鸣喇叭提示行人后，保持原速行驶', '鸣喇叭提示左侧车辆后，保持原速行驶', '2', 'http://images.juheapi.com/jztk/subject4/586.jpg', '如图所示，驾驶机动车遇到这种情形时，以下做法正确的是什么？', '驾驶机动车遇到三个状况：对面来车，前方左侧车辆汇入主路、前方右侧有行人，此时机动车驾驶员应当减速靠右，等对向车辆通过后，再缓慢超越行人。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396422, '放气减压', '及时换胎', '给轮胎充气', '不用更换', '2', '', '如果轮胎胎侧顺线出现裂口，以下做法正确的是什么？', '都出现裂口了.肯定需要更换，以免爆胎，安全第一，不要为了省钱连命都搭进去。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396423, '前方左侧是傍山险路', '靠路左侧行驶', '选择路中心行驶', '靠右侧低速通过', '4', 'http://images.juheapi.com/jztk/subject4/588.jpg', '驾驶机动车遇到这种山路怎样通过？', '注意本题的考点是如何通过，而不是问前方是什么路段。另外前方有来车，因此要会车，而会车时要减速靠右侧行驶，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396425, '设有电子不停车收费（ETC）车道的收费站', '停车领卡标志', '服务区标志', '紧急停车带', '1', 'http://images.juheapi.com/jztk/subject4/590.jpg', '以下这个标志的含义是什么？', '图中标志表示前方设有电子不停车收费（ETC）车道的收费站。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396426, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/591.jpg', '右侧标志表示临时停车不受限制。', '禁止车辆临时或长时停放-表示在限定的范围内，禁止一切车辆临时或长时停放。此标志设在禁止车辆停放的地方。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396427, '加速通过', '占用对向车道', '靠边停车', '减速靠右通过', '4', 'http://images.juheapi.com/jztk/subject4/592.jpg', '如图所示，驾驶机动车遇弯道会车时，以下做法正确的是什么？', '驾驶机动车遇弯道会车时，应当减速靠右通过。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396428, '持续鸣喇叭警告', '保持车速继续行驶', '与其抢行', '加速靠左侧绕行', '17', 'http://images.juheapi.com/jztk/subject4/593.jpg', '如图所示，A车在交叉路口左转时遇到B车强行超越，以下做法错误的是什么？', '在路口遇到特殊情况，优先要做的都是减速慢行，不要加速、抢行和鸣笛，确保安全下通行。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396429, '加塞抢行', '不强行超车', '停车或依次行驶', '不占用非机动车道行驶', '1', '', '发现前方机动车停车排队缓慢行驶时，属于交通陋习的是什么行为？', '加塞抢行只会让交通更拥堵，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396430, '提前开启左转向灯', '提前鸣喇叭', '确认前车让超后超越', '直接加速超越', '13', '', '驾驶机动车在山区路段超车时，以下做法正确的是什么？', '《中华人民共和国道路交通管理条例》第五十条：机动车超车，必须遵守下列规定:行经交叉路口、人行横道、漫水路、漫水桥等，不准超车。驾驶机动车在山区路段超车时，需要注意：提前开启左转向灯；提前鸣喇叭；确认前车让超后超越。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396431, '对向可能有车辆驶来', '前方骑自行车者可能由于上坡等原因突然改变方向', '山区弯道可能转弯半径较小，车速过快容易引起车辆失控', '转弯后路面可能存在落石、凹陷等特殊路况', '17', 'http://images.juheapi.com/jztk/subject4/596.jpg', '如图所示，驾驶机动车驶近这样的山区弯道时，驾驶人应注意的是什么？', '山区弯道情况多变，从图中也可以看到道路上有自行车，因此以上四项都需要注意。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396433, '正确', '错误', '', '', '1', '', '行车中不开转向灯强行并线是违法行为。', '根据有关规定，强行并线是违法行为，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396434, '减速缓慢通过', '鸣喇叭警示牲畜，以免牲畜冲入行车道发生事故', '不能鸣喇叭，避免牲畜因惊吓窜入行车道', '加速通过此事故隐患路段', '8', 'http://images.juheapi.com/jztk/subject4/599.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？', '不能鸣喇叭。避免动物因惊吓窜入行车道，保持一定的安全距离，停车让行等待动物穿过。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396435, '不得妨碍主路车辆正常行驶', '只要不发生事故可随意行驶', '可以碾压实线及导流线', '在不发生事故的前提下干扰主路车流也是可以的', '1', '', '关于驾驶机动车汇入主路车流，以下说法正确的是什么？', '选项B太不和谐了肯定不对,实线是不可以碾压的，D也一样不和谐，所以选A，而且A非常之和谐。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396436, '停车等待', '直行通过路口', '在路口向右转弯', '在路口向左转弯', '1', 'http://images.juheapi.com/jztk/subject4/601.jpg', '看到这种手势信号时怎样行驶？', '举手朝向您，无其他动作时是让告诉您停止。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396437, '鸣喇叭，加速通过', '及时开启远光灯', '使用近光灯，低速会车或停车让行', '使用远光灯，低速会车', '3', '', '夜间会车时，如遇对方持续开启远光灯，应当如何安全会车？', '夜间安全驾驶知识中第三条就有:“夜间会车应当在距对方来车150米以外改用近光灯，若对方车辆不关闭远光灯，可连续变换灯光提示对向车辆。当遇对面来车仍不关闭远光灯时，应及时减速靠右侧行驶或停车让行。”', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396438, '拉起驻车制动器', '开启危险报警闪光灯', '在后方用塞车木或石块塞住车轮，以防车辆后溜', '按规定在车后方设置警示标志', '17', '', '驾驶机动车在山区道路上坡路段行驶，因发生故障需停车检修时，以下做法正确的是什么？', '驾驶机动车在山区道路上坡路段行驶，因发生故障需停车检修时，需要：拉起驻车制动器；开启危险报警闪光灯；在后方用塞车木或石块塞住车轮，以防车辆后溜；按规定在车后方设置警示标志。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396439, '敲碎侧窗玻璃', '关闭车窗', '打电话求救', '用工具撬开车门', '1', '', '机动车不慎落水，车门无法开启时，可选择的自救方法是什么？', '如果进入到水里，你还能开门吗？不能吧，还有时间打电话吗？等你打完电话都不知道沉到哪里去了，关闭车门阻隔水吗？你以为是密封舱啊，就是给你密封了，氧气一下子就用完了，不还是等死，唯一可以做的就赶紧逃生，只能是开窗或者砸玻璃啊，第一时间逃跑才有生的希望啊，一下子沉的时候如果发动机没关车窗还是可以降下来的，或者手动的就更加容易把窗口打开了。 因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396440, '超速行驶', '疲劳驾驶', '客车超员', '驾驶具有安全隐患的机动车', '9', '', '李某驾驶一辆大客车，乘载21人（核载35人），行驶途中察觉制动装置有异常但未处理，行至双岛海湾大桥时时速为50公里（该路段限速40公里），因制动失灵坠入海中，造成13人死亡、8人受伤。李某的主要违法行为是什么？', '违法行为：1、察觉制动装置有异常但未处理，所以为驾驶故障机动车。2、行至双岛海湾大桥时时速为50公里(该路段限速40公里)所以为超速行驶。此题注意核载35人就是规定可以做35个人，实际乘坐21人所以没有客车超员。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396441, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/606.jpg', '如图所示，驾驶机动车在公交车站遇到这种情况要迅速向左变更车道绕行。', '减速慢行，注愈观察，预横穿的行人。拉开横向安全距离。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396442, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/607.jpg', '前方标志指示高速公路的名称和编号。', '没问题，此题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396443, '减速慢行', '注意观察', '拉开横向安全距离', '预防突然横穿的行人', '17', 'http://images.juheapi.com/jztk/subject4/608.jpg', '如图所示，驾驶机动车遇到这种情形，应如何安全通过？', '安全第一，谨慎驾驶，符合这些的都选。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396444, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/609.jpg', '右侧标志表示前方道路限宽3米。', '限制宽度-表示禁止装载宽度超过标志所示数值的车辆通行。此标志设在最大允许宽度受限制的地方。 以图为例：装载宽度不得超过3米', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396447, '停车后立即开门下车', '观察前方交通情况', '先开车门再观察侧后情况', '先观察侧后情况，再缓开车门', '4', '', '驾乘人员下车时要怎样做以保证安全？', '切记安全第一，所以选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396448, '必要时安装防滑链', '必须降低车速', '开启雾灯行驶', '利用发动机制动', '14', '', '驾驶汽车在冰雪道路上怎样安全行车？', '雪天不用开雾灯，开近光就好。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396453, '直行通过路口', '停车等待', '在路口向右转弯', '在路口向左转弯', '2', 'http://images.juheapi.com/jztk/subject4/618.jpg', '看到这种手势信号时怎样行驶？', '交警的面部对着哪个方向就是在指挥哪个方向的车，所以对于这张图片来说，就是在指示交警面部对着的那个方向的车左转，对你来说需要停车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396454, '前车左侧车门可能突然打开', '前车前方可能有行人横穿马路', '前车可能突然掉头', '前车可能突然倒车', '17', 'http://images.juheapi.com/jztk/subject4/619.jpg', '如图所示，驾驶机动车遇到前方道路中间有停驶车辆时，以下可能出现的危险情形有哪些？', '驾驶机动车遇到前方道路中间有停驶车辆时，应减速行车，注意安全，防止前车左侧车门突然打开、前车前方有行人横穿马路、前车突然倒车和掉头等情况发生。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396455, '继续驶入拥堵路口', '鸣喇叭让路口内的车让行', '快速驶入路口', '让路口内的车先行', '13', '', '驾驶机动车准备进入拥堵的环形路口时，以下做法错误的是什么？', '驾驶机动车准备进入拥堵的环形路口时，应当注意观察让路口内的车先行。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396456, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/621.jpg', '如图所示，红色汽车在此地点停车等候是违法行为。', '红色车右侧有禁止停车标志，所以在此停车是违法行为.', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396457, '保持较低车速通过', '停车让行，等行人通过后再通过', '连续鸣喇叭冲开人群', '确保安全的前提下绕行通过', '2', 'http://images.juheapi.com/jztk/subject4/622.jpg', '如图所示，驾驶机动车通过交叉路口时右转遇到人行横道有行人通过时，以下做法正确的是什么？', '驾驶机动车通过交叉路口时右转遇到人行横道有行人通过时，应当停车让行，等行人通过后再通过。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396458, '正确', '错误', '', '', '1', '', '制动时后车轮抱死可能会出侧滑甩尾的情况。', '所谓“侧滑”就是车辆急加速、突然制动或启动时扭矩过大而产生的侧向甩动的现象，俗称“甩尾”。造成车辆侧滑的因素很多。雪天，雨天、冰面上行车时突然抬起油门或突然加速；采取紧急制动，制动时后轮比前轮先抱死；都极易发生车辆侧滑而造成事故。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396459, '张某疲劳驾驶', '李某用私家车拖曳故障车辆', '刘某超速行驶', '未在故障车辆后设置警示标志', '16', '', '张某驾驶车辆在高速公路上发生故障不能移动，开启危险报警闪光灯后下车，联系朋友李某驾驶私家车帮忙拖曳到应急车道。李某拖曳故障车的过程中，刘某驾驶货运车辆以每小时110公里的速度驶来，导致三车相撞。这起事故中的违法行为有哪些？', '题中所示的违法行为主要有：张某未在故障车辆后设置警示标志、李某用私家车拖曳故障车辆、刘某超速行驶等。高速公路上发生故障，正确的做法是，开启危险报警闪光灯后下车，在故障车辆后设置警示标志，联系保险公司或者维修店进行拖曳维修。货运车辆在高速上最高车速不得超过100公里/小时。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396461, '转弯前减速', '进入弯道后加速', '靠路右侧行驶', '挂空挡滑行', '8', '', '驾驶汽车在山区道路转弯下陡坡路段遇对面来车怎样行驶？', '下陡坡不能挂空挡，过弯道不能加速。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396464, '停车场', '道路施划的停车泊位内', '人行横道', '施工路段', '7', '', '应该选择什么地点停车?', '停车场。道路施划的停车泊位内。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396467, '减速缓慢通过', '鸣喇叭警示牲畜，以免牲畜冲入行车道发生事故', '不能鸣喇叭，避免牲畜因惊吓窜入行车道', '加速通过此事故隐患路段', '8', 'http://images.juheapi.com/jztk/subject4/632.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么?', '不能鸣喇叭。避免动物因惊吓窜入行车道，保持一定的安全距离，停车让行等待动物穿过。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396468, '气压不稳', '气压增高', '行驶阻力减小', '爆胎', '4', '', '轮胎气压过低时，高速行驶可能导致什么结果？', '轮胎气压过低，在车子行驶过程中，会让轮胎不正常工作，若是高速行驶的话，产生摩擦之后就很容易发生爆胎了。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396470, '易导致后车追尾', '会相应增大油耗', '易产生侧滑', '会相应减少油耗', '8', '', '雨天驾驶机动车，不可以急踩制动踏板的主要原因是什么?', '在干燥路面驾驶，车速提高，车轮与路面间的附着力(俗称“抓地力“)几乎没有变化。而雨天，当车辆在潮湿路上行驶，车轮的抓地力则随车速的增加而急剧变小，很容易发生“水滑”，此时，不要急踩制动踏板或猛打转向盘。由于雨天路面情况的特殊性，行车时应适当降低车速。准备停车时最好提前100-200米开始减速，给后方的车辆留出足够的反应时间，也为自己留够制动距离，以免发生追尾。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396471, '车上人员要迅速从左侧车门离开', '在来车方向100 米处设置警告标志', '开启危险报警闪光灯和远光灯', '车上人员站到护栏以外安全的地方', '4', '', '大雾天在高速公路遇事故不能继续行驶时怎样处置？', '标准程序是：当车辆发生故障时，打开双闪警示灯，应将车辆停放在最右侧的应急车道或紧急停车带内，夜间打开尾灯示宽灯，司乘人员应立即下车，在路沿下护坡内等候(尽量远离高速公路路面、护栏）救援车辆、人员来，由一个人在车辆尾部150米左右放置三角警示牌或警示物（最好隔十米左右放置一个警示物），切记不能在高速公路内走动、追逐、等候！不能在故障车辆内等候！一般情况禁止在高速公路上修理车辆。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396472, '我方车辆灯光照向路外，前方即将进入弯道', '前方有灯光出现，可能即将发生会车', '左前方视线受阻，转弯后可能遇到突发情况', '为提高会车安全，改用远光灯', '13', 'http://images.juheapi.com/jztk/subject4/637.jpg', '如图所示，驾驶机动车遇到这种情况时，应注意哪些情况？', '在没有灯光的道路，处于弯道，并且由于看到对面车辆的灯光可以知道即将发生会车的情况，因此保持警惕，ABC选项正确，D选项开启远光灯容易影响对方视线造成危险，因此不选。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396474, '超车时从前车左侧超越', '超车时从前车右侧超越', '超车完毕，立即开启右转向灯驶回原车道', '超车完毕，与被超车拉开必要的安全距离后开启右转向灯驶回原车道', '9', '', '关于超车，以下说法正确的是什么?', '《道路交通安全法实施条例》第四十七条：机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。在没有道路中心线或者同方向只有1条机动车道的道路上，前车遇后车发出超车信号时，在条件许可的情况下。应当降低速度、靠右让路。后车应当在确认有充足的安全距离后，从前车的左侧超越。在与被超车辆拉开必要的安全距离后。开启右转向灯，驶回原车道。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396475, '不能准确判断周围的车辆距离', '周围车辆驾驶人不容易看清超车信号', '道路湿滑，车辆易出现侧滑现象', '不能够及时发现危险情况', '17', '', '雨天驾驶机动车，不宜超车的主要原因是什么?', '雨中行车，要随时注意前车的行驶速度和方向，绝不可因前车速度慢而加速超车。尤其是在高速公路上，由于各车道的车速相对较高，司机的视角变窄，加上路面湿滑，强行越线超车时，稍动方向就很容易造成车轮打滑，极易造成与其他车辆发生刮蹭，引发车辆侧翻等意外事故。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396476, '30米', '50米', '80米', '100米', '2', '', '驾驶机动车在距离隧道前多少米内不得停车？', '《道路交通安全法实施条例》第六十三条：交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396478, '上坡路段的安全距离应比平坦路段的大', '下坡路段的安全距离应比平坦路段的小', '急弯路段应当紧随前车', '以上说法都正确', '1', '', '在山区道路行驶时，以下说法正确的是什么？', '上坡路段的安全距离应比平坦路段的大。上坡需要加速，为了避免和前车发生追尾，需要加大安全距离。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396479, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/644.jpg', '如图所示，在这种情况下可以在公交车站临时停车。', '如图所示，右侧是公交车站，根据《道路交通安全法实施条例》第六十三条机动车在道路上临时停车，应当通守下列规定:(三)公共汽车站、急救站、加油站、消防栓或者消防队(站)门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396480, '加速直接汇入车流', '开启转向灯观察主路情况确保安全汇入车流', '开启转向灯直接汇入车流', '不用开启转向灯加速汇入车流', '2', '', '驾驶机动车应当怎样汇入主路车流?', 'B是最和谐的，也是最安全的，所以选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396482, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/647.jpg', '驾驶机动车在路口遇到这种情况要随时准备停车礼让。', '由图可知，此交叉路口是没有信号灯的。因此在这样的情况下要注意避让，必要时停车避让。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396485, '沿道路右侧行驶', '挂低速档减速通过', '沿道路左侧行驶', '挂高速档加速通过', '7', 'http://images.juheapi.com/jztk/subject4/650.jpg', '怎样安全通过这种较窄的弯道？', '右侧标志是上坡路，所以要挂低档沿右侧行驶。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396486, '迅速关闭发动机', '用水进行灭火', '开启发动机罩灭火', '站在下风处灭火', '1', '', '发动机着火后首先怎样处置？', '试题问的是首选做什么，第一步当然是迅速关闭发动机。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396487, '正确', '错误', '', '', '2', '', '发动机着火时，要迅速关闭发动机，开启发动机罩进行灭火。', '开发动机罩后氧气就会迅速涌入，引起更大的火灾。 所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396488, '停车等待动物穿过', '鸣喇叭驱赶动物', '下车驱赶动物', '与动物保持较远距离', '9', 'http://images.juheapi.com/jztk/subject4/653.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？', '不能鸣喇叭。避免动物因惊吓帘入行车道.保持一定的安全距离，停车让行等待动物穿过。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396489, '疲劳驾驶', '客车超员', '超速行驶', '操作不当', '7', '', '某日19时，杨某驾驶大客车，乘载57人（核载55人），连续行驶至次日凌晨1时,在金城江区境内050国道3008公里加110米处，因机动车左前胎爆裂，造成12人死亡、22人受伤的特大交通事故。杨某的主要违法行为是什么？', '违法行为：1、题目提到连续行驶到次日凌晨1时，属疲劳驾驶。2、题目提到只能做55以上，却坐了57人，属客车超员。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396490, '对', '错', '', '', '2', '', '在路口掉头时，可以不避让直行车辆。', '不得妨碍正常车辆通行，直行有优先行驶权。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396492, '从其前方绕过', '从其身后绕行', '鸣喇叭提醒', '主动停车礼让', '4', 'http://images.juheapi.com/jztk/subject4/657.jpg', '驾驶机动车遇到这样的行人怎样行驶？', '遇到这种情况要减速慢行，必要时停车，让其先通过。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396494, '立即超越', '连续鸣喇叭提醒', '保持安全距离超越', '鸣喇叭加速超越', '3', 'http://images.juheapi.com/jztk/subject4/659.jpg', '驾驶机动车在这种情况下正确的做法是什么？', '如图所示，前方有来车，且同向前方有非机动车，因此在超车时要注意一定的安全距离，所以本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396495, '在从动轮下铺垫砂石', '换高速挡加速猛冲', '在驱动轮下铺垫砂石', '猛打转向盘配合急加速', '3', '', '在泥泞路段遇驱动车轮空转打滑时如何处置？', '注意是驱动轮空转打滑，因此应该在驱动轮上下手，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396496, '加速，并超越白色汽车通过路口', '减速，注意避让', '持续鸣喇叭，并保持原有车速', '用车灯闪烁，示意对方让行', '2', 'http://images.juheapi.com/jztk/subject4/661.jpg', '如图所示，驾驶机动车遇到左侧支路白色汽车不减速让行时，以下做法正确的是什么？ ', '驾驶机动车遇到左侧支路白色汽车不减速让行时，为保证自身行车安全，机动车应当减速，注意避让。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396497, '应提前降低车速', '应提前降低挡位', '尽量利用发动机制动控制车速', '尽量利用驻车制动器控制车速', '4', 'http://images.juheapi.com/jztk/subject4/662.jpg', '如图所示，驾驶机动车遇到这种路段时，以下说法错误的是什么？', '上图标志，下坡路，连续使用制动器，致使车轮制动器发热而降低制动效能。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396498, '对', '错', '', '', '1', '', '前方遇有大型拉土（石）货车，应当尽量远离，避让。', '注意避让，安全第一。遇见货车之类的大车一定要离得远远的，你就是再好的车也是碰不过大货的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396499, '易导致后车追尾', '会相应增大油耗', '易产生侧滑', '会相应减少油耗', '8', '', '雨天驾驶机动车，不可以急踩制动踏板的主要原因是什么？', '雨天，当车辆在潮湿路上行驶，车轮的抓地力则随车速的增加而急剧变小。很容易发生.水滑，此时，不要急踩制动踏板或猛打转向盘。由于雨天路面情况的特殊性，行车时应适当降低车速。准备停车时最好提前100-200m开始减速.给后方的车辆留出足够的反应时间，也为自己留够制动距离.以免发生追尾。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396500, '倒车逆行，穿越中央分隔带掉头，或在车道内停车', '骑轧车行道分界线或者在路肩上行驶', '在匝道、加速车道或者在减速车道上超车', '试车或者学习驾驶机动车', '17', '', '为确保机动车在高速公路行驶的安全，不得有下列哪些行为？', '根据《中华人民共和国道路交通安全法实施条例》第八十二条  机动车在高速公路上行驶，不得有下列行为：（一）倒车、逆行、穿越中央分隔带掉头或者在车道内停车；（二）在匝道、加速车道或者减速车道上超车；（三）骑、轧车行道分界线或者在路肩上行驶；（四）非紧急情况时在应急车道行驶或者停车；（五）试车或者学习驾驶机动车。因此本题选ABCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396502, '连续鸣喇叭示意其让道', '加大安全车距，适时超车', '加速行驶，伺机超车', '紧跟前方大货车', '2', 'http://images.juheapi.com/jztk/subject4/667.jpg', '如图所示，驾驶机动车跟车行驶遇到前方大货车行驶缓慢时，以下做法正确的是什么？', '前方有车时，应加大安全车距，适时超车，ACD选项都不利于安全行车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396503, '正确', '错误', '', '', '2', '', '行车途中发动机突然熄火，不能继续启动时要采取紧急制动措施，迫使机动车迅速停住。', '若是采取紧急制动的话，很有可能造成机动车侧滑或侧翻，引发事故。因此正确的做法应当是缓慢减速停车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396504, '正确', '错误', '', '', '2', '', '机动车通过急转弯路段时，在机动车较少的情况下可以超车。', '《中华人民共和国道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 通过隧道，铁路道口，急转弯路段，窄路，窄桥或遇前方道路上有车辆掉头时，不得超车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396505, '客车超员', '乘车人携带易燃易爆危险物品', '超速行驶', '不按规定停车', '7', '', '邹某驾驶大型卧铺客车（核载35人，实载47人），行至京港澳高速公路938公里时，因乘车人携带的大量危险化学品在车厢内突然发生爆燃，造成41人死亡，6人受伤。此事故中的主要违法行为是什么？', '违法行为：1、核载35人，实载47人为超载；2、乘车人携带的大量危险化学品。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396506, '正确', '错误', '', '', '1', '', '下坡路制动失效后，要迅速逐级或越一级减挡，利用发动机制动作用控制车速。', '发动机制动是利用发动机的牵阻作用减慢车速，档位越低牵阻越明显，制动性越强。在下长坡道路行驶，挂入低速档利用发动机的牵阻作用可以减少制动器的负担和减少制动次数，防止制动过热引起制动力热衰减；在冰雪、泥泞的路面上行驶，应用发动机牵阻制动可以防止侧滑。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396507, '紧跟前车后方行驶', '迅速从车左侧超越', '保持较大跟车距离', '迅速从车右侧超越', '3', 'http://images.juheapi.com/jztk/subject4/672.jpg', '驾驶机动车遇到这种情况怎么办？', '遇到这种情况，要多留个心眼，那就是要考虑到可能从车上掉落东西，以免出状况。因此要加大跟车距离，所以本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396508, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/673.jpg', '驾驶机动车通过这个铁路道口时要减速停车。', '红灯亮了呀亲。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396509, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/674.jpg', '左侧标志表示此处允许机动车掉头。', '黄色虚线+掉头标志都表明此处是可以掉头的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396510, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/675.jpg', '黄色斜线填充线指示该区域禁止进入或压线行驶。', '黄色斜线填充双黄实线禁止跨越对向车行道分界线示例 在路面较宽时，为保证车行道宽度不大于3. 75 m，双黄线间距可以适当调整。在双黄线间距大于50 cm时应用黄色斜线或其他设施填充两条黄实线间的部分，禁止车辆压线或进入该区域。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396511, '不开启灯光', '开远光灯', '开危险报警闪光灯', '开启示廓灯、后位灯', '7', '', '夜间路边临时停车，以下做法错误的是什么?', '夜间临时停车要开启危险报警闪光灯、示廓灯和后尾灯。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396512, '正确', '错误', '', '', '2', '', '漫水道路行车时，要挂高速挡，快速通过。', '不应该挂高速挡，而是要“应选用低速挡，以保证汽车有足够的驱动力；入水时应平稳，以防溅起的水花落 入电器设备，造成电器短路;发动机中速运转，保持车速，中途不得停车、换挡和急转转向盘”这样才对。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396513, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/678.jpg', '右侧标志表示前方路段解除时速40公里限制。', '限制速度-表示该标志至前方限制速度标志的路段内，机动车行驶速度不得超过标志所示数值。此标志设在需要限制车辆速度的路段的起点。以图为例：限制行驶时速不得超过40公里。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396514, '行驶阻力大', '车轮侧滑', '机动车颠簸', '方向失控', '2', '', '在泥泞路段行车容易出现什么现象？', '泥泞路面行驶阻力小，凹凸不平的路面机动车才会颠簸，横风时方向才会失控，因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396515, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/680.jpg', '路中心黄色虚实线指示允许暂时越过超车。', '如果双黄线，一条是实线，一条是虚线，虚线在哪一侧，那侧的车辆就可以从此临时跨越，比如超车或转弯。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396516, '降低车速', '随时准备停车', '尽快超越', '加大横向安全距离', '14', 'http://images.juheapi.com/jztk/subject4/681.jpg', '如图所示，驾驶机动车临近停在车站的公交车时，以下做法正确的是什么？', '不要尽快超越，远离大车，注息安全.谨慎驾驶。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396517, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/682.jpg', '驾驶机动车在这样的路段要注意观察，随时避让横过道路的动物。', '遇到这种情况，要减速慢行，同时注意观察牲畜的行动，以免这些牲畜突然变更方向。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396519, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/684.jpg', '如图所示，驾驶机动车进入该居民小区，车速不能超过5km/h。', '前方有最高限速标志，限速5km/h.', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396520, '握稳方向', '抢挂低速挡减速', '使用驻车制动器减速', '开启危险报警闪光灯', '17', '', '机动车在行驶中突遇制动失灵时，驾驶人要采取什么措施？', '机动车在行驶中突遇制动失灵时，应当握稳方向并开启危险报警闪光，抢挂低速挡减速，停车后使用驻车制动器，以防车子出现后溜的情况。选项C：使用驻车制动器减速，是说慢慢使用制动器减速，而不是一下拉紧！', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396522, '为减少交会时间，加速通过', '立即停车，上前查看是否有被困人员', '停车后围观', '立即停车，尽量远离，拨打报警电话', '4', '', '驾驶机动车，遇前方停驶的油料运输车起火冒烟，以下做法正确的是什么？', '太危险了，所以打电话，让专业人来处理吧！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396523, '对', '错', '', '', '1', '', '轮胎气压过高或过低都容易导致爆胎。', '胎压过高或过低都容易娘胎:胎压过高，使轮胎与地面的接触面积减少。单位面积所承受的压力、磨损剧增，容易造成刹车失控;如果胎压过低。使轮胎与地面接触面积增加。行驶时胎内温度不正常增加。同时.由于胎侧变形严重，内部的钢丝、帘布层老化加剧.从而为爆胎埋下隐患。并且使油耗呈增加，轮胎寿命降低。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396524, '会车使用远光灯', '在隧道内超车', '会车时保持安全间距', '开启近光灯行驶', '7', '', '在隧道内通行时哪些行为是不正确的？', '隧道开远光影响前车视线，隧道内禁止超车，因此选择“会灯使用远光灯”“在隧道内超车”。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396525, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/690.jpg', '交通警察发出的是右转弯手势信号。', '图为直行辅助手势。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396526, '正确', '错误', '', '', '1', '', '机动车发生爆胎后，驾驶人在尚未控制住车速前，不要冒险使用行车制动器停车，以避免机动车横甩发生更大的险情。', '爆胎后,驾驶人在尚未控制住车速的时候，是不能急踩刹车的，要不然会横甩或者翻车已经后面来车追尾 把几道题综合起来看，大致的做法觉得应该是紧握方向盘，控制方向，极力保持直线行驶，利用发动机制动，减档或抢档减速度，轻踏制动踏板缓慢减速，平稳靠边停车', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396527, '前后防雾灯', '危险报警闪光灯', '前大灯', '倒车灯', '2', '', '机动车在雨天临时停车时，应开启什么灯？', '下雨天开车视野不清，因此若要临时停车，应当开启危险报警闪光灯，提醒其他车辆注意。另外三个选项，没有警示作用一说。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396528, '确认机动车车胎是否损毁', '确认周围是否有障碍物', '确认在车辆附近是否存在安全隐患', '确认出车方向的安全性', '17', '', '出车前检查的目的是什么？', '都是为了安全。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396529, '继续加速', '向右转向', '向左转向', '紧急制动', '2', '', '在泥泞路段遇车后轮向右侧滑时如何处置？', '好比是后轮向左侧横滑如果向右打方向，会加大横滑的幅度的！因为向右打方向，后轮左侧转动就会快，右侧会变慢，左侧抓地力会下降，所以会加大横滑的幅度的，相反如果向同一方向打，还是这个例子向左侧打，右轮转动会快，左侧转动会慢，抓地力就会提高，就会恢复行使方向的！因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396530, '低速缓慢通过', '加速通过', '连续鸣喇叭通过', '保持正常车速通过', '1', 'http://images.juheapi.com/jztk/subject4/695.jpg', '驾驶机动车遇到这种情况要如何行驶？', '遇到这样的情况要低速慢行，以免水花溅到周围的行人。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396532, '正确', '错误', '', '', '2', '', '如果遇到较强横风，感觉机动车产生横向偏移时，要握紧转向盘并紧急制动。', '当车遇到横风时如果发生的横移，因为在横移的瞬间车轮是离开地面的，所以才会横移，最好的方法就是收油，等车速自己下来，这样就可以让车轮恢复抓地力，然后再进行打轮刹车等动作。这题判错的原因主要是当时车轮是离地的，也就是说车轮是没有抓地力的，如果这时采取紧急制动的话，很有可能会发生侧翻或者侧滑，很危险。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396533, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/698.jpg', '交通警察发出这种手势信号时允许在路口向右转弯。', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396534, '紧急制动', '迅速转向进行调整', '迅速向无障碍一侧转向躲避', '迅速向有障碍一侧转向躲避', '1', '', '当机动车转向失控行驶方向偏离，事故已经无可避免时，要采取什么措施？。', '除了选项A之外，三个选项都有可能因躲避或转向而造成另外的事故，比如与其他车辆擦碰或是撞到障碍物等。另外注意本题中“事故已经无可避免”这一句，也就是说事故是肯定会发生的，那么接下来要做的就是如何不让事故造成的损伤和损害变大，因此只有选项A是唯一的选择。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396535, '可利用避险车道减速停车', '越二级挡位减挡', '挂倒挡迫使停车', '拉紧驻车制动器减速', '1', '', '下坡路行车中制动突然失效怎样处置？', '刹车突然失灵时，摩擦力特别能显神奇功效。可用车前保险杠、车厢等钢性部位与路边岩石、大树或土坡摩擦、碰撞，在不威胁乘车人安全的情况下强行停车。这样，虽对车会造成损害，但可减少交通事故发生率。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396536, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/701.jpg', '驾驶机动车在前方路口掉头前先进入左转直行车道。', '由图可知有掉头车道，因此若要掉头应当提前驶入掉头车道内，所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396537, '客车超员', '超速行驶', '酒后驾驶', '疲劳驾驶', '2', '', '佟某驾驶一辆大客车（乘载54人，核载55人)行至太原境内以45公里的时速通过一处泥泞路段时，机动车侧滑驶出路外坠入深沟，导致14人死亡、40人受伤。佟某的主要违法行为是什么？', '当车辆行驶中遇到如积水,冰雪,泥泞等路段时行驶时速不得超过每小时30公里,所以他超速了！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396538, '正确', '错误', '', '', '2', '', '机动车在泥泞路段后轮发生侧滑时，要将转向盘向侧滑的相反方向缓转修正。', '好比是后轮向左侧横滑如果向右打方向，会加大横滑的幅度的！因为向右打方向，后轮左侧转动就会快，右侧会变慢，左侧抓地力会下降，所以会加大横滑的幅度的，相反如果向同一方向打，还是这个例子向左侧打，右轮转动会快，左侧转动会慢，抓地力就会提高，就会恢复行使方向的！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396539, '使用驻车制动器', '踏下离合器踏板', '踏制动踏板', '用加速踏板', '4', '', '在泥泞路段行车，要怎样控制速度，匀速一次性通过？', '注意关键：匀速一次性通过，因此要控制要油门，避免熄火等情况发生，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396540, '定期维护制动系统', '行车前检查制动踏板的自由行程', '正确使用制动，防止热衰退', '采用液压制动的机动车，行车前检查制动液是否有滴漏', '17', '', '有效预防机动车发生制动失效的措施是什么？', '四个选项都是对制动系统等的检查，通过检查，预防机动车制动失效的发生。因此本题选ABCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396543, '停车等待动物穿过', '鸣喇叭驱赶动物', '下车驱赶动物', '与动物保持较远距离', '9', 'http://images.juheapi.com/jztk/subject4/708.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么?', '不能鸣喇叭。避免动物因惊吓窜入行车道。保持一定的安全距离，停车让行等待动物穿过。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396544, '对', '错', '', '', '2', '', '出车前检查刮水器时，应尽量在干燥状态下进行。', '干燥，摩擦力大，会把爱车的玻璃刮坏的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396546, '对', '错', '', '', '1', '', '驾驶机动车在转弯之前应留意旁边行驶的自行车，是因为自行车比较小，不太容易被看到。', '非机动车和行人。机动车在转弯时视野有盲区不太容易被看到.且随意性比较大.要特别注惫。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396547, '仔细巡视车辆四周的状况，观察车底和车身周围是否有障碍物', '上车后关好车门，调整好座位，系好安全带', '启动车辆，观察仪表，检查车辆工作是否正常', '调整好后视镜', '17', '', '出车前，应该做的准备工作是什么？', '仔细巡视车辆四周的状况，观察车底和车身周围是否有障碍物.上车后关好车门.调整好座位，系好安全带.启动车辆。观察仪裹.检查车辆工作是否正常，调整好后视镜。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396548, '加速从前方绕过', '加速从身后绕行', '减速或停车让行', '连续鸣喇叭提醒', '3', 'http://images.juheapi.com/jztk/subject4/713.jpg', '驾驶机动车遇到这样的行人怎样礼让？', '遇到这种情况要减速慢行，必要时停车让行。因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396549, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/714.jpg', '驾驶机动车遇到这种情况时要向左占道行驶。', '遇到这种情况要减速慢行，必要时停车，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396550, '前方有人行横道', '应当相应减速行驶', '视野范围内无行人可以保持原速行驶', '视野范围内无行人可以适当加速通过', '7', 'http://images.juheapi.com/jztk/subject4/715.jpg', '如图所示，当您看到这个标志时，应该想到什么?', '注意行人标志。用以警告车辆驾驶人减速慢行，注意行人。设在行人密集，或不易被写驶员发现的人行横道线以前适当位置。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396552, '人要站在下风处', '灭火器瞄准火源', '尽量接近火源', '灭火器瞄准火苗', '2', '', '怎样正确使用灭火器灭火？', '1、把灭火器罐上下晃动三次以上  2、将安全梢拉开。  3、握住出粉皮管，将皮管朝向火点。  4、距着火点4―6米处用力压下把手，选择上风位置或者侧风方向接近火点，将干粉射入火焰基部。  5、息灭后并以水冷却除烟以防止复燃。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396553, '占道行驶', '急转转向盘', '弯道前不减速', '靠路右侧行驶', '13', '', '驾驶汽车在转弯路段易引发事故的驾驶行为有哪些？', '看清题目哦，问的是易发生事故的驾驶行为。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396554, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/719.jpg', '驾驶机动车需要掉头时，只要不影响正常交通可以在虚线处掉头。', '黄色虚线中心线处是可以掉头的，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396555, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/720.jpg', '如图所示，驾驶机动车看到路边有这种标志时，表示前方接近学校区域，因此要提前减速注意观察。', '注意儿童标志:用以警告车辆驾驶人减速慢行.注意儿童。设在小学、幼儿园、少年宫等儿全经常出入地点前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396556, '将机动车向上坡道方向行驶', '用车身靠向路旁的岩石或树林碰擦', '利用道路边专设的避险车道停车', '拉紧驻车制动器操纵杆或越二级挡位减挡', '4', '', '下坡路制动突然失效后，不可采用的办法是什么？', '利用重力作用增大汽车前进阻力，就是山坡时停止动力供应后车速逐渐下降。你也可以使用发动机制动，但使用驻车制动器作用很小，是不正确的。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396557, '正确', '错误', '', '', '1', '', '在冰雪道路上行车时，机动车的稳定性降低，加速过急时车轮易空转或溜滑。', '轮胎与地面的摩擦系数降低，轮胎不易附着在地面上，只要一加速，就极易空转或溜滑。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396558, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/723.jpg', '该车道路面导向箭头指示前方道路仅可直行。', '该车道路面导向箭头指示前方道路仅可直行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396559, '正确', '错误', '', '', '1', '', '机动车行经交叉路口，不得超车。', '路口通行时，要减速慢行注意观察。超车当然不可以。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396560, '做好随时停车的准备', '预防公交车突然起步', '预防行人从车前穿出', '与公交车保持安全间距', '17', '', '驾驶汽车驶近停有公交车的车站需要注意什么？', '记住吧', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396561, '可在应急车道停车上下人员', '可在紧急停车带停车装卸货物', '可在减速或加速车道上超车、停车', '非紧急情况时不得在应急车道行驶或者停车', '4', '', '机动车在高速公路行驶，以下哪种说法是正确的？', '在高速公路上，除了遇到障碍、发生故障等必须停车的情况外，不准随意停车、停车上下人员或者装卸货物，也不准掉头、转弯、逆行、倒车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396562, '正确', '错误', '', '', '2', '', '装有转向助力装置的机动车，驾驶人突然发现转向困难，操作费力，要紧握转向盘保持低速行驶。', '出现这种情况时，应当握紧方向盘，减速停车。先下车看一下是不是轮胎问题，如不是，应马上进维修站检查，最好用拖车拖。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396563, '加速抢先绕过障碍物', '占对向车道迫使对向让道', '停车让对向来车优先通行', '鸣喇叭或开启前照灯', '3', 'http://images.juheapi.com/jztk/subject4/728.jpg', '驾驶机动车在这种情况下怎样安全行驶？', '根据有关规定，有障碍物的一方要让无障碍物的一方先行，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396564, '利用道路边专设避险车道减速停车', '用车身靠向路旁的岩石或树木碰擦', '首先拉紧驻车制动', '抢挂低速档', '14', '', '下坡路行驶，制动突然失效后，可采用的减速方法是什么？', '这四个选项中，除了选项C可能会造成车子侧滑等状况外，都是可以让机动车减速的有效措施。因此本题选ABD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396565, '对', '错', '', '', '2', '', '在路口掉头时，为了保证通畅，应加速迅速完成掉头。', '凡是看到“加速”基本上都是错的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396566, '标志规定车速', '每小时80公里', '每小时60公里', '每小时40公里', '1', '', '驶入高速公路减速车道后，应关闭转向灯，注意观察限速标志，进入匝道之前将车速降到多少以下？', '减速车道有限速标志，应该按限速标志规定的车速行驶。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396567, '正确', '错误', '', '', '2', '', '遇伤者被压于车轮或货物下时，要立即拉拽伤者的肢体将其拖出。', '车轮压住了伤者，必须将车轮或货物移开后才能将伤者移出。如果硬拖的话，容易使伤者伤得更严重。因此错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396568, '减速通过', '加速通过', '鸣喇叭通过', '紧急制动', '1', 'http://images.juheapi.com/jztk/subject4/733.jpg', '驾驶机动车遇到这种情况的人行横道怎样通过？', '由图可知，这是一个既没有交通信号灯也没有交警指挥的交叉路口，因此通过时一定要减速慢行，并注意观察周围情况，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396569, '加大两车间的距离', '时刻注意前车刹车灯的变化', '降低行车速度', '鸣喇叭提醒前车提高车速，避免后车追尾', '4', '', '雾天驾驶机动车跟车行驶，以下做法错误的是什么？', '雾天尽量保持安全距离和车速，不要鸣喇叭催促。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396570, '上坡路段的安全距离应比平坦路段的大', '下坡路段的安全距离应比平坦路段的小', '急弯路段应当紧随前车', '急弯路段可以超车', '1', '', '驾驶机动车在山区道路行驶时，以下说法正确的是什么？', '上坡路段的安全距离应比平坦路段的大。上坡需要加速，为了避免和前车发生追尾，需要加大安全距离。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396571, '不可骑、轧车道分界线行驶', '不可在路肩上行驶', '可以在匝道、加速车道或者减速车道上超车', '不可学习驾驶', '3', '', '机动车上高速公路，以下哪种说法是错误的？', '《中华人民共和国道路交通安全法实施条例》第八十二条  机动车在高速公路上行驶，不得有下列行为：　　（一）倒车、逆行、穿越中央分隔带掉头或者在车道内停车；　　（二）在匝道、加速车道或者减速车道上超车；　　（三）骑、轧车行道分界线或者在路肩上行驶；　　（四）非紧急情况时在应急车道行驶或者停车；　　（五）试车或者学习驾驶机动车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396572, '在远处鸣喇叭催促', '从行人间低速穿过', '加速从行人前绕过', '停车等待行人通过', '4', 'http://images.juheapi.com/jztk/subject4/737.jpg', '在路口遇到这种情况的行人，如何做到礼让？', '和谐让行，万一碰到一点事情就麻烦了。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396573, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/738.jpg', '右侧标志警告前方是下陡坡路段。', '连续下坡-用以提醒车辆驾驶人小心驾驶。当连续下坡总长大于3 km后，应重复设置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396574, '加速驶出隧道', '减少与前车距离，利用前车挡住强光', '与前车保持安全距离，降低行驶车速，驶出隧道', '变更至车辆较少的车道，迅速驶出隧道', '3', 'http://images.juheapi.com/jztk/subject4/739.jpg', '当车辆驶出隧道时，驾驶人易出现图中所示的“明适应”现象，以下做法正确的是什么？', '只有C最安全。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396575, '前车通过后再爬坡', '迅速超越前车爬坡', '低速超越前车爬坡', '紧随前车后爬坡', '1', '', '驾驶机动车在山区冰雪道路上遇前车正在爬坡时如何处置？', '本题的关键在于“冰雪道路”，也就是说这样的路面状况很差，极易滑。因此若跟在前车后面，很有可能因前车后溜滑坡而发生事故。因此只能等前车通过后再爬坡。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396576, '正确', '错误', '', '', '1', '', '在雨天湿滑路面行车要尽量避免紧急制动。', '由于湿滑路面很易滑，若是采取紧急制动，容易发生侧滑或横滑，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396577, '视线良好', '对面无来车', '路面无障碍物', '道路宽直', '17', '', '驾驶汽车行车中怎样选择超车路段？', '这个只能牢记', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396578, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/743.jpg', '突然出现这种情况，驾驶人要及时减速或停车避让。', '遇到这种情况时，要让小朋友先通过，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396579, '正确', '错误', '', '', '2', '', '烧伤伤员口渴时，只能喝白开水。', '烧伤有创面，会损失体液，淡盐水可补充体液。至于为啥淡盐水可补充体液，因为人的体液本来也含盐，淡盐水可迅速补充。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396580, '没有开启转向灯', '没有鸣喇叭警示', '未观察左后方情况', '驾驶人没有错误', '3', 'http://images.juheapi.com/jztk/subject4/745.jpg', '这辆小型汽车驾驶人错在哪里？', '下车的时候一定要通过倒车镜观察后方的情况。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396581, '正确', '错误', '', '', '1', '', '高速行驶的机动车，在转向失控的情况下紧急制动，很容易造成翻车。', '注意“高速行驶”，也就是说是车速过快的情况下，若是转向失控时使用紧急制动，很容易造成翻车，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396582, '允许超车', '不准掉头', '允许停车', '可以倒车', '2', '', '驾驶机动车驶入高速公路匝道后，以下哪种说法是正确的？', '“匝道”是指立交桥或高架路的进出道引道。高架：进口路和出口路是分开的，只能顺行，车辆错过了下匝道，就不能从上匝道下路，只能从下一个出口下路。立交桥：匝道的进出，也是按照设定的标志行驶。不管是高架的匝道还是立交桥的匝道，都不能掉头、倒车、超车或是停车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396583, '正确', '错误', '', '', '1', '', '下坡路制动失效后，在不得已的情况下，可用车身侧面擦撞山坡，迫使机动车减速停车。', '刹车突然失灵时，摩擦力特别能显神奇功效。可用车前保险杠、车厢等钢性部位与路边岩石、大树或土坡摩擦、碰撞，在不威胁乘车人安全的情况下强行停车。这样，虽对车会造成损害，但是命更重要，就别心疼车了。 因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396584, '正确', '错误', '', '', '2', '', '伤员骨折处出血时，先固定好肢体再进行止血和包扎。', '本题的关键在于伤员发生这种状况的时机、也就是分轻重缓急来处理主要的病情，如果是突发灾难现场救急，肉眼判断骨折处未伤及动静脉仅皮外划伤无须单独包扎止血、那么可以先行固定，送往医院后再行处置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396585, '紧急制动停车', '缓慢减速停车', '挂空挡滑行', '关闭点火开关', '2', '', '行车中发动机突然熄火怎样处置？', '当发现发动机突然熄火后，立刻开启右转向车灯，缓慢减速停车到路边，取出停车警示牌安放在车后50米外。打电话等待救援。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396586, '低速缓慢倒车', '主动停车避让', '连续鸣喇叭示意', '向右转向倒车', '2', 'http://images.juheapi.com/jztk/subject4/751.jpg', '机动车倒车时遇到这种情况怎样做以保证安全？', '车辆倒车时不得影响其他车辆的正常行驶，因此倒车时发现有状况时，要注意避让，必要时停车等待倒车时机。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396587, '正确', '错误', '', '', '2', '', '大风天气行车中，如果遇到狂风袭来，感觉机动车产生横向偏移时，要急转方向以恢复行驶方向。', '当车遇到横风时如果发生的横移，因为在横移的瞬间车轮是离开地面的，所以才会横移，最好的方法就是收油，等车速自己下来，这样就可以让车轮恢复抓地力，然后再进行打轮刹车等动作。这题判错的原因主要是当时车轮是离地的，也就是说车轮是没有抓地力的，如果这时打轮的话不管是前轮离地还是后轮离地，都容易发生甩尾，再加上当时的横风，可能还会加重甩尾的趋势。即本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396588, '驾驶时接听手持电话', '超速行驶', '客车超员', '疲劳驾驶', '10', '', '钱某驾驶大型卧铺客车，乘载45人（核载40人），保持40公里/小时以上的车速行至八宿县境内连续下坡急转弯路段处，翻下100米深的山崖，造成17人死亡、20人受伤。钱某的主要违法行为是什么？', '1、弯道限速30公里/每小时，故超速行驶。2、乘载45人(核载40人)，故客车超员。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396589, '紧急制动', '抢挂低速挡', '合理使用行车制动和驻车制动，避免紧急制动', '开启危险报警闪光灯', '16', '', '机动车高速行驶中出现转向失控时，驾驶人要如何处置？', '车辆在高速行驶状态下，使用紧急制动，是容易翻车的。因此发生转向失控时，应当要减速，并开启危险报警闪光灯，并注意制动的使用。因此本题选BCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396590, '最外侧行车道上', '内侧行车道上', '应急车道', '匝道口', '3', '', '发生紧急故障必须停车检查时，要在什么地方停车？', '高速路最右的道路就是紧急停车道，也就是应急车道。应急车道就是专门在车子发生故障或事故时停靠的。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396591, '正确', '错误', '', '', '1', '', '抢救昏迷失去知觉的伤员要在抢救前先检查呼吸。', '检查呼吸的目的是为了了解伤员的生命体征，而这必须是要先确认的。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396592, '路面平坦坚实', '可以随意停放', '无禁止停车标志', '不妨碍交通', '15', '', '驾驶汽车在道路上临时停车怎样选择停车路段和地点？', '亲，不能随便停放哦，小心刮伤您的爱车，也有可能被警察贴罚单。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396593, '减速或停车让行', '从行人前方绕行', '持续鸣喇叭提醒', '从行人后方绕行', '1', 'http://images.juheapi.com/jztk/subject4/758.jpg', '驾驶机动车突然遇到这种情况怎样做？', '遇到有行人横穿马路，还是跑动着的，必须要减速慢行，必要时停车，确保安全。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396594, '正确', '错误', '', '', '2', '', '驾驶机动车遇到校车在道路右侧停车上下学生，同向只有三条机动车道时，左侧车道后方机动车应当停车等待。', '校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越。校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396595, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/760.jpg', '驾驶机动车在居民小区遇到这种情形要紧跟其后行驶。', '有图可知前方是陪着骑车儿童的长辈，以防儿童突然改变方向或是从小车子上掉落，因此要保持一定的安全距离，减速慢行，必要时停车避让。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396596, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/761.jpg', '右侧标志提醒前方是野生动物保护区。', '注意牲畜-用以提醒车辆驾驶人注意慢行。设在经过放牧区、畜牧场等区域的公路上，经常有牲畜横穿、出入的地点前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396597, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/762.jpg', '该车道路面导向箭头指示前方路口可左转弯或掉头。', '导向箭头指示前方路口可左转弯或掉头，此题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396598, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/763.jpg', '在铁路道口遇到两个红灯交替闪烁时要停车等待。', '无人看守铁路道口：用以警告车辆驾驶人注意慢行或及时停车。设在无人看守铁路道口以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396599, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/764.jpg', '前方标志表示向右是单向行驶道路。', '单行路向右-表示一切车辆向左或向右单向行驶。此标志设在单行路的路口和入口处的适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396600, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/765.jpg', '右侧标志指示前方是高速公路的终点。', '这是起点标志，终点的是还有一个红色斜杠的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396601, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/766.jpg', '驾驶机动车在这个时候要减速慢行。', '由图可知，机动车即将驶出隧道，在出隧道时需要有一个明适应的过程，因此要减速慢行，确保安全。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396602, '正确', '错误', '', '', '2', '', '驾驶机动车遇到骑自行车人占道影响通行时，可连续鸣喇叭加速从其左侧绕行。', '遇到这种情况应当减速慢行，必要时避让。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396603, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/768.jpg', '遇到这种情况的骑车人可以借对向车道超越。', '黄色实线不能碾压，故不能借用对向车道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396604, '正确', '错误', '', '', '1', '', '驾驶机动车遇到成群青少年绕过路边停放的机动车时，要主动减速让行。', '遇到这种情况，要随时注意出现发生突发状况，因此要减速让行，以免发生意外。所以本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396605, '提前开启转向灯', '仔细观察后变更车道', '随意并线', '不得妨碍其他车道正常行驶的车', '3', '', '驾驶机动车变更车道时，属于交通陋习的是什么行为？', '随意并线会影响其他车辆的正常行驶，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396606, '正确', '错误', '', '', '1', '', '驾驶人边驾车，边打手持电话是违法行为。', '中华人民共和国道路交通安全法实施条例 第六十二条 驾驶机动车不得有下列行为： (三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为；', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396607, '靠弯道外侧行驶', '减速、鸣喇叭、靠右行', '借用对向车道行驶', '靠道路中心行驶', '2', 'http://images.juheapi.com/jztk/subject4/772.jpg', '驾驶机动车在这种山区弯道怎样转弯最安全？', '由图可知，前方是向左急转弯路段，因此为了避免前方有来车，应当减速鸣号靠右行，因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396608, '接近路口时减速慢行', '进入路口后再减速慢行', '可以不减速直接通过', '提前加速通过交叉路口', '1', 'http://images.juheapi.com/jztk/subject4/773.jpg', '驾驶机动车直行通过前方路口怎样行驶？', '前方是没有交通信号灯的交叉路口，应当提前减速慢行，确认安全后通过。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396609, '变正面碰撞为侧面碰撞', '向右急转转向盘躲避', '迅速采取紧急制动', '向左急转转向盘躲避', '3', '', '行车中与其他机动车发生正面碰撞已不可避免时怎样处置？', '题中的前提已经明确说到不可避免正面碰撞，所以单纯的转动方向盘不能达到躲避的效果。并且虽然通过转向由纵向转为侧向移动会有一定的减少碰撞力的效果，但是肯定是不会有C选项的效果好。 所谓紧急制动，是指汽车在行驶过程中遇到紧急情况时，驾驶者迅速，正确的使用制动器，在最短距离内将车停住。若是盲目躲避，车速过快的话，容易发生更大伤害。 因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396610, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/775.jpg', '右侧标志表示前方路段会车时停车让对方车先行。', '减速让行-表示车辆应减速让行，告示车辆驾驶员必须慢行或停车，观察干路行车情况，在确保干道车辆优先的前提下，认为安全时方可续行。此标志设在视线良好交叉道路的次要路口。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396611, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/776.jpg', '右侧标志提醒前方路段有塌方禁止通行。', '注意危险-用以促使车辆驾驶员谨慎慢行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396612, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/777.jpg', '右侧标志警告前方进入两侧变窄路段。', '窄桥-用以警告车辆驾驶人注意前方桥面宽度变窄，应谨慎驾驶。设在桥面净宽较两端路面宽度变窄，且桥的净宽小于6 m的桥梁以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396613, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/778.jpg', '前方标志表示除公交车以外的其他车辆不准进入该车道行驶。', '公交专用车道禁止其他车辆行驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396614, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/779.jpg', '右前方标志表示该路段在规定时间内只供步行。', '记住吧，亲。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396615, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/780.jpg', '前方标志指示前方所要经过的重要地名和距离。', '问的是前方的标志，不是地方的网格线。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396616, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/781.jpg', '右侧标志警告前方道路右侧变宽。', '右侧变窄-用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396617, '鸣喇叭提示行人', '加速，尽快通过', '保持正常行驶', '减速，准备停车', '4', 'http://images.juheapi.com/jztk/subject4/782.jpg', '驾驶机动车在居民小区看到这种情况怎样安全行驶？', '由图可知，前方由行人想要横穿，因此为了安全起见，要减速慢行，必要时停车避让，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396618, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/783.jpg', '右侧标志提醒前方200米有村庄。', '上边是村庄-用以提醒车辆驾驶人小心驾驶。设在紧靠村庄、集镇且视线不良的路段以前适当位置。 下边200米是距离。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396619, '正确', '错误', '', '', '1', '', '机动车不得在隧道中倒车。', '《中华人民共和国道路交通安全法实施条例》第五十条  机动车倒车时，应当察明车后情况，确认安全后倒车。不得在铁路道口、交叉路口、单行路、桥梁、急弯、陡坡或者隧道中倒车。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396620, '关闭前照灯', '使用远光灯', '关闭所有车灯', '使用近光灯', '4', '', '夜间驾驶机动车在照明条件良好的路段跟车行驶怎样使用灯光？', '据《中华人民共和国道路交通安全法实施条例》第四十八条  在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（五）夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396621, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/786.jpg', '路面网状线标示不准进入该区域内停车等待。', '路面黄色网状线表示严格禁止一切车辆长时或临时停车，防止交通阻塞。当黄色网状线前方有车辆停驶时，后车必须在黄色网状线外等候，直到确认黄色网状线前方有足够空间停驶本车时，方可驶过黄色网状线。 ', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396622, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/787.jpg', '右侧这个标志警告进入隧道减速慢行。', '这个标志是隧道开灯的意思。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396623, '采取紧急制动', '减速注意观察', '断续鸣喇叭', '做好绕行准备', '2', 'http://images.juheapi.com/jztk/subject4/788.jpg', '驾驶机动车看到路边有这种标志时怎样行驶？', '由图可知，前方道路是学校，要注意儿童，因此通过这样的道路时，要减速慢行，同时注意周围情况，必要时停车让行。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396624, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/789.jpg', '这辆红色轿车可以在该车道行驶。', '看红色轿车行驶的车道的信号灯是红色，所以是不能在该道行驶的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396625, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/790.jpg', '右侧这个标志提醒前方是单向行驶隧道。', '隧道：用以提醒车辆驾驶人注意慢行。设在双向行驶并且照明不好的隧道口前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396626, '减速保持安全距离', '连续鸣喇叭提示', '保持距离加速通过', '占用对向车道超越', '1', 'http://images.juheapi.com/jztk/subject4/791.jpg', '驾驶机动车超车时遇到这样的情况怎样保证安全？', '前方既有停靠的车辆，也有禁止鸣喇叭标志，因此在这样的路段，为了防止停靠车辆突然起步，要保持安全距离，减速慢行通过。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396627, '经常观察后视镜', '变更车道不开启转向灯', '左臂长时间搭在车门窗上', '长时间抓变速杆', '16', '', '行车中不应该有以下哪些行为？', '看清题目，是不应该有的行为，所以答案是BCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396628, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/793.jpg', '前方标志表示向左是单向行驶道路。', '单行路向左-表示一切车辆向左或向右单向行驶。此标志设在单行路的路口和入口处的适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396629, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/794.jpg', '如下图，进入交叉口前，车辆不允许跨越白色实线变更车道。', '白色实线不能压，此题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396630, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/795.jpg', '红色圆圈内标志表示除非机动车以外的其他车辆不准进入该车道行驶。', '红色圈内是非机动车专用车道标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396631, '减速或停车让行', '紧靠路中心行驶', '保持正常车速', '占对向车道会车', '1', 'http://images.juheapi.com/jztk/subject4/796.jpg', '驾驶机动车遇到这种情况怎样行驶最安全？', '遇到这种情况应当减速慢行，必要时停车避让，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396632, '无障碍一方让对向先行', '速度慢的让速度快的先行', '有障碍的一方让对向先行', '速度快的让速度慢的先行', '3', '', '会车中道路一侧有障碍，双方机动车应如何做？', '有障碍的你要绕开障碍的话，肯定要占被人的道，所以你要后行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396633, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/798.jpg', '左侧标志表示前方路口不准掉头。', '禁止掉头-表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396634, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/799.jpg', '路口两侧导流线表示直行或右转弯不得压线或越线行驶。', '题中的所说的不得压线是说的不能压实线，虚线当然是可以压的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396635, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/800.jpg', '驾驶机动车在居民小区遇到这种情形要连续鸣喇叭。', '图中标志是禁止鸣喇叭标志，也就是在此情形下不得鸣喇叭。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396636, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/801.jpg', '在这种能见度的情况下起步要开启近光灯。', '冰雪等天气情况下起步要开启近光灯，因此本题选A。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396637, '减速靠右侧行驶', '骑轧路中心行驶', '靠弯道外侧行驶', '借对向车道行驶', '1', 'http://images.juheapi.com/jztk/subject4/802.jpg', '驾驶机动车在这种条件的弯道处怎样转弯最安全？', '在对向有来车时出现弯道时，应当减速靠右侧行驶，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396638, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/803.jpg', '右侧标志表示干路车辆优先通行。', '干道的意思应该是主干道的意思，主干道的箭头比较粗当然先行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396639, '观察机动车周围情况', '不用观察周围情况', '开启车门直接上车', '注意观察天气情况', '1', '', '驾驶人进入驾驶室前，首先要做什么？', '注意“进入驾驶室前”，也就是上车前，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396640, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/805.jpg', '前方标志表示除大客车以外的其他车辆不准进入右侧车道行驶。', '应该是除公交车外。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396641, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/806.jpg', '路面标记指示前方路口禁止车辆掉头。', '看路面上的标志一个X一个掉头的标志，合在一起就是禁止掉头的意思。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396642, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/807.jpg', '右侧标志告知前方注意残疾人。', '黄色三角形的都是--注意**的意思。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396643, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/808.jpg', '前方标志预告距离下一左侧出口1公里。', '应该是距离右侧下一个出口，还有一公里。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396644, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/809.jpg', '图中标志提醒障碍物在路中，车辆从左侧绕行。', '右侧绕行-用以告示前方道路有障碍物，车辆应按标志指示减速慢行，右侧绕行通过。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396645, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/810.jpg', '左侧标志表示前方路段允许进入。', '禁止通行-表示禁止一切车辆和行人通行。此标志设在禁止通行的道路入口处。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396646, '左侧机动车', '右侧机动车', '后方机动车', '前方机动车', '1', 'http://images.juheapi.com/jztk/subject4/811.jpg', '在这种情况下驾驶人需要注意什么？', '由图可知，两车之间有空当，但此时车子若想挤入此空档很容易会和后方的车子相撞，因此为了安全起见，要避让，即要注意左侧蓝色货车。所以本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396647, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/812.jpg', '驾驶机动车在这样的道路上只能从左侧超越。', '超车要从左侧超越，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396648, '换空挡利用惯性通过', '进入道口后换低速挡', '进入道口前减速减挡', '道口内停车左右观察', '3', 'http://images.juheapi.com/jztk/subject4/813.jpg', '驾驶机动车怎样安全通过铁路道口？', '由图可知，前方是有管理人员看守且有交通信号灯的铁路道口，为避免有状况发生，要在进入道口前减速减档，以免在通过时出现熄火或停车的情况，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396649, '使用远光灯', '使用近光灯', '使用危险报警闪光灯', '交替使用远近光灯示意', '4', '', '机动车在夜间通过没有交通信号灯控制的交叉路口时，要怎样使用灯光？', '《中华人民共和国道路交通安全法实施条例》第五十九条  机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396650, '货车超速行驶', '非法改装机动车', '客车超员', '客车逆向行驶', '16', '', '杨某驾驶改装小型客车（核载9人，实载64人，其中62人为幼儿园学生），行至榆林子镇马槽沟村处，占用对向车道逆行时与一辆重型自卸货车正面碰撞，造成22人死亡、44人受伤。该起事故中的主要违法行为是什么？', '1、改装小型客车，所以非法改装机动车2、核载9人，实载64人，所以客车超员3、占用对向车道逆行时，所以客车逆向行驶', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396651, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/816.jpg', '交通警察发出这种手势信号可以向左转弯。', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396652, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/817.jpg', '右侧标志表示前方100米是停车接受检查的地点。', '停车检查-表示机动车必须停车接受检查。此标志设在关卡将近处，以便要求车辆接受检查或缴费等手续。标志中可加注说明检查事项。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396653, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/818.jpg', '右侧这个标志提醒前方路面低洼。', '路面低洼-用以提醒车辆驾驶人减速慢行。设在路面突然低洼以前适当位置。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396654, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/819.jpg', '驾驶机动车遇到这样的情况要停车让行。', '遇到行人过马路一定要让行啊，真要发生点什么事故，亲，你就“摊上大事了”', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396655, '鸣喇叭或开前照灯', '减速靠右侧行驶', '尽量靠路中心行驶', '沿道路左侧行驶', '2', 'http://images.juheapi.com/jztk/subject4/820.jpg', '驾驶机动车遇到这种情况怎样做行驶最安全？', '由图可知前方是弯道，且无法获知前方道路情况，因此应当减速靠右侧行驶，以免发生碰撞。所以本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396656, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/821.jpg', '该车道路面导向箭头指示前方道路仅可左转。', '看路面的导向箭头此车道为左转车道，是不能直行的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396658, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/823.jpg', '前方标志预告前方距高速公路终点还有2公里。', '有条�线的高速公路预告牌就是指这条标号某某号的高速公路到了终点，没有斜�线就是指高速公路的起点。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396659, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/824.jpg', '右前方标志指示前方路口左转弯绕行的路线。', '看清楚题，问的右边的标志。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396660, '尽快离开机动车', '尽量站到防护栏以外', '开启危险报警闪光灯和雾灯', '沿行车道到车后设置警告标志', '4', '', '大雾天在高速公路遇事故不能继续行驶时，危险的做法是什么？', '注意本题的大环境是“大雾天”，也就是视野不良，能见度较低。因此在车子发生故障不能继续行驶的情况下，应当开启危险报警闪光灯和雾灯。 若是要放置警示标志，也应当在车后50米至100米处设置警告标志。此外要尽快离开机动车，尽量站到防护栏以外，以策安全。 而本题考的是危险的做法，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396661, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/826.jpg', '驾驶机动车在这个路口允许掉头。', '由图可知此为直行车道，因此在这个路口不能掉头。所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396662, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/827.jpg', '该车道路面导向箭头指示前方路口仅能掉头。', '导向箭头指示前方路口可以直行或者掉头，所以此题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396663, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/828.jpg', '驾驶机动车在这种情况下要尽快加速通过。', '由图可知，应该是刚下过雨的路面，湿滑路面行驶要注意减速慢行，而且在超越非机动车时要注意不要溅水，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396664, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/829.jpg', '右侧这个标志警告前方是有人看守铁路道口。', '火车头是无人看守', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396665, '反应距离延长', '视野变窄', '加重事故后果', '制动距离延长', '17', '', '驾驶汽车超速行驶有哪些危害？', '常识题', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396666, '左转向灯', '右转向灯', '危险报警闪光灯', '前照灯', '1', '', '机动车从匝道驶入高速公路，应当开启什么灯？', '从匝道驶入高速公路，是由右向左进入，因此要开启左转向灯，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396667, '用力踏制动踏板', '间歇踏制动踏板', '缓慢踏制动踏版', '逐渐踏下制动踏板', '1', '', '驾驶装有ABS系统的汽车怎样采取紧急制动？', 'ABS刹车系统，即“防抱死制动装置”。在你踩下刹车踏板以后，制动就开始了，在车轮的转动即将停止之前，ABS会自动将刹车释放，然后，ABS自动放开以后，马上又会处于刹车的状态，在车轮再一次停止转动前，ABS又一次释放刹车。这样，周而复始（这个频率大概是25次/每秒），你的车轮始终不会被抱死。所以用力踏。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396668, '减速靠路右侧行驶', '不能占用方车道', '注意对面来车', '鸣喇叭示意', '17', '', '驾驶汽车在道路急转弯处怎样行驶？', '牢记吧', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396669, '轻踏制动踏板', '拉紧驻车制动器操纵杆', '迅速抢挡减速', '果断地连续踩踏、放松制动踏板', '4', '', '转向失控后，若机动车偏离直线行驶方向，应怎样使机动车尽快减速停车？', '果断的连续踩踏、放松制动踏板，也就是连续的点刹车。如果直接踩死的话 轮胎暴死，还不是直线行驶，车就会旋转，高速的话就会翻车，很危险的，所以要点刹车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396670, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/835.jpg', '这个标志表示前方立体交叉处可以直行和右转弯。', '立体交叉直行和左转弯行驶 表示一切车辆在立体交叉处可以直行和按图示路线左转弯行驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396672, '紧握转向盘', '两腿向前蹬', '从一侧跳车', '身体向后紧靠座椅', '3', '', '机动车发生撞击的位置不在驾驶人一侧或撞击力量较小时，驾驶人不正确的做法是什么？', '撞击位置不在驾驶人一侧或撞击力量较小时，说明没有跳车的必要。此时在车内比跳车安全得多。除此之外，大家要记住不是万不得已都不要选择跳车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396673, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/838.jpg', '行车中遇到这种行人需要保持较大的安全距离。', '大家都知道遇到行人，应控制好车速行驶，确保安全。而本题中的对象为挑担或扛农具的，也就是说，要考虑农具或担子的长度，因此在经过这类人时，除了要控制车速之外，还要留出安全间距，以策安全。 因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396674, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/839.jpg', '右侧车道路面标线表示可以临时借公交专用车道行驶。', '公交专用车道是任何时候都不能占用的，属于专用车道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396676, '开启雾灯、近光灯、示廓灯、前后位灯', '车速不超过60km/h', '与同车道前车保持100米以上的距离', '从最近的出口尽快驶离高速公路', '13', '', '机动车在高速公路上行驶，遇有雾、雨、雪且能见度在100米至200米之间时，应该怎么做？', '根据《中华人民共和国道路交通安全法实施条例》第八十一条  机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：（一）能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；因此本题选ABC。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396677, '正确', '错误', '', '', '2', '', '机动车在高速公路意外撞击护栏时，有效的保护措施是向相反方向大幅度转向。', '这样做容易造成车子侧翻或倾翻，因此正确的做法是握紧方向盘，适当修正。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396678, '正确', '错误', '', '', '2', '', '行车中当驾驶人意识到机动车爆胎时，应在控制住方向的情况下采取紧急制动，迫使机动车迅速停住。', '题目只要出现“迅速”，“快”等字眼就是错的，就只有一道题就是事故无可避免的时候要迅速刹车，其他都是错的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396679, '迅速转动转向盘调整', '控制行驶方向并慢慢减速', '迅速向相反方向转动转向盘', '迅速采取制动措施', '2', '', '后轮胎爆裂时，驾驶人要如何处置？', '若是在行驶中突然打方向，车子会飘或者翻侧的。用紧急制动有可能会发生侧滑或侧翻，因此本题要选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396680, '正确', '错误', '', '', '2', '', '车速较高，前方发生紧急情况时，要先转方向避让，再采取制动减速，以减小碰撞损坏程度。', '车速较高的情况下，若直接转向，很有可能发生侧翻，此题就错在“要先转方向避让”。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396681, '不允许停放车辆', '允许临时停车', '允许停车上下客', '允许停车装卸货', '1', 'http://images.juheapi.com/jztk/subject4/846.jpg', '右侧标志是何含义？', '表示在限定的范围内，禁止一切车辆临时或长时停放。此标志设在禁止车辆停放的地方。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396682, '停车查明原因', '控制转向缓慢行驶', '降低车速行驶', '保持机动车直线行驶', '1', '', '驾驶装有动力转向的机动车发现转向困难怎样处置？', '出现转向困难的情况，就说明有问题产生了，因此要停车检查是什么原因造成转向困难，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396683, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/848.jpg', '驾驶机动车遇到这种情况时，要快速向左绕过。', '遇到这种情况时应减速慢行，必要时停车避让。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396684, '迅速制动减速', '慢慢制动减速', '迅速向左转向', '采取紧急制动', '2', '', '行车中发现右侧轮胎漏气时怎样处置？', '当车辆漏气时，引起轮胎不正常工作，因此此时就应该减速并找安全的地方停车。迅速向左转向或是紧急制动都有可能造成爆胎等状况的发生，因此不可行。选项A和B的差别在于是快还是要慢，若是动作过快也有可能造成爆胎，因此本题要选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396685, '紧急制动，靠边停车', '牢牢地握住转向盘，保持直行', '立即松开加速踏板', '轻踩制动踏板', '16', '', '机动车在高速行驶中，突然爆胎要采取的安全措施是什么？', '如果采取紧急制动的方法，很容易出现侧翻事故的，因此正确的做法应该是握紧方向，同时控制车速，慢慢停车。在此过程中，可以采取的措施则可以是BCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396686, '以正常速度行驶', '临近鸣喇叭示意', '加速从左侧绕行', '提前减速鸣喇叭', '4', '', '驾驶机动车在雨天遇到撑雨伞和穿雨衣的行人在路边行走怎样礼让？', '首先下雨天本来视野就不好，其次行人这样的着装很难在第一时间意识到周围的交通状况变化。因此驾驶人在遇到此类行人时，应当先鸣喇叭示意，再以较低的车速通过。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396687, '迅速制动减速', '紧握转向盘，尽快平稳停车', '迅速转动转向盘调整方向', '低速行驶，寻找换轮胎地点', '2', '', '行车中轮胎突然爆裂时的应急措施是什么？', '绝对不可以制动减速，此时车子重心偏向爆胎一边，制动会加剧重心偏移，造成翻车。爆胎时要注意把握好方向，做适度修正。一般高速爆胎时，车子会急速偏向爆胎方向，此时若不能冷静处理，给予合适的方向修正力度，车子将无法保证行驶方向，在公路上来回摇动，这是非常危险的。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396688, '减速感', '加速感', '压力感', '方向偏移', '4', '', '机动车驶出高速公路隧道口时，如遇横风会明显出现什么情况？', '一直都说遇到横风时握紧方向盘控制好方向，这样做的原因就是因为横风会让方向发生偏移。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396689, '正确', '错误', '', '', '1', '', '危险化学品具有爆炸、易燃、毒害、腐蚀、放射性等特性。', '危险化学品是指具有毒害、腐蚀、爆炸、燃烧、助燃等性质，对人体、设施、环境具有危害的剧毒化学品和其他化学品。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396690, '正确', '错误', '', '', '2', '', '机动车在高速公路上行车，如果因疏忽驶过出口，可沿路肩倒车退回出口处。', '因疏忽驶过出口，应该到下一个出口驶离高速公路，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396691, '正确', '错误', '', '', '1', '', '驾驶人发现轮胎漏气，将机动车驶离主车道时，不要采用紧急制动，以免造成翻车或后车采取制动不及时导致追尾事故。', '当车辆漏气时，引起轮胎不正常工作，为了避免意外发生，应该驶离主干道，在安全地方检查也不应该紧急制动，避免后面车辆反应不及时引起事故。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396692, '降低轮胎气压', '定期检查轮胎', '及时清理轮胎沟槽内的异物', '更换有裂纹或损伤的轮胎', '16', '', '机动车避免爆胎的正确做法是什么？', '对于选项BC相信大家都没有疑问，选项D的意思是把有裂纹或损伤的轮胎更换掉，所以D也是正确的。而选项A，错误的原因是因为若是降低轮胎气压过低，还是会造成爆胎。因此降低轮胎气压这一招不安全。因此本题要选BCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396693, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/858.jpg', '驾驶机动车遇到这种情况可迅速从前车左侧超越。', '从图上来看，此车道应该是加速车道，从前车的行驶路线来看，应该是在前方会汇入行车道内，因此在这种情况下不能超车。所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396694, '先避人后避物', '先避物后避车', '先避车后避人', '先避物后避人', '1', '', '遇紧急情况避险时，要沉着冷静，坚持什么样的处理原则？', '以人为本，所以首先要避人。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396695, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/860.jpg', '驾驶机动车到达隧道出口时要握稳转向盘，预防出口处的强横向风。', '由于隧道是一个除了入口和出口之外都是封闭的，因此在出隧道的时候，会出现强烈的反差。所以此时驾驶员就要握稳方向盘，一旦出现横风还能控制好车身，避免跑偏。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396696, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/861.jpg', '该车道路面导向箭头提示前方道路右侧有路口。', '导向箭头表示方向的意思，并不是右侧有路口的意思。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396697, '正确', '错误', '', '', '1', '', '行车中当机动车突然爆胎时，驾驶人切忌慌乱中急踏制动踏板，尽量采用抢挂低速档的方法，利用发动机制动使机动车减速。', '如果爆胎时，方向盘会偏向爆胎处，而且是打死了的任你怎么搬都搬不动。这个时候如果踩刹车，车子立马会发生侧翻，所以说这个时候应该抬起刹车，轰油门，抢次档，车就会往反方向跑，然后在亲点刹车，同时抢3档，然后2档，1档这样做你就会平安了~只是你如果超速行驶，上面说的效果就没有了~所以说控制好速度才是最重要的！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396698, '迅速转向调整', '尽快减速停车', '向无障碍一侧躲避', '向有障碍一侧躲避', '2', '', '行车中遇到转向失控，行驶方向偏离时怎样处置？', '除了选项B之外，都有可能在躲避或转向过程中发生事故，比如与其他车辆或者障碍物发生碰撞等。因此本题应选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396699, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/864.jpg', '行车中对出现这种行为的人不能礼让。', '虽然行人的这种行为是不可取的，但驾驶人还是要予以让行，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396700, '正确', '错误', '', '', '2', '', '行车中遇突然爆胎时，驾驶人要急踏制动踏板减速停车。', '这样做很危险，容易造成车子侧翻，不能急踏制动踏板减速停车，而是要轻踏制动踏板慢慢减速停车，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396701, '正确', '错误', '', '', '2', '', '驾驶机动车进入高速公路加速车道后再开启左转向灯。', '要先开启左转向灯并确保安全之后才能向左进入高速公路加速车道，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396702, '酒后驾驶', '客车超员', '疲劳驾驶', '超速行驶', '11', '', '唐某驾驶一辆大客车，乘载74人（核载30人），以每小时38公里的速度，行至一连续下陡坡转弯路段时，机动车翻入路侧溪水内，造成17人死亡、57人受伤。唐某的主要违法行为是什么？', '1：乘载74人(核载30人) 所以客车超员。2：弯道限速30公里/每小时，所以超速行驶。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396703, '正确', '错误', '', '', '2', '', '行驶在高速公路上遇大雾视线受阻时，要立即紧急制动停车。', '正确的做法应当是，打开双闪，减低车速，在道路中线行驶。这样就算有人在路边紧急停车你也不会碰上前方车辆，保证了你的行驶安全。若是紧急制动停车，很容易造成后车追尾等事故。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396704, '正确', '错误', '', '', '2', '', '机动车在高速公路匝道提速到每小时60公里以上时，可直接驶入行车道。', '错误的关键在于“直接”两个字，正确的做法是应该先打开左转向灯，再在不防碍已在高速公路内的机动车正常行驶的情况下驶入车道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396705, '靠路左侧，加速绕行', '停车�望，缓慢通过', '注意观察，尽快通过', '勤鸣喇叭，低速通行', '3', 'http://images.juheapi.com/jztk/subject4/870.jpg', '驾驶机动车在山区道路遇到这种情况怎样行驶？', '如图可知，前方是有落石的危险路段，因此要先注意观察，确认安全后尽快通过。因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396706, '正确', '错误', '', '', '2', '', '驾驶人频繁变更车道不属于驾驶陋习。', '变更车道不能太过频繁，否则会影响正常交通，属于陋习，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396707, '叶某驾驶与准驾车型不符的机动车', '王某驾驶摩托车非法载客', '叶某超速行驶', '王某不按信号灯指示行驶', '10', '', '叶某驾驶中型厢式货车，行至陂头镇上汶线3公里加600米弯道路段时，以40公里/小时的速度与王某驾驶的乘载19人正三轮载货摩托车发生正面相撞，造成10人死亡、9人受伤。双方驾驶人的主要违法行为是什么？', '注意看题目，不是道路而是弯道， 弯道的限速每小时/30以上，就算超速了。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396708, '降低轮胎气压', '定期检查轮胎', '及时清理轮胎沟槽里的异物', '更换有裂纹或有很深损伤的轮胎', '1', '', '避免爆胎的错误做法是什么？', '为什么要选A？主要是因为夏天气温较高，轮胎长时间的吸取地面的热量，会造成内胎气体膨胀，如内胎气压较高，在瞬间作用力的情况下，比如磕到马路牙子上，就会爆胎！有些学员会问为什么不是选D。这是因为大家可能误读选项D的意思，更换有裂纹或有很深损伤的轮胎，其实指的是把有裂纹或有很深损伤的轮胎换掉，而不是换上有裂纹或有很深损伤的轮胎。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396709, '逼其回原车道', '紧跟前车不让行', '礼让通行', '鸣喇叭开前照灯', '3', 'http://images.juheapi.com/jztk/subject4/874.jpg', '驾驶机动车在拥堵的路口遇到这种情况怎样处置？', '遇到有车辆插入，应当减速让行，避免发生追尾等情况，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396710, '氧化性物质', '易燃固体', '爆炸品', '自燃物品', '3', '', '火药、炸药和起爆药属于哪类危险化学品？', '氧化性物质是指在有氧气的环境下会被氧化，发生化学反应，很明显这三类物质在空气中不会自己变化，所以不选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396712, '正确', '错误', '', '', '2', '', '避免机动车爆胎的正确做法是降低轮胎气压。', '降低轮胎气压的过程中，若是轮胎气压过低也是会造成爆胎的，因此这个做法不可行。所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396713, '正确', '错误', '', '', '2', '', '机动车行驶中遇有自行车借道通行时，可急促鸣喇叭示意让道。', '发生这种情况，应当予以让行，避免发生碰撞，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396714, '正确', '错误', '', '', '1', '', '驾驶机动车时，长时间左臂搭在车门窗上，或者长时间右手抓住变速器操纵杆球头，是一种驾驶陋习。', '这是一种陋习，但还算不上是违法。因为这样久了，会导致血脉不通，手臂麻木，容易出事！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396716, '正确', '错误', '', '', '1', '', '在高速公路上驾驶机动车不要频繁地变更车道。', '在高速公路上可以变更车道，但是不能太过频繁，所以本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396717, '空挡滑行', '挂低速挡', '踏下离合器踏板滑行', '使用驻车制动器', '2', '', '下长坡时，控制车速的正确方法是什么？', '下坡时不能空挡滑行，也不能塌下离合器踏板滑行，而驻车制动是在停车后用的，因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396718, '正确', '错误', '', '', '1', '', '驾驶机动车从加速车道进入行车道不能影响其他机动车正常行驶。', '不影响其他机动车正常行驶是进入其他车道的大前提，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396719, '正确', '错误', '', '', '1', '', '机动车在高速行驶时，前面扬起的飞石或是遗撒物将挡风玻璃击裂，造成视线模糊不清的状况下，驾驶人要逐渐降低车速、开启危险报警闪光灯并将机动车移至不妨碍交通的地点。', '在视线模糊不清的状况下若还是高速行驶，很容易因为无法及时发现周围情况而发生事故，因此在这样的情况下就应该逐渐降低车速、开启危险报警闪光灯并将机动车移至不妨碍交通的地点。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396720, '正确', '错误', '', '', '1', '', '驾驶机动车驶离高速公路要经过减速车道减速后进入匝道。', '高速公路出口的匝道一般限速每小时60公里以下，弯道限速每小时40公里以下。而在高速公路上行驶时的车速远高于这两个数字，因此应当先减速再进入匝道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396721, '保持镇静，缓抬加速踏板', '紧握转向盘，控制机动车直线行驶', '采取紧急制动，在最短的时间内停车', '待车速降低后，再轻踏制动踏板', '3', '', '行车中轮胎突然爆裂时的不正确做法是什么？', '注意本题所考的是不正确的做法，爆胎时不能采取紧急制动，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396722, '下坡车让上坡车', '坡顶交会时距离坡顶远的一方让行', '上坡车让下坡车', '下坡车已行至中途而上坡车未上坡时，让上坡车', '1', '', '机动车在狭窄的坡路会车时，正确的会车方法是什么？', '《中华人民共和国道路交通安全法实施条例》《中华人民共和国道路交通安全法实施条例》第四十八条  在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（三）在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行；因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396723, '超车', '停车', '掉头', '倒车', '17', '', '机动车通过隧道时，禁止以下哪些行为？', '根据相关规定，在隧道内不能倒车、掉头、停车或是超车。因此本题选ABCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396724, '正确', '错误', '', '', '1', '', '伤员大腿、小腿和脊椎骨折时，一般不要随便移动伤者。', '应该是原地固定等待专业救护人员，脊柱骨折被移动很有可能将导致高位截瘫。所以本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396725, '缩短发动机使用寿命', '驾驶人容易疲劳', '容易造成机动车倾翻', '制动器制动效果下降', '4', '', '下长坡连续使用行车制动会造成什么不良后果？', '持续用刹车，会造成刹车片发热，从而制动力减小，严重的会丧失制动，所以长下坡要用低档，用发动机制动。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396726, '靠右侧正常通过', '鸣喇叭，加速通过隧道', '停车礼让对面车先通过', '开前照灯告知对面车让行', '3', 'http://images.juheapi.com/jztk/subject4/891.jpg', '驾驶机动车遇到这种情况怎样安全通过？', '由图可知，前方应会车让行，因此此时应当让对方先过。所以本题选C。答题技巧：谁粗谁先行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396727, '加速行驶，尽快通过', '车速控制在15公里/小时以内', '按照标志限定速度行驶', '尽量靠桥中心行驶', '3', 'http://images.juheapi.com/jztk/subject4/892.jpg', '驾驶机动车怎样经过公路跨线桥？', '如图所示，前方路段的行驶车速限制在40公里/小时，因此要按照此限速标志行驶，且靠右行驶。所以本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396728, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/893.jpg', '在这种情况下要避让左侧从公交车后横穿的行人。', '常识题', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396729, '正确', '错误', '', '', '2', '', '驾驶机动车从加速车道汇入行车道有困难时可停车让行。', '注意机动车所处的车道是加速车道，也就是说并不是可以停车的地方，因此不能停车让行。正确的做法应该是在汇入行车道有困难时减速让行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396731, '正确', '错误', '', '', '1', '', '救助失血过多出现休克的伤员要采取保暖措施。', '失血过多 + 休克的伤员 注意是休克，休克的意思是血压下降，口渴，体温下降，意识模糊，皮肤发白，尿量减少的一种状态。所以要采取保暖措施', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396732, '正确', '错误', '', '', '2', '', '高速行驶的机动车，在转向失控的情况下紧急制动，不会造成翻车。', '注意“高速行驶”，也就是说是车速过快的情况下，若是转向失控时使用紧急制动，很容易造成翻车，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396733, '路边沙土', '棉衣', '工作服', '水', '4', '', '机动车燃油着火时，不能用于灭火的是什么？', '水的密度比油大，油和水放一起油浮在水的上面。不但不能立即扑灭燃油起火，反而将燃油冲散扩大着火面积的着火点。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396734, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/899.jpg', '驾驶机动车遇到这种情况要靠右侧停车等待。', '如图所示，前方是隧道，且对向有来车出隧道，此外信号灯为红色，因此此时应当靠右侧停车等候，所以本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396735, '驾驶逾期未检验的机动车', '货运机动车载客', '超速行驶', '疲劳驾驶', '2', '', '周某驾驶一辆轻型厢式货车（搭载22人）行驶至丙察公路79公里加150米处时，坠入道路一侧山崖，造成12人死亡、10人受伤。周某的主要违法行为是什么？', '货车上装人，那是严重违法啊！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396736, '可在匝道、加速车道、减速车道上超车', '不准倒车、逆行、穿越中央分隔带掉头', '非紧急情况时可在应急车道行驶', '可以试车或学习驾驶', '2', '', '机动车上高速公路，以下哪种说法是正确的？', '《中华人民共和国道路交通安全法实施条例》第八十二条  机动车在高速公路上行驶，不得有下列行为：　　（一）倒车、逆行、穿越中央分隔带掉头或者在车道内停车；　　（二）在匝道、加速车道或者减速车道上超车；　　（三）骑、轧车行道分界线或者在路肩上行驶；　　（四）非紧急情况时在应急车道行驶或者停车；　　（五）试车或者学习驾驶机动车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396737, '选择安全区域停车', '开启危险报警闪光灯', '开启示廓灯和后位灯', '按规定设置警告标志', '17', '', '机动车在夜间发生故障时，驾驶人要做什么以确保安全？', '《中华人民共和国道路交通安全法实施条例》第六十条  机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。因此本题选ABCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396738, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/903.jpg', '驾驶机动车进入隧道口前按照隧道口标志上规定的速度调整车速。', '在隧道入口前很明确地竖立了两个道路标志，一个是规定了车速的限速标志，一个是提醒前方是隧道的标志。因此综合起来看，本题就是对的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396739, '正确', '错误', '', '', '1', '', '女驾驶人穿高跟鞋驾驶机动车，不利于安全行车。', '高跟鞋踩刹车有安全隐患，因此这题是对的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396741, '以最高设计车速行驶', '降低速度，谨慎驾驶', '保持现有速度行驶', '以超过规定的最高车速行驶', '2', '', '机动车在夜间行驶如何保证安全？', '由于夜间行驶视野不良，因此要降低车速行驶，同时要注意观察周围情况，因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396742, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/907.jpg', '驾驶机动车在这种情况下要跟前车进入路口等待。', '由图可知，前方出现堵塞，遇到这种情况，虽然前方是绿灯，但仍应原地等待，避免造成横向车道的拥堵。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396743, '左转向灯', '右转向灯', '危险报警闪光灯', '前照灯', '2', '', '机动车驶离高速公路时，应当开启什么灯？', '驶离高速公路，就是靠右侧驶离，因此要开启的应该是右转向灯。此情况下与危险报警闪光灯或是前照灯无关。因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396744, '30 公里/小时以上', '40 公里/小时以上', '50 公里/小时以上', '60 公里/小时以上', '4', '', '驾驶机动车进入高速公路加速车道后，尽快将车速提高到多少？', '有的学员会觉得题目有问题，认为尽快提速不对。但大家要看清楚前提：进入高速公路加速车道后，也就是说已经在高速公路上行驶了一定距离了。因此根据高速公路行驶车速不得低于60公里/时的规定，因此要尽快提速到60公里/时。所以选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396745, '在车后150米处设置故障警告标志', '在车后100米处设置故障警告标志', '夜间要开启示廓灯和后位灯', '要开启危险报警闪光灯', '15', '', '机动车因故障必须在高速公路上停车时，要采取的正确做法是什么', '在高速公路上因故障停车，首先要开启危险报警闪光灯，若是夜间的话，还要开启示廓灯和后位灯让其他驾驶员知道车子的轮廓及位置情况。此外，还要在车后150米处设置警示标志。因此本题选ACD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396746, '正确', '错误', '', '', '1', '', '下坡路制动失效后，驾驶人应立即寻找并冲入紧急避险车道；停车后，拉紧驻车制动器，以防溜动发生二次险情。', '不少学员会觉得这一题错误，认为“冲入”不对，我认为本题的考点在于这整个的操作过程，即制动失灵后要找到可以找到停车的地方，那么最快的方法就是找到紧急避险车道，此处用“冲入”应该是想强调快速。 因此在做本题时，提醒大家不要受“冲入”等词汇的影响。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396747, '正确', '错误', '', '', '2', '', '在高速公路上，遇尾随较近行驶的机动车时，可以选择时机迅速从中间插入。', '这种情况下若是从中间插入，很有可能与前后车辆发生擦碰或是追尾，因此不可行。所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396748, '开启危险报警闪光灯', '开启前后雾灯', '开启近光灯', '在车后设置警告标志', '1', '', '驾驶机动车在雨天临时停车注意什么？', '下雨天开车视野不清，因此若要临时停车，应当开启危险报警闪光灯，提醒其他车辆注意。另外三个选项，没有警示作用。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396749, '连续踩踏制动踏板', '抢挂低速档减速后，使用驻车制动', '迅速踏下离合器踏板', '迅速拉紧驻车制动器操纵杆', '2', '', '高速行车中行车制动突然失灵时，驾驶人要如何制动？', '没有刹车首先做的是减档，这是一个需要练习才可以完成的动作，第一，减到2档或者是3档，第二，慢慢拉起手刹，不可拉死，后轮拉死会甩尾，事故更大。第三，抓好方向，抓住一切可以减速的机会，减档成功后不可踩离合器。 因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396750, '突然横穿的儿童', '急速通过的自行车', '缓慢通过的行人', '准备横过的行人', '17', '', '驾驶汽车通过人行横道时要注意下列哪些情况？', '常识题', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396751, '各行其道加速交会', '紧靠路中心行驶', '保持正常车速行驶', '减速行驶', '4', 'http://images.juheapi.com/jztk/subject4/916.jpg', '驾驶机动车在山区道路遇到这种情况如何处理？', '由图可知，前方是向右急转弯路段，且前方有来车，因此应当减速靠右行驶，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396752, '气压不稳', '气压更低', '行驶阻力增大', '爆胎', '4', '', '轮胎气压过低时，高速行驶轮胎会出现波浪变形温度升高而导致什么情况发生？', '爆胎，爆胎并不是只有压强过大才会，轮胎变形了也会的。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396753, '抢挂高速挡', '抢挂低速挡', '抢挂空挡', '紧急制动', '2', '', '前轮爆胎时，驾驶人控制住行驶方向后，要采取什么措施减速停车？', '首先排除紧急制动，其次挂空挡是不可以的，再来是选项A，既然是要减速停车，自然不可能是挂高速档，因此本题的答案就只有B了。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396754, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/919.jpg', '驾驶机动车在这种隧道内要尽量靠左侧行驶。', '隧道中行驶，前方有来车，应当靠右侧行驶，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396755, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/920.jpg', '在前方交叉路口直行时，要提前在虚线区按导向箭头指示向右变更车道。', '这个没问题，要提前变更车道，因为前边是白色实线，不能碾压，白色虚线才可变更车道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396756, '降低车速行驶', '加大横向间距', '应当加速行驶', '必要时停车避让', '14', '', '驾驶汽车遇雨、雪、雾等视线不清或路面较滑时怎样安全会车？', '路滑怎能加速行驶。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396757, '减速靠右行驶', '抢在绿车前绕过障碍', '开启左转向灯', '借对向车道绕过障碍', '1', 'http://images.juheapi.com/jztk/subject4/922.jpg', '如图所示，行驶过程中遇前方有障碍物的情况怎么办？', '安全第一，减速等右车通过了在过。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396758, '正确', '错误', '', '', '1', '', '驾驶汽车通过连续弯道时，尽量靠弯道右侧行驶。', '一定要靠右行驶啊。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396759, '保护颈部不受伤害', '减轻驾乘人员受伤程度', '减轻驾驶人疲劳', '保持正确驾驶姿势', '15', '', '车辆发生碰撞时，关于安全带作用的说法错误的是什么？', '安全带最重要的作用就是保护驾驶人的生命安全。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396760, '加速从左侧超越', '连续鸣喇叭告知', '紧跟在自行车后', '减速避让自行车', '4', 'http://images.juheapi.com/jztk/subject4/925.jpg', '驾驶机动车遇到这种情况怎样行驶？', '遇到这种情况，要考虑到公交车可能会突然起步，那么骑自行车的人就会向外侧偏转方向继续行驶，因此驾驶员要减速慢行，必要时停车避让，不能贸然有动作，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396761, '正确', '错误', '', '', '2', '', '在泥泞路段行车要牢牢握住转向盘加速通过。', '泥泞路面很易滑，若是加速行驶，会打滑，因此应当减速通过，所以本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396762, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/927.jpg', '左侧标志指示高速公路两个行驶方向的目的地。', '指示高速公路两个行驶方向的目的地。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396763, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/928.jpg', '路中两条双黄虚线并列组成的双黄虚线指示潮汐车道的位置。', '所谓“潮汐车道”，是指根据早晚交通流量不同情况，对有条件的道路，通过车道灯的指示方向变化，控制主干道车道行驶方向，来调整车道数。 例如，左转车道很拥堵，但直行车道却车少畅通；交警通过遥控装置，在很短的时间，直行车道就变为左转车道，左转车龙就“消化”了，车道可随车流量随时变化。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396764, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/929.jpg', '右侧标志警告前方150米是无人看守铁路道口。', '火车头是无人看守 铁道是有人看守  横着一杠50 两杠100 三杠150 ', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396765, '提前减速行驶', '观察交汇处车辆', '提前加速通过', '谨慎驾驶通过', '14', 'http://images.juheapi.com/jztk/subject4/930.jpg', '这种情况下怎样安全驾驶？', '看到加速，迅速都要谨慎。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396766, '撞击风窗玻璃', '减少人员伤亡', '被甩出车外', '造成胸部损伤', '15', '', '驾驶汽车不系安全带在遇紧急制动或发生碰撞时可能会发生哪些危险？', '不系安全带非常危险会增加伤亡。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396767, '正确', '错误', '', '', '1', '', '机动车转向突然失控后，若前方道路条件能够保持直线行驶，不要紧急制动。', '注意“能够保持直线行驶”，就说明暂时不需要转向，那么应该继续按照原来的方式行驶，若是贸然使用紧急制动，很有可能发生事故。 因此本题正确。 ', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396768, '采取制动措施减速', '向左侧转向避让', '迅速转动转向盘躲避', '向右侧转向避让', '1', '', '在高速公路上遇到紧急情况避险时需注意什么？', '在高速公路上遇到紧急情况避险时应先减速再调整方向。贸然转动方向盘避让，车速过快的话很容易造成翻车。 因此根据此方法，选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396769, '正确', '错误', '', '', '2', '', '驾驶人在确认后方无来车的情况下，可以不开转向灯变更车道。', '变更车道时要提前开启转向灯，养成良好的开车习惯，防患于未然，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396770, '正确', '错误', '', '', '2', '', '遇高速公路限速标志标明的车速与车道行驶车速的规定不一致的，应按照车道行驶规定的车速行驶。', '《中华人民共和国道路交通安全法实施条例》第七十八条中有提到，道路限速标志标明的车速与上述车道行驶车速的规定不一致的，按照道路限速标志标明的车速行驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396771, '提前开启左转向灯', '仔细观察主干道内情况', '确认安全后汇入车流', '加速直接汇入车流', '13', '', '驾驶汽车从支线道路怎样安全汇入主干道车流？', '看到加速，迅速的字样都要谨慎。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396772, '正确', '错误', '', '', '1', '', '在道路上停车时要尽量避开坡道、积水、结冰或松软路面。', '常识题', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396773, '正确', '错误', '', '', '2', '', '机动车在高速公路上发生故障或者交通事故，无法正常行驶的，可由同行机动车拖曳、牵引。', '《中华人民共和国道路交通安全法》第四章第一节 第六十八条 机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当由救援车、清障车拖曳、牵引。也就是说，要由救援车、清障车拖曳、牵引，而不是同行机动车拖曳、牵引。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396774, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/939.jpg', '驾驶机动车遇到这种情况的行人可连续鸣喇叭催其让道。', '连续鸣喇叭不但会让老人受惊吓，同时也不礼貌。因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396775, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/940.jpg', '右侧标志提醒前方是过水路面。', '过水路面指的是通过平时无水或流水很少的宽浅河流而修筑的在洪水期间容许水流浸过的路面', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396776, '正确', '错误', '', '', '1', '', '驾驶有ABS 系统的机动车在紧急制动的同时转向可能会发生侧滑。', 'ABS使汽车在紧急刹车时车轮不会抱死，这样就能使汽车在紧急制动时仍能保持较好的方向稳定性。由于四轮防抱死制动系统保留着控制转向的能力，因此，在制动过程中有可能绕过障碍物，避免可能发生的事故。然而，驾驶有ABS 系统的机动车在紧急制动的同时转向会发生侧滑。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396777, '正确', '错误', '', '', '2', '', '行车中变更车道不需要提前开启转向灯。', '变更车道一定要提前打灯。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396778, '正确', '错误', '', '', '1', '', '烧伤伤员口渴时，可喝少量的淡盐水。', '烧伤有创面，会损失体液，淡盐水可补充体液。至于为啥淡盐水可补充体液，因为人的体液本来也含盐，淡盐水可迅速补充。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396779, '近心端动脉', '血管下方动脉', '远心端动脉', '血管中部', '1', '', '采用指压止血法为动脉出血伤员止血时，拇指压住伤口的什么位置？', '近心端就是靠近心脏方向的一端。动脉血管中血液的流动依靠心脏搏动来完成，如果动脉血管出血，压迫近心端血管就可以降低心脏对血液的传递，达到减少出血的目的。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396780, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/945.jpg', '驾驶汽车在雨天起步前要使用刮水器。', '起步前使用刮水器使得视线清晰。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396781, '调整到头枕中心对正颈部', '调整到头枕中心与颈部平齐', '调整到头枕中心高出头顶', '调整到头枕中心能支撑头部', '4', '', '怎样调整汽车座椅安全头枕的高度？', '安全头枕要与头平齐，为的是保护颈椎。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396782, '影响乘车人休息', '分散驾驶注意力', '影响正常驾驶操作', '遇紧急情况反应不及', '16', '', '行车中驾驶人接打手机或发短信有什么危害？', '常识题', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396783, '注意观察前车动态', '随时做好减速准备', '尽量靠路左侧行驶', '保持安全距离', '14', '', '在道路上怎样安全跟车行驶？', '靠右行车是常识。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396784, '正确', '错误', '', '', '1', '', '驾驶机动车在高速公路匝道上不准停车。', '首先要知道，高速公路匝道是高速公路出口或入口靠右侧的一条道路，也就是说是高速公路的一部分。根据有关规定，在高速公路上不准掉头、倒车或停车，因此在高速公路匝道上也不能掉头、倒车或是停车。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396785, '向一侧偏驶', '爆胎', '转向困难', '行驶阻力增大', '2', '', '使用已有裂纹或损伤的轮胎容易引起什么后果？', '使用已经有裂纹或损伤的轮胎行驶，造成气压不稳容易爆胎。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396786, '正确', '错误', '', '', '1', '', '在没有绷带急救伤员的情况下，可用毛巾、手帕、床单、长筒尼龙袜子等代替绷带包扎。', '根据受伤位置的不同和创面的大小，你可以利用毛巾、手帕做成简易的三角巾，用于手臂骨折的固定等，长袜子可以作为固定捆绑的绳子。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396787, '站在机动车前方', '留在车上等待救援', '站在机动车后方', '迅速转移至右侧路肩上或应急车道内', '4', '', '机动车因故障或者事故在高速公路行车道上紧急停车时，驾乘人员怎么办？', '当车辆发生故障时，打开双闪警示灯，应将车辆停放在最右侧的应急车道或紧急停车带内，夜间打开尾灯示宽灯，司乘人员应立即下车，在路沿下护坡内等候(尽量远离高速公路路面、护栏）救援车辆、人员来，由一个人在车辆尾部150米左右放置三角警示牌或警示物（最好隔十米左右放置一个警示物），切记不能在高速公路内走动、追逐、等候！不能在故障车辆内等候！一般情况禁止在高速公路上修理车辆。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396788, '正确', '错误', '', '', '1', '', '腐蚀品着火时，不能用水柱直接喷射扑救。', '无机腐蚀品或有机腐蚀品直接燃烧时，除具有与水反应特性的物质外，一般可用大量的水扑救。但宜用雾状水，不能用高压水柱直接喷射物品，以免飞溅的水珠带上腐蚀品灼伤灭火人员。液体腐蚀品应用干砂、干土覆盖吸收，扫干净后，再用水洗刷。大量溢出时可用稀酸或稀碱中和。中和时，要防止发生剧烈反应。用水洗刷撒漏现场时，只能缓慢地浇洗或用雾状水喷淋，以防水珠飞溅伤人。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396789, '紧靠道路中心', '紧靠道路右侧', '保持安全间距', '降低车速行驶', '16', 'http://images.juheapi.com/jztk/subject4/954.jpg', '在这种没有中心线的弯道上怎样安全会车？', '紧靠道路中心岂不是要和对面的来车撞了', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396790, '彭某超速行驶', '彭某驾驶机动车超载', '李某在应急车道内行驶', '李某货车车厢内违法载人', '16', '', '彭某驾驶一辆重型半挂牵引车，载运37.7吨货物（核载25吨），行至大广高速公路一下坡路段，追尾碰撞一辆由李某驾驶在应急车道内行驶的重型自卸货车（货箱内装载3.17立方黄土并搭乘24人），造成16人死亡、13人受伤。此事故中的主要违法行为是什么？', '1、载运37.7吨货物（核载25吨），说明超载；2、李某驾驶在应急车道内行驶，说明李某违法在应急车道行驶，应急车道只能应急使用；3、货箱内装载3.17立方黄土并搭乘24人，货车不能拉人，违法；题目中没有彭某超速行驶的内容，所以正确答案为BCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396791, '缓解交通拥堵', '易引发交通事故', '提高通行效率', '造成道路拥堵', '11', '', '下列关于驾驶汽车突然变道加塞说法正确的是什么？', '就跟大家排队似的，来一插队的。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396792, '加速绕过障碍物', '向左占道行驶', '停车让对方先行', '提前减速让行', '12', 'http://images.juheapi.com/jztk/subject4/957.jpg', '在这种情况下怎样安全会车？', '因为障碍物在你的车道上，故要让对面车先行。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396793, '正确', '错误', '', '', '1', '', '出现制动失效后，要首先控制方向，再设法控制车速。', '控制方向的目的是为了避免发生意外，稳住方向之后再控制车速，也就是慢慢减速，这样才能安全地停车。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396794, '控制速度随尾车后进入', '加速从第二辆车前进入', '加速从第一辆车前进入', '可从任意两车之间插入', '1', 'http://images.juheapi.com/jztk/subject4/959.jpg', '驾驶机动车遇到这种情况怎样进入行车道？', '根据图来看，三辆车之间的距离是安全距离，首先排除穿插在车之间进入行车道，因为插入的话容易发生擦碰等事故。如此一来就只有选项A是正确的。因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396795, '正确', '错误', '', '', '2', '', '在正常行车中，尽量靠近中心线或压线行驶，不给对向机动车留有侵占行驶路线的机会。', '首先压线就错了，然后要和谐礼让嘛。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396796, '正确', '错误', '', '', '2', '', '驾驶装有安全气囊的汽车可以不系安全带。', '研究表明，假设气囊加安全带是100%安全的话，安全带的作用是70%，气囊的作用是30%', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396797, '连续驾驶不超过4小时', '用餐不宜过饱', '保持良好的睡眠', '餐后适当休息后驾车', '17', '', '下列做法哪些可以有效避免驾驶疲劳？', '亲，牢记吧。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396799, '接打手持电话', '无证驾驶', '酒后驾驶', '未系安全带', '17', 'http://images.juheapi.com/jztk/subject4/964.jpg', '这个小型客车驾车人有哪些违法行为？', '从通话中得知刚喝完酒和没驾驶本，从图片中可知没系安全带和打电话。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396800, '直行通过路口', '靠路边停车', '进入左弯待转区', '在路口向右转弯', '4', 'http://images.juheapi.com/jztk/subject4/965.jpg', '看到这种手势信号时怎样行驶？', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396801, '立即停车检修', '立即开启危险报警闪光灯', '将机动车移到不妨碍交通的地点停车', '放置故障车警告标志', '16', '', '行车中发动机突然熄火后，要采取什么措施？', '选项A的做法会影响正常交通，而且立即停车检修的话，若是后方有来车，很有可能会发生追尾等事故。而后三个选项则是正确的做法，既不影响交通，也能确保驾驶人的安全。因此本题选BCD。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396802, '正确', '错误', '', '', '1', '', '行车中当突然爆胎时，驾驶人要双手紧握转向盘，尽力控制机动车直线行驶。', '在行驶中如果突然打方向，车子会飘或者翻侧的。因此车子爆胎时应该要双手紧握转向盘，尽力控制机动车直线行驶，此外还应把车速降下来，靠右停车。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396803, '正确', '错误', '', '', '1', '', '机动车驶离高速公路进入匝道后，要使车速降到限定时速以下。', '高速公路上的行驶车速远高于匝道上的行驶车速规定，因此在进入匝道后就要降低车速，以满足匝道行驶的车速要求。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396804, '遵守标志', '低速行驶', '不鸣喇叭', '避让居民', '17', '', '通过居民小区时需要注意什么？', '记住吧', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396805, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/970.jpg', '驾驶机动车不能快速通过这种情况的铁路道口。', '无人驾驶的铁道路口，要谨慎驾驶通过。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396807, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/972.jpg', '驾驶机动车进入左侧车道可以掉头。', '由图可知，道路中心线为黄色虚实线，虚线一侧可以掉头，因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396809, '停车察明水情', '确认安全后，低速通过', '机动车涉水后，间断轻踏制动踏板', '机动车涉水后，持续轻踏制动踏板', '13', '', '驾驶机动车遇有漫水路时，要采取的正确做法是什么？', '《中华人民共和国道路交通安全法实施条例》第六十四条  机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。因此本题选ABC。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396810, '火柴', '火药', '电石', '炸药', '1', '', '下列属于危险易燃固体的是什么？', '只有火柴是易燃固体，其他选项属于易燃易爆固体。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396811, '超速行驶', '货车超载', '疲劳驾驶', '酒后驾驶', '2', '', '郝某驾驶一辆载有84.84吨货物的重型自卸货车（核载15.58吨），行至滦县境内262省道34公里加623米处，与前方同向行驶的一辆载有45.85吨货物的货车（核载1.71吨）追尾碰撞后，侧翻撞向路边人群，造成19人死亡、17人受伤。双方驾驶人共同的违法行为是什么？', '题目是在问共同的违法行为，明显是超载！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396812, '缩短制动距离', '保持转向能力', '减轻制动惯性', '自动控制方向', '2', '', '机动车在紧急制动时ABS 系统会起到什么作用？', 'ABS通过控制作用于车轮制动分泵上的制动管路压力，使汽车在紧急刹车时车轮不会抱死，这样就能使汽车在紧急制动时仍能保持较好的方向稳定性，所以选B!', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396813, '前后防雾灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '危险报警闪光灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', '3', '', '机动车在夜间临时停车时，应开启什么灯？', '夜间行驶时视野不良，因此若要停车，应当开启危险报警闪光灯，提醒其他车辆注意。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396814, '正确', '错误', '', '', '2', '', '汽车的专用备胎可作为正常轮胎长期使用', '注意“专用备胎”，说明是专门用来作为备用轮胎的，因此不能当做正常轮胎长期使用', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396815, '正确', '错误', '', '', '1', '', '驾驶机动车遇到校车在道路右侧停车上下学生，同向只有一条机动车道时，后方机动车应当停车等待。', '校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越。校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396816, '开启远光灯行驶', '开启示宽灯、尾灯行驶', '开启近光灯行驶', '到达隧道口时鸣喇叭', '3', 'http://images.juheapi.com/jztk/subject4/981.jpg', '驾驶机动车进入高速公路隧道前需要注意什么？', '进入隧道时相当于夜间行驶了，因此要开启近光灯。因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396817, '握紧转向盘，适量修正', '迅速向相反方向转向', '迅速采取紧急制动', '迅速向碰撞一侧转向', '1', '', '在高速公路驾驶机动车意外碰撞护栏时采取什么保护措施？', 'A的错误之处在于少了《握紧方向盘》 。适量修正转向盘，就因为少了握紧才强制我们选适量修正转向盘。D的意思是说撞了后车子会反弹，在反弹的瞬间逆方向转向，根据答案理解的。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396818, '正确', '错误', '', '', '1', '', '在高速公路上除遇障碍、发生故障等必须停车外，不准停车上下人员或者装卸货物。', '看清楚题目，是除……外，因此，这题是对的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396819, '正确', '错误', '', '', '1', '', '驾驶汽车在非禁鸣路段，遇复杂交通情况时可合理使用喇叭。', '合理使用喇叭很和谐啊。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396821, '正确', '错误', '', '', '1', '', '下坡路制动失效后，若无可利用的地形和时机，应迅速逐级或越一级减挡，利用发动机制动作用控制车速。', '发动机制动是利用发动机的牵阻作用减慢车速，档位越低牵阻越明显，制动性越强。在下长坡道路行驶，挂入低速档利用发动机的牵阻作用可以减少制动器的负担和减少制动次数，防止制动过热引起制动力热衰减；在冰雪、泥泞的路面上行驶，应用发动机牵阻制动可以防止侧滑。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396822, '挂高速挡快速通过', '时刻观察水流的变化', '做好随时停车准备', '挂低速挡匀速通过', '4', '', '驾驶机动车通过漫水桥，停车观察水情确认安全后，怎样通过？', '《中华人民共和国道路交通安全法实施条例》第六十四条 机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。 因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396823, '观察左侧道路情况', '打开左转向灯', '不得影响正常通行车辆', '迅速向左变道', '13', '', '驾驶汽车怎样向左安全变更车道？', '凡是迅速的一般都是错误的。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396824, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/989.jpg', '夜间会车遇到这种情况要警惕两车前照灯交汇处（视线盲区）的危险。', '夜间行驶时，灯光交汇处过亮，容易造成视线盲区，因此要特别注意此处的情况。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396825, '陶某客车超员', '陶某超速行驶', '安某未按规定设置警示（告）标志', '安某违法停车', '13', '', '陶某驾驶中型客车（乘载33人），行至许平南高速公路163公里处时，以120公里/小时的速度与停在最内侧车道上安某驾驶的因事故无法移动的小客车（未设置警示标志）相撞，中型客车撞开右侧护栏侧翻，造成16死亡、15人受伤。双方驾驶人的主要违法行为是什么？', '中型客车最多载19人，所以超载选A．时速＞＝120就是超速选B.都写了没有未设置警示标志，选C。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396826, '正确', '错误', '', '', '1', '', '当机动车已偏离直线行驶方向，事故已经无可避免时，应果断地连续踏制动踏板，尽量缩短停车距离，减轻撞车力度。', '果断的连续踩踏、放松制动踏板，也就是连续的点刹车。这样做比较有效。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396827, '加速行驶，在前方继续选择理想位置', '停车选择会车地点，必要时倒车，让对方通过', '靠左占道行驶，让对方停车让行', '变换远近光灯，示意对方停车让行', '2', 'http://images.juheapi.com/jztk/subject4/992.jpg', '如图所示，驾驶机动车在窄桥上会车，选择的交会位置不理想时，以下做法正确的是什么？', '驾驶机动车在窄桥上会车，选择的交会位置不理想时，应当选择合适会车地点停车，必要时倒车，让对方通过。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396828, '正确', '错误', '', '', '2', '', '安装防抱死制动装置（ABS）的机动车制动时，制动距离会大大缩短。', '这种错误认识是由于不了解ABS的工作原理导致的。ABS可以兼顾车辆行驶中的最大的纵向制动力和横向抓地力，有效地保证车辆不会发生失控状况，但不是所有配置了ABS的车辆都会缩短制动距离，如冰雪、潮湿路面上的制动距离比车轮抱死时短一些，但在一般路面上的制动距离反而更长。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396829, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/994.jpg', '驾驶机动车遇到这种情况不要减速。', '虽然不是有障碍物的一方，但对向有机动车的情况下还是要注意减速慢行，以免对向来车直接绕过障碍物驶来，因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396830, '正确', '错误', '', '', '1', '', '驾驶人行车中意识到爆胎时，要轻踏制动踏板，缓慢减速停车。', '意识到爆胎，说明还没爆，可以轻刹；如果已经爆了，刹车容易翻车，所以抢档好些。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396831, '迅速躲离转向盘', '往副驾驶座位躲避', '迅速将两腿抬起', '两腿蹬直', '13', '', '与对向来车发生正面碰撞且碰撞位置在驾驶人正前方时，驾驶人正确的应急驾驶姿势是什么？', '前三个选项的用意是为了不让身体被卡住，而D选项则会让驾驶人受到更大的伤害，因此本题正确的是ABC。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396832, '正确', '错误', '', '', '1', '', '行车中发动机突然熄火后不能启动时，及时靠边停车检查熄火原因。', '发动机突然熄火，由于熄火原因有好几种可能，因此必须及时靠边停车找出原因，避免因判断错误发生意外。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396833, '正确', '错误', '', '', '1', '', '救助全身燃烧伤员可以采取向身上喷冷水灭火的措施。', '这是因为用冷水喷上去除了能够给创面降温以外，还能清洁伤口。因此本题正确。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396834, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/999.jpg', '驾驶机动车在这种情况下可以占用公交车站临时停车。', '根据《中华人民共和国道路交通安全法实施条例》第六十三条  机动车在道路上临时停车，应当遵守下列规定：（三）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；因此本题错误。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396835, '最左侧行车道', '中间行车道', '最右侧行车道', '任意行车道', '3', 'http://images.juheapi.com/jztk/subject4/1000.jpg', '如图所示，在同向3车道高速公路上行驶，车速低于每小时80公里的车辆应在哪条行车道上行驶？', '红圈内的是最高限速标志，蓝色的标志是最低限速标志！', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396836, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1001.jpg', '右侧标志警告前方注意右侧路口有汇入车辆。', '注意合流 用以警告车辆驾驶人注意前方有车辆汇合进来。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396837, '正确', '错误', '', '', '1', '', '驾驶人下车前要观察后视镜和侧头观察左后侧情况。', '这个一定要牢记，关系到你生命的安全。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396838, '尽量避免使用行车制动器', '选用中低速挡慢速行驶', '稳握转向盘', '加速通过', '13', '', '在泥泞道路上行车时，采取的正确做法是什么？', '四个选项中，只有选项D有可能造成侧滑或车轮空转，因此正确的做法就只有ABC。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396839, '对', '错', '', '', '2', '', '驾驶机动车驶入高速公路加速车道后，应迅速将车速提高到每小时100公里以上。', '应迅速将车速提离到每小时60公里以上。《道路交通安全法实施条例》第七十八条:离速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里.', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396840, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1005.jpg', '看到交通警察这种姿势时可以直行通过。', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396841, '在路中心行驶', '持续鸣喇叭', '加速超越', '注意观察动态', '4', 'http://images.juheapi.com/jztk/subject4/1006.jpg', '驾驶机动车遇到这种行人应该注意什么？', '遇到这种情况，要注意行人的动向，避免行人突然改变行走方向，因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396842, '先制动减速，后转向避让', '急转方向向左避让', '急打方向，向右避让', '先转向避让，后制动减速', '1', '', '在车速较高可能与前方机动车发生碰撞时，驾驶人要采取什么措施？', '注意是车速较高的情况下，因此若是直接转向，就很有可能发生侧翻。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396843, '注意观察，随时准备停车', '进入小区前应降低车速', '不与行人抢行', '鸣喇叭示意行人让行', '13', 'http://images.juheapi.com/jztk/subject4/1008.jpg', '如图所示，在居民区内为了预防突发情况出现，驾驶人应如何安全驾驶？', '小区里避免影响大家休息，是不能鸣喇叭的。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396844, '正确', '错误', '', '', '1', '', '机动车行至泥泞或翻浆路段时，要停车观察，选择平整、坚实或有车辙的路段缓慢通过。', '停车观察是在通过泥泞或翻浆路段之前的操作，也就是说看清楚路况之后再行驶。遇到这类情况，大家都知道要走好走的路，那么什么样的路是好走的？就是平整、坚实的或是有车辙的。这三种是并列的，并不是一定要同时满足这三个条件，可以任选其一。 因此这题是对的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396845, '注意观察减速慢行', '加速进入辅路行驶', '从红车后汇入车流', '从红车前汇入车流', '1', 'http://images.juheapi.com/jztk/subject4/1010.jpg', '在这种情况下从主路进入辅路怎样汇入车流？', '注意观察减速慢行是最合适的答案。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396846, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1011.jpg', '如图所示，造成这起事故的主要原因是机动车未按规定避让行人。', '遇人行横道，需要减速让行，必要时停车让行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396847, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1012.jpg', '如图所示，当与对向车辆有会车可能时，不得超车。', '对向车道有车来。不具备超车条件。应主动放弃超车，等候其他超车时机。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396848, '连续变换远、近光灯', '使用示廓灯', '使用远光灯', '使用近光灯', '4', '', '夜间驾驶机动车在窄路或者窄桥遇自行车对向驶来时，要怎样使用灯光？', '根据《中华人民共和国道路交通安全法实施条例》第四十八条  在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（五）夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396849, '扰乱交通秩序', '易导致爆胎', '影响正常通行', '易引发交通事故', '15', '', '驾驶汽车频繁变更车道有哪些危害？', '频繁变更车道跟爆胎关系不大。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396850, '对', '错', '', '', '2', '', '机动车涉水后，制动器的制动效果不会改变。', '开车经过积水道路后。车辆的后制动效果逐渐变差，此时极易发生事故.直接影响到行车安全。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396851, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1016.jpg', '如图所示，驾驶机动车行驶至此路段时，应当提前减速慢行，注意前方可能出现的行人及车辆。', '三岔路口需要减速慢行，谨慎驾驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396852, '不可左转弯', '可以右转，但要避让同向直行车辆', '可以左转，但要避让对向直行车辆', '可以直行', '3', 'http://images.juheapi.com/jztk/subject4/1017.jpg', '图中红框内所示车辆可以怎样行驶？', '左转标线表示可以左转，但要注意对面的车辆。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396853, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1018.jpg', '图中红框内行驶车辆存在交通违法行为。', '导流线的形式主要为一个或几个根据路口地形设置的白色V形线或斜纹线区域，表示车辆必须按规定的路线行驶，不得压线或越线行驶。 主要用于过宽、不规则或行驶条件比较复杂的交叉路口,立体交叉的匝道口或其他特殊地点。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396854, '对', '错', '', '', '2', '', '夜间临时停车时，只要有路灯就可以不开危险报警闪光灯。', '夜间临时停车要开启危险报警闪光灯、示廓灯和后尾灯。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396855, '对', '错', '', '', '1', '', '机动车行经视线受阻的急弯路段时，如遇对方车辆鸣喇叭示意，也应当及时鸣喇叭进行回应。', '看不见，就要靠听的了。人家鸣喇叭，你当然要回一声告诉别人这里有车，以提高注意。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396856, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/1021.jpg', '如图所示，只要没有警察在场就可以在此地点停车。', '右侧禁止停车标志。表示此路段不可以停车，不管有无警察都要守法。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396857, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1022.jpg', '如图所示，驾驶机动车在这样的狭窄路段会车，驾驶人应当减速靠右并保持安全横向距离。', '驾驶人应当减速界右并保持安全横向距离。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396858, '保持原速行驶', '减速行驶', '打开危险报警闪光灯，将机动车停到路外', '减速行驶，不断鸣喇叭，提醒周边驾驶人', '3', '', '驾驶机动车遇暴雨，无法看清路面情况，以下做法正确的是什么？', '在普通道路驾车遇暴雨.刮雨器无法改善驾驶人视线，打开危险报警闪光灯.立即减速靠边停车。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396859, '放慢车速，缓缓绕过', '鸣喇叭示意该车让路', '立即停车等待，直至该车离开', '保持原车速绕行', '3', 'http://images.juheapi.com/jztk/subject4/1024.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？', '校车左侧有停车让行标志，表示前方校车有学生上下车，看到此情况，机动车驾驶员需要立即停车等待.直至校车离开。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396860, '停车等待', '借对向车道绕行', '鸣喇叭催促', '变换远近光灯示意学生让行', '1', 'http://images.juheapi.com/jztk/subject4/1025.jpg', '如图所示，驾驶机动车遇到校车停车上下学生时，以下做法正确的是什么？', '驾驶机动车遇到校车停车上下学生时，应当停车等待。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396861, '快速超越前车', '只要对向无来车，可进行超车', '保持安全距离，跟车行驶', '鸣喇叭示意让行', '3', 'http://images.juheapi.com/jztk/subject4/1026.jpg', '如图所示，驾驶机动车遇前方白色车辆，以下说法正确的是什么？', '前方是急转弯山路，无法判断前方路况.所以需要加大安全距离，谨慎驾驶。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396862, '停车场', '道路施划的停车泊位内', '人行横道', '施工路段', '7', '', '应该选择什么地点停车 ?', '人行道肯定是不能停车的，施工路段本来就堵车，您来在停一车，那道路彻底走不了了！', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396863, '大货车制动距离相对较长', '大货车可能抛洒货物', '大货车盲区较大', '大货车遮挡小型机动车视线', '17', '', '驾驶小型汽车跟随装满货物的大货车行驶时，应当注意以下哪些方面？', '驾驶小型汽车跟随装满货物的大货车行驶时，应当注意：大货车制动距离相对较长；大货车可能抛洒货物；大货车盲区较大；大货车遮挡小型机动车视线。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396864, '减速行驶', '集中注意力谨慎驾驶', '立即减速靠边停驶', '以正常速度行驶', '3', '', '在普通道路驾车遇暴雨，刮水器无法改善驾驶人视线，此时要采取的措施是：', '本题的关键“刮水器无法刮净雨水”，也就是说无法透过前挡风玻璃看清前方情况。在这样的情况下，只能靠边停车，否则很有可能在看不清路况时发生事故。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396865, '紧跟其后行驶', '低速慢行', '连续鸣喇叭示意', '保持必要的安全距离', '11', 'http://images.juheapi.com/jztk/subject4/1030.jpg', '如图所示，驾驶机动车在居民区遇到这种情形，应如何安全驾驶？', 'A、紧跟其后行驶 （容易碾到行人，发生突发事件！）×C、连续鸣喇叭示意  （此处为居民区，不应连续鸣喇叭！）×B、D都为正确的做法~~~~', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396866, '不开转向灯', '提前开启左转向灯', '在掉头车道掉头', '在直行车道掉头', '10', '', '掉头时，以下做法正确的是什么？', '《道路交通安全法实施条例》第五十七条机动车应当按照下列规定使用转向灯:(一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯;(二)向右转夸、向右变更车道、超车完毕驶回原车道、靠路边停车时.应当提前开启右转向灯。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396867, '对', '错', '', '', '1', '', '车辆在路边起步后，应随时注意车辆两侧道路情况，向左缓慢转向，逐渐驶入正常行驶的车道。', '一看二慢三汇入，这样做既出于安全考虑，同时也不影响其他车辆的正常行驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396868, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/1033.jpg', '如图所示，驾驶机动车在居民小区遇到这种情形要连续鸣喇叭，示意行人让路。', '在居民区尽量不要鸣笛影响大家休息，也会吓到路人，在后边慢慢跟着吧。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396869, '鸣喇叭，加速通过', '减速靠右，让其先行', '靠道路左侧停靠让其先行', '抢在对方前先行通过', '2', 'http://images.juheapi.com/jztk/subject4/1034.jpg', '如图所示，行车中遇到这种情况应当如何安全会车？', '前方是窄桥，对向有来车，需要减速靠右，让其先行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396870, '前方道路靠右侧行驶', '前方道路不允许直行', '前方是直行单行路', '前方注意右侧路口', '3', 'http://images.juheapi.com/jztk/subject4/1035.jpg', '右侧标志表示什么？', '表示一切车辆单向行驶。此标志设在单行路的路口和入口处的适当位置。 ', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396871, '车辆行驶速度', '驾驶人的反应时间', '路面状况', '载货量的多少以及制动器的结构形式等', '17', '', '关于影响制动停车距离的因素，以下说法正确的是什么？', '速度、反应时间、路况、载重和刹车系统等因素都会影响制动距离。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396872, '尽量靠道路左侧通行', '停车�望，缓慢通过', '尽量避免临时停车', '勤鸣喇叭，低速通行', '3', 'http://images.juheapi.com/jztk/subject4/1037.jpg', '如图所示，驾驶机动车在这种落石多发的山区道路行驶，以下说法正确的是什么？', '都说了落石多发山区，要避免停车的，不然太危险了。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396873, '按规定使用灯光', '带行驶证、驾驶证', '随意向车外抛洒物品', '遵守交通信号', '3', '', '机动车在道路上行驶时，属于交通陋习的是什么行为？', '随意向车外抛洒物品，会影响其他车辆等的行驶，也不环保卫生，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396874, '对', '错', '', '', '2', '', '行车中遇牲畜通过道路影响通行时，可采取连续鸣喇叭的方式进行驱赶。', '切勿持续鸣喇叭或加速绕行，以防引起牲畜骚动或赶牲畜的人为保护牲畜而突然冲到路中！', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396875, '遇到紧急情况时，能有足够的避让空间', '跟车越近，越不容易掌握前车前方的情况', '防止因前车尾灯损坏，不能及时发现前车制动', '跟车太近，容易发生追尾', '17', '', '跟车行驶时，要留有足够的安全距离，是因为什么？', '遇到紧急情况时，能有足够的避让空间；跟车越近，越不容易掌握前车前方的情况二防止因前车尾灯损坏，不能及时发现前车制动；跟车太近，容易发生追尾。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396876, '开启左转向灯后直接变更车道', '在确认左侧无其他车辆后，变更车道', '开启左转向灯稍向左行驶，后车让行后再变更车道', '开启左转向灯，让后方车辆通过后变更车道', '4', 'http://images.juheapi.com/jztk/subject4/1041.jpg', '驾驶机动车在向左变更车道前，通过左后视镜看到图中情形时，以下做法正确的是什么？', '驾驶机动车在向左变更车道前，通过左后视镜看到图中情形时，应开启左转向灯，让后方车辆通过后变更车道。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396877, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1042.jpg', '如图所示，在这种情况下要注意右侧的非机动车。', '非机动车有很大的随意性，需要非常的注意。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396878, '对', '错', '', '', '1', '', '当感觉与对向驶来的车辆会有会车困难的时候，应及时减速靠边行驶，或停车让行。', '在不确定前方路况的情况下一定要减速慢行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396879, '对', '错', '', '', '2', '', '隧道中可以临时停车休息一会儿，避免疲劳驾驶。', '这个地方，是不能停车的，视线很差很容易造成追尾事故的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396880, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/1045.jpg', '如图所示，这种情况下，B车优先通行。', '三个先行原则：转弯的机动车让直行的车辆先行，右方道路来车先行，右转弯车让左转弯车先行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396881, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/1046.jpg', '路面标记指示这段道路上最高限速为80公里/小时。', '白色的是最低车速，黄色的是最高车速。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396882, '对', '错', '', '', '2', '', '夜间行车时，全车灯光突然熄灭，应当立即迅速制动，靠边停车。', '夜间行车本身视野受限，视距变短，所以要谨值驾驶，灯光不仅有照明作用，如果夜间全车灯光突然媳灭。立即迅速制动的话很容易造成追尾事故，后方的车辆也不容易发现前车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396883, '双手握稳转向盘', '迅速转向调整', '逐渐降低车速', '急踏制动踏板减速', '8', '', '驾驶机动车在高速公路行驶，雨天发生“水滑”现象时，以下做法正确的是什么？', '所谓“水滑”现象就是雨天汽车在积水路面上高速行驶时，轮胎与路面间的存水不能排除，水的压力使轮胎上浮，形成汽车在积水路面上滑行的现象，应双手握紧方向盘并加速踏板的方式减速才能真正让车逐渐减慢', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396884, '前方有人行横道', '应当相应减速行驶', '视野范围内无行人可以保持原速行驶', '视野范围内无行人可以适当加速通过', '7', 'http://images.juheapi.com/jztk/subject4/1049.jpg', '如图所示，当您看到这个标志时，应该想到什么？', '注意行人标志.用以警告车辆驾驶人减速慢行，注意行人。设在行人密集，或不易被写驶员发现的人行横道线以前适当位置。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396885, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/1050.jpg', '如图所示，驾驶机动车在会车过程中遇到这种情况，应当持续鸣喇叭并提高车速迫使其驶回原车道。', '前方有车辆借用车道超车，应主动减速避让，待其超车完毕，驶入正常车道后，再正常行驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396886, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1051.jpg', '如图所示，驾驶机动车通过小区遇到这种情况，应减速行驶，随时准备停车。', '路况比较复杂，需减速慢行，必要时停车让行，谨慎驾驶。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396887, '超车前提前开启左转向灯，提醒前方被超车辆驾驶人', '切换远、近光灯提醒前方被超车辆驾驶人', '长时间鸣喇叭警示被超车辆驾驶人', '完成超车后并回行车道要开启右转向灯', '14', '', '关于超车，以下说法正确的是什么？', '《道路交通安全法》第四十七条机动车超车时.应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。在没有道路中心线或者同方向只有1条机动车道的道路上。前车遇后车发出超车信号时，在条件许可的情况下，应当降低速度、靠右让路。后车应当在确认有充足的安全距离后，从前车的左侧超越.在与被超车辆拉开必要的安全距离后.开启右转向灯。驶回原车通。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396888, '无需观察直接汇入主路车流', '无需避让主路车辆', '在不妨碍主路车辆正常行驶的前提下汇入车流', '鸣喇叭示意主路车避让', '3', '', '驾驶机动车驶出小区上道路行驶，以下做法正确的是什么？', '驾驶机动车驶出小区上道路行驶，要注意在不妨碍主路车辆正常行驶的前提下汇入车流。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396889, '对', '错', '', '', '2', '', '可以选择下坡路段超车。', '下坡路惯性大，车速不好控制，不可以超车。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396890, '逆风向转动转向盘', '顺风向转动转向盘', '采取紧急制动', '双手稳握转向盘', '4', '', '大风天气行车，由于风速和风向不断地发生变化，当感到转向盘突然难以控制时，驾驶人要怎样做？', '风速风向是不固定的，乱转方向盘会出事的，只有减速和稳稳地把握方向盘，才能在风天安全行驶。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396891, '对', '错', '', '', '2', '', '雪天行车，车轮的附着力大大减小，跟车距离不是主要的，只需要保持低速行驶便可以防止事故发生。', '冰雪道路，驾驶人要采取以下措施，以确保行车安全1尽且控制车速。避免紧急制动；2尽量循前车的车撤走；3避免急转方向；4保持足够的安全距离。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396892, '车内驾驶人可能为酒后驾车，应当保持距离', '车内驾驶人可能为酒后驾车，可以加速超过', '车内驾驶人可能为吸毒后驾车，可以加速超过', '此情况较为安全，可以紧密跟随', '1', '', '当你看到前方车辆行驶线路左右摆动，以下说法正确的是什么？', '前方车辆情况不明。可能是酒驾、吸毒后驾车，应保持距离。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396894, '行驶系', '转向系', '发动机', '变速器', '3', '', '水温表是用来指示哪个部件的温度？', '水温表就是检测发动机温度的。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396895, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/1060.jpg', '如图所示，驾驶机动车驶出环岛时，应先驶入最右侧车道不用开启转向灯驶离即可。', '驶出环岛，需要先开启右转向灯。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396896, '不可急踩制动踏板', '逐渐松抬加速踏板，让车速逐渐减缓', '不得迅速转向', '立刻猛踏制动踏板，降低车速', '13', '', '车辆发生“水滑”时，以下做法正确的是什么？', '所谓水滑现象就是雨天汽车在积水路面上高速行驶时，轮胎与路面间的存水不能排除，水的压力使轮胎上浮。形成汽车在积水路面上滑行的现象。“水滑.现象可以通过控制汽车的行驶速度得以遵免.汽车在雨中或在积水路段行驶。如果你感觉到转动方向盘时用力变小，应机敏地意识到这是可能发生.水滑一的危险信号，此时要把车速降到安全行驶速度以下，以确保安全。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396897, '对', '错', '', '', '2', '', '驾驶机动车在高速公路加速车道提速到每小时60公里以上时，可直接驶入行车道。', '应该是开启左转向灯，观察后面无车后在驶入行车道。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396898, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1063.jpg', '如图所示，在这种情况下可以借用快速车道超车。', '路面标线是白色虚线，在安全的情况下，可以借用左侧车道超车.', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396899, '匝道', '加速车道', '减速车道', '服务区', '4', '', '需要在高速公路停车时，要选择在什么地方停车？', '在高速公路上匝道、加速车道和减速车道不能停车，因为不仅会影响正常交通，还容易引发事故。因此本题选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396900, '提前减速观察', '注意避让行人', '随时准备停车', '抢先加速通过', '13', 'http://images.juheapi.com/jztk/subject4/1065.jpg', '如图所示，接近人行横道线时怎样安全行驶？', '避让行人，不要跟行人抢行。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396901, '超车时从前车左侧超越', '超车时从前车右侧超越', '超车完毕，立即开启右转向灯驶回原车道', '超车完毕，与被超车拉开必要的安全距离后开启右转向灯驶回原车道', '9', '', '关于超车，以下说法正确的是什么？', '《道路交通安全法实施条例》第四十七条机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。在没有道路中心线或者同方向只有1条机动车道的道路上，前车遇后车发出超车信号时，在条件许可的情况下。应当降低速度、靠右让路。后车应当在确认有充足的安全距离后，从前车的左侧超越。在与被超车辆拉开必要的安全距离后。开启右转向灯，驶回原车道。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396902, '对', '错', '', '', '1', '', '雪天行车，由于路面湿滑，车轮附着力减小，因此应当加大两车之间的安全距离。', '冰雪道路，绍驶人要采取以下措施，以确保行车安全1尽且控制车速。避免紧急制动；2尽量循前车的车撤走；3避免急转方向；4保存足够的安全距离。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396903, '不能准确判断周围车辆距离', '周围车辆驾驶人不容易看清超车信号', '道路湿滑，车辆易出现侧滑现象', '不能够及时发现危险情况', '17', '', '雨天驾驶机动车，不宜超车的主要原因是什么？', '在干燥路面驾驶，车速提高，车轮与路面间的附着力(俗称“抓地力“)几乎没有变化。而雨天，当车辆在潮湿路上行驶，车轮的抓地力则随车速的增加而急剧变小，很容易发生“水滑”，此时，不要急踩制动踏板或猛打转向盘。由于雨天路面情况的特殊性，行车时应适当降低车速。准备停车时最好提前100-200米开始减速，给后方的车辆留出足够的反应时间，也为自己留够制动距离，以免发生追尾。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396904, '沿路肩倒车驶回', '继续向前行驶', '立即停车', '在原地掉头', '2', '', '高速公路上行车，如果因疏忽驶过出口且下一出口距离较远时怎样做？', '因疏忽驶过了出口，就要等到下一个出口再驶离。根据四个选项，选择B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396905, '对', '错', '', '', '1', '', '多车跟车行驶,为避免追尾事故发生应至少观察前方两到三辆车，从而能对减速或停车具有预见性。', '多车跟车行驶.为避免追尾事故发生应至少观察前方两到三辆车，从而对减速或者停车具有预见性。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396906, '减速或停车让行', '鸣喇叭示意其让道', '抢在行人之前通过', '向右变道绕过行人', '1', 'http://images.juheapi.com/jztk/subject4/1071.jpg', '在这种路口遇到行人突然横穿怎么办？', '当然要减速或停车让行，不然碰到行人麻烦就大了。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396907, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1072.jpg', '如图所示，驾驶机动车行驶至此路段时，应当减速靠右侧行驶。', '地面标线为黄实线。不可以越线，对方车道有来车.需要减速靠右侧行驶，邀免和对向来车发生刮蹭。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396908, '应靠道路右侧', '开关车门不得妨碍其他车辆和行人通行', '交叉路口50米以内不得停车', '开左转向灯', '13', '', '关于停车，以下说法正确的是什么？', '靠边停车首先开启右转向灯然后观察后视镜在安全的情况下再打方向盘停车。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396909, '对', '错', '', '', '2', '', '在立交桥上可以临时停车。', '立交桥车多，路况复杂，还是规矩开车吧。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396910, '注意观察标志标线', '注意减速慢行', '不要鸣喇叭', '快速通过', '13', '', '驾驶机动车通过学校门口时应注意什么？', '车辆通过学校和小区应注葱观察标志标线.低速行驶，不要鸣喇叭，减少噪音，以免影响学生学习。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396911, '加速直接汇入车流', '认真观察主路车流情况', '提前开启转向灯并降低车速', '不得妨碍主路正常行驶车辆', '16', 'http://images.juheapi.com/jztk/subject4/1076.jpg', '如图所示，驾驶机动车遇这种情况应如何安全汇入车流？', '凡是“加速”，一般都是错的。正确的是认真观察主路车流情况，提前开启转向灯并降低车速，不得妨碍主路正常行驶车辆。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396913, '在交叉路口停车', '在铁路道口停车', '在山区易落石路段停车', '在停车场停车', '13', '', '关于停车，以下做法错误的是什么？', '停车肯定去停车场，ABC三个选择都是no zuo no die（不作不死）的人干的事。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396914, '正确', '错误', '', '', '2', '', '高速路上，机动车因故障暂时不能离开应急车道或路肩时，驾乘人员要下车在路边等候，但不得离开高速公路。', '错在【路边】2字！机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；但是，警告标志应当设置在故障车来车方向一百五十米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396915, '人行横道', '停车场', '山区容易塌方、泥石流路段', '道路施划的停车泊位内', '8', '', '以下什么地点不能停车？', '人行横道肯定是不能停车，山区容易塌方、泥石流路段这个也太危险了所以也是不能停车的。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396916, '两车临近时减速', '适当加速交会', '提前减速缓慢交会', '尽量靠近中线交会', '3', '', '驾驶机动车在结冰的道路上怎样会车？', '由于是结冰的道路，说明路面很易滑，因此在这样的路面上会车时，要注意减速慢行，因此本题选C。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396917, '保持大间距', '开启远光灯', '开启近光灯', '适时鸣喇叭', '1', '', '驾驶机动车在雾天怎样跟车行驶？', '本题考的是跟车注意事项，与跟车有关的最先想到就是跟车距离，因此本题选A。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396918, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/1083.jpg', '如图所示，若车后50m范围内无其他车辆，可以不打转向灯变更车道。', '任何时候变道都需要打转向灯.', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396919, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/1084.jpg', '如图所示，驾驶机动车在这样的路段遇前方两车交会应及时减速。', '路况不好，为了大家的安全，减速慢行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396920, '车门开的幅度不要过大', '开门下车动作要迅速', '仔细观察左后方情况', '开车门的动作要缓慢', '15', '', '驾驶人在下车前应注意是什么？', '亲，难道你没听过身边的人因为下车没看后边而发生事故的事情吗？', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396921, '什么也不用检查', '轮胎有没有清洗', '备胎在什么位置', '轮胎的紧固和气压', '4', '', '出车前对轮胎进行哪些方面的检查？', '对轮胎的检查是为了确保在行车过程中能安全行驶，由此本题应选D。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396922, '正确', '错误', '', '', '1', '', '驾驶机动车通过漫水路时驾驶人要挂低速挡匀速通过。', '中华人民共和国道路交通安全法实施条例》第六十四条 机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396924, '正确', '错误', '', '', '1', '', '道路危险货物运输驾驶人、装卸人员和押运员必须了解所运载的危险化学品的性质、危害特性、包装容器的使用特性和发生意外时的应急措施。', '根据《道路运输从业人员管理规定》第十二条 道路危险货物运输装卸管理人员和押运人员应当符合下列条件： （三）接受相关法规、安全知识、专业技术、职业卫生防护和应急救援知识的培训，了解危险货物性质、危害特征、包装容器的使用特性和发生意外时的应急措施； 因此本题是对的。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396925, '同向只有一条机动车道，后方机动车应当停车等待', '同向有两条机动车道，左侧车道后方机动车可以减速通过', '同向有三条机动车道，中间车道后方机动车应当停车等待', '同向有三条机动车道，左侧车道后方机动车可以减速通过', '15', '', '驾驶机动车遇到校车在道路右侧停车上下学生时，应注意什么？', '同方向有两条以上车道，校车停靠后方和相邻车道上机动车应停车等待。　也就是说，当有经过校车停靠的道路时，只有同向车道为三车道或三车道以上时，车辆才能从第三车道或其左侧同向车道缓慢通过。', 4, 1, 1, NULL, 2);
INSERT INTO `me_question` VALUES (104107678396926, '危险报警闪光灯、示廓灯和后位灯', '左转向灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', '1', '', '机动车在雾天临时停车时，应开启什么灯？', '雾天开车视野不良，因此若要停车，就应该开启危险报警闪光灯，提醒其他车辆注意。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396927, '正确', '错误', '', '', '2', '', '在道路上行车时，安全跟车距离无需随着速度变化而变化。', '这个当然不是，必然上了高速，速度很快，还按照普通道路的跟车距离必然不行。', 4, 1, 1, NULL, 1);
INSERT INTO `me_question` VALUES (104107678396928, '预防机动车侧滑', '预防行人横穿', '尽快加速通过', '持续鸣喇叭', '2', 'http://images.juheapi.com/jztk/subject4/1093.jpg', '驾驶机动车遇到这种情形应该注意什么？', '由图可知，此妈妈在对着路对面的孩子招手，因此要注意从对面穿出孩子跑到妈妈这边，因此本题选B。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104107678396929, '适当鸣喇叭，加速通过', '在行人或骑车人通过前提前加速通过', '减速，停车让行', '连续鸣喇叭使其让行', '3', 'http://images.juheapi.com/jztk/subject4/1094.jpg', '如图所示,驾驶机动车遇到这种情况时，以下做法正确的是什么？', '驾驶机动车遇到这种情况时，应该减速，停车让行。', 4, 1, 1, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011394, '开启右转向灯', '开启危险报警闪光灯', '不用开启转向灯', '开启左转向灯', '3', 'http://images.juheapi.com/jztk/subject4/1.jpg', '驾驶机动车进入这个路口怎样使用灯光？', '由图可知，前方是向右环形路段，因此向右行驶时不需要开启转向灯，所以本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011395, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/2.jpg', '在这个路口左转弯要靠路口中心点左侧转弯。', '如图是中心圈，设在平面交叉路口的中心，用以区分车辆大、小转弯，及交叉路口车辆左右转弯的指示，车辆不得压线行驶。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011396, '允许长时停放车辆', '可以临时停车', '允许长时停车等客', '不允许停放车辆', '2', 'http://images.juheapi.com/jztk/subject4/3.jpg', '右侧标志是何含义？', '禁止车辆长时停放：禁止车辆长时停放，临时停放不受限制。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011397, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/4.jpg', '右侧标志表示只能靠左侧道路行驶。', '靠左侧道路行驶-表示只准一切车辆靠左侧道路行驶。此标志设在车辆必须靠左侧行驶的路口以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011398, '正确', '错误', '', '', '2', '', '在道路上跟车行驶时，跟车距离不是主要的，只须保持与前车相等的速度，即可防止发生追尾事故。', '跟车距离很重要，不同的道路对跟车距离要求是不一样的。假设你跟前车的的速度相等，但是距离是1米，是不可能刹得住车的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011399, '开启左转向灯', '开启报警闪光灯', '不用开转向灯', '开启右转向灯', '4', 'http://images.juheapi.com/jztk/subject4/6.jpg', '驾驶机动车驶出这个环岛路口怎样使用灯光？', '由图可知，前方有两条道可供行驶，而驾驶车辆若想要离开环形路口，就要靠右侧行驶转出，此时要开启右转向灯告知其他车辆你要离开了。因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011400, '正确', '错误', '', '', '1', '', '在行驶中，驾驶人在注意与前车保持安全距离的同时，也要谨慎制动，防止被后车追尾。', '在行驶过程中要留意与前后车的距离，因此若是贸然使用制动，很容易使后车来不及反应发生追尾，所以本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011401, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/8.jpg', '右侧标志警告前方道路左侧变宽。', '左侧变窄-用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011402, '向右转弯', '直行和左转弯', '直行和右转弯', '在桥下掉头', '2', 'http://images.juheapi.com/jztk/subject4/9.jpg', '立体交叉处这个标志提示什么？', '立交直行和左转弯行驶-表示车辆在立交处可以直行和按图示路线左转弯行驶。此标志设在立交左转弯出口处适当位置。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011403, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/10.jpg', '驾驶机动车遇到这种情况的人行横道线可以加速通过。', '遇到前方有人行横道应减速慢行，接近时停车让行。而由图中更可知有行人即将穿过，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011404, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/11.jpg', '右侧标志警示前方道路有连续三个或三个以上的弯路。', '反向弯路-用以警告车辆驾驶人减速慢行。设置位置为两反向圆曲线起点的外面，但不应进入相邻的圆曲线内。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011405, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/12.jpg', '右侧这个标志提醒注意前方200米是堤坝道路。', '前方200米是堤坝路-用以提醒车辆驾驶人小心驾驶，设在沿水库、湖泊、河流等堤坝道路以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011406, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/13.jpg', '图中标志提醒障碍物在路中，车辆从右侧绕行。', '左侧绕行-用以告示前方道路有障碍物，车辆应按标志指示减速慢行，左侧绕行通过。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011407, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/14.jpg', '造成这起事故的主要原因是行人从车前横穿。', '由图可知，行人正从人行横道线上穿过，发生这样的事故主要是由于车子没有停车让行造成的，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011408, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/15.jpg', '右侧标志提醒前方路面高突。', '驼峰桥：用以提醒车辆驾驶人谨慎驾驶。设在拱度很大，影响视距的驼峰桥以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011409, '正确', '错误', '', '', '2', '', '机动车可以选择交叉路口进行倒车。', '《中华人民共和国道路交通安全法实施条例》第五十条  机动车倒车时，应当察明车后情况，确认安全后倒车。不得在铁路道口、\"交叉路口\"、单行路、桥梁、急弯、陡坡或者隧道中倒车。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011410, '前后雾灯', '近光灯', '远光灯', '危险报警闪光灯', '2', '', '夜间机动车通过照明条件良好的路段时，要怎样使用灯光？', '用近光灯不会照得对方看不见路，同时为了安全起见，开启近光灯能够让相互间清晰可见。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011411, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/18.jpg', '驾驶机动车在这个路口右转弯可以不变更车道。', '由图可知，此为三车道道路，右转弯时应当靠右侧车道，因此如图情形是不可以直接右转弯的，因为车的右侧是白实线，不能碾压。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011412, '尽快加速通过', '靠边停车等待', '注意�望安全通过', '禁止通行', '3', 'http://images.juheapi.com/jztk/subject4/19.jpg', '驾驶机动车遇到这种信号灯不断闪烁时怎样行驶？', '黄灯闪烁，是一种警示，提示注意安全通过。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011413, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/20.jpg', '驾驶机动车在这种情况下不能左转弯。', '左转红灯，所以不能左转，正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011414, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/21.jpg', '驾驶机动车通过这个路口要注意观察左侧情况。', '从图上看，是有机动车从右边道路驶出，因此要注意观察该车的行驶路线。所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011415, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/22.jpg', '右侧标志表示前方路口不准车辆左转。', '禁止向左转弯-表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011416, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/23.jpg', '这个标志警告前方铁路道口有多股铁路与道路相交。', '上面带叉和折线就代表不止一条铁路！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011417, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/24.jpg', '驾驶机动车在这种情况下不能直行和左转弯', '直行和左转都是红灯，所以不能直行和左转。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011418, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/25.jpg', '右侧标志警告前方是上陡坡路段。', '上陡坡-用以提醒车辆驾驶人小心驾驶。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011419, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/26.jpg', '驾驶机动车在这个路口左转弯要提前按导向箭头指示向左变更车道。', '由图可知，左侧车道是左转弯车道，车辆若要左转弯应当提前变更车道驶入左转弯车道，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011420, '向右转弯', '直行和左转弯', '直行和右转弯', '在桥下掉头', '3', 'http://images.juheapi.com/jztk/subject4/27.jpg', '立体交叉处这个标志提示什么？', '立交直行和右转弯行驶：表示车辆在立交处可以直行和按图示路线右转弯行驶。此标志设在立交右转弯出口处适当位置。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011421, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/28.jpg', '右侧这个标志提示前方是连续急转弯道路。', '易滑-用以促使车辆驾驶人注意慢行。设在路滑容易发生事故的路段以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011422, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/29.jpg', '右侧标志提醒前方是左侧傍山险路。', '注意落石-用以提醒车辆驾驶人注意落石。设在有落石危险的傍山路段以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011423, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/30.jpg', '路面白色反光虚线警告前方路段要减速慢行。', '用于警告车辆驾驶人前方应减速慢行。收费广场减速标线设于收费广场及其前部适当位置，为白色反光虚线。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011424, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/31.jpg', '前方标志告知前方200米处是露天停车场。', '单纯一个P字就是露天停车场，如果P字上边还有个遮挡，就是室内的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011425, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/32.jpg', '驾驶机动车在人行横道前遇到这种情况一定要减速慢行。', '由图可知，有行人正从人行横道线上横穿道路，而机动车已临近人行横道线，此时应停车让行，以免相撞，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011426, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/33.jpg', '右侧标志指示前方路口绕行的路线。', '亲，看清楚试题，问的是有右侧标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011428, '临近时关闭前照灯', '使用近光灯', '使用远光灯', '提前关闭所有灯光', '2', 'http://images.juheapi.com/jztk/subject4/35.jpg', '夜间驾驶机动车遇到这种情况怎样使用灯光？', '据《中华人民共和国道路交通安全法实施条例》第四十八条  在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（五）夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011429, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/36.jpg', '该车道路面导向箭头指示在前方路口仅可直行。', '该车道路面导向箭头指示在前方路口仅可直行或者左转。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011430, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/37.jpg', '前方路口减速让行线表示要停车让干道车先行。', '减速让行不是停车让行!', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011432, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/39.jpg', '驾驶机动车在小区内遇到这样的情况要在自行车前加速通过。', '由图可知，前方是丁字路口，且横向道路有骑自行车的人穿出。遇到这种情况应当让自行车先行，减速慢行注意避让。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011433, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/40.jpg', '前方标志预告高速公路入口在路右侧。', '此为入口预告标志，没有预告高速公路入口在路右侧的标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011434, '鸣喇叭示意其让行', '加速从车前通过', '开前照灯示意其让行', '减速或停车让行', '4', 'http://images.juheapi.com/jztk/subject4/41.jpg', '驾驶机动车在路口直行遇到这种情况怎么办？', '虽然前方绿灯，可以正常行驶通过，但由于对向有来车左转弯，因此应当让其通过后再通过，所以本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011435, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/42.jpg', '驾驶机动车在公交车站遇到这种情况要迅速停车让行。', '由图可知，停靠在一边的公交车后有行人穿出，此时应当予以避让，其一是为了避让行人，其二是以防公交车突然起步。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011436, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/43.jpg', '右侧标志表示前方路段允许超车。', '禁止超车-表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011437, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/44.jpg', '驾驶机动车此时可以加速通过路口。', '由图可知，此时信号灯已经变为黄色，即将转变为红色，此时应当在停车线前停车。否则就是闯黄灯的行为了。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011438, '从列队前方绕过', '减速慢行通过', '及时停车让行', '从列队空隙穿过', '3', 'http://images.juheapi.com/jztk/subject4/45.jpg', '驾驶机动车在学校门口遇到这种情况怎样行驶？', '由图可知，前方人行横道线上有学生排着队通过，此时应当等学生们通过后再通过。因此在临近人行横道线时，应当停车让行。所以本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011439, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/46.jpg', '右侧标志提醒注前方是驼峰桥。', '路面高突-用以提醒车辆驾驶人减速慢行。设在路面突然高突以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011440, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/47.jpg', '右侧标志指示距离设有电子不停车收费车道的收费站1公里。', 'ETC,使用该系统，车主只要在车窗上安装感应卡并预存费用，通过收费站时便不用人工缴费，也无须停车，高速费将从卡中自动扣除。虽然能实现不停车收费，但一般来说，车辆还是需要以较低速度通过。这种收费系统每车收费耗时不到两秒，其收费通道的通行能力是人工收费通道的5到10倍。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011441, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/48.jpg', '右侧标志警告前方路段要注意儿童。', '注意行人-用以警告车辆驾驶人减速慢行，注意行人。设在行人密集，或不易被驾驶员发现的人行横道线以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011442, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/49.jpg', '驾驶机动车在这个路口右转弯时要避让非机动车。', '右转弯时在非机动车道有非机动车正在前进，此时应当注意避让，待非机动车通过后再转弯，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011443, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/50.jpg', '路面标记指示这段道路上最低限速为60公里/小时。', '白色的是最低车速，黄色的是最高车速。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011444, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/51.jpg', '右侧这个标志提醒注意左侧有落石危险。', '注意落石-用以提醒车辆驾驶人注意落石。设在有落石危险的傍山路段以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011445, '在公交车后停车', '迅速超越公交车', '减速，缓慢超越', '连续鸣喇叭提醒', '3', 'http://images.juheapi.com/jztk/subject4/52.jpg', '在这种公交车站怎样预防公交车突然起步？', '注意本题的情景：公交车可能突然起步。因此横向间距要加大，而且要减速慢行，超越时要特别留意公交车的动态，避免发生碰撞，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011446, '靠路左侧转大弯', '靠弯路中心转弯', '靠路右侧转小弯', '借对向车道转弯', '3', 'http://images.juheapi.com/jztk/subject4/53.jpg', '在这种条件的道路上怎样行驶才安全？', '前方是向右急转弯路段，靠右转弯是小弯，靠左转弯是大弯，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011447, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/54.jpg', '右侧这个标志警告前方是无人看守的有多股铁路与道路相交铁路道口。', '这是有人看守铁道路口。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011448, '观察标志标线', '减速慢行', '不要鸣喇叭', '快速通过', '13', '', '驾驶机动车通过学校时要注意什么？', '在有学校的路段一般都会设置提醒有学校或是禁止鸣喇叭的交通标志，因此在经过学校时不仅要观察交通标志，还要记住不得鸣喇叭。另外，由于学校周围会有学生走动，因此在途经时车速一定要慢，避免撞到人或其他。因此本题要选ABC。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432414011450, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/57.jpg', '右侧这个标志提醒注意前方是傍山险路。', '傍山线路-用以提醒车辆驾驶人小心驾驶。设在傍山险路路段以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011451, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/58.jpg', '路两侧的标志提示前方道路线形变化。', '驾驶人员在一条不熟悉的道路上行驶，不可能知道行驶前方存在有潜在的危险。警告标志的作用就是及时地提醒驾驶人员前方道路线形和道路状况的变化，在到达危险点以前有充分时间采取必要行动，确保行驶安全。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011452, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/59.jpg', '驾驶机动车在交叉路口遇到这种情况可以不让行。', '虽然绿灯时可以直行，但由于对向左转车辆已经转到如图位置了，因此应当予以让行，否则会发生碰撞，所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011453, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/60.jpg', '右侧标志提醒前方右侧500米有避险车道。', '避险车道是指在长陡下坡路段行车道外侧增设的供速度失控车辆驶离正线安全减速的专用车道。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011454, '开启危险报警闪光灯', '开启远光灯', '开启雾灯', '开启近光灯', '4', '', '机动车驶入双向行驶隧道前，要如何使用灯光？', '凡是车辆进入双向行驶隧道，一般都应该开启近光灯，而不要开启远光灯。若是隧道本身没有灯光的，可以变换灯光行驶，但是会车前要转为近光灯。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011455, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/62.jpg', '驾驶机动车在路口看到这种信号灯亮时，要加速通过。', '路口黄灯持续闪烁，警示驾驶人要注意�望，确认安全通过。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011456, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/63.jpg', '右侧标志提醒前方是非机动车道。', '黄底的是注意的意思，此标志的意思是注意非机动车。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011457, '大型货车', '大型客车', '各种机动车', '小型客货车', '3', 'http://images.juheapi.com/jztk/subject4/64.jpg', '右侧标志提示哪种车型不能通行？', '禁止机动车通行：表示禁止某种机动车通行。此标志设在禁止机动车通行的路段入口处。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011458, '边观察边缓慢通过', '不换挡加速通过', '在火车到来前通过', '不得越过停止线', '4', 'http://images.juheapi.com/jztk/subject4/65.jpg', '驾驶机动车在铁路道口看到这种信号灯时怎样行驶？', '红灯，你就停了吧。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011459, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/66.jpg', '右侧标志警告前方路面两侧变窄长度为5公里。', '两侧变窄-用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011460, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/67.jpg', '右侧标志提醒前方有村庄或集镇，建议速度30公里/小时。', '上边是村庄标志-此标志设在不易发现前方有村庄或小城镇的路段以前适当位置。下边是建议车速标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011462, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/69.jpg', '右侧这个标志提醒前方山口注意横风。', '注意横风-用以提醒车辆驾驶人小心驾驶。设在经常有很强的侧向风的路段以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011464, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/71.jpg', '右侧标志表示注意避让直行方向来的机动车。', '直行-表示只准一切车辆直行。此标志设在直行的路口以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011465, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/72.jpg', '右侧标志表示前方路段解除时速40公里限制。', '解除限制速度-表示限制速度路段结束。此标志设在限制车辆速度路段的重点。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011466, '立即停车', '加速通过', '连续鸣喇叭', '减速慢行', '1', 'http://images.juheapi.com/jztk/subject4/73.jpg', '驾驶机动车通过居民小区遇到这种情况怎样处置？', '看到前方有皮球，就要联想到可能会有儿童跑出来捡球，而且距离较近，因此要停车避让。所以本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011467, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/74.jpg', '右侧标志警告前方是向右反向弯路。', '连续弯路-用以警告车辆驾驶人减速慢行。设置位置为连续弯路起点的外面，当连续弯路总长度大于500m时，应重复设置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011468, '加速从行人前通过', '从行人后绕行通过', '减速、鸣喇叭示意', '停车等待行人通过', '4', 'http://images.juheapi.com/jztk/subject4/75.jpg', '驾驶机动车在这个位置怎样安全通过？', '肯定是停车等待，顺便看MM！HOHO！', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011470, '向左、向右转弯', '直行或向左转弯', '向左转弯', '直行或向右转弯', '4', 'http://images.juheapi.com/jztk/subject4/77.jpg', '这个路口允许车辆怎样行驶？', '左转是红灯，直行和右转是绿灯。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011471, '提前减速，注意机动车', '保持正常速度行驶', '鸣喇叭，迅速通过', '提前加速，快速通过', '1', 'http://images.juheapi.com/jztk/subject4/78.jpg', '驾驶机动车驶近前方主支干道交汇处要注意什么？', '由图可知，前方会有车辆从支路汇入主路，因此为了避免支路的车辆汇入时发生碰撞，所以要减速慢行，并注意周围的机动车，因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011472, '正确', '错误', '', '', '2', '', '道路划设专用车道的，在专用车道内，其他机动车可以借道超车。', '我觉得专用车道指的是“机动车道、非机动车道、人行道”这些都属于专用车道，什么车走什么道，应该就不能借道超车啊，否则你有可能开到人行道上去借道超车！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011473, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/80.jpg', '驾驶机动车在这个路口可以直接向右转弯。', '遇到这样的情况，应当开启右转向灯，确认右侧没有非机动车接近时右转弯，避免发生碰撞，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011474, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/81.jpg', '右侧标志警告前方路段设有信号灯。', '注意信号灯-用以警告车辆驾驶人注意前方路段设有信号灯，应依信号灯指示行车。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011475, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/82.jpg', '前方标志预告互通式立交桥通往方向的信息。', '互通式立交桥是城市里最常见的立交桥。互通式立交桥桥上面的道路与桥下面的道路有道路(即匝道)相联结，使上下道路上的车辆可以通行。互通式立交桥包括完全互通和部分互通两种形式，根据其平面上的形状，又有苜蓿叶形、菱形、环形、喇叭形立交桥等类型', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011476, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/83.jpg', '路中心黄色双实线指示可以暂时跨越超车。', '中心黄色双实线表示严格禁止车辆跨线超车或压线行驶,属禁止标线。用以划分上下行方向各有两条或两条以上机动车道而没有设置中央分隔带的道路。除交叉路口或允许车辆左转变（或回转）路段外，均应连续设置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011477, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/84.jpg', '右侧标志表示车辆按箭头示意方向选择行驶车道。', '图示为分向行驶车道标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011478, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/85.jpg', '右侧标志提醒前方路面不平。', '路面不平：用以提醒车辆驾驶人减速慢行。设在路面颠簸路段或桥头跳车较严重的地点以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011479, '靠路右侧转小弯', '靠弯路中心转弯', '借对向车道转弯', '靠路右侧转大弯', '4', 'http://images.juheapi.com/jztk/subject4/86.jpg', '在这种条件的道路上怎样安全行驶？', '由图可知，前方是向左急转弯道路，首先应当靠右侧行驶，靠左转的是小弯，靠右转的是大弯，因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011480, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/87.jpg', '路面同向车行道分界线指示不允许跨越超车。', '实线不能跨越，故不能超越。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011481, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/88.jpg', '如下图，左转弯车辆不可以直接进入左转弯待转区，等待放行信号。', '直行的信号灯的意思就是你可以进入左转待转区域了，然后左转的灯变成绿灯就是你可以左转了，这样做也是为了提升道路通行效率。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011482, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/89.jpg', '道路右侧车行道边缘白色虚线指示允许跨越。', '如果是需要通过路口右转弯，是允许被跨越的，但是如果直行就不允许越线占非机动车道行驶。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011483, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/90.jpg', '右侧标志表示前方路口只能车辆向左转弯。', '向左转弯-表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011484, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/91.jpg', '右侧标志指示高速公路紧急电话的位置。', '此标志的意思是紧急电话号码。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011485, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/92.jpg', '右侧标志表示前方路口要停车让行。', '停车让行-表示车辆必须在停止线以外停车了望，确认安全后，才准许通行。停车让行标志在下列情况下设置：(1)与交通流量较大的干路平交的支路路口；(2)无人看守的铁路道口；(3)其他需要设置的地方。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011486, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/93.jpg', '该车道路面导向箭头提示前方道路需向左合流。', '此题无需解释，跟着箭头走就是向左合流。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011487, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/94.jpg', '图中标志提醒障碍物在路中，车辆从两侧绕行。', '左右绕行-用以告示前方道路有障碍物，车辆应按标志指示减速慢行，绕行通过。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011488, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/95.jpg', '路面白色虚实线指示变道或靠边停车时允许跨越。', '车行道边缘白色虚实线的虚线侧允许车辆越线行驶，实线侧不允许车辆越线行驶，用以规范车辆行驶轨迹。在必要的地点，如公交车站临近路段、允许路边停车路段等，可设置车行道边缘白色虚实线。跨线行驶的车辆，应避让其他正常行驶的车辆、非机动车辆和行人。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011489, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/96.jpg', '左侧标志警示前方道路右侧不能通行。', '这是靠右侧行驶标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011490, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/97.jpg', '右侧标志表示会车时对向车辆先行。', '此图是会车先行的意思，记住哥一句话，谁粗谁先行。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011491, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/98.jpg', '驾驶机动车在这种情况下不能超车。', '有图可猜测前方是铁路道口，而根据《中华人民共和国道路交通安全法实施条例》规定，在铁路道口是不能超车的，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011492, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/99.jpg', '前方标志预告高速公路终点距离信息。', '标志的意思是告之道路前方要经过的重要的城镇的地名和距离。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011493, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/100.jpg', '右侧标志指示距离前方加油站入口200米。', '没问题，记住。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011494, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/101.jpg', '这个标志表示前方车道数量增加。', '图中是车道数变少标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011495, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/102.jpg', '右侧标志提醒前方道路正在施工。', '施工标志-用以告示前方道路施工，车辆应减速慢行或绕道行驶。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011496, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/103.jpg', '右侧标志表示最高车速不准超过每小时50公里。', '此图的标志为最低车速不低于50公里/每小时。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011497, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/104.jpg', '右侧标志提醒前方路段保持车距。', '事故易发路段-此标志设在交通事故易发路段以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011498, '及时减速停车让行', '鸣喇叭示意其让道', '加速从行人前通过', '开前照灯示意其让道', '1', 'http://images.juheapi.com/jztk/subject4/105.jpg', '驾驶机动车在路口遇到这种情况的行人怎么办？', '虽然横穿道路的行人算乱穿，但面对这样的情况驾驶员依旧要减速慢行，必要时停车避让，因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011499, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/106.jpg', '前方标志告知向右100米为室内停车场。', '你看那个P字上边还有个遮挡，说明是室内的，如果没有上边那个遮挡就是露天停车场。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011500, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/107.jpg', '该车道路面导向箭头指示在前方路口仅可右转弯。', '图中的标志表示可以直行或者右转弯。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011501, '机动车占对向道行驶', '在弯道内急转转向盘', '在驶入弯道前不减速', '机动车靠路右侧行驶', '13', '', '机动车行驶至转弯路段时，易引发事故的驾驶行为有什么？', '选项A容易与对向来车发生擦碰，选项BC容易造成侧滑，严重的话会侧翻，因此本题选ABC。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432414011502, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/109.jpg', '驾驶机动车在这种信号灯亮的路口，可以右转弯。', '没有问题，只要安全的情况下就可以右转，前边的红灯是管直行的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011503, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/110.jpg', '右侧标志提醒前方经常有牲畜横穿、出入。', '注意野生动物-用以提醒车辆驾驶人注意慢行。设在经过野生动物保护区的公路上，经常有野生动物横穿、出入的地点前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011504, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/111.jpg', '前方标志指示路右侧是高速公路临时停车处。', '图示为紧急停车道。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011505, 'T型交叉路口', 'Y型交叉路口', '十字交叉路口', '环行交叉路口', '4', 'http://images.juheapi.com/jztk/subject4/112.jpg', '右侧标志警示前方是什么路口？', '交叉路口-用以警告车辆驾驶人谨慎慢行，注意横向来车。设在平面交叉路口驶入路段的适当位置。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011506, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/113.jpg', '道路右侧白色虚线指示可越线变更车道。', '试题说的是可越线变更车道并没说右侧超车，白色虚线是可以变更车道的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011507, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/114.jpg', '路中黄色虚线指示任何情况都不允许越线绕行。', '黄色虚线是中心线，可以跨越；黄色单实线就已经不能跨越了；黄色双实线是严格禁止压线或者跨越', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011508, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/115.jpg', '驾驶机动车进入居民小区不能超过限速标志限定的速度行驶。', '由图可知，进入此小区车速限制在5公里/小时，也就是说不能超过5公里/小时，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011509, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/116.jpg', '右侧标志告知右前方100米是应急避难场所。', '此适标志用于突发公共事件状态下，供居民紧急疏散、安置的应急避难场所的标志设置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011510, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/117.jpg', '右侧标志警告距前方有人看守铁路道口150米。', '此标志为前方100米处无人看守铁道路口。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011511, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/118.jpg', '右侧标志表示鸣喇叭提醒。', '右侧标志是人行横道的意思。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011512, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/119.jpg', '驾驶机动车看到这个标志时要及时减速。', '如图前方路段要注意儿童，因此为了避免有儿童突然穿出，要减速慢行，必要时停车避让，所以本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011513, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/120.jpg', '允许沿着图中箭头方向驶入高速公路行车道。', '不能跨越左边的白色实线。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011514, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/121.jpg', '路中黄色斜线填充标记警告前方有固定性障碍物。', '用以指示路面有固定性障碍物，警告车辆驾驶人谨慎行车，引导交通流顺畅驶离障碍物区域。接近障碍物标线的颜色，应根据障碍物所在的位置，与对向车行道分界线或同向车行道分界线的颜色一致。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011515, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/122.jpg', '右侧标志警告前方路段要注意儿童。', '不要被标志下边的学校误导，题目问的是标志的意思，注意儿童-用以警告车辆驾驶人减速慢行，注意儿童。设在小学、幼儿园、少年宫等儿童经常出入地点前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011516, '沿直行车道左转', '进入左转弯待转区', '进入直行等待区', '沿左车道左转弯', '2', 'http://images.juheapi.com/jztk/subject4/123.jpg', '驾驶机动车在这个路口怎样左转弯行驶？', '路口施划了左转弯待转区，因此左转弯时应当先进入左转弯待转区后再左转，而且此时是直行车道的绿灯信号。因此应当在左转弯待转区等待，所以本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011517, '禁止车辆在两侧车道通行', '减速进入两侧车道行驶', '进入右侧车道行驶', '加速进入两侧车道行驶', '1', 'http://images.juheapi.com/jztk/subject4/124.jpg', '遇到这种情况时怎样行驶？', '俩侧车道都是红灯，都不可以通行，所以是禁止车辆在两侧车道通行。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011518, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/125.jpg', '右侧标志提示一切车辆都不能驶入。', '禁止驶入-表示禁止车辆驶入。此标志设在禁止驶入的路段入口处。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011519, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/126.jpg', '右侧标志提示前方200米是车距确认路段。', '白色折线车距确认线：白色折线车距确认线总宽300cm,线条宽40cm或45cm，从确认基点0m开始，每个5米设置一道，两道为一组，间隔50米,重复设置五组，也可根据现场设置多组。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011520, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/127.jpg', '在这个区域内不允许长时间停放机动车。', '如图是禁止长时间停车标志，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011521, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/128.jpg', '在学校门口遇到这种情况要做好随时停车的准备。', '如图是前方路段是学校要注意儿童。因此此时要减速慢行，必要时停车。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011522, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/129.jpg', '右侧标志指示前方设有避让来车的处所。', '这个不常见，看见这题选对就好！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011523, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/130.jpg', '这个标志指示高速公路交通广播和无线电视频道。', '图示为雷达测速监控。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011524, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/131.jpg', '如下图，左转弯车辆可直接进入左转弯待转区，等待放行信号。', '直行的信号灯是红色是禁止进入左转待转区域的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011525, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/132.jpg', '右侧标志提醒前方路交口向右100米是渡口。', '渡口：用以提醒车辆驾驶人谨慎驾驶。设在车辆渡口以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011526, '迅速加速行驶', '紧跟前车行驶', '靠右加速行驶', '靠右减速让行', '4', 'http://images.juheapi.com/jztk/subject4/133.jpg', '驾驶机动车遇到这种情况怎样礼让？', '只有D最和谐，快选它吧。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011527, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/134.jpg', '这个标志表示前方路段不允许超车。', '解除禁止超车-表示禁止超车路段结束。此标志设在禁止超车的终点。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011528, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/135.jpg', '右侧标志警告前方是向右急转弯路。', '向右急弯路-用以警告车辆驾驶人减速慢行。设置位置为曲线起点的外面，但不应进入相邻的圆曲线内。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011529, '正确', '错误', '', '', '2', '', '机动车遇有急弯路时要在进入弯路后减速。', '遇到急弯路应当提前减速慢行，等到进入弯路后减速很容易发生侧滑或侧翻。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011530, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/137.jpg', '路面上的出口标线用于引导驶出该高速公路。', '高速公路出入口标线为白色虚线，主要用于高速公路和其他采用立体交叉道路。该标线作用为，高速公路出入口横向标线或三角地带标线是为驶入或驶出匝道车辆提供安全交汇引导，减少与突出部缘石碰撞。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011531, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/138.jpg', '右侧标志警示前方道路两侧不能通行。', '这是两侧通行标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011532, '正确', '错误', '', '', '2', '', '机动车倒车时，后方道路条件较好的，应加速倒车，迅速完成操作。', '《中华人民共和国道路交通安全法实施条例》第五十条  机动车倒车时，应当察明车后情况，确认安全后倒车。不得在铁路道口、交叉路口、单行路、桥梁、急弯、陡坡或者隧道中倒车。因此本题错误。另：施主别那么迅速啊， 安全第一！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011533, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/140.jpg', '红色圆圈内标志表示左侧道路只供小型车行驶。', '红色圆圈内标志是机动车行驶车道。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011534, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/141.jpg', '右侧标志表示前方是分流路口。', '图中是车道数增加标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011535, '货车超载', '货车违法超车', '客车超员', '客车驾驶人疲劳驾驶', '13', '', '石某驾驶低速载货机动车，运载4.05吨货物（核载1.2吨），行驶至宁津县境内314省道51公里加260米处，在越过道路中心线超越前方同向行驶的机动车时，与对向正常行驶的中型客车（乘载12人，核载11人）正面相撞，造成10人死亡、2人受伤。此事故中的违法行为是什么？', '易错题，超员超载都清楚，但是违规超车得留点心，意思是货车师傅在超车时发现对向有驶来的车时，应该立即停止超车。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432414011536, '正确', '错误', '', '', '2', '', '夜间驾驶机动车在照明条件良好的路段可以不使用灯光。', '用近光灯不会照得对方看不见路，同时为了安全起见，开启近光灯能够让相互间清晰可见。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011537, '正确', '错误', '', '', '1', '', '安装防抱死制动装置（ABS）的机动车紧急制动时，可用力踏制动踏板。', 'ABS技术的出现，就是大大地降低了侧滑，和原地掉头的现象，保证了行驶的安全。如果，你在使用带ABS的车辆，在刹车时的动作，类似不带ABS的车这样轻轻的踩下去，很可能会不能触发ABS系统，也就起不到ABS的作用了，所以，一般就是要求，刹车动作要果断，一脚就踩到底，保证ABS的正确执行。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011538, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/145.jpg', '路面中心黄虚线指示在保证安全的情况下可以越线超车。', '黄色虚线是中心线，可以跨越；黄色单实线就已经不能跨越了；黄色双实线是严格禁止压线或者跨越', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011539, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/146.jpg', '驾驶机动车在这种情况下要加速冲过坡顶。', '根据图来看，只能知道前方是一个制高点，但对于前方是平路还是下坡路并不清楚，因此不能加速通过。所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011540, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/147.jpg', '右侧标志表示只能车辆向右转弯。', '向右转弯-表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011541, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/148.jpg', '路面上菱形标识预告前方道路设置人行横道。', '道路路面上的白色菱形图案，是中华人民共和国国家标准（GB5768/1999）《道路交通标志和标线》中新设置的一种交通标线，正式名称为人行横道预告标线。这种标线在到达人行横道前的道路30-50米处设置，它的作用是用来提示驾驶员，前方已接近人行横道，应减速慢行，并须注意行人横过马路', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011542, '沿直行车道右转弯', '停止线前停车等待', '沿右侧道路右转弯', '借非机动车道右转', '3', 'http://images.juheapi.com/jztk/subject4/149.jpg', '驾驶机动车在这个路口怎样右转弯行驶？', '车辆右转弯不受此红灯信号限制，因此只要沿右侧道路右转弯即可。所以本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011543, '靠路中心行驶', '靠路右侧行驶', '在路中间行驶', '靠路左侧行驶', '2', 'http://images.juheapi.com/jztk/subject4/150.jpg', '驾驶机动车在这种道路上怎样会车最安全？', '《中华人民共和国道路交通安全法实施条例》第四十八条  在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（一）减速靠右行驶，并与其他车辆、行人保持必要的安全距离；因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011544, '停车观察路口情况', '加速尽快进入路口', '减速观察左后方情况', '减速缓慢进入路口', '4', 'http://images.juheapi.com/jztk/subject4/151.jpg', '驾驶机动车在有这种标志的路口怎样通过最安全？', '如图交通标志是减速让行标志，因此在此路段要减速慢行，应当注意的还有前方情况，因此本题选D，不能选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011545, '选择下坡路段停放', '选择上坡路段停放', '选择平缓路段停放', '选择坡顶位置停放', '3', '', '驾驶机动车在山区道路因故障停车需要注意什么？', '停车时要尽可能选择安全的地点停车，而不管是上坡路、下坡路或是坡顶都有可能让所停车辆发生溜车，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011546, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/153.jpg', '路缘石的黄色实线指示路边允许临时停、放车辆。', '黄色虚线是允许临时，全黄的就禁止了。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011547, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/154.jpg', '隧道上方标志表示限制高度3.5米。', '表示禁止装载高度超过标志所示数值的车辆通行。此标志设在最大允许高度受限制的地方。 以图为例：装载高度不得超过3.5米。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011548, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/155.jpg', '路口内人行横道线警示行人优先横过道路。', '请记住，无论任何时候都是行人优先。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432414011549, '直接进入路口内等待', '在路口停止线外等待', '从右侧非机动车道通过', '借对向车道通过路口', '2', 'http://images.juheapi.com/jztk/subject4/156.jpg', '驾驶机动车在交叉路口遇到这种情况如何对待？', '虽然前边是绿灯，但是前边已经堵到路口，你在过去直接把横向的车道也给堵了，还是和谐一点在路口停止线外等待吧。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432414011550, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/157.jpg', '路中心黄色实虚线指示允许超车时越过。', '无论单黄线还是双黄线，只要是实线，就严禁跨越的，比如超车、转弯、掉头等。行车时没有特别情况就不应该越线。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925954, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/158.jpg', '发生该事故的主要原因是驾驶人倒车前没有进行安全确认。', '倒车没多久就撞到了后方的障碍物，这说明驾驶员在到车前没有了解清楚后方的情况。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925955, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/159.jpg', '驾驶机动车在这种道路上从前车右侧超越最安全。', '超车要从左侧超车，根据如图的情况来看，还是放弃超车比较好。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925956, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/160.jpg', '右侧标志警告距前方有人看守铁路道口100米。', '此标志为前方50米处无人看守铁道路口。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925957, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/161.jpg', '在这个区域内可以临时停车。', '如图是禁止临时或长时停车标志，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925958, '正确', '错误', '', '', '1', '', '驾驶机动车遇到校车在道路右侧停车上下学生，同向有两条机动车道时，左侧车道后方机动车应当停车等待。', '校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越。校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925959, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/163.jpg', '驾驶机动车通过这段山区道路要靠路中心行驶。', '根据如图交通标志来看，前方道路是连续急弯路，因此要靠右侧行驶，以免前方弯道突然出现来车。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925960, '正确', '错误', '', '', '1', '', '机动车涉水后，驾驶人要间断轻踩制动踏板，以恢复制动效能。', '通过积水路面时，会进水，若持续轻踏制动踏板，磨擦后会产生污物，粘附在轮鼓内后会影响制动效果。 因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925961, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/165.jpg', '这个标志提示该路段已实行交通监控。', '交通监控设备标志属于指路标志，蓝底、白图形、白边框、蓝色衬边。用于告知机动车驾驶人该区域设置有固定式交通监控设备。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925962, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/166.jpg', '驾驶机动车在这种道路上超车可借对向车道行驶。', '黄色单实线是不能越线超车的，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925963, '行人从车后穿出', '行人从车前穿出', '公交车突然倒车', '公交车突然起步', '2', 'http://images.juheapi.com/jztk/subject4/167.jpg', '驾驶机动车在这种情况下注意什么？', '由图可知，公交车前方有一名撑伞的女士，因此要考虑到可能这名女士会横穿，所以本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476925964, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/168.jpg', '高速公路上的白色折线为行车中判断行车速度提供参考。', '白色折线车距确认线：白色折线车距确认线总宽300cm,线条宽40cm或45cm，从确认基点0m开始，每个5米设置一道，两道为一组，间隔50米,重复设置五组，也可根据现场设置多组。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925965, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/169.jpg', '交通警察发出这种手势信号时要减速慢行。', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925966, '换入空档，滑行通过', '一停、二看、三通过', '加速、观察、快通过', '减速、观察、慢通过', '2', 'http://images.juheapi.com/jztk/subject4/170.jpg', '驾驶机动车怎样通过这个铁路道口？', '由图可知，前方是既没有交通信号灯也没有管理人员看守的铁路道口，在这样的情况下若想要通过，就要一停二看三通过，以确保安全。因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476925967, '醉酒驾驶', '超速驾驶', '疲劳驾驶', '肇事逃逸', '14', '', '林某驾车以110公里/小时的速度在城市道路行驶，与一辆机动车追尾后弃车逃离被群众拦下。经鉴定，事发时林某血液中的酒精浓度为135.8毫克/百毫升。林某的主要违法行为是什么？', '违法行为一：以110公里/小时的速度在城市道路行驶违法行为二：与一辆机动车追尾后弃车逃离违法行为三：林某血液中的酒精浓度为135.8毫克/百毫升因此本题选ABD。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476925968, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/172.jpg', '路面白色虚线实线指示实线一侧允许跨越。', '虚线一侧可以越线，您所在的这个车道是不可以的，因为是实线。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925969, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/173.jpg', '驾驶机动车可在该路口处向右变更车道。', '因为已经到路口了，路口150米内不得变更车道。他停在左侧道，只能直行或左转。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925970, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/174.jpg', '前方标志预告交叉路口通往方向的信息。', '此为交叉路口预告标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925971, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/175.jpg', '驾驶机动车在这个路段允许超车。', '如图是禁止超车标志，因此不能超车，所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925972, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/176.jpg', '驾驶机动车遇到这种情况要主动减速让后车超越。', '由图可知，后车有超车意图，根据本车的情况，应当予以让行，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925973, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/177.jpg', '路缘石上的黄色虚线指示路边不允许停车上下人员或装卸货物。', '应该是允许短时停车和卸货。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925974, '紧随前车之后', '加大安全距离', '减小纵向间距', '尽快超越前车', '2', '', '驾驶机动车在山区道路怎样跟车行驶？', '山区道路复杂多变，因此跟车时也要非常注意，因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476925976, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/180.jpg', '因故障在山区上坡路段长时间停车时，要用这种办法塞住车轮。', '停在如图的上坡路面上，容易后溜，如图做法并不能防止车辆后溜，正确的做法是在轮胎后面塞住。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925977, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/181.jpg', '左侧标志提示前方收费口停车领卡。', '这个没问题，图片显示的很清楚，有个手拿卡，简直就是看图说话。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925978, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/182.jpg', '驾驶机动车遇到这种情况要迅速向左变更车道。', '由图可知，左侧后方有来车，所以为了避免发生碰撞及影响其他车辆正常通行，因此就算要变更车道也应该等后车通过后再变更。正确的做法是，提前开启转向灯，确保左侧车道后方没有车辆临近时向左变更车道。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925979, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/183.jpg', '该车道路面导向箭头指示前方道路仅可左右转弯。', '嗯，看路上的标志，是仅可以左转右转的意思。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925980, '连续鸣喇叭', '快速通过', '减速慢行', '从一侧绕行', '3', 'http://images.juheapi.com/jztk/subject4/184.jpg', '驾驶机动车遇到这样的情景怎样行驶？', '遇到这样的情况要减速慢行，必要时停车避让，以免孩子们突然跑动被撞伤，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476925981, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/185.jpg', '右侧标志警告前方道路是向左连续弯路。', '向左急弯路-用以警告车辆驾驶人减速慢行。设置位置为曲线起点的外面，但不应进入相邻的圆曲线内。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925982, '超速行驶', '不按规定会车', '疲劳驾驶', '不按规定使用灯光', '14', '', '周某夜间驾驶大货车在没有路灯的城市道路上以90公里/小时的速度行驶，一直开启远光灯，在通过一窄路时，因加速抢道，导致对面驶来的一辆小客车撞上右侧护栏。周某的主要违法行为是什么？', '第一，夜间没有路灯的情况下90公里/小时，这个肯定是是超速啊，高速上这个速度还可以，没有路灯就危险了。第二，通过窄路时，应该会车减速行驶。第三，应该交替使用近远光灯。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476925983, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/187.jpg', '右侧标志表示不允许长时鸣喇叭。', '禁止鸣喇叭-表示禁止鸣喇叭。此标志设在需要禁止鸣喇叭的地方。禁止鸣喇叭的时间和范围可用辅助标志说明。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925984, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/188.jpg', '前方标志告知各个路口出口方向的信息。', '前方标志告知各个路口出口方向的信息，此题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925985, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/189.jpg', '看到这种手势信号时可以向左转弯。', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925986, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/190.jpg', '右侧标志警告前方是连续下坡路段。', '下陡坡-用以提醒车辆驾驶人小心驾驶。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925987, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/191.jpg', '驾驶机动车在该位置不能变更车道。', '由图可知，前方是实线区，实线区不得变更车道，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925988, '从前车两侧超越', '紧跟前车后停车', '保持大距离停车', '连续鸣喇叭提示', '3', '', '山区上坡路段跟车过程中遇前车停车时怎么办？', '上坡路段停车的车辆容易出现后溜的情况，因此后车在跟车时要加大跟车距离，确保安全，所以本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476925989, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/193.jpg', '前方标志预告距离高速公路入口1公里。', '没问题，记住吧。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925990, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/194.jpg', '右侧标志表示此处不准鸣喇叭。', '右侧标志是表示此处要鸣喇叭的意思。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925991, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/195.jpg', '驾驶机动车向左变更车道遇到这种情况要注意让行。', '如图情况时，车辆若是想要向左变更车道，就应当让左侧车辆通过后再变道，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925992, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/196.jpg', '右侧标志指示前方收费站设有电子不停车收费行驶车道。', 'ETC车道就是电子不停车收费行驶车道。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925993, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/197.jpg', '右侧标志提示距离设有电子不停车收费车道的收费站1公里。', '标志的意思是人工收费站，不是电子不停车收费车道。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925994, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/198.jpg', '交通警察发出这种手势可以向左转弯。', '警察的手势不是给你看的，没看人家没看你，看的是右侧。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925995, '正确', '错误', '', '', '2', '', '预计在超车过程中与对面来车有会车可能时，应提前加速超越。', '预计在超车过程中与对面来车有会车可能时，应当放弃超车。若加速超越，有可能会引起不必要的事故。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925996, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/200.jpg', '路面标记指示这段道路上最高限速为50公里/小时。', '限速标志为50公里/小时，所以此题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925997, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/201.jpg', '路右侧白色矩形虚线框内表示允许长时间停车。', '虚线短时停车，实线长时停车。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925998, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/202.jpg', '驾驶机动车在学校附近遇到这种情况要尽快加速通过。', '遇到这种情况，要预防有学生在道路上跑动等行为，且前方有人行横道线，因此要减速慢行，必要时停车，所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476925999, '经左弯待转区进行掉头', '在路口虚线处进行掉头', '左转信号灯亮时方可掉头', '直行信号灯亮时方可掉头', '2', 'http://images.juheapi.com/jztk/subject4/203.jpg', '驾驶机动车在前方路口怎样掉头？', '在左转待转区是要左转的车辆，与掉头车辆无关。选项C错在方可二字，因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926000, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/204.jpg', '前方标志告知前方道路各行其道的信息。', '不是各行其道，是各方向终点信息。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926001, '靠中心线行驶', '开前照灯行驶', '向路右侧避让', '向车左侧避让', '3', 'http://images.juheapi.com/jztk/subject4/205.jpg', '在这种情况下怎样会车最安全？', '遇到前方车辆越线占道的，应当靠右侧行驶，避免发生碰撞，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926002, '从前车左侧超越', '占对向车道超越', '从前车右侧超越', '跟在前车后行驶', '4', 'http://images.juheapi.com/jztk/subject4/206.jpg', '驾驶机动车在这种情况下怎样做？', '遇到这种情况时，不能超车，因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926003, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/207.jpg', '右侧标志表示前方路口不准车辆右转。', '禁止向右转弯-表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926004, '易发生侧滑', '能见度低', '行驶阻力大', '视野变宽', '2', '', '雾天对安全行车的主要影响是什么？', '雾天的能见度很低，容易造成视野不良。而易发生侧滑的应该是滑溜的路面，行驶阻力大这一点更无从得知。因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926005, '开远光灯', '交替使用远近光灯', '开近光灯', '开危险报警闪光灯', '2', 'http://images.juheapi.com/jztk/subject4/209.jpg', '夜间驾驶机动车遇到这种情况怎样超车？', '《中华人民共和国道路交通安全法实施条例》第四十七条  机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926006, '正确', '错误', '', '', '2', '', '夜间驾驶汽车在急弯道超车时要不断变换远、近光灯。', '急转弯道你还超车，老实在后边跟着吧。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926007, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/211.jpg', '驾驶机动车在对向没有来车的情况下可以超车。', '如图所示，前方道路是连续急弯路，在这样的路段是不能超车的。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926008, '挂入空挡滑行', '踏下离合器滑行', '利用发动机制动', '持续踏制动踏板', '3', '', '下长坡控制车速最安全的方法是什么？', '动机制动有三个显著特点：一是由于差速器的作用，可将制动力矩平均地分配在左右车轮上，减少侧滑、甩尾的可能性；二是有效地减少脚制动的使用频率，避免因长时间使用制动器，导致制动器摩擦片的温度升高，使制动力下降，甚至失去作用；三是车速始终被限定在一定范围内，有利于及时降速或停车，确保行车安全。因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926009, '尽快加速超越前车', '主动减速放弃超车', '鸣喇叭让前车让路', '开前照灯让对向让速', '2', 'http://images.juheapi.com/jztk/subject4/213.jpg', '驾驶机动车遇到这种情况怎样做最安全？', '本来在这样的道路上是可以超车的，但由于对向有来车，因此避免发生擦碰，要放弃超车。因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926010, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/214.jpg', '驾驶机动车在这种情况下临时停车后，为避免机动车后溜可将转向盘向左转。', '这样做，与在轮胎后方塞东西是同样的道理，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926011, '疲劳驾驶', '酒后驾驶', '客车超员', '超速行驶', '3', '', '徐某驾驶一辆中型客车（乘载27人）行至四都镇前岭村壶南头路段，在上坡过程中，机动车发生后溜驶出路外坠入落差约80米的山崖，造成11人死亡、7人受伤。徐某的主要违法行为是什么？', '中型客车核载10人以上19人以下', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926012, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/216.jpg', '左侧标志警告前方注意左侧路口有汇入车辆。', '请记住。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926013, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/217.jpg', '驾驶机动车遇到这种情况时对向机动车优先通过。', '根据规定，有障碍的一方要让无障碍的一方先行，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926014, '安装防滑装置', '提高车速行驶', '降低车速行驶', '断续使用制动', '3', '', '大雨天在高速公路行车时，怎样避免发生“水滑”现象？', '水滑就是因为雨天雨水多了，使轮胎与路面的摩擦系数变小了，就容易侧滑。因此降低车速就能减少水滑发生的可能，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926015, '保持正常车速行驶', '高速行驶避开灯光', '减速或停车让行', '开启远光灯对射', '3', 'http://images.juheapi.com/jztk/subject4/219.jpg', '夜间驾驶机动车遇到这种情况时怎样处理？', '由图可知，对向来车开着远光灯，此时不要意气用事，而是要为了安全考虑，要减速或停车让行，避免会车时发生意外。因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926016, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/220.jpg', '驾驶机动车在这种情况下可以越过中心实线超车。', '中心实线不能越线超车，而且在前方有障碍物的情况，应当避免超车。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926017, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/221.jpg', '交通警察发出这种手势信号可以左转弯。', '图为左转弯待转信号。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926018, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/222.jpg', '驾驶机动车通过这种傍山险路要靠右侧行驶。', '在这种道路上行驶要靠右行驶，以策安全，同时以免对向有来车无法及时避开。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926019, '及时减速让行，必要时靠边停车', '开启远光灯，迫使来车变换灯光', '视线向右平移，防止眩目', '交替变换远近光灯，提醒来车', '15', '', '夜间会车时，对面来车不关闭远光灯怎么办？', '变身圣人吧（PS：您就交替变换远近光灯，闪死丫的）', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926020, '正确', '错误', '', '', '1', '', '机动车在路边起步后，应随时注意机动车两侧道路情况，向左缓慢转向，逐渐驶入正常行驶道路。', '一看二慢三汇入，这样做既出于安全考虑，同时也不影响其他车辆的正常行驶。因此本题选A。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926021, '正确', '错误', '', '', '1', '', '涉水驾驶要保持车速均匀有足够动力，避免停留。', '若是动力不够，很有可能在水中停下来，停下来之后很容易熄火。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926022, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/226.jpg', '驾驶机动车在山区道路遇到这种情况要加速超越前车。', '在如图的山区道路上行驶，遇到前方出现这样的情况，不能超车，因为前方有弯道，而且受前车影响无法清楚掌握路况，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926023, '靠左侧减速让行', '靠右侧减速让行', '加速靠左侧让行', '保持原行驶路线', '1', 'http://images.juheapi.com/jztk/subject4/227.jpg', '驾驶机动车遇到这种特殊情况怎样行驶？', '由于超车的是执行公务的警车，因此要减速慢行。另外，由于警车是右侧穿出的，因此只能靠左侧避让。所以本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926024, '开启远光灯', '开启前后雾灯', '只能开启左转向灯', '长时间鸣喇叭', '2', 'http://images.juheapi.com/jztk/subject4/228.jpg', '在这种气象条件下起步要注意哪些方面？', '前面都有雾了，那不开雾灯开什么灯？', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926025, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/229.jpg', '交通警察发出的是禁止通行手势信号。', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926026, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/230.jpg', '路口内中心圈标示左小转弯要沿内侧行驶。', '亲，这都是规定的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926027, '妨碍安全驾驶', '可提高注意力', '可缓解驾驶疲劳', '不影响驾驶操作', '1', '', '驾驶人边驾车边吸烟的做法有什么影响？', '这样做不仅妨碍驾驶，也会分散驾驶人的注意，另外缓解疲劳没有根据，因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926028, '服务区', '加速车道', '减速车道', '匝道', '1', '', '在高速公路行车选择什么地方停车？', '驾驶员在高速公路上不能随意靠边停车，而是要停靠在紧急停车带里面，不是应急车道，没有紧急情况占用应急车道是违反高速公路管理规定的，所以要停车就应把车停在紧急停车带或服务区休息。即本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926029, '迅速从车左侧超越', '保持较大跟车距离', '连续鸣喇叭告知', '迅速从车右侧超越', '2', 'http://images.juheapi.com/jztk/subject4/233.jpg', '驾驶机动车遇到这种情形怎么办？', '由图可知，前方车辆从左侧超越后有一个曲线行驶的过程，遇到这种情况应当保持较大的距离，以策安全。因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926030, '正确', '错误', '', '', '1', '', '出车前检查冷却液、发动机机油、燃油等是否有渗漏现象。', '确保出行前一定都OK。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926031, '应当在规定地点停放', '禁止在人行道上停放', '在道路上临时停车时，不得妨碍其他机动车和行人通行', '可以停放在非机动车道上', '4', '', '机动车停车的错误做法是什么？', '注意本题考的是错误的做法，机动车不能停放在非机动车道上，因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926032, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/236.jpg', '交通警察发出这种手势信号时可以直行通过。', '准许右方的车辆右转弯面向一方代表告知，伸手拦截告知的一方就不能直行，右手斜下告知可以右转。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926033, '正确', '错误', '', '', '2', '', '在雪天临时停车要开启前照灯和雾灯。', '雪天在道路上临时停车，应当开启危险报警闪光灯，用以提醒其他车辆注意。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926034, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/238.jpg', '会车遇到这种情况要低速会车或停车让行。', '由图可知，前方是窄桥，要避免在窄桥上会车，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926035, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/239.jpg', '驾驶机动车遇到这种情形要迅速靠右侧减速让行。', '由图可知，前方车辆为执行公务的消防车，因此遇到这种情况要减速靠右慢行进行避让。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926036, '正确', '错误', '', '', '1', '', '扑救易散发腐蚀性蒸气或有毒气体的火灾时，扑救人员应穿戴防毒面具和相应的防护用品，站在上风处施救。', '由于是“易散发腐蚀性蒸气或有毒气体的火灾”，因此既要做到不中毒，也要做到不被烫伤等，因此在扑救时，要穿戴防毒面具和相应的防护用品，这是为了防止中毒，而站在上风处施救，是因为不会因火势被伤到。因此本题是对的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926037, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/241.jpg', '右侧标志指示前方是T型路口。', '应该是此路不通。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926038, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/242.jpg', '路右侧黄色矩形标线框内表示允许临时停车。', '这么长的简易停车应该是公交车专用停车下客上车的，所以小车不能去停。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926039, '正确', '错误', '', '', '2', '', '驾驶机动车在冰雪路面发生侧滑时，要猛打方向调整。', '此时若是转向调整，容易造成更大的状况，因此正确的做法应该是应降低车速，利用发动机制动进行减速。所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926040, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/244.jpg', '交通警察发出的是左转弯待转手势信号。', '减速慢行的手势是右手，此图是左转弯待转手势信号。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926041, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/245.jpg', '驾驶机动车遇到这种道路，可充分利用空挡滑行。', '根据如图的交通标志来看，是一个连续下坡路，在这种路况下不能空挡滑行，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926042, '正确', '错误', '', '', '1', '', '机动车在高速公路意外撞击护栏时，要稳住方向，适当修正，切忌猛转转向盘。', '此时猛转转向盘很有可能造成车子侧滑或侧翻，造成更大的损伤。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926043, '尽快加速超越前车', '鸣喇叭让前车让路', '保持距离跟车行驶', '从前车的右侧超越', '3', 'http://images.juheapi.com/jztk/subject4/247.jpg', '驾驶机动车在这种道路上怎样行驶最安全？', '在这样的交通状况下，不能超车，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926045, '空挡滑行通过', '保持高速通过', '适当加速通过', '低速缓慢通过', '4', 'http://images.juheapi.com/jztk/subject4/249.jpg', '驾驶机动车在这样的路面如何安全行驶？', '由图中交通标志可知前方是路面不平的道路，因此要减速慢行，避免颠簸等，所以本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926046, '观察', '包扎', '止血', '询问', '3', '', '抢救失血伤员时，要先采取什么措施？', '既然是失血伤员，首先要做的当然是止血。因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926047, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/251.jpg', '驾驶机动车遇到这种道路要提前减速减挡，利用发动机制动控制速度。', '看右侧的标志，此处为下陡坡，故要减速慢行，但是不能空挡滑行所以要利用发动机制动控制速度。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926048, '开启前照灯、示廓灯和后位灯', '高频率鸣喇叭使其他交通参与者知道自己的位置', '同向跟车较近时，应使用远光灯', '适当提高车速，尽快到达目的地，结束行车', '1', '', '驾驶机动车遇到沙尘、冰雹、雾、雨、雪等低能见度条件时，应该怎样做？', '', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926049, '靠边减速或停车让行', '占用其他车道行驶', '加速变更车道避让', '在原车道内继续行驶', '1', '', '行车中遇抢救伤员的救护车从本车道逆向驶来时，要怎样做？', '遇到正在执行任务的特种车辆，应当予以让行。而本题的救护车正是因为抢救伤员而逆行，因此其他车辆要给予方便，为其让行。因此正确的做法是靠边减速或停车让行。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926050, '开启远光灯', '只能开启左转向灯', '开启左转向灯、近光灯', '开启危险报警闪光灯', '3', 'http://images.juheapi.com/jztk/subject4/254.jpg', '在这种环境中安全起步怎样使用灯光？', '夜间起步应开启近光灯，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926051, '客车超员', '客车超速行驶', '货车超载', '货车驾驶人经验不足', '10', '', '戚某驾驶大客车，乘载28人（核载55人），由南向北行至一无交通信号控制的交叉路口，以50公里的时速与由东向西行至该路口李某驾驶的重型半挂牵引车（核载40吨，实载55.2吨）侧面相撞，造成12人死亡、17人受伤。此事故中的主要违法行为是什么？', '核载=就是设计的能乘载的人数或者吨数。理论值，标准值。乘载=就是实际乘载的人数，实际值。交叉路口，以时速50公里的速度是超速。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926052, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/256.jpg', '如图所示，遇有这种情况要停车让行。', '看到一个自行车前轮没，要让行。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926053, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/257.jpg', '道路右侧白色实线标示机动车道与人行道的分界线。', '这是机动车与非机动车的分界线，看旁边有非机动车标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926054, '正确', '错误', '', '', '1', '', '泥泞路对安全行车的影响是车轮极易空转和侧滑。', '因为泥泞路面很易滑，因此车轮容易空转或是侧滑，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926055, '减速让非机动车先行', '连续鸣喇叭告知让道', '从非机动车左侧绕过', '占对向车道加速超越', '1', 'http://images.juheapi.com/jztk/subject4/259.jpg', '驾驶机动车遇到这种情况怎样行驶？', '遇到这种情况时要减速，并让非机动车先通过，因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926057, '连续鸣喇叭提示', '开远光灯尾随行驶', '保持距离等待让行', '连续变换前照灯远、近光', '3', '', '夜间驾驶机动车超车遇前车不让路时怎样处置？', '遇到前车不让路时，只能等到前车让路了才能超车，否则容易发生危险，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926058, '在路口向左转弯', '停车等待', '在路口直行', '进入左弯待转区', '2', 'http://images.juheapi.com/jztk/subject4/262.jpg', '看到这种手势信号时怎样行驶？', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926059, '在最外侧行车道上停车', '在内侧行车道上停车', '在应急车道停车', '在匝道口三角地带停车', '3', '', '机动车在高速公路上发生故障需检查时怎样停车？', '高速路最右的道路就是紧急停车道，也就是应急车道。应急车道就是专门在车子发生故障或事故时停靠的。 因此选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926060, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/264.jpg', '路面菱形块虚线警告前方道路要减速慢行。', '车行道纵向减速标线为一组平行于车行道分界线的菱形块虚线。在车行道纵向减速标线的起始位置，设置30 m的渐变段，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926061, '正确', '错误', '', '', '2', '', '驾驶机动车在大雾天临时停车后，只开启雾灯和近光灯。', '雾天停车要开危险报警灯。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926062, '前后防雾灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '危险报警闪光灯、示廓灯和后位灯', '4', '', '机动车在雪天临时停车时，应开启什么灯？', '报警灯就是前后两黄灯的一亮一灭，四个灯是同时的。示廓灯就是指表示整车轮廓的，在车的最边最高处，一般是红色小灯。后位灯一般是指让后面车辆看到本车而使用的，一般是红色，晚上打开车灯时发亮。因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926063, '正确', '错误', '', '', '1', '', '救火时不要张嘴呼吸或高声呐喊，以免烟火灼伤上呼吸道。', '这样做能避免吸入有害气体或被烟呛着。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926064, '连续鸣喇叭警告', '加速从前方绕过', '出现危险再减速', '主动减速让行', '4', 'http://images.juheapi.com/jztk/subject4/268.jpg', '驾驶机动车遇到这种情况怎样应对？', '虽然是绿灯可以行驶，但由于前方有非机动车横穿，为了安全，要注意避让，因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926065, '等行人通过后再起步', '起步从行人前方绕过', '鸣喇叭告知行人让道', '起步后缓慢靠近行人', '1', 'http://images.juheapi.com/jztk/subject4/269.jpg', '驾驶机动车在这种情况下怎样礼让行人？', '虽然是绿灯，但也要等行人通过以后再行驶，以免发生意外。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926066, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/270.jpg', '机动车在这种情况下可以超车。', '根据《中华人民共和国道路交通安全法实施条例》的规定，行经繁华路段、交叉路口、铁路道口、人行横道、急弯路、宽度不足4米的窄路或者窄桥、陡坡、隧道或者容易发生危险的路段，不得超车。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926067, '正确', '错误', '', '', '2', '', '冰雪路面处理情况不能使用紧急制动，但可采取急转向的方法躲避。', '冰雪路面行驶过程中不能采取紧急制动或是急转方向的方法，这是因为冰雪路面易滑，这样做容易造成车子侧滑或侧翻。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926069, '用沙土覆盖火焰灭火', '向身上喷冷水灭火', '用灭火器进行灭火', '帮助脱掉燃烧的衣服', '2', '', '救助全身燃烧伤员采取哪种应急措施？', '先大家要明确，燃烧过后人体皮肤已经受损因此若是用A的方法，且不说沙土上附着的病原体会造成严重感染，事后的清理和后续治疗也会相当麻烦。而选项B，除了能够给创面降温以外，还有可能是清洁伤口，救治时限可以放宽到12小时。而选项C，则很有可能灭火器喷出的东西会造成身体上的伤害，因此不妥当。选项D，应该是帮助脱去未燃烧的衣服。因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926070, '鸣喇叭进行警告', '减速或靠右停车', '开远光灯抗议', '加速反超后告诫', '2', 'http://images.juheapi.com/jztk/subject4/274.jpg', '在这种情况下被超机动车驾驶人怎样应对？', '虽然遇到机动车这样超车，会心里不爽，但为了安全起见，要让对方先通过，因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926071, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/275.jpg', '驾驶机动车在这种情况下可以适当鸣喇叭加速通过。', '遇到这种情况应当减速慢行，必要时停车，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926072, '在车速下降前减挡', '在车速下降后减挡', '在车速过低时减挡', '尽量使用越级减挡', '1', '', '驾驶机动车上坡行驶如何保持充足动力？', '车速慢了再换挡不容易前进甚至会熄火，造成车掉下坡来。因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926073, '采取保暖措施', '将伤员转移到有新鲜空气的地方', '进行人工呼吸', '进行胸外心脏按压', '2', '', '救助有害气体中毒伤员，首先采取的措施是什么？', '中毒伤员所处的空间肯定空气不流通，空气新鲜的地方可以让人体的呼吸管道起畅通的作用。因此应当先转移位置到空气新鲜的地方。 只有在讲中毒伤员转移至空气新鲜的地方之后，才能做ACD等措施。因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926074, '超速行驶', '不按交通标线行驶', '客车超员', '疲劳驾驶', '1', '', '某日13时10分，罗某驾驶一辆中型客车从高速公路0公里处出发，下午14时10分行至该高速公路125公里加200米处时，发生追尾碰撞，机动车驶出西南侧路外边坡，造成11人死亡、2人受伤。罗某的主要违法行为是什么？', '因为，高速最高时速为120公里/小时，而案例中根据时间可推算出超速5.2公里，所以该案例为超速。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926075, '雾灯和危险报警闪光灯', '雾灯和转向灯', '雾灯和远光灯', '雾灯和近光灯', '1', '', '机动车在雾天行驶时，要开启什么灯？', '《中华人民共和国道路交通安全法实施条例》第五十八条  机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。【机动车雾天行驶应当开启雾灯和危险报警闪光灯】。 因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926076, '挂高速档加速冲坡', '提前观察坡道长度', '尽量避免途中减挡', '上坡前减挡保持动力', '16', '', '驾驶汽车在山区上陡坡路段怎样行驶？', '上陡坡应该挂低档。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926077, '制动距离延长', '抗滑能力变大', '路面附着力变大', '制动距离变短', '1', '', '驾驶机动车在冰雪路面行车注意什么？', '由于冰雪路面易滑，因此要比平时更早的踩刹车，也就是说制动距离延长。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926078, '正确', '错误', '', '', '2', '', '机动车电器、汽油着火后可用水来熄灭。', '水的密度比油大，不能用来灭火。而电器类，若是用水熄灭的话，很容易导电。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926079, '急剧增大', '逐渐增大', '急剧减小', '没有变化', '3', '', '当机动车在湿滑路面上行驶时，路面附着力随着车速的增加如何变化？', '湿滑路面就是路面又湿又滑，即很易滑，因此车子与地面的抓地力就会变小。因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926080, '会车前两车之间相互提示', '驾驶操作的习惯行为', '便于双方观察前方情况', '驾驶人之间的一种礼节', '3', '', '夜间会车前，两车在相距150 米之外交替变换前照灯远近光的作用是什么？', '夜间会车，由于视线受限的关系，必须借助灯光等了解周围情况，因此这么做是为了了解情况，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926081, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/285.jpg', '驾驶机动车通过这种傍山险路要靠左侧行驶。', '车辆在道路上行驶应靠右侧行驶，如图这种行为是占道行驶，若是对向有来车，很容易发生事故。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926082, '减速慢行', '保持安全车距', '正确使用灯光', '高速行驶', '13', '', '雾天机动车在道路上通行，驾驶人要怎样做？', '雾天开车，能见度低，视野不良，因此车速要放慢，同时和其他车辆保持安全距离，视野不良时车灯的使用就显得很重要了。因此本题选ABC。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926083, '正确', '错误', '', '', '2', '', '伤员骨折处出血时，要先固定，然后止血和包扎伤口。', '本题的关键在于伤员发生这种状况的时机、也就是分轻重缓急来处理主要的病情，如果是突发灾难现场救急，肉眼判断骨折处未伤及动静脉仅皮外划伤无须单独包扎止血、那么可以先行固定，送往医院后再行处置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926084, '连续鸣喇叭加速超越', '加速继续超越', '停止继续超车', '紧跟其后，伺机再超', '3', '', '超车时前方机动车不减速、不让道，驾驶人怎么办？', '安全第一，文明行车，所以选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926085, '正确', '错误', '', '', '1', '', '冰雪道路行车，由于积雪对光线的反射，极易造成驾驶人眩目。', '因为太阳光通过冰的折射到人的眼睛里，冰在太阳光下就好像一面镜子。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926086, '客车超员', '超速行驶', '疲劳驾驶', '操作不当', '2', '', '罗某驾驶大型卧铺客车（乘载44人，核载44人）行至沿河县境内540县道58公里加500米处时，在结冰路面以每小时44公里速度行驶，导致机动车侧滑翻下公路，造成15人死亡、27人受伤。罗某的主要违法行为是什么？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　（二）掉头、转弯、下陡坡时；　　（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　（四）在冰雪、泥泞的道路上行驶时；　（五）牵引发生故障的机动车时。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926087, '交替变换远近光灯绕过行人', '开启近光灯绕过行人', '使用远光灯绕过行人', '停车让行人优先通过', '4', 'http://images.juheapi.com/jztk/subject4/291.jpg', '夜间驾驶汽车在人行横道前遇行人横过时怎样行驶？', '和谐  和谐第一', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926088, '注意观察动态', '适当减速慢行', '保持安全间距', '鸣喇叭加速超越', '13', 'http://images.juheapi.com/jztk/subject4/292.jpg', '遇到这种同向行驶的非机动车时怎样行驶？', '请以圣人的眼光答题，D最不符合圣人的标准。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926089, '切断一切电源', '戴好防护面具和手套', '关闭阀门制止渗漏', '组织人员向下风方向疏散', '4', '', '液化石油气罐车在运输途中发生大量泄漏时，下列措施错误的是什么？', '为了防止泄漏事件造成更大的伤害和损失，因此采取的措施应该是戴好防护面具和手套、切断一切电源、关闭阀门制止渗漏，并组织人员向上风方向疏散。而本题考的是错误的措施，如此一来，只有选项D是错误的。不是往下风方向，而是应当往上风方向。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926090, '正确', '错误', '', '', '2', '', '机动车在路边起步后应尽快提速，并向左迅速转向驶入正常行驶道路。', '驾车要谨慎，所以出现迅速基本都是错误！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926091, '前轮侧滑，向侧滑方向转动转向盘', '前轮侧滑，向侧滑相反方向转动转向盘', '后轮侧滑，向侧滑方向转动转向盘', '后轮侧滑，向侧滑相反方向转动转向盘', '10', '', '机动车发生侧滑时要如何调整方向？', '如果前轮侧滑时，应稳住油门，纠正方向驶出。当后轮侧滑时，应将方向盘朝侧滑方向转动，待后轮摆正后再驶回路中。下坡中遇到后轮侧滑时，可适当点一下油门，提高车速，待侧滑消除后再按原车速行驶。因此本题选BC。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926092, '正确', '错误', '', '', '2', '', '大雾天气能见度低，开启远光灯会提高能见度。', '雾天，当然是雾灯和危险警报灯。另外，雾天的能见度不是灯能改变的！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926093, '占对向车道行驶', '靠右侧减速行驶', '在道路中心行驶', '紧靠路右侧行驶', '2', 'http://images.juheapi.com/jztk/subject4/297.jpg', '驾驶机动车在这种山区弯路怎样行驶？', '由图可知，前方是向左急转弯路段，因此为了避免前方有来车，应当减速鸣号靠右行，因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926094, '开启前照灯低速行驶', '开启雾灯低速行驶', '选择安全地点停车', '紧靠路边低速行驶', '3', '', '驾驶机动车遇到大雾或特大雾等能见度过低天气时如何做？', '注意题中的“大雾或特大雾等能见度过低天气”，也就是说可能出现无法正常行驶的情况，因此为了安全起见，应停车，而停车则应选择安全的地点。 因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926095, '紧跟其后，伺机超越', '加速强行超越', '连续鸣喇叭催前车让路', '停止超车，让前方机动车先超车', '4', '', '超车时，发现前方机动车正在超车，驾驶人怎么办？', '若强行超车，很有可能发生危险。只有在前车完成超车后，才能看清前方道路的状况，此时超车也才较为安全。因此选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926096, '正确', '错误', '', '', '1', '', '驾驶机动车在雾天行车要开启雾灯。', '《中华人民共和国道路交通安全法实施条例》第五十八条  机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。【机动车雾天行驶应当开启雾灯和危险报警闪光灯】。因此本题选A。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926097, '正确', '错误', '', '', '1', '', '在大雨天行车，为避免发生“水滑”而造成危险，要控制速度行驶。', '控制速度行驶：减速或者停车都是为了安全。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926098, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/302.jpg', '驾驶机动车在这种环境条件下起步前要开启远光灯。', '起步开近光灯，牢记！先近后远！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926099, '正确', '错误', '', '', '2', '', '雨天避免“水滑”现象的有效方法就是保持高速行驶。', '应该是降低车速，实际驾驶员也是把车速尽量降低，不是有“十次事故九次快”吗？何况是雨天。所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926100, '刚下雨的路面', '大雨过后路面', '暴雨中的路面', '大雨中的路面', '1', '', '在雨天哪类路面最容易发生侧滑？', '刚下雨的路面是水和土的混合物简称泥，很滑。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926101, '加速迅速超越', '变换远近光灯超越', '减速放弃超车', '持续鸣喇叭超越', '3', '', '超车过程中，被超车辆突然加速怎么办？', '和谐第一，您就别跟前车飙车了，别斗气，出事故多半是斗气引起的。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926103, '充分利用行车制动器', '充分利用发动机的牵制作用', '充分利用驻车制动器', '充分利用缓速器', '2', '', '在冰雪路面上减速或停车，要怎样降低车速？', '发动机制动有三个显著特点：一是由于差速器的作用，可将制动力矩平均地分配在左右车轮上，减少侧滑、甩尾的可能性；二是有效地减少脚制动的使用频率，避免因长时间使用制动器，导致制动器摩擦片的温度升高，使制动力下降，甚至失去作用；三是车速始终被限定在一定范围内，有利于及时降速或停车，确保行车安全。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926104, '正确', '错误', '', '', '1', '', '雾天公路行车可多使用喇叭引起对向注意；听到对向机动车鸣喇叭，也要鸣喇叭回应。', '雾天视野不好，看不清楚的情况下，只能通过声音来判断其他车辆的位置。因此雾天比较多用喇叭来“交流”，当听到其他车辆鸣喇叭时，也该告知对方自己所在的位置，避免因看不清发生事故。 所以这题是对的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926105, '正确', '错误', '', '', '1', '', '在泥泞路行车中发生侧滑时，要向后轮侧滑的方向转动转向盘适量修正。', '因为好比是后轮向左侧横滑如果向右打方向，会加大横滑的幅度的！因为向右打方向，后轮左侧转动就会快，右侧会变慢，左侧抓地力会下降，所以会加大横滑的幅度的，相反如果向同一方向打，还是这个例子向左侧打，右轮转动会快，左侧转动会慢，抓地力就会提高，就会恢复行使方向的！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926106, '正确', '错误', '', '', '1', '', '驾驶机动车在雨天起步前要使用刮水器。', '刮水器是为了提高汽车在雨天和雪天行驶时驾驶员的能见度而专门设置的，也就是说使用刮水器，在雨天能够让驾驶员能够清楚地透过前挡风玻璃了解周围情况，因此本题选A。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926107, '超速行驶', '客车超员', '疲劳驾驶', '酒后驾驶', '11', '', '吴某驾驶一辆大客车，乘载33人（核载22人），行至163县道7公里加300米处时，机动车失控坠入山沟，造成10人死亡、21人受伤。事后经酒精检测，吴某血液酒精含量为26毫克/百毫升。吴某的主要违法行为是什么？', '1：乘载33人(核载22人)，所以客车超员2：吴某血液酒精含量为26毫克/百毫升，所以酒后驾驶。（此处既然检测到酒精就算酒后驾驶，不然人的血液里边哪里会有酒精）', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926108, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/312.jpg', '在积雪覆盖的冰雪路行车时，可根据路边树木、电杆等参照物判断行驶路线。', '这个常识题。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926109, '正确', '错误', '', '', '2', '', '驾驶汽车遇到牲畜横穿抢道的情况，要及时鸣喇叭进行驱赶。', '小心惹恼了牲畜，把咱的车撞翻了。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926110, '正确', '错误', '', '', '2', '', '雨天超车要开启前照灯，连续鸣喇叭迅速超越。', '这种天气里面能见度低，车辆应该慢行，就算要超车，也不能连续鸣喇叭，而且要确保安全的情况下超车。所以此题的关键在“迅速”两字。 因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926111, '正确', '错误', '', '', '2', '', '机动车因故障不能离开高速公路时，驾乘人员要在车上等候救援。', '标准程序是：当车辆发生故障时，打开双闪警示灯，应将车辆停放在最右侧的应急车道或紧急停车带内，夜间打开尾灯示宽灯，司乘人员应立即下车，在路沿下护坡内等候救援车辆、人员来，由一个人在车辆尾部150米左右放置三角警示牌或警示物），切记不能在高速公路内走动、追逐、等候！不能在故障车辆内等候！一般情况禁止在高速公路上修理车辆。所以，在车上等候是错的，应该在路沿下的护坡或护栏外等候。 因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926112, '比在平路时提前', '比在平路时推迟', '和平路时一样', '要轻踏制动踏板', '1', '', '驾驶机动车在下坡路段停车怎样使用行车制动？', '下坡行驶时，在重力的作用下速度会逐渐提高，比平缓路段需要的距离长。因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926113, '正确', '错误', '', '', '1', '', '在紧急情况下为伤员止血时，须先用压迫法止血后再根据出血情况改用其他止血法。', '一般小动脉和静脉出血可用加压包扎止血法，较大的动脉出血，应用止血带止血。但在紧急情况下，须先用压迫法止血，然后再根据出血情况改用其它止血法。指压止血法可以在较大的动脉出血后，用拇指压住出血的血管上方(近心端)，使血管被压闭住，中断血液。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926114, '开启远光灯', '低速大间距', '开启近光灯', '开启雾灯', '2', '', '驾驶机动车在雾天两车交会时怎样做最安全？', '雾天行车能见度低，视野不良，因此与其他车辆会车时要低速会车，目的是为了加大会车距离。因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926115, '正确', '错误', '', '', '2', '', '雾天在高速公路上发生事故后，车上人员不要随便下车行走。', '标准程序是：当车辆发生故障时，打开双闪警示灯，应将车辆停放在最右侧的应急车道或紧急停车带内，夜间打开尾灯示宽灯，司乘人员应立即下车，在路沿下等候救援车辆、人员来，由一个人在车辆尾部150米左右放置三角警示牌或警示物，切记不能在高速公路内走动、追逐、等候！不能在故障车辆内等候！一般情况禁止在高速公路上修理车辆。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926116, '先避车后避物', '先避人后避物', '先避车后避人', '先避物后避人', '2', '', '高速公路行车紧急情况避险的处理原则是什么？', '以人为本，所以首先要避人。 因此选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926117, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/321.jpg', '夜间在这种情况下跟车要注意观察前车信号灯的变化，随时做好减速或停车的准备。', '前车的刹车灯亮了，说明前车在减速，所以你也要跟着减速和刹车。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926118, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/322.jpg', '交通警察发出这种手势信号可以直行通过。', '这个手势是左转弯。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926119, '使其他交通参与者更容易发现自己', '更容易看清路面情况', '提醒其他车辆我在让行', '以上说法都不对', '1', '', '夜间驾驶汽车通过十字交叉路口交替使用远近光灯的目的是什么？', '交替使用远近光灯就是提醒的作用。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926120, '干燥水泥路面', '下雨开始时的路面', '潮湿水泥路面', '大雨中的路面', '2', '', '最容易发生侧滑的路面是：', '当雨刚开始时便把路面上的那些媒质给弄湿了，此时的路面便像洒上一层薄薄的润滑剂！但在雨中，由于雨水不断增加，路面上的“润滑剂”亦随之被稀释，大雨时，会将其完全冲洗干净，顾有之题目的说法。但是，就算是路面被雨冲洗干净了，也不能掉于轻心地开快车，因为橡胶轮胎遇到水后使大大减少了对地面的付着力，这是它的天性！', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926121, '比在平路时提前', '比在平路时推迟', '和平路时一样', '要重踏制动踏板', '2', '', '上坡路段停车怎样使用行车制动？', '汽车上坡行驶时，由于坡度阻力车速自然会降下来，与平缓路段相比较只需轻轻制动。因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926122, '正确', '错误', '', '', '1', '', '驾驶机动车汇入车流时不能影响其他机动车通行。', '汇入车流的前提是不能影响其他车辆的正常通行，因此本题选A。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926123, '依次跟车行驶', '从右侧超越', '靠边停车等待', '从左侧超越', '1', 'http://images.juheapi.com/jztk/subject4/327.jpg', '遇到这种前方拥堵路段通行缓慢时怎样行驶？', '请按规矩行驶。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926124, '正确', '错误', '', '', '1', '', '为防止有害气体中毒伤员继续中毒，首先将伤员转移到空气新鲜的地方。', '中毒伤员所处的空间肯定空气不流通，空气新鲜的地方可以让人体的呼吸管道起畅通的作用。因此应当先转移位置到空气新鲜的地方。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926125, '提前减速行驶', '提前鸣喇叭提醒', '不得急加速绕行', '保持安全间距', '17', 'http://images.juheapi.com/jztk/subject4/329.jpg', '雨天遇到这种行人占道行走时怎样通行？', '牢记', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926126, '快速通过', '使用危险报警闪光灯', '减速慢行并鸣喇叭示意', '随意通行', '3', '', '机动车驶近坡道顶端等影响安全视距的路段时，要如何保证安全？', '注意“影响安全视距的路段”，也就是说在此路段行驶时无法完全掌握路况，因此要减速慢行谨慎小心，同时鸣喇叭告知其他车辆。因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926127, '加速直接汇入车流', '从主路内灰色车后汇入车流', '从主路内红色车前汇入车流', '开启转向灯直接汇入车流', '2', 'http://images.juheapi.com/jztk/subject4/331.jpg', '驾驶机动车在这种情况下怎样汇入主路车流？', '前方有个“让”的牌子，所以要让行红色的车子！', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926128, '正确', '错误', '', '', '2', '', '变更车道前确认后方无来车时可以不开转向灯变道。', '变更车道时要提前开启转向灯，养成良好的开车习惯，防患于未然，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926130, '视线受阻', '路面湿滑', '附着力变小', '行驶阻力增大', '13', '', '雨天影响安全行车的主要因素有哪些？', '牢记', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926131, '停车观察路口情况', '加速尽快进入路口', '减速缓慢进入路口', '减速观察左后方情况', '1', 'http://images.juheapi.com/jztk/subject4/335.jpg', '驾驶机动车在有这种标志的路口怎样通过最安全？', '前方有个“停”的牌子，肯定是要停车观察在通过！', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926132, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/336.jpg', '按照下图红框内的标志，机动车应当在B区域内行驶。', 'B区域是非机动车道，你看那有个非机动车标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926133, '正确', '错误', '', '', '2', '', '机动车在行驶中，遇雨雪天气向右侧滑时，要向左打方向，使其稳定。', '所谓“侧滑”就是车辆急加速、突然制动或启动时扭矩过大而产生的侧向甩动的现象，俗称“甩尾”。造成车辆侧滑的因素很多。雪天，雨天、冰面上行车时突然抬起油门或突然加速；采取紧急制动，制动时后轮比前轮先抱死；都极易发生车辆侧滑而造成事故。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926134, '正确', '错误', '', '', '1', '', '机动车落水后，只有在水快浸满车厢时，才有可能开启车门或摇下车窗玻璃逃生。', '车辆掉水后，车里有空气，车内压力低于车外的压力，这个时候你想打开车门，一是比较困难，二是即使你打开了车门，车外的水一下子涌进来，可能会造成更大的伤害。当水浸满车厢后，内外压力接近时，以上问题都能解决了。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926135, '保持较大的安全距离', '开启危险报警闪光灯', '不断变换前照灯远近光', '适时鸣喇叭提示前车', '1', '', '驾驶机动车在冰雪路面怎样跟车行驶？', '冰雪路面易滑，因此在跟车时要加大跟车距离，因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926137, '正确', '错误', '', '', '1', '', '机动车转弯时速度过快，容易发生侧滑。', '转弯速度越快，角速度越大，离心力也越大。这样一来车子的抓地力就变小了，此时就容易发生侧滑。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926138, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/342.jpg', '在山区道路跟车行驶的距离要比平路时大。', '复杂道路一定要加大跟车距离，开车一定要离前车远一点，这样最安全，出了问题有处理的时间。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926139, '避免紧急制动、紧急转向', '保持足够的安全距离', '注意非机动车和行人动态', '选择安全车速行驶', '17', '', '雨天安全行车的注意事项是什么？', '雨天行车，受视线及路面的影响，要减速慢行，同时要注意行车和非机动车的动态，并与其他车辆保持较大的安全距离，也不能采取紧急制动或是急转方向的方法，因为容易造成侧滑或横滑状况。因此本题选ABCD。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926140, '正确', '错误', '', '', '1', '', '对无骨端外露的骨折伤员肢体固定时，要超过伤口上下关节。', '为什么要超过伤口上下关节呢？这是因为不能让这两个关节活动。这样做能防止骨折端的移位，防止造成血管和神经的损伤，后者的损伤是比较头疼的，比单纯的骨折要麻烦多了，尤其是神经的损伤，很可能会造成感觉或运动方面障碍；此外，防止骨折断端戳破皮肤从而造成骨折端的污染影响愈合。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926141, '尽量选择安全区域停车', '开启危险报警闪光灯', '开启示廓灯和后位灯', '按规定设置警告标志', '17', '', '夜间行车中汽车发生故障需要停车时怎么办？', '都是安全常识啊。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926143, '正确', '错误', '', '', '1', '', '雪天行车中，在有车辙的路段要循车辙行驶。', '雪天行驶时，道路通常会被雪所掩盖，因此无法了解路面的实际情况。因此为了避免不可见的危险，就要尽可能沿着车辙行驶。因为这样安全系数更高一些。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926144, '正确', '错误', '', '', '1', '', '雨天在高速公路行车，为避免发生“水滑”现象而造成方向失控，要降低车速。', '水滑就是因为雨天雨水多了，使轮胎与路面的摩擦系数变小了，就容易侧滑。因此要降低车速行驶，减低发生侧滑的可能性，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926145, '正确', '错误', '', '', '2', '', '移动脊柱骨折的伤员，切勿扶持伤者走动，可用软担架运送。', '注意担架会引起误解，脊柱骨折意味着柔软的脊髓面临机械损伤（哪怕是非常微小的震动）所以必须保持脊柱固定（软担架根本不能规避机械损伤），所以，用软担架运送也是错误的！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926146, '正确', '错误', '', '', '2', '', '易燃液体一旦发生火灾，要及时用水扑救。', '着火时，人们往往立即想到的是用水来灭，但有些火灾却是不能用水来扑救的。如果易燃液体密度比水小，就不应该用水，因为易燃液体会浮在水面继续燃烧。所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926147, '注意观察行人动态', '适当降低车速', '保持安全距离', '提前轻按喇叭提醒', '17', 'http://images.juheapi.com/jztk/subject4/351.jpg', '雨天遇到这些撑雨伞和穿雨衣的行人在路边行走怎样通行？', '常识题', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926148, '救护车', '消防车', '警车', '校车', '17', '', '当您遇到以下车辆时，需要礼让的是什么？', '这些车都是我们需要让行的，因为他们不是我们的未来就是在保障我们基本的生活。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926149, '爆炸品', '氧化性物质', '自燃物品', '易燃固体', '4', '', '火柴、硫黄和赤磷属于哪类危险化学品？', '易燃固体是指在常温下以固态形式存在，燃点较低，遇火受热、撞击、摩擦或接触氧化剂能引起燃烧的物质。这三类物质正好符合，因此选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926150, '正确', '错误', '', '', '1', '', '受伤者在车内无法自行下车时，可设法将其从车内移出，尽量避免二次受伤。', '如题，受伤者既然无法自行下车，那肯定说明是行动不便的，若不设法将伤者从车内转移出来，万一车子发生什么状况，受伤者可能都无法及时躲避，这样很容易造成更大的伤害。因此这题是正确的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926151, '开启远光灯', '适当提高车速', '关闭近光灯', '提前降低车速', '4', '', '驾驶汽车在进出隧道时应注意什么？', '只有D最安全，10次事故9次快，亲，慢点。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926152, '客车超员', '驾驶逾期未检验的机动车', '驾驶与准驾车型不符的机动车', '疲劳驾驶', '3', '', '赵某（持有A2驾驶证）驾驶大型卧铺客车，行驶至叶城县境内219国道226公里加215米处转弯路段时，坠入道路一侧山沟，致16人死亡，26人受伤。赵某的主要违法行为是什么？', 'A2是不能开大型客车的！', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926153, '急踏制动踏板减速', '缓抬加速踏板减速', '迅速转向进行调整', '提速增大车轮排水量', '2', '', '雨天机动车在高速公路行驶发生“水滑”现象时怎样处置？', '所谓“水滑”现象就是雨天汽车在积水路面上高速行驶时，轮胎与路面间的存水不能排除，水的压力使轮胎上浮，形成汽车在积水路面上滑行的现象。选项ACD都有可能造成车辆侧滑等事故的发生，而通过缓抬加速踏板的方式减速才能真正让车渐渐减慢，因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926154, '空挡滑行', '减挡，充分利用发动机制动', '踏下离合器滑行', '长时间使用驻车制动器制动', '2', '', '驾驶机动车下长坡时，车速会因为重力作用越来越快，以下控制车速方法正确的是什么？', '发动机制动有三个显著特点：一是由于差速器的作用，可将制动力矩平均地分配在左右车轮上，减少侧滑、甩尾的可能性；二是有效地减少脚制动的使用频率，避免因长时间使用制动器，导致制动器摩擦片的温度升高，使制动力下降，甚至失去作用；三是车速始终被限定在一定范围内，有利于及时降速或停车，确保行车安全。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926155, '正确', '错误', '', '', '2', '', '夜间驾驶汽车在急弯道停车时要开启危险报警闪光灯。', '急转弯不需要开启危险报警闪光灯，危险报警闪光灯，通常称为“双蹦”（红三角里有个！的标志按扭开关，俗称双闪灯 或双跳灯），是一种提醒其他车辆与行人注意本车发生了特殊情况的信号灯。 在驾车过程中遇到浓雾时，能见度低于50米时，由于视线不好，不但应该开启前、后防雾灯，此时还应该开启危险报警闪光灯，以提醒过往车辆及行人的注意，特别是后方行驶的车辆，保持应有的安全距离和必要的安全车速，避免紧急刹引起追尾。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926156, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/360.jpg', '前方标志预告距离下一出口4公里 。', '下一个出口预告：预告下一出口距离。当互通式立体交叉间距大于或等于3 km、小于5 km时，应设置下一出口预告标志，预告下一出口的信息和距离。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926158, '轻踏或间歇踩踏制动踏板', '与其他路面一样踏制动踏板', '重踏制动踏板', '猛踏制动踏板', '1', '', '驾驶未安装制动防抱死装置（ABS）的机动车在冰雪路面怎样使用制动？', '未安装制动防抱死装置的车辆，在冰雪、湿滑、砾石路面或者比较光滑的路面上制动时，要轻踏或间歇踩踏制动踏板。这是因为在冰雪、湿滑、砾石路面或者比较光滑的路面车辆极易抱死失控，而不是侧滑那么简单，不能猛踩刹车，可先踩刹车，然后离合，松开离合后间接性地踩刹车，双手尽力控制方向，这样有效减少刹车距离。因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926159, '开启近光灯', '靠右侧行驶', '开启远光灯', '注意对向来车', '14', '', '驾驶汽车进入双向通行的隧道时应注意什么？', '隧道内禁止开远光，影响他人开车视线。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926160, '用手帕包扎', '用毛巾包扎', '用棉质衣服包扎', '用细绳缠绕包扎', '4', '', '在没有绷带急救伤员的情况下，以下救护行为中错误的是什么？', '根据受伤位置的不同和创面的大小，可以利用毛巾、手帕做成简易的三角巾。细绳由于其表面不是平整光滑的，也不够柔软。因此不能用来包扎，而棉质衣服相对比较柔软，因此可以。所以本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926161, '正确', '错误', '', '', '1', '', '制动时前车轮抱死会出现丧失转向能力的情况。', '现在小车都有ABS防抱死系统，紧急刹车时不会再抱死出现丧失转向能力的情况，急刹车你会感觉到脚下会强力反弹，就是ABS启动了，这时一样可以打方向。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926162, '迅速抬上担架送往医院', '适当调整损伤时的姿势', '用绷带对骨折部位进行包扎', '不要移动身体骨折部位', '4', '', '抢救骨折伤员时注意什么？', '面对骨折伤员，抢救时一定要确保不会造成二次伤害，即不能让骨折情况变严重或恶化，因此除了选项D之外，ABC都有可能造成二次伤害，因此本题只能选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926163, '正确', '错误', '', '', '2', '', '救火时不要脱去所穿的化纤服装，以免伤害暴露的皮肤。', '化纤易着是一方面主要是化纤不能耐高温，融化后会紧贴在皮肤上，对皮肤伤害很大，导致烧伤。 因此救火时应当脱去化纤服装避免烧伤。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926164, '注意避让已在路口内车辆', '优先驶入环形路口', '鸣喇叭示意其他车辆让行', '超越前方车辆进入路口', '1', '', '驾驶机动车驶入拥堵的环形路口，以下做法正确的是什么？', '驾驶机动车驶入拥堵的环形路口，需要注意避让已在路口内车辆。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926165, '爆胎后，车辆行驶方向易发生变化，须紧握转向盘', '爆胎后，车辆自身开始减速，所以只需轻踏制动踏板', '爆胎后，紧急制动容易引起侧翻', '轻踏制动踏板进行减速是为了保护轮胎', '8', '', '驾驶员在高速公路上行驶时，车辆左前轮突然爆胎，须第一时间紧握转向盘，然后轻踏制动踏板进行减速，并将车停靠在紧急停车带上。这样做的原因是什么？', '在车辆爆胎时如果不尽力控制方向，或者采取紧急制动都容易引起车辆侧翻，所以需要紧握方向盘，轻踩制动踏板进行减速。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926166, '位置A', '位置B', '位置C', '位置D', '4', 'http://images.juheapi.com/jztk/subject4/370.jpg', '当高速公路上车辆发生故障时，人员应当疏散到下图哪个位置。', '发生事故后人员应该疏散到护栏外D处，车辆停在位置C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926168, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/372.jpg', '驾驶汽车可以在这种急弯处超车。', '这么复杂的路况您就好好在后边跟车吧。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926169, '在成人和儿童之间快速通过', '连续鸣喇叭提示后通过', '从成人身后绕行', '减速鸣喇叭提示，做好随时停车准备', '4', 'http://images.juheapi.com/jztk/subject4/373.jpg', '如图所示，驾驶机动车在乡间道路上行驶，以下做法正确的是什么？', '在乡道上行驶，应该减速鸣喇叭提示，做好随时停车准备。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926170, '加速冲过坡顶', '开启远光灯', '合理控制车速', '交替变换远近光灯', '12', '', '夜间驾驶汽车驶近上坡路坡顶怎样行驶？', '交替变换远近光灯是为了提醒对面坡上的车，因为坡上的车视线看不到你。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926171, '正确', '错误', '', '', '2', '', '机动车在高速公路上，因故障不能离开行车道时，可在行车道上迅速抢修。', '应该立即在车辆后方150米以上的距离设置警示标志，开启示宽灯。车上人员退到路边防护栏外，并拨打应急报警电话。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926172, '在最左侧车道内停车', '在路肩上行驶', '学习驾驶机动车', '倒车', '17', '', '驾驶机动车在城市快速路上行驶，以下做法错误的是什么？', '城市快速道路是指城市道路中设有中央分隔带，具有四条以上的车道，全部或部分采用立体交叉与控制出入，供车辆以较高的速度行驶的道路。以上四个选项都是在城市快速道路中禁止的行为。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926173, '集中注意力', '降低车速', '注意鸣喇叭', '做好停车准备', '17', 'http://images.juheapi.com/jztk/subject4/377.jpg', '在这种路面较窄的急弯处行车时要注意什么？', '谨慎驾驶，安全行驶第一，以上都是安全行驶的条件。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926174, '正确', '错误', '', '', '2', '', '搬运昏迷失去知觉的伤员要采取仰卧位。', '正确的做法是先开放气道，再采取去枕仰卧位，头偏向一侧，保持呼吸道通畅，以防窒息。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926175, '采取保暖措施', '用软板担架运送', '用三角巾固定', '扶持伤者移动', '3', '', '怎样抢救脊柱骨折的伤员？', '应该是原地固定等待专业救护人员，脊柱骨折被移动很有可能将导致高位截瘫。所以本题唯一可以选的就是C了！', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926176, '最左侧', '中间', '最右侧', '任意', '1', '', '在同向3车道高速公路上行车，车速高于每小时90公里、低于每小时110公里的机动车不应在哪条车道上行驶？', '高速路的车最低时速是要求60公里，最高不得超过120公里。如果高速路同方向有2条车道，右侧车的速度范围为60公里到100公里，左侧车的速度范围为100公里到120公里。如果高速路同方向有3条车道，最右侧的速度范围为60公里到90公里，中间的为90公里到110公里，最左侧的为110公里到120公里。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926177, '停车等待', '从对向车道加速超越', '连续鸣喇叭催促', '从非机动车道通过', '1', 'http://images.juheapi.com/jztk/subject4/381.jpg', '如图所示，驾驶机动车跟车行驶遇到出租车正在接送乘客时，以下做法正确的是什么？', '驾驶机动车跟车行驶遇到出租车正在接送乘客时，应停车等待。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926178, '超速行驶', '不按交通标线行驶', '客车超员', '疲劳驾驶', '4', '', '某日早上6时，冉某驾驶一辆大客车出发，连续行驶至上午11时，在宣汉县境内宣南路1公里处，坠于公路一侧垂直高度8.5米的陡坎下，造成13人死亡、9人受伤。冉某的主要违法行为是什么？', '规定是说 连续4小时就 要停车休息20分钟！', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926179, '混凝土路', '土路', '冰雪路面', '沙土路', '3', '', '机动车在什么样的路面上制动时车轮最容易抱死?', '由于冰雪路面摩擦系数较另外三种路面来说小许多，所以摩擦力比较小，当刹车片和刹车盘之间的摩擦力大于路面和车轮间的摩擦力时,车轮就会抱死而打滑！因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926180, '爆胎', '汽车行驶油耗增大', '操纵失控', '加剧轮胎磨损', '17', '', '汽车各轮胎气压不一致时，容易造成的后果是什么？', '轮胎气压不足或左、右两侧轮胎气压不均匀，会引起制动效果时差，操纵失控，转向困难或转向沉里.同时也降低了轮胎的使用寿命，容易燎胎，增大油耗。载重全大时.以上后果更严重。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926181, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/385.jpg', '如图所示，驾驶机动车在路口前遇黄灯亮时，应停车等待。', '黄灯亮时，已越过停止线的车辆可以继续通行；其它情况需要停车等待。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926182, '不干扰前车视线，有利自己看清道路', '提醒前方车辆让行', '提醒前方车辆减速', '提醒前车后方有车跟随，前车应适当提速', '1', 'http://images.juheapi.com/jztk/subject4/386.jpg', '在这种大雨中，跟车行驶时使用近光灯的目的是？', '图片分析：大雨中，跟车行驶时使用近光灯的目的是不干扰前车视线，有利自己看清道路。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926184, '前车通过后再上坡', '迅速超越前车上坡', '低速超越前车上坡', '紧随前车后上坡', '1', 'http://images.juheapi.com/jztk/subject4/388.jpg', '在山区冰雪道路上遇到这种前车正在上坡的情况如何处置？', '为了防止前车打滑撞到您的车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926185, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/389.jpg', '遇到这种跨江、河、海大桥时，可能会遇到横风，要控制好方向。', '常识题', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926187, '靠路中心减速行驶', '加速甩掉后车', '开启左转向灯警示', '减速靠右侧让行', '4', 'http://images.juheapi.com/jztk/subject4/391.jpg', '夜间遇到这种后车发出超车信号时怎样行驶？', '安全第一，开车重要的就是别斗气，都让着点。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926188, '注意前车信号灯变化', '使用近光灯', '保持安全距离', '做好减速或停车准备', '17', 'http://images.juheapi.com/jztk/subject4/392.jpg', '夜间在这种道路条件怎样跟车行驶？', '谨慎驾驶', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926189, '加速通过', '在骑车人通过前提前加速通过', '停车让行', '连续鸣喇叭使其让行', '3', 'http://images.juheapi.com/jztk/subject4/393.jpg', '如图所示，驾驶机动车行经交叉路口遇到这种情况时，以下做法正确的是什么？', '驾驶机动车行经交叉路口遇到这种情况时，应停车让行。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926190, '正确', '错误', '', '', '2', '', '驾驶机动车在山区道路应紧跟前车之后行驶。', '山区道路突发状况多，需保持一定的距离来应急。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926192, '提前减速并停车等待', '借对向车道超越前车', '鸣喇叭催促前车让路', '继续行驶，靠近前车', '1', 'http://images.juheapi.com/jztk/subject4/396.jpg', '如图所示，驾驶机动车遇到前方车辆正在停车时，以下做法正确的是什么？', '驾驶机动车遇到前方车辆正在停车时，需要提前减速并停车等待，等前车走后方可再过。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926194, '适当鸣喇叭加速通过', '长鸣喇叭催促', '挤靠自行车', '减速让行', '4', 'http://images.juheapi.com/jztk/subject4/398.jpg', '如图所示，驾驶机动车遇到这种情况，B车做法正确的是什么？ ', 'B做法不恰当，AC做法易引发交通事故，减速让行才能保证安全。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926195, '减速让行', '加速行驶', '向左打转向迅速超越', '连续鸣喇叭', '1', 'http://images.juheapi.com/jztk/subject4/399.jpg', '如图所示，驾驶机动车遇到右侧车道车辆突然变更车道时，应当如何避让？', '驾驶机动车遇到右侧车道车辆突然变更车道时，应当减速让行。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926196, '前面的车可能停下', '右侧视野盲区内可能有自行车直行', '行人可能突然进入本车前的人行横道', '直行的黄色车辆可能影响本车右转弯', '13', 'http://images.juheapi.com/jztk/subject4/400.jpg', '如图所示，驾驶机动车跟随前车右转弯时，应当注意的是什么？', '图中直行的黄色车辆在左侧车道上，不会影响该车右转弯。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926199, '加速汇入主路车流', '可以不避让主路车辆', '无需观察,鸣喇叭示意后汇入车流', '减速慢行，在不妨碍主路车辆行驶的前提下汇入车流', '4', 'http://images.juheapi.com/jztk/subject4/403.jpg', '如图所示，驾驶机动车驶离停车场进主路时，以下做法正确的是什么？', '驾驶机动车驶离停车场进主路时，需要：减速慢行，在不妨碍主路车辆行驶的前提下汇入车流。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926200, '可牵引2辆挂车', '可牵引3辆挂车', '挂车的载质量不得超过载货汽车本身的载质量', '允许牵引总质量超过本身载质量的挂车', '3', '', '载货汽车牵引挂车时,下列做法正确的是？', '当然不能超过本身质量啦，不然拉都拉不动。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926201, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/405.jpg', '高速公路安全距离确认路段用于确认车速在每小时100 公里时的安全距离。', '如图所示，一共竖立了三块绿色的牌子，上面的数字分别为，0m、50m，在远处的一个虽然看不清楚，但也可推算出是100m的牌子，也就是要看在这100米内车子的速度。如此说来，本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926202, '主动减速让行', '加速行驶', '即使有让行条件也不让', '靠道路中心行驶', '1', '', '驾驶机动车，遇到后方执行任务的特种车辆时，以下做法正确的是什么？', '驾驶机动车，遇到后方执行任务的特种车辆时，应主动减速让行。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926203, '系好安全带', '调整好后视镜', '不要把身体伸出车外', '不要向车外抛洒物品', '15', '', '驾驶机动车起步前，驾驶人对乘车人需要提出什么要求？', '调整后视镜是您老自己来调的。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926204, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/408.jpg', '右侧标志预告距离高速公路东芦山服务区2公里。', '此图为服务区，没有问题。顺便普及下服务区跟停车区的区别：服务区有吃住加油等服务，停车区属于紧急停车，没有相关的服务。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926205, '两车临近时减速', '适当加速交会', '提前减速缓慢交会', '尽量靠近中线交会', '3', 'http://images.juheapi.com/jztk/subject4/409.jpg', '在这种结冰的道路上怎样会车？', '结冰路面，车况不好控制，得提前预防，减速。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926206, '选择安全的地点', '做到先让、先慢、先停', '靠山体一侧的让行', '不靠山体一侧的让行', '13', 'http://images.juheapi.com/jztk/subject4/410.jpg', '在这种山区危险路段怎样安全会车？', '不靠山体的一侧就是悬崖啊，危险的多，所以靠山体的一侧让行。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926207, '车辆操控难度增大', '制动距离延长', '易产生车轮滑转', '极易发生侧滑', '17', '', '冰雪路面对行车有那些不利影响？', '牢记', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926208, '跟随出租汽车行驶时，要预防其随时可能靠边停车上下乘客', '当前方汽车贴有实习标志时，应该增大跟车距离，预防前车紧急制动', '前方为装满货物的大货车时，应增大跟车距离并避免长时间跟随，以预防货物抛洒和车后盲区带来的危险', '雾天跟车行驶，注意前车紧急制动', '17', '', '以下跟车情况中，应当注意的情形有哪些？', '以上四种情况是行车过程中较为常见的情况，都要注意。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926209, '随时准备停车', '减速行驶，防止泥水溅到行人身上', '减速行驶，注意行人动态', '鸣喇叭提醒行人后，加速通过', '13', 'http://images.juheapi.com/jztk/subject4/413.jpg', '如图所示，驾驶机动车在雨天行驶遇到这种情形时，以下做法正确的是什么？', '驾驶机动车在雨天行驶遇到这种情形时，做法正确的是：随时准备停车；减速行驶，防止泥水溅到行人身上；减速行驶，注意行人动态。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926210, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/414.jpg', '如图所示，在这种情况下要充分减速靠右行驶。', '遇到急转鸯路应当提前减速慢行，等到进入弯路后减速很容易发生侧滑或侧翻。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926211, '马上实施心肺复苏', '使劲掐伤员的人中', '连续拍打伤员面部', '抢救前先检查呼吸', '4', '', '抢救昏迷失去知觉的伤员需注意什么？', '选D是因为抢救前要先确认伤员的生命体征，只有确认了之后才能做出适当的抢救措施。因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926212, '绷带', '三角巾', '止血带', '麻绳', '4', '', '包扎止血不能用的物品是什么？', '包扎止血，要求包扎物表面平整而且松软，这样才能有效地扎紧受伤部位体内的血管，阻止血管内的血液再在流通，达到止血的目的。麻绳，一般说来总是比较粗的，是由几股细绳绞合而成的。其表面不是平整光滑的，也不够柔软。因此一般不适合用以止血包扎。有时，可见护士用纱布撕成条，再合并起来用以包扎止血，这是因为纱布比较柔软，合并后较粗一些，包扎效果更好。 因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926214, '安全帽', '灭火器', '安全锤', '冷冻液', '2', '', '驾驶机动车时，为了预防行车中突然起火造成的危险，应随车携带以下哪项物品？', 'A、C、D选项都不能有效灭火，冷冻液是防冻液，是一种化学合成制剂，用于防止发动机的冷却系统在低温时冻结，其次是散热，不能灭火。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926215, '正确', '错误', '', '', '1', '', '变更车道或超车不开启转向灯属于违法行为。', '现在的违规，违章统称违法行为，所以是对的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926216, '提前开启右转向灯', '驶入减速车道', '按减速车道规定的时速行驶', '加速直接驶离高速公路', '13', '', '以下关于驶离高速公路的做法正确的是什么？', '驶离高速公路的做法正确的是：提前开启右转向灯；驶入减速车道；按减速车道规定的时速行驶。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926219, '适当降低行驶车速', '靠右行驶', '集中注意力驾驶', '使用远光灯，提醒对方车辆', '4', 'http://images.juheapi.com/jztk/subject4/423.jpg', '如图所示，浓雾天气中驾驶机动车两车交会，以下做法错误的是什么？ ', '浓雾天气中驾驶机动车两车交会，应当适当集中注意力驾驶、降低行驶车速、靠右行驶。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926220, '转弯前减速', '靠右侧行驶', '鸣喇叭提示', '转弯时加档', '13', 'http://images.juheapi.com/jztk/subject4/424.jpg', '驾驶汽车在山区上这种陡坡道转弯时怎样行驶？', '转弯时要匀速或者减速，安全第一。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926221, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/425.jpg', '路面网状线标示允许进入该区域内等待。', '路面黄色网状线表示严格禁止一切车辆长时或临时停车，防止交通阻塞。当黄色网状线前方有车辆停驶时，后车必须在黄色网状线外等候，直到确认黄色网状线前方有足够空间停驶本车时，方可驶过黄色网状线。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926223, '降低车速', '靠右通行', '鸣喇叭示意', '抢行通过', '13', '', '驾驶机动车行经驼峰桥会车时，以下做法正确的是什么？', '驾驶机动车行经驼峰桥会车时，正确的做法：降低车速；靠右通行；鸣喇叭示意。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926225, '接近坡顶时视线受阻，无法观察坡顶之后道路走向', '接近坡顶时视线受阻，无法观察对向来车情况', '接近坡顶时车速较慢', '接近坡顶时视线受阻，无法观察坡顶之后是否有障碍物', '14', '', '驾驶机动车在山区道路上坡路段接近坡顶时，超车存在风险，以下说法正确的是什么？', '驾驶机动车在山区道路上坡路段接近坡顶时，接近坡顶时视线受阻需要注意：观察坡顶之后道路走向；观察对向来车情况；观察坡顶之后是否有障碍物。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926226, '正确', '错误', '', '', '2', '', '由于冰雪路面不能使用紧急制动，遇到突然情况可采取急转向的方法躲避。', '冰雪路面最忌紧急转向，本来就不好控制你还玩漂移。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926228, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/432.jpg', '驾驶机动车看到这个标志时，将车速迅速提高到40 公里/小时以上。', '那是限速标志，限制速度在40公里以内。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926229, '及时减速让行', '向左猛打转向盘躲避', '向右猛打转向盘躲避', '加速从右侧绕行', '1', 'http://images.juheapi.com/jztk/subject4/433.jpg', '如图所示，夜间驾驶机动车遇到其他机动车突然驶入本车道时，应当如何避让？', 'BC选项中，动作过猛会导致车辆失控，D中加速错误，且超车要从左侧，只有A是对的。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926230, '按照B车、C车、A车的顺序通行', '按照A车、B车、C车的顺序通行', '按照C车、A车、B车的顺序通行', '按照C车、B车、A车的顺序通行', '4', 'http://images.juheapi.com/jztk/subject4/434.jpg', '如图所示，图中车辆如何通行符合安全文明行车要求？ ', '机动车应让行自行车，转弯应让行直行，因此选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926232, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/436.jpg', '右侧路面标记表示可以暂时借用超车。', '旁边是实线就不能越过，而且右侧是非机动车道，亲要注意安全啊。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926235, '保持较大的跟车距离', '开启危险报警闪光灯', '不断变换远近光灯', '持续鸣喇叭提示前车', '1', 'http://images.juheapi.com/jztk/subject4/439.jpg', '在这种冰雪路面怎样跟车行驶？', '冰雪路面太滑了，离前车远点。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926236, '不开启灯光', '开远光灯', '开危险报警闪光灯', '开启示廓灯、后位灯', '7', '', '夜间路边临时停车，以下做法错误的是什么？', '夜间临时停车要开启危险报警闪光灯、示廓灯和后尾灯。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926237, '正确', '错误', '', '', '1', '', '驾驶汽车在交叉路口违法抢行容易引发交通事故。', '按规矩来行驶，尤其是交叉路口不能抢行。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926238, '减速避让', '向左打转向盘避让', '向右借用人行道避让', '迎着来车鸣喇叭将其逼回', '1', 'http://images.juheapi.com/jztk/subject4/442.jpg', '如图所示，驾驶机动车遇到对向来车正在强行超车时，以下做法正确的是什么？', '驾驶机动车遇到对向来车正在强行超车时，应当减速避让以防发生危险。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926239, '不超过60公里/小时', '不超过50公里/小时', '不超过40公里/小时', '不超过30公里/小时', '4', 'http://images.juheapi.com/jztk/subject4/443.jpg', '通过这种路面条件较好的窄桥怎样控制车速？', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时；', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926241, '靠近山体一侧的车危险性更高', '靠近山体一侧的车更加容易减速', '临崖一侧的车危险性更高', '临崖一侧的车更容易通过', '3', 'http://images.juheapi.com/jztk/subject4/445.jpg', '如图所示，驾驶机动车遇到这种情况，A车应当主动减速让行的原因是什么？', '山路上坡道转弯会车，驾驶机动车遇到这种情况，A车应当主动减速让行，因为临崖一侧的车危险性更高。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926242, '选择安全区域停车', '开启危险报警闪光灯、示廓灯和后位灯', '按规定设置警告标志', '给朋友打电话，坐在车内等待救援', '13', '', '驾驶机动车在夜间发生故障时，以下做法正确的是什么？', '驾驶机动车在夜间发生故障时，应当选择安全区域停车，同时开启危险报警闪光灯、示廓灯和后位灯，按规定设置警告标志，报警或者打给保险公司或者4S店等待救援。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926244, '先治伤，后救命', '先救命，后治伤', '先帮助轻伤员', '后救助重伤员', '2', '', '在事故现场抢救伤员的基本要求是什么？', '必须先救人，否则很有可能因为送医不及时而造成伤者死亡。而受伤的人员没有生命危险，可以暂缓一下。因此选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926246, '快速闪烁前照灯，提醒前方车辆快速驶离路口', '紧急制动保证车辆能够在停车线前停止', '降低车速确认安全后通过', '快速从前方车辆后方绕行', '3', 'http://images.juheapi.com/jztk/subject4/450.jpg', '遇到这样的路口，以下哪做法是正确的？', '图片分析：图中只有一个黄灯的路口，黄灯闪烁起警示作用，请降低车速确认安全后通过。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926247, '不停变换远近光灯以及鸣喇叭提醒对方', '视线适当右移，避免直视灯光', '降低车速，靠右行驶', '变换远光灯行驶', '10', '', '夜间驾驶机动车会车时，对方一直使用远光灯，以下做法正确的是什么？', '只有在没有路灯或者没有照明的情况下才能用远光灯，一般都用近光灯。夜间会车，若使用远光灯，会让对方完全看不清，进而引发交通事故。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926248, '会缩短发动机寿命', '增加车辆油耗', '会使制动器温度升高而使制动效能急剧下降', '容易造成车辆倾翻', '3', '', '驾驶机动车下长坡时，连续使用行车制动器，以下说法正确的是什么？', '驾驶机动车下长坡时，车子向下的冲力很大，采取制动，刹车垫与车轮轴就摩擦起热，刹车垫也磨损严重。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926250, '注意儿童', '禁止停车', '禁止鸣喇叭', '前方禁止通行', '4', 'http://images.juheapi.com/jztk/subject4/454.jpg', '如图所示，驾驶机动车行经该路段时，以下说法错误的是什么？', '驾驶机动车行经小学门口，应当注意儿童，同时右侧标志说明该路段禁止停车、禁止鸣喇叭。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926253, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/457.jpg', '驾驶机动车在这种情况下临时停车后，为避免机动车后溜可将转向盘向右转。', '这样做，与在轮胎前方塞东西是同样的道理，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926254, 'B车、A车、C车', 'C车、A车、B车', 'A车、B车、C车', 'A车、C车、B车', '3', 'http://images.juheapi.com/jztk/subject4/458.jpg', '如图所示，在这种无信号灯控制情况下，A、B、C车的通行权顺序是什么？', '交规说明，在这种无信号灯控制情况下应该转弯让直行，右转车辆要让左转车辆。所以A、B、C车的通行权顺序是：A车、B车、C车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926255, '注意非机动车辆的动向，减速行驶', '谨慎驾驶低速通过', '持续鸣喇叭警告非机动车避让', '保持与非机动车安全车距', '3', '', '驾驶机动车遇到非机动车违法在机动车道上行驶，并阻碍机动车前进时，以下做法错误的是什么？', '驾驶机动车遇到非机动车违法在机动车道上行驶，并阻碍机动车前进时，注意非机动车辆的动向，减速行驶；谨慎驾驶低速通过；保持与非机动车安全车距；避让非机动车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926259, '挂入空挡', '挂入低速挡', '踏下离合器踏板', '挂入高速挡', '2', '', '驾驶汽车在山区道路下陡坡时，怎样利用发动机制动控制车速？', '山区下坡路要挂低档，只有低档能控制车速。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926260, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/464.jpg', '因故障在山区下坡路段长时间停车时，要用这种办法塞住车轮。', '由于是下坡路，因此若是停在此处很容易前溜，而这样做能防止车辆向下溜滑，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926263, '变换使用远近光灯提示', '长时间鸣喇叭', '使用远光灯', '鸣喇叭并使用远光灯', '1', '', '夜间驾驶机动车遇到对向来车未关闭远光灯时，以下做法正确的是什么？', '记住，只有在没有路灯或者没有照明的情况下才能用远光灯，一般都用近光灯。窄路窄桥，你若用远光灯，会让对方完全看不清，掉到桥下去的。夜间驾驶机动车遇到对向来车未关闭远光灯时，变换使用远近光灯提示。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926265, '救护车违反交通信号通行，不予避让', '减速，避让救护车', '按照信号灯指示，正常通行', '加速通过', '2', 'http://images.juheapi.com/jztk/subject4/469.jpg', '如图所示，驾驶机动车遇到执行紧急任务的救护车时，以下做法正确的是什么？', '驾驶机动车遇到执行紧急任务的救护车时，应当减速，避让救护车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926266, '紧跟前车行驶', '加大安全距离', '减小跟车距离', '尽快超越前车', '2', 'http://images.juheapi.com/jztk/subject4/470.jpg', '在这种山区道路怎样跟车行驶？', '道路狭窄，除了B选项其他选项都不够安全。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926267, '减速行驶', '集中注意力谨慎驾驶', '注意观察，减速靠边停车', '以正常速度行驶', '3', 'http://images.juheapi.com/jztk/subject4/471.jpg', '如图所示，驾驶机动车在暴雨天气条件下行驶，当刮水器无法刮净雨水影响行车安全时，以下做法正确的是什么？', '驾驶机动车在暴雨天气条件下行驶，当刮水器无法刮净雨水影响行车安全时，应当注意观察，减速靠边停车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926270, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/474.jpg', '如图所示，起步时此灯亮起表示驻车制动（手刹）放下。', '此灯亮起是在提醒我们驻车制动没有放下，先放下它才能开车！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926271, '可以超车', '可以倒车', '依次通行', '可以掉头', '3', '', '驾驶机动车在匝道内行驶，以下做法正确的是什么？', '“匝道”是指立交桥或高架路的进出道引道。驾驶机动车在匝道内行驶，可依次通行，不可倒车、掉头、超车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926274, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/478.jpg', '路面标记指示前方路口仅允许车辆向右转弯。', '路面上的标志是禁止右转弯。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926276, '迅速关闭发动机', '用覆盖法灭火', '开启发动机罩灭火', '用灭火器灭火', '3', '', '驾驶机动车行驶过程中发动机着火，以下做法错误的是什么？', '驾驶机动车行驶过程中发动机着火，应当迅速关闭发动机，用覆盖法或者灭火器灭火。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926278, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/482.jpg', '前方路口停车让行线表示减速让干道车先行。', '应该是停车让干道车先行，而不是减速让行。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926280, '鸣喇叭从左侧超越自行车', '减速待自行车通过后再从B车左侧超越', '停车等待B车驶离后，在原车道行驶', '借用对向车道加速通过', '2', 'http://images.juheapi.com/jztk/subject4/484.jpg', '如图所示，A车正确的做法是什么？', 'B车发生故障，路边临时停车，A车应当减速待自行车通过后再从B车左侧超越。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926283, '可以超车', '可以倒车', '依次通行', '可以掉头', '3', '', '驾驶机动车在高速公路减速车道行驶时，以下做法正确的是什么？', '“匝道”是指立交桥或高架路的进出道引道。驾驶机动车在匝道内行驶，可依次通行，不可倒车、掉头、超车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926285, '减速，停车让行', '交替变换远近光灯加速通过', '连续鸣喇叭后通过', '确保安全加速通过', '1', 'http://images.juheapi.com/jztk/subject4/489.jpg', '如图所示，夜间驾驶机动车通过没有交通信号灯的人行横道时，以下做法正确的是什么？', '夜间驾驶机动车通过没有交通信号灯的人行横道时，应当减速，停车让行。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926286, '没有系安全带', '离转向盘距离过近', '没有握紧转向盘', '安全气囊没有打开', '1', 'http://images.juheapi.com/jztk/subject4/490.jpg', '事故中造成这个驾驶人致命伤害的原因是什么？', '有图可看出是驾驶员在冲撞下向前扑出，因为没有安全带的保护，才会致使驾驶员向前倾，因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926287, '避免紧急制动', '循车辙行驶', '避免急转方向', '降低车速行驶', '17', 'http://images.juheapi.com/jztk/subject4/491.jpg', '在这种有车辙的冰雪路段怎样行驶？', '安全第一', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926289, '电子不停车收费专用车道', '应急车道', '绿色通道', '快速公交车道', '1', 'http://images.juheapi.com/jztk/subject4/493.jpg', '以下这个标志的含义是什么？', '这个标志的含义是前方有电子不停车收费专用车道。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926290, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/494.jpg', '驾驶机动车在这个路口可以沿掉头车道直接掉头。', '《中华人民共和国道路交通安全法实施条例》第四十九条  机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。而本题的情况是在坡上，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926291, '连续变换远近光灯', '开启危险报警闪光灯', '使用远光灯，减速避让', '使用近光灯，减速避让', '4', '', '夜间驾驶机动车在窄路遇到对面驶来非机动车时，以下做法正确的是什么？', '夜间驾驶机动车在窄路遇到对面驶来非机动车时，使用近光灯，减速避让。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926292, '30米', '50米', '80米', '100米', '2', 'http://images.juheapi.com/jztk/subject4/496.jpg', '驾驶机动车行经此路段多少米内不得停车？', '根据交通法，机动车在道路上临时停车，应当遵守下列规定：(一)在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车；(二)交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；(三)公共汽车站、急救站、加油站、消防栓或者消防队(站)门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；(四)车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926293, '尽快将中毒人员移出毒区', '脱去接触有毒空气的衣服', '用清水清洗暴露部位', '原地等待救援', '4', '', '交通事故中急救中毒伤员，以下做法错误的是什么？', '交通事故中急救中毒伤员，需要：尽快将中毒人员移出毒区；脱去接触有毒空气的衣服；用清水清洗暴露部位。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926296, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/500.jpg', '驾驶机动车在该处不影响行人正常通行的情况下可以掉头。', '由图可知是黄色双实线，且本车道为直行车道，因此不能掉头，所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926297, '正确', '错误', '', '', '2', '', '高速公路行车发生火灾时，要将机动车驶进服务区或停车场灭火。', '服务区的车辆多，而且还有加油站，若是这样做会将损失扩大，因此不能这么做，正确的做法是将车停到紧急停车带内予以灭火，若是火势太大，就赶紧逃开吧。。。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926298, '应尽量匀速前进', '应尽量避免换挡', '时刻注意下行车辆', '应选择高速挡', '13', '', '驾驶机动车在山区上坡路段行驶，以下做法正确的是什么？', '驾驶机动车在山区上坡路段行驶，应该挂低档、尽量匀速前进，且途中减挡可能会导致机动车熄火。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926299, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/503.jpg', '该车道路面导向箭头指示前方路口仅可左转弯。', '图片的标志为掉头的标志。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926300, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/504.jpg', '路面同向车行道分界线指示允许跨越变换车道。', '实线不能跨越，故不能变换车道超车。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926301, '鸣喇叭提示行人后掉头', '对面黄色车辆通过后掉头', '行人通过后掉头', '继续直行，寻找可掉头路段', '4', 'http://images.juheapi.com/jztk/subject4/505.jpg', '如图所示，驾驶机动车在这个路段想要掉头时，以下做法正确的是什么？', '该路段为直行道路，不可掉头或者转弯，车辆想要掉头时需要继续直行，寻找下一个可掉头路段。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926302, '左前方行人可能在前方机动车驶过后马上横穿道路', '左前方行人对是否横穿马路可能犹豫不决，无法准确判断', '前方机动车可能遇有其他横穿道路的行人减速或紧急停车', '前方机动车可能躲避横穿道路的行人，突然变更车道', '17', 'http://images.juheapi.com/jztk/subject4/506.jpg', '如图所示，驾驶机动车遇到这种情况时，驾驶人应注意的是什么？ ', '前方有车辆且有行人欲横穿马路，驾驶机动车遇到这种情况时，驾驶人应注意：左前方行人可能在前方机动车驶过后马上横穿道路；左前方行人对是否横穿马路可能犹豫不决，无法准确判断；前方机动车可能遇有其他横穿道路的行人减速或紧急停车；前方机动车可能躲避横穿道路的行人，突然变更车道。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926303, '用清水喷洒扑灭', '及时报警', '使用车内备用的灭火器灭火', '在来车方向设置警告标志', '16', '', '机动车行驶时突然发生自燃，驾驶人采取的以下紧急避险措施中，正确做法是什么？', '水的密度比油大，不能用来灭火。因此选项A首先排除，BD是用来告知自燃地点，而C选项则是进行灭火。因此BCD都是正确的做法，即本题选BCD。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926305, '孙某违法停车', '孙某客车超员', '李某超速', '李某疲劳驾驶', '9', '', '某日3时40分，孙某驾驶大客车（乘载54人、核载55人）行至随岳高速公路229公里加300米处，在停车下客过程中，被后方驶来李某驾驶的重型半挂机动车追尾，造成26人死亡，29人受伤。事后查明，李某从昨日18时许出发，途中一直未休息。双方驾驶人的主要违法行为是什么？', '违法行为：1、高速公路上不允许停车下客。2、李某从昨日18时许出发，途中一直未休息。故属于疲劳驾驶。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926306, '从匝道驶入高速公路，应当开启右转向灯', '驶离高速公路进入匝道时，应当开启右转向灯', '可在匝道上超车', '驶入错误的匝道后，可倒车行驶回高速公路', '2', '', '关于在高速公路匝道路段行驶，以下说法正确的是什么？', '“匝道”是指立交桥或高架路的进出道引道。高架：进口路和出口路是分开的，只能顺行，车辆错过了下匝道，就不能从上匝道下路，只能从下一个出口下路。立交桥：匝道的进出，也是按照设定的标志行驶。不管是高架的匝道还是立交桥的匝道，都不能掉头、倒车、超车或是停车。驶离高速公路进入匝道时，应当开启右转向灯。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926307, '迅速向右方倒车躲避', '停车鸣喇叭提示', '迅速向左方倒车躲避', '直接倒车躲避', '2', 'http://images.juheapi.com/jztk/subject4/511.jpg', '如图所示，驾驶机动车发现前车向后溜车，以下做法正确的是什么？', '上坡路上，前车向后溜车，应当立刻停车鸣喇叭提示。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926308, '从左侧超越前车', '鸣喇叭催促前车向前行驶', '从右侧超越前车', '与前车保持安全距离,排队等待', '4', '', '驾驶机动车遇到前方车辆停车，等待行人通过人行横道时，以下做法正确的是什么？', '前车等待行人过道时，自己也应耐心等待，B做法不恰当，AC做法易引发交通事故。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926311, '减速慢行', '正常行驶', '加速通过', '连续鸣喇叭', '1', '', '驾驶机动车行经两侧有非机动车行驶且有积水的路面时，应怎样做？', '驾驶车辆行经两侧有行人或非机动车行驶且有积水的路面时。更应减速慢行，以免泥水飞溅到行人或非机动车驾驶员身上。当我们没车的时候是不是很讨厌那些开车溅我们一身水的人，所以不要当我们有自己的车的时候不要成为那时我们讨厌的人。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926312, '直行通过路口', '停在停止线外等待', '在路口向左转弯', '在路口减速慢行', '3', 'http://images.juheapi.com/jztk/subject4/516.jpg', '看到这种手势信号时怎样行驶？', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926313, '保持与前车的安全距离', '避免转弯时占道行驶', '上陡坡提前换低速挡', '下长坡时，充分利用发动机制动', '17', '', '在山区道路行驶时，驾驶人要注意什么？', '四个选项都是在山区道路安全驾驶的注意事项，因此本题选ABCD。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926314, '慢慢制动减速', '迅速制动减速', '迅速向右转向', '采取紧急制动', '1', '', '行车中发现左侧轮胎漏气时怎样处置？', '当车辆漏气时，引起轮胎不正常工作，因此此时就应该减速并找安全的地方停车。迅速向右转向或是紧急制动都有可能造成爆胎等状况的发生，因此不可行。选项A和B的差别在于是快还是要慢，若是动作过快也有可能造成爆胎，因此本题要选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926315, '正确', '错误', '', '', '2', '', '驾驶机动车的驾驶人不需要系安全带。', '开车一定要系安全带，不系属于违法行为。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926317, '减速并鸣喇叭提示', '交替变换远近光灯提示', '加速通过', '持续鸣喇叭催促', '1', 'http://images.juheapi.com/jztk/subject4/521.jpg', '如图所示，驾驶机动车遇到非机动车占道行驶时，以下做法正确的是什么？', '对于灯光不明的路口，应交替变换远近光灯进行提示；加速通过不利于行车安全；鸣喇叭不必持续进行；因此选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926318, '下坡路段车辆由于重力作用，车速容易过快', '下坡路段由于重力作用，车辆比平路时操控困难', '下坡路段车辆阻力很大', '下坡路段前车车速较快，难以超越', '7', '', '驾驶机动车在山区道路下坡路段尽量避免超车，以下说法正确的是什么？', '驾驶机动车在山区道路下坡路段尽量避免超车，因为下坡路段车辆由于重力作用，车速容易过快，车辆比平路时更难操控，此时超车容易发生危险。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926319, '前方行人可能滞留在人行横道内', '左前方骑自行车者可能突然右转弯', '右前方骑摩托车者可能突然向左变更车道横穿道路', '右前方白色机动车行驶动态', '17', 'http://images.juheapi.com/jztk/subject4/523.jpg', '如图所示，驾驶机动车驶近这样的人行横道时，驾驶人应注意的是什么？', '驾驶机动车驶近这样的人行横道时，驾驶人应注意：前方行人可能滞留在人行横道内；左前方骑自行车者可能突然右转弯；右前方骑摩托车者可能突然向左变更车道横穿道路；右前方白色机动车行驶动态。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926321, '迅速跳车逃生', '双手紧握转向盘', '双脚勾住踏板', '背部紧靠椅背', '16', '', '驾驶机动车突然发生倾翻时，以下做法正确的是什么？', '驾驶机动车突然发生倾翻时，应当双手紧握转向盘、双脚勾住踏板、背部紧靠椅背，在车辆停稳前切不可跳车逃生，以防发生被砸或者碾压危险。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926322, '停在平坦的地方，在启动前检查', '停在平坦的地方，在怠速状态下检查', '无需停在平坦的地方，在启动前检查', '无需停在平坦的地方，在怠速状态下检查', '1', '', '检查机动车机油时，以下做法正确是什么？', '对的检查的方式是需要注意检查的条件.千万不要在汽车发动的时候去检查。检查的时候要把车先停好，然后拉紧手刹，再取出机油尺，先族干净后放进去，然后再重新拿出来检查机油油位。如果在机油尺下端的标记之间.就表示了油且是正常的。如果在标记下面，就表示机油留太少。机油太多的话就会出现在机油最在标记上面。如果少了就需要适里添加机油。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926323, '左侧来车应该给己车让行', '己车应该给左侧来车让行', '不需让行，谁车速快谁先过', '不需让行，己车有优先通行权', '2', 'http://images.juheapi.com/jztk/subject4/527.jpg', '如图所示，驾驶机动车遇到这种主路左侧来车的情况，以下说法正确的是什么？', '图中路考为辅路近主路，此时己车应该给左侧来车让行。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926324, '轮胎磨损严重', '轮胎气压过高', '尖锐物体刺伤轮胎', '车辆超载超员', '17', '', '以下哪些能够引起轮胎爆裂？', '引发轮胎爆裂的几种情况：轮胎磨损严重，轮胎气压过高，尖锐物体刺伤轮胎，车辆超载超员。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926325, '正确', '错误', '', '', '1', '', '行车前应对机动车驾驶室、发动机舱、车外部、轮胎进行检查。', '驾驶室内检查包括制动、离合、转向，灯光，油量、电路等，发动机舱里包括机油，水等。一辆车的全部检查都在这里了。所以只能是对的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926328, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/532.jpg', '路面可变导向车道线指示可以随意选择通行方向。', '可变车道，是指车道内侧划了多条斜线，有点像趴下的“非”字，顾名思义就是能随时根据交通流量更改指示方向的车道。这个主要是针对部分高峰时段车流集中，但车道偏少；或者早晚高峰时段来回车流量有明显差异的路段。碰到这种车道，根据可变车道的指示走就行了。是有时段限制的，不是任何时候都可以随意选择通行方向。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926329, '正确', '错误', '', '', '2', '', '机动车落水后，要迅速关闭车窗阻挡车内进水，短暂闭绝空气，可打电话告知救援人员失事地点，等待救援。', '你要是有把握在短时间内被救起来当然是这样最好了，但是在正常情况下，你一旦进入河里并且封闭了空间，那么凭普通人的力气根本就不能再次打开车门了，最后活活的闷死在里面。最安全的方法，还是打开车门快速逃生，车坏了就坏了吧，破财免灾。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926330, '150米以内', '150米以外', '100米以内', '50米以内', '2', '', '夜间驾驶机动车在道路上会车，为避免对方驾驶人眩目，应距离对向来车多远改用近光灯？', '夜间驾驶机动车在道路上会车，为避免对方驾驶人眩目，应距离对向来车150米以外改用近光灯。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926331, '减速、观察、慢行', '鸣喇叭驱赶牲畜', '从牲畜的空隙中穿过', '低速行驶冲开牲畜群', '1', 'http://images.juheapi.com/jztk/subject4/535.jpg', '驾驶机动车看到这种标志需要注意什么？', '如图所示的交通标志是注意牲畜标志，因此在此路段要注意减速慢行，同时注意观察有没有牲畜穿出，因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926335, '下车的乘客可能从公交车前方横穿道路', '公交车可能即将启动并向左变更车道', '右侧摩托车可能驶入机动车道并穿插变更车道', '对向车道内的机动车可能违法跨越道路中心线超车', '17', 'http://images.juheapi.com/jztk/subject4/539.jpg', '如图所示，驾驶机动车驶近公交车站时，驾驶人应注意的是什么？', '驾驶机动车驶近公交车站时，驾驶人应注意：下车的乘客可能从公交车前方横穿道路；公交车可能即将启动并向左变更车道；右侧摩托车可能驶入机动车道并穿插变更车道；对向车道内的机动车可能违法跨越道路中心线超车。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926337, '加速通过', '连续鸣喇叭警示', '停车让行', '迅速超越前方非机动车', '14', 'http://images.juheapi.com/jztk/subject4/541.jpg', '如图所示，驾驶机动车遇到这种情形时，以下做法错误的是什么？', '图中机动车遇到了三个状况：有行人穿过、过人行横线、有自行车行车，左侧有车辆汇入，此时驾驶员应当首先停车让行。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926339, '超速行驶', '客车超员', '驾驶逾期未年检机动车', '操作不当', '2', '', '何某驾驶一辆乘载53人的大客车（核载47人），行至宁合高速公路南京境内454公里加100米处，被一辆重型半挂牵引车追尾，导致大客车翻出路侧护栏并起火燃烧，造成17人死亡、27人受伤。何某的主要违法行为是什么？', '核载47人实际乘载53人题干主要透漏的是这个违法信息！', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926340, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/544.jpg', '驾驶机动车遇到这种道路要提前减挡，以保持充足动力。', '如图可知，前方道路是上坡路，因此要提前减档，避免因动力不足后溜。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926343, '正确', '错误', '', '', '2', '', '驾驶人一边驾车，一边吸烟对安全行车无影响。', '一心两用妨碍安全驾驶，容易发生危险。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926344, '注意观察', '从一侧加速绕过', '谨慎驾驶', '做好停车准备', '15', '', '驾驶机动车过程中遇到专注于使用手机的行人时，以下说法正确的是什么？', '驾驶机动车过程中遇到专注于使用手机的行人时，应当注意观察、谨慎驾驶，随时做好停车准备。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926345, '在有让超车条件，保证安全的情况下，减速靠右让路', '加速行驶，使他不能超越', '开启危险报警闪光灯，暗示他不要超越', '向左行驶，阻止他超越', '1', '', '驾驶机动车发现后车开启左转向灯发出超车信号时，以下做法正确的是什么？', '行车过程中，要时刻确保安全第一，BCD三个选项都不利于安全行车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926346, '与前车保持50米以上的距离', '车速不得超过40公里/小时', '开启危险报警闪光灯', '在应急车道行驶', '13', '', '驾驶机动车在高速公路上行驶，遇有能见度小于100米的气象条件时，如何安全行驶？', '《中华人民共和国道路交通安全法实施条例》第八十一条：“机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926352, '60公里/小时', '50公里/小时', '40公里/小时', '30公里/小时', '3', 'http://images.juheapi.com/jztk/subject4/556.jpg', '如图所示，驾驶机动车驶入减速车道后最高时速不能超过多少？', '右侧方有限速标志，限速40公里/小时。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926353, '加速缩短会车时间', '减速靠右行驶', '保持安全间距', '注意避让非机动车', '16', 'http://images.juheapi.com/jztk/subject4/557.jpg', '在这种情况下怎样安全会车？', '因为前边有自行车为了安全一定要慢，不要加速缩短会车时间。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926355, '与前车保持足以采取紧急制动措施的安全距离', '与前车保持较近距离，以防加塞', '将注意力全部集中在所跟随的车辆上', '将注意力全部集中在后方的车辆上', '1', '', '关于驾驶机动车跟车行驶，以下做法正确的是什么？', '驾驶机动车跟车行驶时，应当与前车保持足以采取紧急制动措施的安全距离。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926356, '驶入紧急避险车道', '使用发动机制动', '使用驻车制动器制动', '必要时，可用车体刮擦路边障碍物减速', '17', '', '驾驶机动车在下坡行驶过程中行车制动器失效，以下做法正确的是什么？', '以上四个选项都可以在行车制动器失效的情况下起到制动的效果，因此应该选ABCD。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926357, '紧跟前车通过交叉口', '减速做好停车准备', '立即变更车道', '立即停车', '2', 'http://images.juheapi.com/jztk/subject4/561.jpg', '如图所示，驾驶机动车跟车行驶遇到前车遮挡路口交通信号灯时，以下做法正确的是什么？', '驾驶机动车跟车行驶遇到前车遮挡路口交通信号灯时，应当减速行车，注意观察，做好停车准备。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926358, '紧跟在后面行驶', '从左侧加速让过', '鸣喇叭示意让道', '减速或停车避让', '4', 'http://images.juheapi.com/jztk/subject4/562.jpg', '驾驶机动车看到这样的儿童怎样行驶？', '遇到这样的情况要减速慢行，必要时停车避让，以免孩子从滑板上掉下来或是移动位置。因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926360, '视线不清，不能及时发现行人', '行人可能滑倒', '行人可能突然进入行车道', '行人可能会横过道路', '17', 'http://images.juheapi.com/jztk/subject4/564.jpg', '如图所示，驾驶机动车在雨天行驶，驾驶人应当注意的是什么？', '下雨天驾驶人应当注意：视线不清，不能及时发现行人；行人可能滑倒；行人可能突然进入行车道；行人可能会横过道路。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926362, '正确', '错误', '', '', '2', '', '驾驶机动车从辅路汇入主路车流时要迅速。', '驾车要谨慎，不要那么“迅速”！注意是汇入车流，所以正确的做法是要减速慢行，在不影响其他车辆正常行驶的情况下汇入主路，因此本题选B。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926365, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/569.jpg', '夜间行车，后方车辆提示超车，前方遇到这种情况时不能盲目让超。', '让超车时是要在条件允许的情况下减速靠右行，而图中的情况则是前方有行人及非机动车，因此不能盲目让超。所以本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926368, '持续鸣喇叭警示', '交替使用远近光灯提示', '使用近光灯，减速靠右避让', '加速通过', '3', '', '夜间驾驶机动车在农村道路行驶，遇到对向驶来畜力车时，以下做法正确的是什么？', '夜间驾驶机动车在农村道路行驶，遇到对向驶来畜力车时，应当使用近光灯，减速靠右避让。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926369, '在不具备超车条件的急弯路段加速超车', '驾驶逾期未检验的机动车', '驾驶与准驾车型不符的机动车', '疲劳驾驶', '8', '', '赵某（持有A2驾驶证）驾驶大型卧铺客车（核载36人），行驶至叶城县境内某处急弯路段，加速超越前车时，坠入道路一侧山沟，致16人死亡，20人受伤。赵某的主要违法行为是什么？', '赵某的主要违法行为是在不具备超车条件的急弯路段加速超车和驾驶与准驾车型不符的机动车。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926372, '正确', '错误', '', '', '2', '', '安全头枕要调整到与颈部平齐的高度。', '应该与头平齐。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926373, '人行横道', '停车场', '山区容易塌方、泥石流路段', '道路施划的停车泊位内', '8', '', '以下什么地点不能停车?', '人行横道，山区容易塌方、泥石流路段。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926374, '道路左侧儿童可能突然跑进路中', '前方行人可能未察觉有机动车驶近', '迎面来车可能造成会车困难', '右侧停放的机动车可能会突然起步', '17', 'http://images.juheapi.com/jztk/subject4/578.jpg', '如图所示，驾驶机动车遇到这种情况时，驾驶人应注意的是什么？', '图中左侧有儿童，前方有来向车辆和行人，右侧有停放车辆，驾驶机动车遇到这种情况时，驾驶人应注意：道路左侧儿童可能突然跑进路中，前方行人可能未察觉有机动车驶近，迎面来车可能造成会车困难，右侧停放的机动车可能会突然起步。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926375, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/579.jpg', '高速公路两侧白色半圆状的间隔距离是50米。', '白色半圆状车距确认线：白色半圆形车距确认线半球半径为30cm，间隔50米设置，一般在一定路段内连续设置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926377, '正确', '错误', '', '', '1', '', '驾驶客车遇非常情况或者发生事故时，要力所能及的将损失降到最低限度，决不能因紧急避险造成二次事故或更大的损失。', '要以人为本，要以将伤害和损失降到最低为原则。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926378, '行人通过速度较慢可能滞留在道路内', '电动自行车可能发生故障无法及时通过道路', '右侧驶来的机动车可能未能及时停车而进入路口', '考虑雨天路面湿滑，应注意按照限速规定控制车速', '17', 'http://images.juheapi.com/jztk/subject4/582.jpg', '如图所示，驾驶机动车遇到这种情况时，应注意哪些可能发生的危险？ ', '雨天行车，路人、电动车横穿人行横线时红灯变绿灯，驾驶机动车遇到这种情况时，应注意可能出现以下情况：行人通过速度较慢可能滞留在道路内、电动自行车可能发生故障无法及时通过道路、右侧驶来的机动车可能未能及时停车而进入路口，考虑雨天路面湿滑，应注意按照限速规定控制车速。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926380, '疏散人员', '开启危险报警闪光灯', '标记伤员的原始位置', '正确放置危险警告标志', '14', '', '发生交通事故后，防止二次事故的有效措施是什么？', '选项A/B/D都是防止二次事故的有效措施，而选项C则让人有种要明确责任归属的感觉了。因此本题选ABD。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926382, '加速行驶，在对面来车交会前超过行人', '减速靠右，等对向车辆通过后，再缓慢超越行人', '鸣喇叭提示行人后，保持原速行驶', '鸣喇叭提示左侧车辆后，保持原速行驶', '2', 'http://images.juheapi.com/jztk/subject4/586.jpg', '如图所示，驾驶机动车遇到这种情形时，以下做法正确的是什么？', '驾驶机动车遇到三个状况：对面来车，前方左侧车辆汇入主路、前方右侧有行人，此时机动车驾驶员应当减速靠右，等对向车辆通过后，再缓慢超越行人。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926383, '放气减压', '及时换胎', '给轮胎充气', '不用更换', '2', '', '如果轮胎胎侧顺线出现裂口，以下做法正确的是什么？', '都出现裂口了.肯定需要更换，以免爆胎，安全第一，不要为了省钱连命都搭进去。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926384, '前方左侧是傍山险路', '靠路左侧行驶', '选择路中心行驶', '靠右侧低速通过', '4', 'http://images.juheapi.com/jztk/subject4/588.jpg', '驾驶机动车遇到这种山路怎样通过？', '注意本题的考点是如何通过，而不是问前方是什么路段。另外前方有来车，因此要会车，而会车时要减速靠右侧行驶，因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926386, '设有电子不停车收费（ETC）车道的收费站', '停车领卡标志', '服务区标志', '紧急停车带', '1', 'http://images.juheapi.com/jztk/subject4/590.jpg', '以下这个标志的含义是什么？', '图中标志表示前方设有电子不停车收费（ETC）车道的收费站。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926387, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/591.jpg', '右侧标志表示临时停车不受限制。', '禁止车辆临时或长时停放-表示在限定的范围内，禁止一切车辆临时或长时停放。此标志设在禁止车辆停放的地方。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926388, '加速通过', '占用对向车道', '靠边停车', '减速靠右通过', '4', 'http://images.juheapi.com/jztk/subject4/592.jpg', '如图所示，驾驶机动车遇弯道会车时，以下做法正确的是什么？', '驾驶机动车遇弯道会车时，应当减速靠右通过。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926389, '持续鸣喇叭警告', '保持车速继续行驶', '与其抢行', '加速靠左侧绕行', '17', 'http://images.juheapi.com/jztk/subject4/593.jpg', '如图所示，A车在交叉路口左转时遇到B车强行超越，以下做法错误的是什么？', '在路口遇到特殊情况，优先要做的都是减速慢行，不要加速、抢行和鸣笛，确保安全下通行。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926390, '加塞抢行', '不强行超车', '停车或依次行驶', '不占用非机动车道行驶', '1', '', '发现前方机动车停车排队缓慢行驶时，属于交通陋习的是什么行为？', '加塞抢行只会让交通更拥堵，因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926391, '提前开启左转向灯', '提前鸣喇叭', '确认前车让超后超越', '直接加速超越', '13', '', '驾驶机动车在山区路段超车时，以下做法正确的是什么？', '《中华人民共和国道路交通管理条例》第五十条：机动车超车，必须遵守下列规定:行经交叉路口、人行横道、漫水路、漫水桥等，不准超车。驾驶机动车在山区路段超车时，需要注意：提前开启左转向灯；提前鸣喇叭；确认前车让超后超越。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926392, '对向可能有车辆驶来', '前方骑自行车者可能由于上坡等原因突然改变方向', '山区弯道可能转弯半径较小，车速过快容易引起车辆失控', '转弯后路面可能存在落石、凹陷等特殊路况', '17', 'http://images.juheapi.com/jztk/subject4/596.jpg', '如图所示，驾驶机动车驶近这样的山区弯道时，驾驶人应注意的是什么？', '山区弯道情况多变，从图中也可以看到道路上有自行车，因此以上四项都需要注意。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926394, '正确', '错误', '', '', '1', '', '行车中不开转向灯强行并线是违法行为。', '根据有关规定，强行并线是违法行为，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926395, '减速缓慢通过', '鸣喇叭警示牲畜，以免牲畜冲入行车道发生事故', '不能鸣喇叭，避免牲畜因惊吓窜入行车道', '加速通过此事故隐患路段', '8', 'http://images.juheapi.com/jztk/subject4/599.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？', '不能鸣喇叭。避免动物因惊吓窜入行车道，保持一定的安全距离，停车让行等待动物穿过。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926396, '不得妨碍主路车辆正常行驶', '只要不发生事故可随意行驶', '可以碾压实线及导流线', '在不发生事故的前提下干扰主路车流也是可以的', '1', '', '关于驾驶机动车汇入主路车流，以下说法正确的是什么？', '选项B太不和谐了肯定不对,实线是不可以碾压的，D也一样不和谐，所以选A，而且A非常之和谐。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926397, '停车等待', '直行通过路口', '在路口向右转弯', '在路口向左转弯', '1', 'http://images.juheapi.com/jztk/subject4/601.jpg', '看到这种手势信号时怎样行驶？', '举手朝向您，无其他动作时是让告诉您停止。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926398, '鸣喇叭，加速通过', '及时开启远光灯', '使用近光灯，低速会车或停车让行', '使用远光灯，低速会车', '3', '', '夜间会车时，如遇对方持续开启远光灯，应当如何安全会车？', '夜间安全驾驶知识中第三条就有:“夜间会车应当在距对方来车150米以外改用近光灯，若对方车辆不关闭远光灯，可连续变换灯光提示对向车辆。当遇对面来车仍不关闭远光灯时，应及时减速靠右侧行驶或停车让行。”', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926399, '拉起驻车制动器', '开启危险报警闪光灯', '在后方用塞车木或石块塞住车轮，以防车辆后溜', '按规定在车后方设置警示标志', '17', '', '驾驶机动车在山区道路上坡路段行驶，因发生故障需停车检修时，以下做法正确的是什么？', '驾驶机动车在山区道路上坡路段行驶，因发生故障需停车检修时，需要：拉起驻车制动器；开启危险报警闪光灯；在后方用塞车木或石块塞住车轮，以防车辆后溜；按规定在车后方设置警示标志。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926400, '敲碎侧窗玻璃', '关闭车窗', '打电话求救', '用工具撬开车门', '1', '', '机动车不慎落水，车门无法开启时，可选择的自救方法是什么？', '如果进入到水里，你还能开门吗？不能吧，还有时间打电话吗？等你打完电话都不知道沉到哪里去了，关闭车门阻隔水吗？你以为是密封舱啊，就是给你密封了，氧气一下子就用完了，不还是等死，唯一可以做的就赶紧逃生，只能是开窗或者砸玻璃啊，第一时间逃跑才有生的希望啊，一下子沉的时候如果发动机没关车窗还是可以降下来的，或者手动的就更加容易把窗口打开了。 因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926401, '超速行驶', '疲劳驾驶', '客车超员', '驾驶具有安全隐患的机动车', '9', '', '李某驾驶一辆大客车，乘载21人（核载35人），行驶途中察觉制动装置有异常但未处理，行至双岛海湾大桥时时速为50公里（该路段限速40公里），因制动失灵坠入海中，造成13人死亡、8人受伤。李某的主要违法行为是什么？', '违法行为：1、察觉制动装置有异常但未处理，所以为驾驶故障机动车。2、行至双岛海湾大桥时时速为50公里(该路段限速40公里)所以为超速行驶。此题注意核载35人就是规定可以做35个人，实际乘坐21人所以没有客车超员。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926402, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/606.jpg', '如图所示，驾驶机动车在公交车站遇到这种情况要迅速向左变更车道绕行。', '减速慢行，注愈观察，预横穿的行人。拉开横向安全距离。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926403, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/607.jpg', '前方标志指示高速公路的名称和编号。', '没问题，此题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926404, '减速慢行', '注意观察', '拉开横向安全距离', '预防突然横穿的行人', '17', 'http://images.juheapi.com/jztk/subject4/608.jpg', '如图所示，驾驶机动车遇到这种情形，应如何安全通过？', '安全第一，谨慎驾驶，符合这些的都选。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926405, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/609.jpg', '右侧标志表示前方道路限宽3米。', '限制宽度-表示禁止装载宽度超过标志所示数值的车辆通行。此标志设在最大允许宽度受限制的地方。 以图为例：装载宽度不得超过3米', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926408, '停车后立即开门下车', '观察前方交通情况', '先开车门再观察侧后情况', '先观察侧后情况，再缓开车门', '4', '', '驾乘人员下车时要怎样做以保证安全？', '切记安全第一，所以选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926409, '必要时安装防滑链', '必须降低车速', '开启雾灯行驶', '利用发动机制动', '14', '', '驾驶汽车在冰雪道路上怎样安全行车？', '雪天不用开雾灯，开近光就好。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926414, '直行通过路口', '停车等待', '在路口向右转弯', '在路口向左转弯', '2', 'http://images.juheapi.com/jztk/subject4/618.jpg', '看到这种手势信号时怎样行驶？', '交警的面部对着哪个方向就是在指挥哪个方向的车，所以对于这张图片来说，就是在指示交警面部对着的那个方向的车左转，对你来说需要停车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926415, '前车左侧车门可能突然打开', '前车前方可能有行人横穿马路', '前车可能突然掉头', '前车可能突然倒车', '17', 'http://images.juheapi.com/jztk/subject4/619.jpg', '如图所示，驾驶机动车遇到前方道路中间有停驶车辆时，以下可能出现的危险情形有哪些？', '驾驶机动车遇到前方道路中间有停驶车辆时，应减速行车，注意安全，防止前车左侧车门突然打开、前车前方有行人横穿马路、前车突然倒车和掉头等情况发生。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926416, '继续驶入拥堵路口', '鸣喇叭让路口内的车让行', '快速驶入路口', '让路口内的车先行', '13', '', '驾驶机动车准备进入拥堵的环形路口时，以下做法错误的是什么？', '驾驶机动车准备进入拥堵的环形路口时，应当注意观察让路口内的车先行。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926417, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/621.jpg', '如图所示，红色汽车在此地点停车等候是违法行为。', '红色车右侧有禁止停车标志，所以在此停车是违法行为.', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926418, '保持较低车速通过', '停车让行，等行人通过后再通过', '连续鸣喇叭冲开人群', '确保安全的前提下绕行通过', '2', 'http://images.juheapi.com/jztk/subject4/622.jpg', '如图所示，驾驶机动车通过交叉路口时右转遇到人行横道有行人通过时，以下做法正确的是什么？', '驾驶机动车通过交叉路口时右转遇到人行横道有行人通过时，应当停车让行，等行人通过后再通过。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926419, '正确', '错误', '', '', '1', '', '制动时后车轮抱死可能会出侧滑甩尾的情况。', '所谓“侧滑”就是车辆急加速、突然制动或启动时扭矩过大而产生的侧向甩动的现象，俗称“甩尾”。造成车辆侧滑的因素很多。雪天，雨天、冰面上行车时突然抬起油门或突然加速；采取紧急制动，制动时后轮比前轮先抱死；都极易发生车辆侧滑而造成事故。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926420, '张某疲劳驾驶', '李某用私家车拖曳故障车辆', '刘某超速行驶', '未在故障车辆后设置警示标志', '16', '', '张某驾驶车辆在高速公路上发生故障不能移动，开启危险报警闪光灯后下车，联系朋友李某驾驶私家车帮忙拖曳到应急车道。李某拖曳故障车的过程中，刘某驾驶货运车辆以每小时110公里的速度驶来，导致三车相撞。这起事故中的违法行为有哪些？', '题中所示的违法行为主要有：张某未在故障车辆后设置警示标志、李某用私家车拖曳故障车辆、刘某超速行驶等。高速公路上发生故障，正确的做法是，开启危险报警闪光灯后下车，在故障车辆后设置警示标志，联系保险公司或者维修店进行拖曳维修。货运车辆在高速上最高车速不得超过100公里/小时。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926422, '转弯前减速', '进入弯道后加速', '靠路右侧行驶', '挂空挡滑行', '8', '', '驾驶汽车在山区道路转弯下陡坡路段遇对面来车怎样行驶？', '下陡坡不能挂空挡，过弯道不能加速。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926425, '停车场', '道路施划的停车泊位内', '人行横道', '施工路段', '7', '', '应该选择什么地点停车?', '停车场。道路施划的停车泊位内。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926428, '减速缓慢通过', '鸣喇叭警示牲畜，以免牲畜冲入行车道发生事故', '不能鸣喇叭，避免牲畜因惊吓窜入行车道', '加速通过此事故隐患路段', '8', 'http://images.juheapi.com/jztk/subject4/632.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么?', '不能鸣喇叭。避免动物因惊吓窜入行车道，保持一定的安全距离，停车让行等待动物穿过。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926429, '气压不稳', '气压增高', '行驶阻力减小', '爆胎', '4', '', '轮胎气压过低时，高速行驶可能导致什么结果？', '轮胎气压过低，在车子行驶过程中，会让轮胎不正常工作，若是高速行驶的话，产生摩擦之后就很容易发生爆胎了。因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926431, '易导致后车追尾', '会相应增大油耗', '易产生侧滑', '会相应减少油耗', '8', '', '雨天驾驶机动车，不可以急踩制动踏板的主要原因是什么?', '在干燥路面驾驶，车速提高，车轮与路面间的附着力(俗称“抓地力“)几乎没有变化。而雨天，当车辆在潮湿路上行驶，车轮的抓地力则随车速的增加而急剧变小，很容易发生“水滑”，此时，不要急踩制动踏板或猛打转向盘。由于雨天路面情况的特殊性，行车时应适当降低车速。准备停车时最好提前100-200米开始减速，给后方的车辆留出足够的反应时间，也为自己留够制动距离，以免发生追尾。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926432, '车上人员要迅速从左侧车门离开', '在来车方向100 米处设置警告标志', '开启危险报警闪光灯和远光灯', '车上人员站到护栏以外安全的地方', '4', '', '大雾天在高速公路遇事故不能继续行驶时怎样处置？', '标准程序是：当车辆发生故障时，打开双闪警示灯，应将车辆停放在最右侧的应急车道或紧急停车带内，夜间打开尾灯示宽灯，司乘人员应立即下车，在路沿下护坡内等候(尽量远离高速公路路面、护栏）救援车辆、人员来，由一个人在车辆尾部150米左右放置三角警示牌或警示物（最好隔十米左右放置一个警示物），切记不能在高速公路内走动、追逐、等候！不能在故障车辆内等候！一般情况禁止在高速公路上修理车辆。因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926433, '我方车辆灯光照向路外，前方即将进入弯道', '前方有灯光出现，可能即将发生会车', '左前方视线受阻，转弯后可能遇到突发情况', '为提高会车安全，改用远光灯', '13', 'http://images.juheapi.com/jztk/subject4/637.jpg', '如图所示，驾驶机动车遇到这种情况时，应注意哪些情况？', '在没有灯光的道路，处于弯道，并且由于看到对面车辆的灯光可以知道即将发生会车的情况，因此保持警惕，ABC选项正确，D选项开启远光灯容易影响对方视线造成危险，因此不选。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926435, '超车时从前车左侧超越', '超车时从前车右侧超越', '超车完毕，立即开启右转向灯驶回原车道', '超车完毕，与被超车拉开必要的安全距离后开启右转向灯驶回原车道', '9', '', '关于超车，以下说法正确的是什么?', '《道路交通安全法实施条例》第四十七条：机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。在没有道路中心线或者同方向只有1条机动车道的道路上，前车遇后车发出超车信号时，在条件许可的情况下。应当降低速度、靠右让路。后车应当在确认有充足的安全距离后，从前车的左侧超越。在与被超车辆拉开必要的安全距离后。开启右转向灯，驶回原车道。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926436, '不能准确判断周围的车辆距离', '周围车辆驾驶人不容易看清超车信号', '道路湿滑，车辆易出现侧滑现象', '不能够及时发现危险情况', '17', '', '雨天驾驶机动车，不宜超车的主要原因是什么?', '雨中行车，要随时注意前车的行驶速度和方向，绝不可因前车速度慢而加速超车。尤其是在高速公路上，由于各车道的车速相对较高，司机的视角变窄，加上路面湿滑，强行越线超车时，稍动方向就很容易造成车轮打滑，极易造成与其他车辆发生刮蹭，引发车辆侧翻等意外事故。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926437, '30米', '50米', '80米', '100米', '2', '', '驾驶机动车在距离隧道前多少米内不得停车？', '《道路交通安全法实施条例》第六十三条：交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926439, '上坡路段的安全距离应比平坦路段的大', '下坡路段的安全距离应比平坦路段的小', '急弯路段应当紧随前车', '以上说法都正确', '1', '', '在山区道路行驶时，以下说法正确的是什么？', '上坡路段的安全距离应比平坦路段的大。上坡需要加速，为了避免和前车发生追尾，需要加大安全距离。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926440, '对', '错', '', '', '2', 'http://images.juheapi.com/jztk/subject4/644.jpg', '如图所示，在这种情况下可以在公交车站临时停车。', '如图所示，右侧是公交车站，根据《道路交通安全法实施条例》第六十三条机动车在道路上临时停车，应当通守下列规定:(三)公共汽车站、急救站、加油站、消防栓或者消防队(站)门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926441, '加速直接汇入车流', '开启转向灯观察主路情况确保安全汇入车流', '开启转向灯直接汇入车流', '不用开启转向灯加速汇入车流', '2', '', '驾驶机动车应当怎样汇入主路车流?', 'B是最和谐的，也是最安全的，所以选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926443, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/647.jpg', '驾驶机动车在路口遇到这种情况要随时准备停车礼让。', '由图可知，此交叉路口是没有信号灯的。因此在这样的情况下要注意避让，必要时停车避让。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926446, '沿道路右侧行驶', '挂低速档减速通过', '沿道路左侧行驶', '挂高速档加速通过', '7', 'http://images.juheapi.com/jztk/subject4/650.jpg', '怎样安全通过这种较窄的弯道？', '右侧标志是上坡路，所以要挂低档沿右侧行驶。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926447, '迅速关闭发动机', '用水进行灭火', '开启发动机罩灭火', '站在下风处灭火', '1', '', '发动机着火后首先怎样处置？', '试题问的是首选做什么，第一步当然是迅速关闭发动机。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926448, '正确', '错误', '', '', '2', '', '发动机着火时，要迅速关闭发动机，开启发动机罩进行灭火。', '开发动机罩后氧气就会迅速涌入，引起更大的火灾。 所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926449, '停车等待动物穿过', '鸣喇叭驱赶动物', '下车驱赶动物', '与动物保持较远距离', '9', 'http://images.juheapi.com/jztk/subject4/653.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？', '不能鸣喇叭。避免动物因惊吓帘入行车道.保持一定的安全距离，停车让行等待动物穿过。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926450, '疲劳驾驶', '客车超员', '超速行驶', '操作不当', '7', '', '某日19时，杨某驾驶大客车，乘载57人（核载55人），连续行驶至次日凌晨1时,在金城江区境内050国道3008公里加110米处，因机动车左前胎爆裂，造成12人死亡、22人受伤的特大交通事故。杨某的主要违法行为是什么？', '违法行为：1、题目提到连续行驶到次日凌晨1时，属疲劳驾驶。2、题目提到只能做55以上，却坐了57人，属客车超员。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926451, '对', '错', '', '', '2', '', '在路口掉头时，可以不避让直行车辆。', '不得妨碍正常车辆通行，直行有优先行驶权。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926453, '从其前方绕过', '从其身后绕行', '鸣喇叭提醒', '主动停车礼让', '4', 'http://images.juheapi.com/jztk/subject4/657.jpg', '驾驶机动车遇到这样的行人怎样行驶？', '遇到这种情况要减速慢行，必要时停车，让其先通过。因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926455, '立即超越', '连续鸣喇叭提醒', '保持安全距离超越', '鸣喇叭加速超越', '3', 'http://images.juheapi.com/jztk/subject4/659.jpg', '驾驶机动车在这种情况下正确的做法是什么？', '如图所示，前方有来车，且同向前方有非机动车，因此在超车时要注意一定的安全距离，所以本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926456, '在从动轮下铺垫砂石', '换高速挡加速猛冲', '在驱动轮下铺垫砂石', '猛打转向盘配合急加速', '3', '', '在泥泞路段遇驱动车轮空转打滑时如何处置？', '注意是驱动轮空转打滑，因此应该在驱动轮上下手，因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926457, '加速，并超越白色汽车通过路口', '减速，注意避让', '持续鸣喇叭，并保持原有车速', '用车灯闪烁，示意对方让行', '2', 'http://images.juheapi.com/jztk/subject4/661.jpg', '如图所示，驾驶机动车遇到左侧支路白色汽车不减速让行时，以下做法正确的是什么？ ', '驾驶机动车遇到左侧支路白色汽车不减速让行时，为保证自身行车安全，机动车应当减速，注意避让。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926458, '应提前降低车速', '应提前降低挡位', '尽量利用发动机制动控制车速', '尽量利用驻车制动器控制车速', '4', 'http://images.juheapi.com/jztk/subject4/662.jpg', '如图所示，驾驶机动车遇到这种路段时，以下说法错误的是什么？', '上图标志，下坡路，连续使用制动器，致使车轮制动器发热而降低制动效能。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926459, '对', '错', '', '', '1', '', '前方遇有大型拉土（石）货车，应当尽量远离，避让。', '注意避让，安全第一。遇见货车之类的大车一定要离得远远的，你就是再好的车也是碰不过大货的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926460, '易导致后车追尾', '会相应增大油耗', '易产生侧滑', '会相应减少油耗', '8', '', '雨天驾驶机动车，不可以急踩制动踏板的主要原因是什么？', '雨天，当车辆在潮湿路上行驶，车轮的抓地力则随车速的增加而急剧变小。很容易发生.水滑，此时，不要急踩制动踏板或猛打转向盘。由于雨天路面情况的特殊性，行车时应适当降低车速。准备停车时最好提前100-200m开始减速.给后方的车辆留出足够的反应时间，也为自己留够制动距离.以免发生追尾。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926461, '倒车逆行，穿越中央分隔带掉头，或在车道内停车', '骑轧车行道分界线或者在路肩上行驶', '在匝道、加速车道或者在减速车道上超车', '试车或者学习驾驶机动车', '17', '', '为确保机动车在高速公路行驶的安全，不得有下列哪些行为？', '根据《中华人民共和国道路交通安全法实施条例》第八十二条  机动车在高速公路上行驶，不得有下列行为：（一）倒车、逆行、穿越中央分隔带掉头或者在车道内停车；（二）在匝道、加速车道或者减速车道上超车；（三）骑、轧车行道分界线或者在路肩上行驶；（四）非紧急情况时在应急车道行驶或者停车；（五）试车或者学习驾驶机动车。因此本题选ABCD。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926463, '连续鸣喇叭示意其让道', '加大安全车距，适时超车', '加速行驶，伺机超车', '紧跟前方大货车', '2', 'http://images.juheapi.com/jztk/subject4/667.jpg', '如图所示，驾驶机动车跟车行驶遇到前方大货车行驶缓慢时，以下做法正确的是什么？', '前方有车时，应加大安全车距，适时超车，ACD选项都不利于安全行车。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926464, '正确', '错误', '', '', '2', '', '行车途中发动机突然熄火，不能继续启动时要采取紧急制动措施，迫使机动车迅速停住。', '若是采取紧急制动的话，很有可能造成机动车侧滑或侧翻，引发事故。因此正确的做法应当是缓慢减速停车。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926465, '正确', '错误', '', '', '2', '', '机动车通过急转弯路段时，在机动车较少的情况下可以超车。', '《中华人民共和国道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 通过隧道，铁路道口，急转弯路段，窄路，窄桥或遇前方道路上有车辆掉头时，不得超车。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926466, '客车超员', '乘车人携带易燃易爆危险物品', '超速行驶', '不按规定停车', '7', '', '邹某驾驶大型卧铺客车（核载35人，实载47人），行至京港澳高速公路938公里时，因乘车人携带的大量危险化学品在车厢内突然发生爆燃，造成41人死亡，6人受伤。此事故中的主要违法行为是什么？', '违法行为：1、核载35人，实载47人为超载；2、乘车人携带的大量危险化学品。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926467, '正确', '错误', '', '', '1', '', '下坡路制动失效后，要迅速逐级或越一级减挡，利用发动机制动作用控制车速。', '发动机制动是利用发动机的牵阻作用减慢车速，档位越低牵阻越明显，制动性越强。在下长坡道路行驶，挂入低速档利用发动机的牵阻作用可以减少制动器的负担和减少制动次数，防止制动过热引起制动力热衰减；在冰雪、泥泞的路面上行驶，应用发动机牵阻制动可以防止侧滑。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926468, '紧跟前车后方行驶', '迅速从车左侧超越', '保持较大跟车距离', '迅速从车右侧超越', '3', 'http://images.juheapi.com/jztk/subject4/672.jpg', '驾驶机动车遇到这种情况怎么办？', '遇到这种情况，要多留个心眼，那就是要考虑到可能从车上掉落东西，以免出状况。因此要加大跟车距离，所以本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926469, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/673.jpg', '驾驶机动车通过这个铁路道口时要减速停车。', '红灯亮了呀亲。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926470, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/674.jpg', '左侧标志表示此处允许机动车掉头。', '黄色虚线+掉头标志都表明此处是可以掉头的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926471, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/675.jpg', '黄色斜线填充线指示该区域禁止进入或压线行驶。', '黄色斜线填充双黄实线禁止跨越对向车行道分界线示例 在路面较宽时，为保证车行道宽度不大于3. 75 m，双黄线间距可以适当调整。在双黄线间距大于50 cm时应用黄色斜线或其他设施填充两条黄实线间的部分，禁止车辆压线或进入该区域。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926472, '不开启灯光', '开远光灯', '开危险报警闪光灯', '开启示廓灯、后位灯', '7', '', '夜间路边临时停车，以下做法错误的是什么?', '夜间临时停车要开启危险报警闪光灯、示廓灯和后尾灯。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926473, '正确', '错误', '', '', '2', '', '漫水道路行车时，要挂高速挡，快速通过。', '不应该挂高速挡，而是要“应选用低速挡，以保证汽车有足够的驱动力；入水时应平稳，以防溅起的水花落 入电器设备，造成电器短路;发动机中速运转，保持车速，中途不得停车、换挡和急转转向盘”这样才对。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926474, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/678.jpg', '右侧标志表示前方路段解除时速40公里限制。', '限制速度-表示该标志至前方限制速度标志的路段内，机动车行驶速度不得超过标志所示数值。此标志设在需要限制车辆速度的路段的起点。以图为例：限制行驶时速不得超过40公里。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926475, '行驶阻力大', '车轮侧滑', '机动车颠簸', '方向失控', '2', '', '在泥泞路段行车容易出现什么现象？', '泥泞路面行驶阻力小，凹凸不平的路面机动车才会颠簸，横风时方向才会失控，因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926476, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/680.jpg', '路中心黄色虚实线指示允许暂时越过超车。', '如果双黄线，一条是实线，一条是虚线，虚线在哪一侧，那侧的车辆就可以从此临时跨越，比如超车或转弯。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926477, '降低车速', '随时准备停车', '尽快超越', '加大横向安全距离', '14', 'http://images.juheapi.com/jztk/subject4/681.jpg', '如图所示，驾驶机动车临近停在车站的公交车时，以下做法正确的是什么？', '不要尽快超越，远离大车，注息安全.谨慎驾驶。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926478, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/682.jpg', '驾驶机动车在这样的路段要注意观察，随时避让横过道路的动物。', '遇到这种情况，要减速慢行，同时注意观察牲畜的行动，以免这些牲畜突然变更方向。因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926480, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/684.jpg', '如图所示，驾驶机动车进入该居民小区，车速不能超过5km/h。', '前方有最高限速标志，限速5km/h.', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926481, '握稳方向', '抢挂低速挡减速', '使用驻车制动器减速', '开启危险报警闪光灯', '17', '', '机动车在行驶中突遇制动失灵时，驾驶人要采取什么措施？', '机动车在行驶中突遇制动失灵时，应当握稳方向并开启危险报警闪光，抢挂低速挡减速，停车后使用驻车制动器，以防车子出现后溜的情况。选项C：使用驻车制动器减速，是说慢慢使用制动器减速，而不是一下拉紧！', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926483, '为减少交会时间，加速通过', '立即停车，上前查看是否有被困人员', '停车后围观', '立即停车，尽量远离，拨打报警电话', '4', '', '驾驶机动车，遇前方停驶的油料运输车起火冒烟，以下做法正确的是什么？', '太危险了，所以打电话，让专业人来处理吧！', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926484, '对', '错', '', '', '1', '', '轮胎气压过高或过低都容易导致爆胎。', '胎压过高或过低都容易娘胎:胎压过高，使轮胎与地面的接触面积减少。单位面积所承受的压力、磨损剧增，容易造成刹车失控;如果胎压过低。使轮胎与地面接触面积增加。行驶时胎内温度不正常增加。同时.由于胎侧变形严重，内部的钢丝、帘布层老化加剧.从而为爆胎埋下隐患。并且使油耗呈增加，轮胎寿命降低。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926485, '会车使用远光灯', '在隧道内超车', '会车时保持安全间距', '开启近光灯行驶', '7', '', '在隧道内通行时哪些行为是不正确的？', '隧道开远光影响前车视线，隧道内禁止超车，因此选择“会灯使用远光灯”“在隧道内超车”。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926486, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/690.jpg', '交通警察发出的是右转弯手势信号。', '图为直行辅助手势。手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926487, '正确', '错误', '', '', '1', '', '机动车发生爆胎后，驾驶人在尚未控制住车速前，不要冒险使用行车制动器停车，以避免机动车横甩发生更大的险情。', '爆胎后,驾驶人在尚未控制住车速的时候，是不能急踩刹车的，要不然会横甩或者翻车已经后面来车追尾 把几道题综合起来看，大致的做法觉得应该是紧握方向盘，控制方向，极力保持直线行驶，利用发动机制动，减档或抢档减速度，轻踏制动踏板缓慢减速，平稳靠边停车', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926488, '前后防雾灯', '危险报警闪光灯', '前大灯', '倒车灯', '2', '', '机动车在雨天临时停车时，应开启什么灯？', '下雨天开车视野不清，因此若要临时停车，应当开启危险报警闪光灯，提醒其他车辆注意。另外三个选项，没有警示作用一说。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926489, '确认机动车车胎是否损毁', '确认周围是否有障碍物', '确认在车辆附近是否存在安全隐患', '确认出车方向的安全性', '17', '', '出车前检查的目的是什么？', '都是为了安全。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432476926490, '继续加速', '向右转向', '向左转向', '紧急制动', '2', '', '在泥泞路段遇车后轮向右侧滑时如何处置？', '好比是后轮向左侧横滑如果向右打方向，会加大横滑的幅度的！因为向右打方向，后轮左侧转动就会快，右侧会变慢，左侧抓地力会下降，所以会加大横滑的幅度的，相反如果向同一方向打，还是这个例子向左侧打，右轮转动会快，左侧转动会慢，抓地力就会提高，就会恢复行使方向的！因此本题选B。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926491, '低速缓慢通过', '加速通过', '连续鸣喇叭通过', '保持正常车速通过', '1', 'http://images.juheapi.com/jztk/subject4/695.jpg', '驾驶机动车遇到这种情况要如何行驶？', '遇到这样的情况要低速慢行，以免水花溅到周围的行人。因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432476926493, '正确', '错误', '', '', '2', '', '如果遇到较强横风，感觉机动车产生横向偏移时，要握紧转向盘并紧急制动。', '当车遇到横风时如果发生的横移，因为在横移的瞬间车轮是离开地面的，所以才会横移，最好的方法就是收油，等车速自己下来，这样就可以让车轮恢复抓地力，然后再进行打轮刹车等动作。这题判错的原因主要是当时车轮是离地的，也就是说车轮是没有抓地力的，如果这时采取紧急制动的话，很有可能会发生侧翻或者侧滑，很危险。因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432476926494, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/698.jpg', '交通警察发出这种手势信号时允许在路口向右转弯。', '手势题详解：http://www.jszks.com/onepage/16203.html', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432565006338, '紧急制动', '迅速转向进行调整', '迅速向无障碍一侧转向躲避', '迅速向有障碍一侧转向躲避', '1', '', '当机动车转向失控行驶方向偏离，事故已经无可避免时，要采取什么措施？。', '除了选项A之外，三个选项都有可能因躲避或转向而造成另外的事故，比如与其他车辆擦碰或是撞到障碍物等。另外注意本题中“事故已经无可避免”这一句，也就是说事故是肯定会发生的，那么接下来要做的就是如何不让事故造成的损伤和损害变大，因此只有选项A是唯一的选择。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432565006339, '可利用避险车道减速停车', '越二级挡位减挡', '挂倒挡迫使停车', '拉紧驻车制动器减速', '1', '', '下坡路行车中制动突然失效怎样处置？', '刹车突然失灵时，摩擦力特别能显神奇功效。可用车前保险杠、车厢等钢性部位与路边岩石、大树或土坡摩擦、碰撞，在不威胁乘车人安全的情况下强行停车。这样，虽对车会造成损害，但可减少交通事故发生率。因此本题选A。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432565006340, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/701.jpg', '驾驶机动车在前方路口掉头前先进入左转直行车道。', '由图可知有掉头车道，因此若要掉头应当提前驶入掉头车道内，所以本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432565006341, '客车超员', '超速行驶', '酒后驾驶', '疲劳驾驶', '2', '', '佟某驾驶一辆大客车（乘载54人，核载55人)行至太原境内以45公里的时速通过一处泥泞路段时，机动车侧滑驶出路外坠入深沟，导致14人死亡、40人受伤。佟某的主要违法行为是什么？', '当车辆行驶中遇到如积水,冰雪,泥泞等路段时行驶时速不得超过每小时30公里,所以他超速了！', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432565006342, '正确', '错误', '', '', '2', '', '机动车在泥泞路段后轮发生侧滑时，要将转向盘向侧滑的相反方向缓转修正。', '好比是后轮向左侧横滑如果向右打方向，会加大横滑的幅度的！因为向右打方向，后轮左侧转动就会快，右侧会变慢，左侧抓地力会下降，所以会加大横滑的幅度的，相反如果向同一方向打，还是这个例子向左侧打，右轮转动会快，左侧转动会慢，抓地力就会提高，就会恢复行使方向的！', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432565006343, '使用驻车制动器', '踏下离合器踏板', '踏制动踏板', '用加速踏板', '4', '', '在泥泞路段行车，要怎样控制速度，匀速一次性通过？', '注意关键：匀速一次性通过，因此要控制要油门，避免熄火等情况发生，因此本题选D。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432565006344, '定期维护制动系统', '行车前检查制动踏板的自由行程', '正确使用制动，防止热衰退', '采用液压制动的机动车，行车前检查制动液是否有滴漏', '17', '', '有效预防机动车发生制动失效的措施是什么？', '四个选项都是对制动系统等的检查，通过检查，预防机动车制动失效的发生。因此本题选ABCD。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432565006347, '停车等待动物穿过', '鸣喇叭驱赶动物', '下车驱赶动物', '与动物保持较远距离', '9', 'http://images.juheapi.com/jztk/subject4/708.jpg', '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么?', '不能鸣喇叭。避免动物因惊吓窜入行车道。保持一定的安全距离，停车让行等待动物穿过。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432569200642, '对', '错', '', '', '2', '', '出车前检查刮水器时，应尽量在干燥状态下进行。', '干燥，摩擦力大，会把爱车的玻璃刮坏的。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432569200644, '对', '错', '', '', '1', '', '驾驶机动车在转弯之前应留意旁边行驶的自行车，是因为自行车比较小，不太容易被看到。', '非机动车和行人。机动车在转弯时视野有盲区不太容易被看到.且随意性比较大.要特别注惫。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432569200645, '仔细巡视车辆四周的状况，观察车底和车身周围是否有障碍物', '上车后关好车门，调整好座位，系好安全带', '启动车辆，观察仪表，检查车辆工作是否正常', '调整好后视镜', '17', '', '出车前，应该做的准备工作是什么？', '仔细巡视车辆四周的状况，观察车底和车身周围是否有障碍物.上车后关好车门.调整好座位，系好安全带.启动车辆。观察仪裹.检查车辆工作是否正常，调整好后视镜。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432569200646, '加速从前方绕过', '加速从身后绕行', '减速或停车让行', '连续鸣喇叭提醒', '3', 'http://images.juheapi.com/jztk/subject4/713.jpg', '驾驶机动车遇到这样的行人怎样礼让？', '遇到这种情况要减速慢行，必要时停车让行。因此本题选C。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432569200647, '正确', '错误', '', '', '2', 'http://images.juheapi.com/jztk/subject4/714.jpg', '驾驶机动车遇到这种情况时要向左占道行驶。', '遇到这种情况要减速慢行，必要时停车，因此本题错误。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432569200648, '前方有人行横道', '应当相应减速行驶', '视野范围内无行人可以保持原速行驶', '视野范围内无行人可以适当加速通过', '7', 'http://images.juheapi.com/jztk/subject4/715.jpg', '如图所示，当您看到这个标志时，应该想到什么?', '注意行人标志。用以警告车辆驾驶人减速慢行，注意行人。设在行人密集，或不易被写驶员发现的人行横道线以前适当位置。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432569200650, '人要站在下风处', '灭火器瞄准火源', '尽量接近火源', '灭火器瞄准火苗', '2', '', '怎样正确使用灭火器灭火？', '1、把灭火器罐上下晃动三次以上  2、将安全梢拉开。  3、握住出粉皮管，将皮管朝向火点。  4、距着火点4―6米处用力压下把手，选择上风位置或者侧风方向接近火点，将干粉射入火焰基部。  5、息灭后并以水冷却除烟以防止复燃。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432569200651, '占道行驶', '急转转向盘', '弯道前不减速', '靠路右侧行驶', '13', '', '驾驶汽车在转弯路段易引发事故的驾驶行为有哪些？', '看清题目哦，问的是易发生事故的驾驶行为。', 4, 1, 2, NULL, 2);
INSERT INTO `me_question` VALUES (104432569200652, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/719.jpg', '驾驶机动车需要掉头时，只要不影响正常交通可以在虚线处掉头。', '黄色虚线中心线处是可以掉头的，因此本题正确。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432569200653, '对', '错', '', '', '1', 'http://images.juheapi.com/jztk/subject4/720.jpg', '如图所示，驾驶机动车看到路边有这种标志时，表示前方接近学校区域，因此要提前减速注意观察。', '注意儿童标志:用以警告车辆驾驶人减速慢行.注意儿童。设在小学、幼儿园、少年宫等儿全经常出入地点前适当位置。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432569200654, '将机动车向上坡道方向行驶', '用车身靠向路旁的岩石或树林碰擦', '利用道路边专设的避险车道停车', '拉紧驻车制动器操纵杆或越二级挡位减挡', '4', '', '下坡路制动突然失效后，不可采用的办法是什么？', '利用重力作用增大汽车前进阻力，就是山坡时停止动力供应后车速逐渐下降。你也可以使用发动机制动，但使用驻车制动器作用很小，是不正确的。', 4, 1, 2, NULL, 0);
INSERT INTO `me_question` VALUES (104432569200655, '正确', '错误', '', '', '1', '', '在冰雪道路上行车时，机动车的稳定性降低，加速过急时车轮易空转或溜滑。', '轮胎与地面的摩擦系数降低，轮胎不易附着在地面上，只要一加速，就极易空转或溜滑。', 4, 1, 2, NULL, 1);
INSERT INTO `me_question` VALUES (104432569200656, '正确', '错误', '', '', '1', 'http://images.juheapi.com/jztk/subject4/723.jpg', '该车道路面导向箭头指示前方道路仅可直行。', '该车道路面导向箭头指示前方道路仅可直行。', 4, 1, 2, NULL, 1);
